RAW_ANIME_DATA = [{"id": 59978, "name_en": "Frieren: Beyond Journey's End Season 2", "name_jp": "葬送のフリーレン 第2期", "image_url": "https://cdn.myanimelist.net/images/anime/1921/154528.jpg", "genres": ["Adventure", "Drama", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Madhouse"], "year": 2026, "episodes": 10, "source": "Manga", "score": 9.3, "synopsis": "Second season of Sousou no Frieren."}, {"id": 52991, "name_en": "Frieren: Beyond Journey's End", "name_jp": "葬送のフリーレン", "image_url": "https://cdn.myanimelist.net/images/anime/1015/138006.jpg", "genres": ["Adventure", "Drama", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Madhouse"], "year": 2023, "episodes": 28, "source": "Manga", "score": 9.28, "synopsis": "During their decade-long quest to defeat the Demon King, the members of the hero's party—Himmel himself, the priest Heiter, the dwarf warrior Eisen, and the elven mage Frieren—forge bonds through adventures and battles, creating unforgettable precious memories for most of them.\n\nHowever, the time that Frieren spends with her comrades is equivalent to merely a fraction of her life, which has lasted over a thousand years. When the party disbands after their victory, Frieren casually returns to her \"usual\" routine of collecting spells across the continent. Due to her different sense of time, she seemingly holds no strong feelings toward the experiences she went through.\n\nAs the years pass, Frieren gradually realizes how her days in the hero's party truly impacted her. Witnessing the deaths of two of her former companions, Frieren begins to regret having taken their presence for granted; she vows to better understand humans and create real personal connections. Although the story of that once memorable journey has long ended, a new tale is about to begin.\n\n[Written by MAL Rewrite]"}, {"id": 57555, "name_en": "Chainsaw Man – The Movie: Reze Arc", "name_jp": "劇場版 チェンソーマン レゼ篇", "image_url": "https://cdn.myanimelist.net/images/anime/1763/150638.jpg", "genres": ["Action", "Fantasy"], "themes": ["Gore", "Urban Fantasy"], "demographics": ["Shounen"], "studios": ["MAPPA"], "year": 2025, "episodes": 1, "source": "Manga", "score": 9.11, "synopsis": "Sequel to Chainsaw Man."}, {"id": 5114, "name_en": "Fullmetal Alchemist: Brotherhood", "name_jp": "鋼の錬金術師 FULLMETAL ALCHEMIST", "image_url": "https://cdn.myanimelist.net/images/anime/1208/94745.jpg", "genres": ["Action", "Adventure", "Drama", "Fantasy"], "themes": ["Military"], "demographics": ["Shounen"], "studios": ["Bones"], "year": 2009, "episodes": 64, "source": "Manga", "score": 9.1, "synopsis": "After a horrific alchemy experiment goes wrong in the Elric household, brothers Edward and Alphonse are left in a catastrophic new reality. Ignoring the alchemical principle banning human transmutation, the boys attempted to bring their recently deceased mother back to life. Instead, they suffered brutal personal loss: Alphonse's body disintegrated while Edward lost a leg and then sacrificed an arm to keep Alphonse's soul in the physical realm by binding it to a hulking suit of armor.\n\nThe brothers are rescued by their neighbor Pinako Rockbell and her granddaughter Winry. Known as a bio-mechanical engineering prodigy, Winry creates prosthetic limbs for Edward by utilizing \"automail,\" a tough, versatile metal used in robots and combat armor. After years of training, the Elric brothers set off on a quest to restore their bodies by locating the Philosopher's Stone—a powerful gem that allows an alchemist to defy the traditional laws of Equivalent Exchange.\n\nAs Edward becomes an infamous alchemist and gains the nickname \"Fullmetal,\" the boys' journey embroils them in a growing conspiracy that threatens the fate of the world.\n\n[Written by MAL Rewrite]"}, {"id": 9253, "name_en": "Steins;Gate", "name_jp": "STEINS;GATE", "image_url": "https://cdn.myanimelist.net/images/anime/1935/127974.jpg", "genres": ["Drama", "Sci-Fi", "Suspense"], "themes": ["Psychological", "Time Travel"], "demographics": [], "studios": ["White Fox"], "year": 2011, "episodes": 24, "source": "Visual novel", "score": 9.07, "synopsis": "Eccentric scientist Rintarou Okabe has a never-ending thirst for scientific exploration. Together with his ditzy but well-meaning friend Mayuri Shiina and his roommate Itaru Hashida, Okabe founds the Future Gadget Laboratory in the hopes of creating technological innovations that baffle the human psyche. Despite claims of grandeur, the only notable \"gadget\" the trio have created is a microwave that has the mystifying power to turn bananas into green goo.\n\nHowever, when Okabe attends a conference on time travel, he experiences a series of strange events that lead him to believe that there is more to the \"Phone Microwave\" gadget than meets the eye. Apparently able to send text messages into the past using the microwave, Okabe dabbles further with the \"time machine,\" attracting the ire and attention of the mysterious organization SERN.\n\nDue to the novel discovery, Okabe and his friends find themselves in an ever-present danger. As he works to mitigate the damage his invention has caused to the timeline, Okabe fights a battle to not only save his loved ones but also to preserve his degrading sanity.\n\n[Written by MAL Rewrite]"}, {"id": 38524, "name_en": "Attack on Titan Season 3 Part 2", "name_jp": "進撃の巨人 Season3 Part.2", "image_url": "https://cdn.myanimelist.net/images/anime/1517/100633.jpg", "genres": ["Action", "Drama", "Suspense"], "themes": ["Gore", "Military", "Survival"], "demographics": ["Shounen"], "studios": ["Wit Studio"], "year": 2019, "episodes": 10, "source": "Manga", "score": 9.05, "synopsis": "Seeking to restore humanity's diminishing hope, the Survey Corps embark on a mission to retake Wall Maria, where the battle against the merciless \"Titans\" takes the stage once again.\n\nReturning to the tattered Shiganshina District that was once his home, Eren Yeager and the Corps find the town oddly unoccupied by Titans. Even after the outer gate is plugged, they strangely encounter no opposition. The mission progresses smoothly until Armin Arlert, highly suspicious of the enemy's absence, discovers distressing signs of a potential scheme against them. \n\nShingeki no Kyojin Season 3 Part 2 follows Eren as he vows to take back everything that was once his. Alongside him, the Survey Corps strive—through countless sacrifices—to carve a path towards victory and uncover the secrets locked away in the Yeager family's basement.\n\n[Written by MAL Rewrite]"}, {"id": 28977, "name_en": "Gintama Season 4", "name_jp": "銀魂°", "image_url": "https://cdn.myanimelist.net/images/anime/3/72078.jpg", "genres": ["Action", "Comedy", "Sci-Fi"], "themes": ["Gag Humor", "Historical", "Parody", "Samurai"], "demographics": ["Shounen"], "studios": ["Bandai Namco Pictures"], "year": 2015, "episodes": 51, "source": "Manga", "score": 9.05, "synopsis": "Gintoki, Shinpachi, and Kagura return as the fun-loving but broke members of the Yorozuya team! Living in an alternate-reality Edo, where swords are prohibited and alien overlords have conquered Japan, they try to thrive on doing whatever work they can get their hands on. However, Shinpachi and Kagura still haven't been paid... Does Gin-chan really spend all that cash playing pachinko?\n\nMeanwhile, when Gintoki drunkenly staggers home one night, an alien spaceship crashes nearby. A fatally injured crew member emerges from the ship and gives Gintoki a strange, clock-shaped device, warning him that it is incredibly powerful and must be safeguarded. Mistaking it for his alarm clock, Gintoki proceeds to smash the device the next morning and suddenly discovers that the world outside his apartment has come to a standstill. With Kagura and Shinpachi at his side, he sets off to get the device fixed; though, as usual, nothing is ever that simple for the Yorozuya team.\n\nFilled with tongue-in-cheek humor and moments of heartfelt emotion, Gintama's fourth season finds Gintoki and his friends facing both their most hilarious misadventures and most dangerous crises yet.\n\n[Written by MAL Rewrite]"}, {"id": 39486, "name_en": "Gintama: The Very Final", "name_jp": "銀魂 THE FINAL", "image_url": "https://cdn.myanimelist.net/images/anime/1245/116760.jpg", "genres": ["Action", "Comedy", "Drama", "Sci-Fi"], "themes": ["Gag Humor", "Historical", "Parody", "Samurai"], "demographics": ["Shounen"], "studios": ["Bandai Namco Pictures"], "year": 2021, "episodes": 1, "source": "Manga", "score": 9.05, "synopsis": "Two years have passed following the Tendoshuu's invasion of the O-Edo Central Terminal. Since then, the Yorozuya have gone their separate ways. Foreseeing Utsuro's return, Gintoki Sakata begins surveying Earth's ley lines for traces of the other man's Altana. After an encounter with the remnants of the Tendoshuu—who continue to press on in search of immortality—Gintoki returns to Edo.\n\nLater, the regrouped Shinsengumi and Yorozuya begin an attack on the occupied Central Terminal. With the Altana harvested by the wreckage of the Tendoshuu's ship in danger of detonating, the Yorozuya and their allies fight their enemies while the safety of Edo—and the rest of the world—hangs in the balance. Fulfilling the wishes of their teacher, Shouyou Yoshida's former students unite and relive their pasts one final time in an attempt to save their futures.\n\n[Written by MAL Rewrite]"}, {"id": 11061, "name_en": "Hunter x Hunter", "name_jp": "HUNTER×HUNTER（ハンター×ハンター）", "image_url": "https://cdn.myanimelist.net/images/anime/1337/99013.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Madhouse"], "year": 2011, "episodes": 148, "source": "Manga", "score": 9.03, "synopsis": "Hunters devote themselves to accomplishing hazardous tasks, all from traversing the world's uncharted territories to locating rare items and monsters. Before becoming a Hunter, one must pass the Hunter Examination—a high-risk selection process in which most applicants end up handicapped or worse, deceased.\n\nAmbitious participants who challenge the notorious exam carry their own reason. What drives 12-year-old Gon Freecss is finding Ging, his father and a Hunter himself. Believing that he will meet his father by becoming a Hunter, Gon takes the first step to walk the same path.\n\nDuring the Hunter Examination, Gon befriends the medical student Leorio Paladiknight, the vindictive Kurapika, and ex-assassin Killua Zoldyck. While their motives vastly differ from each other, they band together for a common goal and begin to venture into a perilous world.\n\n[Written by MAL Rewrite]"}, {"id": 60022, "name_en": "One Piece Fan Letter", "name_jp": "ONE PIECE FAN LETTER", "image_url": "https://cdn.myanimelist.net/images/anime/1455/146229.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Toei Animation"], "year": 2024, "episodes": 1, "source": "Light novel", "score": 9.02, "synopsis": "Although the golden age of piracy is about to reach new heights, most people do not seek the glory of finding the elusive One Piece—a treasure signifying a new conqueror of all seas that was once embodied by the legendary King of the Pirates, Gol D. Roger. However, even if civilians generally despise pirates, they secretly cheer for at least one of them. \n\nOne red-headed girl from Sabaody Archipelago is no exception: She reveres Nami, the ingenious female navigator of Monkey D. Luffy's Straw Hat crew. Determined to deliver a fan letter to her idol, the Sabaody child is prepared to challenge forces of authority who strive to prevent Luffy and his friends from departing for their next destination: the New World. But to succeed, Nami's fan may need to risk her life and interfere with the Marines' plans, potentially causing devastating consequences for the wider world.\n\n[Written by MAL Rewrite]"}, {"id": 9969, "name_en": "Gintama Season 2", "name_jp": "銀魂'", "image_url": "https://cdn.myanimelist.net/images/anime/4/50361.jpg", "genres": ["Action", "Comedy", "Sci-Fi"], "themes": ["Gag Humor", "Historical", "Parody", "Samurai"], "demographics": ["Shounen"], "studios": ["Sunrise"], "year": 2011, "episodes": 51, "source": "Manga", "score": 9.02, "synopsis": "After a one-year hiatus, Shinpachi Shimura returns to Edo, only to stumble upon a shocking surprise: Gintoki and Kagura, his fellow Yorozuya members, have become completely different characters! Fleeing from the Yorozuya headquarters in confusion, Shinpachi finds that all the denizens of Edo have undergone impossibly extreme changes, in both appearance and personality. Most unbelievably, his sister Otae has married the Shinsengumi chief and shameless stalker Isao Kondou and is pregnant with their first child.\n\nBewildered, Shinpachi agrees to join the Shinsengumi at Otae and Kondou's request and finds even more startling transformations afoot both in and out of the ranks of the the organization. However, discovering that Vice Chief Toushirou Hijikata has remained unchanged, Shinpachi and his unlikely Shinsengumi ally set out to return the city of Edo to how they remember it.\n\nWith even more dirty jokes, tongue-in-cheek parodies, and shameless references, Gintama' follows the Yorozuya team through more of their misadventures in the vibrant, alien-filled world of Edo.\n\n[Written by MAL Rewrite]"}, {"id": 15417, "name_en": "Gintama: Enchousen", "name_jp": "銀魂' 延長戦", "image_url": "https://cdn.myanimelist.net/images/anime/1452/123686.jpg", "genres": ["Action", "Comedy", "Sci-Fi"], "themes": ["Gag Humor", "Historical", "Parody", "Samurai"], "demographics": ["Shounen"], "studios": ["Sunrise"], "year": 2012, "episodes": 13, "source": "Manga", "score": 9.02, "synopsis": "While Gintoki Sakata was away, the Yorozuya found themselves a new leader: Kintoki, Gintoki's golden-haired doppelganger. In order to regain his former position, Gintoki will need the help of those around him, a troubling feat when no one can remember him! Between Kintoki and Gintoki, who will claim the throne as the main character?\n\nIn addition, Yorozuya make a trip back down to red-light district of Yoshiwara to aid an elderly courtesan in her search for her long-lost lover. Although the district is no longer in chains beneath the earth's surface, the trio soon learn of the tragic backstories of Yoshiwara's inhabitants that still haunt them. With flashback after flashback, this quest has Yorozuya witnessing everlasting love and protecting it as best they can with their hearts and souls.\n\n[Written by MAL Rewrite]"}, {"id": 34096, "name_en": "Gintama Season 5", "name_jp": "銀魂。", "image_url": "https://cdn.myanimelist.net/images/anime/3/83528.jpg", "genres": ["Action", "Comedy", "Sci-Fi"], "themes": ["Gag Humor", "Historical", "Parody", "Samurai"], "demographics": ["Shounen"], "studios": ["Bandai Namco Pictures"], "year": 2017, "episodes": 12, "source": "Manga", "score": 8.98, "synopsis": "After joining the resistance against the bakufu, Gintoki and the gang are in hiding, along with Katsura and his Joui rebels. The Yorozuya is soon approached by Nobume Imai and two members of the Kiheitai, who explain that the Harusame pirates have turned against 7th Division Captain Kamui and their former ally Takasugi. The Kiheitai present Gintoki with a job: find Takasugi, who has been missing since his ship was ambushed in a Harusame raid. Nobume also makes a stunning revelation regarding the Tendoushuu, a secret organization pulling the strings of numerous factions, and their leader Utsuro, the shadowy figure with an uncanny resemblance to Gintoki's former teacher.\n\nHitching a ride on Sakamoto's space ship, the Yorozuya and Katsura set out for Rakuyou, Kagura's home planet, where the various factions have gathered and tensions are brewing. Long-held grudges, political infighting, and the Tendoushuu's sinister overarching plan finally culminate into a massive, decisive battle on Rakuyou.\n\n[Written by MAL Rewrite]"}, {"id": 41467, "name_en": "Bleach: Thousand-Year Blood War", "name_jp": "BLEACH 千年血戦篇", "image_url": "https://cdn.myanimelist.net/images/anime/1908/135431.jpg", "genres": ["Action", "Adventure", "Supernatural"], "themes": [], "demographics": ["Shounen"], "studios": ["Studio Pierrot"], "year": 2022, "episodes": 13, "source": "Manga", "score": 8.98, "synopsis": "Substitute Soul Reaper Ichigo Kurosaki spends his days fighting against Hollows, dangerous evil spirits that threaten Karakura Town. Ichigo carries out his quest with his closest allies: Orihime Inoue, his childhood friend with a talent for healing; Yasutora Sado, his high school classmate with superhuman strength; and Uryuu Ishida, Ichigo's Quincy rival.\n\nIchigo's vigilante routine is disrupted by the sudden appearance of Asguiaro Ebern, a dangerous Arrancar who heralds the return of Yhwach, an ancient Quincy king. Yhwach seeks to reignite the historic blood feud between Soul Reaper and Quincy, and he sets his sights on erasing both the human world and the Soul Society for good.\n\nYhwach launches a two-pronged invasion into both the Soul Society and Hueco Mundo, the home of Hollows and Arrancar. In retaliation, Ichigo and his friends must fight alongside old allies and enemies alike to end Yhwach's campaign of carnage before the world itself comes to an end.\n\n[Written by MAL Rewrite]"}, {"id": 43608, "name_en": "Kaguya-sama: Love is War -Ultra Romantic-", "name_jp": "かぐや様は告らせたい-ウルトラロマンティック-", "image_url": "https://cdn.myanimelist.net/images/anime/1160/122627.jpg", "genres": ["Comedy", "Romance"], "themes": ["School"], "demographics": ["Seinen"], "studios": ["A-1 Pictures"], "year": 2022, "episodes": 13, "source": "Manga", "score": 8.96, "synopsis": "The elite members of Shuchiin Academy's student council continue their competitive day-to-day antics. Council president Miyuki Shirogane clashes daily against vice-president Kaguya Shinomiya, each fighting tooth and nail to trick the other into confessing their romantic love. Kaguya struggles within the strict confines of her wealthy, uptight family, rebelling against her cold default demeanor as she warms to Shirogane and the rest of her friends.\n\nMeanwhile, council treasurer Yuu Ishigami suffers under the weight of his hopeless crush on Tsubame Koyasu, a popular upperclassman who helps to instill a new confidence in him. Miko Iino, the newest student council member, grows closer to the rule-breaking Ishigami while striving to overcome her own authoritarian moral code.\n\nAs love further blooms at Shuchiin Academy, the student council officers drag their outsider friends into increasingly comedic conflicts.\n\n[Written by MAL Rewrite]"}, {"id": 42938, "name_en": "Fruits Basket: The Final Season", "name_jp": "フルーツバスケット The Final", "image_url": "https://cdn.myanimelist.net/images/anime/1085/114792.jpg", "genres": ["Drama", "Romance", "Supernatural"], "themes": [], "demographics": ["Shoujo"], "studios": ["TMS Entertainment"], "year": 2021, "episodes": 13, "source": "Manga", "score": 8.94, "synopsis": "Hundreds of years ago, the Chinese zodiac spirits and their god swore to stay together eternally. United by this promise, the possessed members of the Souma family shall always return to each other under any circumstances. Yet, when these bonds shackle them from freedom, it becomes an undesirable burden—a curse. As head of the clan, Akito is convinced that he shares a special connection with the other Soumas. While he desperately clings to this fantasy, the rest of the family remains isolated and suppressed by the fear of punishment.\n\nTooru Honda, who has grown attached to the Soumas, is determined to break the chains that bind them. Her companionship with the family and her friends encourages her to move forward with lifting the curse. However, due to confounding revelations, she struggles to find the tenacity to continue her endeavors. With time slowly withering away, Tooru contends with an uncertain future in hopes of reaching the tranquility that may lie beyond all this commotion.\n\n[Written by MAL Rewrite]"}, {"id": 4181, "name_en": "Clannad: After Story", "name_jp": "CLANNAD〜AFTER STORY〜 クラナド アフターストーリー", "image_url": "https://cdn.myanimelist.net/images/anime/1299/110774.jpg", "genres": ["Drama", "Romance"], "themes": [], "demographics": [], "studios": ["Kyoto Animation"], "year": 2008, "episodes": 24, "source": "Visual novel", "score": 8.93, "synopsis": "Tomoya Okazaki and Nagisa Furukawa have graduated from high school, and together, they experience the emotional rollercoaster of growing up. Unable to decide on a course for his future, Tomoya learns the value of a strong work ethic and discovers the strength of Nagisa's support. Through the couple's dedication and unity of purpose, they push forward to confront their personal problems, deepen their old relationships, and create new bonds.\n\nTime also moves on in the Illusionary World. As the plains grow cold with the approach of winter, the Illusionary Girl and the Garbage Doll are presented with a difficult situation that reveals the World's true purpose.\n\n[Written by MAL Rewrite]"}, {"id": 918, "name_en": "Gintama", "name_jp": "銀魂", "image_url": "https://cdn.myanimelist.net/images/anime/10/73274.jpg", "genres": ["Action", "Comedy", "Sci-Fi"], "themes": ["Gag Humor", "Historical", "Parody", "Samurai"], "demographics": ["Shounen"], "studios": ["Sunrise"], "year": 2006, "episodes": 201, "source": "Manga", "score": 8.93, "synopsis": "Edo is a city that was home to the vigor and ambition of samurai across the country. However, following feudal Japan's surrender to powerful aliens known as the \"Amanto,\" those aspirations now seem unachievable. With the once-influential shogunate rebuilt as a puppet government, a new law is passed that promptly prohibits all swords in public. \n\nEnter Gintoki Sakata, an eccentric silver-haired man who always carries around a wooden sword and maintains his stature as a samurai despite the ban. As the founder of Yorozuya, a small business for odd jobs, Gintoki often embarks on endeavors to help other people—though usually in rather strange and unforeseen ways. \n\nAssisted by Shinpachi Shimura, a boy with glasses supposedly learning the way of the samurai; Kagura, a tomboyish girl with superhuman strength and an endless appetite; and Sadaharu, their giant pet dog who loves biting on people's heads, the Yorozuya encounter anything from alien royalty to scuffles with local gangs in the ever-changing world of Edo.\n\n[Written by MAL Rewrite]"}, {"id": 28851, "name_en": "A Silent Voice", "name_jp": "聲の形", "image_url": "https://cdn.myanimelist.net/images/anime/1122/96435.jpg", "genres": ["Award Winning", "Drama"], "themes": [], "demographics": ["Shounen"], "studios": ["Kyoto Animation"], "year": 2016, "episodes": 1, "source": "Manga", "score": 8.93, "synopsis": "As a wild youth, elementary school student Shouya Ishida sought to beat boredom in the cruelest ways. When the deaf Shouko Nishimiya transfers into his class, Shouya and the rest of his class thoughtlessly bully her for fun. However, when her mother notifies the school, he is singled out and blamed for everything done to her. With Shouko transferring out of the school, Shouya is left at the mercy of his classmates. He is heartlessly ostracized all throughout elementary and middle school, while teachers turn a blind eye.\n\nNow in his third year of high school, Shouya is still plagued by his wrongdoings as a young boy. Sincerely regretting his past actions, he sets out on a journey of redemption: to meet Shouko once more and make amends.\n\nKoe no Katachi tells the heartwarming tale of Shouya's reunion with Shouko and his honest attempts to redeem himself, all while being continually haunted by the shadows of his past.\n \n[Written by MAL Rewrite]"}, {"id": 58514, "name_en": "The Apothecary Diaries Season 2", "name_jp": "薬屋のひとりごと 第2期", "image_url": "https://cdn.myanimelist.net/images/anime/1025/147458.jpg", "genres": ["Drama", "Mystery"], "themes": ["Historical", "Medical"], "demographics": [], "studios": ["OLM", "TOHO animation STUDIO"], "year": 2025, "episodes": 24, "source": "Light novel", "score": 8.91, "synopsis": "Using her wit and vast knowledge of medicines and poisons alike, Maomao played a pivotal role in solving a series of mysteries and conspiracies that plagued the imperial court. Having recently come to terms with the secrets of her parents, she returns to fulfill her normal duties on behalf of the emperor's highest-ranking consorts. Maomao also works alongside the eunuch Jinshi to better the consorts' many ladies-in-waiting, including helping them learn to read.\n\nHowever, with the arrival of a merchant caravan comes a new wave of intrigue. A pattern of strange coincidences involving the visitors and their wares unsettles Maomao, driving her to investigate the puzzling circumstances behind the convoy. As dangers from both outside and within threaten the balance between the imperial concubines, Maomao continues to utilize her cunning and medical expertise to keep the women safe from harm.\n\n[Written by MAL Rewrite]"}, {"id": 2904, "name_en": "Code Geass: Lelouch of the Rebellion R2", "name_jp": "コードギアス 反逆のルルーシュ R2", "image_url": "https://cdn.myanimelist.net/images/anime/1088/135089.jpg", "genres": ["Award Winning", "Drama", "Sci-Fi"], "themes": ["Mecha", "Military", "Super Power"], "demographics": [], "studios": ["Sunrise"], "year": 2008, "episodes": 25, "source": "Original", "score": 8.91, "synopsis": "One year has passed since the Black Rebellion, a failed uprising against the Holy Britannian Empire led by the masked vigilante Zero, who is now missing. At a loss without their revolutionary leader, Area 11's resistance group—the Black Knights—find themselves too powerless to combat the brutality inflicted upon the Elevens by Britannia, which has increased significantly in order to crush any hope of a future revolt. \n\nLelouch Lamperouge, having lost all memory of his double life, is living peacefully alongside his friends as a high school student at Ashford Academy. His former partner C.C., unable to accept this turn of events, takes it upon herself to remind him of his past purpose, hoping that the mastermind Zero will rise once again to finish what he started.\n\n[Written by MAL Rewrite]"}, {"id": 35180, "name_en": "March Comes In Like a Lion 2nd Season", "name_jp": "3月のライオン 第2シリーズ", "image_url": "https://cdn.myanimelist.net/images/anime/3/88469.jpg", "genres": ["Drama"], "themes": ["Childcare", "Strategy Game"], "demographics": ["Seinen"], "studios": ["Shaft"], "year": 2017, "episodes": 22, "source": "Manga", "score": 8.9, "synopsis": "Now in his second year of high school, Rei Kiriyama continues pushing through his struggles in the professional shogi world as well as his personal life. Surrounded by vibrant personalities at the shogi hall, the school club, and in the local community, his solitary shell slowly begins to crack. Among them are the three Kawamoto sisters—Akari, Hinata, and Momo—who forge an affectionate and familial bond with Rei. Through these ties, he realizes that everyone is burdened by their own emotional hardships and begins learning how to rely on others while supporting them in return. \n\nNonetheless, the life of a professional is not easy. Between tournaments, championships, and title matches, the pressure mounts as Rei advances through the ranks and encounters incredibly skilled opponents. As he manages his relationships with those who have grown close to him, the shogi player continues to search for the reason he plays the game that defines his career.\n\n[Written by MAL Rewrite]"}, {"id": 15335, "name_en": "Gintama: The Movie: The Final Chapter: Be Forever Yorozuya", "name_jp": "劇場版 銀魂 完結篇 万事屋よ永遠なれ", "image_url": "https://cdn.myanimelist.net/images/anime/10/51723.jpg", "genres": ["Action", "Comedy", "Sci-Fi"], "themes": ["Gag Humor", "Historical", "Parody", "Samurai", "Time Travel"], "demographics": ["Shounen"], "studios": ["Sunrise"], "year": 2013, "episodes": 1, "source": "Manga", "score": 8.89, "synopsis": "When Gintoki apprehends a movie pirate at a premiere, he checks the camera's footage and finds himself transported to a bleak, post-apocalyptic version of Edo, where a mysterious epidemic called the \"White Plague\" has ravished the world's population. It turns out that the movie pirate wasn't a pirate after all—it was an android time machine, and Gintoki has been hurtled five years into the future! Shinpachi and Kagura, his Yorozuya cohorts, have had a falling out and are now battle-hardened solo vigilantes and he himself has been missing for years, disappearing without a trace after scribbling a strange message in his journal.\n\nSetting out in the disguise given to him by the android time machine, Gintoki haphazardly reunites the Yorozuya team to investigate the White Plague, and soon discovers that the key to saving the future lies in the darkness of his own past. Determined to confront a powerful foe, he makes an important discovery—with a ragtag band of friends and allies at his side, he doesn't have to fight alone.\n\n[Written by MAL Rewrite]"}, {"id": 19, "name_en": "Monster", "name_jp": "モンスター", "image_url": "https://cdn.myanimelist.net/images/anime/10/18793.jpg", "genres": ["Drama", "Mystery", "Suspense"], "themes": ["Adult Cast", "Psychological"], "demographics": ["Seinen"], "studios": ["Madhouse"], "year": 2004, "episodes": 74, "source": "Manga", "score": 8.89, "synopsis": "Dr. Kenzou Tenma, an elite neurosurgeon recently engaged to his hospital director's daughter, is well on his way to ascending the hospital hierarchy. That is until one night, a seemingly small event changes Dr. Tenma's life forever. While preparing to perform surgery on someone, he gets a call from the hospital director telling him to switch patients and instead perform life-saving brain surgery on a famous performer. His fellow doctors, fiancée, and the hospital director applaud his accomplishment; but because of the switch, a poor immigrant worker is dead, causing Dr. Tenma to have a crisis of conscience.\n\nSo when a similar situation arises, Dr. Tenma stands his ground and chooses to perform surgery on the young boy Johan Liebert instead of the town's mayor. Unfortunately, this choice leads to serious ramifications for Dr. Tenma—losing his social standing being one of them. However, with the mysterious death of the director and two other doctors, Dr. Tenma's position is restored. With no evidence to convict him, he is released and goes on to attain the position of hospital director. \n\nNine years later when Dr. Tenma saves the life of a criminal, his past comes back to haunt him—once again, he comes face to face with the monster he operated on. He must now embark on a quest of pursuit to make amends for the havoc spread by the one he saved.\n\n[Written by MAL Rewrite]"}, {"id": 37491, "name_en": "Gintama. Silver Soul Arc - Second Half War", "name_jp": "銀魂. 銀ノ魂篇 後半戦", "image_url": "https://cdn.myanimelist.net/images/anime/1776/96566.jpg", "genres": ["Action", "Comedy", "Sci-Fi"], "themes": ["Gag Humor", "Historical", "Parody", "Samurai"], "demographics": ["Shounen"], "studios": ["Bandai Namco Pictures"], "year": 2018, "episodes": 14, "source": "Manga", "score": 8.88, "synopsis": "Following the temporary retreat of the Altana Liberation Army from the Kabuki District, the state of the war has seemingly improved. However, as the Oniwaban, Shinsengumi, and residents of the district combat the army's remnants, Edo's greatest inventor Gengai Hiraga is abducted. Responsible for causing the enemy's withdrawal by rendering their weapons useless, Gengai's nanomachine virus is now at risk of being shut down.\n\nMeanwhile, a laser capable of obliterating a planet is activated in Earth's orbit on the Liberation Army's mother ship. Another battle ensues when Shinsuke Takasugi and the rest of the Kiheitai arrive on the vessel to stop the weapon from firing. Forced to fight a war on two fronts, the Yorozuya and their allies must prevail on both sides to save Edo and the rest of the world.\n\n[Written by MAL Rewrite]"}, {"id": 51535, "name_en": "Attack on Titan: Final Season - The Final Chapters", "name_jp": "進撃の巨人 The Final Season完結編", "image_url": "https://cdn.myanimelist.net/images/anime/1279/131078.jpg", "genres": ["Action", "Drama", "Suspense"], "themes": ["Gore", "Military", "Survival"], "demographics": ["Shounen"], "studios": ["MAPPA"], "year": 2023, "episodes": 2, "source": "Manga", "score": 8.86, "synopsis": "In the wake of Eren Yeager's cataclysmic actions, his friends and former enemies form an alliance against his genocidal rampage. Though once bitter foes, Armin Arlert, Mikasa Ackerman, and the remaining members of the Scout Regiment join forces with Reiner Braun and the survivors of the Marleyan military. Their meager united front sets out on a mission to stop Eren's wrath and—if possible—save their old comrade in the process.\n\nAs Eren pushes forward at any cost, he battles his own internal turmoil. Although he feels immense remorse over his horrific invasion, Eren believes he harbors noble intentions: he believes the path ahead is the only way to save his friends and, to a greater extent, his people.\n\nThe opposing battalions spiral toward an inevitable final clash that may claim the lives of millions. Though they face an army of monsters beyond anything they could have previously imagined, Mikasa, Armin, and their allies stand brave in the face of certain doom.\n\n[Written by MAL Rewrite]"}, {"id": 35247, "name_en": "Owarimonogatari Second Season", "name_jp": "終物語", "image_url": "https://cdn.myanimelist.net/images/anime/6/87322.jpg", "genres": ["Comedy", "Mystery", "Supernatural"], "themes": ["Vampire"], "demographics": [], "studios": ["Shaft"], "year": 2017, "episodes": 7, "source": "Light novel", "score": 8.86, "synopsis": "Following an encounter with oddity specialist Izuko Gaen, third-year high school student Koyomi Araragi wakes up in a strange, deserted void only to be greeted by a joyfully familiar face in an alarmingly unfamiliar place.  \n\nAraragi, with the help of his girlfriend Hitagi Senjougahara, maneuvers through the webs of his past and the perplexities of the present in search of answers. However, fate once again delivers him to the eccentric transfer student Ougi Oshino, who brings forth an unexpected proposal that may unearth the very foundation to which he is anchored. As Araragi peels back the layers of mystery surrounding an apparition, he discovers a truth not meant to be revealed.\n\n[Written by MAL Rewrite]"}, {"id": 54492, "name_en": "The Apothecary Diaries", "name_jp": "薬屋のひとりごと", "image_url": "https://cdn.myanimelist.net/images/anime/1708/138033.jpg", "genres": ["Drama", "Mystery"], "themes": ["Historical", "Medical"], "demographics": [], "studios": ["OLM", "TOHO animation STUDIO"], "year": 2023, "episodes": 24, "source": "Light novel", "score": 8.85, "synopsis": "Maomao, an apothecary's daughter, has been plucked from her peaceful life and sold to the lowest echelons of the imperial court. Now merely a maid, Maomao settles into her new mundane life and hides her extensive knowledge of medicine in order to avoid any unwanted attention.\n\nNot long after Maomao's arrival, the emperor's infant children inexplicably begin to experience grave symptoms—almost as if a curse has been cast. The curious Maomao easily solves the mystery and, to remain out of the limelight, attempts to leave an anonymous tip. Unfortunately, the dashing and perceptive eunuch Jinshi sees through it and manages to single her out.\n\nIn recognition of her talent, Maomao is promoted to lady-in-waiting for the emperor's favorite concubine, Gyokuyou. As Maomao continues to remedy the numerous ailments afflicting the imperial court, her pharmaceutical expertise quickly proves indispensable.\n\n[Written by MAL Rewrite]"}, {"id": 40682, "name_en": "Kingdom Season 3", "name_jp": "キングダム 第3シリーズ", "image_url": "https://cdn.myanimelist.net/images/anime/1443/111830.jpg", "genres": ["Action"], "themes": ["Historical", "Military"], "demographics": ["Seinen"], "studios": ["Studio Pierrot", "Studio Signpost"], "year": 2020, "episodes": 26, "source": "Manga", "score": 8.84, "synopsis": "Following the successful Sanyou campaign, the Qin army, including 1,000-Man Commander Xin, inches ever closer to fulfilling King Ying Zheng's dream of unifying China. With a major geographical foothold in the state of Wei now under its control, Qin sets its sights eastward toward the remaining warring states.\n\nMeanwhile Li Mu—an unparalleled strategist and the newly appointed prime minister of the state of Zhao—has taken advantage of Zhao's temporary truce with Qin to negotiate with the other states without interruption. Seemingly without warning, Ying Zheng receives news that armies from the states of Chu, Zhao, Wei, Han, Yan, and Qi have crossed into Qin territory. Realizing too late the purpose behind Li Mu's truce with Qin, Zheng quickly gathers his advisors to devise a plan to address the six-state coalition army on their doorstep. For the first time in history, the state of Qin faces complete destruction and must use every resource and strategy at their disposal to prevent themselves from being wiped off the map.\n\n[Written by MAL Rewrite]"}, {"id": 37987, "name_en": "Violet Evergarden: The Movie", "name_jp": "劇場版 ヴァイオレット・エヴァーガーデン", "image_url": "https://cdn.myanimelist.net/images/anime/1825/110716.jpg", "genres": ["Award Winning", "Drama"], "themes": [], "demographics": [], "studios": ["Kyoto Animation"], "year": 2020, "episodes": 1, "source": "Light novel", "score": 8.84, "synopsis": "Several years have passed since the end of The Great War. As the radio tower in Leidenschaftlich continues to be built, telephones will soon become more relevant, leading to a decline in demand for \"Auto Memory Dolls.\" Even so, Violet Evergarden continues to rise in fame after her constant success with writing letters. However, sometimes the one thing you long for is the one thing that does not appear.\n\nViolet Evergarden Movie follows Violet as she continues to comprehend the concept of emotion and the meaning of love. At the same time, she pursues a glimmer of hope that the man who once told her, \"I love you,\" may still be alive even after the many years that have passed.\n\n[Written by MAL Rewrite]"}, {"id": 59571, "name_en": "Attack on Titan: The Last Attack", "name_jp": "劇場版 進撃の巨人 完結編 THE LAST ATTACK", "image_url": "https://cdn.myanimelist.net/images/anime/1379/145452.jpg", "genres": ["Action", "Drama", "Suspense"], "themes": ["Gore", "Military", "Survival"], "demographics": ["Shounen"], "studios": ["MAPPA"], "year": 2024, "episodes": 1, "source": "Manga", "score": 8.83, "synopsis": "A compilation movie for Shingeki no Kyojin: The Final Season - Kanketsu-hen."}, {"id": 32281, "name_en": "Your Name.", "name_jp": "君の名は。", "image_url": "https://cdn.myanimelist.net/images/anime/5/87048.jpg", "genres": ["Award Winning", "Drama"], "themes": [], "demographics": [], "studios": ["CoMix Wave Films"], "year": 2016, "episodes": 1, "source": "Original", "score": 8.83, "synopsis": "Mitsuha Miyamizu, a high school girl, yearns to live the life of a boy in the bustling city of Tokyo—a dream that stands in stark contrast to her present life in the countryside. Meanwhile in the city, Taki Tachibana lives a busy life as a high school student while juggling his part-time job and hopes for a future in architecture.\n\nOne day, Mitsuha awakens in a room that is not her own and suddenly finds herself living the dream life in Tokyo—but in Taki's body! Elsewhere, Taki finds himself living Mitsuha's life in the humble countryside. In pursuit of an answer to this strange phenomenon, they begin to search for one another.\n\nKimi no Na wa. revolves around Mitsuha and Taki's actions, which begin to have a dramatic impact on each other's lives, weaving them into a fabric held together by fate and circumstance.\n\n[Written by MAL Rewrite]"}, {"id": 49387, "name_en": "Vinland Saga Season 2", "name_jp": "ヴィンランド・サガ SEASON2", "image_url": "https://cdn.myanimelist.net/images/anime/1170/124312.jpg", "genres": ["Action", "Adventure", "Drama"], "themes": ["Gore", "Historical"], "demographics": ["Seinen"], "studios": ["MAPPA"], "year": 2023, "episodes": 24, "source": "Manga", "score": 8.82, "synopsis": "After his father's death and the destruction of his village at the hands of English raiders, Einar wishes for a peaceful life with his family on their newly rebuilt farms. However, fate has other plans: his village is invaded once again. Einar watches helplessly as the marauding Danes burn his lands and slaughter his family. The invaders capture Einar and take him back to Denmark as a slave. \n\nEinar clings to his mother's final words to survive. He is purchased by Ketil, a kind slave owner and landlord who promises that Einar can regain his freedom in return for working in the fields. Soon, Einar encounters his new partner in farm cultivation—Thorfinn, a dejected and melancholic slave. As Einar and Thorfinn work together toward their freedom, they are haunted by both sins of the past and the ploys of the present. Yet they carry on, grasping for a glimmer of hope, redemption, and peace in a world that is nothing but unjust and unforgiving.\n\n[Written by MAL Rewrite]"}, {"id": 36838, "name_en": "Gintama. Silver Soul Arc", "name_jp": "銀魂. 銀ノ魂篇", "image_url": "https://cdn.myanimelist.net/images/anime/12/89603.jpg", "genres": ["Action", "Comedy", "Sci-Fi"], "themes": ["Gag Humor", "Historical", "Parody", "Samurai"], "demographics": ["Shounen"], "studios": ["Bandai Namco Pictures"], "year": 2018, "episodes": 12, "source": "Manga", "score": 8.81, "synopsis": "After the fierce battle on Rakuyou, the untold past and true goal of the immortal Naraku leader, Utsuro, are finally revealed. By corrupting the Altana reserves of several planets, Utsuro has successfully triggered the intervention of the Tendoshuu’s greatest enemy: the Altana Liberation Army. With Earth as the main battleground in this interplanetary war, Utsuro's master plan to destroy the planet—and himself—is nearly complete. \n\nAn attack on the O-Edo Central Terminal marks the beginning of the final battle to take back the land of the samurai. With the Yorozuya nowhere in sight, the bakufu all but collapsed, and the Shogun missing, the people are left completely helpless as the Liberation Army begins pillaging Edo in the name of freeing them from the Tendoshuu's rule. \n\nCaught in the crossfire between two equally imposing forces, can Gintoki, Kagura, Shinpachi, and the former students of Shouyou Yoshida put aside their differences and unite their allies to protect what they hold dear?\n\n[Written by MAL Rewrite]"}, {"id": 37510, "name_en": "Mob Psycho 100 II", "name_jp": "モブサイコ100 II", "image_url": "https://cdn.myanimelist.net/images/anime/1918/96303.jpg", "genres": ["Action", "Comedy", "Supernatural"], "themes": [], "demographics": [], "studios": ["Bones"], "year": 2019, "episodes": 13, "source": "Web manga", "score": 8.78, "synopsis": "Shigeo \"Mob\" Kageyama is now maturing and understanding his role as a supernatural psychic that has the power to drastically affect the livelihood of others. He and his mentor Reigen Arataka continue to deal with supernatural requests from clients, whether it be exorcizing evil spirits or tackling urban legends that haunt the citizens.\n\nWhile the workflow remains the same, Mob isn't just blindly following Reigen around anymore. With all his experiences as a ridiculously strong psychic, Mob's supernatural adventures now have more weight to them. Things take on a serious and darker tone as the dangers Mob and Reigen face are much more tangible and unsettling than ever before.\n\n[Written by MAL Rewrite]"}, {"id": 31758, "name_en": "Kizumonogatari Part 3: Cold-Blooded", "name_jp": "傷物語〈Ⅲ冷血篇〉", "image_url": "https://cdn.myanimelist.net/images/anime/1084/112813.jpg", "genres": ["Action", "Mystery", "Supernatural"], "themes": ["Vampire"], "demographics": [], "studios": ["Shaft"], "year": 2017, "episodes": 1, "source": "Light novel", "score": 8.78, "synopsis": "After helping revive the legendary vampire Kiss-shot Acerola-orion Heart-under-blade, Koyomi Araragi has become a vampire himself and her servant. Kiss-shot is certain she can turn him back into a human, but only once regaining her full power. \n\nAraragi has hunted down the three vampire hunters that defeated Kiss-shot and retrieved her limbs to return her to full strength. However, now that Araragi has almost accomplished what he’s been fighting for this whole time, he has to consider if this is what he really wants. Once he revives this powerful immortal vampire, there is no telling what she might do, and there would be no way of stopping her.\n\nBut there is more to the story that Araragi doesn’t understand. If a newborn vampire like him could defeat the hunters, how did they overpower Kiss-shot? Can he trust her to turn him back to a human? And how is that even possible in the first place?\n\nAraragi is at his limit but he must come to a decision, and it may not be possible to resolve this situation without doing something he’ll regret…\n\n[Written by MAL Rewrite]"}, {"id": 40028, "name_en": "Attack on Titan: Final Season", "name_jp": "進撃の巨人 The Final Season", "image_url": "https://cdn.myanimelist.net/images/anime/1000/110531.jpg", "genres": ["Action", "Drama", "Suspense"], "themes": ["Gore", "Military", "Survival"], "demographics": ["Shounen"], "studios": ["MAPPA"], "year": 2021, "episodes": 16, "source": "Manga", "score": 8.78, "synopsis": "Gabi Braun and Falco Grice have been training their entire lives to inherit one of the seven Titans under Marley's control and aid their nation in eradicating the Eldians on Paradis. However, just as all seems well for the two cadets, their peace is suddenly shaken by the arrival of Eren Yeager and the remaining members of the Survey Corps.\n\nHaving finally reached the Yeager family basement and learned about the dark history surrounding the Titans, the Survey Corps has at long last found the answer they so desperately fought to uncover. With the truth now in their hands, the group set out for the world beyond the walls.\n\nIn Shingeki no Kyojin: The Final Season, two utterly different worlds collide as each party pursues its own agenda in the long-awaited conclusion to Paradis' fight for freedom.\n\n[Written by MAL Rewrite]"}, {"id": 37521, "name_en": "Vinland Saga", "name_jp": "ヴィンランド・サガ", "image_url": "https://cdn.myanimelist.net/images/anime/1500/103005.jpg", "genres": ["Action", "Adventure", "Drama"], "themes": ["Gore", "Historical"], "demographics": ["Seinen"], "studios": ["Wit Studio"], "year": 2019, "episodes": 24, "source": "Manga", "score": 8.78, "synopsis": "Young Thorfinn grew up listening to the stories of old sailors that had traveled the ocean and reached the place of legend, Vinland. It's said to be warm and fertile, a place where there would be no need for fighting—not at all like the frozen village in Iceland where he was born, and certainly not like his current life as a mercenary. War is his home now. Though his father once told him, \"You have no enemies, nobody does. There is nobody who it's okay to hurt,\" as he grew, Thorfinn knew that nothing was further from the truth.\n\nThe war between England and the Danes grows worse with each passing year. Death has become commonplace, and the viking mercenaries are loving every moment of it. Allying with either side will cause a massive swing in the balance of power, and the vikings are happy to make names for themselves and take any spoils they earn along the way. Among the chaos, Thorfinn must take his revenge and kill Askeladd, the man who murdered his father. The only paradise for the vikings, it seems, is the era of war and death that rages on.\n\n[Written by MAL Rewrite]"}, {"id": 263, "name_en": "Fighting Spirit", "name_jp": "はじめの一歩 THE FIGHTING!", "image_url": "https://cdn.myanimelist.net/images/anime/4/86334.jpg", "genres": ["Sports"], "themes": ["Combat Sports"], "demographics": ["Shounen"], "studios": ["Madhouse"], "year": 2000, "episodes": 75, "source": "Manga", "score": 8.78, "synopsis": "In his father's absence, teenager Ippo Makunouchi works hard to help his mother run her fishing boat rental business. Ippo's timid nature, his lack of sleep, and the sea smell make him an easy target for relentless bullies who leave him bruised and beaten on a daily basis. Mamoru Takamura, an up-and-coming boxer, rescues Ippo from a violent after-school incident and takes him back to the Kamogawa Boxing Gym for recovery. Takamura and his fellow boxers, Masaru Aoki and Tatsuya Kimura, are stunned by Ippo's powerful punches—a result of strong muscles developed through years serving his physically taxing family business. \n\nFollowing brief training under Takamura, Ippo impresses the other boxers in a practice match against prodigy Ichirou Miyata. He gains a rival in Miyata and a coach in Genji Kamogawa, the gym owner and a former boxer himself. As Ippo takes the first steps in his official boxing career, he faces off against a series of challenging opponents, each more powerful than the last. Victory, loss, and a cycle of dedicated training await Ippo on his journey to achieve greatness. With his tough body and unstoppable fighting spirit, the kind young man seeks to take on the world.\n\n[Written by MAL Rewrite]"}, {"id": 60489, "name_en": "Takopi's Original Sin", "name_jp": "タコピーの原罪", "image_url": "https://cdn.myanimelist.net/images/anime/1182/149879.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["Psychological", "Time Travel"], "demographics": ["Shounen"], "studios": ["Enishiya"], "year": 2025, "episodes": 6, "source": "Manga", "score": 8.77, "synopsis": "A squid-like creature, known as a Happian, leaves his home planet with the desire to spread happiness across the universe. He lands on Earth, but quickly finds himself in danger of captivity by its inhabitants. Fortunately, he is found by an unsmiling little girl named Shizuka Kuze, who feeds him and names him Takopii. Feeling indebted, Takopii decides to do everything in his power to bring a smile to her face.\n\nThe task is easier said than done, however. Shizuka is bullied by her classmates, she does not have a father, and her mother is never home—though the gravity of these issues flies over the naive Takopii's head. Even so, Shizuka does have one source of happiness: her dog Chappy. The connection Shizuka and Chappy share only increases Takopii's desire to make the girl smile.\n\nWhile Takopii's attempts to lift Shizuka's spirits lead to unintended consequences, he is determined to take things into his own tentacles, test his understanding of human beings, and achieve his goal of spreading happiness.\n\n[Written by MAL Rewrite]"}, {"id": 32935, "name_en": "Haikyu!! 3rd Season", "name_jp": "ハイキュー!! 烏野高校 VS 白鳥沢学園高校", "image_url": "https://cdn.myanimelist.net/images/anime/7/81992.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2016, "episodes": 10, "source": "Manga", "score": 8.77, "synopsis": "After the victory against Aoba Jousai High, Karasuno High School, once called “a fallen powerhouse, a crow that can’t fly,” has finally reached the climax of the heated Spring tournament. Now, to advance to nationals, the Karasuno team has to defeat the powerhouse Shiratorizawa Academy. Karasuno’s greatest hurdle is their adversary’s ace, Wakatoshi Ushijima, the number one player in the Miyagi Prefecture, and one of the country’s top three aces.\n\nOnly the strongest team will make it to the national tournament. Since this match is the third-year players’ last chance to qualify for nationals, Karasuno has to use everything they learned during the training camp and prior matches to attain victory. Filled with restlessness and excitement, both teams are determined to come out on top in the third season of Haikyuu!!.\n\n[Written by MAL Rewrite]"}, {"id": 199, "name_en": "Spirited Away", "name_jp": "千と千尋の神隠し", "image_url": "https://cdn.myanimelist.net/images/anime/6/79597.jpg", "genres": ["Adventure", "Award Winning", "Fantasy"], "themes": ["Mythology"], "demographics": [], "studios": ["Studio Ghibli"], "year": 2001, "episodes": 1, "source": "Original", "score": 8.77, "synopsis": "Stubborn, spoiled, and naïve, 10-year-old Chihiro Ogino is less than pleased when she and her parents discover an abandoned amusement park on the way to their new house. Cautiously venturing inside, she realizes that there is more to this place than meets the eye, as strange things begin to happen once dusk falls. Ghostly apparitions and food that turns her parents into pigs are just the start—Chihiro has unwittingly crossed over into the spirit world. Now trapped, she must summon the courage to live and work amongst spirits, with the help of the enigmatic Haku and the cast of unique characters she meets along the way.\n\nVivid and intriguing, Sen to Chihiro no Kamikakushi tells the story of Chihiro's journey through an unfamiliar world as she strives to save her parents and return home.\n\n[Written by MAL Rewrite]"}, {"id": 48583, "name_en": "Attack on Titan: Final Season Part 2", "name_jp": "進撃の巨人 The Final Season Part 2", "image_url": "https://cdn.myanimelist.net/images/anime/1948/120625.jpg", "genres": ["Action", "Drama", "Suspense"], "themes": ["Gore", "Military", "Survival"], "demographics": ["Shounen"], "studios": ["MAPPA"], "year": 2022, "episodes": 12, "source": "Manga", "score": 8.77, "synopsis": "Turning against his former allies and enemies alike, Eren Yeager sets a disastrous plan in motion. Under the guidance of the Beast Titan, Zeke, Eren takes extreme measures to end the ancient conflict between Marley and Eldia—but his true intentions remain a mystery. Delving deep into his family's past, Eren fights to control his own destiny.\n\nMeanwhile, the long-feuding nations of Marley and Eldia utilize both soldiers and Titans in a brutal race to eliminate the other. Reiner Braun uses his own powers in a desperate bid to hold off Eren's own militaristic force, and his fellow Eldians—children Falco Grice and Gabi Braun—struggle to survive in the unfolding chaos.\n\nElsewhere, Eren's childhood friends Mikasa Ackerman and Armin Arlert remain imprisoned alongside Eren's former Survey Corps companions, all disturbed by Eren's monstrous transformation. Under the blind belief that Eren still secretly harbors good intentions, Mikasa and the others enter the fray in an attempt to save their friend's very soul.\n\n[Written by MAL Rewrite]"}, {"id": 17074, "name_en": "Monogatari Series: Second Season", "name_jp": "〈物語〉シリーズ セカンドシーズン", "image_url": "https://cdn.myanimelist.net/images/anime/1807/121534.jpg", "genres": ["Comedy", "Mystery", "Romance", "Supernatural"], "themes": ["Vampire"], "demographics": [], "studios": ["Shaft"], "year": 2013, "episodes": 26, "source": "Light novel", "score": 8.76, "synopsis": "Apparitions, oddities, and gods continue to manifest around Koyomi Araragi and his close-knit group of friends: Tsubasa Hanekawa, the group's modest genius; Shinobu Oshino, the resident doughnut-loving vampire; athletic deviant Suruga Kanbaru; bite-happy spirit Mayoi Hachikuji; Koyomi's cute admirer Nadeko Sengoku; and Hitagi Senjougahara, Koyomi's eclectic girlfriend.\n\nA new semester has begun and with graduation looming over Koyomi, he must quickly decide the paths he will walk, as well as the relationships he will form and friends that he will save. But as strange events begin to unfold, Koyomi is nowhere to be found, and a vicious tiger apparition has appeared in his absence. Hanekawa has become its target, and she quickly finds she must fend for herself.\n\n[Written by MAL Rewrite]"}, {"id": 60098, "name_en": "My Hero Academia Final Season", "name_jp": "僕のヒーローアカデミア FINAL SEASON", "image_url": "https://cdn.myanimelist.net/images/anime/1959/151055.jpg", "genres": ["Action"], "themes": ["School", "Super Power"], "demographics": ["Shounen"], "studios": ["Bones Film"], "year": 2025, "episodes": 11, "source": "Manga", "score": 8.75, "synopsis": "Final season of Boku no Hero Academia."}, {"id": 47917, "name_en": "Bocchi the Rock!", "name_jp": "ぼっち・ざ・ろっく！", "image_url": "https://cdn.myanimelist.net/images/anime/1448/127956.jpg", "genres": ["Comedy"], "themes": ["CGDCT", "Music"], "demographics": [], "studios": ["CloverWorks"], "year": 2022, "episodes": 12, "source": "4-koma manga", "score": 8.74, "synopsis": "Yearning to make friends and perform live with a band, lonely and socially anxious Hitori \"Bocchi\" Gotou devotes her time to playing the guitar. On a fateful day, Bocchi meets the outgoing drummer Nijika Ijichi, who invites her to join Kessoku Band when their guitarist, Ikuyo Kita, flees before their first show. Soon after, Bocchi meets her final bandmate—the cool bassist Ryou Yamada. \n\nAlthough their first performance together is subpar, the girls feel empowered by their shared love for music, and they are soon rejoined by Kita. Finding happiness in performing, Bocchi and her bandmates put their hearts into improving as musicians while making the most of their fleeting high school days.\n\n[Written by MAL Rewrite]"}, {"id": 61517, "name_en": "Kingdom Season 6", "name_jp": "キングダム 第6シリーズ", "image_url": "https://cdn.myanimelist.net/images/anime/1282/151476.jpg", "genres": ["Action"], "themes": ["Historical", "Military"], "demographics": ["Seinen"], "studios": ["Studio Pierrot", "Studio Signpost"], "year": 2025, "episodes": 13, "source": "Manga", "score": 8.73, "synopsis": "In ancient China, during the Warring States period, a former servant named Xin rises through the ranks with a dream: to become the greatest general under the heavens. Fighting alongside King Ying Zheng of Qin—who seeks to unify China and end the chaos—Xin leads his own unit, the Fei Xin Force, onto the battlefield.\n\nWith power reclaimed from the influential Chancellor Buwei Lü, King Ying Zheng begins laying the groundwork for a new, unified legal state with the support of key allies like strategist Changping Jun and legalist Li Si.\n\nAs Qin launches a campaign to seize new territory from the state of Zhao, their advance is halted by the brilliant strategist, Li Mu. In response, Changping Jun devises a bold plan: to bypass Zhao's defenses and strike at the key city of Gyou, near Zhao's capital.\n\nTo carry out this risky maneuver, a powerful coalition army is formed—led by the tacticians Ou Sen (Wang Jian), Kan Ki (Huan Yi), and the warrior queen Yo Tan Wa (Yang Duanhe). Joining them are the next generation of Qin commanders: Xin's Fei Xin Force, Mou Ten (Meng Tian)'s Gaku Ka Unit, and Ou Hon (Wang Ben)'s Gyoku Hou Unit, each operating independently.\n\nThe fierce battle for Gyou begins, as the future of Qin's unification hangs in the balance.\n\n(Source: MAL News)"}, {"id": 53223, "name_en": "Kingdom Season 5", "name_jp": "キングダム 第5シリーズ", "image_url": "https://cdn.myanimelist.net/images/anime/1050/139641.jpg", "genres": ["Action"], "themes": ["Historical", "Military"], "demographics": ["Seinen"], "studios": ["Studio Pierrot", "Studio Signpost"], "year": 2024, "episodes": 13, "source": "Manga", "score": 8.73, "synopsis": "Fifth season of Kingdom."}, {"id": 50160, "name_en": "Kingdom Season 4", "name_jp": "キングダム 第4シリーズ", "image_url": "https://cdn.myanimelist.net/images/anime/1566/122794.jpg", "genres": ["Action"], "themes": ["Historical", "Military"], "demographics": ["Seinen"], "studios": ["Studio Pierrot", "Studio Signpost"], "year": 2022, "episodes": 26, "source": "Manga", "score": 8.73, "synopsis": "Following the conclusion of the large-scale coalition campaign, the entirety of China is in a state of economic recovery. The victor of the battle, the state of Qin, is no different. There, the political parties led by Ying Zheng and Buwei Lü continue their inner conflict. Having played the role of king in the coalition battle, Zheng has the trust of the people—but Lü is far from out of the fight. In 18 months, he plans to interrupt Zheng's coming-of-age ceremony.\n\nMeanwhile, a Zhao army numbering 20,000 troops has set out toward Qin. In the royal court, due to the lack of generals capable of responding to the incoming threat, Lü slyly suggests that Zheng take command. However, Cheng Jiao, Zheng's half-brother, volunteers instead. As they grew to trust each other during the coalition battle, Zheng now accepts Jiao as his replacement.\n\nHowever, the Zhao forces retreat a mere half-day after clashing with Jiao's army. With trouble quickly brewing in the shadows, the internal struggle of Qin is only complicated further. There are only two men Zheng feels he can rely on: Bi, a general who commands 30,000 men; and Xin, the leader of the Fei Xin force.\n\n[Written by MAL Rewrite]"}, {"id": 52198, "name_en": "Kaguya-sama: Love is War -The First Kiss That Never Ends-", "name_jp": "かぐや様は告らせたい -ファーストキッスは終わらない-", "image_url": "https://cdn.myanimelist.net/images/anime/1670/130060.jpg", "genres": ["Comedy", "Drama", "Romance"], "themes": ["School"], "demographics": ["Seinen"], "studios": ["A-1 Pictures"], "year": 2022, "episodes": 1, "source": "Manga", "score": 8.72, "synopsis": "After their first kiss, Kaguya Shinomiya and Miyuki Shirogane are left unsure where their relationship stands. The troubling uncertainty of whether they could be considered an official couple unleashes newfound problems as both Kaguya and Shirogane struggle to sort out their feelings.\n\nWhile the lovestruck student council officers fret, the Christmas season rolls around, and romance is in the air. In the face of widespread tenderness, Kaguya and Shirogane must endure their affectionate battle of wits once more. Should they reconcile their feelings for one another, they may find themselves within reach of what they have both been longing for so long: true love.\n\n[Written by MAL Rewrite]"}, {"id": 39894, "name_en": "Sound! Euphonium 3", "name_jp": "響け！ユーフォニアム3", "image_url": "https://cdn.myanimelist.net/images/anime/1216/142086.jpg", "genres": ["Drama"], "themes": ["Music", "Performing Arts", "School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2024, "episodes": 13, "source": "Novel", "score": 8.72, "synopsis": "With the ensemble contest behind them, the members of the Kitauji High School concert band now aim to win a gold medal at the national competition. For third-year club president Kumiko Oumae and her friends, it is their last chance before graduation. Filled with anticipation and anxiety, Kumiko worries if she can successfully lead the band.\n\nAmid her struggles, Kumiko encounters transfer student Mayu Kuroe. From her gentle demeanor to her talented euphonium skills, Mayu was a member of the Seira Girls' School concert band—a regular at nationals. While Mayu is friendly, Kumiko cannot help but have an indescribable feeling from their meeting.\n\nAs new members arrive and plans are set in motion, the club, with an insatiable hunger for a gold medal, learns what it truly takes to propel themselves to reach the national level.\n\n[Written by MAL Rewrite]"}, {"id": 24701, "name_en": "Mushi-shi: Next Passage Part 2", "name_jp": "蟲師 続章", "image_url": "https://cdn.myanimelist.net/images/anime/9/68095.jpg", "genres": ["Adventure", "Mystery", "Slice of Life", "Supernatural"], "themes": ["Adult Cast", "Historical", "Iyashikei"], "demographics": ["Seinen"], "studios": ["Artland"], "year": 2014, "episodes": 10, "source": "Manga", "score": 8.72, "synopsis": "Ghostly, primordial beings known as Mushi continue to cause mysterious changes in the lives of humans. The travelling Mushishi, Ginko, persists in trying to set right the strange and unsettling situations he encounters. Time loops, living shadows, and telepathy are among the overt effects of interference from Mushi, but more subtle symptoms that take years to be noticed also rouse Ginko's concern as he passes from village to village.\n\nThrough circumstance, Ginko has become an arbiter, determining which Mushi are blessings and which are curses. But the lines that he seeks to draw are subjective. Some of his patients would rather exercise their new powers until they are utterly consumed by them; others desperately strive to rid themselves of afflictions which are in fact protecting their lives from devastation. Those who cross paths with Mushi must learn to accept seemingly impossible consequences for their actions, and heal wounds they did not know they had. Otherwise, they risk meeting with fates beyond their comprehension.\n\n[Written by MAL Rewrite]"}, {"id": 48569, "name_en": "86 Eighty-Six Part 2", "name_jp": "86―エイティシックス―", "image_url": "https://cdn.myanimelist.net/images/anime/1321/117508.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["Mecha", "Military"], "demographics": [], "studios": ["A-1 Pictures"], "year": 2021, "episodes": 12, "source": "Light novel", "score": 8.72, "synopsis": "The disappearance of the Spearhead Squadron beyond the horizon does little to hide the intensity of the Republic of San Magnolia's endless propaganda. Vladilena Milizé continues to operate as \"Handler One,\" the commander of yet another dehumanized 86th faction's squadron in the continuous war against the Legion.\n\nOn the Western Front, Shinei Nouzen and his squad are quarantined in a military base controlled by the Federal Republic of Giad, formerly known as the Giadian Empire. The newly-established government grants the saved Eighty-Six full citizenship and freedom. Housed by the president Ernst Zimmerman himself, the group meets his adoptive daughter and the last Empress, Augusta Frederica Adel-Adler.\n\nHowever, within the calm of this tender society, Shinei and his team feel that their purpose is on the battlefield. Before long, they are once again in the midst of the Legion's onslaught as a part of the Federacy's Nordlicht Squadron, accompanied by Augusta Frederica. But, as history repeats itself, they realize that no matter the side, death and pain on the front lines are the only comfort they know.\n\n[Written by MAL Rewrite]"}, {"id": 51009, "name_en": "Jujutsu Kaisen Season 2", "name_jp": "呪術廻戦 懐玉・玉折／渋谷事変", "image_url": "https://cdn.myanimelist.net/images/anime/1792/138022.jpg", "genres": ["Action", "Supernatural"], "themes": ["Gore", "School"], "demographics": ["Shounen"], "studios": ["MAPPA"], "year": 2023, "episodes": 23, "source": "Manga", "score": 8.72, "synopsis": "The year is 2006, and the halls of Tokyo Prefectural Jujutsu High School echo with the endless bickering and intense debate between two inseparable best friends. Exuding unshakeable confidence, Satoru Gojou and Suguru Getou believe there is no challenge too great for young and powerful Special Grade sorcerers such as themselves. They are tasked with safely delivering a sensible girl named Riko Amanai to the entity whose existence is the very essence of the jujutsu world. However, the mission plunges them into an exhausting swirl of moral conflict that threatens to destroy the already feeble amity between sorcerers and ordinary humans.\n\nTwelve years later, students and sorcerers are the frontline defense against the rising number of high-level curses born from humans' negative emotions. As the entities grow in power, their self-awareness and ambition increase too. The curses unite for the common goal of eradicating humans and creating a world of only cursed energy users, led by a dangerous, ancient cursed spirit. To dispose of their greatest obstacle—the strongest sorcerer, Gojou—they orchestrate an attack at Shibuya Station on Halloween. Dividing into teams, the sorcerers enter the fight prepared to risk everything to protect the innocent and their own kind.\n\n[Written by MAL Rewrite]"}, {"id": 55690, "name_en": "The Dangers in My Heart Season 2", "name_jp": "僕の心のヤバイやつ 第2期", "image_url": "https://cdn.myanimelist.net/images/anime/1643/138581.jpg", "genres": ["Comedy", "Romance"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["Shin-Ei Animation"], "year": 2024, "episodes": 13, "source": "Manga", "score": 8.71, "synopsis": "After an eventful winter break, Kyoutarou Ichikawa and Anna Yamada reunite with a stronger bond. They continue to grow in their own ways, with Yamada taking on more challenging photoshoots and Ichikawa maturing both physically and emotionally as he tackles his affections for Yamada. However, spending time together outside of school allows for their relationship to deepen, and it becomes increasingly difficult to deny their budding romantic feelings.\n\nGrappling with these unexpected and new emotions, Ichikawa and Yamada realize that, with the passage of time, their relationship is bound to change—and they must ultimately decide whether they wish to remain close friends or finally become a couple.\n\n[Written by MAL Rewrite]"}, {"id": 55016, "name_en": "Idol", "name_jp": "アイドル", "image_url": "https://cdn.myanimelist.net/images/anime/1921/135489.jpg", "genres": [], "themes": ["Music"], "demographics": [], "studios": ["Doga Kobo"], "year": 2023, "episodes": 1, "source": "Original", "score": 8.71, "synopsis": "Music video for the song Idol by YOASOBI."}, {"id": 52215, "name_en": "Orb: On the Movements of the Earth", "name_jp": "チ。―地球の運動について―", "image_url": "https://cdn.myanimelist.net/images/anime/1749/145922.jpg", "genres": ["Drama"], "themes": ["Historical"], "demographics": ["Seinen"], "studios": ["Madhouse"], "year": 2024, "episodes": 25, "source": "Manga", "score": 8.71, "synopsis": "Twelve-year-old prodigy Rafal believes in living rationally, so as to earn praise and respect from society while not being led astray by his emotions. To this end, he publicly states his intention to study theology—the academic field held in highest regard in early 15th century Poland. However, an encounter with a mysterious man upends Rafal's life, sparking an illogical desire to instead pursue his passion for astronomy.\n\nRafal is determined to prove the beauty and rationality of heliocentrism—the theory that the Earth revolves around the Sun. This belief is considered heretical by the powerful Church, which promotes geocentrism—the Sun revolving around the Earth—as the sole truth of the universe. Those whose beliefs do not align with the will of the Church suffer unfathomably gruesome consequences.\n\nIn pursuit of evidence for a heliocentric model of the universe, Rafal grapples with obtaining precise calculations and building empirical theories. His greatest challenge, however, lies in conducting this research discreetly—lest he wish to meet the same fate as other heretics.\n\n[Written by MAL Rewrite]"}, {"id": 1575, "name_en": "Code Geass: Lelouch of the Rebellion", "name_jp": "コードギアス 反逆のルルーシュ", "image_url": "https://cdn.myanimelist.net/images/anime/1032/135088.jpg", "genres": ["Award Winning", "Drama", "Sci-Fi"], "themes": ["Mecha", "Military", "School", "Super Power"], "demographics": [], "studios": ["Sunrise"], "year": 2006, "episodes": 25, "source": "Original", "score": 8.71, "synopsis": "In the year 2010, the Holy Empire of Britannia is establishing itself as a dominant military nation, starting with the conquest of Japan. Renamed to Area 11 after its swift defeat, Japan has seen significant resistance against these tyrants in an attempt to regain independence.\n\nLelouch Lamperouge, a Britannian student, unfortunately finds himself caught in a crossfire between the Britannian and the Area 11 rebel armed forces. He is able to escape, however, thanks to the timely appearance of a mysterious girl named C.C., who bestows upon him Geass, the \"Power of Kings.\" Realizing the vast potential of his newfound \"power of absolute obedience,\" Lelouch embarks upon a perilous journey as the masked vigilante known as Zero, leading a merciless onslaught against Britannia in order to get revenge once and for all.\n\n[Written by MAL Rewrite]"}, {"id": 45649, "name_en": "The First Slam Dunk", "name_jp": "THE FIRST SLAM DUNK", "image_url": "https://cdn.myanimelist.net/images/anime/1745/129284.jpg", "genres": ["Award Winning", "Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Toei Animation", "DandeLion Animation Studio"], "year": 2022, "episodes": 1, "source": "Manga", "score": 8.71, "synopsis": "Shohoku's \"speedster\" and point guard, Ryouta Miyagi, always plays with brains and lightning speed, running circles around his opponents while feigning composure. Born and raised in Okinawa, Ryouta had a brother who was three years older. Following in the footsteps of his older brother, who was a famous local player from a young age, Ryouta also became addicted to basketball.\n\nIn his second year of high school, Ryouta plays with the Shohoku High School basketball team along with Sakuragi, Rukawa, Akagi, and Mitsui as they take the stage at the Inter-High School National Championship. And now, they are on the brink of challenging the reigning champions, Sannoh Kogyo High School.\n\n(Source: GKIDS, edited)"}, {"id": 50172, "name_en": "Mob Psycho 100 III", "name_jp": "モブサイコ100 III", "image_url": "https://cdn.myanimelist.net/images/anime/1228/125011.jpg", "genres": ["Action", "Comedy", "Supernatural"], "themes": [], "demographics": [], "studios": ["Bones"], "year": 2022, "episodes": 12, "source": "Web manga", "score": 8.71, "synopsis": "After foiling a world-threatening plot, Shigeo \"Mob\" Kageyama returns to tackle the more exhausting aspects of his mundane life—starting with filling out his school's nerve-racking career form. Meanwhile, he continues to assist his mentor Arataka Reigen and the office's new recruit, Katsuya Serizawa, in solving paranormal cases of their clients. While continuing his duties, Mob also works on gaining more independence in his esper and human lives, as well as trying to integrate better with the people around him.\n\nHowever, new supernatural and ordinary challenges test Mob’s emotional stability and force him to confront the realities around him. As he strives to continue forward on the path to maturity, Mob must resolve his emotional crises and reassess the naivety he has held on for so long.\n\n[Written by MAL Rewrite]"}, {"id": 44074, "name_en": "Link Click", "name_jp": "时光代理人", "image_url": "https://cdn.myanimelist.net/images/anime/1135/114867.jpg", "genres": ["Drama", "Mystery", "Suspense"], "themes": ["Adult Cast", "Super Power", "Time Travel"], "demographics": [], "studios": ["LAN Studio"], "year": 2021, "episodes": 11, "source": "Original", "score": 8.71, "synopsis": "It is said that a picture is worth a thousand words. In this case, it holds an infinite amount of secrets. These are secrets that only Cheng Xiaoshi and Lu Guang are able to find. In a small shop called \"Time Photo Studio,\" the two friends provide a special service: using their extraordinary powers that let them enter photographs, they jump into pictures brought to them by clients in order to grant their wishes. Through the eyes of the photographer, they live through the events surrounding the picture and try to decipher how to solve their client's request.\n\nBut every time they jump into a picture, they take a great risk. One wrong move and they could alter the future of the person who took the picture... and possibly countless other events too. So when the events they are forced to live through in these pictures start to become personal, it will take the utmost strength to push their feelings aside and focus on accomplishing the task they were paid to do.\n\n[Written by MAL Rewrite]"}, {"id": 53447, "name_en": "To Be Hero X", "name_jp": "凸变英雄X", "image_url": "https://cdn.myanimelist.net/images/anime/1492/150628.jpg", "genres": ["Action"], "themes": ["Super Power"], "demographics": [], "studios": ["Pb Animation", "LAN Studio", "Paper Plane Animation Studio", "B.COOL STUDIO"], "year": 2025, "episodes": 24, "source": "Original", "score": 8.7, "synopsis": "This is a world where heroes are created by people's trust, and the hero who has received the most trust is known as \"X.\" In this world, people's trust can be calculated by data, and these values will be reflected on everyone's wrist. As long as enough trust points are obtained, ordinary people can also have superpowers and become superheroes that save the world. However, the ever-changing trust value makes the hero's path full of unknowns...\n\n(Source: Bilibili, translated)"}, {"id": 53998, "name_en": "Bleach: Thousand-Year Blood War - The Separation", "name_jp": "BLEACH 千年血戦篇-訣別譚-", "image_url": "https://cdn.myanimelist.net/images/anime/1164/138058.jpg", "genres": ["Action", "Adventure", "Supernatural"], "themes": [], "demographics": ["Shounen"], "studios": ["Studio Pierrot"], "year": 2023, "episodes": 13, "source": "Manga", "score": 8.7, "synopsis": "After a brutal surprise attack by the forces of Quincy King Yhwach, the resident Reapers of the Soul Society lick their wounds and mourn their losses. Many of the surviving Soul Reaper captains train to battle without their Bankai, the ultimate technique wielded by the fiercest warriors.\n\nIn the previous assault, Ichigo Kurosaki narrowly managed to help fend off Yhwach's fearsome wrath. However, to ultimately defeat his godly adversary and save his allies, Ichigo must now undergo severe training that will push him beyond his physical, emotional, and mental limits.\n\nThough Yhwach already holds the upper hand in this ongoing blood feud, he also successfully recruits Uryuu Ishida, Ichigo's close friend and rival, to be his successor. Yhwach strikes out once again at the weakened Soul Society, intent on finally obliterating his long-standing enemies. As Ichigo struggles to attain new power, the Soul Reaper captains fight for survival and borrowed time.\n\n[Written by MAL Rewrite]"}, {"id": 33095, "name_en": "Descending Stories: Showa Genroku Rakugo Shinju", "name_jp": "昭和元禄落語心中～助六再び篇～", "image_url": "https://cdn.myanimelist.net/images/anime/1493/124765.jpg", "genres": ["Drama", "Romance"], "themes": ["Adult Cast", "Historical", "Love Polygon", "Performing Arts"], "demographics": ["Josei"], "studios": ["Studio Deen"], "year": 2017, "episodes": 12, "source": "Manga", "score": 8.7, "synopsis": "Even after having risen to the utmost rank of shin'uchi, Yotarou struggles to find his own identity in the world of rakugo. Caught between his master's teachings and the late Sukeroku's unique style, his performance lacks an important ingredient—ego. And while his popularity packs the theaters, he is but one of the few; rakugo is under threat of being eclipsed.\n\nMeanwhile Yakumo, regarded by many as the last bastion of preserving the popularity of rakugo, struggles to cope with his elderly state. Even though his performances are still stellar, he fears that he is nearing his limits. His doubts grow stronger as an old friend creeps ever closer. Konatsu, for her part, attempts to raise her son as a single mother, which Yotarou is heavily opposed to. Instead, he seeks to persuade her to marry him and in turn raise her son as his own.\n\nIn Shouwa Genroku Rakugo Shinjuu: Sukeroku Futatabi-hen, the curtains fall on Yotarou and Yakumo's story, tasked with restoring the near-obsolete art form as well as overcoming their internal conflicts.\n\n[Written by MAL Rewrite]"}, {"id": 47778, "name_en": "Demon Slayer: Kimetsu no Yaiba Entertainment District Arc", "name_jp": "鬼滅の刃 遊郭編", "image_url": "https://cdn.myanimelist.net/images/anime/1908/120036.jpg", "genres": ["Action", "Supernatural"], "themes": ["Historical"], "demographics": ["Shounen"], "studios": ["ufotable"], "year": 2022, "episodes": 11, "source": "Manga", "score": 8.7, "synopsis": "The devastation of the Mugen Train incident still weighs heavily on the members of the Demon Slayer Corps. Despite being given time to recover, life must go on, as the wicked never sleep: a vicious demon is terrorizing the alluring women of the Yoshiwara Entertainment District. The Sound Hashira, Tengen Uzui, and his three wives are on the case. However, when he soon loses contact with his spouses, Tengen fears the worst and enlists the help of Tanjirou Kamado, Zenitsu Agatsuma, and Inosuke Hashibira to infiltrate the district's most prominent houses and locate the depraved Upper Rank Demon.\n\n[Written by MAL Rewrite]"}, {"id": 33352, "name_en": "Violet Evergarden", "name_jp": "ヴァイオレット・エヴァーガーデン", "image_url": "https://cdn.myanimelist.net/images/anime/1795/95088.jpg", "genres": ["Drama"], "themes": [], "demographics": [], "studios": ["Kyoto Animation"], "year": 2018, "episodes": 13, "source": "Light novel", "score": 8.69, "synopsis": "The Great War finally came to an end after four long years of conflict; fractured in two, the continent of Telesis slowly began to flourish once again. Caught up in the bloodshed was Violet Evergarden, a young girl raised for the sole purpose of decimating enemy lines. Hospitalized and maimed in a bloody skirmish during the War's final leg, she was left with only words from the person she held dearest, but with no understanding of their meaning.\n\nRecovering from her wounds, Violet starts a new life working at CH Postal Services after a falling out with her new intended guardian family. There, she witnesses by pure chance the work of an \"Auto Memory Doll,\" amanuenses that transcribe people's thoughts and feelings into words on paper. Moved by the notion, Violet begins work as an Auto Memory Doll, a trade that will take her on an adventure, one that will reshape the lives of her clients and hopefully lead to self-discovery.\n\n[Written by MAL Rewrite]"}, {"id": 61930, "name_en": "Umamusume: Cinderella Gray Part 2", "name_jp": "ウマ娘 シンデレラグレイ 第2クール", "image_url": "https://cdn.myanimelist.net/images/anime/1120/152280.jpg", "genres": ["Drama", "Sports"], "themes": ["Anthropomorphic", "Racing"], "demographics": ["Seinen"], "studios": ["CygamesPictures"], "year": 2025, "episodes": 10, "source": "Manga", "score": 8.68, "synopsis": "Ever since horse girl Oguri Cap left the small town of Kasamatsu to chase greater heights in Tokyo, she has rapidly gained attention with her remarkable results. Her goal to become the best horse girl in Japan seems within reach, but there is a rival in her way—another running prodigy named Tamamo Cross.\n\nHowever, Tamamo is not the only threat to Oguri: several world-class racers from overseas are joining the Japan Cup, pushing the renowned race's competition to unprecedented levels. With the support from the people close to her and her intense desire to win, Oguri will do anything to conquer the Japan Cup and continue sprinting for the top.\n\n[Written by MAL Rewrite]"}, {"id": 59192, "name_en": "Demon Slayer: Kimetsu no Yaiba - The Movie: Infinity Castle - Part 1: Akaza Returns", "name_jp": "劇場版 鬼滅の刃 無限城編 第一章 猗窩座再来", "image_url": "https://cdn.myanimelist.net/images/anime/1681/148216.jpg", "genres": ["Action", "Supernatural"], "themes": ["Historical"], "demographics": ["Shounen"], "studios": ["ufotable"], "year": 2025, "episodes": 1, "source": "Manga", "score": 8.68, "synopsis": "First anime movie of the trilogy adaptation of the Infinity Castle Arc."}, {"id": 21939, "name_en": "Mushi-shi: Next Passage Part 1", "name_jp": "蟲師 続章", "image_url": "https://cdn.myanimelist.net/images/anime/13/58533.jpg", "genres": ["Adventure", "Mystery", "Slice of Life", "Supernatural"], "themes": ["Adult Cast", "Historical", "Iyashikei"], "demographics": ["Seinen"], "studios": ["Artland"], "year": 2014, "episodes": 10, "source": "Manga", "score": 8.68, "synopsis": "Perceived as strange and feared by man, over time the misshapen ones came to be known as Mushi. Although they harbor no ill intentions towards humans, many suffer from the side effects of their existence and strange nature; exploiting the Mushi without understanding them, even unintentionally, can lead to disaster and strife for any involved. Mushishi Zoku Shou continues the story of Mushishi Ginko on his journey to help the visible world to coexist with the Mushi.\n\nDuring his travels, Ginko discovers various gifted individuals—those cursed by circumstance and those maintaining a fragile symbiosis with the Mushi—inevitably confronting the question of whether humanity, talented and tortured alike, can manage the responsibility of the unseen. Moreover, as a Mushishi, Ginko must learn more about these strange beings and decide if he has the right to interfere with the complex relationships between Mushi and mankind.\n\n[Written by MAL Rewrite]"}, {"id": 40434, "name_en": "The Master of Diabolism 3", "name_jp": "魔道祖师 完结篇", "image_url": "https://cdn.myanimelist.net/images/anime/1634/116782.jpg", "genres": ["Action", "Adventure", "Drama", "Fantasy", "Mystery"], "themes": ["Historical", "Mythology", "Reincarnation"], "demographics": [], "studios": ["B.CMAY PICTURES"], "year": 2021, "episodes": 12, "source": "Web novel", "score": 8.68, "synopsis": "Along an empty road in the rural countryside, Wei Wuxian and Lan Wangji stumble across a stone plaque that reads \"Yi City.\" Still on their quest to unravel the mystery behind the cursed severed arm, they venture into the deserted city to obtain further leads.\n\nHowever, with the appearance of a mysterious new cultivator, the demonic arm investigation leads Wei Wuxian and Lan Wangji to a plot that threatens the cultivation world as they know it. The gravity of the new situation escalates when the two find it involves the chief cultivator of the renowned Lanling Jin Sect—Jin Guangyao.\n\nAs the pair continue to piece together the crucial conspiracies of the cultivation history, their friends and colleagues grow suspicious of their motives. With the world against him, Wei Wuxian must prove he is not the demonic sorcerer people believe him to be to regain their trust and save the cultivation world from the sinister evil lurking within it.\n\n[Written by MAL Rewrite]"}, {"id": 56784, "name_en": "Bleach: Thousand-Year Blood War - The Conflict", "name_jp": "BLEACH 千年血戦篇-相剋譚-", "image_url": "https://cdn.myanimelist.net/images/anime/1595/144074.jpg", "genres": ["Action", "Adventure", "Supernatural"], "themes": [], "demographics": ["Shounen"], "studios": ["Pierrot Films"], "year": 2024, "episodes": 14, "source": "Manga", "score": 8.67, "synopsis": "After an awe-inspiring battle with Ichibei Hyousube—leader of the Soul Society's Royal Guard—the powerful Yhwach moves into the final stage of his master plan. He aims to slay the Soul King, the being whose very existence maintains the status quo of three worlds: Hueco Mundo, the Soul Society, and the realm of humans that Ichigo Kurosaki and his closest friends hail from. Conquering his own bout with the remainder of the Royal Guard, Uryuu Ishida joins Yhwach in his efforts to create a new world in his image.\n\nWith a flood of resolution and newfound power, Ichigo rushes to stop Yhwach from accomplishing his ultimate goal and save the countless lives within the three existing realms. But Ichigo has a complicated lineage, one that leaves him susceptible to Yhwach's sinister influence.\n\nMeanwhile, in a final desperate gambit, Jirou Sakuranosuke Shunsui Kyouraku, the newly promoted head captain of the Soul Society's combat corps, enlists the help of an old enemy whose immense power may turn the tide of battle.\n\n[Written by MAL Rewrite]"}, {"id": 5258, "name_en": "Fighting Spirit: New Challenger", "name_jp": "はじめの一歩 新シリーズ", "image_url": "https://cdn.myanimelist.net/images/anime/8/56617.jpg", "genres": ["Sports"], "themes": ["Combat Sports"], "demographics": ["Shounen"], "studios": ["Madhouse"], "year": 2009, "episodes": 26, "source": "Manga", "score": 8.67, "synopsis": "Japanese Featherweight Champion Ippo Makunouchi has successfully defended and retained his title. Meanwhile, his rival, Ichirou Miyata, has resurfaced in Japan, aiming for his own Featherweight belt in the Oriental Pacific Boxing Federation. When the rest of the world comes knocking, however, will Japan's best fighters rise to the challenge and achieve glory at the top? Or will the small island nation be crushed under the weight of greater entities? This time, champions will become challengers issuing a call to the rest of the world and ready to show off their fighting spirit!\n\n[Written by MAL Rewrite]"}, {"id": 431, "name_en": "Howl's Moving Castle", "name_jp": "ハウルの動く城", "image_url": "https://cdn.myanimelist.net/images/anime/1470/138723.jpg", "genres": ["Adventure", "Award Winning", "Drama", "Fantasy", "Romance"], "themes": [], "demographics": [], "studios": ["Studio Ghibli"], "year": 2004, "episodes": 1, "source": "Novel", "score": 8.67, "synopsis": "That jumbled piece of architecture, that cacophony of hissing steam and creaking joints, with smoke billowing from it as it moves on its own... That castle is home to the magnificent wizard Howl, infamous for both his magical prowess and for being a womanizer—or so the rumor goes in Sophie Hatter's small town. Sophie, as the plain daughter of a hatmaker, does not expect much from her future and is content with working hard in the shop. \n\nHowever, Sophie's simple life takes a turn for the exciting when she is ensnared in a disturbing situation, and the mysterious wizard appears to rescue her. Unfortunately, this encounter, brief as it may be, spurs the vain and vengeful Witch of the Waste—in a fit of jealousy caused by a past discord with Howl—to put a curse on the maiden, turning her into an old woman.\n\nIn an endeavor to return to normal, Sophie must accompany Howl and a myriad of eccentric companions—ranging from a powerful fire demon to a hopping scarecrow—in his living castle, on a dangerous adventure as a raging war tears their kingdom apart.\n\n[Written by MAL Rewrite]"}, {"id": 57864, "name_en": "Monogatari Series: Off & Monster Season", "name_jp": "〈物語〉シリーズ オフ&モンスターシーズン", "image_url": "https://cdn.myanimelist.net/images/anime/1142/146776.jpg", "genres": ["Comedy", "Mystery", "Supernatural"], "themes": ["Vampire"], "demographics": [], "studios": ["Shaft"], "year": 2024, "episodes": 14, "source": "Light novel", "score": 8.65, "synopsis": "Koyomi Araragi spent his last year of high school helping girls in his town resolve various supernatural afflictions. But now Araragi has departed for university, leaving his friends to fend for themselves against new problems and curses that plague them. Yotsugi Ononoki, once a human corpse and now a living doll, takes residence in Araragi's home, keeping watch over his sister Tsukihi, a girl harboring a mystical secret of her own. As part of her duties, Yotsugi fills Araragi's vacated role as occult expert, assisting others in town with their issues.\n\nOne of these girls, middle school student Nadeko Sengoku, slowly recovers from her own recent brushes with the paranormal. She avoids returning to school, instead spending time alone in her room and pursuing her dream of becoming a professional manga artist. In order to speed up Nadeko's quest for mastery of her craft, Yotsugi convinces her to create four copies of herself, each representing a distinct aspect of Nadeko's personality. However, the clones refuse to help Nadeko, instead escaping into the town and creating a chaotic mess. Now forced to grapple with her own fractured sense of identity, Nadeko sets out to capture them and resolve her inner conflict.\n\n[Written by MAL Rewrite]"}, {"id": 57647, "name_en": "Umamusume: Pretty Derby - Beginning of a New Era", "name_jp": "ウマ娘 プリティーダービー 新時代の扉", "image_url": "https://cdn.myanimelist.net/images/anime/1427/142210.jpg", "genres": ["Sports"], "themes": ["Anthropomorphic", "Racing"], "demographics": [], "studios": ["CygamesPictures"], "year": 2024, "episodes": 1, "source": "Game", "score": 8.65, "synopsis": "Running in the highest level of horse racing, the Twinkle Series, is the dream of many horse girls. Although she has only taken part in freestyle racing before, horse girl Jungle Pocket discovers that very same aspiration after she witnesses Fuji Kiseki's tremendous victory on a grand stage. Equipped with unwavering self-confidence, Jungle Pocket is quickly able to accumulate a few victories, allowing her to participate in higher-level races.\n\nWhen Jungle Pocket takes part in her first G1 race alongside another rising racer—the highly intelligent and eccentric Agnes Tachyon—she realizes the enormous gap in their abilities. However, that only motivates Jungle Pocket even further as she resolves to get her revenge against Tachyon by winning the honored Triple Crown Classic title the following year. Although the world of horse racing is never predictable or easy, Jungle Pocket is determined to take down Tachyon and herald the beginning of a new era.\n\n[Written by MAL Rewrite]"}, {"id": 55255, "name_en": "Alien Stage", "name_jp": "ALIEN STAGE (에일리언스테이지)", "image_url": "https://cdn.myanimelist.net/images/anime/1524/143502.jpg", "genres": ["Drama", "Sci-Fi", "Suspense"], "themes": ["High Stakes Game", "Music", "Psychological"], "demographics": [], "studios": ["LICO"], "year": 2022, "episodes": 12, "source": "Music", "score": 8.65, "synopsis": "Humans once lived like Earth was the center of the universe. Some people embraced religion and believed that God would deliver them from suffering. After humanity was enslaved by aliens, no one spoke of God any longer.\n\nNow the grim truth is that humans are mere property of aliens. Recently, entering pet humans into the entertainment industry has become popular, and the biggest, most thrilling spectacle is known as Alien Stage. In this reality singing competition, alien sponsors flaunt their investments, while human contestants must battle to survive. No matter the relationship between opponents—whether it is love, hate, or something more extreme—only one victor can emerge each round.\n\n[Written by MAL Rewrite]"}, {"id": 457, "name_en": "Mushi-Shi", "name_jp": "蟲師", "image_url": "https://cdn.myanimelist.net/images/anime/2/73862.jpg", "genres": ["Adventure", "Mystery", "Slice of Life", "Supernatural"], "themes": ["Adult Cast", "Historical", "Iyashikei"], "demographics": ["Seinen"], "studios": ["Artland"], "year": 2005, "episodes": 26, "source": "Manga", "score": 8.65, "synopsis": "\"Mushi\": the most basic forms of life in the world. They exist without any goals or purposes aside from simply \"being.\" They are beyond the shackles of the words \"good\" and \"evil.\" Mushi can exist in countless forms and are capable of mimicking things from the natural world such as plants, diseases, and even phenomena like rainbows.\n\nThis is, however, just a vague definition of these entities that inhabit the vibrant world of Mushishi, as to even call them a form of life would be an oversimplification. Detailed information on Mushi is scarce because the majority of humans are unaware of their existence.\n\nSo what are Mushi and why do they exist? This is the question that a \"Mushishi,\" Ginko, ponders constantly. Mushishi are those who research Mushi in hopes of understanding their place in the world's hierarchy of life.\n\nGinko chases rumors of occurrences that could be tied to Mushi, all for the sake of finding an answer.\n\nIt could, after all, lead to the meaning of life itself.\n\n[Written by MAL Rewrite]"}, {"id": 50399, "name_en": "Heaven Official's Blessing Season 2", "name_jp": "天官賜福 貳", "image_url": "https://cdn.myanimelist.net/images/anime/1203/139210.jpg", "genres": ["Action", "Adventure", "Drama", "Fantasy"], "themes": ["Historical", "Mythology"], "demographics": [], "studios": ["Red Dog Culture House"], "year": 2023, "episodes": 12, "source": "Web novel", "score": 8.65, "synopsis": "Although most gods in the heavenly realm avoid dealing with the forgotten god Xie Lian, Heavenly Emperor Jun Wu holds him in good graces. Xie Lian's luck is otherwise terrible, and adding to his troubles, the gods seem to suspect that Xie Lian's recent companion was the dreaded Ghost King Hua Cheng. Despite what others may think of him, Xie Lian fondly recalls Hua Cheng's promise: the next time they meet, Hua Cheng will appear in his true form.\n\nJun Wu informs Xie Lian of the unsettling news that a god's distress signal was seen coming from the Ghost City—the most prosperous place in the ghost realm, where the only law is Hua Cheng himself. As Xie Lian has no qualms about stepping into Hua Cheng's territory, he infiltrates the city to locate the lost god. There, the genuine Hua Cheng is already waiting. He is the most feared being across the three realms, yet before Xie Lian, his arrogance erodes. Without revealing the reason for his generosity, he spares no power or means to ease the burdens of Xie Lian's endlessly unfortunate past.\n\n[Written by MAL Rewrite]"}, {"id": 33050, "name_en": "Fate/stay night: Heaven's Feel - III. Spring Song", "name_jp": "劇場版「Fate/stay night [Heaven's Feel] III.spring song」", "image_url": "https://cdn.myanimelist.net/images/anime/1142/112957.jpg", "genres": ["Action", "Fantasy"], "themes": ["Urban Fantasy"], "demographics": [], "studios": ["ufotable"], "year": 2020, "episodes": 1, "source": "Visual novel", "score": 8.64, "synopsis": "The Fifth Holy Grail War in Fuyuki City has reached a turning point in which the lives of all participants are threatened as the hidden enemy finally reveals itself. As Shirou Emiya, Rin Toosaka, and Illyasviel von Einzbern discover the true, corruptive nature of the shadow that has been rampaging throughout the city, they realize just how dire the situation is. In order to protect their beloved ones, the group must hold their own against the seemingly insurmountable enemy force—even if some of those foes were once their allies, or perhaps, something more intimate.\n\nAs the final act of this chaotic war commences, the ideals Shirou believes will soon be challenged by an excruciating dilemma: is it really possible to save a world where everything seems to have gone wrong?\n\n[Written by MAL Rewrite]"}, {"id": 2001, "name_en": "Gurren Lagann", "name_jp": "天元突破グレンラガン", "image_url": "https://cdn.myanimelist.net/images/anime/4/5123.jpg", "genres": ["Adventure", "Award Winning", "Sci-Fi"], "themes": ["Mecha"], "demographics": [], "studios": ["Gainax"], "year": 2007, "episodes": 27, "source": "Original", "score": 8.64, "synopsis": "Simon and Kamina were born and raised in a deep, underground village, hidden from the fabled surface. Kamina is a free-spirited loose cannon bent on making a name for himself, while Simon is a timid young boy with no real aspirations. One day while excavating the earth, Simon stumbles upon a mysterious object that turns out to be the ignition key to an ancient artifact of war, which the duo dubs Lagann. Using their new weapon, Simon and Kamina fend off a surprise attack from the surface with the help of Yoko Littner, a hot-blooded redhead wielding a massive gun who wanders the world above.\n\nIn the aftermath of the battle, the sky is now in plain view, prompting Simon and Kamina to set off on a journey alongside Yoko to explore the wastelands of the surface. Soon, they join the fight against the \"Beastmen,\" humanoid creatures that terrorize the remnants of humanity in powerful robots called \"Gunmen.\" Although they face some challenges and setbacks, the trio bravely fights these new enemies alongside other survivors to reclaim the surface, while slowly unraveling a galaxy-sized mystery.\n\n[Written by MAL Rewrite]"}, {"id": 49413, "name_en": "Link Click Season 2", "name_jp": "时光代理人II", "image_url": "https://cdn.myanimelist.net/images/anime/1897/137108.jpg", "genres": ["Drama", "Mystery", "Suspense"], "themes": ["Adult Cast", "Super Power", "Time Travel"], "demographics": [], "studios": ["LAN Studio"], "year": 2023, "episodes": 12, "source": "Original", "score": 8.64, "synopsis": "The attempt to capture the mysterious perpetrator who possesses people ends tragically: Lu Guang is rushed to the hospital in a critical state, while Cheng Xiaoshi is arrested for the alleged crime. In light of recent events, the father of Liu Min unleashes his skilled, ruthless lawyer—Qian Jin—after Police Chief Li Xiao, who is spearheading the investigation related to the photo studio owned by Qiao Ling.\n\nIt appears that no one is safe from the unpredictable and fatal attacks of the adversary who painstakingly hides their identity. As he tries to learn from his past mistakes, Cheng Xiaoshi must act swiftly and decisively to put an end to this devastating trail of death.\n\n[Written by MAL Rewrite]"}, {"id": 23273, "name_en": "Your Lie in April", "name_jp": "四月は君の嘘", "image_url": "https://cdn.myanimelist.net/images/anime/1405/143284.jpg", "genres": ["Drama", "Romance"], "themes": ["Love Polygon", "Music", "Performing Arts", "School"], "demographics": ["Shounen"], "studios": ["A-1 Pictures"], "year": 2014, "episodes": 22, "source": "Manga", "score": 8.64, "synopsis": "Kousei Arima is a child prodigy known as the \"Human Metronome\" for playing the piano with precision and perfection. Guided by a strict mother and rigorous training, Kousei dominates every competition he enters, earning the admiration of his musical peers and praise from audiences. When his mother suddenly passes away, the subsequent trauma makes him unable to hear the sound of a piano, and he never takes the stage thereafter.\n\nNowadays, Kousei lives a quiet and unassuming life as a junior high school student alongside his friends Tsubaki Sawabe and Ryouta Watari. While struggling to get over his mother's death, he continues to cling to music. His monochrome life turns upside down the day he encounters the eccentric violinist Kaori Miyazono, who thrusts him back into the spotlight as her accompanist. Through a little lie, these two young musicians grow closer together as Kaori tries to fill Kousei's world with color.\n\n[Written by MAL Rewrite]"}, {"id": 35760, "name_en": "Attack on Titan Season 3", "name_jp": "進撃の巨人 Season3", "image_url": "https://cdn.myanimelist.net/images/anime/1173/92110.jpg", "genres": ["Action", "Drama", "Suspense"], "themes": ["Gore", "Military", "Survival"], "demographics": ["Shounen"], "studios": ["Wit Studio"], "year": 2018, "episodes": 12, "source": "Manga", "score": 8.64, "synopsis": "Still threatened by the \"Titans\" that rob them of their freedom, mankind remains caged inside the two remaining walls. Efforts to eradicate these monsters continue; however, threats arise not only from the Titans beyond the walls, but from the humans within them as well.\n\nAfter being rescued from the Colossal and Armored Titans, Eren Yaeger devotes himself to improving his Titan form. Krista Lenz struggles to accept the loss of her friend, Captain Levi chooses Eren and his friends to form his new personal squad, and Commander Erwin Smith recovers from his injuries. All seems well for the soldiers, until the government suddenly demands custody of Eren and Krista. The Survey Corps' recent successes have drawn attention, and a familiar face from Levi's past is sent to collect the wanted soldiers. Sought after by the government, Levi and his new squad must evade their adversaries in hopes of keeping Eren and Krista safe.\n\nEren and his fellow soldiers are not only fighting for their survival against the terrifying Titans, but also against the terror of a far more conniving foe: their fellow humans.\n\n[Written by MAL Rewrite]"}, {"id": 57658, "name_en": "Jujutsu Kaisen: The Culling Game Part 1", "name_jp": "呪術廻戦 「死滅回游 前編」", "image_url": "https://cdn.myanimelist.net/images/anime/1659/154920.jpg", "genres": ["Action", "Supernatural"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["MAPPA"], "year": 2026, "episodes": 12, "source": "Manga", "score": 8.63, "synopsis": "Sequel to Jujutsu Kaisen 2nd Season."}, {"id": 34599, "name_en": "Made in Abyss", "name_jp": "メイドインアビス", "image_url": "https://cdn.myanimelist.net/images/anime/6/86733.jpg", "genres": ["Adventure", "Drama", "Fantasy", "Mystery", "Sci-Fi"], "themes": ["Gore"], "demographics": [], "studios": ["Kinema Citrus"], "year": 2017, "episodes": 13, "source": "Web manga", "score": 8.63, "synopsis": "The Abyss—a gaping chasm stretching down into the depths of the earth, filled with mysterious creatures and relics from a time long past. How did it come to be? What lies at the bottom? Countless brave individuals, known as Divers, have sought to solve these mysteries of the Abyss, fearlessly descending into its darkest realms. The best and bravest of the Divers, the White Whistles, are hailed as legends by those who remain on the surface.\n\nRiko, daughter of the missing White Whistle Lyza the Annihilator, aspires to become like her mother and explore the furthest reaches of the Abyss. However, just a novice Red Whistle herself, she is only permitted to roam its most upper layer. Even so, Riko has a chance encounter with a mysterious robot with the appearance of an ordinary young boy. She comes to name him Reg, and he has no recollection of the events preceding his discovery. Certain that the technology to create Reg must come from deep within the Abyss, the two decide to venture forth into the chasm to recover his memories and see the bottom of the great pit with their own eyes. However, they know not of the harsh reality that is the true existence of the Abyss.\n\n[Written by MAL Rewrite]"}, {"id": 11665, "name_en": "Natsume's Book of Friends Season 4", "name_jp": "夏目友人帳 肆", "image_url": "https://cdn.myanimelist.net/images/anime/3/37449.jpg", "genres": ["Slice of Life", "Supernatural"], "themes": ["Iyashikei", "Mythology"], "demographics": ["Shoujo"], "studios": ["Brain's Base"], "year": 2012, "episodes": 13, "source": "Manga", "score": 8.63, "synopsis": "Takashi Natsume, the timid youkai expert and master of the Book of Friends, continues his journey towards self-understanding and acceptance with the help of friends both new and old. His most important ally is still his gluttonous and sake-loving bodyguard, the arrogant but fiercely protective wolf spirit Madara—or Nyanko-sensei, as Madara is called when in his usual disguise of an unassuming, pudgy cat.\n\nNatsume, while briefly separated from Nyanko-sensei, is ambushed and kidnapped by a strange group of masked, monkey-like youkai, who have spirited him away to their forest as they desperately search for the Book of Friends. Realizing that his \"servant\" has been taken out from right under his nose, Nyanko-sensei enlists the help of Natsume's youkai friends and mounts a rescue operation. However, the forest of the monkey spirits holds many dangerous enemies, including the Matoba Clan, Natsume's old nemesis.\n\nStretching from the formidable hideout of the Matoba to Natsume's own childhood home, Natsume Yuujinchou Shi is a sweeping but familiar return to a world of danger and friendship, where Natsume will finally confront the demons of his own past.\n\n[Written by MAL Rewrite]"}, {"id": 22135, "name_en": "Ping Pong the Animation", "name_jp": "ピンポン THE ANIMATION", "image_url": "https://cdn.myanimelist.net/images/anime/1586/146565.jpg", "genres": ["Award Winning", "Drama", "Sports"], "themes": [], "demographics": ["Seinen"], "studios": ["Tatsunoko Production"], "year": 2014, "episodes": 11, "source": "Manga", "score": 8.63, "synopsis": "Despite being polar opposites, Makoto \"Smile\" Tsukimoto and Yutaka \"Peco\" Hoshino have been best friends since childhood. Although the overly confident Peco strives to be the best ping-pong player in the world, he often skips practice, earning the ire of his fellow teammates on the Katase High School ping-pong team. Meanwhile, Smile—in spite of his innate talent for the sport—cannot help but hold back his full strength when playing against others. Through their mutual love for ping-pong, the two have developed a bond that is seemingly unbreakable.\n\nWhen Peco hears that an ex-national team player from China is coming to Japan, he drags Smile over to rival Tsujido High School to observe them. The subsequent trip leads to a clash between Peco and Kong Wenge, who overwhelmingly defeats the former in one game. Stunned by such a comprehensive loss, Peco finds himself questioning why he plays to begin with. Seeing his potential as a player, Katase's coach begins to train Smile to overcome his hesitation, but he is reluctant to play if it is not for enjoyment.\n\nAs the two struggle to find meaning in the sport, a plethora of stronger players—each with their own internal strifes—await them at the inter-high tournament, where only the very best can persevere. But when these young athletes let their unbridled ambition go unchecked, the hardships they face paint a somber reality as they pursue glory.\n\n[Written by MAL Rewrite]"}, {"id": 46102, "name_en": "Odd Taxi", "name_jp": "オッドタクシー", "image_url": "https://cdn.myanimelist.net/images/anime/1981/113348.jpg", "genres": ["Award Winning", "Drama", "Mystery", "Suspense"], "themes": ["Adult Cast", "Anthropomorphic", "Organized Crime"], "demographics": [], "studios": ["OLM", "P.I.C.S."], "year": 2021, "episodes": 13, "source": "Original", "score": 8.63, "synopsis": "Eccentric and blunt, the walrus Hiroshi Odokawa lives a relatively normal life. He drives a taxi for a living, and there he meets several unique individuals: the jobless Taichi Kabasawa who is dead-set on going viral, the mysterious nurse Miho Shirakawa, the struggling comedic duo \"Homo Sapiens,\" and Dobu, a well-known delinquent.\n\nBut Odokawa's simple way of life is about to be turned upside down. The case of a missing girl the police have been tracking leads back to him, and now both the yakuza and a duo of corrupt cops are on his tail.\n\n[Written by MAL Rewrite]"}, {"id": 58125, "name_en": "Look Back", "name_jp": "ルックバック", "image_url": "https://cdn.myanimelist.net/images/anime/1716/142633.jpg", "genres": ["Drama"], "themes": ["Otaku Culture"], "demographics": ["Shounen"], "studios": ["Studio DURIAN"], "year": 2024, "episodes": 1, "source": "Manga", "score": 8.62, "synopsis": "Ayumu Fujino may only be in the fourth grade, but she already basks in high praise for her hand-drawn four-panel comics featured in the school's newspaper. However, when she is asked to share the page with Kyoumoto—a reclusive student she has never met—Fujino feels inadequate for the first time: her free-spirited drawings look embarrassingly amateurish next to Kyoumoto's breathtakingly detailed art.\n\nFor a year, Fujino shuts out the world, obsessively studying manga creation and drawing tirelessly to catch up to her faceless competition. But Kyoumoto's talent far exceeds hers, and Fujino quits it all. Another year passes, and on the day of their graduation, Fujino finally meets Kyoumoto. This unkempt, shy, and stuttering girl has actually been Fujino's biggest fan all along. Their encounter reignites Fujino's passion for art and sparks the beginning of a years-long friendship built on rivalry, admiration, and their shared love of manga.\n\n[Written by MAL Rewrite]"}, {"id": 49818, "name_en": "Lord of Mysteries", "name_jp": "诡秘之主 小丑篇", "image_url": "https://cdn.myanimelist.net/images/anime/1952/149229.jpg", "genres": ["Action", "Fantasy", "Mystery", "Suspense"], "themes": ["Adult Cast", "Isekai"], "demographics": [], "studios": ["B.CMAY PICTURES"], "year": 2025, "episodes": 13, "source": "Web novel", "score": 8.62, "synopsis": "In a Victorian world of steam, dreadnoughts, and occult horrors, Zhou Mingrui awakens as Klein Moretti. He walks a razor's edge between light and darkness, entangled with warring Churches. This is the legend of unlimited potential...and unspeakable danger.\n\n(Source: Crunchyroll)"}, {"id": 40591, "name_en": "Kaguya-sama: Love is War?", "name_jp": "かぐや様は告らせたい？～天才たちの恋愛頭脳戦～", "image_url": "https://cdn.myanimelist.net/images/anime/1764/106659.jpg", "genres": ["Comedy", "Romance"], "themes": ["School"], "demographics": ["Seinen"], "studios": ["A-1 Pictures"], "year": 2020, "episodes": 12, "source": "Manga", "score": 8.62, "synopsis": "After a slow but eventful summer vacation, Shuchiin Academy's second term is now starting in full force. As August transitions into September, Miyuki Shirogane's birthday looms ever closer, leaving Kaguya Shinomiya in a serious predicament as to how to celebrate it. Furthermore, the tenure of the school's 67th student council is coming to an end. Due to the council members being in different classes, the only time Kaguya and Miyuki have to be together will soon disappear, putting all of their cunning plans at risk.\n\nA long and difficult election that will decide the fate of the new student council awaits, as multiple challengers fight for the coveted title of president.\n\n[Written by MAL Rewrite]"}, {"id": 42310, "name_en": "Cyberpunk: Edgerunners", "name_jp": "サイバーパンク エッジランナーズ", "image_url": "https://cdn.myanimelist.net/images/anime/1818/126435.jpg", "genres": ["Action", "Sci-Fi"], "themes": ["Gore", "Organized Crime"], "demographics": [], "studios": ["Trigger"], "year": 2022, "episodes": 10, "source": "Game", "score": 8.62, "synopsis": "Dreams are doomed to die in Night City, a futuristic Californian metropolis. As a teenager living in the city's slums, David Martinez is trying to fulfill his mother's lifelong wish for him to reach the top of Arasaka, the world's leading security corporation. To this end, he attends the prestigious Arasaka Academy while his mother works tirelessly to keep their family afloat.\n\nWhen an incident with a street gang leaves David's life in tatters, he stumbles upon Sandevistan cyberware—a prosthetic that grants its wearer superhuman speed. Fueled by rage, David implants the device in his back, using it to exact revenge on one of his tormentors. This gets him expelled from the academy, shattering his hopes of ever making his mother proud.\n\nAfter witnessing David's newfound abilities, the beautiful data thief Lucyna \"Lucy\" Kushinada offers to team up with him, handing him a ticket to salvation. However, associating with Lucy introduces David to the world of Edgerunners—cyborg criminals who will break any law for money. Edgerunners often lose their lives, if the cyberware does not break their minds first; but in his fight for survival inside a corrupt system, David is ready to risk it all.\n\n[Written by MAL Rewrite]"}, {"id": 1535, "name_en": "Death Note", "name_jp": "デスノート", "image_url": "https://cdn.myanimelist.net/images/anime/1079/138100.jpg", "genres": ["Supernatural", "Suspense"], "themes": ["Psychological"], "demographics": ["Shounen"], "studios": ["Madhouse"], "year": 2006, "episodes": 37, "source": "Manga", "score": 8.62, "synopsis": "Brutal murders, petty thefts, and senseless violence pollute the human world. In contrast, the realm of death gods is a humdrum, unchanging gambling den. The ingenious 17-year-old Japanese student Light Yagami and sadistic god of death Ryuk share one belief: their worlds are rotten.\n\nFor his own amusement, Ryuk drops his Death Note into the human world. Light stumbles upon it, deeming the first of its rules ridiculous: the human whose name is written in this note shall die. However, the temptation is too great, and Light experiments by writing a felon's name, which disturbingly enacts his first murder.\n\nAware of the terrifying godlike power that has fallen into his hands, Light—under the alias Kira—follows his wicked sense of justice with the ultimate goal of cleansing the world of all evil-doers. The meticulous mastermind detective L is already on his trail, but as Light's brilliance rivals L's, the grand chase for Kira turns into an intense battle of wits that can only end when one of them is dead.\n\n[Written by MAL Rewrite]"}, {"id": 28891, "name_en": "Haikyu!! 2nd Season", "name_jp": "ハイキュー!! セカンドシーズン", "image_url": "https://cdn.myanimelist.net/images/anime/9/76662.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2015, "episodes": 25, "source": "Manga", "score": 8.62, "synopsis": "Following their participation at the Inter-High, the Karasuno High School volleyball team attempts to refocus their efforts, aiming to conquer the Spring tournament instead. \n\nWhen they receive an invitation from long-standing rival Nekoma High, Karasuno agrees to take part in a large training camp alongside many notable volleyball teams in Tokyo and even some national level players. By playing with some of the toughest teams in Japan, they hope not only to sharpen their skills, but also come up with new attacks that would strengthen them. Moreover, Hinata and Kageyama attempt to devise a more powerful weapon, one that could possibly break the sturdiest of blocks. \n\nFacing what may be their last chance at victory before the senior players graduate, the members of Karasuno's volleyball team must learn to settle their differences and train harder than ever if they hope to overcome formidable opponents old and new—including their archrival Aoba Jousai and its world-class setter Tooru Oikawa.\n\n[Written by MAL Rewrite]"}, {"id": 54898, "name_en": "Bungo Stray Dogs 5", "name_jp": "文豪ストレイドッグス", "image_url": "https://cdn.myanimelist.net/images/anime/1161/136691.jpg", "genres": ["Action", "Mystery"], "themes": ["Adult Cast", "Detective", "Organized Crime", "Super Power", "Vampire"], "demographics": ["Seinen"], "studios": ["Bones"], "year": 2023, "episodes": 11, "source": "Manga", "score": 8.61, "synopsis": "The Armed Detective Agency is still on the run from the Hunting Dogs, but not all hope is lost. Detective Ranpo Edogawa has a plan to prove the Agency's innocence and save the world from chaos: to find and capture Kamui—the leader of the terrorist organization Decay of the Angel. \n\nIn order to determine Kamui's whereabouts, Ranpo and his colleague Atsushi Nakajima must convince Ouchi Fukuchi, the renowned captain of the Hunting Dogs, for amnesty. Although Fukuchi was tasked with arresting the members of the Agency, his past with the Agency's president, Yukichi Fukuzawa, may be the key to earning his trust. But unbeknownst to them, Fukuchi might not be as honorable as he proclaims.\n\n[Written by MAL Rewrite]"}, {"id": 52742, "name_en": "Haikyu!! Movie: The Dumpster Battle", "name_jp": "劇場版ハイキュー!! ゴミ捨て場の決戦", "image_url": "https://cdn.myanimelist.net/images/anime/1665/140360.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2024, "episodes": 1, "source": "Manga", "score": 8.61, "synopsis": "Kenma Kozume has never considered volleyball fun or thrilling: it is merely something he is good at. But now Nekoma High School's volleyball team has qualified for the Spring Nationals and prepares to battle their long-standing rivals—Karasuno. Now, Kenma has to analyze the most confounding and resilient team and lead Nekoma to victory against them. Moreover, he will play against his friend Shouyou Hinata, Karasuno's short but incredibly proficient middle blocker.\n\nKarasuno has never been able to beat Nekoma in practice matches. Even so, despite his usual indifference, Kenma feels a tinge of excitement at the prospect of facing Karasuno in a high-stakes official game with no do-overs. To advance to the semifinals and ultimately restore their team's former glory, Karasuno must find a way to overcome Kenma's brilliant strategy and defeat Nekoma in their own territory.\n\n[Written by MAL Rewrite]"}, {"id": 34591, "name_en": "Natsume's Book of Friends Season 6", "name_jp": "夏目友人帳 陸", "image_url": "https://cdn.myanimelist.net/images/anime/6/84416.jpg", "genres": ["Slice of Life", "Supernatural"], "themes": ["Iyashikei", "Mythology"], "demographics": ["Shoujo"], "studios": ["Shuka"], "year": 2017, "episodes": 11, "source": "Manga", "score": 8.61, "synopsis": "Takashi Natsume has grown accustomed to his encounters with youkai through the Book of Friends, which contains the names of youkai whom his grandmother, Reiko Natsume, has sealed in contracts. These encounters allow Natsume to better understand the youkai, Reiko, and himself. \n\nThe Book of Friends is a powerful tool that can be used to control youkai; it is sought after by both youkai and exorcists alike. Natsume just wants to live out his daily life in peace but is constantly disrupted by these experiences. If he is to end this torment, Natsume must explore more about the book and the world of exorcism, as well as begin to open his heart to those who can help him.\n\n[Written by MAL Rewrite]"}, {"id": 45576, "name_en": "Mushoku Tensei: Jobless Reincarnation Part 2", "name_jp": "無職転生 ～異世界行ったら本気だす～ 第2クール", "image_url": "https://cdn.myanimelist.net/images/anime/1028/117777.jpg", "genres": ["Adventure", "Drama", "Fantasy", "Ecchi"], "themes": ["Isekai", "Reincarnation"], "demographics": [], "studios": ["Studio Bind"], "year": 2021, "episodes": 12, "source": "Light novel", "score": 8.61, "synopsis": "After the mysterious mana calamity, Rudeus Greyrat and his fierce student Eris Boreas Greyrat are teleported to the Demon Continent. There, they team up with their newfound companion Ruijerd Supardia—the former leader of the Superd's Warrior group—to form \"Dead End,\" a successful adventurer party. Making a name for themselves, the trio journeys across the continent to make their way back home to Fittoa.\n\nFollowing the advice he received from the faceless god Hitogami, Rudeus saves Kishirika Kishirisu, the Great Emperor of the Demon World, who rewards him by granting him a strange power. Now, as Rudeus masters the powerful ability that offers a number of new opportunities, it might prove to be more than what he bargained for when unexpected dangers threaten to hinder their travels.\n\n[Written by MAL Rewrite]"}, {"id": 19647, "name_en": "Fighting Spirit: Rising", "name_jp": "はじめの一歩 Rising", "image_url": "https://cdn.myanimelist.net/images/anime/6/56147.jpg", "genres": ["Sports"], "themes": ["Combat Sports"], "demographics": ["Shounen"], "studios": ["Madhouse", "MAPPA"], "year": 2013, "episodes": 25, "source": "Manga", "score": 8.61, "synopsis": "Japanese Featherweight Champion Makunouchi Ippo has defended his title belt once more with the help of his devastating signature move: the Dempsey Roll. However, new challengers are rising up left and right, claiming to have an answer for the move responsible for crushing his opponents. Will Ippo be able to step up to the challenge, or will the weight of his pride destroy him before he finds out just what it means to be strong? Meanwhile, fellow Kamogawa Gym mate Aoki Masaru is just a hop, skip, and a Frog Punch away from claiming his own belt, ready to take on the Japanese Lightweight Champion!\n\nHajime no Ippo: Rising continues Ippo's quest to become stronger, featuring the same cast of loveable dimwits from Kamogawa Gym, as they put their bodies and hearts on the line to make their way in the harsh world of professional boxing. With a will of iron, Ippo steps into the ring once again.\n\n[Written by MAL Rewrite]"}, {"id": 4565, "name_en": "Gurren Lagann The Movie: The Lights in the Sky are Stars", "name_jp": "劇場版 天元突破グレンラガン 螺巌篇", "image_url": "https://cdn.myanimelist.net/images/anime/12/19698.jpg", "genres": ["Sci-Fi"], "themes": ["Mecha", "Space", "Super Power"], "demographics": [], "studios": ["Gainax"], "year": 2009, "episodes": 1, "source": "Original", "score": 8.6, "synopsis": "Humans have enjoyed their lavish, peaceful, and prosperous lives for seven years since the day the almighty Spiral King was defeated—the day they reclaimed their homeland, Earth. However, the boon of this lifestyle leaves them unprepared when an unknown, hostile threat arises due to the ever-growing human population. This calamity is the Anti-Spiral—a fearsome enemy with unparalleled power. \n \nAs the Spiral King's prognosis postulating the destruction of \"The Spiral's World\" begins to come true, the pieces are in place, and Team Dai-Gurren is ready. With his late brother's hope to see a better future for mankind, Simon—along with Nia Teppelin and the rest of the team—is determined to overthrow the mighty Anti-Spiral in order to revive humanity's lost hope.\n\n[Written by MAL Rewrite]"}, {"id": 36862, "name_en": "Made in Abyss: Dawn of the Deep Soul", "name_jp": "劇場版メイドインアビス 深き魂の黎明", "image_url": "https://cdn.myanimelist.net/images/anime/1803/117183.jpg", "genres": ["Adventure", "Drama", "Fantasy", "Mystery", "Sci-Fi"], "themes": ["Gore"], "demographics": [], "studios": ["Kinema Citrus"], "year": 2020, "episodes": 1, "source": "Web manga", "score": 8.6, "synopsis": "After bonding over a tragic loss, the long-suffering Nanachi joins Riko and Reg on their journey into the depths of the Abyss. Awaiting the children is the Sea of Corpses—the Abyss's fifth layer, and the deepest level from which a traveler can return without losing their human form.\n\nThe masked sadist Bondrewd stands between the children and the rest of their adventure. Bondrewd's horrific laboratory serves as a final checkpoint for those wishing to traverse deeper into the Abyss, and the sociopathic scientist has no desire to allow Riko's party to pass through at no cost. Deeply scarred by Bondrewd's impact on their childhood, Nanachi is engulfed in turmoil over his resurgence in their life.\n\nBondrewd's only apparent weakness is Prushka, a brash child who claims to be his daughter. Riko, Reg, and Nanachi befriend Prushka and work with the girl to overcome her father's machinations and breach the Abyss's sixth layer.\n\n[Written by MAL Rewrite]"}, {"id": 41084, "name_en": "Made in Abyss: The Golden City of the Scorching Sun", "name_jp": "メイドインアビス 烈日の黄金郷", "image_url": "https://cdn.myanimelist.net/images/anime/1864/122519.jpg", "genres": ["Adventure", "Drama", "Fantasy", "Mystery", "Sci-Fi"], "themes": ["Gore"], "demographics": [], "studios": ["Kinema Citrus"], "year": 2022, "episodes": 12, "source": "Web manga", "score": 8.6, "synopsis": "After surviving the brutal challenges of Idofront, Riko now possesses a White Whistle, allowing her to descend into the Abyss's sixth layer—The Capital of the Unreturned. Alongside Reg and Nanachi, Riko begins to explore the uncharted domain, where the ruins of the promised Golden City are located.\n\nAs the trio starts to adapt to the harsh environment, they soon encounter dangerous creatures and treacherous landscapes. Their expedition leads them to a village inhabited by strange beings known as \"hollows.\" Despite the creeping sense of unease that welcomes them, the three venture onward to uncover the mysteries of the settlement and long-lost legacies of the forgotten adventurers who once descended into the horrors of the unexplored Abyss.\n\n[Written by MAL Rewrite]"}, {"id": 61952, "name_en": "The Legend of Hei 2", "name_jp": "罗小黑战记2", "image_url": "https://cdn.myanimelist.net/images/anime/1288/151853.jpg", "genres": ["Adventure", "Drama", "Fantasy"], "themes": [], "demographics": [], "studios": ["HMCH"], "year": 2025, "episodes": 1, "source": "Original", "score": 8.59, "synopsis": "When an attack shatters the fragile peace between the spirit world and humanity, Hei teams up with Luye, the last disciple of his Shifu Wuxian, to expose a conspiracy that threatens both realms—and the bond they've sworn to protect.\n\n(Source: AMC Theatres)"}, {"id": 58567, "name_en": "Solo Leveling Season 2: Arise from the Shadow", "name_jp": "俺だけレベルアップな件 Season 2 -Arise from the Shadow-", "image_url": "https://cdn.myanimelist.net/images/anime/1448/147351.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Adult Cast", "Urban Fantasy"], "demographics": [], "studios": ["A-1 Pictures"], "year": 2025, "episodes": 13, "source": "Web manga", "score": 8.59, "synopsis": "Sung Jin-Woo, dubbed the weakest hunter of all mankind, grows stronger by the day with the supernatural powers he has gained. However, keeping his skills hidden becomes more difficult as dungeon-related incidents pile up around him.\n\nWhen Jin-Woo and a few other low-ranked hunters are the only survivors of a dungeon that turns out to be a bigger challenge than initially expected, he draws attention once again, and hunter guilds take an increased interest in him. Meanwhile, a strange hunter who has been lost for ten years returns with a dire warning about an upcoming catastrophic event. As the calamity looms closer, Jin-Woo must continue leveling up to make sure nothing stops him from reaching his ultimate goal—saving the life of his mother.\n\n[Written by MAL Rewrite]"}, {"id": 52701, "name_en": "Delicious in Dungeon", "name_jp": "ダンジョン飯", "image_url": "https://cdn.myanimelist.net/images/anime/1711/142478.jpg", "genres": ["Adventure", "Comedy", "Fantasy", "Gourmet"], "themes": [], "demographics": ["Seinen"], "studios": ["Trigger"], "year": 2024, "episodes": 24, "source": "Manga", "score": 8.59, "synopsis": "Adventuring knight Laios Touden leads a small party through a seemingly endless dungeon, a subterranean maze full of dangerous monsters and precarious traps. Through the use of advanced magic, an explorer can sometimes be resurrected, allowing them to learn from past mistakes and give traversing the dungeon another go. However, when a powerful dragon eats Falin, Laios' spellcasting sister, she sends her brother and his companions back to the beginning to save them from permanent ends.\n\nThough strapped for cash and equipment, Laios resolves to fight his way through the dungeon and rescue Falin before she can be digested by the dragon. Despite some of Laios' allies abandoning him, two remain by his side: elven mage Marcille Donato and halfling locksmith Chilchuck Tims. Due to their lack of funds, the party faces the daunting prospect of starving before being able to complete their quest. However, they find salvation in Senshi, a dwarven warrior with a penchant for cooking and safely eating defeated monsters.\n\nWith Senshi's culinary expertise, Laios and his companions struggle through the dungeon while learning about gourmet dining—and each other—along the way.\n\n[Written by MAL Rewrite]"}, {"id": 38329, "name_en": "Rascal Does Not Dream of a Dreaming Girl", "name_jp": "青春ブタ野郎はゆめみる少女の夢を見ない", "image_url": "https://cdn.myanimelist.net/images/anime/1613/102179.jpg", "genres": ["Drama", "Romance", "Supernatural"], "themes": ["School"], "demographics": [], "studios": ["CloverWorks"], "year": 2019, "episodes": 1, "source": "Light novel", "score": 8.59, "synopsis": "Six months ago, Sakuta Azusagawa had a chance encounter with a bunny girl in a library. Ever since then, he's been blissfully happy with his girlfriend: Mai Sakurajima, that same bunny girl. However, the reappearance of his mysterious first crush, the now-adult Shouko Makinohara, adds a new complication to his relationship with Mai. To make matters worse, he then encounters a middle school Shouko in the hospital, suffering from a grave illness. Mysteriously, his old scars begin throbbing whenever he's near her.\n\nWith Shouko's bizarre situation somehow revolving around him, Sakuta will need to come to terms with his own conflicting feelings, for better or worse. With a girl's life in his hands, just what can he do?\n\n[Written by MAL Rewrite]"}, {"id": 7311, "name_en": "The Disappearance of Haruhi Suzumiya", "name_jp": "涼宮ハルヒの消失", "image_url": "https://cdn.myanimelist.net/images/anime/1248/112352.jpg", "genres": ["Award Winning", "Mystery", "Sci-Fi", "Supernatural"], "themes": ["School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2010, "episodes": 1, "source": "Light novel", "score": 8.59, "synopsis": "On a cold December day, Kyon arrives at school prepared for another outing with his fellow SOS Brigade members. However, much to his surprise, he discovers that almost everything has changed completely: Haruhi Suzumiya and Itsuki Koizumi are nowhere to be found; Mikuru Asahina does not recognize him at all; Yuki Nagato is a regular human; and Ryouko Asakura has mysteriously returned. Although he is no stranger to the supernatural, Kyon is disturbed by this odd turn of events and decides to investigate on his own.\n\nFinding himself to be the only person that is aware of the previous reality, Kyon is now faced with a difficult choice: to finally live the normal life he has always wanted, or uncover a way to turn back the hands of time and restore his chaotic yet familiar world.\n\n[Written by MAL Rewrite]"}, {"id": 59845, "name_en": "The Fragrant Flower Blooms with Dignity", "name_jp": "薫る花は凛と咲く", "image_url": "https://cdn.myanimelist.net/images/anime/1744/150433.jpg", "genres": ["Drama", "Romance"], "themes": ["School"], "demographics": [], "studios": ["CloverWorks"], "year": 2025, "episodes": 13, "source": "Web manga", "score": 8.58, "synopsis": "The all-girls Kikyo Private Academy and Chidori Public High School are polar opposites. With its prestigious history, Kikyo is attended by noble young ladies from distinguished families, while Chidori is infamously full of simple-minded delinquents. It is no surprise that their students clash with their differences.\n\nHaving a tall stature and fierce appearance, Chidori student Rintarou Tsumugi is often avoided by others despite his gentle heart. One day, while helping out at his family's patisserie, he meets a customer who, after a brief moment, runs away from him. The next day, the customer returns to apologize to Rintarou, introducing herself as the cheerful Kaoruko Waguri.\n\nAfter spending time with Kaoruko, Rintarou appreciates that she does not judge him based on his appearance and looks forward to when they will meet again. However, when Rintarou discovers Kaoruko attends Kikyo, their relationship will challenge the social expectations and dynamics around them.\n\n[Written by MAL Rewrite]"}, {"id": 59636, "name_en": "Umamusume: Cinderella Gray", "name_jp": "ウマ娘 シンデレラグレイ", "image_url": "https://cdn.myanimelist.net/images/anime/1626/148097.jpg", "genres": ["Drama", "Sports"], "themes": ["Anthropomorphic", "Racing"], "demographics": ["Seinen"], "studios": ["CygamesPictures"], "year": 2025, "episodes": 13, "source": "Manga", "score": 8.58, "synopsis": "Tokyo is the home of national-level horse girls and the next generation of running prodigies. Jou Kitahara, a rookie trainer with big dreams and modest expectations, does not expect to find talent in the quiet town of Kasamatsu—until he meets an ash-gray-haired girl with a wild, unconventional stride.\n\nAs a child with bad knees, Oguri Cap spent much of her early life in pain, struggling to stand. But through relentless perseverance, she overcame her limits and found liberation in the very thing that once seemed impossible: running. While the other horse girls at Kasamatsu chase victory and fame, Oguri runs without ambition, driven only by the joy of movement.\n\nFujimasa March, a rising regional star, commands attention with her discipline, talent, and tenacity. For her, running is a matter of pride. But when a school-organized race brings her face-to-face with Oguri's raw, unpolished stride, March's confidence begins to waver. In turn, something in Oguri shifts after racing March. For the first time, a spark of ambition ignites within her—a desire to win that will take her beyond the confines of her hometown to the grand stages waiting on the horizon.\n\n[Written by MAL Rewrite]"}, {"id": 55823, "name_en": "Natsume's Book of Friends Season 7", "name_jp": "夏目友人帳 漆", "image_url": "https://cdn.myanimelist.net/images/anime/1270/145168.jpg", "genres": ["Slice of Life", "Supernatural"], "themes": ["Iyashikei", "Mythology"], "demographics": ["Shoujo"], "studios": ["Shuka"], "year": 2024, "episodes": 12, "source": "Manga", "score": 8.58, "synopsis": "As a young child, Takashi Natsume believed his ability to see youkai to be nothing but a curse. Now a high school student, Natsume has managed to heal from his old wounds and form unbreakable bonds with humans and youkai alike. Natsume's newfound friendships—including the one with his spirit bodyguard Madara, nicknamed \"Nyanko-sensei\"—are often interlaced with his duty to return the names of the spirits written inside the Book of Friends, his late grandmother Reiko's keepsake. While more remarkable youkai reach out to him, Natsume discovers that he still has much to learn about the world that surrounds him.\n\n[Written by MAL Rewrite]"}, {"id": 3786, "name_en": "Evangelion: 3.0+1.0 Thrice Upon a Time", "name_jp": "シン・エヴァンゲリオン劇場版𝄇", "image_url": "https://cdn.myanimelist.net/images/anime/1422/113533.jpg", "genres": ["Award Winning", "Drama", "Sci-Fi", "Suspense"], "themes": ["Mecha", "Psychological"], "demographics": [], "studios": ["Khara"], "year": 2021, "episodes": 1, "source": "Original", "score": 8.58, "synopsis": "Following NERV's failed attempt to retrieve the Spears of Longinus and carry out the Human Instrumentality Project, the destruction caused by the Fourth Impact has been largely averted. In a state of disarray, Shinji Ikari, Asuka Langley Shikinami, and Rei Ayanami travel to Village 3—a survivor settlement free from Earth's ruination. There, Shinji slowly comes to terms with his past, developing an entirely different life from his days as an Evangelion pilot.\n\nMeanwhile, NERV makes preparations to continue the Instrumentality Project by means of a new Impact. When WILLE's main aerial battleship arrives at the village, Shinji decides to board, believing that he can help by piloting an Evangelion. As new secrets are uncovered and a battle between WILLE and NERV approaches, the future of Earth hangs in the balance. Can Shinji save humanity and the rest of the world one last time?\n\n[Written by MAL Rewrite]"}, {"id": 37991, "name_en": "JoJo's Bizarre Adventure: Golden Wind", "name_jp": "ジョジョの奇妙な冒険 黄金の風", "image_url": "https://cdn.myanimelist.net/images/anime/1882/94989.jpg", "genres": ["Action", "Adventure"], "themes": ["Organized Crime", "Super Power"], "demographics": ["Shounen"], "studios": ["David Production"], "year": 2018, "episodes": 39, "source": "Manga", "score": 8.58, "synopsis": "In the coastal city of Naples, corruption is teeming—the police blatantly conspire with outlaws, drugs run rampant around the youth, and the mafia governs the streets with an iron fist. However, various fateful encounters will soon occur.\n\nEnter Giorno Giovanna, a 15-year-old boy with an eccentric connection to the Joestar family, who makes a living out of part-time jobs and pickpocketing. Furthermore, he is gifted with the unexplained Stand ability to give and create life—growing plants from the ground and turning inanimate objects into live animals, an ability he has dubbed \"Gold Experience.\" Fascinated by the might of local gangsters, Giorno has dreamed of rising up in their ranks and becoming a \"Gang-Star,\" a feat made possible by his encounter with Bruno Bucciarati, a member of the Passione gang with his own sense of justice.\n\nJoJo no Kimyou na Bouken: Ougon no Kaze follows the endeavors of Giorno after joining Bruno's team while working under Passione, fending off other gangsters and secretly plotting to overthrow their mysterious boss.\n\n[Written by MAL Rewrite]"}, {"id": 28957, "name_en": "Mushi-Shi: The Next Chapter - Drops of Bells", "name_jp": "蟲師 続章: 鈴の雫", "image_url": "https://cdn.myanimelist.net/images/anime/9/72689.jpg", "genres": ["Adventure", "Mystery", "Slice of Life", "Supernatural"], "themes": ["Adult Cast", "Historical", "Iyashikei"], "demographics": ["Seinen"], "studios": ["Artland"], "year": 2015, "episodes": 1, "source": "Manga", "score": 8.58, "synopsis": "On a warm summer day, a boy heard the sound of bells ringing, as if in celebration, in the mountain near his home. Several years later in that same mountain, the mushishi Ginko encounters a strange girl with weeds growing out of her body. Soon after, Ginko coincidentally runs into the now grown-up boy Yoshiro on his way off the mountain. With Yoshiro's help, Ginko soon begins to uncover who this mysterious girl is and what happened to her.\n\nAn adaptation of the last arc in the manga, Mushishi Zoku Shou: Suzu no Shizuku follows Ginko's peculiar journey amidst the occult to unravel the mystery behind the enigmatic girl called Kaya and the mountain that has become her home.\n\n[Written by MAL Rewrite]"}, {"id": 61903, "name_en": "Kaguya-sama: Love Is War - Stairway to Adulthood", "name_jp": "かぐや様は告らせたい 大人への階段", "image_url": "https://cdn.myanimelist.net/images/anime/1112/150697.jpg", "genres": ["Comedy", "Romance"], "themes": ["School"], "demographics": ["Seinen"], "studios": ["A-1 Pictures"], "year": 2025, "episodes": 1, "source": "Manga", "score": 8.57, "synopsis": "TV special for Kaguya-sama wa Kokurasetai."}, {"id": 16498, "name_en": "Attack on Titan", "name_jp": "進撃の巨人", "image_url": "https://cdn.myanimelist.net/images/anime/10/47347.jpg", "genres": ["Action", "Award Winning", "Drama", "Suspense"], "themes": ["Gore", "Military", "Survival"], "demographics": ["Shounen"], "studios": ["Wit Studio"], "year": 2013, "episodes": 25, "source": "Manga", "score": 8.57, "synopsis": "Centuries ago, mankind was slaughtered to near extinction by monstrous humanoid creatures called Titans, forcing humans to hide in fear behind enormous concentric walls. What makes these giants truly terrifying is that their taste for human flesh is not born out of hunger but what appears to be out of pleasure. To ensure their survival, the remnants of humanity began living within defensive barriers, resulting in one hundred years without a single titan encounter. However, that fragile calm is soon shattered when a colossal Titan manages to breach the supposedly impregnable outer wall, reigniting the fight for survival against the man-eating abominations.\n\nAfter witnessing a horrific personal loss at the hands of the invading creatures, Eren Yeager dedicates his life to their eradication by enlisting into the Survey Corps, an elite military unit that combats the merciless humanoids outside the protection of the walls. Eren, his adopted sister Mikasa Ackerman, and his childhood friend Armin Arlert join the brutal war against the Titans and race to discover a way of defeating them before the last walls are breached.\n\n[Written by MAL Rewrite]"}, {"id": 877, "name_en": "Nana", "name_jp": "NANA [ナナ]", "image_url": "https://cdn.myanimelist.net/images/anime/2/11232.jpg", "genres": ["Drama", "Romance"], "themes": ["Adult Cast", "Love Polygon", "Music"], "demographics": ["Shoujo"], "studios": ["Madhouse"], "year": 2006, "episodes": 47, "source": "Manga", "score": 8.57, "synopsis": "Departing from their respective hometowns, two young women with identical names are brought together in their pursuit of new beginnings. With their hearts set on going to Tokyo, Nana Komatsu dreams about blissful love, while Nana Osaki aims for a successful music career.\n\nThe former has a cheerful and friendly nature, but her naivety has steered her romantic life astray until she meets her dependable boyfriend—Shouji Endo. Without letting herself be dismayed by Shouji's decision to study in Tokyo, Nana works hard to earn enough money and follow him there. Meanwhile, her namesake is a solitary punk vocalist whose impassioned romance with her band's bassist, Ren Honjou, comes to a sudden end. Though heartbroken, Nana bravely looks forward and travels to the capital with the ambition of becoming a recognized artist.\n\nShortly after they arrive in Tokyo, the girls cross paths again due to an unexpected coincidence that ultimately leads them to live under the same roof. As they grow closer, the two strive to support one another amid their struggles to forge a future for themselves.\n\n[Written by MAL Rewrite]"}, {"id": 32983, "name_en": "Natsume's Book of Friends Season 5", "name_jp": "夏目友人帳 伍", "image_url": "https://cdn.myanimelist.net/images/anime/11/81755.jpg", "genres": ["Slice of Life", "Supernatural"], "themes": ["Iyashikei", "Mythology"], "demographics": ["Shoujo"], "studios": ["Shuka"], "year": 2016, "episodes": 11, "source": "Manga", "score": 8.56, "synopsis": "Blessed with eyes that are able to perceive the otherwise invisible youkai, Takashi Natsume hides his ability from his newfound family and friends to protect everyone's peaceful daily life. Nonetheless, Natsume never fails to show the same kindness to the benevolent youkai and happily returns their names by using the infamous Book of Friends he inherited from his late grandmother, Reiko.\n\nMeanwhile, the exorcist clan Matoba still wishes for Natsume to join their ranks due to his overwhelming gift. However, Natsume firmly rejects the clan's invitation since not all exorcists are as reasonable as his friend Shuuichi Natori, and many improperly and indiscriminately seal away every youkai in their way. Unsatisfied with Natsume's answer, Seiji Matoba blackmails Natsume into attending a grand gathering of powerful exorcist families. Natsume soon finds himself in the company of dangerous people and youkai alike. But even then, he continues to defy the exorcists' hard-handed methods and believes that peace between both worlds is possible.\n\n[Written by MAL Rewrite]"}, {"id": 31757, "name_en": "Kizumonogatari Part 2: Hot-Blooded", "name_jp": "傷物語〈Ⅱ熱血篇〉", "image_url": "https://cdn.myanimelist.net/images/anime/1981/112812.jpg", "genres": ["Action", "Mystery", "Supernatural"], "themes": ["Vampire"], "demographics": [], "studios": ["Shaft"], "year": 2016, "episodes": 1, "source": "Light novel", "score": 8.56, "synopsis": "No longer truly human, Koyomi Araragi decides to retrieve Kiss-shot Acerola-orion Heart-under-blade's severed body parts that were stolen by three powerful vampire hunters. Awaiting him are Dramaturgie, a vampire hunter who is a vampire himself; Episode, a half-vampire with the ability to transform into mist; and Guillotinecutter, a human priest who is the most dangerous of them all.\n\nUnbeknownst to Araragi, each minute he spends trying to retrieve Kiss-shot's limbs makes him less of a human and more of a vampire. Will he be able to keep his wish of becoming human once again by the end of his battles?\n\n[Written by MAL Rewrite]"}, {"id": 12355, "name_en": "Wolf Children", "name_jp": "おおかみこどもの雨と雪", "image_url": "https://cdn.myanimelist.net/images/anime/9/35721.jpg", "genres": ["Award Winning", "Slice of Life", "Supernatural"], "themes": ["Childcare"], "demographics": [], "studios": ["Studio Chizu"], "year": 2012, "episodes": 1, "source": "Original", "score": 8.56, "synopsis": "Hana, a hard-working college student, falls in love with a mysterious man who attends one of her classes though he is not an actual student. As it turns out, he is not truly human either. On a full moon night, he transforms, revealing that he is the last werewolf alive. Despite this, Hana's love remains strong, and the two ultimately decide to start a family.\n\nHana gives birth to two healthy children—Ame, born during rainfall, and Yuki, born during snowfall—both possessing the ability to turn into wolves, a trait inherited from their father. All too soon, however, the sudden death of her lover devastates Hana's life, leaving her to raise a peculiar family completely on her own. The stress of raising her wild-natured children in a densely populated city, all while keeping their identity a secret, culminates in a decision to move to the countryside, where she hopes Ame and Yuki can live a life free from the judgments of society. Wolf Children is the heartwarming story about the challenges of being a single mother in an unforgiving modern world.\n\n[Written by MAL Rewrite]"}, {"id": 10379, "name_en": "Natsume's Book of Friends Season 3", "name_jp": "夏目友人帳 参", "image_url": "https://cdn.myanimelist.net/images/anime/8/82394.jpg", "genres": ["Slice of Life", "Supernatural"], "themes": ["Iyashikei", "Mythology"], "demographics": ["Shoujo"], "studios": ["Brain's Base"], "year": 2011, "episodes": 13, "source": "Manga", "score": 8.56, "synopsis": "Natsume Yuujinchou San follows Takashi Natsume, a boy who is able to see youkai. Natsume and his bodyguard Madara, nicknamed Nyanko-sensei, continue on their quest to release youkai from their contracts in the \"Book of Friends.\"\n\nNatsume comes to terms with his ability to see youkai and stops thinking of it as a curse. As he spends more time with his human and youkai friends, he realizes how much he values them both and decides he doesn't have to choose between the spirit and human worlds to be happy.\n\n[Written by MAL Rewrite]"}, {"id": 40776, "name_en": "Haikyu!! To the Top 2nd-cour", "name_jp": "ハイキュー TO THE TOP 第2クール", "image_url": "https://cdn.myanimelist.net/images/anime/1453/106768.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2020, "episodes": 12, "source": "Manga", "score": 8.56, "synopsis": "Once called a fallen powerhouse and known as \"Flightless Crows,\" Karasuno High School has finally taken flight at nationals. With a comprehensive performance against Tsubakihara Academy in their first match, the team is now facing its toughest opponent yet: the runners-up of the last Spring Tournament, Inarizaki High School. Furthermore, dealing with the formidable twin Miya brothers only makes things more difficult for Karasuno.\n\nAs soon as the match begins, Karasuno is overwhelmed by all the noise and jeers from the supporters of Inarizaki High but rekindles its strength thanks to its own loyal fans. Karasuno also gains some momentum by utilizing an attack centered on Shouyou Hinata, but the eccentric play of Atsumu and Osamu Miya delivers an unexpected blow that leaves their opponent astounded.\n\nThings are bound to get intense as the match progresses between these two teams. Will Karasuno be able to defeat Inarizaki High and overcome the hurdles that threaten its pursuit to the top?\n\n[Written by MAL Rewrite]"}, {"id": 36098, "name_en": "I Want To Eat Your Pancreas", "name_jp": "君の膵臓をたべたい", "image_url": "https://cdn.myanimelist.net/images/anime/1768/93291.jpg", "genres": ["Drama", "Romance"], "themes": ["School"], "demographics": [], "studios": ["Studio VOLN"], "year": 2018, "episodes": 1, "source": "Novel", "score": 8.55, "synopsis": "The aloof protagonist: a bookworm who is deeply detached from the world he resides in. He has no interest in others and is firmly convinced that nobody has any interest in him either. His story begins when he stumbles across a handwritten book, titled Living with Dying. He soon identifies it as a secret diary belonging to his popular, bubbly classmate Sakura Yamauchi. She then confides in him about the pancreatic disease she is suffering from and that her time left is finite. Only her family knows about her terminal illness; not even her best friends are aware. Despite this revelation, he shows zero sympathy for her plight, but caught in the waves of Sakura's persistent buoyancy, he eventually concedes to accompanying her for her remaining days.\n\nAs the pair of polar opposites interact, their connection strengthens, interweaving through their choices made with each passing day. Her apparent nonchalance and unpredictability disrupts the protagonist's impassive flow of life, gradually opening his heart as he discovers and embraces the true meaning of living.\n\n[Written by MAL Rewrite]"}, {"id": 30484, "name_en": "Steins;Gate 0", "name_jp": "シュタインズ・ゲート ゼロ", "image_url": "https://cdn.myanimelist.net/images/anime/1375/93521.jpg", "genres": ["Drama", "Sci-Fi", "Suspense"], "themes": ["Time Travel"], "demographics": [], "studios": ["White Fox"], "year": 2018, "episodes": 23, "source": "Visual novel", "score": 8.55, "synopsis": "The eccentric, self-proclaimed mad scientist Rintarou Okabe has become a shell of his former self. Depressed and traumatized after failing to rescue his friend Makise Kurisu, he has decided to forsake his mad scientist alter ego and live as an ordinary college student. Surrounded by friends who know little of his time travel experiences, Okabe spends his days trying to forget the horrors of his adventures alone. \n\nWhile working as a receptionist at a college technology forum, Okabe meets the short, spunky Maho Hiyajo, who \nlater turns out to be the interpreter at the forum's presentation, conducted by Professor Alexis Leskinen. In front of a stunned crowd, Alexis and Maho unveil Amadeus—a revolutionary AI capable of storing a person's memories and creating a perfect simulation of that person complete with their personality and quirks. Meeting with Maho and Alexis after the presentation, Okabe learns that the two were Kurisu's colleagues in university, and that they have simulated her in Amadeus. Hired by Alexis to research the simulation's behavior, Okabe is given the chance to interact with the shadow of a long-lost dear friend. Dangerously tangled in the past, Okabe must face the harsh reality and carefully maneuver around the disastrous consequences that come with disturbing the natural flow of time.\n\n[Written by MAL Rewrite]"}, {"id": 49701, "name_en": "Battle Through the Heavens: Three Year Agreement", "name_jp": "斗破苍穹 三年之约", "image_url": "https://cdn.myanimelist.net/images/anime/1029/118861.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Motion Magic"], "year": 2021, "episodes": 13, "source": "Web novel", "score": 8.55, "synopsis": "Three years ago, Nalan Yanran, the young sect leader of the Yunlan Sect, rashly broke off her engagement. Feeling deeply humiliated, Xiao Yan made a three-year pact with Nalan Yanran. For this pact, Xiao Yan cultivated tirelessly, enduring trials from the Magical Beast Mountain Range to the Tagor Desert, from alchemy to the excruciating pain of absorbing a strange fire—all to prove the saying: \"Thirty years east of the river, thirty years west of the river; never underestimate a young man's potential.\" Now, the men of the Xiao family will rise to the challenge, never retreating. Xiao Yan's path of growth continues…\n\n(Source: Tencent, translated)"}, {"id": 7785, "name_en": "The Tatami Galaxy", "name_jp": "四畳半神話大系", "image_url": "https://cdn.myanimelist.net/images/anime/1633/123689.jpg", "genres": ["Award Winning", "Comedy", "Mystery", "Romance", "Suspense"], "themes": ["Adult Cast", "Psychological", "Time Travel"], "demographics": [], "studios": ["Madhouse"], "year": 2010, "episodes": 11, "source": "Novel", "score": 8.55, "synopsis": "One autumn evening at a mysterious ramen stand behind the Shimogamo Shrine, a lonely third-year college student bumps into a man with an eggplant-shaped head who calls himself a god of matrimony. Meeting this man causes the student to reflect upon his past two years at college—two years bitterly spent trying to break up couples on campus with his only friend Ozu, a ghoulish-looking man seemingly set on making his life as miserable as possible. Resolving to make the most out of the rest of his college life, the student attempts to ask out the unsociable but kind-hearted underclassman Akashi, yet fails to follow through, prompting him to regret not living out his college life differently. As soon as this thought passes through his head, however, he is hurtled through time and space to the beginning of his years at college and given another chance to live his life.\n\nSurreal, artistic, and mind-bending, Yojouhan Shinwa Taikei chronicles the misadventures of a young man on a journey to make friends, find love, and experience the rose-colored campus life he always dreamed of.\n\n[Written by MAL Rewrite]"}, {"id": 52034, "name_en": "[Oshi No Ko]", "name_jp": "【推しの子】", "image_url": "https://cdn.myanimelist.net/images/anime/1812/134736.jpg", "genres": ["Award Winning", "Drama"], "themes": ["Reincarnation", "Showbiz"], "demographics": ["Seinen"], "studios": ["Doga Kobo"], "year": 2023, "episodes": 11, "source": "Manga", "score": 8.54, "synopsis": "In the entertainment world, celebrities often show exaggerated versions of themselves to the public, concealing their true thoughts and struggles beneath elaborate lies. Fans buy into these fabrications, showering their idols with undying love and support, until something breaks the illusion. Sixteen-year-old rising star Ai Hoshino of pop idol group B Komachi has the world captivated; however, when she announces a hiatus due to health concerns, the news causes many to become worried.\n\nAs a huge fan of Ai, gynecologist Gorou Amemiya cheers her on from his countryside medical practice, wishing he could meet her in person one day. His wish comes true when Ai shows up at his hospital—not sick, but pregnant with twins! While the doctor promises Ai to safely deliver her children, he wonders if this encounter with the idol will forever change the nature of his relationship with her.\n\n[Written by MAL Rewrite]"}, {"id": 28735, "name_en": "Showa Genroku Rakugo Shinju", "name_jp": "昭和元禄落語心中", "image_url": "https://cdn.myanimelist.net/images/anime/1354/124768.jpg", "genres": ["Drama", "Romance"], "themes": ["Adult Cast", "Historical", "Love Polygon", "Performing Arts"], "demographics": ["Josei"], "studios": ["Studio Deen"], "year": 2016, "episodes": 13, "source": "Manga", "score": 8.54, "synopsis": "Yotarou is a former yakuza member fresh out of prison and fixated on just one thing: rather than return to a life of crime, the young man aspires to take to the stage of rakugo, a traditional Japanese form of comedic storytelling. Inspired during his incarceration by the performance of distinguished practitioner Yakumo Yuurakutei, he sets his mind on meeting the man who changed his life. After hearing Yotarou's desperate appeal for his mentorship, Yakumo is left with no choice but to accept his very first apprentice.\n\nAs he eagerly begins his training, Yotarou meets Konatsu, an abrasive young woman who has been under Yakumo's care ever since her beloved father Sukeroku Yuurakutei, another prolific rakugo performer, passed away. Through her hidden passion, Yotarou is drawn to Sukeroku's unique style of rakugo despite learning under contrasting techniques. Upon seeing this, old memories and feelings return to Yakumo who reminisces about a much earlier time when he made a promise with his greatest rival.\n\nShouwa Genroku Rakugo Shinjuu is a story set in both the past and present, depicting the art of rakugo, the relationships it creates, and the lives and hearts of those dedicated to keeping the unique form of storytelling alive.\n\n[Written by MAL Rewrite]"}, {"id": 25777, "name_en": "Attack on Titan Season 2", "name_jp": "進撃の巨人 Season2", "image_url": "https://cdn.myanimelist.net/images/anime/4/84177.jpg", "genres": ["Action", "Drama", "Suspense"], "themes": ["Gore", "Military", "Survival"], "demographics": ["Shounen"], "studios": ["Wit Studio"], "year": 2017, "episodes": 12, "source": "Manga", "score": 8.54, "synopsis": "For centuries, humanity has been hunted by giant, mysterious predators known as the Titans. Three mighty walls—Wall Maria, Rose, and Sheena—provided peace and protection for humanity for over a hundred years. That peace, however, was shattered when the Colossal Titan and Armored Titan appeared and destroyed the outermost wall, Wall Maria. Forced to retreat behind Wall Rose, humanity waited with bated breath for the Titans to reappear and destroy their safe haven once more.\n\nIn Shingeki no Kyojin Season 2, Eren Yeager and others of the 104th Training Corps have just begun to become full members of the Survey Corps. As they ready themselves to face the Titans once again, their preparations are interrupted by the invasion of Wall Rose—but all is not as it seems as more mysteries are unraveled. As the Survey Corps races to save the wall, they uncover more about the invading Titans and the dark secrets of their own members.\n\n[Written by MAL Rewrite]"}, {"id": 11741, "name_en": "Fate/Zero Season 2", "name_jp": "フェイト/ゼロ 2ndシーズン", "image_url": "https://cdn.myanimelist.net/images/anime/1522/117645.jpg", "genres": ["Action", "Fantasy"], "themes": ["Urban Fantasy"], "demographics": [], "studios": ["ufotable"], "year": 2012, "episodes": 12, "source": "Light novel", "score": 8.54, "synopsis": "As the Fourth Holy Grail War rages on with no clear victor in sight, the remaining Servants and their Masters are called upon by Church supervisor Risei Kotomine, in order to band together and confront an impending threat that could unravel the Grail War and bring about the destruction of Fuyuki City. The uneasy truce soon collapses as Masters demonstrate that they will do anything in their power, no matter how despicable, to win.\n\nSeeds of doubt are sown between Kiritsugu Emiya and Saber, his Servant, as their conflicting ideologies on heroism and chivalry clash. Meanwhile, an ominous bond forms between Kirei Kotomine, who still seeks to find his purpose in life, and one of the remaining Servants. As the countdown to the end of the war reaches zero, the cost of winning begins to blur the line between victory and defeat.\n\n[Written by MAL Rewrite]"}, {"id": 40456, "name_en": "Demon Slayer: Kimetsu no Yaiba - The Movie: Mugen Train", "name_jp": "劇場版 鬼滅の刃 無限列車編", "image_url": "https://cdn.myanimelist.net/images/anime/1704/106947.jpg", "genres": ["Action", "Supernatural"], "themes": ["Historical"], "demographics": ["Shounen"], "studios": ["ufotable"], "year": 2020, "episodes": 1, "source": "Manga", "score": 8.54, "synopsis": "After a string of mysterious disappearances begin to plague a train, the Demon Slayer Corps' multiple attempts to remedy the problem prove fruitless. To prevent further casualties, the Flame Pillar, Kyoujurou Rengoku, takes it upon himself to eliminate the threat. Accompanying him are some of the Corps' most promising new blood: Tanjirou Kamado, Zenitsu Agatsuma, and Inosuke Hashibira, who all hope to witness the fiery feats of this model demon slayer firsthand.\n\nUnbeknownst to them, the demonic forces responsible for the disappearances have already put their sinister plan in motion. Under this demonic presence, the group must muster every ounce of their willpower and draw their swords to save all two hundred passengers onboard. As things begin to spiral out of control, Tanjirou's resolve and commitment to duty are put to the test.\n\n[Written by MAL Rewrite]"}, {"id": 55791, "name_en": "[Oshi No Ko] Season 2", "name_jp": "【推しの子】第2期", "image_url": "https://cdn.myanimelist.net/images/anime/1006/143302.jpg", "genres": ["Drama"], "themes": ["Reincarnation", "Showbiz"], "demographics": ["Seinen"], "studios": ["Doga Kobo"], "year": 2024, "episodes": 13, "source": "Manga", "score": 8.53, "synopsis": "With the help of producer Masaya Kaburagi, Aquamarine \"Aqua\" Hoshino and Kana Arima have landed the roles of Touki and Tsurugi in Lala Lai Theatrical Company's stage adaptation of the popular manga series Tokyo Blade. Co-starring with them is Aqua's girlfriend, Akane Kurokawa, who plays Touki's fiancée, Princess Saya. Due to the fanbase preferring Tsurugi as Touki's love interest, Saya has made fewer and fewer appearances in the manga, making it difficult for Akane to fully immerse herself in the role. Her struggles are compounded by differences between the play's script and the original work—differences that also frustrate Tokyo Blade's author, Abiko Samejima.\n\nAqua, however, is more concerned with his personal goals than he is with the play. He has only one objective in mind: to grow closer to director Toshirou Kindaichi and find out what he knows about Aqua's mother, Ai.\n\n[Written by MAL Rewrite]"}, {"id": 5300, "name_en": "Natsume's Book of Friends Season 2", "name_jp": "続 夏目友人帳", "image_url": "https://cdn.myanimelist.net/images/anime/4/79738.jpg", "genres": ["Slice of Life", "Supernatural"], "themes": ["Iyashikei", "Mythology"], "demographics": ["Shoujo"], "studios": ["Brain's Base"], "year": 2009, "episodes": 13, "source": "Manga", "score": 8.53, "synopsis": "As with its prequel, Zoku Natsume Yuujinchou is about Takashi Natsume, a boy who has had the ability to see youkai since he was young, and his bodyguard youkai Madara, nicknamed Nyanko-sensei. Natsume attempts to return names written in his \"Book of Friends\" (which he inherited from his grandmother Reiko) to youkai in his village. Throughout these adventures, he meets some youkai that are friendly, some that want to steal the Book, and some that want to kill him, as Natsume learns about himself and his relationship with these mysterious beings along the way.\n\n[Written by MAL Rewrite]"}, {"id": 40417, "name_en": "Fruits Basket 2nd Season", "name_jp": "フルーツバスケット 2nd season", "image_url": "https://cdn.myanimelist.net/images/anime/1972/111635.jpg", "genres": ["Drama", "Romance", "Supernatural"], "themes": ["School"], "demographics": ["Shoujo"], "studios": ["TMS Entertainment"], "year": 2020, "episodes": 25, "source": "Manga", "score": 8.53, "synopsis": "A year has passed since Tooru Honda began living in the Souma residence, and she has since created stronger relationships with its inhabitants Shigure, Kyou, and Yuki. She has also grown closer to the rest of the Souma family and has become familiar with their ancestral secret, having helped them with many of their personal issues. The closer Tooru gets, however, the more she begins to realize that their secret holds a darker truth than she first presumed.\n\nSummer is approaching and Tooru is invited to spend her days with the Soumas, mainly Kyou and Yuki. Tooru wishes for an easy-going vacation, but her close relationships with the two boys and the rest of the Soumas may prove to cause trouble. As they grow more intimate, their carefree time together is hindered by older hardships and feelings from the past that begin to resurface. The Eternal Banquet also dawns on the members of the zodiac, and they must tend to their duties alongside the unnerving head of the family, Akito Souma.\n\nWith the banquet approaching and a plethora of feelings to be solved, will Tooru's life with the Soumas remain peaceful, or will she find herself in a situation from which she cannot escape?\n\n[Written by MAL Rewrite]"}, {"id": 61322, "name_en": "Dr. Stone: Science Future Part 2", "name_jp": "Dr.STONE SCIENCE FUTURE 第2クール", "image_url": "https://cdn.myanimelist.net/images/anime/1785/151710.jpg", "genres": ["Adventure", "Comedy"], "themes": [], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2025, "episodes": 12, "source": "Manga", "score": 8.52, "synopsis": "After a strategic maneuver, Senkuu Ishigami's team stalls Dr. Xeno's more scientifically advanced forces. A ceasefire between the two factions allows for the establishment of Corn City in North America, a hope for a future thriving population. Meanwhile, Senkuu and some of his teammates rush toward the Amazon in South America—the epicenter of the petrification beam's impact—to create Superalloy City and progress their spaceship project.\n\nWith dangerous enemies still pursuing Senkuu and his companions, it is a race against time to reach the heart of the dense Amazon. As unexpected allies and scientific creations further fuel this leg of their journey, Senkuu is closer than ever to finally uncovering the mystery behind the petrification phenomenon.\n\n[Written by MAL Rewrite]"}, {"id": 60557, "name_en": "A Record Of Mortal's Journey To Immortality 4th Season", "name_jp": "凡人修仙传 第四季", "image_url": "https://cdn.myanimelist.net/images/anime/1421/153427.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Original Force", "Wonder Cat Animation"], "year": 2025, "episodes": 52, "source": "Web novel", "score": 8.52, "synopsis": "Fourth season of Fanren Xiu Xian Zhuan."}, {"id": 50250, "name_en": "Chiikawa", "name_jp": "ちいかわ", "image_url": "https://cdn.myanimelist.net/images/anime/1783/121944.jpg", "genres": ["Slice of Life"], "themes": [], "demographics": [], "studios": ["Doga Kobo"], "year": 2022, "episodes": 0, "source": "Web manga", "score": 8.52, "synopsis": "What's a Chiikawa? No one really knows, but everyone can see they're small, they're cute, and they're always having a fun time! Join them as they explore their surreal world with the sharp humor and clever antics that make the Chiikawa irresistible.\n\n(Source: HIDIVE, edited)"}, {"id": 21329, "name_en": "Mushi-shi: The Shadow that Devours the Sun", "name_jp": "蟲師 特別篇「日蝕む翳」", "image_url": "https://cdn.myanimelist.net/images/anime/1559/147521.jpg", "genres": ["Adventure", "Mystery", "Slice of Life", "Supernatural"], "themes": ["Adult Cast", "Historical", "Iyashikei"], "demographics": ["Seinen"], "studios": ["Artland"], "year": 2014, "episodes": 1, "source": "Manga", "score": 8.52, "synopsis": "The entire countryside comes to a halt midday to witness a rare solar eclipse that is rumored to allow the average person to see Mushi. Unable to avert their gaze, the air is full of awe and wonder—but those who know the Mushi are preparing for the eclipse's aftermath.\n\nBased on a prediction from Tanyuu Karibusa, the cursed recorder, Mushishi Ginko finds himself in a very unlucky farming village. Immediately following the solar eclipse, a strange black cloud begins to gather in the sky and blocks the sun once more. Suspecting it to be the work of a Mushi known as Hihami, Ginko seeks to liberate the village from perpetual darkness. However, it seems that not all of the villagers are eager to return to the light.\n\n[Written by MAL Rewrite]"}, {"id": 7472, "name_en": "Gintama: The Movie", "name_jp": "劇場版 銀魂 新訳紅桜篇", "image_url": "https://cdn.myanimelist.net/images/anime/4/28803.jpg", "genres": ["Action", "Comedy", "Sci-Fi"], "themes": ["Historical", "Parody", "Samurai"], "demographics": ["Shounen"], "studios": ["Sunrise"], "year": 2010, "episodes": 1, "source": "Manga", "score": 8.52, "synopsis": "Gintoki and his Yorozuya friends (or rather, employees suffering under labor violations), Shinpachi and Kagura, continue to scrape by in the futuristic, alien-infested city of Edo. They take on whatever work they can find while trying not to get involved in anything too dangerous. But when Katsura, the leader of the Joui rebels and Gintoki's long-time acquaintance, disappears after being brutally attacked by an unknown assassin, Shinpachi and Kagura begin an investigation into his whereabouts and the identity of the assailant. Meanwhile, Gintoki takes on a seemingly unrelated job: the blacksmith Tetsuya requests that Gin recover a strange and powerful sword called the Benizakura which was recently stolen.\n\nAs the two investigations gradually intersect, the Yorozuya crew find themselves in the midst of a major conspiracy that hinges on the sinister nature of the Benizakura sword. Gintoki resolves to take the fight directly to the enemy headquarters, and together with a few unexpected allies, sets out on one of his most perilous jobs yet.\n\n[Written by MAL Rewrite]"}, {"id": 12365, "name_en": "Bakuman. Season 3", "name_jp": "バクマン。", "image_url": "https://cdn.myanimelist.net/images/anime/6/41845.jpg", "genres": ["Comedy", "Drama", "Romance"], "themes": ["Otaku Culture"], "demographics": ["Shounen"], "studios": ["J.C.Staff"], "year": 2012, "episodes": 25, "source": "Manga", "score": 8.52, "synopsis": "Onto their third serialization, manga duo Moritaka Mashiro and Akito Takagi—also known by their pen name, Muto Ashirogi—are ever closer to their dream of an anime adaption. However, the real challenge is only just beginning: if they are unable to compete with the artist Eiji Niizuma in the rankings within the span of six months, they will be canceled. To top it off, numerous rivals are close behind and declaring war. They don't even have enough time to spare thinking about an anime!\n\nIn Bakuman. 3rd Season, Muto Ashirogi must find a way to stay atop the colossal mountain known as the Shounen Jack rankings. With new problems and new assistants, the pair continue to strive for their dream.\n\n[Written by MAL Rewrite]"}, {"id": 205, "name_en": "Samurai Champloo", "name_jp": "サムライチャンプルー", "image_url": "https://cdn.myanimelist.net/images/anime/1370/135212.jpg", "genres": ["Action", "Adventure", "Comedy"], "themes": ["Historical", "Samurai"], "demographics": [], "studios": ["Manglobe"], "year": 2004, "episodes": 26, "source": "Original", "score": 8.52, "synopsis": "Fuu Kasumi is a young and clumsy waitress who spends her days peacefully working in a small teahouse. That is, until she accidentally spills a drink all over one of her customers! With a group of samurai now incessantly harassing her, Fuu desperately calls upon another samurai in the shop, Mugen, who quickly defeats them with his wild fighting technique, utilizing movements reminiscent to that of breakdancing. Unfortunately, Mugen decides to pick a fight with the unwilling ronin Jin, who wields a more precise and traditional style of swordfighting, and the latter proves to be a formidable opponent. The only problem is, they end up destroying the entire shop as well as accidentally killing the local magistrate's son.\n\nFor their crime, the two samurai are captured and set to be executed. However, they are rescued by Fuu, who hires the duo as her bodyguards. Though she no longer has a place to return to, the former waitress wishes to find a certain samurai who smells of sunflowers and enlists the help of the now exonerated pair to do so. Despite initially disapproving of this idea, the two eventually agree to assist the girl in her quest; thus, the trio embark upon an adventure to find this mysterious warrior—that is, if Fuu can keep Mugen and Jin from killing each other.\n\nSet in an alternate Edo Period of Japan, Samurai Champloo follows the journey of these three eccentric individuals in an epic quest full of action, comedy, and dynamic sword fighting, all set to the beat of a unique hip-hop infused soundtrack.\n\n[Written by MAL Rewrite]"}, {"id": 40748, "name_en": "Jujutsu Kaisen", "name_jp": "呪術廻戦", "image_url": "https://cdn.myanimelist.net/images/anime/1171/109222.jpg", "genres": ["Action", "Award Winning", "Supernatural"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["MAPPA"], "year": 2020, "episodes": 24, "source": "Manga", "score": 8.52, "synopsis": "Idly indulging in baseless paranormal activities with the Occult Club, high schooler Yuuji Itadori spends his days at either the clubroom or the hospital, where he visits his bedridden grandfather. However, this leisurely lifestyle soon takes a turn for the strange when he unknowingly encounters a cursed item. Triggering a chain of supernatural occurrences, Yuuji finds himself suddenly thrust into the world of Curses—dreadful beings formed from human malice and negativity—after swallowing the said item, revealed to be a finger belonging to the demon Sukuna Ryoumen, the King of Curses.\n\nYuuji experiences first-hand the threat these Curses pose to society as he discovers his own newfound powers. Introduced to the Tokyo Prefectural Jujutsu High School, he begins to walk down a path from which he cannot return—the path of a Jujutsu sorcerer.\n\n[Written by MAL Rewrite]"}, {"id": 53273, "name_en": "JoJo's Bizarre Adventure: Stone Ocean Part 3", "name_jp": "ジョジョの奇妙な冒険 ストーンオーシャン", "image_url": "https://cdn.myanimelist.net/images/anime/1233/128920.jpg", "genres": ["Action", "Adventure"], "themes": ["Super Power"], "demographics": ["Shounen"], "studios": ["David Production"], "year": 2022, "episodes": 14, "source": "Manga", "score": 8.51, "synopsis": "After finally escaping the confines of Green Dolphin Street Jail, Jolyne Kuujou—alongside her companions Ermes Costello and Emporio Alniño—pursues the villainous priest Enrico Pucci across the state of Florida. Jolyne's allies, Weather Report and Narciso Anasui, struggle to catch up with her in order to help bring an end to Pucci's plot. As both parties pursue the priest, they must battle against Pucci's band of enemy Stand users.\n\nWhile Jolyne's comrades fight for their lives, Pucci races to the Kennedy Space Center. There he hopes to enact his ultimate goal, one he believes God has entrusted to him. He aims to fulfill the will of the Joestars' blood enemy Dio Brando and—by robbing humanity of free will and making them slaves to fate—to create a world where all humans are blissfully happy.\n\nUnable to rely on the aid of her comatose father Joutarou, Jolyne must weaponize all she has learned in prison to confront Pucci in a climactic battle while the world itself hangs in the balance.\n\n[Written by MAL Rewrite]"}, {"id": 35843, "name_en": "Gintama. Slip Arc", "name_jp": "銀魂。ポロリ編", "image_url": "https://cdn.myanimelist.net/images/anime/11/88325.jpg", "genres": ["Action", "Comedy", "Sci-Fi"], "themes": ["Gag Humor", "Historical", "Parody", "Samurai"], "demographics": ["Shounen"], "studios": ["Bandai Namco Pictures"], "year": 2017, "episodes": 13, "source": "Manga", "score": 8.51, "synopsis": "Following the grim events of Iga, Kokujou Island, Rakuyou, and multiple fruitless confrontations with the Tenshouin Naraku and Tendoshuu, Gintama. Porori-hen takes its viewers on a trip down memory lane to when Yorozuya were mostly doing what they did best—odd jobs. The great space hunter Umibouzu has returned to Edo and is livid when he finds out that his daughter Kagura has a boyfriend. He blames Gintoki for being an incompetent guardian, but has the time finally come for him to let go of his daughter?\n\nBack with shameless parodies, risqué humor, and lively camaraderie, Gintoki, Kagura, and Shinpachi are faced with unforeseen situations that manage to be both hilarious and emotionally stirring.\n\n[Written by MAL Rewrite]"}, {"id": 801, "name_en": "Ghost in the Shell: Stand Alone Complex 2nd GIG", "name_jp": "攻殻機動隊 S.A.C. 2nd GIG", "image_url": "https://cdn.myanimelist.net/images/anime/1646/135390.jpg", "genres": ["Mystery", "Sci-Fi"], "themes": ["Adult Cast", "Detective", "Mecha", "Military"], "demographics": ["Seinen"], "studios": ["Production I.G"], "year": 2004, "episodes": 26, "source": "Manga", "score": 8.51, "synopsis": "Following the closure of the \"Laughing Man\" case, Section 9 is re-established by Japan's newly elected Prime Minister, Youko Kayabuki, to combat the persistent threat of cyber-terrorism.\n\nA group calling themselves \"The Individual Eleven\" has begun committing acts of terror across Japan. While Motoko Kusanagi, Daisuke Aramaki, Batou, and the other members of Section 9 investigate this new menace, the Japanese government faces a separate crisis, as foreign refugees displaced by the Third World War seek asylum in Japan. But as the members of the special-ops team continually encounter Gouda Kazundo—a leading member of the Cabinet Intelligence Service—in their hunt, they begin to suspect that he may be involved, and that the events of the refugee crisis and The Individual Eleven may be more connected than they realize...\n\n[Written by MAL Rewrite]"}, {"id": 59986, "name_en": "Grand Blue Dreaming Season 2", "name_jp": "ぐらんぶる Season 2", "image_url": "https://cdn.myanimelist.net/images/anime/1108/150583.jpg", "genres": ["Comedy"], "themes": ["Adult Cast", "Gag Humor"], "demographics": ["Seinen"], "studios": ["Zero-G", "Liber"], "year": 2025, "episodes": 12, "source": "Manga", "score": 8.5, "synopsis": "Three months have passed since Iori Kitahara began living above Grand Blue, his uncle's scuba diving shop, and joined his university's Diving Club. Alongside the other members, he has spent his days drinking and recklessly having fun. However, when Iori's sister, Shiori, hears about his behavior, she decides to take matters into her own hands—and bring him back home to make him take over their family's inn.\n\nDespite her best efforts, Shiori temporarily gives up as she sees how Iori has grown attached to Grand Blue and his friends. As Iori and the rest of the Diving Club continue to party like there is no tomorrow, they end up in all kinds of bizarrely amusing situations, still somehow managing to hone their scuba diving skills!\n\n[Written by MAL Rewrite]"}, {"id": 38474, "name_en": "Laid-Back Camp Season 2", "name_jp": "ゆるキャン△ SEASON2", "image_url": "https://cdn.myanimelist.net/images/anime/1255/110636.jpg", "genres": ["Slice of Life"], "themes": ["CGDCT", "Iyashikei"], "demographics": [], "studios": ["C-Station"], "year": 2021, "episodes": 13, "source": "Manga", "score": 8.5, "synopsis": "Having spent Christmas camping with her new friends, Rin Shima embarks on a solo-camping trip to see the New Year sunrise by the sea. All goes according to plan until unforeseen weather blocks the roads back home, making a return trip impossible. Rin, who is now stranded for a few days, is invited by Nadeshiko Kagamihara to stay at her grandmother's house. \n\nWhat is supposed to be a two-day trip becomes an extended period of sightseeing and new experiences for Rin, and she encounters some new and old faces along the way. Yuru Camp△ Season 2 continues the story of Rin, Nadeshiko, and their friends as they further explore the joys of camping.\n\n[Written by MAL Rewrite]"}, {"id": 11981, "name_en": "Puella Magi Madoka Magica the Movie: Rebellion", "name_jp": "劇場版 魔法少女まどか☆マギカ 叛逆の物語", "image_url": "https://cdn.myanimelist.net/images/anime/5/54231.jpg", "genres": ["Award Winning", "Drama", "Suspense"], "themes": ["Mahou Shoujo", "Psychological"], "demographics": [], "studios": ["Shaft"], "year": 2013, "episodes": 1, "source": "Original", "score": 8.5, "synopsis": "The young girls of Mitakihara happily live their lives, occasionally fighting off evil, but otherwise going about their peaceful, everyday routines. However, Homura Akemi feels that something is wrong with this unusually pleasant atmosphere—though the others remain oblivious, she can't help but suspect that there is more to what is going on than meets the eye: someone who should not exist is currently present to join in on their activities.\n\nMahou Shoujo Madoka★Magica Movie 3: Hangyaku no Monogatari follows Homura in her struggle to uncover the painful truth behind the mysterious circumstances, as she selfishly and desperately fights for the sake of her undying love in this despair-ridden conclusion to the story of five magical girls.\n\n[Written by MAL Rewrite]"}, {"id": 35839, "name_en": "A Place Further Than The Universe", "name_jp": "宇宙よりも遠い場所", "image_url": "https://cdn.myanimelist.net/images/anime/6/89879.jpg", "genres": ["Adventure", "Comedy", "Drama"], "themes": ["CGDCT"], "demographics": [], "studios": ["Madhouse"], "year": 2018, "episodes": 13, "source": "Original", "score": 8.5, "synopsis": "Filled with an overwhelming sense of wonder for the world around her, Mari Tamaki has always dreamt of what lies beyond the reaches of the universe. However, despite harboring such large aspirations on the inside, her fear of the unknown and anxiety over her own possible limitations have always held her back from chasing them. But now, in her second year of high school, Mari is more determined than ever to not let any more of her youth go to waste. Still, her fear continues to prevent her from taking that ambitious step forward—that is, until she has a chance encounter with a girl who has grand dreams of her own.\n\nSpurred by her mother's disappearance, Shirase Kobuchizawa has been working hard to fund her trip to Antarctica. Despite facing doubt and ridicule from virtually everyone, Shirase is determined to embark on this expedition to search for her mother in a place further than the universe itself. Inspired by Shirase's resolve, Mari jumps at the chance to join her. Soon, their efforts attract the attention of the bubbly Hinata Miyake, who is eager to stand out, and Yuzuki Shiraishi, a polite girl from a high class background. Together, the four spirited girls set sail toward the frozen south, all in search of something great.\n\n[Written by MAL Rewrite]"}, {"id": 4282, "name_en": "The Garden of Sinners Chapter 5: Paradox Spiral", "name_jp": "劇場版 空の境界 the Garden of sinners 第五章『矛盾螺旋』", "image_url": "https://cdn.myanimelist.net/images/anime/4/75851.jpg", "genres": ["Action", "Drama", "Fantasy", "Mystery", "Romance", "Suspense"], "themes": ["Urban Fantasy"], "demographics": [], "studios": ["ufotable"], "year": 2008, "episodes": 1, "source": "Light novel", "score": 8.5, "synopsis": "In November 1998, a double homicide occurs at the newly constructed Ogawa apartment complex in the heart of Mifune City. The murderer, Tomoe Enjou, has fled in a panic. To his astonishment, he is not pursued by the police and news of the incident has not been reported through media outlets. After Shiki Ryougi defends Tomoe from a group of thugs, she allows him to use her residence as a hideout. However, a few days later, Tomoe is shaken to discover that his mother is alive, even though he is convinced that he killed her.\n \nCoincidentally, Mikiya Kokutou is investigating a tip that his associate Touko Aozaki receives regarding the murder at the unique apartment complex. As he uncovers more information about the incident, Mikiya takes a particular interest in Tomoe. Deciding to investigate him further, Mikiya soon discovers the disturbing truth of the foreboding Ogawa complex.\n\nThe fifth installment of the Kara no Kyoukai film series, Mujun Rasen combines an intricately constructed mystery with established themes and characters to produce a dark, thought-provoking story.\n\n[Written by MAL Rewrite]"}, {"id": 31933, "name_en": "JoJo's Bizarre Adventure: Diamond Is Unbreakable", "name_jp": "ジョジョの奇妙な冒険 ダイヤモンドは砕けない", "image_url": "https://cdn.myanimelist.net/images/anime/3/79156.jpg", "genres": ["Action", "Adventure"], "themes": ["Super Power"], "demographics": ["Shounen"], "studios": ["David Production"], "year": 2016, "episodes": 39, "source": "Manga", "score": 8.5, "synopsis": "The year is 1999. Morioh, a normally quiet and peaceful town, has recently become a hotbed of strange activity. Joutarou Kuujou, now a marine biologist, heads to the mysterious town to meet Jousuke Higashikata. While the two may seem like strangers at first, Jousuke is actually the illegitimate child of Joutarou's grandfather, Joseph Joestar. When they meet, Joutarou realizes that he may have more in common with Jousuke than just a blood relation.\n\nAlong with the mild-mannered Kouichi Hirose and the boisterous Okuyasu Nijimura, the group dedicates themselves to investigating recent disappearances and other suspicious occurrences within Morioh. Aided by the power of Stands, the four men will encounter danger at every street corner, as it is up to them to unravel the town's secrets, before another occurs.\n\n[Written by MAL Rewrite]"}, {"id": 40664, "name_en": "Revue Starlight: The Movie", "name_jp": "劇場版 少女☆歌劇 レヴュースタァライト", "image_url": "https://cdn.myanimelist.net/images/anime/1378/119190.jpg", "genres": ["Drama"], "themes": ["Music", "Performing Arts", "School"], "demographics": [], "studios": ["Kinema Citrus"], "year": 2021, "episodes": 1, "source": "Original", "score": 8.5, "synopsis": "With the arrival of a new generation of talented girls, another lively year at Seishou Music Academy begins. The graduating 99th class is already preparing for the much-anticipated rendition of Starlight, a bittersweet story about the parting of two girls.\n\nIt seems like all students in the Actor Training Department have their post-graduation future figured out—except for the cheerful Karen Aijou. Having fulfilled her promise of performing in a play with Hikari Kagura, her ambitious childhood friend, Karen struggles to find her purpose. What lies beyond their promise is a mystery to Karen, as Hikari was the fuel that kept her passion for performing aflame. Now alone, Karen must confront the reality of stage girls and find her way toward the future—her very own stage.\n\n[Written by MAL Rewrite]"}, {"id": 55830, "name_en": "Fate/strange Fake", "name_jp": "Fate/strange Fake", "image_url": "https://cdn.myanimelist.net/images/anime/1752/150192.jpg", "genres": ["Action", "Fantasy"], "themes": ["Urban Fantasy"], "demographics": [], "studios": ["A-1 Pictures"], "year": 2026, "episodes": 13, "source": "Light novel", "score": 8.49, "synopsis": "Fate/strange Fake centers around an imperfect copy of a Grail War based on the Third Holy Grail War in Fuyuki. Following the conclusion of the third Grail War, a US-based organization with magi distinct from the London-based Mage Association, used data from Fuyuki's Grail War to plan their own ritual. Seventy years later, they used the city of Snowfield as the Sacred Land for their own Grail War. However, they were unable to successfully replicate every aspect of the ritual, resulting in it acting only as an imitation that has lost the Saber class and allowed for the summoning of strange Servants due to the blurring of the definition of a \"hero.\"\n\nRohngall and his pupil, Faldeus, have been dispatched by the Mage's Association to investigate the city and the state of the war. Faldeus, a US organization spy, has Rohngall sniped upon his arrival, despite knowing that Rohngall is a puppet. He declares that their Holy Grail War has been in development and is real, causing a commotion at the Clock Tower, and that he wishes to \"advertise\" the project to the Association.\n\n(Source: MAL News)"}, {"id": 32182, "name_en": "Mob Psycho 100", "name_jp": "モブサイコ100", "image_url": "https://cdn.myanimelist.net/images/anime/8/80356.jpg", "genres": ["Action", "Comedy", "Supernatural"], "themes": [], "demographics": [], "studios": ["Bones"], "year": 2016, "episodes": 12, "source": "Web manga", "score": 8.49, "synopsis": "Eighth-grader Shigeo \"Mob\" Kageyama has tapped into his inner wellspring of psychic prowess at a young age. But the power quickly proves to be a liability when he realizes the potential danger in his skills. Choosing to suppress his power, Mob's only present use for his ability is to impress his longtime crush, Tsubomi, who soon grows bored of the same tricks.\n\nIn order to effectuate control on his skills, Mob enlists himself under the wing of Arataka Reigen, a con artist claiming to be a psychic, who exploits Mob's powers for pocket change. Now, exorcising evil spirits on command has become a part of Mob's daily, monotonous life. However, the psychic energy he exerts is barely the tip of the iceberg; if his vast potential and unrestrained emotions run berserk, a cataclysmic event that would render him completely unrecognizable will be triggered. The progression toward Mob's explosion is rising and attempting to stop it is futile.\n\n[Written by MAL Rewrite]"}, {"id": 44070, "name_en": "Heaven Official's Blessing Special", "name_jp": "天官赐福 特别篇", "image_url": "https://cdn.myanimelist.net/images/anime/1043/110306.jpg", "genres": ["Action", "Adventure", "Drama", "Fantasy"], "themes": ["Historical", "Mythology"], "demographics": [], "studios": ["Haoliners Animation"], "year": 2021, "episodes": 1, "source": "Web novel", "score": 8.49, "synopsis": "Xie Lian and Hua Cheng recount their recent adventures during a quiet night at Puqi Shrine. Amidst the serenity, the Crimson Rain Sought Flower reveals a little more about Xie Lian's true character, and an embarrassing memory reminds him of how much he has changed since his previous ascensions. However, no matter how much time has passed, Xie Lian never fails to believe in the good he sees in the people around him. Though the heavens may ridicule him, his words and actions have earned him the most devout of followers.\n\n[Written by MAL Rewrite]"}, {"id": 40834, "name_en": "Ranking of Kings", "name_jp": "王様ランキング", "image_url": "https://cdn.myanimelist.net/images/anime/1347/117616.jpg", "genres": ["Adventure", "Fantasy"], "themes": [], "demographics": [], "studios": ["Wit Studio"], "year": 2021, "episodes": 23, "source": "Web manga", "score": 8.49, "synopsis": "The people of the kingdom look down on the young Prince Bojji, who can neither hear nor speak. They call him \"The Useless Prince\" while jeering at his supposed foolishness.\n\nHowever, while Bojji may not be physically strong, he is certainly not weak of heart. When a chance encounter with a shadow creature should have left him traumatized, it instead makes him believe that he has found a friend amidst those who only choose to notice his shortcomings. He starts meeting with Kage, the shadow, regularly, to the point where even the otherwise abrasive creature begins to warm up to him.\n\nKage and Bojji's unlikely friendship lays the budding foundations of the prince's journey, one where he intends to conquer his fears and insecurities. Despite the constant ridicule he faces, Bojji resolves to fulfill his desire of becoming the best king he can be.\n\n[Written by MAL Rewrite]"}, {"id": 60544, "name_en": "Ne Zha 2", "name_jp": "哪吒之魔童闹海", "image_url": "https://cdn.myanimelist.net/images/anime/1616/147002.jpg", "genres": ["Action", "Comedy", "Drama", "Fantasy"], "themes": ["Historical"], "demographics": [], "studios": ["October Media"], "year": 2025, "episodes": 1, "source": "Original", "score": 8.48, "synopsis": "After the heavenly lightning, although Ne Zha and Ao Bing survived by becoming Spirits, they would soon dissipate completely. Taiyi plans to rebuild Ne Zha and Ao Bing's mortal bodies with the Seven-colored Precious Lotus. However, during the process of reconstruction, numerous obstacles arise. What will become of the fate of Ne Zha and Ao Bing? \n\n(Source: AMC)"}, {"id": 58222, "name_en": "given The Movie: To the Sea", "name_jp": "映画 ギヴン 海へ", "image_url": "https://cdn.myanimelist.net/images/anime/1663/144261.jpg", "genres": ["Boys Love", "Drama"], "themes": ["Music"], "demographics": [], "studios": ["Lerche"], "year": 2024, "episodes": 1, "source": "Manga", "score": 8.48, "synopsis": "Second part of Given Movie: Hiiragi Mix."}, {"id": 12431, "name_en": "Space Brothers", "name_jp": "宇宙兄弟", "image_url": "https://cdn.myanimelist.net/images/anime/1290/135694.jpg", "genres": ["Comedy"], "themes": ["Adult Cast", "Space", "Workplace"], "demographics": ["Seinen"], "studios": ["A-1 Pictures"], "year": 2012, "episodes": 99, "source": "Manga", "score": 8.48, "synopsis": "On a fateful summer night in 2006, Mutta Nanba and his younger brother Hibito witness what they believe to be a UFO flying toward the Moon. This impressing and unusual phenomenon leads both siblings vowing to become astronauts, with Hibito aiming for the Moon and Mutta, convinced that the eldest brother has to be one step ahead, for Mars.\n\nNow an adult, life hasn't turned out how Mutta had pictured it: he is diligently working in an automotive company, whereas Hibito is on his way to be the very first Japanese man to step on the Moon. However, after losing his job, Mutta is presented with an unexpected opportunity to catch up to his younger brother when the Japanese Aerospace Exploration Agency, commonly known as JAXA, accepts his application to participate in the next astronaut selection. Despite self-doubts about his prospects, Mutta is unwilling to waste this chance of a lifetime, and thus embarks on an ambitious journey to fulfill the promise made 19 years ago.\n\n[Written by MAL Rewrite]"}, {"id": 30276, "name_en": "One-Punch Man", "name_jp": "ワンパンマン", "image_url": "https://cdn.myanimelist.net/images/anime/12/76049.jpg", "genres": ["Action", "Comedy"], "themes": ["Adult Cast", "Parody", "Super Power"], "demographics": ["Seinen"], "studios": ["Madhouse"], "year": 2015, "episodes": 12, "source": "Web manga", "score": 8.48, "synopsis": "The seemingly unimpressive Saitama has a rather unique hobby: being a hero. In order to pursue his childhood dream, Saitama relentlessly trained for three years, losing all of his hair in the process. Now, Saitama is so powerful, he can defeat any enemy with just one punch. However, having no one capable of matching his strength has led Saitama to an unexpected problem—he is no longer able to enjoy the thrill of battling and has become quite bored.\n\nOne day, Saitama catches the attention of 19-year-old cyborg Genos, who witnesses his power and wishes to become Saitama's disciple. Genos proposes that the two join the Hero Association in order to become certified heroes that will be recognized for their positive contributions to society. Saitama, who is shocked that no one knows who he is, quickly agrees. Meeting new allies and taking on new foes, Saitama embarks on a new journey as a member of the Hero Association to experience the excitement of battle he once felt.\n\n[Written by MAL Rewrite]"}, {"id": 37779, "name_en": "The Promised Neverland", "name_jp": "約束のネバーランド", "image_url": "https://cdn.myanimelist.net/images/anime/1830/118780.jpg", "genres": ["Mystery", "Suspense"], "themes": ["Psychological", "Survival"], "demographics": ["Shounen"], "studios": ["CloverWorks"], "year": 2019, "episodes": 12, "source": "Manga", "score": 8.47, "synopsis": "Surrounded by a forest and a gated entrance, the Grace Field House is inhabited by orphans happily living together as one big family, looked after by their \"Mama,\" Isabella. Although they are required to take tests daily, the children are free to spend their time as they see fit, usually playing outside, as long as they do not venture too far from the orphanage—a rule they are expected to follow no matter what. However, all good times must come to an end, as every few months, a child is adopted and sent to live with their new family, never to be heard from again.\n\nHowever, the three oldest siblings have their suspicions about what is actually happening at the orphanage, and they are about to discover the cruel fate that awaits the children living at Grace Field, including the twisted nature of their beloved Mama.\n\n[Written by MAL Rewrite]"}, {"id": 30654, "name_en": "Assassination Classroom Second Season", "name_jp": "暗殺教室　第２期", "image_url": "https://cdn.myanimelist.net/images/anime/8/77966.jpg", "genres": ["Action", "Comedy"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["Lerche"], "year": 2016, "episodes": 25, "source": "Manga", "score": 8.47, "synopsis": "Returning from their summer vacation, the students of Class 3-E at the prestigious Kunugigaoka Middle School find themselves up against unbeatable odds. Faced with the possibility of world annihilation, the students must come up with increasingly elaborate and creative ways to kill their teacher, the cunning yet optimistic and helpful Koro-sensei.\n \nHowever, eliminating Koro-sensei is not the only objective the students need to worry about. Gakuhou Asano, the academy's merciless and cruel principal, seeks to prevent Class 3-E's success by brainwashing his other hard-working pupils into ruthlessly competitive studying machines. Hostility begins to linger in the air as traitors and killers alike attempt to claim the bounty on Koro-sensei's head for themselves.\n \nNagisa Shiota, one of Class 3-E's most skilled assassins, finds himself in the middle of the conflict. While he works to maintain his academic standing and prevent the end of the world, domestic affairs jeopardize his place in Class 3-E. Together with his dedicated classmates, he must now face the threats head-on.\n\n[Written by MAL Rewrite]"}, {"id": 3297, "name_en": "Aria the Origination", "name_jp": "ARIA The ORIGINATION", "image_url": "https://cdn.myanimelist.net/images/anime/1431/132867.jpg", "genres": ["Sci-Fi", "Slice of Life"], "themes": ["Iyashikei"], "demographics": ["Shounen"], "studios": ["HAL Film Maker"], "year": 2008, "episodes": 13, "source": "Manga", "score": 8.47, "synopsis": "In the 24th century on the planet Aqua, three girls—Akari Mizunashi, Alice Carroll, and Aika S. Granzchesta—continue to work hard toward achieving their goal of becoming Prima Undines: professional tour guide gondoliers. Luckily, the girls have the guidance of the three best Prima Undines in Neo-Venezia—Alicia Florence, Athena Glory, and Akira E. Ferrari—who are known as the \"Water Fairies\" in honor of their skill. With their help, the young apprentices train hard and work to overcome any situations that they find themselves in.\n\nAs these three young girls experience both the joys and hardships of life, they do their best to improve as tour gondoliers in Neo-Venezia, a terraformed replica of Venice.\n\n[Written by MAL Rewrite]"}, {"id": 47194, "name_en": "Summer Time Rendering", "name_jp": "サマータイムレンダ", "image_url": "https://cdn.myanimelist.net/images/anime/1120/120796.jpg", "genres": ["Mystery", "Supernatural", "Suspense"], "themes": ["Time Travel"], "demographics": ["Shounen"], "studios": ["OLM"], "year": 2022, "episodes": 25, "source": "Manga", "score": 8.47, "synopsis": "Since the death of his parents, Shinpei Ajiro had lived with the Kofune family and their two daughters—Mio and Ushio. Although he then left his home island to continue his education in Tokyo, Shinpei returns after Ushio tragically drowns during the attempted rescue of a little girl. During the funeral, his best friend informs him about bruises found around Ushio's neck, casting doubt over the cause of her death.\n\nSuspecting a murder has taken place, Shinpei reevaluates recent events, but strange incidents only continue to transpire. Disappearing people and other unexplainable occurrences lead Mio to recall an old folktale referring to entities called \"Shadows,\" which may not be entirely fantasy. Supposedly, an encounter with one's Shadow foretells the person's impending demise.\n\nFacing the dark side of Hitogashima Island, Shinpei stands against his grim fate to fulfill Ushio's final will—to protect Mio.\n\n[Written by MAL Rewrite]"}, {"id": 10165, "name_en": "Nichijou - My Ordinary Life", "name_jp": "日常", "image_url": "https://cdn.myanimelist.net/images/anime/3/75617.jpg", "genres": ["Comedy"], "themes": ["Gag Humor", "School"], "demographics": ["Shounen"], "studios": ["Kyoto Animation"], "year": 2011, "episodes": 26, "source": "Manga", "score": 8.47, "synopsis": "Nichijou primarily focuses on the daily antics of a trio of childhood friends—high school girls Mio Naganohara, Yuuko Aioi and Mai Minakami—whose stories soon intertwine with the young genius Hakase Shinonome, her robot caretaker Nano, and their talking cat Sakamoto. With every passing day, the lives of these six, as well as of the many people around them, experience both the calms of normal life and the insanity of the absurd. Walking to school, being bitten by a talking crow, spending time with friends, and watching the principal suplex a deer: they are all in a day's work in the extraordinary everyday lives of those in Nichijou. \n\n[Written by MAL Rewrite]"}, {"id": 63019, "name_en": "Love Through a Prism", "name_jp": "プリズム輪舞曲〈ロンド〉", "image_url": "https://cdn.myanimelist.net/images/anime/1862/153881.jpg", "genres": ["Drama", "Romance"], "themes": ["Adult Cast", "Historical", "Visual Arts"], "demographics": [], "studios": ["Wit Studio"], "year": 2026, "episodes": 20, "source": "Original", "score": 8.46, "synopsis": "In early-1900s London, Lili Ichijouin, a Japanese exchange student travels to England to enroll in the prestigious Saint Thomas Art Academy. Determined to become a painter, she arrives under pressure: her parents expect her to rise to the top of her class within six months or return home.\n\nLili's drive intensifies when she encounters Kit Church, a gifted aristocratic student known for his talent and detachment. While Kit has devoted himself entirely to painting, Lili's sincerity and resilience begin to shift something in him. The two share little beyond their love for art, yet their relationship quickly becomes central to life at the academy.\n\n(Source: Netflix, edited)"}, {"id": 38450, "name_en": "The Master of Diabolism 2", "name_jp": "魔道祖师 羡云篇", "image_url": "https://cdn.myanimelist.net/images/anime/1404/106707.jpg", "genres": ["Action", "Adventure", "Drama", "Fantasy", "Mystery"], "themes": ["Historical", "Mythology", "Reincarnation"], "demographics": [], "studios": ["B.CMAY PICTURES"], "year": 2019, "episodes": 8, "source": "Web novel", "score": 8.46, "synopsis": "Continuing his masquerade as the deranged lunatic from the Lanling Jin Clan, Wei Wuxian resides in the Cloud Recesses while his former cultivation classmate, Lan Wangji, searches for answers about the demonic severed arm they have in custody. With an overwhelming dark energy emanating from the arm, the two are forced to work together in order to keep it contained. However, the demonic arm is not the only dark force lurking in the region, and as spiritual tensions rise in the mountains of the Gusu Lan Clan, it is up to the two of them to try and restore the natural order.\n\nThe story of Wei Wuxian's fall from grace continues as more light is shed on his descent into the path of demonic cultivation. The demonic arm only further strains his mischievous spirit. This is the time for him to prove that he has truly broken free from the forbidden path and is not the maniacal sorcerer that everyone remembers him to be.\n\n[Written by MAL Rewrite]"}, {"id": 33049, "name_en": "Fate/stay night: Heaven's Feel - II. Lost Butterfly", "name_jp": "劇場版「Fate/stay night [Heaven's Feel] II.lost butterfly」", "image_url": "https://cdn.myanimelist.net/images/anime/1974/98158.jpg", "genres": ["Action", "Fantasy"], "themes": ["Urban Fantasy"], "demographics": [], "studios": ["ufotable"], "year": 2019, "episodes": 1, "source": "Visual novel", "score": 8.46, "synopsis": "The Fifth Holy Grail War continues, and the ensuing chaos results in higher stakes for all participants. Shirou Emiya continues to participate in the war, aspiring to be a hero of justice who saves everyone. He sets out in search of the truth behind a mysterious dark shadow and its murder spree, determined to defeat it.\n\nMeanwhile, Shinji Matou sets his own plans into motion, threatening Shirou through his sister Sakura Matou. Shirou and Rin Toosaka battle Shinji, hoping to relieve Sakura from the abuses of her brother. But the ugly truth of the Matou siblings begins to surface, and many dark secrets are exposed.\n\nFate/stay night Movie: Heaven's Feel - II. Lost Butterfly continues to focus on the remaining Masters and Servants as they fight each other in the hopes of obtaining the Holy Grail. However, as darkness arises within Fuyuki City, even the state of their sacred war could be in danger.\n\n[Written by MAL Rewrite]"}, {"id": 37208, "name_en": "The Master of Diabolism", "name_jp": "魔道祖师", "image_url": "https://cdn.myanimelist.net/images/anime/1908/153734.jpg", "genres": ["Action", "Adventure", "Drama", "Fantasy", "Mystery"], "themes": ["Historical", "Mythology", "Reincarnation"], "demographics": [], "studios": ["B.CMAY PICTURES"], "year": 2018, "episodes": 15, "source": "Web novel", "score": 8.46, "synopsis": "Xian: the state of immortality that all cultivators strive to achieve. However, there is a dark energy that lies underneath—the forbidden Mo Dao, or demonic path. Through an unfortunate series of tragedies, this is the path that cultivator Wei Wuxian experiments with during his teachings. His rise in power is accompanied by chaos and destruction, but his reign of terror comes to an abrupt end when the cultivation clans overpower him and he is killed by his closest ally.\n\nThirteen years later, Wei Wuxian is reincarnated in the body of a lunatic and reunited with Lan Wangji, a former classmate of his. This marks the beginning of a supernatural mystery that plagues the clans and threatens to disrupt their everyday life.\n\nMo Dao Zu Shi follows these two men on their mission to unravel the mysteries of the spiritual world. Fighting demons, ghosts, and even other cultivators, the two end up forming a bond that neither of them had ever expected.\n\n[Written by MAL Rewrite]"}, {"id": 6114, "name_en": "Rainbow", "name_jp": "RAINBOW 二舎六房の七人", "image_url": "https://cdn.myanimelist.net/images/anime/9/72697.jpg", "genres": ["Drama", "Suspense"], "themes": ["Delinquents", "Historical"], "demographics": ["Seinen"], "studios": ["Madhouse"], "year": 2010, "episodes": 26, "source": "Manga", "score": 8.46, "synopsis": "Japan, 1955: Mario Minakami has just arrived at Shounan Special Reform School along with five other teenagers who have been arrested on serious criminal charges. All assigned to the same cell, they meet older inmate Rokurouta Sakuragi—a former boxer—with whom they establish a close bond. Under his guidance, and with the promise that they will meet again on the outside after serving their sentences, the delinquents begin to view their hopeless situation in a better light.\n\nThe seven cellmates struggle together against the brutal suffering and humiliation inflicted upon them by Ishihara, a sadistic guard with a grudge on Rokurouta, and Gisuke Sasaki, a doctor who takes pleasure in violating boys. Facing such hellish conditions, the seven inmates must scrape together all the strength they have to survive until their sentences are up; but even if they do, just what kind of lives are waiting for them on the other side?\n\n[Written by MAL Rewrite]"}, {"id": 60988, "name_en": "Heaven Official's Blessing Short Film", "name_jp": "天官賜福 全新动画短片", "image_url": "https://cdn.myanimelist.net/images/anime/1174/149943.jpg", "genres": ["Action", "Adventure", "Drama", "Fantasy"], "themes": ["Historical", "Mythology"], "demographics": [], "studios": ["Red Dog Culture House"], "year": 2025, "episodes": 0, "source": "Web novel", "score": 8.45, "synopsis": "A short film celebrating the fourth anniversary of the donghua's releases, highlighting Hua Cheng's and Xie Lian's first encounter."}, {"id": 31181, "name_en": "Owarimonogatari", "name_jp": "終物語", "image_url": "https://cdn.myanimelist.net/images/anime/8/76479.jpg", "genres": ["Comedy", "Mystery", "Supernatural"], "themes": ["Vampire"], "demographics": [], "studios": ["Shaft"], "year": 2015, "episodes": 12, "source": "Light novel", "score": 8.45, "synopsis": "A peculiar transfer student named Ougi Oshino has just arrived at Naoetsu Private High School. She is quickly introduced to senior student Koyomi Araragi by their mutual friend Kanbaru Suruga, in hopes of obtaining advice regarding a strange discovery she has made. After taking a look at the school's layout, Ougi notices that a classroom has appeared in an otherwise empty area—a place that should not exist. \n\nUnsure if this is the work of an apparition, Araragi and Ougi attempt to unravel the truth behind this enigma. But Araragi soon discovers, after finding himself locked in with Ougi, that the room holds the memory of an event he had long since forgotten.\n\n[Written by MAL Rewrite]"}, {"id": 11577, "name_en": "Steins;Gate: The Movie - Load Region of Déjà Vu", "name_jp": "劇場版 シュタインズゲート 負荷領域のデジャヴ", "image_url": "https://cdn.myanimelist.net/images/anime/1611/112806.jpg", "genres": ["Drama", "Sci-Fi"], "themes": [], "demographics": [], "studios": ["White Fox"], "year": 2013, "episodes": 1, "source": "Visual novel", "score": 8.45, "synopsis": "After a year in America, Kurisu Makise returns to Akihabara and reunites with Rintarou Okabe. However, their reunion is cut short when Okabe begins to experience recurring flashes of other timelines as the consequences of his time traveling start to manifest. These side effects eventually culminate in Okabe suddenly vanishing from the world, and only the startled Kurisu has any memory of his existence.\n\nIn the midst of despair, Kurisu is faced with a truly arduous choice that will test both her duty as a scientist and her loyalty as a friend: follow Okabe's advice and stay away from traveling through time to avoid the potential consequences it may have on the world lines, or ignore it to rescue the person that she cherishes most. Regardless of her decision, the path she chooses is one that will affect the past, the present, and the future.\n\n[Written by MAL Rewrite]"}, {"id": 36649, "name_en": "Banana Fish", "name_jp": "BANANA FISH", "image_url": "https://cdn.myanimelist.net/images/anime/1190/93472.jpg", "genres": ["Action", "Adventure", "Drama", "Suspense"], "themes": ["Delinquents", "Organized Crime", "Psychological"], "demographics": ["Shoujo"], "studios": ["MAPPA"], "year": 2018, "episodes": 24, "source": "Manga", "score": 8.45, "synopsis": "Aslan Jade Callenreese, known as Ash Lynx, was a runaway picked off the streets of New York City and raised by the infamous godfather of the mafia, Dino Golzine. Now 17 years old and the boss of his own gang, Ash begins investigating the mysterious \"Banana Fish\"—the same two words his older brother, Griffin, has muttered since his return from the Iraq War. However, his inquiries are hindered when Dino sends his men after Ash at an underground bar he uses as a hideout.\n\nAt the bar, Skip, Ash's friend, introduces him to Shunichi Ibe and his assistant, Eiji Okumura, who are Japanese photographers reporting on American street gangs. However, their conversation is interrupted when Shorter Wong, one of Ash's allies, calls to warn him about Dino. Soon, Dino's men storm the bar, and in the ensuing chaos kidnap Skip and Eiji. Now, Ash must find a way to rescue them and continue his investigation into Banana Fish, but will his history with the mafia prevent him from succeeding?\n\n[Written by MAL Rewrite]"}, {"id": 37105, "name_en": "Grand Blue Dreaming", "name_jp": "ぐらんぶる", "image_url": "https://cdn.myanimelist.net/images/anime/1302/94882.jpg", "genres": ["Comedy"], "themes": ["Adult Cast", "Gag Humor"], "demographics": ["Seinen"], "studios": ["Zero-G"], "year": 2018, "episodes": 12, "source": "Manga", "score": 8.45, "synopsis": "Iori Kitahara moves to the coastal town of Izu for his freshman year at its university, taking residence above Grand Blue, his uncle's scuba diving shop. Iori has high hopes and dreams about having the ideal college experience, but when he enters the shop he is sucked into the alcoholic activities of the carefree members of the Diving Club who frequent the place. Persuaded by upperclassmen Shinji Tokita and Ryuujirou Kotobuki, Iori reluctantly joins their bizarre party. His cousin Chisa Kotegawa later walks in and catches him in the act, earning Iori her utter disdain.\n\nBased on Kenji Inoue and Kimitake Yoshioka's popular comedy manga, Grand Blue follows Iori's misadventures with his eccentric new friends as he strives to realize his ideal college dream, while also learning how to scuba dive.\n\n[Written by MAL Rewrite]"}, {"id": 44087, "name_en": "Gintama: The Semi-Final", "name_jp": "銀魂 THE SEMI-FINAL", "image_url": "https://cdn.myanimelist.net/images/anime/1694/110521.jpg", "genres": ["Action", "Comedy"], "themes": ["Gag Humor", "Historical", "Parody", "Samurai"], "demographics": ["Shounen"], "studios": ["Bandai Namco Pictures"], "year": 2021, "episodes": 2, "source": "Manga", "score": 8.45, "synopsis": "As the war temporarily calms down and Edo rebuilds, Gintoki finds Shinpachi—who is still unaware of his return—on a bridge. However, as a fight quickly breaks out between the Yorozuya and the Tenshouin Naraku, suspicion grows, forcing Gintoki to use what is nearest—a loincloth—to mask his identity. Saved for the time being, Gintoki enters the Yorozuya office, but unbeknownst to him, someone else is already waiting there...\n\nMeanwhile, Kondou departs Earth to marry Princess Bubbles in an attempt to improve diplomatic relations. After boarding the Gorilla Amanto mother ship, he realizes that he doesn't speak their language. Confused, Kondou tries conversing with them, only to inadvertently gain their support. However, someone associated with the princess crashes the ongoing ceremony. Will the wedding continue, or has Kondou just been saved from becoming the next Gorilla Prince?\n\n[Written by MAL Rewrite]"}, {"id": 37379, "name_en": "Chihayafuru 3", "name_jp": "ちはやふる 3", "image_url": "https://cdn.myanimelist.net/images/anime/1898/110709.jpg", "genres": ["Drama", "Romance", "Sports"], "themes": ["Love Polygon", "School", "Strategy Game"], "demographics": ["Josei"], "studios": ["Madhouse"], "year": 2019, "episodes": 24, "source": "Manga", "score": 8.45, "synopsis": "Winning the high school team tournament was a great accomplishment for the Mizusawa members. Each of them has made great strides in improving themselves, and the victory symbolizes how far they have come. But after accomplishing one goal, their individual aims are within reach. Chihaya Ayase has her sights set on Shinobu Wakamiya and the title of Queen, and now that Taichi Mashima has made it into Class A, he can finally compete against Arata Wataya. Everyone in Mizusawa wants to get better, and there is no telling what the future holds if they keep trying.\n\n[Written by MAL Rewrite]"}, {"id": 60058, "name_en": "[Oshi No Ko] Season 3", "name_jp": "【推しの子】 第3期", "image_url": "https://cdn.myanimelist.net/images/anime/1979/153329.jpg", "genres": ["Drama"], "themes": ["Reincarnation", "Showbiz"], "demographics": ["Seinen"], "studios": ["Doga Kobo"], "year": 2026, "episodes": 11, "source": "Manga", "score": 8.44, "synopsis": "The story moves on to a new stage──half a year has passed since the release of \"POP IN 2.\" Thanks to Mem-cho's hard work, B-Komachi are on the verge of their big break. Aqua continues to grow as a personality, while Akane advances on her path to becoming a skilled actress. Meanwhile, Kana has lost her usual cheerfulness.\n\nFinally, in her quest to uncover the truth behind Ai and Gorou's deaths, Ruby rises through the entertainment industry—using lies as her weapon.\n\n(Source: Press Release, edited)"}, {"id": 57334, "name_en": "Dan Da Dan", "name_jp": "ダンダダン", "image_url": "https://cdn.myanimelist.net/images/anime/1584/143719.jpg", "genres": ["Action", "Comedy", "Supernatural"], "themes": [], "demographics": ["Shounen"], "studios": ["Science SARU"], "year": 2024, "episodes": 12, "source": "Manga", "score": 8.44, "synopsis": "Reeling from her recent breakup, Momo Ayase, a popular high schooler, shows kindness to her socially awkward schoolmate, Ken Takakura, by standing up to his bullies. Takakura misunderstands her intentions, believing he has made a new friend who shares his obsession with aliens and UFOs. However, Momo's own eccentric occult beliefs lie in the supernatural realm; she thinks aliens do not exist. A rivalry quickly brews as each becomes determined to prove the other wrong.\n\nDespite their initial clash over their opposing beliefs, Momo and Takakura form an unexpected but intimate friendship, a bond forged in a series of supernatural battles and bizarre encounters with urban legends and paranormal entities. As both develop unique superhuman abilities, they learn to supplement each other's weaknesses, leading them to wonder if their newfound partnership may be about more than just survival.\n\n[Written by MAL Rewrite]"}, {"id": 56538, "name_en": "Kimi ni Todoke: From Me to You Season 3", "name_jp": "君に届け3RD SEASON", "image_url": "https://cdn.myanimelist.net/images/anime/1617/142448.jpg", "genres": ["Drama", "Romance"], "themes": ["School"], "demographics": ["Shoujo"], "studios": ["Production I.G"], "year": 2024, "episodes": 5, "source": "Manga", "score": 8.44, "synopsis": "Summer is in full swing, and so is the love between Sawako Kuronuma and Shouta Kazehaya. From attending summer school together to meeting each other's parents, the two enjoy quality time together while tackling the feelings of self-doubt and emotional strain that come with their newfound love.\n\nMeanwhile, Sawako's close friends, Chizuru Yoshida and Ayane Yano, struggle through their own romantic dilemmas as their past experiences with relationships clash with their present feelings. Full of inner turmoil, the youngsters' desire for intimacy persists through their second year of high school as they weave through the labyrinth of indecision and insecurity that is love. \n\n[Written by MAL Rewrite]"}, {"id": 55809, "name_en": "Renegade Immortal", "name_jp": "仙逆", "image_url": "https://cdn.myanimelist.net/images/anime/1289/149708.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["BUILD DREAM"], "year": 2023, "episodes": 128, "source": "Web novel", "score": 8.44, "synopsis": "Wang Lin is a very smart boy with loving parents. Although he and his parents are shunned by the rest of their relatives, his parents have always held high hopes that he will one day achieve greatness. One day, Wang Lin suddenly gained the chance to walk the path of an immortal but found that he only had mediocre talent at best. Watch Wang Lin as he breaks through his lack of talent and walks the path towards becoming a real immortal!\n\n(Source: Xian Ni Wiki)"}, {"id": 35737, "name_en": "Pluto", "name_jp": "プルートウ", "image_url": "https://cdn.myanimelist.net/images/anime/1021/138568.jpg", "genres": ["Action", "Mystery", "Sci-Fi", "Suspense"], "themes": ["Detective", "Psychological"], "demographics": ["Seinen"], "studios": ["Studio M2"], "year": 2023, "episodes": 8, "source": "Manga", "score": 8.44, "synopsis": "Gesicht, an android police detective of Europol, is tasked with finding the murderer of Montblanc, a retired war hero robot. Although it appears that only a robot could have committed this crime, the murder of a renowned robot rights activist casts doubts on the criminal's identity. Indeed, outside of an isolated and unexplained incident that occurred eight years ago, robots are programmed to be unable to kill human beings. However, the lack of human evidence on the crime scene and the similarity of modus operandi lead Gesicht to suspect that the two murderers might be the same being—be they man or robot.\n\nShortly after Montblanc's passing, another retired elite war robot is mysteriously eliminated. Gesicht notices a pattern in the choice of murder victim: both dead robots belonged to a group of the seven most powerful war machines ever designed. Determined to stop the murderer from eliminating the five remaining veterans, Gesicht seeks help from Atom, a cutting-edge android who resembles a human boy. The duo must now hunt down the rogue killer before the series of murders is carried on, lest the very fabric of society suffer irremediable damage.\n\n[Written by MAL Rewrite]"}, {"id": 26055, "name_en": "JoJo's Bizarre Adventure: Stardust Crusaders - Battle in Egypt", "name_jp": "ジョジョの奇妙な冒険 スターダストクルセイダース エジプト編", "image_url": "https://cdn.myanimelist.net/images/anime/11/75045.jpg", "genres": ["Action", "Adventure"], "themes": ["Super Power"], "demographics": ["Shounen"], "studios": ["David Production"], "year": 2015, "episodes": 24, "source": "Manga", "score": 8.44, "synopsis": "Joutarou Kuujou and his allies have finally made it to Egypt, where the immortal Dio awaits. Upon their arrival, the group gains a new comrade: Iggy, a mutt who wields the Stand \"The Fool.\" It's not all good news however, as standing in their path is a new group of Stand users who serve Dio, each with a Stand representative of an ancient Egyptian god. As their final battle approaches, it is a race against time to break Joutarou's mother free from her curse and end Dio's reign of terror over the Joestar family once and for all.\n\n[Written by MAL Rewrite]"}, {"id": 60543, "name_en": "Dan Da Dan Season 2", "name_jp": "ダンダダン 第2期", "image_url": "https://cdn.myanimelist.net/images/anime/1721/149001.jpg", "genres": ["Action", "Comedy", "Supernatural"], "themes": [], "demographics": ["Shounen"], "studios": ["Science SARU"], "year": 2025, "episodes": 12, "source": "Manga", "score": 8.43, "synopsis": "While the mission to exorcise Jin \"Jiji\" Enjouji's family home is underway, things are not going as expected. Momo Ayase narrowly evades an attempted abduction while Ken \"Okarun\" Takakura and Jiji are ambushed by the Kitou family—the unsettling landlords of the cursed estate. The trio's efforts threaten the foundation of this enigmatic town shrouded in legends and mysteries.\n\nAs Momo, Okarun, and Jiji are drawn deeper into the maze of folklore and supernatural entities, they must each utilize their unique powers if they want to survive and unravel the secrets of the uncanny town.\n\n[Written by MAL Rewrite]"}, {"id": 54857, "name_en": "Re:ZERO -Starting Life in Another World- Season 3", "name_jp": "Re:ゼロから始める異世界生活 3rd season", "image_url": "https://cdn.myanimelist.net/images/anime/1706/144725.jpg", "genres": ["Drama", "Fantasy", "Suspense"], "themes": ["Isekai", "Psychological", "Time Travel"], "demographics": [], "studios": ["White Fox"], "year": 2024, "episodes": 16, "source": "Light novel", "score": 8.43, "synopsis": "One year after the events at the Sanctuary, Subaru Natsuki trains hard to better face future challenges. The peaceful days come to an end when Emilia receives an invitation to a meeting in the Watergate City of Priestella from none other than Anastasia Hoshin, one of her rivals in the royal selection. Considering the meeting's significance and the potential dangers Emilia could face, Subaru and his friends accompany her.\n\nHowever, as Subaru reconnects with old associates and companions in Priestella, new formidable foes emerge. Driven by fanatical motivations and engaging in ruthless methods to achieve their ambitions, the new enemy targets Emilia and threaten the very existence of the city. Rallying his allies, Subaru must give his all once more to stop their nefarious goals from becoming a concrete reality.\n\n[Written by MAL Rewrite]"}, {"id": 36999, "name_en": "Zoku Owarimonogatari", "name_jp": "続・終物語", "image_url": "https://cdn.myanimelist.net/images/anime/1022/96168.jpg", "genres": ["Comedy", "Mystery", "Supernatural"], "themes": ["Vampire"], "demographics": [], "studios": ["Shaft"], "year": 2019, "episodes": 6, "source": "Light novel", "score": 8.43, "synopsis": "Graduation day is finally here, marking the end of Koyomi Araragi's eccentric high school life full of peculiar relationships with otherworldly beings.\n\nHowever, Araragi is unexpectedly absorbed into his own bathroom mirror and trapped inside a bizarre world where everything he knows is completely reversed—the haughty Karen Araragi is shorter than usual, poker-faced Yotsugi Ononoki is brimming with emotion, and cute ghost girl Mayoi Hachikuji is a grown woman! But not everything is as it seems.\n\nZoku Owarimonogatari details the story of Araragi's endeavors in this new world as he struggles to return to his home and understand the nature of this bizarre dimension.\n\n[Written by MAL Rewrite]"}, {"id": 44511, "name_en": "Chainsaw Man", "name_jp": "チェンソーマン", "image_url": "https://cdn.myanimelist.net/images/anime/1806/126216.jpg", "genres": ["Action", "Fantasy"], "themes": ["Gore", "Urban Fantasy"], "demographics": ["Shounen"], "studios": ["MAPPA"], "year": 2022, "episodes": 12, "source": "Manga", "score": 8.43, "synopsis": "Denji is robbed of a normal teenage life, left with nothing but his deadbeat father's overwhelming debt. His only companion is his pet, the chainsaw devil Pochita, with whom he slays devils for money that inevitably ends up in the yakuza's pockets. All Denji can do is dream of a good, simple life: one with delicious food and a beautiful girlfriend by his side. But an act of greedy betrayal by the yakuza leads to Denji's brutal, untimely death, crushing all hope of him ever achieving happiness.\n\nRemarkably, an old contract allows Pochita to merge with the deceased Denji and bestow devil powers on him, changing him into a hybrid able to transform his body parts into chainsaws. Because Denji's new abilities pose a significant risk to society, the Public Safety Bureau's elite devil hunter Makima takes him in, letting him live as long as he obeys her command. Guided by the promise of a content life alongside an attractive woman, Denji devotes everything and fights with all his might to make his naive dreams a reality.\n\n[Written by MAL Rewrite]"}, {"id": 50265, "name_en": "Spy x Family", "name_jp": "SPY×FAMILY", "image_url": "https://cdn.myanimelist.net/images/anime/1441/122795.jpg", "genres": ["Action", "Award Winning", "Comedy"], "themes": ["Childcare", "Super Power"], "demographics": ["Shounen"], "studios": ["Wit Studio", "CloverWorks"], "year": 2022, "episodes": 12, "source": "Manga", "score": 8.43, "synopsis": "Corrupt politicians, frenzied nationalists, and other warmongering forces constantly jeopardize the thin veneer of peace between neighboring countries Ostania and Westalis. In spite of their plots, renowned spy and master of disguise \"Twilight\" fulfills dangerous missions one after another in the hope that no child will have to experience the horrors of war.\n\nIn the bustling Ostanian city of Berlint, Twilight dons the alias of \"Loid Forger,\" an esteemed psychiatrist. However, his true intention is to gather intelligence on prominent politician Donovan Desmond, who only appears rarely in public at his sons' school: the prestigious Eden Academy. Enlisting the help of unmarried city hall clerk Yor Briar to act as his wife and adopting the curious six-year-old orphan Anya as his daughter, Loid enacts his master plan. He will enroll Anya in Eden Academy, where Loid hopes she will excel and give him the opportunity to meet Donovan without arousing suspicion. \n\nUnfortunately for Loid, even a man of his talents has trouble playing the figure of a loving father and husband. And just like Loid is hiding his true identity, Yor—who is an underground assassin known as \"Thorn Princess\"—and Anya—an esper who can read people's minds—have no plans to disclose their own secrets either. Although this picture-perfect family is founded on deception, the Forgers gradually come to understand that the love they share for one another trumps all else.\n\n[Written by MAL Rewrite]"}, {"id": 51039, "name_en": "Battle Through The Heavens Season 5", "name_jp": "斗破苍穹年番", "image_url": "https://cdn.myanimelist.net/images/anime/1419/126374.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Motion Magic"], "year": 2022, "episodes": 209, "source": "Web novel", "score": 8.43, "synopsis": "Fifth season of Doupo Cangqiong."}, {"id": 20583, "name_en": "Haikyu!!", "name_jp": "ハイキュー!!", "image_url": "https://cdn.myanimelist.net/images/anime/7/76014.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2014, "episodes": 25, "source": "Manga", "score": 8.43, "synopsis": "Ever since having witnessed the \"Little Giant\" and his astonishing skills on the volleyball court, Shouyou Hinata has been bewitched by the dynamic nature of the sport. Even though his attempt to make his debut as a volleyball regular during a middle school tournament went up in flames, he longs to prove that his less-than-impressive height ceases to be a hindrance in the face of his sheer will and perseverance.\n\nWhen Hinata enrolls in Karasuno High School, the Little Giant's alma mater, he believes that he is one step closer to his goal of becoming a professional volleyball player. Although the school only retains a shadow of its former glory, Hinata's conviction isn't shaken until he learns that Tobio Kageyama—the prodigy who humiliated Hinata's middle school volleyball team in a crushing defeat—is now his teammate.\n\nTo fulfill his desire of leaving a mark on the realm of volleyball—so often regarded as the domain of the tall and the strong—Hinata must smooth out his differences with Kageyama. Only when Hinata learns what it takes to be a part of a team will he be able to join the race to the top in earnest.\n\n[Written by MAL Rewrite]"}, {"id": 40059, "name_en": "Golden Kamuy Season 3", "name_jp": "ゴールデンカムイ", "image_url": "https://cdn.myanimelist.net/images/anime/1763/108108.jpg", "genres": ["Action", "Adventure"], "themes": ["Adult Cast", "Historical", "Military"], "demographics": ["Seinen"], "studios": ["Geno Studio"], "year": 2020, "episodes": 12, "source": "Manga", "score": 8.43, "synopsis": "After obtaining information about Asirpa from the foreteller Inkarmat, Saichi \"The Immortal\" Sugimoto, Genjirou Tanigaki, and Cikapasi join the Seventh Division's Otonoshin Koito and Hajime Tsukishima on a journey to Karafuto. Heading further north into the freezing Russian territory, they collect any clues about Asirpa's whereabouts they can find and rush after her.\n\nMeanwhile, Asirpa is unaware of what truly transpired at Abashiri Prison. Deeply disturbed and confused by her father Wilk's past actions, Asirpa follows his old friend Kiroranke to the place where Wilk was born and raised in order to better understand his motives. However, Kiroranke and the master sniper Hyakunosuke Ogata are scheming behind her back.\n\nEven if they were to obtain all 24 tattooed skins that form a map to the lost Ainu gold, the various factions searching for it now understand that the map is useless without Asirpa. Realizing the danger she could be in, Sugimoto desperately hopes to reach Asirpa first and protect her from the greedy clutches of those who plan to drag her into their sins.\n\n[Written by MAL Rewrite]"}, {"id": 24687, "name_en": "Mushi-shi Path of Thorns", "name_jp": "蟲師 続章", "image_url": "https://cdn.myanimelist.net/images/anime/8/63539.jpg", "genres": ["Adventure", "Mystery", "Slice of Life", "Supernatural"], "themes": ["Adult Cast", "Historical", "Iyashikei"], "demographics": ["Seinen"], "studios": ["Artland"], "year": 2014, "episodes": 1, "source": "Manga", "score": 8.42, "synopsis": "Mysterious, unknowable creatures alien to the laws of nature—known only to some and feared by others—\"Mushi\" lie behind many of life's strange phenomena.\n\nLong ago, a Mushi of terrifying power threatened to extinguish all life. The Minai clan of Mushishi were born from those who stopped this malevolent force, their members bound by duty to serve as retainers to the Karibusa family, within whom the Mushi remains sealed. The Mushishi Ginko is given a job request from Tanyuu Karibusa: oversee the work of the head of the Minai clan, Kumado Minai, in investigating an abandoned village where dead wood and even houses spring back to life as flourishing plants.\n\nThough the Minai clan are oddly ruthless among Mushishi, even more peculiar is their widespread dull character, with little appreciation for beauty or sentiment. Tanyuu believes there is more to this trend than meets the eye. Ginko aims to answer her curiosity as he follows Kumado into a \"Path of Thorns,\" a place where Mushi flow from their own strange sources into the world of the living. Rare and deadly varieties of Mushi lurk in these depths, along with the secret nature of the Minai clan's resolve to their ancient task.\n\n[Written by MAL Rewrite]"}, {"id": 36538, "name_en": "Natsume's Book of Friends Movie: Ephemeral Bond", "name_jp": "劇場版 夏目友人帳 ～うつせみに結ぶ～", "image_url": "https://cdn.myanimelist.net/images/anime/1250/94846.jpg", "genres": ["Slice of Life", "Supernatural"], "themes": ["Iyashikei", "Mythology"], "demographics": ["Shoujo"], "studios": ["Shuka"], "year": 2018, "episodes": 1, "source": "Manga", "score": 8.42, "synopsis": "Takashi Natsume and his spirit companion Madara, nicknamed \"Nyanko,\" continue returning the names of spirits from the Book of Friends given by his late grandmother Reiko Natsume.\n\nOn his way back from school one day, Takashi encounters a lurking spirit named Monmonbou, who recalls memories of Takashi's grandmother after hearing his name. Takashi's natural curiosity leads him to explore a mysterious town where his grandmother used to live. Befriending her old acquaintance Yorie Tsumura and Yorie's son Mukuo, Takashi unveils more of his grandmother's past.\n\nIn the meantime, Nyanko detours for food and stumbles upon a suspicious \"Spirit Seed,\" which miraculously sprouts into a fruit tree overnight. Giving in to temptation, Nyanko consumes the fruit, splitting him into three. Seeking a solution to Nyanko's predicament, Takashi and his friends lend a hand, unexpectedly uncovering more secrets the town holds in the process.\n\n[Written by MAL Rewrite]"}, {"id": 42203, "name_en": "Re:ZERO -Starting Life in Another World- Season 2 Part 2", "name_jp": "Re：ゼロから始める異世界生活 2 part 2", "image_url": "https://cdn.myanimelist.net/images/anime/1724/117421.jpg", "genres": ["Drama", "Fantasy", "Suspense"], "themes": ["Isekai", "Psychological", "Time Travel"], "demographics": [], "studios": ["White Fox"], "year": 2021, "episodes": 12, "source": "Light novel", "score": 8.42, "synopsis": "After a stern yet compelling speech by Otto Suwen, Subaru Natsuki solemnly swears that he will successfully make it through this timeline and save everyone he can along the way. The first step toward achieving this goal is to help Emilia work through her past; however, that is easier said than done. Feeling as if she has been lied to by everyone around her, it will be difficult for Emilia to trust anyone, even Subaru, her self-proclaimed knight.\n\nRe:Zero kara Hajimeru Isekai Seikatsu 2nd Season Part 2 presents the culmination of Subaru's experiences with the Sanctuary and its people, along with his unwillingness to give up hope on saving them.\n\n[Written by MAL Rewrite]"}, {"id": 38889, "name_en": "Kono Oto Tomare!: Sounds of Life Season 2", "name_jp": "この音とまれ！", "image_url": "https://cdn.myanimelist.net/images/anime/1422/111621.jpg", "genres": ["Drama"], "themes": ["Music", "Performing Arts", "School"], "demographics": ["Shounen"], "studios": ["Platinum Vision"], "year": 2019, "episodes": 13, "source": "Manga", "score": 8.42, "synopsis": "The Tokise High School Koto Club has courageously pushed through their fractured and unsynchronized performance at the Kanto Region Traditional Japanese Music Festival. Club members Chika Kudou, Satowa Houzuki, Takezou Kurata, Hiro Kurusu, Kouta Mizuhara, Saneyasu Adachi, and Michitaka Sakai are devastated to learn the negative results of their performance, leaving them crushed. Nonetheless, the group recognizes their potential and enthusiastically agree to collectively sharpen their skills, improve their flaws, and develop higher caliber playing to succeed in the upcoming national qualifiers in winter.\n\nWith the help of their now willing club advisor Suzuka Takinami, the group's goal gradually becomes achievable as they begin to grasp the foundations of good music and refine their koto-playing abilities, with the suggestion of performing more often to gain what they lack most—experience.\n\nHowever, as their journey to nationals is underway, the koto club members face challenges that obstruct their focus and progress. Not only does the threat of other powerhouse schools and musicians remain, but the high school issues of budding romance and soon-to-be-graduating seniors also begin to push the limits of the determined group of teenagers and the future of the koto club.\n\n[Written by MAL Rewrite]"}, {"id": 21899, "name_en": "Gintama: Yorinuki Gintama-san on Theater 2D", "name_jp": "銀魂 よりぬき銀魂さんオンシアター2D", "image_url": "https://cdn.myanimelist.net/images/anime/1172/90571.jpg", "genres": ["Action", "Comedy", "Sci-Fi"], "themes": ["Gag Humor", "Historical", "Parody", "Samurai"], "demographics": ["Shounen"], "studios": ["Sunrise"], "year": 2012, "episodes": 2, "source": "Manga", "score": 8.42, "synopsis": "Demonic Vice-Commander of the Shinsengumi, Toushirou Hijikata, acquires a cursed sword—one which completely rewrites his personality, morphing him from a hard-boiled, no-nonsense cop into a hopeless otaku. As he struggles to break the curse, an ambitious new member of the police force, Itou Kamotarou, seizes the opportunity to depose Hijikata in his bid for power within the organization. However, Itou's scheme is revealed to be more devious than anyone imagined, and the very existence of the Shinsengumi is thrown into peril.\n\nIn another time and place, the Yorozuya squad is suddenly greeted by a potential new recruit. Before them is a mysterious young woman named Pirako Doromizu who hides a penchant for extreme violence behind her smiling, enthusiastic exterior. However, unbeknownst to Gintoki and the others, Pirako has strong ties to one of the ruling figures of the Kabuki district of Edo, and her arrival sets off a chain reaction that throws the inhabitants of the district into a civil war.\n\n[Written by MAL Rewrite]"}, {"id": 25781, "name_en": "Attack on Titan: No Regrets", "name_jp": "進撃の巨人 悔いなき選択", "image_url": "https://cdn.myanimelist.net/images/anime/8/69497.jpg", "genres": ["Action"], "themes": [], "demographics": [], "studios": ["Wit Studio"], "year": 2014, "episodes": 2, "source": "Visual novel", "score": 8.42, "synopsis": "Many years before becoming the famed captain of the Survey Corps, a young Levi struggles to survive in the capital's garbage dump, the Underground. As the boss of his own criminal operation, Levi attempts to get by with meager earnings while aided by fellow criminals, Isabel Magnolia and Farlan Church. With little hope for the future, Levi accepts a deal from the anti-expedition faction leader Nicholas Lobov, who promises the trio citizenship aboveground if they are able to successfully assassinate Erwin Smith, a squad leader of the Survey Corps.\n\nAs Levi and Erwin cross paths, Erwin acknowledges Levi's agility and skill and gives him the option to either become part of the expedition team, or be turned over to the Military Police, to atone for his crimes. Now closer to the man they are tasked to kill, the group plans to complete their mission and save themselves from a grim demise in the dim recesses of their past home. However, they are about to learn that the surface world is not as liberating as they had thought and that sometimes, freedom can come at a heavy price.\n\nBased on the popular spin-off manga of the same name, Shingeki no Kyojin: Kuinaki Sentaku illustrates the encounter between two of Shingeki no Kyojin's pivotal characters, as well as the events of the 23rd expedition beyond the walls.\n\n[Written by MAL Rewrite]"}, {"id": 40730, "name_en": "Heaven Official's Blessing", "name_jp": "天官賜福", "image_url": "https://cdn.myanimelist.net/images/anime/1319/109301.jpg", "genres": ["Action", "Adventure", "Drama", "Fantasy"], "themes": ["Historical", "Mythology"], "demographics": [], "studios": ["Haoliners Animation"], "year": 2020, "episodes": 11, "source": "Web novel", "score": 8.42, "synopsis": "The heavens shake, the thunder rumbles, and Xie Lian appears with an apologetic smile—again! Eight hundred years prior, he was a beloved martial god, known as the Crown Prince of Xianle. Now, he ascends to the heavenly realm for the third time, but simply as a pitiful scrap-collecting god with no followers behind him. \n\nOn his first mission, Xie Lian finds himself alone in the dark moonlit night. There, a gentle man dressed in red guides him through the forest. However, as abruptly as he appeared, the man suddenly dissipates into a swarm of silver butterflies.\n\nXie Lian later learns that this mysterious stranger was none other than Hua Cheng, the Crimson Rain Sought Flower, a Ghost King feared by both demons and gods alike. But before Xie Lian can figure out why Hua Cheng would help a Heavenly Official like himself, he meets San Lang. A young man possessing great knowledge on not only the Ghost King, but also the now forgotten Crown Prince, San Lang decides to accompany Xie Lian on his journey of unveiling the mysteries of the past.\n\n[Written by MAL Rewrite]"}, {"id": 467, "name_en": "Ghost in the Shell: Stand Alone Complex", "name_jp": "攻殻機動隊 STAND ALONE COMPLEX", "image_url": "https://cdn.myanimelist.net/images/anime/11/50857.jpg", "genres": ["Award Winning", "Mystery", "Sci-Fi"], "themes": ["Adult Cast", "Detective", "Mecha", "Military"], "demographics": ["Seinen"], "studios": ["Production I.G"], "year": 2002, "episodes": 26, "source": "Manga", "score": 8.42, "synopsis": "In the not so distant future, mankind has advanced to a state where complete body transplants from flesh to machine is possible. This allows for great increases in both physical and cybernetic prowess and blurring the lines between the two worlds. However, criminals can also make full use of such technology, leading to new and sometimes, very dangerous crimes. In response to such innovative new methods, the Japanese Government has established Section 9, an independently operating police unit which deals with such highly sensitive crimes.\n\nLed by Daisuke Aramaki and Motoko Kusanagi, Section 9 deals with such crimes over the entire social spectrum, usually with success. However, when faced with a new A level hacker nicknamed \"The Laughing Man,\" the team is thrown into a dangerous cat and mouse game, following the hacker's trail as it leaves its mark on Japan.\n\n[Written by MAL Rewrite]"}, {"id": 50330, "name_en": "Bungo Stray Dogs 4", "name_jp": "文豪ストレイドッグス", "image_url": "https://cdn.myanimelist.net/images/anime/1263/132759.jpg", "genres": ["Action", "Mystery"], "themes": ["Adult Cast", "Detective", "Organized Crime", "Super Power"], "demographics": ["Seinen"], "studios": ["Bones"], "year": 2023, "episodes": 13, "source": "Manga", "score": 8.42, "synopsis": "No longer concerned with military affairs, Yukichi Fukuzawa intends to act as a lone bodyguard-for-hire, making use of his deadly swordsmanship. However, things are not going as planned for his freelance business, and that is when he crosses paths with a mouthy boy named Ranpo Edogawa. While their initial interactions are intertwined with a bizarre murder mystery, the aftermath prompts the formation of the Armed Detective Agency.\n\nPresently, Ranpo finds himself chasing down a gifted individual with the dangerous ability to execute the perfect crime. But as the great detective unravels the case, he soon discovers an elaborate plot to obliterate the Agency in its entirety.\n\nAlthough forewarned of the trap, the Agency continue their pursuit of the criminals, only to end up framed for the crime themselves. Now branded as wanted terrorists, the remaining members must find a way to prove their innocence—even if they must turn to sworn enemies for assistance.\n\n[Written by MAL Rewrite]"}, {"id": 39741, "name_en": "Violet Evergarden: Eternity and the Auto Memory Doll", "name_jp": "ヴァイオレット・エヴァーガーデン 外伝 -永遠と自動手記人形-", "image_url": "https://cdn.myanimelist.net/images/anime/1667/112943.jpg", "genres": ["Drama"], "themes": [], "demographics": [], "studios": ["Kyoto Animation"], "year": 2019, "episodes": 1, "source": "Light novel", "score": 8.42, "synopsis": "Isabella, the daughter of the noble York family, is enrolled in an all-girls academy to be groomed into a dame worthy of nobility. However, she has given up on her future, seeing the prestigious school as nothing more than a prison from the outside world. Her family notices her struggling in her lessons and decides to hire Violet Evergarden to personally tutor her under the guise of a handmaiden. \n\nAt first, Isabella treats Violet coldly. Violet seems to be able to do everything perfectly, leading Isabella to assume that she was born with a silver spoon. After some time together, Isabella begins to realize that Violet has had her own struggles and starts to open up to her. Isabella soon reveals that she has lost contact with her beloved younger sister, Taylor Bartlett, whom she yearns to see again. \n\nHaving experienced the power of words through her past clientele, Violet asks if Isabella wishes to write a letter to Taylor. Will Violet be able to help Isabella convey her feelings to her long-lost sister?\n\n[Written by MAL Rewrite]"}, {"id": 45556, "name_en": "Scissor Seven: Xuanwu Kingdom Chapter", "name_jp": "伍六七之玄武国篇", "image_url": "https://cdn.myanimelist.net/images/anime/1884/111224.jpg", "genres": ["Action", "Adventure", "Comedy", "Drama", "Mystery"], "themes": ["Martial Arts", "Super Power"], "demographics": [], "studios": ["Sharefun Studio"], "year": 2021, "episodes": 10, "source": "Original", "score": 8.42, "synopsis": "In order to protect the residents of Xiaoji Island and the peaceful life here, Wu Liuqi and his partners Dabao and Xiaofei embark on a journey to the Xuanwu Kingdom to find out the truth about his identity and a way to save the island. Waiting for them is more unknowns and adventures.\n\n(Source: bilibili, edited)"}, {"id": 33255, "name_en": "The Disastrous Life of Saiki K.", "name_jp": "斉木楠雄のΨ難", "image_url": "https://cdn.myanimelist.net/images/anime/1973/142750.jpg", "genres": ["Comedy"], "themes": ["Gag Humor", "School", "Super Power"], "demographics": ["Shounen"], "studios": ["J.C.Staff", "Egg Firm"], "year": 2016, "episodes": 120, "source": "Manga", "score": 8.41, "synopsis": "To the average person, psychic abilities might seem a blessing; for Kusuo Saiki, however, this could not be further from the truth. Gifted with a wide assortment of supernatural abilities ranging from telepathy to x-ray vision, he finds this so-called blessing to be nothing but a curse. As all the inconveniences his powers cause constantly pile up, all Kusuo aims for is an ordinary, hassle-free life—a life where ignorance is bliss.\n\nUnfortunately, the life of a psychic is far from quiet. Though Kusuo tries to stay out of the spotlight by keeping his powers a secret from his classmates, he ends up inadvertently attracting the attention of many odd characters, such as the empty-headed Riki Nendou and the delusional Shun Kaidou. Forced to deal with the craziness of the people around him, Kusuo comes to learn that the ordinary life he has been striving for is a lot more difficult to achieve than expected.\n\n[Written by MAL Rewrite]"}, {"id": 34612, "name_en": "The Disastrous Life of Saiki K. 2", "name_jp": "斉木楠雄のΨ難 2", "image_url": "https://cdn.myanimelist.net/images/anime/1961/91383.jpg", "genres": ["Comedy"], "themes": ["Gag Humor", "School", "Super Power"], "demographics": ["Shounen"], "studios": ["J.C.Staff", "Egg Firm"], "year": 2018, "episodes": 24, "source": "Manga", "score": 8.41, "synopsis": "The disastrous life of the gifted psychic Kusuo Saiki continues, despite his utmost effort to live an ordinary life. Although he has certainly grown accustomed to dealing with his troublesome friends—who are his biggest hurdle to achieving a peaceful life—he still has a long way to go. Also joining the usual oddballs are a few new faces whose shenanigans add to Saiki's misery, making his dreams of a hassle-free life a distant fantasy.\n\n[Written by MAL Rewrite]"}, {"id": 2246, "name_en": "Mononoke", "name_jp": "モノノ怪", "image_url": "https://cdn.myanimelist.net/images/anime/3/20713.jpg", "genres": ["Horror", "Mystery", "Supernatural", "Suspense"], "themes": ["Adult Cast", "Historical", "Mythology", "Psychological"], "demographics": [], "studios": ["Toei Animation"], "year": 2007, "episodes": 12, "source": "Original", "score": 8.41, "synopsis": "The \"Medicine Seller\" is a deadly and mysterious master of the occult who travels across feudal Japan in search of malevolent spirits called \"mononoke\" to slay. When he locates one of these spirits, he cannot simply kill it; he must first learn its Form, its Truth, and its Reason in order to wield the mighty Exorcism Sword and fight against it. He must begin  his strange exorcisms with intense psychological analysis and careful investigative work—an extremely dangerous step, as he must first confront and learn about the mononoke before he even has the means to defeat it.\n\nThe Medicine Seller's journey leads him to an old-fashioned inn where Shino, a pregnant woman, has finally found a place to rest. The owner has reluctantly placed her in the last vacant room; however, as she settles in, it quickly becomes clear that the room is infested by a lethal band of mononoke, the Zashiki Warashi. With his hunter's intuition, the Medicine Seller begins his investigation to discover the Form, the Truth, and the Reason before the Zashiki Warashi can kill again.\n\n[Written by MAL Rewrite]"}, {"id": 49722, "name_en": "Teasing Master Takagi-san: The Movie", "name_jp": "からかい上手の高木さん", "image_url": "https://cdn.myanimelist.net/images/anime/1376/123398.jpg", "genres": ["Romance"], "themes": ["Love Status Quo", "School"], "demographics": ["Shounen"], "studios": ["Shin-Ei Animation"], "year": 2022, "episodes": 1, "source": "Manga", "score": 8.41, "synopsis": "Takagi and Nishikata's final summer in junior high is about to begin, and it's already off to a heartwarming start! When the pair find an abandoned kitten, they decide to work together and take care of their adorable new companion until they can locate its missing mother.\n\n(Source: Sentai Filmworks)"}, {"id": 36885, "name_en": "Saekano the Movie: Finale", "name_jp": "冴えない彼女の育てかた Fine", "image_url": "https://cdn.myanimelist.net/images/anime/1671/111411.jpg", "genres": ["Comedy", "Romance"], "themes": ["Harem", "Otaku Culture", "School"], "demographics": [], "studios": ["CloverWorks"], "year": 2019, "episodes": 1, "source": "Light novel", "score": 8.41, "synopsis": "With the second Winter Comiket just around the corner, Blessing Software has been vigorously producing its new game, \"How to Raise a Boring Girlfriend.\" Despite Utaha Kasumigaoka and Eriri Spencer Sawamura leaving the circle, Megumi Katou and Tomoya Aki are hopeful that, by sticking to Tomoya's original vision for the game, their upcoming creation will exceed Blessing Software's previous installment.\n\nWith the addition of new members Iori and Izumi Hashima, development ensues—but not without its share of setbacks. Things rarely go as planned in the dating sim industry, with numerous obstacles forcing Tomoya to decide between helping his friends or completing the game. \n\nSaenai Heroine no Sodatekata Fine draws the series to a close as Tomoya selects his final route, both within his personal life and Blessing Software.\n\n[Written by MAL Rewrite]"}, {"id": 38000, "name_en": "Demon Slayer: Kimetsu no Yaiba", "name_jp": "鬼滅の刃", "image_url": "https://cdn.myanimelist.net/images/anime/1286/99889.jpg", "genres": ["Action", "Award Winning", "Supernatural"], "themes": ["Historical"], "demographics": ["Shounen"], "studios": ["ufotable"], "year": 2019, "episodes": 26, "source": "Manga", "score": 8.41, "synopsis": "Ever since the death of his father, the burden of supporting the family has fallen upon Tanjirou Kamado's shoulders. Though living impoverished on a remote mountain, the Kamado family are able to enjoy a relatively peaceful and happy life. One day, Tanjirou decides to go down to the local village to make a little money selling charcoal. On his way back, night falls, forcing Tanjirou to take shelter in the house of a strange man, who warns him of the existence of flesh-eating demons that lurk in the woods at night.\n\nWhen he finally arrives back home the next day, he is met with a horrifying sight—his whole family has been slaughtered. Worse still, the sole survivor is his sister Nezuko, who has been turned into a bloodthirsty demon. Consumed by rage and hatred, Tanjirou swears to avenge his family and stay by his only remaining sibling. Alongside the mysterious group calling themselves the Demon Slayer Corps, Tanjirou will do whatever it takes to slay the demons and protect the remnants of his beloved sister's humanity.\n\n[Written by MAL Rewrite]"}, {"id": 55888, "name_en": "Mushoku Tensei: Jobless Reincarnation Season 2 Part 2", "name_jp": "無職転生 II ～異世界行ったら本気だす～ (第2クール)", "image_url": "https://cdn.myanimelist.net/images/anime/1876/141251.jpg", "genres": ["Adventure", "Drama", "Fantasy", "Ecchi"], "themes": ["Isekai", "Reincarnation"], "demographics": [], "studios": ["Studio Bind"], "year": 2024, "episodes": 12, "source": "Light novel", "score": 8.4, "synopsis": "Following the faceless god Hitogami's advice seems to have worked wonders for Rudeus Greyrat. After enrolling into the University of Magic as he was told, Rudeus reunites with his childhood friend Sylphiette, who put a valiant effort into curing his condition. The two grow ever closer together and decide to host a wedding party, inviting the friends they have made over the years to announce and formalize their relationship.\n\nFor all his recent blessings, however, Rudeus' troubles are far from over. The research he is helping Shizuka Nanahoshi conduct hits a bottleneck, sending her into a deep slump much like he experienced in his previous life. Furthermore, a letter from his father, Paul, brings complications to Rudeus' relationships, and Sylphiette still knows next to nothing about his real background. In the face of these issues, Rudeus will have to apply the lessons he has learned in this new world to navigate through the challenges that come with living a life to its fullest.\n\n[Written by MAL Rewrite]"}, {"id": 36275, "name_en": "Natsume's Book of Friends Season 6 Specials", "name_jp": "夏目友人帳 陸 特別編", "image_url": "https://cdn.myanimelist.net/images/anime/10/87615.jpg", "genres": ["Slice of Life", "Supernatural"], "themes": ["Iyashikei", "Mythology"], "demographics": ["Shoujo"], "studios": ["Shuka"], "year": 2017, "episodes": 2, "source": "Manga", "score": 8.4, "synopsis": "Suzunaru no Kirikabu \nOn a hot summer day, Takashi Natsume and his feline companion Madara take a walk to buy jelly. Suddenly a truck passes the two, running over an old, masked man who asks Natsume for help. The mysterious individual turns out to be a youkai, the spirit of the Suzunaru tree that was recently hit by lightning and cut down. As the tree withers away, the youkai requests Natsume to fulfill his one final wish: to bring him water every day until he draws his last breath.\n\nMugen no Kakera \nTamami, a young schoolgirl, counts her steps through a forest, following instructions on a piece of paper. She stops to take a break and falls asleep but is awakened by an odd talking cat. Through meeting the cat and his human partner, Natsume, she learns that the forest has its own separate world from the contemporary, where one can see things otherwise invisible. Putting her trust in the duo, Tamami tells them about the story of how she helped a white owl that reappeared in her dreams to lead her back inside the bizarre forest.\n\n[Written by MAL Rewrite]"}, {"id": 30709, "name_en": "Kamisama Hajimemashita: Kako-hen", "name_jp": "神様はじめました～過去編～", "image_url": "https://cdn.myanimelist.net/images/anime/3/81250.jpg", "genres": ["Comedy", "Romance", "Supernatural"], "themes": ["Mythology", "Time Travel"], "demographics": ["Shoujo"], "studios": ["TMS Entertainment"], "year": 2015, "episodes": 4, "source": "Manga", "score": 8.4, "synopsis": "While playing in the snow one day at her shrine, the land god Nanami Momozono witnesses her familiar—the fox youkai Tomoe—collapse, with dark markings appearing on his body. Tomoe's former master, Lord Mikage, appears after his long absence and places Tomoe into a magical pocket mirror in order to stave off his ailment.\n\nMikage explains that long ago, before he and Tomoe had met, the fox youkai was in love with a human woman. Seeking to live as a human with his beloved, he made a deal with a fallen god, but he only ended up cursed and dying. When Mikage discovered Tomoe, the god made the youkai forget his human love as a quick solution. However, something has changed recently to reactivate the curse; Tomoe has fallen in love with his new human master, Nanami. Since there is no way to stop the curse, Nanami wants to stop Tomoe from getting cursed in the first place by traveling back through time, even if it means they may never meet. As Nanami travels back hundreds of years to save her precious familiar, she discovers that she is far more closely bonded to Tomoe than she previously thought.\n\n[Written by MAL Rewrite]"}, {"id": 38040, "name_en": "KonoSuba: God's Blessing on This Wonderful World! - Legend of Crimson", "name_jp": "映画 この素晴らしい世界に祝福を！紅伝説", "image_url": "https://cdn.myanimelist.net/images/anime/1638/119321.jpg", "genres": ["Adventure", "Comedy", "Fantasy"], "themes": ["Isekai", "Parody"], "demographics": [], "studios": ["J.C.Staff"], "year": 2019, "episodes": 1, "source": "Light novel", "score": 8.4, "synopsis": "It is not strange that the Demon Lord's forces fear the Crimson Demons, the clan from which Megumin and Yunyun originate. Even if the Demon Lord's generals attack their village, the Crimson Demons can just easily brush them off with their supreme mastery of advanced and overpowered magic.\n\nWhen Yunyun receives a seemingly serious letter regarding a potential disaster coming to her hometown, she immediately informs Kazuma Satou and the rest of his party. After a series of wacky misunderstandings, it turns out to be a mere prank by her fellow demon who wants to be an author. Even so, Megumin becomes worried about her family and sets out toward the Crimson Demons' village with the gang.\n\nThere, Kazuma and the others decide to sightsee the wonders of Megumin's birthplace. However, they soon come to realize that the nonsense threat they received might have been more than just a joke.\n\n[Written by MAL Rewrite]"}, {"id": 37999, "name_en": "Kaguya-sama: Love is War", "name_jp": "かぐや様は告らせたい～天才たちの恋愛頭脳戦～", "image_url": "https://cdn.myanimelist.net/images/anime/1295/106551.jpg", "genres": ["Comedy", "Romance"], "themes": ["School"], "demographics": ["Seinen"], "studios": ["A-1 Pictures"], "year": 2019, "episodes": 12, "source": "Manga", "score": 8.4, "synopsis": "At the renowned Shuchiin Academy, Miyuki Shirogane and Kaguya Shinomiya are the student body's top representatives. Ranked the top student in the nation and respected by peers and mentors alike, Miyuki serves as the student council president. Alongside him, the vice president Kaguya—eldest daughter of the wealthy Shinomiya family—excels in every field imaginable. They are the envy of the entire student body, regarded as the perfect couple.\n\nHowever, despite both having already developed feelings for the other, neither are willing to admit them. The first to confess loses, will be looked down upon, and will be considered the lesser. With their honor and pride at stake, Miyuki and Kaguya are both equally determined to be the one to emerge victorious on the battlefield of love!\n\n[Written by MAL Rewrite]"}, {"id": 56009, "name_en": "Sentenced to Be a Hero", "name_jp": "勇者刑に処す 懲罰勇者9004隊刑務記録", "image_url": "https://cdn.myanimelist.net/images/anime/1062/151911.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": [], "studios": ["Studio KAI"], "year": 2026, "episodes": 12, "source": "Light novel", "score": 8.39, "synopsis": "\"Hero\" is the worst punishment in the world. Those convicted of heinous crimes are sentenced to become \"Heroes\" and forced to enter the mandatory military service in the war against the Demon Lords. These convicts are not even allowed to die—if killed, they will be resurrected to fight another day.\n\nHero Xylo Forbartz, former head of the Order of the Holy Knights, leads a penal unit of deplorables fighting on the front lines. It is in these direst of circumstances that he meets Teoritta, one of the world's strongest weapons. \"When every last enemy has been defeated, you are to shower me with praise and pat my head.\" In order to survive and to take revenge on those who wronged him, he makes a pact with the goddess and launches headfirst into a maelstrom of war and intrigue.\n\n(Source: Official site, edited)"}, {"id": 53672, "name_en": "Violet Evergarden: Recollections", "name_jp": "ヴァイオレット・エヴァーガーデン特別総集版", "image_url": "https://cdn.myanimelist.net/images/anime/1902/143097.jpg", "genres": ["Drama"], "themes": [], "demographics": [], "studios": ["Kyoto Animation"], "year": 2021, "episodes": 1, "source": "Light novel", "score": 8.39, "synopsis": "Recap of Violet Evergarden."}, {"id": 35851, "name_en": "Maquia: When the Promised Flower Blooms", "name_jp": "さよならの朝に約束の花をかざろう", "image_url": "https://cdn.myanimelist.net/images/anime/11/89556.jpg", "genres": ["Drama", "Fantasy"], "themes": [], "demographics": [], "studios": ["P.A. Works"], "year": 2018, "episodes": 1, "source": "Original", "score": 8.39, "synopsis": "Maquia is a member of a special race called the Iorph—mystical beings who can live for hundreds of years and remain separate from the lives and daily troubles of mankind. However, Maquia has always felt lonely despite being surrounded by her people, as she was orphaned from a young age. She daydreams about the outside world, but dares not travel from her home due to the warnings of the clan's chief.\n\nOne day however, the outside world finds her, as the power-hungry kingdom of Mezarte invades her homeland. They already have what is left of the giant dragons, the Renato, under their control, and now their king wishes to add the immortality of the Iorph to his bloodline.\n\nThe humans and their Renato ravage the Iorph homeland and kill most of its inhabitants. Caught in the midst of the attack, Maquia is carried off by one of the Renato that has gone berserk. It soon dies, and she is left deserted in a forest far from home, now truly alone save for the cries of a single baby off in the distance. Maquia finds the baby in a destroyed village and decides to raise him as her own, naming him Ariel. Although she knows nothing of the human world, how to raise a child that ages much faster than her, or how to live with the smoldering loneliness inside, she is determined to make it all work somehow.\n\n[Written by MAL Rewrite]"}, {"id": 37515, "name_en": "Made in Abyss: Wandering Twilight", "name_jp": "劇場版総集編【後編】メイドインアビス 放浪する黄昏", "image_url": "https://cdn.myanimelist.net/images/anime/1336/95168.jpg", "genres": ["Adventure", "Drama", "Fantasy", "Mystery", "Sci-Fi"], "themes": ["Gore"], "demographics": [], "studios": ["Kinema Citrus"], "year": 2019, "episodes": 1, "source": "Web manga", "score": 8.39, "synopsis": "The movie is a compilation of episodes 9-13 of the 2017 television series. Riko and Reg descend to the third layer where Riko has her first experience of the Curse. They descend to the fourth layer where Riko's arm is injured by an Orbed Piercer and Reg tries to save her. Nanachi comes to their aid and saves Riko's poisoned arm. In return Nanachi asks Reg to kill her immortal companion Mitty. Nanachi then joins Riko and Reg in their quest to reach the bottom of the Abyss.\n\n(Source: Wikipedia)"}, {"id": 35557, "name_en": "Land of the Lustrous", "name_jp": "宝石の国", "image_url": "https://cdn.myanimelist.net/images/anime/3/88293.jpg", "genres": ["Action", "Drama", "Fantasy", "Mystery"], "themes": ["Anthropomorphic"], "demographics": ["Seinen"], "studios": ["Orange"], "year": 2017, "episodes": 12, "source": "Manga", "score": 8.39, "synopsis": "In the mysterious future, crystalline organisms called Gems inhabit a world that has been destroyed by six meteors. Each Gem is assigned a role in order to fight against the Lunarians, a species who attacks them in order to shatter their bodies and use them as decorations. \n\nPhosphophyllite, also known as Phos, is a young and fragile Gem who dreams of helping their friends in the war effort. Instead, they are told to compile an encyclopedia because of their delicate condition. After begrudgingly embarking on this task, Phos meets Cinnabar, an intelligent gem who has been relegated to patrolling the isolated island at night because of the corrosive poison their body creates. After seeing how unhappy Cinnabar is, Phos decides to find a role that both of the rejected Gems can enjoy. Houseki no Kuni follows Phos' efforts to be useful and protect their fellow Gems.\n\n[Written by MAL Rewrite]"}, {"id": 5028, "name_en": "Major S5", "name_jp": "メジャー", "image_url": "https://cdn.myanimelist.net/images/anime/9/75218.jpg", "genres": ["Sports"], "themes": ["Team Sports"], "demographics": ["Shounen"], "studios": ["SynergySP"], "year": 2009, "episodes": 25, "source": "Manga", "score": 8.39, "synopsis": "The Baseball World Cup is nearing, and Gorou Honda is eager to represent his country. However, one small problem stands in his way: the team roster has already been selected, and he is not a part of it. \n\nNonetheless, Gorou remains adamant to join. He becomes a practice pitcher for the Japanese national team, hoping to demonstrate his incredible speed and skills to the team's manager. Fortunately, Gorou is given the chance to prove his worth: he must show an impressive performance in the upcoming practice match involving the veteran Japanese team and the young players who hope to replace them. \n\nThe World Cup will bring together some of the strongest players from around the globe. It will also set the stage for Gorou's long-awaited showdown with his longtime rival, Joe Gibson.\n\n[Written by MAL Rewrite]"}, {"id": 56523, "name_en": "Swallowed Star 3rd Season", "name_jp": "吞噬星空 第三季", "image_url": "https://cdn.myanimelist.net/images/anime/1250/138217.jpg", "genres": ["Action", "Adventure", "Fantasy", "Sci-Fi"], "themes": ["Martial Arts"], "demographics": [], "studios": ["Sparkly Key Animation Studio"], "year": 2022, "episodes": 33, "source": "Web novel", "score": 8.38, "synopsis": "Third season of Tunshi Xingkong."}, {"id": 56524, "name_en": "Swallowed Star 4th Season", "name_jp": "吞噬星空 第四季", "image_url": "https://cdn.myanimelist.net/images/anime/1335/138907.jpg", "genres": ["Action", "Adventure", "Fantasy", "Sci-Fi"], "themes": ["Martial Arts"], "demographics": [], "studios": ["Sparkly Key Animation Studio"], "year": 2023, "episodes": 175, "source": "Web novel", "score": 8.38, "synopsis": "Fourth season of Tunshi Xingkong."}, {"id": 53236, "name_en": "Road of Naruto", "name_jp": "ROAD OF NARUTO", "image_url": "https://cdn.myanimelist.net/images/anime/1731/128787.jpg", "genres": ["Action", "Fantasy"], "themes": ["Martial Arts"], "demographics": ["Shounen"], "studios": ["Studio Pierrot"], "year": 2022, "episodes": 1, "source": "Manga", "score": 8.38, "synopsis": "In celebration of 20 years of Naruto, Studio Pierrot posted an anniversary PV on their YouTube channel. The PV is a compilation of newly reworked scenes from Naruto containing new animation."}, {"id": 37965, "name_en": "Run with the Wind", "name_jp": "風が強く吹いている", "image_url": "https://cdn.myanimelist.net/images/anime/1936/129119.jpg", "genres": ["Drama", "Sports"], "themes": ["Adult Cast"], "demographics": [], "studios": ["Production I.G"], "year": 2018, "episodes": 23, "source": "Novel", "score": 8.38, "synopsis": "Former ace runner of Sendai Josei High School, Kakeru Kurahara is chased away from a convenience store for shoplifting. Shaking off his pursuer, he runs into Haiji Kiyose, another student from his university. Haiji is impressed by Kakeru's agility and persuades him to live in Chikusei-sou, the run-down apartment where Haiji resides along with eight other students. Having lost his entire apartment deposit at a mahjong parlor, Kakeru accepts the offer reluctantly.\n\nHowever, Haiji reveals a secret during Kakeru's welcoming party: the apartment is actually the dormitory of the Kansei University Track Club. He unveils his ultimate goal of participating in the Hakone Ekiden—one of the most prominent university marathon relay races in Japan. Unfortunately, all the residents apart from Haiji and Kakeru are complete running novices. Worse still, none of the inhabitants are even remotely interested in being involved with Haiji's ridiculous plan! With only months before the deadline, will the fourth-year student be able to convince them otherwise and realize his elusive dream of running in the Hakone Ekiden?\n\n[Written by MAL Rewrite]"}, {"id": 46095, "name_en": "Vivy -Fluorite Eye's Song-", "name_jp": "Vivy -Fluorite Eye's Song- (ヴィヴィ -フローライトアイズソング-)", "image_url": "https://cdn.myanimelist.net/images/anime/1551/128960.jpg", "genres": ["Action", "Sci-Fi", "Suspense"], "themes": ["Music", "Time Travel"], "demographics": [], "studios": ["Wit Studio"], "year": 2021, "episodes": 13, "source": "Original", "score": 8.38, "synopsis": "When highly evolved AIs set out to eradicate mankind, the carnage that ensues fills the air with the stench of fresh blood and burning bodies. In a desperate bid to prevent the calamity from ever occurring, a scientist bets everything on a remnant from the past.\n\nTurning the clock back a hundred years, AIs are already an integral part of human society, programmed with specific missions meant to be carried out for their entire course of operation. Vivy, the first ever autonomous AI, is a songstress tasked with spreading happiness through her voice. In a theme park where she hardly ever gets a proper audience, she strives to pour her heart out into her performances, bound to repeat it day after day—that is, until an advanced AI from the future appears before her and enlists her help in stopping a devastating war a hundred years in the making. With no time to process the revelation that flips her world upside down, Vivy is catapulted into a century-long journey to avert the violent history yet to come.\n\n[Written by MAL Rewrite]"}, {"id": 11979, "name_en": "Puella Magi Madoka Magica the Movie Part 2: Eternal", "name_jp": "劇場版 魔法少女まどか☆マギカ 永遠の物語", "image_url": "https://cdn.myanimelist.net/images/anime/6/42265.jpg", "genres": ["Drama", "Suspense"], "themes": ["Mahou Shoujo", "Psychological"], "demographics": [], "studios": ["Shaft"], "year": 2012, "episodes": 1, "source": "Original", "score": 8.38, "synopsis": "Though Sayaka Miki's wish was fulfilled, the unforeseen consequences that came with it overwhelm her, causing her soul gem to become tainted as she succumbs to despair and eventually loses her humanity. Homura Akemi reveals to Kyouko Sakura and Madoka Kaname the ultimate fate of magical girls: once their soul gem becomes tainted, it transforms into a Grief Seed, and they are reborn as witches—a truth Homura learned only through repeating history countless times in a bid to prevent Madoka's tragedy.\n\nKyuubey only compounds their despair when he confesses his true intentions: to harness the energy created from magical girls and use it to prolong the life of the universe. As the threat of Walpurgisnacht, a powerful witch, looms overhead, Homura once again vows to protect Madoka and the world from a grim fate.  \n\nCaught between honoring Homura's wish and saving the world, which one will Madoka choose in the end?\nMahou Shoujo Madoka★Magica Movie 2: Eien no Monogatari is a story of inescapable destiny, and an unlikely hero who could change it all.\n\n[Written by MAL Rewrite]"}, {"id": 48561, "name_en": "Jujutsu Kaisen 0", "name_jp": "劇場版 呪術廻戦 0", "image_url": "https://cdn.myanimelist.net/images/anime/1121/119044.jpg", "genres": ["Action", "Supernatural"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["MAPPA"], "year": 2021, "episodes": 1, "source": "Manga", "score": 8.38, "synopsis": "Violent misfortunes frequently occur around 16-year-old Yuuta Okkotsu, a timid victim of high school bullying. Yuuta is saddled with a monstrous curse, a power that dishes out brutal revenge against his bullies. Rika Orimoto, Yuuta's curse, is a shadow from his tragic childhood and a potentially lethal threat to anyone who dares wrong him.\n\nYuuta's unique situation catches the attention of Satoru Gojou, a powerful sorcerer who teaches at Tokyo Prefectural Jujutsu High School. Gojou sees immense potential in Yuuta, and he hopes to help the boy channel his deadly burden into a force for good. Yet Yuuta struggles to find his place among his talented classmates: the selectively mute Toge Inumaki, weapons expert Maki Zenin, and Panda.\n\nYuuta clumsily utilizes Rika on missions with the other first-year students, but the grisly aftermath of Rika's tremendous displays of power draws the interest of the calculating curse user Suguru Getou. As Getou strives to claim Rika's strength and use it to eliminate all non-jujutsu users from the world, Yuuta fights alongside his friends to stop the genocidal plot.\n\n[Written by MAL Rewrite]"}, {"id": 40787, "name_en": "Josee, the Tiger and the Fish", "name_jp": "ジョゼと虎と魚たち", "image_url": "https://cdn.myanimelist.net/images/anime/1714/108892.jpg", "genres": ["Drama", "Romance"], "themes": ["Adult Cast", "Visual Arts"], "demographics": [], "studios": ["Bones"], "year": 2020, "episodes": 1, "source": "Novel", "score": 8.38, "synopsis": "Equipped with his passion for diving and admiration for marine biology, university student Tsuneo Suzukawa tries his best to juggle several part-time jobs to earn enough money to study abroad. But one night, in a fateful accident, he meets a girl in a wheelchair, driving his current path into a detour.\n\nThe girl, Kumiko—who prefers to be called \"Josee\"—initially comes off as rude. Tsuneo, however, is then convinced by Josee's grandmother to take on the paid job to be Josee's caretaker. Despite being annoyed with her bossy demeanor, Tsuneo sees the opportunity to save more funds to support his academic dream. Nonetheless, after putting up with Josee's behavior for some time, Tsuneo tries to quit, only to discover Josee's dreams of traversing the outside world—to experience a life free from her crippling condition.\n\nChanging his mind, Tsuneo decides to accompany Josee in exploring the wonders that the world has to offer. Through their time together, the two begin to realize that the traits that bind them may be vital toward fulfilling their respective aspirations.\n\n[Written by MAL Rewrite]"}, {"id": 9756, "name_en": "Puella Magi Madoka Magica", "name_jp": "魔法少女まどか★マギカ", "image_url": "https://cdn.myanimelist.net/images/anime/11/55225.jpg", "genres": ["Award Winning", "Drama", "Suspense"], "themes": ["Mahou Shoujo", "Psychological"], "demographics": [], "studios": ["Shaft"], "year": 2011, "episodes": 12, "source": "Original", "score": 8.38, "synopsis": "Madoka Kaname and Sayaka Miki are regular middle school girls with regular lives, but all that changes when they encounter Kyuubey, a cat-like magical familiar, and Homura Akemi, the new transfer student.\n\nKyuubey offers them a proposition: he will grant any one of their wishes and in exchange, they will each become a magical girl, gaining enough power to fulfill their dreams. However, Homura Akemi, a magical girl herself, urges them not to accept the offer, stating that everything is not what it seems.\n\nA story of hope, despair, and friendship, Mahou Shoujo Madoka★Magica deals with the difficulties of being a magical girl and the price one has to pay to make a dream come true.\n\n[Written by MAL Rewrite]"}, {"id": 50183, "name_en": "Blue Giant", "name_jp": "BLUE GIANT", "image_url": "https://cdn.myanimelist.net/images/anime/1958/132159.jpg", "genres": [], "themes": ["Music"], "demographics": ["Seinen"], "studios": ["Nut"], "year": 2023, "episodes": 1, "source": "Manga", "score": 8.38, "synopsis": "Ever since a fateful encounter with jazz music, Dai Miyamoto has dreamed of becoming the world's best jazz saxophonist. After only three years of playing the instrument, he makes the bold decision to move to Tokyo in order to break into the music scene.\n\nWhen he meets pianist Yukinori Sawabe at a small music club, Dai believes that he has found a kindred spirit he can form a band with. However, the classically trained Yukinori is reluctant to accept someone who makes such bold claims with so little experience under his belt. Yet, when he hears Dai's undeniable skill, Yukinori is forced to accept the offer.\n\nAlong with the even less experienced Shunji Tamada, the trio form JASS, a group with a unique sound that might just be able to shake up the stagnating jazz scene.\n\n[Written by MAL Rewrite]"}, {"id": 32366, "name_en": "Gintama: Love Incense Arc", "name_jp": "銀魂　愛染香篇", "image_url": "https://cdn.myanimelist.net/images/anime/1962/133573.jpg", "genres": ["Comedy"], "themes": ["Gag Humor", "Parody"], "demographics": ["Shounen"], "studios": ["Bandai Namco Pictures"], "year": 2016, "episodes": 2, "source": "Manga", "score": 8.38, "synopsis": "The red-light district, Yoshiwara, is suddenly taken over by \"Aizen Kou,\" an incense that has the ability to make anyone fall in love with the first person they see. Having been outlawed for its strong potency, the love drug was thought to have disappeared from the red-light district for good; however, it seems to have found its way back inside Yoshiwara's walls and now has the entire district under its influence.\n\nOf those affected is the cold-hearted Hyakka captain, Tsukuyo, and the first person she sees after inhaling the smoke is none other than the brazen Yorozuya leader, Gintoki Sakata. Unbeknownst to everyone else though, she was already attracted to Gintoki. With those hidden feelings now intensified, Tsukuyo must overcome her afflictions and work with the Yorozuya to return everyone back to normal. \n\n[Written by MAL Rewrite]"}, {"id": 5, "name_en": "Cowboy Bebop: The Movie", "name_jp": "カウボーイビバップ 天国の扉", "image_url": "https://cdn.myanimelist.net/images/anime/1439/93480.jpg", "genres": ["Action", "Sci-Fi"], "themes": ["Adult Cast", "Space"], "demographics": [], "studios": ["Bones"], "year": 2001, "episodes": 1, "source": "Original", "score": 8.38, "synopsis": "Another day, another bounty—such is the life of the often unlucky crew of the Bebop. However, this routine is interrupted when Faye, who is chasing a fairly worthless target on Mars, witnesses an oil tanker suddenly explode, causing mass hysteria. As casualties mount due to a strange disease spreading through the smoke from the blast, a whopping three hundred million woolong price is placed on the head of the supposed perpetrator.\n\nWith lives at stake and a solution to their money problems in sight, the Bebop crew springs into action. Spike, Jet, Faye, and Edward, followed closely by Ein, split up to pursue different leads across Alba City. Through their individual investigations, they discover a cover-up scheme involving a pharmaceutical company, revealing a plot that reaches much further than the ragtag team of bounty hunters could have realized.\n\n[Written by MAL Rewrite]"}, {"id": 38475, "name_en": "Laid-Back Camp: The Movie", "name_jp": "映画 ゆるキャン△", "image_url": "https://cdn.myanimelist.net/images/anime/1806/124396.jpg", "genres": ["Slice of Life"], "themes": ["Adult Cast", "CGDCT", "Iyashikei"], "demographics": [], "studios": ["C-Station"], "year": 2022, "episodes": 1, "source": "Manga", "score": 8.38, "synopsis": "As time marches forward, Rin Shima, Nadeshiko Kagamihara, and their friends have grown into full-fledged adults. Now with careers and responsibilities, they can no longer freely spend their days camping and adventuring. However, Chiaki Oogaki, who currently works for the Yamanashi Tourism Organization, reunites the group as she comes up with an exciting proposal—to build a campground together to revitalize an abandoned patch of land from their homeplace, the Yamanashi Prefecture.\n\nMotivated by this ambitious project, the girls combine their efforts to create an environment where future generations can enjoy unique moments as they once did, all while reminiscing the charms of camping from their high school days.\n\n[Written by MAL Rewrite]"}, {"id": 55318, "name_en": "Medalist", "name_jp": "メダリスト", "image_url": "https://cdn.myanimelist.net/images/anime/1029/146850.jpg", "genres": ["Drama", "Sports"], "themes": ["Performing Arts"], "demographics": ["Seinen"], "studios": ["ENGI"], "year": 2025, "episodes": 13, "source": "Manga", "score": 8.37, "synopsis": "Tsukasa Akeuraji always dreamed of becoming a competitive solo figure skater, but starting too late in life meant his ambitions never got off the ground. Now barely scraping by, he takes on an assistant coach job, resigned to a future far from the one he once imagined.\n\nBefore his first day, Tsukasa meets Inori Yuitsuka, a shy fifth grader sneaking into the rink to practice. Captivated by figure skating, but held back by her mother's overprotectiveness and her own self-doubt, Inori has never been encouraged to pursue her passion until she encounters Tsukasa. Beneath her personality lies great potential, waiting to be unlocked with the right guidance.\n\nMoved by Inori's determination to change and reminded of his own struggles, Tsukasa offers to coach and help her chase the dream she has been too afraid to voice. While the road ahead is long and filled with unfamiliar challenges and rival skaters, Inori dedicates herself to the art and strives to one day reach the Olympics and become a medalist. \n\n[Written by MAL Rewrite]"}, {"id": 31646, "name_en": "March Comes In Like a Lion", "name_jp": "3月のライオン", "image_url": "https://cdn.myanimelist.net/images/anime/3/82899.jpg", "genres": ["Drama"], "themes": ["Childcare", "Strategy Game"], "demographics": ["Seinen"], "studios": ["Shaft"], "year": 2016, "episodes": 22, "source": "Manga", "score": 8.37, "synopsis": "Having reached professional status in middle school, Rei Kiriyama is one of the few elite in the world of shogi. Due to this, he faces an enormous amount of pressure, both from the shogi community and his adoptive family. Seeking independence from his tense home life, he moves into an apartment in Tokyo. As a 17-year-old living on his own, Rei tends to take poor care of himself, and his reclusive personality ostracizes him from his peers in school and at the shogi hall.\n\nHowever, not long after his arrival in Tokyo, Rei meets Akari, Hinata, and Momo Kawamoto, a trio of sisters living with their grandfather who owns a traditional wagashi shop. Akari, the oldest of the three girls, is determined to combat Rei's loneliness and poorly sustained lifestyle with motherly hospitality. The Kawamoto sisters, coping with past tragedies, also share with Rei a unique familial bond that he has lacked for most of his life. As he struggles to maintain himself physically and mentally through his shogi career, Rei must learn how to interact with others and understand his own complex emotions.\n\n[Written by MAL Rewrite]"}, {"id": 38883, "name_en": "Haikyu!! To the Top", "name_jp": "ハイキュー!! TO THE TOP", "image_url": "https://cdn.myanimelist.net/images/anime/1813/105367.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2020, "episodes": 13, "source": "Manga", "score": 8.37, "synopsis": "After their triumphant victory over Shiratorizawa Academy, the Karasuno High School volleyball team has earned their long-awaited ticket to nationals. As preparations begin, genius setter Tobio Kageyama is invited to the All-Japan Youth Training Camp to play alongside fellow nationally recognized players. Meanwhile, Kei Tsukishima is invited to a special rookie training camp for first-years within the Miyagi Prefecture. Not receiving any invitations himself, the enthusiastic Shouyou Hinata feels left behind.\n\nHowever, Hinata does not back down. Transforming his frustration into self-motivation, he boldly decides to sneak himself into the same rookie training camp as Tsukishima. Even though Hinata only lands himself a job as the ball boy, he comes to see this as a golden opportunity. He begins to not only reflect on his skills as a volleyball player but also analyze the plethora of information available on the court and how he can apply it.\n\nAs the much-anticipated national tournament approaches, the members of Karasuno's volleyball team attempt to overcome their weak points and refine their skills, all while aiming for the top!\n\n[Written by MAL Rewrite]"}, {"id": 49721, "name_en": "Teasing Master Takagi-san 3", "name_jp": "からかい上手の高木さん3", "image_url": "https://cdn.myanimelist.net/images/anime/1861/120361.jpg", "genres": ["Comedy", "Romance"], "themes": ["Love Status Quo", "School"], "demographics": ["Shounen"], "studios": ["Shin-Ei Animation"], "year": 2022, "episodes": 12, "source": "Manga", "score": 8.37, "synopsis": "As summer break comes to an end, Nishikata is stoked to try out his newest pranks and finally outdo his classmate Takagi once and for all. Despite his losing streak, he is slowly getting to know the unrelenting workings of her crafty mind. However, he soon realizes that Takagi's motivations behind her teasing may not be what he initially assumed. One thing is certain, though: each fun-filled day of strategizing and competing brings the two closer than ever.\n\n[Written by MAL Rewrite]"}, {"id": 14397, "name_en": "Chihayafuru 2", "name_jp": "ちはやふる 2", "image_url": "https://cdn.myanimelist.net/images/anime/6/47435.jpg", "genres": ["Drama", "Romance", "Sports"], "themes": ["Love Polygon", "School", "Strategy Game"], "demographics": ["Josei"], "studios": ["Madhouse"], "year": 2013, "episodes": 25, "source": "Manga", "score": 8.37, "synopsis": "Chihaya Ayase is obsessed with developing her school's competitive karuta club, nursing daunting ambitions like winning the national team championship at the Omi Jingu and becoming the Queen, the best female karuta player in Japan—and in extension, the world. As their second year of high school rolls around, Chihaya and her fellow teammates must recruit new members, train their minds and bodies alike, and battle the formidable opponents that stand in their way to the championship title. Meanwhile, Chihaya's childhood friend, Arata Wataya, the prodigy who introduced her to karuta, rediscovers his lost love for the old card game.\n\n[Written by MAL Rewrite]"}, {"id": 62896, "name_en": "Cosmic Princess Kaguya!", "name_jp": "超かぐや姫！", "image_url": "https://cdn.myanimelist.net/images/anime/1544/154878.jpg", "genres": ["Action", "Sci-Fi", "Supernatural"], "themes": ["Music", "Mythology"], "demographics": [], "studios": ["Studio Colorido", "Studio Chromato"], "year": 2026, "episodes": 1, "source": "Original", "score": 8.36, "synopsis": "Tsukuyomi is a virtual realm where hopes and dreams come together. Iroha Sakayori, a 17-year-old high school student in Tokyo, leads an extremely busy life trying to balance part-time work and academics. She finds peace by watching a popular streamer named Yachiyo Runami, who is the administrator of an online virtual space called Tsukuyomi.\n\nIroha frequents Tsukuyomi, where people can live different lives and freely pursue their creativity, and she passes the time by supporting Yachiyo and playing battle games to earn a little extra money. On her way home one day, Iroha finds a telephone pole shining with iridescent hues. To her surprise, an adorable baby emerges from the pole. Unable to abandon it, Iroha takes the infant home and watches her rapidly grow into a girl her own age.\n\n\"Are you Princess Kaguya?\"\n\nThe grown-up Kaguya develops a self-indulgent personality. At Kaguya's fervent request, Iroha helps her start streaming in Tsukuyomi. With Iroha as producer and songwriter and Kaguya as streamer and singer, the two grow steadily closer. Little do they know that ominous forces lie in wait, eager to take Kaguya back to the moon.\n\nThis is the tale of Princess Kaguya as never seen before.\n\n(Source: Netflix, edited)"}, {"id": 58919, "name_en": "100 Meters", "name_jp": "ひゃくえむ。", "image_url": "https://cdn.myanimelist.net/images/anime/1259/151080.jpg", "genres": ["Drama", "Sports"], "themes": [], "demographics": [], "studios": ["Rock'n Roll Mountain"], "year": 2025, "episodes": 1, "source": "Web manga", "score": 8.36, "synopsis": "Togashi is born to run. As a kid, he is naturally gifted and wins every 100-meter race without effort. But in sixth grade, he meets Komiya, a transfer student who is full of determination but lacks technique. In teaching him, Togashi gives Komiya a new purpose: to win no matter what. Years pass by, Togashi and Komiya meet again as rivals on the track and reveal their true selves.\n\n(Source: Annecy Festival)"}, {"id": 55102, "name_en": "Girls Band Cry", "name_jp": "ガールズバンドクライ", "image_url": "https://cdn.myanimelist.net/images/anime/1711/140515.jpg", "genres": ["Drama"], "themes": ["Music"], "demographics": [], "studios": ["Toei Animation"], "year": 2024, "episodes": 13, "source": "Original", "score": 8.36, "synopsis": "Everyone yearns to find their true purpose in life. This is also true for Nina Iseri, a 17-year-old girl looking to enroll in a good university, but the world always seems to work against her. On her very first day in Tokyo, she gets lost, ignored, and locked out of her new apartment. However, an unexpected opportunity arises when Nina meets Momoka Kawaragi, one of her favorite guitarists.\n\nAfter Nina joins Momoka for a street performance, Momoka decides they should start a band together. While Nina initially hesitates, her determination grows as they start acquiring more bandmates—the beautiful drummer Subaru Awa, the aloof keyboardist Tomo Ebizuka, and the intelligent bassist Rupa. The five girls face challenges both in the music industry and within themselves, but their shared passion for music never lets them give up on their dreams.\n\n[Written by MAL Rewrite]"}, {"id": 9617, "name_en": "K-ON! The Movie", "name_jp": "映画 けいおん！", "image_url": "https://cdn.myanimelist.net/images/anime/5/76233.jpg", "genres": ["Award Winning", "Comedy"], "themes": ["CGDCT", "Music"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2011, "episodes": 1, "source": "4-koma manga", "score": 8.36, "synopsis": "Graduation looms for the founding members of the Light Music Club. With only a few precious weeks of school left, the girls decide to make the most of it and plan a trip abroad. Hawaii, New York, Dubai—many destinations are suggested, but after a little help from the club's precious pet turtle, Ton-chan, London is chosen as the host of their next misadventure!\n\nYui Hirasawa, Mio Akiyama, Tsumugi Kotobuki, Ritsu Tainaka, and Azusa Nakano will visit famous landmarks, perform live music for Londoners, and eat all sorts of delicious food, all while stumbling clumsily from place to place. But the fun won't last forever, as heartfelt songs and goodbyes will be made as their high school days together come to a close. One thing is for certain though: the undeniable friendships these girls have formed is something that will carry on long after the final scene rolls.\n\n[Written by MAL Rewrite]"}, {"id": 39167, "name_en": "Girls und Panzer das Finale – Part 4", "name_jp": "『ガールズ＆パンツァー 最終章』第4話", "image_url": "https://cdn.myanimelist.net/images/anime/1652/138188.jpg", "genres": [], "themes": ["CGDCT", "Military", "School"], "demographics": [], "studios": ["Actas"], "year": 2023, "episodes": 1, "source": "Original", "score": 8.36, "synopsis": "The Winter Continuous Track Cup semifinals are underway and Ooarai Girls’ Academy find themselves in deep trouble early in their match against Continuation High School. The Anglerfish Team are knocked out by Continuation's sharpshooter, forcing the rest of the team to use a different game plan. Stepping up in the absence of her star teammates, Azusa Sawa shows her leadership potential by temporarily taking over as Ooarai's commander. However, the match proves that it will take more than a new strategy for Ooarai to survive this time around.\n\nOn the other side of the tournament, Kuromorimine Girls' Academy and St. Gloriana Girls' College are locked in a fiery desert battle. Having developed her own unorthodox tactical style, Erika Itsumi rallies Kuromorimine from the brink of defeat into a fierce showdown against their opponent's veteran commander, Darjeeling. But St. Gloriana have bolstered their ranks with a familiar face who recently transferred to their school, hampering Kuromorimine's quest for victory.\n\nThe championship round is within striking distance for these four schools, but only two will have what it takes to move on!\n\n[Written by MAL Rewrite]"}, {"id": 5205, "name_en": "The Garden of Sinners Chapter 7: A Study in Murder - Part 2", "name_jp": "劇場版 空の境界 the Garden of sinners 第七章『殺人考察（後）』", "image_url": "https://cdn.myanimelist.net/images/anime/9/56619.jpg", "genres": ["Action", "Fantasy", "Mystery", "Romance", "Suspense"], "themes": ["Urban Fantasy"], "demographics": [], "studios": ["ufotable"], "year": 2009, "episodes": 1, "source": "Light novel", "score": 8.36, "synopsis": "In February 1999, a string of murders has Shiki Ryougi and Mikiya Kokutou on edge. These crimes share a disturbing resemblance to a similar set of homicides from 1995, when Shiki and Mikiya first met, and awaken a dark, murderous desire that has laid dormant within Shiki's soul ever since then.\n\nWith Shiki under suspicion due to her involvement in the past killings and supposed resemblance to the killer, she and Mikiya set out to find the true perpetrator. In the midst of their separate investigations, Mikiya grows increasingly concerned with Shiki's well-being and hurries to find the one responsible in order to protect Shiki from her own impulses. With the lead he receives from his cousin, police investigator Daisuke Akimi, Mikiya is led into the underbelly of Mifune City, as the salvation of Shiki's soul lies in his determination to prove her innocence once and for all.\n\n[Written by MAL Rewrite]"}, {"id": 9260, "name_en": "Kizumonogatari Part 1: Iron-Blooded", "name_jp": "傷物語〈Ⅰ鉄血篇〉", "image_url": "https://cdn.myanimelist.net/images/anime/1783/112810.jpg", "genres": ["Action", "Mystery", "Supernatural"], "themes": ["Vampire"], "demographics": [], "studios": ["Shaft"], "year": 2016, "episodes": 1, "source": "Light novel", "score": 8.36, "synopsis": "During Koyomi Araragi's second year at Naoetsu Private High School, he has a chance encounter with Tsubasa Hanekawa, the top honor student in his class. When they strike up a conversation, Hanekawa mentions a shocking rumor: a vampire with beautiful blonde hair and freezing cold eyes has been seen lurking around town.\n\nHappy to have made a new friend, Araragi writes off the rumor and goes about the rest of his evening in a carefree manner. However, on his way home, he stumbles across splatters of blood leading down the stairs to the subway. His curiosity pushes him to investigate further, so he follows the gruesome pools into the depths of the station. \n\nWhen he arrives at the source of the blood, he is terrified by what he sees—the rumored blonde vampire herself, completely dismembered. After she calls for his help, Araragi must make a decision, one which carries the potential to change his life forever.\n\n[Written by MAL Rewrite]"}, {"id": 6945, "name_en": "Gintama: Jump Festa 2008 Special", "name_jp": "ジャンプアニメツアー 2008 白夜叉降誕", "image_url": "https://cdn.myanimelist.net/images/anime/6/16161.jpg", "genres": ["Action", "Comedy", "Sci-Fi"], "themes": ["Historical", "Parody"], "demographics": [], "studios": ["Sunrise"], "year": 2008, "episodes": 1, "source": "Manga", "score": 8.36, "synopsis": "According to Aniplex, a 10min short anime of Gintama \"Shiroyasha Koutan\" will be released on September 30th. It was shown at Jump Super Anime Tour 2008 and had never been released as a DVD.\n\nIt's a trailer of an imaginary Gintama movie. Gintama OVA shown at Jump Anime Tour '05 is also included in the DVD."}, {"id": 5941, "name_en": "Cross Game", "name_jp": "クロスゲーム", "image_url": "https://cdn.myanimelist.net/images/anime/6/22103.jpg", "genres": ["Drama", "Romance", "Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["SynergySP"], "year": 2009, "episodes": 50, "source": "Manga", "score": 8.36, "synopsis": "Kou Kitamura and Aoba Tsukishima are often at odds—even though their families happen to be close friends and business partners. Although the only child of a sports shop owner, Kou has never been interested in playing baseball. Despite this, he possesses an impressive batting ability honed by frequent visits to the local baseball batting center run by the Tsukishima family. On the other hand, Aoba loves to play baseball and is a star player with exceptional pitching form.\n\nHowever, these two seemingly complete opposites share something very important to them—Wakaba Tsukishima, Aoba's older sister and Kou's destined sweetheart. Admired by the quarrelsome duo, Wakaba often finds herself the catalyst to their never-ending rivalry. But whether or not they realize that they have more in common than either would care to admit, only time will tell. The game of baseball may just be what the pair needs to ultimately overcome their own personal struggles.\n\n[Written by MAL Rewrite]"}, {"id": 49310, "name_en": "Fruits Basket: Prelude", "name_jp": "フルーツバスケット -prelude-", "image_url": "https://cdn.myanimelist.net/images/anime/1034/120096.jpg", "genres": ["Drama", "Romance"], "themes": [], "demographics": ["Shoujo"], "studios": ["TMS Entertainment"], "year": 2022, "episodes": 1, "source": "Manga", "score": 8.36, "synopsis": "Despite Kyouko Honda's tragic death, her vivid memory lives on, providing guidance through times of hardship to her close ones. However, Kyouko was not always the wise and radiant person that she is fondly remembered as in the present day.\n\nFeeling deserted by her own family and rejected by society, a young Kyouko abandons stability for a life of delinquency, jeopardizing her education and future. Fortunately, her descent into despair is interrupted by a fateful encounter with her husband-to-be Katsuya, who has recently started his teaching internship at her school. \n\nWith a composure surprisingly unaffected by Kyouko's tantrums, Katsuya quickly wins her over through his gentleness and attention toward her—sincere gestures that she has never received before. As the two grow closer together, Kyouko opens her tormented heart to him and, along with it, a door to new horizons.\n\n[Written by MAL Rewrite]"}, {"id": 37822, "name_en": "Soul Land", "name_jp": "斗罗大陆", "image_url": "https://cdn.myanimelist.net/images/anime/1538/93566.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Sparkly Key Animation Studio"], "year": 2018, "episodes": 237, "source": "Novel", "score": 8.36, "synopsis": "Second season of Douluo Dalu."}, {"id": 22789, "name_en": "Barakamon", "name_jp": "ばらかもん", "image_url": "https://cdn.myanimelist.net/images/anime/1426/111248.jpg", "genres": ["Slice of Life"], "themes": ["Childcare", "Iyashikei", "Visual Arts"], "demographics": [], "studios": ["Kinema Citrus"], "year": 2014, "episodes": 12, "source": "Web manga", "score": 8.36, "synopsis": "Seishuu Handa is an up-and-coming calligrapher: young, handsome, talented, and unfortunately, a narcissist to boot. When a veteran labels his award-winning piece as \"unoriginal,\" Seishuu quickly loses his cool with severe repercussions.\n\nAs punishment, and also in order to aid him in self-reflection, Seishuu's father exiles him to the Goto Islands, far from the comfortable Tokyo lifestyle the temperamental artist is used to. Now thrown into a rural setting, Seishuu must attempt to find new inspiration and develop his own unique art style—that is, if boisterous children (headed by the frisky Naru Kotoishi), fujoshi middle schoolers, and energetic old men stop barging into his house! The newest addition to the intimate and quirky Goto community only wants to get some work done, but the islands are far from the peaceful countryside he signed up for. Thanks to his wacky neighbors who are entirely incapable of minding their own business, the arrogant calligrapher learns so much more than he ever hoped to.\n\n[Written by MAL Rewrite]"}, {"id": 39547, "name_en": "My Teen Romantic Comedy SNAFU Climax!", "name_jp": "やはり俺の青春ラブコメはまちがっている。完", "image_url": "https://cdn.myanimelist.net/images/anime/1958/107912.jpg", "genres": ["Comedy", "Romance"], "themes": ["Love Polygon", "School"], "demographics": [], "studios": ["feel."], "year": 2020, "episodes": 12, "source": "Light novel", "score": 8.35, "synopsis": "Resolved to become a more independent person, Yukino Yukinoshita decides to smoothen things out with her parents, and the first step toward achieving that goal is to prove herself.\n\nAs graduation draws closer for the third-year students, Iroha Isshiki—the president of the student council—requests a graduation prom in collaboration with the Volunteer Service Club. Yukino accepts this request of her own volition, hoping to use it as a chance to demonstrate her self-reliance, but what lies ahead of her may prove to be a hard hurdle to cross.\n\nYahari Ore no Seishun Love Comedy wa Machigatteiru. Kan revolves around the graduation prom as emotions are poured into the preparations for the event. At the same time, a chance for the Volunteer Service Club members to better understand each other presents itself. And thus, Hachiman Hikigaya's hectic and bittersweet high school life begins to draw to a close.\n\n[Written by MAL Rewrite]"}, {"id": 39808, "name_en": "Non Non Biyori Nonstop", "name_jp": "のんのんびより のんすとっぷ", "image_url": "https://cdn.myanimelist.net/images/anime/1033/111540.jpg", "genres": ["Slice of Life"], "themes": ["CGDCT", "Iyashikei"], "demographics": ["Seinen"], "studios": ["SILVER LINK."], "year": 2021, "episodes": 12, "source": "Manga", "score": 8.35, "synopsis": "The carefree journeys of the four girls continue! As their fleeting yet eventful summer becomes a distant memory, the arrival of fall signals a new school year with limitless opportunities. Even as the four girls return to their peaceful routine, the appearance of new faces enlivens the anything but exciting rural life of Asahigaoka.\n\nNon Non Biyori Nonstop leisurely walks back into the lives of Hotaru Ichijou, Komari Koshigaya, Natsumi Koshigaya, and Renge Miyauchi as they fully embrace their rural lifestyles and turn anything they find into nothing but fun!\n\n[Written by MAL Rewrite]"}, {"id": 41025, "name_en": "To Your Eternity", "name_jp": "不滅のあなたへ", "image_url": "https://cdn.myanimelist.net/images/anime/1880/118484.jpg", "genres": ["Adventure", "Drama", "Supernatural"], "themes": [], "demographics": ["Shounen"], "studios": ["Brain's Base"], "year": 2021, "episodes": 20, "source": "Manga", "score": 8.35, "synopsis": "An Orb, known only as It, is cast to Earth to be observed from afar. Capable of changing forms from beings whose reflections It captures, It first becomes a rock and then, due to the rising temperature, moss.\n\nIt does not move until one snowy day, a wolf at death's door barely crosses by. When It takes the animal's form, It attains awareness of its consciousness and starts to wander with an unclear destination in mind. Soon, It comes across the wolf's master—a young boy waiting for his tribe to return from a paradise abundant with fish and fruit in the south. Although the boy is lonely, he still hopes those whom he holds dear in his memories have not forgotten him and that he will reunite with them one day.\n\nThe boy wants to explore new surroundings and decides to abandon his home with It to find the paradise using the traces his tribe left behind. However, with a heavily injured body and no sight of his elder comrades, what will become of the boy?\n\nFumetsu no Anata e illustrates the story of an immortal being experiencing humanity, meeting all types of people in many places throughout time.\n\n[Written by MAL Rewrite]"}, {"id": 2251, "name_en": "Baccano!", "name_jp": "バッカーノ！", "image_url": "https://cdn.myanimelist.net/images/anime/3/14547.jpg", "genres": ["Action", "Mystery", "Supernatural"], "themes": ["Adult Cast", "Historical", "Organized Crime"], "demographics": [], "studios": ["Brain's Base"], "year": 2007, "episodes": 13, "source": "Light novel", "score": 8.35, "synopsis": "During the early 1930s in Chicago, the transcontinental train, Flying Pussyfoot, is starting its legendary journey that will leave a trail of blood all over the country. At the same time in New York, the ambitious scientist Szilard and his unwilling aide Ennis are looking for missing bottles of the immortality elixir. In addition, a war between the mafia groups is getting worse. On board the Advena Avis, in 1711, alchemists are about to learn the price of immortality.\n\nBased on the award-winning light novels of the same name, Baccano! follows several events that initially seem unrelated, both in time and place, but are part of a much bigger story—one of alchemy, survival, and immortality. Merging these events together are the kindhearted would-be thieves, Isaac and Miria, connecting various people, all of them with their own hidden ambitions and agendas, and creating lifelong bonds and consequences for everyone involved.\n\n[Written by MAL Rewrite]"}, {"id": 37095, "name_en": "Violet Evergarden: The Day You Understand \"I Love You\" Will Surely Come", "name_jp": "ヴァイオレット・エヴァーガーデンきっと\"愛\"を知る日が来るのだろう", "image_url": "https://cdn.myanimelist.net/images/anime/9/89993.jpg", "genres": ["Drama"], "themes": [], "demographics": [], "studios": ["Kyoto Animation"], "year": 2018, "episodes": 1, "source": "Light novel", "score": 8.35, "synopsis": "The CH Postal Company has just received a request to transcribe a love letter from Irma Felice, a famous opera singer. Accepting the task, Violet Evergarden visits Irma to write her letter. However, not only does Irma provide little information, she asks Violet to write based on her own feelings. Despite Violet's numerous attempts, Irma finds every version of the letter inadequate.\n\nViolet consults her colleagues, and they help her out by writing love letters of their own. Yet even those are rejected by the opera singer. As a last resort, Violet asks Irma for her true thoughts and feelings, hoping to find the missing puzzle piece. Will the Auto Memory Doll be able to translate Irma's emotions into words?\n\n[Written by MAL Rewrite]"}, {"id": 58390, "name_en": "Call of the Night Season 2", "name_jp": "よふかしのうた Season2", "image_url": "https://cdn.myanimelist.net/images/anime/1509/148453.jpg", "genres": ["Romance", "Supernatural"], "themes": ["Vampire"], "demographics": ["Shounen"], "studios": ["LIDENFILMS"], "year": 2025, "episodes": 12, "source": "Manga", "score": 8.34, "synopsis": "A human may become a vampire only if they fall in love with one—and 14-year-old Kou Yamori wishes for nothing more. Kou has decided to fall in love with a vampire named Nazuna Nanakusa, and he has only 10 months of nightly rendezvous left to realize his goal.\n\nKou's friends, Mahiru Seki and Akira Asai, are slowly coming to terms with the existence of vampires and Kou's decision to become one. As Kou realizes that a woman close to Mahiru is a vampire, he is unsure whether to support this relationship.\n\nMeanwhile, Anko Uguisu, the human detective bent on killing vampires, closes in on Nazuna and her coven. When an encounter leaves one of the vampires seriously injured, they learn that their human memories hold the key to a weakness that can be their demise—and to protect Nazuna, Kou must find out about her past.\n\n[Written by MAL Rewrite]"}, {"id": 53540, "name_en": "Detective Conan Movie 26: Black Iron Submarine", "name_jp": "名探偵コナン 黒鉄の魚影 (サブマリン)", "image_url": "https://cdn.myanimelist.net/images/anime/1138/133101.jpg", "genres": ["Action", "Mystery"], "themes": ["Detective"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2023, "episodes": 1, "source": "Manga", "score": 8.34, "synopsis": "This time's location is set in the sea near the Hachijo-jima island, Tokyo. Engineers from around the world have gathered for the full-scale operation of \"Pacific Buoy,\" an offshore facility to connect security cameras owned by the worldwide police forces. A test of a certain \"new technology\" based on a face recognition system is underway there.\n\nMeanwhile, Conan and the Detective Boys visit Hachijo-jima at Sonoko's invitation and receive a phone call from Subaru Okiya informing them that a Europol employee was murdered in Germany by the Black Organization's Jin.\n\nConan, who is disquieted, sneaks into the facility and finds that a female engineer has been kidnapped by the Black Organization...! Furthermore, a USB drive containing certain information in her possession ends up in the hands of the organization... A black shadow also creeps up on Ai Haibara ...\n\n(Source: Crunchyroll, edited)"}, {"id": 31988, "name_en": "Sound! Euphonium 2", "name_jp": "響け！ユーフォニアム2", "image_url": "https://cdn.myanimelist.net/images/anime/10/81155.jpg", "genres": ["Drama"], "themes": ["Music", "Performing Arts", "School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2016, "episodes": 13, "source": "Novel", "score": 8.34, "synopsis": "Following their success in the qualifying round for the Kansai regional competition, the members of the Kitauji High School concert band set their sights on the next upcoming performance. Utilizing their summer break to the utmost, the band participates in a camp where they are instructed by their band advisor Noboru Taki and his friends who make their living as professional musicians.\n\nKumiko Oumae and her friends remain determined to attain gold at the Kansai competition, but trouble arises when a student who once quit the band shows interest in rejoining and sparks unpleasant memories for the second-year members. Kumiko also learns about her teacher's surprising past and the motivation behind his desire to lead the band to victory. Reaching nationals will require hard work, and the adamant conviction in each student's commitment to the band will be put to the test.\n\n[Written by MAL Rewrite]"}, {"id": 41457, "name_en": "86 Eighty-Six", "name_jp": "86―エイティシックス―", "image_url": "https://cdn.myanimelist.net/images/anime/1987/117507.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["Mecha", "Military"], "demographics": [], "studios": ["A-1 Pictures"], "year": 2021, "episodes": 11, "source": "Light novel", "score": 8.34, "synopsis": "According to the Republic of San Magnolia, their ongoing war against the Giadian Empire has no casualties—however, that is mere propaganda. While the silver-haired Alba of the Republic's eighty-five sectors live safely behind protective walls, those of different appearances are interned in a secret eighty-sixth faction. Known within the military as the Eighty-Six, they are forced to fight against the Empire's autonomous Legion under the command of the Republican \"Handlers.\"\n\nVladilena Milizé is assigned to the Spearhead squadron to replace their previous Handler. Shunned by her peers for being a fellow Eighty-Six supporter, she continues to fight against their inhumane discrimination. Shinei Nouzen is the captain of the Spearhead squadron. Infamous for being the sole survivor of every squadron he's been in, he insists on shouldering the names and wishes of his fallen comrades. When the fates of these young souls from two different worlds collide, will it ignite the spark that lights their path to salvation, or will they burn themselves in the flames of despair?\n\n[Written by MAL Rewrite]"}, {"id": 46654, "name_en": "IDOLiSH7 Third BEAT! Part 2", "name_jp": "アイドリッシュセブン", "image_url": "https://cdn.myanimelist.net/images/anime/1458/125998.jpg", "genres": ["Drama"], "themes": ["Idols (Male)", "Music"], "demographics": [], "studios": ["TROYCA"], "year": 2022, "episodes": 17, "source": "Game", "score": 8.34, "synopsis": "IDOLiSH7 is more successful than ever, but no one is smiling because a new threat has taken the stage: the debuting idol group ŹOOĻ. Managed by the nefarious entertainment executive Ryou Tsukumo, they will stop at nothing to get their way, resorting to all kinds of underhanded tactics to bring down their rivals. TRIGGER's image has already been ruined by scandal, and IDOLiSH7 and Re:vale are headed for the same fate—or worse.\n\nMeanwhile, Zero, the legendary idol who disappeared 15 years ago, continues to haunt the present through those he left behind. His manager seems to be in contact with Tsukumo for his own schemes, and Zero's songwriter reveals himself in a letter after having been missing for years. IDOLiSH7 finds an ally in Re:vale to thwart ŹOOĻ's plans while trying to make sense of Zero's colleagues' actions, but it may be impossible for everyone to emerge unscathed.\n\n[Written by MAL Rewrite]"}, {"id": 49458, "name_en": "KonoSuba: God's Blessing on This Wonderful World! 3", "name_jp": "この素晴らしい世界に祝福を！3", "image_url": "https://cdn.myanimelist.net/images/anime/1758/141268.jpg", "genres": ["Adventure", "Comedy", "Fantasy"], "themes": ["Isekai", "Parody"], "demographics": [], "studios": ["Drive"], "year": 2024, "episodes": 11, "source": "Light novel", "score": 8.34, "synopsis": "Kazuma Satou, former NEET and current reincarnated adventurer, finally returns home after the incident at the Crimson Demon village. He is joined by his ever-reliable companions: self-absorbed goddess Aqua, one-trick pony arch-wizard Megumin, and masochistic crusader Lalatina Ford \"Darkness\" Dustiness. Following their recent exploits, the party is one step closer to vanquishing the Demon King and his armies—a terrifying task that Kazuma is eager to ignore for as long as possible. Things, however, are not going as smoothly as the group would like; they are constantly racking up debt, getting sidetracked during missions, and falling into traps due to their recklessness and eccentricities.\n\nAs the party struggles with their perennial issues, they receive an invitation from a princess interested in hearing tales of the group's \"heroic\" deeds. Darkness' pleas to refuse the invitation only fire the others up to accept it, marking the start of yet another uncanny adventure with unforeseen consequences.\n\n[Written by MAL Rewrite]"}, {"id": 777, "name_en": "Hellsing Ultimate", "name_jp": "HELLSING OVA", "image_url": "https://cdn.myanimelist.net/images/anime/1012/143965.jpg", "genres": ["Action", "Horror", "Supernatural"], "themes": ["Adult Cast", "Gore", "Military", "Vampire"], "demographics": ["Seinen"], "studios": ["Madhouse", "Satelight", "Graphinica"], "year": 2006, "episodes": 10, "source": "Manga", "score": 8.34, "synopsis": "There exist creatures of darkness and evil that plague the night, devouring any human unfortunate enough to be caught in their grasp. On the other side is Hellsing, an organization dedicated to destroying these supernatural forces that threaten the very existence of humanity. At its head is Integra Fairbrook Wingates Hellsing, who commands a powerful military and spends her life fighting the undead.\n\nIntegra's vast army, however, pales in comparison with her ultimate weapon: the vampire Alucard, who works against his own kind as an exterminator for Hellsing. With his new vampire servant, Seras Victoria, at his side, Alucard must battle not only monsters, but all those who stand to oppose Hellsing, be they in the guise of good or evil.\n\nIn a battle for mankind's survival, Hellsing Ultimate proves that appearances are not all they may seem, and sometimes the greatest weapon can come in the form of one's worst nightmare.\n\n[Written by MAL Rewrite]"}, {"id": 39535, "name_en": "Mushoku Tensei: Jobless Reincarnation", "name_jp": "無職転生 ～異世界行ったら本気だす～", "image_url": "https://cdn.myanimelist.net/images/anime/1530/117776.jpg", "genres": ["Adventure", "Drama", "Fantasy", "Ecchi"], "themes": ["Isekai", "Reincarnation"], "demographics": [], "studios": ["Studio Bind"], "year": 2021, "episodes": 11, "source": "Light novel", "score": 8.34, "synopsis": "Despite being bullied, scorned, and oppressed all of his life, a 34-year-old shut-in still found the resolve to attempt something heroic—only for it to end in a tragic accident. But in a twist of fate, he awakens in another world as Rudeus Greyrat, starting life again as a baby born to two loving parents.\n\nPreserving his memories and knowledge from his previous life, Rudeus quickly adapts to his new environment. With the mind of a grown adult, he starts to display magical talent that exceeds all expectations, honing his skill with the help of a mage named Roxy Migurdia. Rudeus learns swordplay from his father, Paul, and meets Sylphiette, a girl his age who quickly becomes his closest friend.\n\nAs Rudeus' second chance at life begins, he tries to make the most of his new opportunity while conquering his traumatic past. And perhaps, one day, he may find the one thing he could not find in his old world—love.\n\n[Written by MAL Rewrite]"}, {"id": 49926, "name_en": "Demon Slayer: Kimetsu no Yaiba Mugen Train Arc", "name_jp": "鬼滅の刃 無限列車編", "image_url": "https://cdn.myanimelist.net/images/anime/1065/118763.jpg", "genres": ["Action", "Supernatural"], "themes": ["Historical"], "demographics": ["Shounen"], "studios": ["ufotable"], "year": 2021, "episodes": 7, "source": "Manga", "score": 8.34, "synopsis": "A mysterious string of disappearances on a certain train has caught the attention of the Demon Slayer Corps, and they have sent one of their best to exterminate what can only be a demon responsible. However, the plan to board the Mugen Train is delayed by a lesser demon who is terrorizing the mechanics and targeting a kind, elderly woman and her granddaughter. Kyoujurou Rengoku, the Flame Hashira, must eliminate the threat before boarding the train.\n\nSent to assist the Hashira, Tanjirou Kamado, Inosuke Hashira, and Zenitsu Agatsuma enter the train prepared to fight. But their monstrous target already has a devious plan in store for them and the two hundred passengers: by delving deep into their consciousness, the demon intends to obliterate everyone in a stunning display of the power held by the Twelve Kizuki. \n\n[Written by MAL Rewrite]"}, {"id": 39551, "name_en": "That Time I Got Reincarnated as a Slime Season 2", "name_jp": "転生したらスライムだった件", "image_url": "https://cdn.myanimelist.net/images/anime/1271/109841.jpg", "genres": ["Action", "Comedy", "Fantasy"], "themes": ["Isekai", "Reincarnation"], "demographics": ["Shounen"], "studios": ["8bit"], "year": 2021, "episodes": 12, "source": "Manga", "score": 8.34, "synopsis": "Taking a break from his time as a teacher, the powerful slime Rimuru Tempest returns to his kingdom, eponymously named Tempest, just in time to begin negotiations with a nearby nation—the Kingdom of Eurazania. While the negotiations are anything but peaceful, they do end successfully, allowing Rimuru to return and finish teaching. When trying to again return to Tempest, this time permanently, Rimuru is stopped by a mysterious figure who is somehow able to constrain the many magical abilities he has at his disposal. \n\nIn Tempest, the situation is even worse. A group of unknown humans has invaded the land and are assaulting its citizens, both influential and innocent. They are not just trying to bring harm either—they have the intent to kill. Can Rimuru overcome his powerful and dangerous foe and return to Tempest before it is too late?  \n\n[Written by MAL Rewrite]"}, {"id": 57067, "name_en": "A Record Of Mortal's Journey To Immortality 3rd Season", "name_jp": "凡人修仙传 星海飞驰", "image_url": "https://cdn.myanimelist.net/images/anime/1119/139470.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Original Force", "Wonder Cat Animation"], "year": 2023, "episodes": 48, "source": "Web novel", "score": 8.33, "synopsis": "Third season of Fanren Xiu Xian Zhuan."}, {"id": 40211, "name_en": "The Legend of Hei", "name_jp": "罗小黑战记", "image_url": "https://cdn.myanimelist.net/images/anime/1583/108598.jpg", "genres": ["Adventure", "Drama", "Fantasy"], "themes": [], "demographics": [], "studios": ["HMCH"], "year": 2019, "episodes": 1, "source": "Original", "score": 8.33, "synopsis": "Luo Xiaohei is a young monster who normally takes the form of a small black cat. Living freely in the forests, gradual deforestation and human development force him to flee and find a new home. Wandering the city streets, the black cat struggles to survive until he encounters Feng Xi, a fellow monster who takes Xiaohei to an isolated island inhabited by a small group of monsters. Excited to find what he thinks is his new home, Xiaohei lives with the monsters for only a short time before Wuxian, a human Guild Executor, arrives on the island in pursuit of Feng Xi.\n\nFeng Xi and his companions manage to teleport off the island, leaving Xiaohei and Wuxian alone. Though Xiaohei refuses to cooperate with the man who attacked his friends, Wuxian forces the black cat to travel to the Guild along with him. Embarking on an uncertain journey, Xiaohei is exposed to a new perspective on the relations between humans and monsters in a rapidly changing world.\n\n[Written by MAL Rewrite]"}, {"id": 39587, "name_en": "Re:ZERO -Starting Life in Another World- Season 2", "name_jp": "Re：ゼロから始める異世界生活 2", "image_url": "https://cdn.myanimelist.net/images/anime/1444/108005.jpg", "genres": ["Drama", "Fantasy", "Suspense"], "themes": ["Isekai", "Psychological", "Time Travel"], "demographics": [], "studios": ["White Fox"], "year": 2020, "episodes": 13, "source": "Light novel", "score": 8.33, "synopsis": "A reunion that was supposed to spell the arrival of peaceful times is quickly shattered when Subaru Natsuki and Emilia return to Irlam village. Witnessing the devastation left behind by the calamities known as Sin Archbishops, Subaru sinks into the depths of despair as his ability to redo proves futile.\n\nAs the group makes their way to the Sanctuary in search of answers, Subaru has an unexpected encounter with the Witch of Greed—Echidna. Subjected to her untamed rhythm, he is forced to dive into the spirals of the past and future. At the same time, several mysterious threats set their sights on the Sanctuary, heralding a horrific fate for the hapless people trapped within.\n\nEverlasting contracts, past sins, and unrequited love will clash and submerge into a river of blood in the second season of Re:Zero kara Hajimeru Isekai Seikatsu. Pushed to the brink of hopelessness, how long will Subaru's resolve to save his loved ones last?\n\n[Written by MAL Rewrite]"}, {"id": 13601, "name_en": "Psycho-Pass", "name_jp": "サイコパス", "image_url": "https://cdn.myanimelist.net/images/anime/1314/142015.jpg", "genres": ["Action", "Mystery", "Sci-Fi", "Suspense"], "themes": ["Adult Cast", "Detective", "Psychological"], "demographics": [], "studios": ["Production I.G"], "year": 2012, "episodes": 22, "source": "Original", "score": 8.33, "synopsis": "Justice, and the enforcement of it, has changed. In the 22nd century, Japan enforces the Sibyl System, an objective means of determining the threat level of each citizen by examining their mental state for signs of criminal intent, known as their Psycho-Pass. Inspectors uphold the law by subjugating, often with lethal force, anyone harboring the slightest ill-will; alongside them are Enforcers, citizens that have become latent criminals, granted relative freedom in exchange for carrying out the Inspectors' dirty work.\n\nInto this world steps Akane Tsunemori, a young woman with an honest desire to uphold justice. However, as she works alongside veteran Enforcer Shinya Kougami, she soon learns that the Sibyl System's judgments are not as perfect as her fellow Inspectors assume. With everything she has known turned on its head, Akane wrestles with the question of what justice truly is, and whether it can be upheld through the use of a system that may already be corrupt.\n\n[Written by MAL Rewrite]"}, {"id": 10534, "name_en": "Doraemon: Nobita and the Steel Troops - The New Age", "name_jp": "映画 ドラえもん 新・のび太と鉄人兵団～はばたけ 天使たち～", "image_url": "https://cdn.myanimelist.net/images/anime/7/72436.jpg", "genres": ["Adventure", "Comedy", "Fantasy"], "themes": ["Mecha"], "demographics": ["Kids", "Shounen"], "studios": ["Shin-Ei Animation"], "year": 2011, "episodes": 1, "source": "Manga", "score": 8.33, "synopsis": "Jealous of Suneo's new robot toy, Nobita asks Doraemon to build him an even better one. Doraemon initially refuses, until Nobita accidentally discovers pieces of a mysterious robot that falls from the sky. After gathering all the robot parts and assembled them together, the giant robot, Zanda Claus, is soon completed. The duo soon learn that the robot is not a mere toy, but a powerful weapon in the fight against the coming Robot Army that is going to attack Earth and enslave the human inhabitants of it. An invasion is near, as a mysterious girl Riruru (リルル, Alternative spelling: Lilulu, Lillele [Doko Demo Doa Scanlations]) shows up, looking for the robot.\n\n(Source: Wikipedia)"}, {"id": 10030, "name_en": "Bakuman. Season 2", "name_jp": "バクマン。2ndシーズン", "image_url": "https://cdn.myanimelist.net/images/anime/3/34923.jpg", "genres": ["Comedy", "Drama", "Romance"], "themes": ["Otaku Culture"], "demographics": ["Shounen"], "studios": ["J.C.Staff"], "year": 2011, "episodes": 25, "source": "Manga", "score": 8.33, "synopsis": "With the serialization of their new manga, \"Detective Trap,\" the writer-artist team, Akito Takagi and Moritaka Mashiro, better known by their pseudonym Muto Ashirogi, are one step closer to becoming world-renowned mangaka. For Mashiro, however, serialization is just the first step. Having promised to marry his childhood sweetheart and aspiring voice actress, Azuki Miho, once his manga gets an anime adaptation, Mashiro must continue his to popularize Ashirogi's work. A tremendously competitive cast of ambitious mangaka—including the wild genius, Eiji Niizuma; the elegant student, Yuriko Aoki, and her older admirer and partner, Takurou Nakai; the lazy prodigy, Kazuya Hiramaru; and the abrasive artist, Shinta Fukuda—both support and compete against Muto Ashirogi in creating the next big hit.\n\nAs they adjust to their young and seemingly untested new editor, the dynamic duo struggle to maintain their current serialization, secure the top spot in Shounen Jack, and ultimately, achieve an anime adaptation of their manga. With new rivals and friends, Bakuman. 2nd Season continues Takagi and Mashiro's inspiring story of hard work and young love.\n\n[Written by MAL Rewrite]"}, {"id": 20651, "name_en": "Natsume’s Book of Friends: On a Certain Snowy Day", "name_jp": "夏目友人帳 新作OVA いつかゆきのひに", "image_url": "https://cdn.myanimelist.net/images/anime/11/79742.jpg", "genres": ["Slice of Life", "Supernatural"], "themes": ["Iyashikei", "Mythology"], "demographics": ["Shoujo"], "studios": ["Brain's Base"], "year": 2014, "episodes": 1, "source": "Manga", "score": 8.33, "synopsis": "As the first delicate snowflake touches the cold ground, Nyanko-sensei dozes on his hot-water bottle next to Takashi Natsume. While his aunt prepares a warm dinner, Natsume takes a walk, welcoming the snow and the chilly weather. However, his peaceful promenade is disturbed by a white, fluffy snowman yokai with perturbing black eyes, searching for something warm and sparkly. With no further information, Natsume sets off to help find what the spirit desires.\n\n[Written by MAL Rewrite]"}, {"id": 55357, "name_en": "Bocchi the Rock! Movie", "name_jp": "ぼっち・ざ・ろっく！劇場総集編", "image_url": "https://cdn.myanimelist.net/images/anime/1256/142261.jpg", "genres": ["Comedy"], "themes": ["CGDCT", "Music"], "demographics": [], "studios": ["CloverWorks"], "year": 2024, "episodes": 2, "source": "4-koma manga", "score": 8.32, "synopsis": "Theatrical compilation of Bocchi the Rock!"}, {"id": 51805, "name_en": "Legend of the Galactic Heroes: Die Neue These - Intrigue", "name_jp": "銀河英雄伝説 Die Neue These 策謀", "image_url": "https://cdn.myanimelist.net/images/anime/1700/123762.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["Adult Cast", "Military", "Space"], "demographics": [], "studios": ["Production I.G"], "year": 2022, "episodes": 12, "source": "Novel", "score": 8.32, "synopsis": "After Yang Wen-li successfully captures the Iserlohn Fortress, the Free Planets Alliance grows bolder in its militaristic ambitions. With recent incursions into Galactic Empire territory and the possession of a perfect beachhead, the Alliance is now able to deal significant damage to the Empire. However, Reinhard von Lohengramm has no intention of allowing the Free Planets Alliance to gain a foothold. He is adamant about carrying on his best friend Siegfried Kircheis' dearest wish: to be the one to unite the universe. \n\nAs wide-scale military clashes continue, the opportunist Adrian Rubinsky, Fezzan's ambitious leader, attempts to take advantage of the war to further his personal goals.\n\n[Written by MAL Rewrite]"}, {"id": 10937, "name_en": "Mobile Suit Gundam: The Origin", "name_jp": "機動戦士ガンダムTHE ORIGIN", "image_url": "https://cdn.myanimelist.net/images/anime/4/72702.jpg", "genres": ["Sci-Fi"], "themes": ["Mecha", "Military", "Space"], "demographics": ["Shounen"], "studios": ["Sunrise"], "year": 2015, "episodes": 6, "source": "Manga", "score": 8.32, "synopsis": "In the year 0068 of the Universal Century, Casval Rem Deikun's life is thrown into chaotic disarray after the assassination of his father, a prolific figure in the crusade towards civil rights for people who live away from the Earth's surface. Casval and his sister Artesia receive aid from soldiers who were loyal to their father, and the siblings are whisked away from their home, separated, and thrown into a twisted fate that sees them come into their own as soldiers and adults.\n\nYears before stealing the name Char Aznable or his \"Red Comet\" moniker, Casval must contend with the rise of a militarized version of his father's ideology and his own vengeful nature as intergalactic war molds him into a legendary mobile suit pilot.\n\n[Written by MAL Rewrite]"}, {"id": 28701, "name_en": "Fate/stay night [Unlimited Blade Works] Season 2", "name_jp": "Fate/stay night [Unlimited Blade Works] 2nd シーズン", "image_url": "https://cdn.myanimelist.net/images/anime/1881/124810.jpg", "genres": ["Action", "Fantasy"], "themes": ["Urban Fantasy"], "demographics": [], "studios": ["ufotable"], "year": 2015, "episodes": 13, "source": "Visual novel", "score": 8.32, "synopsis": "In the midst of the Fifth Holy Grail War, Caster sets her plans into motion, beginning with the capture of Shirou's Servant Saber. With the witch growing ever more powerful, Rin and Archer determine she is a threat that must be dealt with at once. But as the balance of power in the war begins to shift, the Master and Servant find themselves walking separate ways.\n\nMeanwhile, despite losing his Servant and stumbling from injuries, Shirou ignores Rin's warning to abandon the battle royale, forcing his way into the fight against Caster. Determined to show his resolve in his will to fight, Shirou's potential to become a protector of the people is put to the test.\n\nAmidst the bloodshed and chaos, the motivations of each Master and Servant are slowly revealed as they sacrifice everything in order to arise as the victor and claim the Holy Grail.\n\n[Written by MAL Rewrite]"}, {"id": 5040, "name_en": "One Outs", "name_jp": "ワンナウツ", "image_url": "https://cdn.myanimelist.net/images/anime/7/21065.jpg", "genres": ["Sports", "Suspense"], "themes": ["Adult Cast", "Psychological", "Team Sports"], "demographics": ["Seinen"], "studios": ["Madhouse"], "year": 2008, "episodes": 25, "source": "Manga", "score": 8.32, "synopsis": "Toua Tokuchi is a prodigy when it comes to both baseball and gambling. Pitching nothing but mediocre fastballs, he has made a name for himself by attaining 499 consecutive victories in the game of One Outs: a one-on-one showdown between a pitcher and a batter. Despite his remarkable abilities, however, Tokuchi sees the sport as nothing more than a way to make some quick money.\n\nWhen Hiromichi Kojima, a legendary hitter for the struggling Saikyou Saitama Lycaons, witnesses Tokuchi's skills firsthand, he realizes that the man might be exactly what the team needs to get out of their slump. Determined to recruit Tokuchi no matter what, Kojima places his own career on the line and defeats Tokuchi at his own specialty—One Outs.\n\nForced to become a Lycaon, Tokuchi soon finds himself at odds with the team's owner, Tsuneo Saikawa. After Saikawa refuses to give him a reasonable salary, Tokuchi proposes a special contract: pitching an out will earn him five million yen, but giving up a run will cost him fifty million. With every game having the potential to either make or break him, Tokuchi must outwit all who stand in his way.\n\n[Written by MAL Rewrite]"}, {"id": 5081, "name_en": "Bakemonogatari", "name_jp": "化物語", "image_url": "https://cdn.myanimelist.net/images/anime/11/75274.jpg", "genres": ["Mystery", "Romance", "Supernatural"], "themes": ["Vampire"], "demographics": [], "studios": ["Shaft"], "year": 2009, "episodes": 15, "source": "Light novel", "score": 8.32, "synopsis": "Koyomi Araragi, a third-year high school student, manages to survive a vampire attack with the help of Meme Oshino, a strange man residing in an abandoned building. Though being saved from vampirism and now a human again, several side effects such as superhuman healing abilities and enhanced vision still remain. Regardless, Araragi tries to live the life of a normal student, with the help of his friend and the class president, Tsubasa Hanekawa.\n\nWhen fellow classmate Hitagi Senjougahara falls down the stairs and is caught by Araragi, the boy realizes that the girl is unnaturally weightless. Despite Senjougahara's protests, Araragi insists he help her, deciding to enlist the aid of Oshino, the very man who had once helped him with his own predicament.\n\nThrough several tales involving demons and gods, Bakemonogatari follows Araragi as he attempts to help those who suffer from supernatural maladies.\n\n[Written by MAL Rewrite]"}, {"id": 22535, "name_en": "Parasyte: The Maxim", "name_jp": "寄生獣 セイの格率", "image_url": "https://cdn.myanimelist.net/images/anime/3/73178.jpg", "genres": ["Action", "Horror", "Sci-Fi", "Suspense"], "themes": ["Gore", "Psychological"], "demographics": ["Seinen"], "studios": ["Madhouse"], "year": 2014, "episodes": 24, "source": "Manga", "score": 8.32, "synopsis": "All of a sudden, they arrived: parasitic aliens that descended upon Earth and quickly infiltrated humanity by burrowing into the brains of vulnerable targets. These insatiable beings acquire full control of their host and are able to morph into a variety of forms in order to feed on unsuspecting prey.\n \nSixteen-year-old high school student Shinichi Izumi falls victim to one of these parasites, but it fails to take over his brain, ending up in his right hand instead. Unable to relocate, the parasite, now named Migi, has no choice but to rely on Shinichi in order to stay alive. Thus, the pair is forced into an uneasy coexistence and must defend themselves from hostile parasites that hope to eradicate this new threat to their species.\n\n[Written by MAL Rewrite]"}, {"id": 50407, "name_en": "Fog Hill of Five Elements", "name_jp": "雾山五行·犀川幻紫林", "image_url": "https://cdn.myanimelist.net/images/anime/1983/147467.jpg", "genres": ["Action", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Samsara Animation Studio"], "year": 2023, "episodes": 4, "source": "Original", "score": 8.32, "synopsis": "Second season of Wu Shan Wu Xing."}, {"id": 10162, "name_en": "Bunny Drop", "name_jp": "うさぎドロップ", "image_url": "https://cdn.myanimelist.net/images/anime/1460/98853.jpg", "genres": ["Slice of Life"], "themes": ["Childcare", "Iyashikei"], "demographics": ["Josei"], "studios": ["Production I.G"], "year": 2011, "episodes": 11, "source": "Manga", "score": 8.32, "synopsis": "Daikichi Kawachi is a 30-year-old bachelor working a respectable job but otherwise wandering aimlessly through life. When his grandfather suddenly passes away, he returns to the family home to pay his respects. Upon arriving at the house, he meets a mysterious young girl named Rin who, to Daikichi’s astonishment, is his grandfather's illegitimate daughter!\n \nThe shy and unapproachable girl is deemed an embarrassment to the family, and finds herself ostracized by her father's relatives, all of them refusing to take care of her in the wake of his death. Daikichi, angered by their coldness toward Rin, announces that he will take her in—despite the fact that he is a young, single man with no prior childcare experience.\n\nUsagi Drop is the story of Daikichi's journey through fatherhood as he raises Rin with his gentle and affectionate nature, as well as an exploration of the warmth and interdependence that are at the heart of a happy, close-knit family.\n\n[Written by MAL Rewrite]"}, {"id": 42886, "name_en": "Legend of the Galactic Heroes: Die Neue These - Collision", "name_jp": "銀河英雄伝説 Die Neue These 激突", "image_url": "https://cdn.myanimelist.net/images/anime/1859/118711.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["Adult Cast", "Military", "Space"], "demographics": [], "studios": ["Production I.G"], "year": 2022, "episodes": 12, "source": "Novel", "score": 8.32, "synopsis": "As Yang is ordered to appear before the Free Planet Alliance, he’s shaken by the corruption he discovers. Meanwhile, Marshal Reinhardt uncovers disloyalty under his command as an upstart admiral launches a vicious offensive to retake the Iserlohn Fortress. Struggling to make strides in this never-ending bloody war, these two finally see their true enemy lies not from without—but from within!\n\n(Source: Crunchyroll)"}, {"id": 55644, "name_en": "Dr. Stone: New World Part 2", "name_jp": "Dr.STONE NEW WORLD", "image_url": "https://cdn.myanimelist.net/images/anime/1236/138696.jpg", "genres": ["Adventure", "Comedy"], "themes": [], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2023, "episodes": 11, "source": "Manga", "score": 8.31, "synopsis": "Years ago, astronaut Byakuya left behind precious materials in the belief that his son, the scientifically-gifted Senkuu, would one day find them. In the present, the Kingdom of Science's Kohaku and Ginrou have infiltrated the harem of the Petrification Kingdom’s leader with the help of Amaryllis, a local girl. At first, their mission progresses without a hitch. Unfortunately, an error enacted by Kohaku and Ginrou leads Minister Ibara and the Island’s strongest warrior, Moz, to identify the intruding duo as enemies.\n\nEven worse, Moz tails Amaryllis when she returns to inform Senkuu of the news, discovering the scientist's secret hideout. However, Moz's agenda does not align with Ibara's—he wants to dethrone the minister and keep the petrification device for himself. If Senkuu plays his cards right, he could gain a powerful ally and win the war once and for all.\n\n[Written by MAL Rewrite]"}, {"id": 51440, "name_en": "Sasaki and Miyano: Graduation", "name_jp": "映画 佐々木と宮野ー卒業編ー", "image_url": "https://cdn.myanimelist.net/images/anime/1574/128922.jpg", "genres": ["Boys Love"], "themes": ["Otaku Culture", "School"], "demographics": [], "studios": ["Studio Deen"], "year": 2023, "episodes": 1, "source": "Manga", "score": 8.31, "synopsis": "With university entrance exams and graduation drawing close, high school senior Shuumei Sasaki has to start taking his future more seriously. However, Sasaki is increasingly finding it hard to study; all he can think about is his boyfriend and junior, Yoshikazu Miyano. Having only recently made their relationship official, Sasaki cannot help but yearn to hold his boyfriend close. But with Sasaki's graduation on their minds, the couple begins to wonder what lies ahead for them both—especially as a public relationship can garner scrutiny from friends and family.\n\n[Written by MAL Rewrite]"}, {"id": 34534, "name_en": "Natsume's Book of Friends Season 5 Specials", "name_jp": "夏目友人帳 伍 特別編", "image_url": "https://cdn.myanimelist.net/images/anime/3/84887.jpg", "genres": ["Slice of Life", "Supernatural"], "themes": ["Iyashikei", "Mythology"], "demographics": ["Shoujo"], "studios": ["Shuka"], "year": 2017, "episodes": 2, "source": "Manga", "score": 8.31, "synopsis": "One Night Sake Cup \nOn his way back home from an errand, Takashi Natsume gets lost in the forest. Luckily, he is rescued by a traveling youkai couple who make sake cups for a living. Taking a sip of water from one, Natsume feels a surge of energy rush throughout his body, thanks to the \"One Night Cup.\" It is a special cup that makes every drink delicious, but unfortunately, its effects last only for one night. The following day, Natsume attends the youkai banquet with his loyal protector, the sake-loving chubby cat Madara, and gets the once-in-a-lifetime opportunity to learn how to make sake cups.\n\nParty of Fun &amp; Games\nNatsume is filled with regret after hastily declining a rare invitation to a banquet from Madara. Curious, he chases after the cat to an old, run-down mansion but soon enough feels the uncanny presence of numerous youkai nearby. When he runs into the seductive youkai Hinoe, she explains that the empty estate serves as a hide-and-seek playground where the winning spirit acquires authority over a valley. With still no trace of Madara, Natsume starts having eerie dreams of the mansion, but this time as the game's participant.\n\n[Written by MAL Rewrite]"}, {"id": 12029, "name_en": "Star Blazers: Space Battleship Yamato 2199", "name_jp": "宇宙戦艦ヤマト2199", "image_url": "https://cdn.myanimelist.net/images/anime/2/36607.jpg", "genres": ["Action", "Drama", "Sci-Fi"], "themes": ["Adult Cast", "Military", "Space"], "demographics": [], "studios": ["Xebec", "AIC"], "year": 2012, "episodes": 26, "source": "Original", "score": 8.31, "synopsis": "Earth's once green hills and rich blue oceans have been converted into a desolate wasteland by the relentless onslaught of planet bombs from the expansionist Gamilas Empire. By the year 2199, humankind has retreated to the depths of underground cities to escape the radiation.\n\nHowever, all hope is not lost, as the distant planet Iscandar offers Earth its final salvation: the Cosmo Reverse System, capable of restoring Earth's irradiated surface. In a desperate bid to retrieve this technology from Iscandar, the Earth Defense Force pours its remaining resources into the Yamato—an innovative, state-of-the-art battleship equipped with an infinite energy Wave Motion Engine.\n\nFrom young talent such as Tactical Officer Susumu Kodai to accomplished veterans like Captain Juuzou Okita, the Yamato's crew consists of many individuals, all varying in their experience on the battlefield. But despite their differences, one goal unifies them all: to complete their perilous 168,000 light-year voyage to Iscandar and save humanity from imminent doom.\n\n[Written by MAL Rewrite]"}, {"id": 39112, "name_en": "Crying for Rain", "name_jp": "カワキヲアメク", "image_url": "https://cdn.myanimelist.net/images/anime/1776/121796.jpg", "genres": [], "themes": ["Music"], "demographics": [], "studios": [], "year": 2019, "episodes": 1, "source": "Original", "score": 8.31, "synopsis": "Music video for the song Kawaki wo Ameku by Minami."}, {"id": 7655, "name_en": "Major S6", "name_jp": "メジャー 第6シリーズ", "image_url": "https://cdn.myanimelist.net/images/anime/2/75216.jpg", "genres": ["Sports"], "themes": ["Team Sports"], "demographics": ["Shounen"], "studios": ["SynergySP"], "year": 2010, "episodes": 25, "source": "Manga", "score": 8.31, "synopsis": "The intense Baseball World Cup has reached its conclusion. Gorou Honda has regained his passion for baseball and is once again back in full gear. He has secured a team position with the Hornets and has travelled back to America to prepare for his spectacular debut as a Major League pitcher.\n\nHowever, Gorou encounters a sudden series of unexpected issues and devastating events follow, crushing his motivation and potentially reducing the baseball career that he has worked tirelessly to maintain into crumbs. In the final season of Major, Gorou must yet again overcome immense hardship in order to save his baseball career. This time there is no simple solution, as the problem is deeply rooted within his own mind...\n\n[Written by MAL Rewrite]"}, {"id": 42984, "name_en": "Gotcha!", "name_jp": "GOTCHA!", "image_url": "https://cdn.myanimelist.net/images/anime/1637/109512.jpg", "genres": ["Fantasy"], "themes": ["Music"], "demographics": [], "studios": ["Bones"], "year": 2020, "episodes": 1, "source": "Game", "score": 8.31, "synopsis": "In a bright, grandiose city, a young boy and girl look for their partners amidst a technicolor tour de force. As the two embark on their respective quests, they encounter the familiar faces of those who have made their mark on the Pokémon world. Villains, champions, rivals, and professors alike grace the tallest skyscrapers and largest screens while the adventurous teens carry on in the search for their companions.\n\nGotcha! takes its viewers on a nostalgic trip to the riveting realm of Pokémon, encapsulating the electrifying essence of the franchise.\n\n[Written by MAL Rewrite]"}, {"id": 34240, "name_en": "Shelter", "name_jp": "シェルター", "image_url": "https://cdn.myanimelist.net/images/anime/5/82388.jpg", "genres": ["Sci-Fi"], "themes": ["Music"], "demographics": [], "studios": ["A-1 Pictures"], "year": 2016, "episodes": 1, "source": "Original", "score": 8.31, "synopsis": "Day 2539: Rin wakes up alone again with blurred memories and still no contact from any other human. She is not bored, however, because in her arms lies a tablet capable of creating any world her heart desires. Day after day, Rin crafts a wonderful reality—one utopia at a time—to shelter her from loneliness, hoping to one day reveal the truth behind her very existence.\n\n[Written by MAL Rewrite]"}, {"id": 137, "name_en": "Hunter x Hunter: Original Video Animation", "name_jp": "HUNTER×HUNTER Original Video Animation", "image_url": "https://cdn.myanimelist.net/images/anime/1/137.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Nippon Animation"], "year": 2002, "episodes": 8, "source": "Manga", "score": 8.31, "synopsis": "After reuniting with Gon and his friends, Kurapika explained to them the risks he bears because of his abilities. Believing that his target of revenge is no longer alive and the search for his fallen comrade's eyes could truly begin, Kurapika soon after receives a message informing him that all the Spiders still lived. After much discussion between his friends, Gon, Kurapika and company decided to hunt after the one Spider member who's ability could ultimately result in Kurapika's defeat and death. Based on the manga by Togashi Yoshihiro."}, {"id": 1210, "name_en": "Welcome to the N.H.K.", "name_jp": "N・H・Kにようこそ！", "image_url": "https://cdn.myanimelist.net/images/anime/3/52675.jpg", "genres": ["Comedy", "Drama", "Romance"], "themes": ["Otaku Culture", "Psychological"], "demographics": [], "studios": ["Gonzo"], "year": 2006, "episodes": 24, "source": "Novel", "score": 8.31, "synopsis": "Twenty-two-year-old college dropout Tatsuhiro Satou has been a hikikomori for almost four years now. In his isolation, he has come to believe in many obscure conspiracy theories, but there is one in particular which he holds unshakable faith in: the theory that the evil conspirator behind his shut-in NEET (Not in Employment, Education or Training) status is the Nihon Hikikomori Kyokai (NHK)—an evil and secret organization dedicated to fostering the spread of hikikomori culture.\n\nNHK ni Youkoso! is a psychological dramedy that follows Tatsuhiro as he strives to escape from the NHK's wicked machinations and the disease of self-wrought isolation, while struggling to even just leave his apartment and find a job. His unexpected encounter with the mysterious Misaki Nakahara might signal a reversal of fortune for Tatsuhiro, but with this meeting comes the inevitable cost of having to face his greatest fear—society.\n\n[Written by MAL Rewrite]"}, {"id": 48653, "name_en": "Racing into the Night", "name_jp": "夜に駆ける", "image_url": "https://cdn.myanimelist.net/images/anime/1548/124083.jpg", "genres": [], "themes": ["Music"], "demographics": [], "studios": [], "year": 2019, "episodes": 1, "source": "Other", "score": 8.31, "synopsis": "Music video of the song Yoru ni Kakeru by Yoasobi."}, {"id": 56752, "name_en": "Link Click: Bridon Arc", "name_jp": "時光代理人 英都篇", "image_url": "https://cdn.myanimelist.net/images/anime/1496/147189.jpg", "genres": ["Drama"], "themes": ["Adult Cast", "Super Power", "Time Travel"], "demographics": [], "studios": ["CMC Media"], "year": 2024, "episodes": 6, "source": "Original", "score": 8.3, "synopsis": "Lu Guang loses everything when a mysterious man bursts into the photo studio that he is renting with his close friend Cheng Xiaoshi. The man, who comes to punish the members at the studio for playing with time, murders both Qiao Ling and Cheng Xiaoshi in cold blood. However, Lu Guang inherits his friend's power to go back in time and decides to save everyone from their grim fate.\n\nNow in the past, Lu Guang travels to Bridon to help Cheng Xiaoshi investigate his father's whereabouts; however, he discovers an uncanny series of small disparities that cause the situation to spiral into complete uncertainty. Suddenly unable to rely on his ability to predict the future, Lu Guang must invent a new strategy to foil the plans of the man who took everything from him.\n\n[Written by MAL Rewrite]"}, {"id": 5341, "name_en": "Spice and Wolf II", "name_jp": "狼と香辛料II", "image_url": "https://cdn.myanimelist.net/images/anime/6/59399.jpg", "genres": ["Adventure", "Drama", "Romance", "Supernatural"], "themes": ["Adult Cast"], "demographics": [], "studios": ["Brain's Base", "Marvy Jack"], "year": 2009, "episodes": 12, "source": "Light novel", "score": 8.3, "synopsis": "Traveling merchant Kraft Lawrence continues his northward journey with wolf goddess Holo, in search of her lost home of Yoitsu. Lawrence and his sharp-witted partner continue to make some small profits along the way, while slowly uncovering more information about Holo's hometown. However, the road to Yoitsu is a bumpy one filled with many troubles—Lawrence runs into a charming young fellow merchant who has his eyes set on the female wolf companion, and he begins to doubt if Holo will remain by his side; he and the goddess will also have to consider precarious and risky business deals as Lawrence strives to achieve his dream of becoming a shopowner. All the while, with his determination tested at every turn during his journey, Lawrence must question his relationship with Holo, take on business ventures, and ask himself whether it is time for him and Holo to go their separate ways.\n\n[Written by MAL Rewrite]"}, {"id": 4081, "name_en": "Natsume's Book of Friends", "name_jp": "夏目友人帳", "image_url": "https://cdn.myanimelist.net/images/anime/1681/108439.jpg", "genres": ["Slice of Life", "Supernatural"], "themes": ["Iyashikei", "Mythology"], "demographics": ["Shoujo"], "studios": ["Brain's Base"], "year": 2008, "episodes": 13, "source": "Manga", "score": 8.3, "synopsis": "Due to an unusual ability to see strange creatures called youkai, Takashi Natsume has never fit in. Passed around from one foster home to another, he was left isolated and lonely. Over time, he has accepted that no one would ever believe him and has closed himself off to his current caretakers and classmates.\n\nWhen Natsume accidentally breaks an intangible barrier, he frees Madara—a mighty spirit in the form of a lucky cat. Madara notices that Natsume bears a remarkable resemblance to his late grandmother Reiko Natsume, an outcast girl who became known across the youkai world for creating the Book of Friends. It is now in Natsume's possession, along with its power to call upon the written names of the youkai Reiko had defeated. \n\nWith no interest in its powers, Natsume decides to keep the book for the sake of his grandmother's memories and to protect it from scheming youkai. Therefore, he makes a deal with Madara: he will hand him the book once his time is up, and in turn, Madara will act as Natsume's unofficial bodyguard, nicknamed Nyanko-sensei. With his newfound goal of freeing those Reiko had sealed, Natsume's relationship with both youkai and humans slowly begins to improve.\n\n[Written by MAL Rewrite]"}, {"id": 30230, "name_en": "Ace of Diamond: Second Season", "name_jp": "ダイヤのA[エース]～Second Season～", "image_url": "https://cdn.myanimelist.net/images/anime/9/74398.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G", "Madhouse"], "year": 2015, "episodes": 51, "source": "Manga", "score": 8.3, "synopsis": "After the National Tournament, the Seidou High baseball team moves forward with uncertainty as the Fall season quickly approaches. In an attempt to build a stronger team centered around their new captain, fresh faces join the starting roster for the very first time. Previous losses weigh heavily on the minds of the veteran players as they continue their rigorous training, preparing for what will inevitably be their toughest season yet.\n \nRivals both new and old stand in their path as Seidou once again climbs their way toward the top, one game at a time. Needed now more than ever before, Furuya and Eijun must be determined to pitch with all their skill and strength in order to lead their team to victory. And this time, one of these young pitchers may finally claim that coveted title: \"The Ace of Seidou.\"\n\n[Written by MAL Rewrite]"}, {"id": 42941, "name_en": "Umamusume: Pretty Derby Season 2", "name_jp": "ウマ娘 プリティーダービー Season 2", "image_url": "https://cdn.myanimelist.net/images/anime/1447/114282.jpg", "genres": ["Sports"], "themes": ["Anthropomorphic", "Racing"], "demographics": [], "studios": ["Studio KAI"], "year": 2021, "episodes": 13, "source": "Game", "score": 8.3, "synopsis": "Within the horse-racing world, the horse girls of Team Spica have been gaining popularity as they continue to rack up victories. The one receiving the most attention in the group is Toukai Teiou, known for her cheerful personality and exceptional running skills. Teiou aims to become an undefeated Triple Crown horse girl by winning all three G1 races consecutively, a feat that has not been accomplished since the legendary Symboli Rudolf managed to do so many years ago.\n\nOn her path to obtaining the Triple Crown, Teiou is met with many challenges, encountering both old and new rivals. Amidst her fierce rivalries, however, Teiou is soon confronted with decisions that will leave a huge impact on her very own racing career.\n\n[Written by MAL Rewrite]"}, {"id": 3784, "name_en": "Evangelion: 2.0 You Can (Not) Advance", "name_jp": "ヱヴァンゲリヲン新劇場版:破", "image_url": "https://cdn.myanimelist.net/images/anime/5/74983.jpg", "genres": ["Drama", "Sci-Fi", "Suspense"], "themes": ["Mecha", "Psychological"], "demographics": [], "studios": ["Khara"], "year": 2009, "episodes": 1, "source": "Original", "score": 8.3, "synopsis": "When the threat of the Angel menace escalates, mankind's defense force is pushed to its limits, with NERV at the forefront of the struggle. Shinji Ikari and his partner Rei Ayanami are assisted by two new pilots: the fiery Asuka Langley Shikinami and the mysterious Mari Illustrious Makinami.\n\nWith the aid of their mechanized Evangelion units, equipped with weapons perfect for engaging their monstrous opponents, the four young souls fight desperately to protect their loved ones and prevent an impending apocalypse. But when startling secrets are brought to light, will the heroes' greatest challenge prove to be the monsters...or humanity itself?\n\n[Written by MAL Rewrite]"}, {"id": 22507, "name_en": "Initial D Final Stage", "name_jp": "頭文字 D Final Stage", "image_url": "https://cdn.myanimelist.net/images/anime/1404/101364.jpg", "genres": ["Action", "Drama"], "themes": ["Racing"], "demographics": ["Seinen"], "studios": ["SynergySP"], "year": 2014, "episodes": 4, "source": "Manga", "score": 8.3, "synopsis": "In the final match against the Kanagawa street racing team Sidewinder, Project D street racer Takumi Fujiwara is faced with a surprising challenger: Shinji Inui, a 17-year-old high school student. Beginning to drive at a very young age, Shinji is not only familiar with the mountain pass where the race is taking place, but he also drives a Toyota AE86—the same car model Takumi uses.\n\nInitially reluctant to participate due to a lack of enthusiasm, Shinji becomes motivated after witnessing the fiery battle between Keisuke Takahashi and Gou Houjou. Undefeated in his home course known as the Tsubaki Line, Shinji might prove to be Takumi's most formidable opponent yet.\n\n[Written by MAL Rewrite]"}, {"id": 31043, "name_en": "Erased", "name_jp": "僕だけがいない街", "image_url": "https://cdn.myanimelist.net/images/anime/10/77957.jpg", "genres": ["Mystery", "Suspense"], "themes": ["Psychological", "Time Travel"], "demographics": ["Seinen"], "studios": ["A-1 Pictures"], "year": 2016, "episodes": 12, "source": "Manga", "score": 8.3, "synopsis": "When tragedy is about to strike, Satoru Fujinuma finds himself sent back several minutes before the accident occurs. The detached, 29-year-old manga artist has taken advantage of this powerful yet mysterious phenomenon, which he calls \"Revival,\" to save many lives.\n \nHowever, when he is wrongfully accused of murdering someone close to him, Satoru is sent back to the past once again, but this time to 1988, 18 years in the past. Soon, he realizes that the murder may be connected to the abduction and killing of one of his classmates, the solitary and mysterious Kayo Hinazuki, that took place when he was a child. This is his chance to make things right.\n \nBoku dake ga Inai Machi follows Satoru in his mission to uncover what truly transpired 18 years ago and prevent the death of his classmate while protecting those he cares about in the present.\n\n[Written by MAL Rewrite]"}, {"id": 10863, "name_en": "Steins;Gate: Egoistic Poriomania", "name_jp": "シュタインズ ゲート 横行跋扈のポリオマニア", "image_url": "https://cdn.myanimelist.net/images/anime/1805/123188.jpg", "genres": ["Comedy", "Sci-Fi"], "themes": ["Time Travel"], "demographics": [], "studios": ["White Fox"], "year": 2012, "episodes": 1, "source": "Visual novel", "score": 8.29, "synopsis": "A few months after the events of Steins;Gate, Rintarou Okabe and his group of friends are invited to tag along with their acquaintance Faris NyanNyan, who is participating in a Rai-Net battle event in the United States. There, they meet up with an old colleague: Kurisu Makise, who has been recalling fragmented memories of events that happened in the other world lines in the form of dreams. She confronts Okabe, questioning him as to whether these events—particularly the incidents between the two of them—did indeed happen.\n\n[Written by MAL Rewrite]"}, {"id": 17389, "name_en": "Kingdom Season 2", "name_jp": "キングダム 第2シリーズ", "image_url": "https://cdn.myanimelist.net/images/anime/13/53589.jpg", "genres": ["Action"], "themes": ["Historical", "Military"], "demographics": ["Seinen"], "studios": ["Studio Pierrot"], "year": 2013, "episodes": 39, "source": "Manga", "score": 8.29, "synopsis": "A year after the devastating battle against the formidable Zhao, the State of Qin has returned its focus to pursuing King Ying Zheng's ambition of conquering the other six states and unifying China. Their next target is Wei, a smaller state which stands as a geographic stepping stone for the sake of conquest.\n\nXin, now a three hundred man commander of the swiftly rising Fei Xin Unit, continues to seek out lofty achievements in order to garner recognition for himself and his soldiers, motivated by those previously lost in battle. In the preliminary battles ahead of Qin's invasion of Wei, Xin finds competition in other young commanders who are of a higher social status than him. Back in Qin, the royal palace faces turmoil as opposing factions begin to make their move against Ying Zheng's regime.\n\nWith their hands full both abroad and at home, Zheng and Xin must lead the way in this era of unending war, resolved to etch their names in history by creating a unified China.\n\n[Written by MAL Rewrite]"}, {"id": 6675, "name_en": "Redline", "name_jp": "REDLINE", "image_url": "https://cdn.myanimelist.net/images/anime/12/28553.jpg", "genres": ["Action", "Sci-Fi"], "themes": ["Adult Cast", "Racing"], "demographics": [], "studios": ["Madhouse"], "year": 2009, "episodes": 1, "source": "Original", "score": 8.29, "synopsis": "Every five years, an exhilarating race called Redline is held, and the universe's most anticipated competition has only one rule: that there are none. Racers are pushed to their absolute limit—a feeling that daredevil driver JP knows all too well. Having just qualified to participate in Redline, he is eager to battle against the other highly skilled drivers, particularly the beautiful rising star and the only other human that qualified, Sonoshee McLaren.\n\nBut this year's Redline may be far more dangerous than usual—it has been announced to take place on the planet Roboworld with its trigger-happy military and criminals who look to turn the race to their own advantage. However, the potential danger doesn't stop the racers; in fact, it only adds to the thrill. Relying solely on his vehicle's speed, JP prepares for the event to come, aiming to take first place in the biggest race of his life.\n\n[Written by MAL Rewrite]"}, {"id": 5420, "name_en": "The Beast Player Erin", "name_jp": "獣の奏者エリン", "image_url": "https://cdn.myanimelist.net/images/anime/1929/121700.jpg", "genres": ["Drama", "Fantasy"], "themes": [], "demographics": [], "studios": ["Production I.G", "Trans Arts"], "year": 2009, "episodes": 50, "source": "Novel", "score": 8.29, "synopsis": "In the land of Ryoza, the neighboring provinces of Shin-Ou and Tai-Kou have been at peace. Queen Shinou is the ruler of Ryoza and her greatest general, Grand Duke Taikou, defends the kingdom with his army of powerful war-lizards known as the \"Touda.\" Although the two regions have enjoyed a long-standing alliance, mounting tensions threaten to spark a fierce civil war.\n\nWithin Ake, a village in Tai-Kou tasked with raising the Grand Duke's army, lives Erin, a bright girl who spends her days watching the work of her mother Soyon, the village's head Touda doctor. But while under Soyon's care, a disastrous incident befalls the Grand Duke's strongest Touda, and the peace that Erin and her mother had been enjoying vanishes as Soyon is punished severely. In a desperate attempt to save her mother, Erin ends up falling in a river and is swept towards Shin-Ou.\n\nUnable to return home, Erin must learn to lead a new life with completely different people, all while hunting for the truth of both beasts and humanity itself, with tensions between the two regions constantly escalating.\n\n[Written by MAL Rewrite]"}, {"id": 24415, "name_en": "Kuroko's Basketball 3", "name_jp": "黒子のバスケ", "image_url": "https://cdn.myanimelist.net/images/anime/4/68299.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2015, "episodes": 25, "source": "Manga", "score": 8.29, "synopsis": "Seirin prepares to face major obstacles on their path to winning the Winter Cup, including the teams each possessing a member of the Generation of Miracles. Kuroko goes head-to-head with his old teammates once more as he attempts to show them that individual skill is not the only way to play basketball. His firm belief that his form of basketball, team play, is the right way to play the sport will clash with the talents of a perfect copy and an absolute authority.\n\nWhile Kuroko tries to prove that his basketball is \"right,\" he and the rest of Seirin High ultimately have one goal: to win the Winter Cup and overcome the strength of the Generation of Miracles, who have long dominated the scene of middle and high school basketball.\n\n[Written by MAL Rewrite]"}, {"id": 6594, "name_en": "Katanagatari", "name_jp": "刀語", "image_url": "https://cdn.myanimelist.net/images/anime/1112/119225.jpg", "genres": ["Action", "Adventure", "Romance"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["White Fox"], "year": 2010, "episodes": 12, "source": "Light novel", "score": 8.29, "synopsis": "In an Edo-era Japan lush with a variety of sword-fighting styles, Shichika Yasuri practices the most unique one: Kyotouryuu, a technique in which the user's own body is wielded as a blade. The enigmatic seventh head of the Kyotouryuu school, Shichika lives quietly in exile with his sister Nanami until one day—the wildly ambitious strategist Togame barges into their lives.\n\nTogame brazenly requests that Shichika help in her mission to collect twelve unique swords, known as the \"Deviant Blades,\" for the shogunate. Shichika accepts, interested in the girl herself rather than petty politics, and thus sets out on a journey. Standing in their way are the fierce wielders of these legendary weapons as well as other power-hungry entities who seek to thwart Togame's objective. In order to prevail against their enemies, the duo must become an unbreakable team as they forge ahead on a path of uncertainty and peril.\n\n[Written by MAL Rewrite]"}, {"id": 49571, "name_en": "Swallowed Star 2nd Season", "name_jp": "吞噬星空 第二季", "image_url": "https://cdn.myanimelist.net/images/anime/1558/117008.jpg", "genres": ["Action", "Adventure", "Fantasy", "Sci-Fi"], "themes": ["Martial Arts"], "demographics": [], "studios": ["Sparkly Key Animation Studio"], "year": 2021, "episodes": 26, "source": "Web novel", "score": 8.29, "synopsis": "Second season of Tunshi Xingkong."}, {"id": 12531, "name_en": "Kids on the Slope", "name_jp": "坂道のアポロン", "image_url": "https://cdn.myanimelist.net/images/anime/1604/98654.jpg", "genres": ["Drama", "Romance"], "themes": ["Historical", "Love Polygon", "Music", "School"], "demographics": ["Josei"], "studios": ["Tezuka Productions", "MAPPA"], "year": 2012, "episodes": 12, "source": "Manga", "score": 8.29, "synopsis": "Introverted classical pianist and top student Kaoru Nishimi has just arrived in Kyushu for his first year of high school. Having constantly moved from place to place since his childhood, he abandons all hope of fitting in, preparing himself for another lonely, meaningless year. That is, until he encounters the notorious delinquent Sentarou Kawabuchi.\n\nSentarou's immeasurable love for jazz music inspires Kaoru to learn more about the genre, and as a result, he slowly starts to break out of his shell, making his very first friend. Kaoru begins playing the piano at after-school jazz sessions, located in the basement of fellow student Ritsuko Mukae's family-owned record shop. As he discovers the immense joy of using his musical talents to bring enjoyment to himself and others, Kaoru's summer might just crescendo into one that he will remember forever.\n\nSakamichi no Apollon is a heartwarming story of friendship, music, and love that follows three unique individuals brought together by their mutual appreciation for jazz.\n\n[Written by MAL Rewrite]"}, {"id": 39533, "name_en": "given", "name_jp": "ギヴン", "image_url": "https://cdn.myanimelist.net/images/anime/1666/102238.jpg", "genres": ["Boys Love", "Drama"], "themes": ["Music", "School"], "demographics": [], "studios": ["Lerche"], "year": 2019, "episodes": 11, "source": "Manga", "score": 8.29, "synopsis": "Tightly clutching his Gibson guitar, Mafuyu Satou steps out of his dark apartment to begin another day of his high school life. While taking a nap in a quiet spot on the gymnasium staircase, he has a chance encounter with fellow student Ritsuka Uenoyama, who berates him for letting his guitar's strings rust and break. Noticing Uenoyama's knowledge of the instrument, Satou pleads for him to fix it and to teach him how to play. Uenoyama eventually agrees and invites him to sit in on a jam session with his two band mates: bassist Haruki Nakayama and drummer Akihiko Kaji.\n    \nSatou's voice is strikingly beautiful, filling Uenoyama with the determination to make Satou the lead singer of the band. Though reticent at first, Satou takes the offer after an emotional meeting with an old friend. With the support of his new friends, Satou must not only learn how to play guitar, but also come to terms with the mysterious circumstances that led him to be its owner.\n\n[Written by MAL Rewrite]"}, {"id": 1365, "name_en": "Case Closed Movie 06: The Phantom of Baker Street", "name_jp": "名探偵コナン ベイカー街の亡霊", "image_url": "https://cdn.myanimelist.net/images/anime/1400/94320.jpg", "genres": ["Adventure", "Mystery"], "themes": ["Detective"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2002, "episodes": 1, "source": "Manga", "score": 8.29, "synopsis": "The latest innovation in virtual reality gaming is set for a private showcase, with the children of Japan's elite given the opportunity to playtest it. Unfortunately, their carefree fun is cut short when a company employee is found murdered, his dying message pointing to a clue hidden within the game's historical setting.\n\nAlong with the Detective Boys and Ran Mouri, Conan Edogawa enters the game to solve this mystery and ensure the perpetrator is caught. But once they load in, an artificial intelligence named Noah's Ark imprisons them and the other children within. To escape and bring the murderer to justice, Conan and company must navigate a simulated 19th-century London and track down the infamous Jack the Ripper—with the lives of 50 innocent children depending on them.\n\n[Written by MAL Rewrite]"}, {"id": 41487, "name_en": "That Time I Got Reincarnated as a Slime Season 2 Part 2", "name_jp": "転生したらスライムだった件", "image_url": "https://cdn.myanimelist.net/images/anime/1033/118296.jpg", "genres": ["Action", "Comedy", "Fantasy"], "themes": ["Isekai", "Reincarnation"], "demographics": ["Shounen"], "studios": ["8bit"], "year": 2021, "episodes": 12, "source": "Manga", "score": 8.29, "synopsis": "The nation of Tempest is in a festive mood after successfully overcoming the surprise attack from the Falmuth Army and the Western Holy Church. Beyond the festivities lies a meeting between Tempest and its allies to decide the future of the Nation of Monsters. The aftermath of the Falmuth invasion, Milim Nava's suspicious behavior, and the disappearance of Demon Lord Carrion—the problems seem to keep on piling up.\n\nRimuru Tempest, now awakened as a True Demon Lord, decides to go on the offensive against Clayman. With the fully revived Storm Dragon Veldora, Ultimate Skill Raphael, and other powerful comrades, the ruler of the Tempest is confident in taking down his enemies one by one until he can face the man pulling the strings.\n\n[Written by MAL Rewrite]"}, {"id": 57, "name_en": "Beck: Mongolian Chop Squad", "name_jp": "ベック", "image_url": "https://cdn.myanimelist.net/images/anime/11/11636.jpg", "genres": ["Drama"], "themes": ["Music"], "demographics": ["Shounen"], "studios": ["Madhouse"], "year": 2004, "episodes": 26, "source": "Manga", "score": 8.29, "synopsis": "Fourteen-year-old Yukio \"Koyuki\" Tanaka is a dispirited young boy with no goals in life. However, this all changes when Koyuki saves a strange-looking dog named Beck from being harassed by a group of local kids. The dog's owner, 16-year-old Ryuusuke \"Ray\" Minami, is an emerging guitarist and the former member of a popular rock band. \n\nAfter Koyuki meets Ray again in a diner, the older boy leads him to his former band's meeting place and dazzles Koyuki with his amazing guitar skills. Slowly becoming interested in the glamour of western rock culture, Koyuki decides to start playing the guitar while helping Ray achieve his dream of leading the ultimate rock band. Together with Ray's younger sister Maho and a few other members, the two boys launch their career into the world of rock by forming a band called BECK. Beck follows the group's struggles and successes as they spread their fame across Japan.\n\n[Written by MAL Rewrite]"}, {"id": 9989, "name_en": "Anohana: The Flower We Saw That Day", "name_jp": "あの日見た花の名前を僕達はまだ知らない。", "image_url": "https://cdn.myanimelist.net/images/anime/5/79697.jpg", "genres": ["Drama", "Supernatural"], "themes": [], "demographics": [], "studios": ["A-1 Pictures"], "year": 2011, "episodes": 11, "source": "Original", "score": 8.29, "synopsis": "Jinta Yadomi is peacefully living as a recluse, spending his days away from school and playing video games at home instead. One hot summer day, his childhood friend, Meiko \"Menma\" Honma, appears and pesters him to grant a forgotten wish. He pays her no mind, which annoys her, but he doesn't really care. After all, Menma already died years ago.\n\nAt first, Jinta thinks that he is merely hallucinating due to the summer heat, but he is later on convinced that what he sees truly is the ghost of Menma. Jinta and his group of childhood friends grew apart after her untimely death, but they are drawn together once more as they try to lay Menma's spirit to rest. Re-living their pain and guilt, will they be able to find the strength to help not only Menma move on—but themselves as well?\n\n[Written by MAL Rewrite]"}, {"id": 1735, "name_en": "Naruto Shippuden", "name_jp": "-ナルト- 疾風伝", "image_url": "https://cdn.myanimelist.net/images/anime/1565/111305.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Martial Arts"], "demographics": ["Shounen"], "studios": ["Studio Pierrot"], "year": 2007, "episodes": 500, "source": "Manga", "score": 8.29, "synopsis": "It has been two and a half years since Naruto Uzumaki left Konohagakure, the Hidden Leaf Village, for intense training following events which fueled his desire to be stronger. Now Akatsuki, the mysterious organization of elite rogue ninja, is closing in on their grand plan which may threaten the safety of the entire shinobi world.\n \nAlthough Naruto is older and sinister events loom on the horizon, he has changed little in personality—still rambunctious and childish—though he is now far more confident and possesses an even greater determination to protect his friends and home. Come whatever may, Naruto will carry on with the fight for what is important to him, even at the expense of his own body, in the continuation of the saga about the boy who wishes to become Hokage.\n\n[Written by MAL Rewrite]"}, {"id": 43325, "name_en": "Moriarty the Patriot Part 2", "name_jp": "憂国のモリアーティ", "image_url": "https://cdn.myanimelist.net/images/anime/1200/111522.jpg", "genres": ["Mystery", "Suspense"], "themes": ["Adult Cast", "Historical", "Organized Crime", "Psychological"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2021, "episodes": 13, "source": "Manga", "score": 8.29, "synopsis": "Great Britain is ablaze with news of a so-called \"Lord of Crime,\" a criminal mastermind responsible for the downfall of several unruly nobles. In truth, the Lord of Crime is not an individual, but rather a group consisting of William James Moriarty and his two brothers, Louis and Albert. Together, they wish to destroy everything rotten about their current world and create a new, fair society for all. To accomplish their goal, they must commit criminal acts, which the great detective Sherlock Holmes and his partner, John H. Watson, cannot abide by. A dangerous cat and mouse game begins between the Lord of Crime and Sherlock, with each trying to outwit the other. Yet Sherlock, despite his skills, has no idea that his foe is right under his nose.\n\nInvolved with both parties is a woman named Irene Adler, who is as beautiful as she is cunning. No stranger to scandal, Irene has embroiled herself in one that may be too big even for her, as the classified documents she stole could shake the very foundations of the British Empire. Can Irene be saved, or does a deadly future await her?\n\n[Written by MAL Rewrite]"}, {"id": 54870, "name_en": "Rascal Does Not Dream of a Knapsack Kid", "name_jp": "青春ブタ野郎はランドセルガールの夢を見ない", "image_url": "https://cdn.myanimelist.net/images/anime/1937/138379.jpg", "genres": ["Drama", "Romance", "Supernatural"], "themes": ["School"], "demographics": [], "studios": ["CloverWorks"], "year": 2023, "episodes": 1, "source": "Light novel", "score": 8.28, "synopsis": "As Sakuta Azusagawa waits for Mai Sakurajima at the beach on her high school graduation day, he encounters a version of Mai from her child acting days. However, when Mai finally arrives, her younger self disappears, leaving him confused. Sakuta spots an unfamiliar scar on his body when he returns home, which confirms his suspicion—he is involved in another case of the inexplicable Puberty Syndrome.\n\nNot long after, Sakuta and his sister, Kaede, receive a call from their father. He tells them that their mother, who had been recently discharged from the hospital, wants to see her daughter once more. Experiencing strange occurrences while visiting his mother with Kaede, Sakuta must find a way to solve the Puberty Syndrome abnormalities.\n\n[Written by MAL Rewrite]"}, {"id": 11917, "name_en": "Major: World Series", "name_jp": "メジャー ワールドシリーズ編", "image_url": "https://cdn.myanimelist.net/images/anime/5/75220.jpg", "genres": ["Sports"], "themes": ["Team Sports"], "demographics": ["Shounen"], "studios": ["SynergySP"], "year": 2011, "episodes": 2, "source": "Manga", "score": 8.28, "synopsis": "Gorou Shigeno's journey with the Indiana Hornets continues as they seek to win the prestigious World Series. However, after an unexpected accident during the match with his fated rival, his quest in becoming the world's best becomes more challenging. Undaunted, Gorou continues to put the weight of his dreams into every pitch he makes, and forges a path towards his desired future through high-speed pitching.\n\n[Written by MAL Rewrite]"}, {"id": 36371, "name_en": "Legend of the Galactic Heroes: Die Neue These - Stellar War Part 3", "name_jp": "銀河英雄伝説 Die Neue These 星乱 第3章", "image_url": "https://cdn.myanimelist.net/images/anime/1782/103917.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["Adult Cast", "Military", "Space"], "demographics": [], "studios": ["Production I.G"], "year": 2019, "episodes": 4, "source": "Novel", "score": 8.28, "synopsis": "At the behest of Admiral Yang Wen-li, defected intelligence officer Commander Baghdash makes an emergency broadcast announcing that the National Salvation Military Council staged a coup under the direction of the Galactic Empire. Despite the lack of physical evidence, this debilitating declaration inspires former Rear Admiral Andrew Lynch to reveal his own role in sowing discord within the Free Planets Alliance. A fatal shootout between Lynch and Admiral Dwight Greenhill acts as the final death knell to the short-lived period of martial rule.\n\nWithin the Galactic Empire, footage of Duke Otto von Braunschweig's nuclear bombing of Westerland results in the dissolution of the Lippstadt League. Marquis Reinhard von Lohengramm's decision to allow the massacre for personal gain creates a rift between him and High Admiral Siegfried Kircheis, souring the taste of their inevitable victory. Now on the cusp of achieving absolute power, Reinhard is embattled by his apparent personal failings and the heavy responsibilities of leadership.\n\nThough the civil wars in both the Alliance and the Empire are coming to a close, neither side can ever regain what is lost. Yang Wen-li and Reinhard von Lohengramm each take bitter solace in the knowledge that just on the other side of the galaxy is a worthy opponent—and a true equal.\n\n[Written by MAL Rewrite]"}, {"id": 38731, "name_en": "Ace of Diamond Act II", "name_jp": "ダイヤのA[エース] actII", "image_url": "https://cdn.myanimelist.net/images/anime/1153/100511.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Madhouse"], "year": 2019, "episodes": 52, "source": "Manga", "score": 8.28, "synopsis": "The hallowed ground of Koshien Stadium is the \"field of dreams,\" where the ambitions of high school baseball players come true. After emerging victorious in the autumn tournament last year, Seidou High School baseball's team  has finally earned the right to compete there for the first time in seven years.  Beyond the spring tournament looms the battle to decide who is the best team in the nation  — the Summer Koshien.\n\n With the third-year players due to retire after the summer tournament, the team has to integrate the experience of the seniors and the potential of the newcomers to overcome familiar and new opponents alike and win the coveted national title. \n\nMeanwhile, pitcher Eijun Sawamura is as determined as ever to earn jersey No. 1  and seize the position of \"ace\" from his persistent rival, Satoru Furuya.  As the team prepares for their greatest challenge yet, Sawamura and Furuya carry on their struggle to lead their team to glory and become the star of the game: the true \"Ace of the Diamond.\" \n\n[Written by MAL Rewrite]"}, {"id": 759, "name_en": "Tokyo Godfathers", "name_jp": "東京ゴッドファーザーズ", "image_url": "https://cdn.myanimelist.net/images/anime/1480/132791.jpg", "genres": ["Award Winning", "Comedy", "Drama"], "themes": ["Adult Cast", "Childcare"], "demographics": [], "studios": ["Madhouse"], "year": 2003, "episodes": 1, "source": "Original", "score": 8.28, "synopsis": "One Christmas Eve, Hana, Gin, and Miyuki are rummaging for presents through heaps of garbage when they chance upon an abandoned baby in the cold winter night. Appalled at the pitiful sight, Hana's maternal instincts kick in and she insists on finding the baby's biological mother to demand an explanation. Naming the baby Kiyoko—meaning pure child—they begin their search using the possible clues left alongside her: a mysterious key and a single note. However, their plans are soon thrown into disarray as they get caught up in a series of unprecedented events.\n\nTokyo Godfathers follows the journey of the trio as they stick together through thick and thin, hoping to deliver Kiyoko to her true home, and find their very own Christmas miracle.\n\n[Written by MAL Rewrite]"}, {"id": 486, "name_en": "Kino's Journey", "name_jp": "キノの旅 -the Beautiful World-", "image_url": "https://cdn.myanimelist.net/images/anime/1763/95397.jpg", "genres": ["Adventure", "Drama", "Fantasy"], "themes": [], "demographics": [], "studios": ["A.C.G.T."], "year": 2003, "episodes": 13, "source": "Light novel", "score": 8.28, "synopsis": "Kino, a 15-year-old traveler, forms a bond with Hermes, a talking motorcycle. Together, they wander the lands and venture through various countries and places, despite having no clear idea of what to expect. After all, life is a journey filled with the unknown. \n\nThroughout their journeys, they encounter different kinds of customs, from the morally gray to tragic and fascinating. They also meet many people: some who live to work, some who live to make others happy, and some who live to chase their dreams. Thus, in every country they visit, there is always something to learn from the way people carry out their lives. \n\nIt is not up to Kino or Hermes to decide whether these asserted values are wrong or right, as they merely assume the roles of observers within this small world. They do not attempt to change or influence the places they visit, despite how absurd these values would appear. That's because in one way or another, they believe things are fine as they are, and that \"the world is not beautiful; therefore, it is.\"\n\n[Written by MAL Rewrite]"}, {"id": 19363, "name_en": "Silver Spoon 2nd Season", "name_jp": "銀の匙", "image_url": "https://cdn.myanimelist.net/images/anime/8/57995.jpg", "genres": ["Comedy"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["A-1 Pictures"], "year": 2014, "episodes": 11, "source": "Manga", "score": 8.27, "synopsis": "Now accustomed to his life at Ooezo Agricultural High School, Yuugo Hachiken explores the deeper aspects of what school life really means. As Hachiken learns more about himself and earns the skills necessary for his everyday tasks, he is even offered the position of the Equestrian Club's vice-president. The new semester brings a variety of agricultural dilemmas and personal conflicts, but, nevertheless, Hachiken perseveres in order to discover his dream and continue working together with his friends.\n\n[Written by MAL Rewrite]"}, {"id": 30346, "name_en": "Doukyusei: Classmates", "name_jp": "同級生", "image_url": "https://cdn.myanimelist.net/images/anime/3/78606.jpg", "genres": ["Boys Love"], "themes": ["School"], "demographics": [], "studios": ["A-1 Pictures"], "year": 2016, "episodes": 1, "source": "Manga", "score": 8.27, "synopsis": "Hikaru Kusakabe is a normal, carefree boy in a rock band who is always focused on the present. During the summer, his entire class is forced to participate in an upcoming chorus festival. By coincidence, he discovers his classmate Rihito Sajou—known for being an honor student with excellent grades—practicing his singing alone. Sajou just cannot seem to get their class' song right, and Kusakabe, delighted at seeing a new side of his straight-laced classmate, offers to help him prepare for the event.\n\nAlthough their lives and personalities are total opposites, they begin to grow closer as time progresses. But with the pressure of an unknown future, what will become of them and their growing relationship?\n\n[Written by MAL Rewrite]"}, {"id": 9735, "name_en": "Gintama: Shinyaku Benizakura-hen", "name_jp": "銀魂 新訳紅桜篇", "image_url": "https://cdn.myanimelist.net/images/anime/12/26966.jpg", "genres": ["Action", "Comedy", "Sci-Fi"], "themes": ["Historical", "Parody", "Samurai"], "demographics": ["Shounen"], "studios": ["Sunrise"], "year": 2010, "episodes": 1, "source": "Manga", "score": 8.27, "synopsis": "It's the running gag of Ben Johnson episode where animation staff reuse the New Years footage; Yorozuya would sit around the kotatsu eating a bowl of tangerines. They discuss elaborate subjects, Shinpachi complains about reusing animation footage, and then Gin-chan always ending the gag with a pointless announcement."}, {"id": 38826, "name_en": "Weathering with You", "name_jp": "天気の子", "image_url": "https://cdn.myanimelist.net/images/anime/1880/101146.jpg", "genres": ["Award Winning", "Drama", "Romance", "Supernatural"], "themes": [], "demographics": [], "studios": ["CoMix Wave Films"], "year": 2019, "episodes": 1, "source": "Original", "score": 8.27, "synopsis": "Tokyo is currently experiencing rain showers that seem to disrupt the usual pace of everyone living there to no end. Amidst this seemingly eternal downpour arrives the runaway high school student Hodaka Morishima, who struggles to financially support himself—ending up with a job at a small-time publisher. At the same time, the orphaned Hina Amano also strives to find work to sustain herself and her younger brother.\n\nBoth fates intertwine when Hodaka attempts to rescue Hina from shady men, deciding to run away together. Subsequently, Hodaka discovers that Hina has a strange yet astounding power: the ability to call out the sun whenever she prays for it. With Tokyo's unusual weather in mind, Hodaka sees the potential of this ability. He suggests that Hina should become a \"sunshine girl\"—someone who will clear the sky for people when they need it the most.\n\nThings begin looking up for them at first. However, it is common knowledge that power always comes with a hefty price...\n\n[Written by MAL Rewrite]"}, {"id": 59150, "name_en": "Scissor Seven: Fragments of Memory", "name_jp": "伍六七之记忆碎片", "image_url": "https://cdn.myanimelist.net/images/anime/1787/151956.jpg", "genres": ["Action", "Adventure", "Comedy", "Drama", "Mystery", "Sci-Fi", "Supernatural"], "themes": [], "demographics": [], "studios": ["Sharefun Studio"], "year": 2024, "episodes": 10, "source": "Original", "score": 8.26, "synopsis": "In order to find his cherished partner, Wu Liuqi returns to Xuanwu Country and faces those forgotten fragments of memory. The dusty truth brings pain and anger, but now he is no longer alone. What is the purpose of having power? This time, Wu Liuqi has to make a new choice for his life...\n\n(Source: Official Site)"}, {"id": 58572, "name_en": "Shangri-La Frontier Season 2", "name_jp": "シャングリラ・フロンティア～クソゲーハンター、神ゲーに挑まんとす～ 2nd season", "image_url": "https://cdn.myanimelist.net/images/anime/1996/147601.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Video Game"], "demographics": ["Shounen"], "studios": ["C2C"], "year": 2024, "episodes": 25, "source": "Manga", "score": 8.26, "synopsis": "Ever since Rakurou \"Sunraku\" Hizutome started to play the extremely popular virtual reality game Shangri-La Frontier, he has truly fallen in love with it. Sunraku has quickly made a big name for himself by fighting two of the seven unique, nearly unbeatable monsters, which is unthinkable for most players. To progress the game's story, he sets out on an adventure with his leporine guide, Emul, to acquire a magic operation unit from an ancient workshop.\n\nThough Sunraku breezes through this quest alongside his clanmates Towa \"Arthur Pencilgon\" Amane and Kei \"OiKatzo\" Uomi, there seems to be an ulterior motive as to why the two of them decided to help Sunraku in the first place. Nevertheless, Sunraku gains valuable knowledge and allies, critical for his advancement in the game. By seeking out powerful enemies and unraveling the inner workings of the game's world, Sunraku may just change Shangri-La Frontier forever.\n\n[Written by MAL Rewrite]"}, {"id": 54595, "name_en": "The Eminence in Shadow Season 2", "name_jp": "陰の実力者になりたくて！ 2nd Season", "image_url": "https://cdn.myanimelist.net/images/anime/1938/138295.jpg", "genres": ["Action", "Comedy", "Fantasy"], "themes": ["Isekai", "Reincarnation"], "demographics": [], "studios": ["Nexus"], "year": 2023, "episodes": 12, "source": "Light novel", "score": 8.26, "synopsis": "Enticed by its rumored wealth, Cid Kagenou happily tags along with his sister, Claire, on a trip to the infamous Lawless City. The region is ruled by three monarchs: Yukime, the Spirit Fox; Juggernaut, the Tyrant; and Elisabeth, a dormant progenitor vampire known as the Blood Queen. Hunting Elisabeth down serves as the perfect opportunity for Claire to prove her true mettle. On the other hand, Cid has different objectives—sneaking away from Claire's sight, collecting as much loot as possible, and making Shadow's glorious entrance at the perfect moment.\n\nAs Elisabeth's awakening draws near, her subordinate Crimson enacts a treacherous plot, sending the town into a frenzy. Searching for her now-missing brother, Claire finds an unexpected ally in Mary, a vampire hunter, and the pair hurry to the Crimson Tower—the home of the Blood Queen. Meanwhile, Beta and a small team of Shadow Garden recruits also arrive at the Lawless City. As the key players converge on the Crimson Tower, the stage is set for a grand confrontation under the crimson moon.\n\n[Written by MAL Rewrite]"}, {"id": 35111, "name_en": "Haikyu!! The Movie: Battle of Concepts", "name_jp": "ハイキュー!! コンセプトの戦い", "image_url": "https://cdn.myanimelist.net/images/anime/1854/147254.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2017, "episodes": 1, "source": "Manga", "score": 8.26, "synopsis": "The long-awaited finals of the Spring Tournament have arrived for Karasuno High School's volleyball team. The winner of the match will go on to represent all of Miyagi Prefecture at the National Tournament. However, the last challenge Karasuno must overcome is to win against Shiratorizawa Academy, the school that houses Wakatoshi Ushijima, one of Japan's top three aces. Shiratorizawa is the reigning Miyagi champion for the past three years, and Karasuno seems to have little chance at dethroning them. \n\nWhen the other school's captain Ushijima insults Karasuno to their faces, freshmen duo Shouyou Hinata and Tobio Kageyama swear to defeat them and steal their spot in the nationals. Against a roster full of nationally-ranked players, the former flightless crows will have to use everything in their arsenal if they want to represent Miyagi.\n\n[Written by MAL Rewrite]"}, {"id": 32188, "name_en": "Steins;Gate: Open the Missing Link - Divide By Zero", "name_jp": "シュタインズ・ゲート境界面上のミッシングリンク-Divide By Zero-", "image_url": "https://cdn.myanimelist.net/images/anime/7/77324.jpg", "genres": ["Sci-Fi", "Suspense"], "themes": ["Time Travel"], "demographics": [], "studios": ["White Fox"], "year": 2015, "episodes": 1, "source": "Visual novel", "score": 8.26, "synopsis": "Having reached his emotional breaking point, Rintarou Okabe refuses to continue aiding time traveler Suzuha Amane in her mission to prevent World War III, believing any further efforts to save Makise Kurisu will be in vain. Shortly after, Okabe abandons his mad scientist persona and becomes a seemingly regular university student. \n\nOkabe's close friend Mayuri Shiina perceives him to be recovering from his trauma and is visibly happy. However, something still seems to be bothering Okabe. Steins;Gate: Kyoukaimenjou no Missing Link - Divide By Zero marks the beginning of a critical divergence—a timeline in which the burden of fate escalates beyond one's limits.\n\n[Written by MAL Rewrite]"}, {"id": 23327, "name_en": "Space Dandy 2nd Season", "name_jp": "スペース☆ダンディ 第2シリーズ", "image_url": "https://cdn.myanimelist.net/images/anime/3/64451.jpg", "genres": ["Comedy", "Sci-Fi"], "themes": ["Adult Cast", "Space"], "demographics": [], "studios": ["Bones"], "year": 2014, "episodes": 13, "source": "Original", "score": 8.26, "synopsis": "Second season of Space Dandy.\n\nSpace Dandy is a dandy guy, in space! This dreamy adventurer with a to-die-for pompadour travels across the galaxy in search of aliens no one has ever laid eyes on. Each new species he discovers earns him a hefty reward, but this dandy has to be quick on his feet because it's first come, first served! Accompanied by his sidekicks, a rundown robot named QT and Meow the cat-looking space alien, Dandy bravely explores unknown worlds inhabited by a variety of aliens. Join the best dressed alien hunter in all of space and time as he embarks on an adventure that ends at the edge of the universe!\n\n(Source: Bandai Visual)"}, {"id": 37520, "name_en": "Dororo", "name_jp": "どろろ", "image_url": "https://cdn.myanimelist.net/images/anime/1879/100467.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Mythology", "Samurai"], "demographics": ["Shounen"], "studios": ["Tezuka Productions", "MAPPA"], "year": 2019, "episodes": 24, "source": "Manga", "score": 8.26, "synopsis": "The greedy samurai lord Daigo Kagemitsu's land is dying, and he would do anything for power, even renounce Buddha and make a pact with demons. His prayers are answered by 12 demons who grant him the power he desires by aiding his prefecture's growth, but at a price. When Kagemitsu's first son is born, the boy has no limbs, no nose, no eyes, no ears, nor even skin—yet still, he lives.\n\nThis child is disposed of in a river and forgotten. But as luck would have it, he is saved by a medicine man who provides him with prosthetics and weapons, allowing for him to survive and fend for himself. The boy lives and grows, and although he cannot see, hear, or feel anything, he must defeat the demons that took him as sacrifice. With the death of each one, he regains a part of himself that is rightfully his. For many years he wanders alone, until one day an orphan boy, Dororo, befriends him. The unlikely pair of castaways now fight for their survival and humanity in an unforgiving, demon-infested world.\n\n[Written by MAL Rewrite]"}, {"id": 34798, "name_en": "Laid-Back Camp", "name_jp": "ゆるキャン△", "image_url": "https://cdn.myanimelist.net/images/anime/4/89877.jpg", "genres": ["Slice of Life"], "themes": ["CGDCT", "Iyashikei"], "demographics": [], "studios": ["C-Station"], "year": 2018, "episodes": 12, "source": "Manga", "score": 8.26, "synopsis": "While the perfect getaway for most girls her age might be a fancy vacation with their loved ones, Rin Shima's ideal way of spending her days off is camping alone at the base of Mount Fuji. From pitching her tent to gathering firewood, she has always done everything by herself, and has no plans of leaving her little solitary world.\n\nHowever, what starts off as one of Rin's usual camping sessions somehow ends up as a surprise get-together for two when the lost Nadeshiko Kagamihara is forced to take refuge at her campsite. Originally intending to see the picturesque view of Mount Fuji for herself, Nadeshiko's plans are disrupted when she ends up falling asleep partway to her destination. Alone and with no other choice, she seeks help from the only other person nearby. Despite their hasty introductions, the two girls nevertheless enjoy the chilly night together, eating ramen and conversing while the campfire keeps them warm. And even after Nadeshiko's sister finally picks her up later that night, both girls silently ponder the possibility of another camping trip together.\n\n[Written by MAL Rewrite]"}, {"id": 25835, "name_en": "Shirobako", "name_jp": "SHIROBAKO", "image_url": "https://cdn.myanimelist.net/images/anime/1460/141897.jpg", "genres": ["Award Winning", "Comedy", "Drama"], "themes": ["Adult Cast", "Otaku Culture", "Workplace"], "demographics": [], "studios": ["P.A. Works"], "year": 2014, "episodes": 24, "source": "Original", "score": 8.26, "synopsis": "It all started in Kaminoyama High School, when five best friends—Aoi Miyamori, Ema Yasuhara, Midori Imai, Shizuka Sakaki, and Misa Toudou—discovered their collective love for all things anime and formed the animation club. After making their first amateur anime together and showcasing it at the culture festival, the group vows to pursue careers in the industry, aiming to one day work together and create their own mainstream show.\n\nTwo and a half years later, Aoi and Ema have managed to land jobs at the illustrious Musashino Animation production company. The others, however, are finding it difficult to get their dream jobs. Shizuka is feeling the weight of not being recognized as a capable voice actor, Misa has a secure yet unsatisfying career designing 3D models for a car company, and Midori is a university student intent on pursuing her dream as a story writer. These five girls will learn that the path to success is one with many diversions, but dreams can still be achieved through perseverance and a touch of eccentric creativity.\n\n[Written by MAL Rewrite]"}, {"id": 3002, "name_en": "Kaiji: Ultimate Survivor", "name_jp": "逆境無頼カイジ Ultimate Survivor", "image_url": "https://cdn.myanimelist.net/images/anime/12/80032.jpg", "genres": ["Suspense"], "themes": ["Adult Cast", "High Stakes Game", "Psychological", "Strategy Game"], "demographics": ["Seinen"], "studios": ["Madhouse"], "year": 2007, "episodes": 26, "source": "Manga", "score": 8.26, "synopsis": "After one of his coworkers fails to repay a debt, apathetic bum Kaiji Itou is pressured by loan sharks to settle the large sum of cash. Since Kaiji has little money and no future prospects of his own, he is unable to return what his colleague borrowed. Faced with massive financial burdens, Kaiji is given an offer to board the illustrious Espoir—it is said that \"one night aboard the ship, and all of your debts will be paid.\"\n\nWhat seems like a simple night of gambling quickly turns into a game of strategy and betrayal. With the losers of the tournament being subjected to a backbreaking life of indebted servitude, Kaiji must choose his actions—and allies—carefully. However, this is only the beginning of a series of gambles that will wager Kaiji's life, testing the full extent of his wits and will to survive.\n\n[Written by MAL Rewrite]"}, {"id": 38691, "name_en": "Dr. Stone", "name_jp": "ドクターストーン", "image_url": "https://cdn.myanimelist.net/images/anime/1613/102576.jpg", "genres": ["Adventure", "Comedy"], "themes": [], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2019, "episodes": 24, "source": "Manga", "score": 8.26, "synopsis": "After five years of harboring unspoken feelings, high-schooler Taiju Ooki is finally ready to confess his love to Yuzuriha Ogawa. Just when Taiju begins his confession however, a blinding green light strikes the Earth and petrifies mankind around the world—turning every single human into stone.\n\nSeveral millennia later, Taiju awakens to find the modern world completely nonexistent, as nature has flourished in the years humanity stood still. Among a stone world of statues, Taiju encounters one other living human: his science-loving friend Senkuu, who has been active for a few months. Taiju learns that Senkuu has developed a grand scheme—to launch the complete revival of civilization with science. Taiju's brawn and Senkuu's brains combine to forge a formidable partnership, and they soon uncover a method to revive those petrified.\n\nHowever, Senkuu's master plan is threatened when his ideologies are challenged by those who awaken. All the while, the reason for mankind's petrification remains unknown.\n\n[Written by MAL Rewrite]"}, {"id": 10087, "name_en": "Fate/Zero", "name_jp": "フェイト/ゼロ", "image_url": "https://cdn.myanimelist.net/images/anime/1887/117644.jpg", "genres": ["Action", "Fantasy"], "themes": ["Urban Fantasy"], "demographics": [], "studios": ["ufotable"], "year": 2011, "episodes": 13, "source": "Light novel", "score": 8.26, "synopsis": "With the promise of granting any wish, the omnipotent Holy Grail triggered three wars in the past, each too cruel and fierce to leave a victor. In spite of that, the wealthy Einzbern family is confident that the Fourth Holy Grail War will be different; namely, with a vessel of the Holy Grail now in their grasp. Solely for this reason, the much hated \"Magus Killer\" Kiritsugu Emiya is hired by the Einzberns, with marriage to their only daughter Irisviel as binding contract.\n\nKiritsugu now stands at the center of a cutthroat game of survival, facing off against six other participants, each armed with an ancient familiar, and fueled by unique desires and ideals. Accompanied by his own familiar, Saber, the notorious mercenary soon finds his greatest opponent in Kirei Kotomine, a priest who seeks salvation from the emptiness within himself in pursuit of Kiritsugu.\n\nBased on the light novel written by Gen Urobuchi, Fate/Zero depicts the events of the Fourth Holy Grail War—10 years prior to Fate/stay night. Witness a battle royale in which no one is guaranteed to survive.\n\n[Written by MAL Rewrite]"}, {"id": 10408, "name_en": "Into the Forest of Fireflies' Light", "name_jp": "蛍火の杜へ", "image_url": "https://cdn.myanimelist.net/images/anime/1599/112267.jpg", "genres": ["Award Winning", "Drama", "Romance", "Supernatural"], "themes": [], "demographics": ["Shoujo"], "studios": ["Brain's Base"], "year": 2011, "episodes": 1, "source": "Manga", "score": 8.26, "synopsis": "During a summer vacation at her grandfather’s house, six-year-old Hotaru Takegawa gets lost in a forest rumored to be inhabited by spirits. While crying out in desperation, Hotaru is approached by Gin—a mysterious boy wearing a mask—who offers to help her.\n\nOverjoyed at the sight of another person, Hotaru runs to Gin with open arms only to be rudely fended off. However, she quickly learns the grave reason behind his behavior: a dreadful curse has been cast upon Gin. Should he ever be touched by a human being, he will disappear forever.\n\nThough Gin urges her to never return, Hotaru does the exact opposite, and before too long, the two become close friends despite his delicate situation. Nonetheless, as years pass and their mutual feelings grow stronger, Hotaru and Gin start struggling with the boundaries that destiny has set between them.\n\n[Written by MAL Rewrite]"}, {"id": 57592, "name_en": "Dr. Stone: Science Future", "name_jp": "Dr.STONE SCIENCE FUTURE", "image_url": "https://cdn.myanimelist.net/images/anime/1403/146479.jpg", "genres": ["Adventure", "Comedy"], "themes": [], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2025, "episodes": 12, "source": "Manga", "score": 8.25, "synopsis": "Having resolved the crisis in the Petrification Kingdom, Senkuu and his companions gear up to deal directly with the mastermind behind the petrification phenomenon—who supposedly resides on the moon. The ambitious spaceship project, however, requires immense manpower and critical resources scattered across the world. Thus, the Kingdom of Science exploration team kick-starts its transcontinental journey on the ship Perseus to gather them all.\n\nEquipped with modern technology and bolstered by the new, capable members in their ranks, the exploration team feels confident about setting sail to their first destination: America. They soon realize that the region now hosts adversaries controlling the resources they need, doing so with scientific technology which rivals and even exceeds their own. In a battle of science versus science, Senkuu and his team must outsmart their rivals if they are to have any chance of ensuring the continued survival and rejuvenation of humanity.\n\n[Written by MAL Rewrite]"}, {"id": 54029, "name_en": "Scissor Seven: Shadow Fate", "name_jp": "伍六七之暗影宿命", "image_url": "https://cdn.myanimelist.net/images/anime/1016/153863.jpg", "genres": ["Action", "Adventure", "Comedy", "Drama", "Mystery"], "themes": ["Martial Arts", "Super Power"], "demographics": [], "studios": ["Sharefun Studio"], "year": 2023, "episodes": 10, "source": "Original", "score": 8.25, "synopsis": "The protagonist Wu Liuqi originally lived a carefree life on Xiaoji Island, but his identity became a mystery due to amnesia. As outsiders landed on the island and recognized Wu Liuqi, various conspiracies began to cover Xiaoji Island. So Wu Liuqi decided to return to Xuanwu Kingdom to meet his destiny in order to protect his partners and their homeland. \n\n(Source: Bilibili, translated)"}, {"id": 53111, "name_en": "Is It Wrong to Try to Pick Up Girls in a Dungeon? IV Part 2", "name_jp": "ダンジョンに出会いを求めるのは間違っているだろうかⅣ深章 厄災篇", "image_url": "https://cdn.myanimelist.net/images/anime/1476/128693.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": [], "studios": ["J.C.Staff"], "year": 2023, "episodes": 11, "source": "Light novel", "score": 8.25, "synopsis": "Second part of Dungeon ni Deai wo Motomeru no wa Machigatteiru Darou ka IV."}, {"id": 44412, "name_en": "Fights Break Sphere 4", "name_jp": "斗破苍穹 第4季", "image_url": "https://cdn.myanimelist.net/images/anime/1887/112452.jpg", "genres": ["Action", "Adventure", "Fantasy", "Romance"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Motion Magic"], "year": 2021, "episodes": 24, "source": "Web novel", "score": 8.25, "synopsis": "To awaken his teacher Yao Cheng, Xiao Yan travels to the capital for a meeting of the pill masters. His power was immediately noticed, people who wanted to use it, but at the decisive moment Xiao Yan still becomes a champion and defends the honor of the empire. But Xiao Yan is waiting for a meeting, for which he has been preparing for three whole years..."}, {"id": 1698, "name_en": "Nodame Cantabile", "name_jp": "のだめカンタービレ", "image_url": "https://cdn.myanimelist.net/images/anime/9/11986.jpg", "genres": ["Comedy", "Romance"], "themes": ["Adult Cast", "Music", "Performing Arts"], "demographics": ["Josei"], "studios": ["J.C.Staff"], "year": 2007, "episodes": 23, "source": "Manga", "score": 8.25, "synopsis": "Shinichi Chiaki is a first class musician whose dream is to play among the elites in Europe. Coming from a distinguished family, he is an infamous perfectionist—not only is he highly critical of himself, but of others as well. The only thing stopping Shinichi from leaving for Europe is his fear of flying. As a result, he's grounded in Japan.\n\nDuring his fourth year at Japan's top music university, Shinichi happens to meet Megumi Noda or, as she refers to herself, Nodame. On the surface, she seems to be an unkempt girl with no direction in life. However, when Shinichi hears Nodame play the piano for the first time, he is in awe of the kind of music she creates. Nevertheless, Shinichi is dismayed to discover that Nodame is his neighbor, and worse, she ends up falling head over heels in love with him.\n\n[Written by MAL Rewrite]"}, {"id": 2685, "name_en": "Tsubasa RESERVoir CHRoNiCLE: Tokyo Revelations", "name_jp": "ツバサ TOKYO REVELATIONS", "image_url": "https://cdn.myanimelist.net/images/anime/4/49129.jpg", "genres": ["Action", "Adventure", "Drama", "Fantasy", "Romance"], "themes": [], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2007, "episodes": 3, "source": "Manga", "score": 8.25, "synopsis": "Continuing their journey from Record Country (the place that held the book of memories), Syaoran and company land into Tokyo Country, a blood-filled country under war from opposing factions for natural resources and survival. While reluctantly involved in the race to live, the group faces their own problems as certain revelations are made, changing their journey forever.\n\nA shocking betrayal and a battle that risks all of their lives. After this revelation, they will never be the same again."}, {"id": 265, "name_en": "Fighting Spirit: Mashiba vs. Kimura", "name_jp": "はじめの一歩 間柴vs木村 死刑執行", "image_url": "https://cdn.myanimelist.net/images/anime/4/64737.jpg", "genres": ["Sports"], "themes": ["Combat Sports"], "demographics": ["Shounen"], "studios": ["Madhouse"], "year": 2003, "episodes": 1, "source": "Manga", "score": 8.25, "synopsis": "Tatsuya Kimura is beginning to feel left behind. With his friends Ippo Makunouchi and Mamoru Takamura holding the championship belt in their respective weight classes, Kimura's inability to become Japan's Junior Lightweight boxing champion eats away at him. Scheduled to fight with the current champion Ryo Mashiba, Kimura begins to train with Ippo's rival Ichirou Miyata, who previously suffered a crushing defeat from Mashiba's signature \"Hitman\" style of boxing.\n\nBut Mashiba has his own desire to hold onto the belt, hoping to soon shoot for the World title and secure financial stability for his sister Kumi. Will Kimura's overwhelming drive to prove his strength as a boxer overcome Mashiba's desperate wish to provide a better life for Kumi?\n\n[Written by MAL Rewrite]"}, {"id": 264, "name_en": "Fighting Spirit: Champion Road", "name_jp": "はじめの一歩 -Champion Road-", "image_url": "https://cdn.myanimelist.net/images/anime/1521/137978.jpg", "genres": ["Sports"], "themes": ["Combat Sports"], "demographics": ["Shounen"], "studios": ["Madhouse"], "year": 2003, "episodes": 1, "source": "Manga", "score": 8.25, "synopsis": "The challenger has become the champion as Ippo Makunouchi now wears the featherweight championship belt of Japan. \nSome time has passed since Ippo's victory, and he has found his friends and coach as supportive as ever; his crush, Kumi Mashiba, seems to enjoy spending time with him as well. Things are looking bright for the new champion, but just as he once set his sights on becoming the best, his first challenger poses an intimidating threat. \n\nKazuki Sanada works as a doctor at the same hospital as Kumi and fights strategically. Known for integrating his knowledge of the human body into his fights, Sanada is a fearsome contender—however, more unnerving than his physical ability, he has garnered the support of the nurses. Despite being the champion, Ippo feels the pressure as he must face the daunting challenge, retain his belt, and win over the girl he loves.\n\n[Written by MAL Rewrite]"}, {"id": 50594, "name_en": "Suzume", "name_jp": "すずめの戸締まり", "image_url": "https://cdn.myanimelist.net/images/anime/1598/128450.jpg", "genres": ["Adventure", "Supernatural"], "themes": ["Mythology"], "demographics": [], "studios": ["CoMix Wave Films"], "year": 2022, "episodes": 1, "source": "Original", "score": 8.25, "synopsis": "On her way to school one day, Suzume Iwato stumbles upon Souta Munakata, a young man searching for abandoned areas. The high school girl directs Souta to a nearby ruin, but out of pure curiosity, she herself decides to head to the same destination.\n\nOnce there, Suzume discovers an isolated door with a dreamlike universe lying beyond it—a place that she can see and feel, but not enter. A strange stone rests on the ground nearby, but it turns into a cat-like creature and scurries away when Suzume lifts it. Suddenly afraid, she heads back toward her school, not realizing that her act of leaving the door open will have consequences.\n\nWith the \"keystone\" released, the evil within the other universe can now freely escape and wreak havoc throughout Japan. Intending to correct her dangerous mistake, Suzume joins Souta—whose true goal is to prevent evil from festering—in finding and locking all open doors before the country is destroyed.\n\n[Written by MAL Rewrite]"}, {"id": 139, "name_en": "Hunter x Hunter: Greed Island Final", "name_jp": "HUNTER×HUNTER G・I Final", "image_url": "https://cdn.myanimelist.net/images/anime/1/139.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Nippon Animation"], "year": 2004, "episodes": 14, "source": "Manga", "score": 8.25, "synopsis": "Continuing with their adventure at Greed Island, Gon and Killua train furiously under the guidance of Biscuit for better preparation at attempting the game. With their abilities vastly improving and the threat of player killers menacing Greed Island, Gon and company continue the game despite the dangers that they will face with the game activities and encounters with other players. Based on the manga by Togashi Yoshihiro."}, {"id": 59953, "name_en": "Tales of Herding Gods", "name_jp": "牧神记", "image_url": "https://cdn.myanimelist.net/images/anime/1324/150012.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Sparkly Key Animation Studio"], "year": 2024, "episodes": 0, "source": "Web novel", "score": 8.24, "synopsis": "In the Disabled Elderly Village, there live nine elderly people with unpredictable origins and the young man Qin Mu they raised. One day, the cows that Qin Mu was responsible for grazing began to speak human words. From then on, Qin Mu became more aware of the dangers and beauty of Daxu, a land abandoned by gods: demons descended with darkness, divine bones danced in the ruins, and dragon bones protected their young, a giant ship that drags the sun... No matter what kind of danger he faces, Qin Mu is fearless. He has integrated the skills passed down by the Nine Elders and vowed to carve out a world with his unparalleled hegemony. \n\n(Source: Bilibili, translated)"}, {"id": 59408, "name_en": "Mononoke The Movie: Chapter II - The Ashes of Rage", "name_jp": "劇場版モノノ怪 第二章 火鼠", "image_url": "https://cdn.myanimelist.net/images/anime/1593/147889.jpg", "genres": ["Horror", "Mystery", "Supernatural", "Suspense"], "themes": ["Adult Cast", "Historical", "Mythology", "Psychological"], "demographics": [], "studios": ["EOTA", "Crew-Cell"], "year": 2025, "episodes": 1, "source": "Original", "score": 8.24, "synopsis": "One month after the Karakasa incident, the situation at the Inner Chambers is slowly returning to normal. To the noble families that run the country, normalcy means carrying on with their usual schemes to prevent any undesirable power shifts in the government. Their current target is the high-ranking concubine Fuki Tokita, who seems to be receiving Lord Tenshi's attention despite being the daughter of a merchant and, therefore, socially unfit to give him an heir.\n\nHowever, supernatural forces still dwell in the shadows. When several unnatural deaths take place in the Inner Chambers, its residents are once again forced to allow the presence of the mysterious Medicine Seller inside its halls. The Medicine Seller is the only one who might be able to determine the cause of these incidents—and find out what role Fuki plays in them.\n\n[Written by MAL Rewrite]"}, {"id": 56215, "name_en": "A Will Eternal 3rd Season", "name_jp": "一念永恒 第3季", "image_url": "https://cdn.myanimelist.net/images/anime/1105/150044.jpg", "genres": ["Action", "Adventure", "Comedy", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["B.CMAY PICTURES"], "year": 2024, "episodes": 59, "source": "Web novel", "score": 8.24, "synopsis": "After Bai Xiaochun merged the four downstream sects to form the Reverse River Sect, he successfully challenged the three midstream sects and established a firm foothold. He was then chosen by the Celestial Being of the Starry Sky Dao Sect in the upstream to be a hostage, thus beginning the third season, the Hostage arc.\n\n(Source: Tencent, translated)"}, {"id": 37989, "name_en": "Golden Kamuy Season 2", "name_jp": "ゴールデンカムイ", "image_url": "https://cdn.myanimelist.net/images/anime/1180/95018.jpg", "genres": ["Action", "Adventure"], "themes": ["Adult Cast", "Historical", "Military"], "demographics": ["Seinen"], "studios": ["Geno Studio"], "year": 2018, "episodes": 12, "source": "Manga", "score": 8.24, "synopsis": "In Hokkaido, it is rumored that there is a stash of hidden gold. This gold was supposedly stolen by a man who killed the original Ainu owners; and before being captured and imprisoned by the police, he hid it in a secret location. In order to relay the gold's location to his comrades on the outside, he tattooed the map on the bodies of his cellmates and promised them a share of the gold—provided they managed to escape and find it.\n\nIn Golden Kamuy 2nd Season, First Lieutenant Tokushirou Tsurumi plans to give the 7th Division an advantage in the war for the tattoos by getting a taxidermist to create skins that only he can distinguish as fake. Meanwhile, Saichi \"The Immortal\" Sugimoto, Asirpa, and their companions continue their hunt for the skins by following a strange rumor: a thief who broke into a home in Yubari found taxidermied human corpses, among which was a torso with strange tattoos.\n\n[Written by MAL Rewrite]"}, {"id": 10271, "name_en": "Kaiji: Against All Rules", "name_jp": "逆境無頼カイジ 破戒録篇", "image_url": "https://cdn.myanimelist.net/images/anime/10/30599.jpg", "genres": ["Suspense"], "themes": ["Adult Cast", "High Stakes Game", "Psychological", "Strategy Game"], "demographics": ["Seinen"], "studios": ["Madhouse"], "year": 2011, "episodes": 26, "source": "Manga", "score": 8.24, "synopsis": "Owing to an increasing debt, Kaiji Itou ends up resuming his old lifestyle. One day, while walking on the street, he stumbles upon Yuuji Endou, who is hunting Kaiji due to the money he owes to the Teiai Group. Unaware of this, Kaiji eagerly follows Endou, hoping for a chance to participate in another gamble, but soon finds out the loan shark's real intentions when he is kidnapped.\n\nGiven that Kaiji is unable to pay off his huge debt, the Teiai Group instead sends him to work in an underground labor camp. He is told that he will have to live in this hell for 15 years, alongside other debtors, until he can earn his freedom. His only hope to put an early end to this nightmare is by saving enough money to be able to go back to the surface for a single day. Once he is there, he plans to obtain the remaining money needed to settle his account by making a high-stakes wager. However, as many temptations threaten his scarce income, Kaiji may have to resort to gambling sooner than he had expected.\n\n[Written by MAL Rewrite]"}, {"id": 25681, "name_en": "Kamisama Kiss Season 2", "name_jp": "神様はじめました◎", "image_url": "https://cdn.myanimelist.net/images/anime/8/69187.jpg", "genres": ["Comedy", "Romance", "Supernatural"], "themes": ["Mythology"], "demographics": ["Shoujo"], "studios": ["TMS Entertainment"], "year": 2015, "episodes": 12, "source": "Manga", "score": 8.24, "synopsis": "Nanami Momozono and her familiars Tomoe and Mizuki have survived quite a few challenges since Nanami took up the mantle of Mikage Shrine's patron god. Naturally, the wind god Otohiko comes to invite Nanami to the Divine Assembly in Izumo, the home of the gods, and Nanami chooses to take Mizuki with her, leaving Tomoe to pose as her at school. However, she has an ulterior motive for attending the Divine Assembly: to discover the whereabouts of the missing Lord Mikage, the former god of the shrine. \n\nAfter her adventures in Izumo, Nanami meets Botanmaru, a tengu child looking for someone she knows all too well—tengu turned goth idol Shinjirou Kurama. Botanmaru needs Shinjirou, their prince, to return home to Mount Kurama and stop the tyranny of Jirou, who has taken over the rule of their hometown. However, Nanami soon discovers a force much darker than Jirou is at work on the mountain.\n\nAs a fledgling god becoming more accustomed to divinity, Nanami finds herself dealing with a tengu rebellion, her blooming feelings for Tomoe, and a strange man with ties to both Tomoe's past and Nanami's future.\n\n[Written by MAL Rewrite]"}, {"id": 13125, "name_en": "From the New World", "name_jp": "新世界より", "image_url": "https://cdn.myanimelist.net/images/anime/1549/136389.jpg", "genres": ["Drama", "Fantasy", "Horror", "Mystery", "Sci-Fi", "Suspense"], "themes": ["Psychological"], "demographics": [], "studios": ["A-1 Pictures"], "year": 2012, "episodes": 25, "source": "Novel", "score": 8.24, "synopsis": "In the year 2011, a small percentage of humans began manifesting psychokinetic abilities known as \"Cantus.\" Over a millennium later, in the small town of Kamisu 66, Saki Watanabe is the last of her friends to awaken her powers and join the Sage Academy, a school for psychics like her. Although everyone at the institution has Cantus, they are not all equal; shortly after Saki enrolls, one of her classmates who is regarded as being weaker than the others suddenly disappears.\n\nWalking home one day with her friends—the determined Maria Akizuki, the intelligent Shun Aonuma, the observant Satoru Asahina, and the timid Mamoru Itou—she comes across two unfamiliar creatures known as \"Monster Rats.\" These beings resemble moles and worship those with Cantus as gods. As a result, when Saki uses her abilities to save one from trouble, she is met with exceptional gratitude.\n\nNow unsure about the Monster Rats' place in society, Saki and her friends find out about another disappearance. As time passes, they slowly look for answers to the mysteries that surround them and begin to realize that this seemingly \"perfect\" new world masks humanity's dark past.\n\n[Written by MAL Rewrite]"}, {"id": 2418, "name_en": "Sword of the Stranger", "name_jp": "ストレンヂア -無皇刃譚-", "image_url": "https://cdn.myanimelist.net/images/anime/9/87066.jpg", "genres": ["Action", "Adventure"], "themes": ["Historical", "Samurai"], "demographics": [], "studios": ["Bones"], "year": 2007, "episodes": 1, "source": "Original", "score": 8.24, "synopsis": "In the Sengoku period of Japan, a young orphan named Kotarou and his dog Tobimaru steal from unsuspecting villagers in order to make ends meet. However, Kotarou is forced to remain on the run when he finds himself being hunted down by assassins sent by China's Ming Dynasty for mysterious reasons not involving his petty crimes. \n\nFortunately, the duo run into Nanashi, a ronin who has taken refuge in a small temple, when Kotarou is attacked and Tobimaru poisoned. Although the samurai  saves the helpless pair from their pursuers, he feels that there is no need to help them further; but when offered a gem in exchange for his services as a bodyguard, he reluctantly accepts Kotarou's offer of employment—just until Tobimaru is healed and the two reach their destination. As the three set out on a perilous journey, it soon becomes evident that their path is riddled with danger, as the Ming Dynasty has now sent a terrifying swordsman after them to capture Kotarou and fulfill a certain prophecy.\n\n[Written by MAL Rewrite]"}, {"id": 44940, "name_en": "World Trigger 3rd Season", "name_jp": "ワールドトリガー", "image_url": "https://cdn.myanimelist.net/images/anime/1617/117474.jpg", "genres": ["Action", "Sci-Fi"], "themes": [], "demographics": ["Shounen"], "studios": ["Toei Animation"], "year": 2021, "episodes": 14, "source": "Manga", "score": 8.24, "synopsis": "At the Border Defense Agency's headquarters, the B-Rank Wars are underway as each team fights intensely to secure a spot in the upcoming Away Mission. Though Osamu Mikumo and the rest of Tamakoma-2 have pulled through thus far, the final rounds will require them to push far beyond the limits of their current abilities. Taking in a talented new member, the squad begins to train and develop new strategies, but, amid these efforts, the team is shaken by a heated dispute—one that could jeopardize their ticket to the Neighbor's dimension.\n\n[Written by MAL Rewrite]"}, {"id": 32937, "name_en": "KonoSuba: God's Blessing on This Wonderful World! 2", "name_jp": "この素晴らしい世界に祝福を！ 2", "image_url": "https://cdn.myanimelist.net/images/anime/2/83188.jpg", "genres": ["Adventure", "Comedy", "Fantasy"], "themes": ["Isekai", "Parody"], "demographics": [], "studios": ["Studio Deen"], "year": 2017, "episodes": 10, "source": "Light novel", "score": 8.24, "synopsis": "When Kazuma Satou died, he was given two choices: pass on to heaven or be revived in a fantasy world. After choosing the new world, the goddess Aqua tasked him with defeating the Demon King, and let him choose any weapon to aid him. Unfortunately, Kazuma chose to bring Aqua herself and has regretted the decision ever since then.\n\nNot only is he stuck with a useless deity turned party archpriest, the pair also has to make enough money for living expenses. To add to their problems, their group continued to grow as more problematic adventurers joined their ranks. Their token spellcaster, Megumin, is an explosion magic specialist who can only cast one spell once per day and refuses to learn anything else. There is also their stalwart crusader, Lalatina Ford \"Darkness\" Dustiness, a helpless masochist who makes Kazuma look pure in comparison.\n\nKono Subarashii Sekai ni Shukufuku wo! 2 continues to follow Kazuma and the rest of his party through countless more adventures as they struggle to earn money and have to deal with one another's problematic personalities. However, things rarely go as planned, and they are often sidetracked by their own idiotic tendencies.\n\n[Written by MAL Rewrite]"}, {"id": 1033, "name_en": "Millennium Actress", "name_jp": "千年女優", "image_url": "https://cdn.myanimelist.net/images/anime/1648/93626.jpg", "genres": ["Action", "Adventure", "Award Winning", "Drama", "Fantasy", "Romance"], "themes": ["Adult Cast", "Historical", "Showbiz"], "demographics": [], "studios": ["Madhouse"], "year": 2002, "episodes": 1, "source": "Original", "score": 8.24, "synopsis": "At the turn of the millennium, Ginei Studio's dilapidated buildings are set to be demolished. Ex-employee and filmmaker Genya Tachibana decides to honor this occasion with a commemorative documentary about the company's star actress: Chiyoko Fujiwara, the reclusive sweetheart of Shouwa Era cinema. Having finally obtained permission to interview the retired starlet, an enamored Genya drags along cynical cameraman Kyouji Ida to meet her, ready to put his lifelong idol back in the spotlight once more.\n\nHidden in this secluded mountain retreat is a thousand years of history condensed into one lifetime, waiting to be narrated. Chiyoko's recollections take them on an illusionary journey through Japanese cinematic history that transcends the boundaries of reality; the saga of her acting career intertwines with her filmography, the actors in her life blend seamlessly with the characters on screen, and the present melds with the past. Though the actress may have retired at the height of her career 30 years ago, the curtain on her life's stage has yet to fall.\n\n[Written by MAL Rewrite]"}, {"id": 31812, "name_en": "Black Butler: Book of the Atlantic", "name_jp": "劇場版 黒執事 Book of the Atlantic", "image_url": "https://cdn.myanimelist.net/images/anime/9/85792.jpg", "genres": ["Action", "Mystery", "Supernatural"], "themes": ["Historical", "Mythology"], "demographics": ["Shounen"], "studios": ["A-1 Pictures"], "year": 2017, "episodes": 1, "source": "Manga", "score": 8.24, "synopsis": "The young Earl Ciel Phantomhive—the Queen's Guard Dog—is once again called to investigate seemingly supernatural phenomena when news of miraculous resurrections begins to surface in Victorian London. Along with Sebastian Michaelis, his demon butler, they board the luxury cruise liner Campania to investigate rumors of the Aurora Society—a medical organization suspected of experimenting on the dead.\n\nGrim reapers begin to appear on the ship, and it becomes apparent that the ship is about to be overrun with the undead as a devious plan is put into motion. Ciel and Sebastian must now uncover the secrets that lie behind the Aurora Society's phoenix symbol, and with the help of some old acquaintances, return the undead to their coffins or share a watery grave.\n\n[Written by MAL Rewrite]"}, {"id": 329, "name_en": "Planetes", "name_jp": "プラネテス", "image_url": "https://cdn.myanimelist.net/images/anime/1209/142900.jpg", "genres": ["Award Winning", "Drama", "Romance", "Sci-Fi"], "themes": ["Adult Cast", "Space", "Workplace"], "demographics": ["Seinen"], "studios": ["Sunrise"], "year": 2003, "episodes": 26, "source": "Manga", "score": 8.24, "synopsis": "In 2075, space travel is no longer just a dream, but an everyday reality for mankind. Advancements in science and technology have led to the colonization of the moon, the commercialization of outer space, and the formation of large space corporations. Ai Tanabe, an upbeat woman whose interests lie in the cosmos, joins Technora Corporation as a member of their Debris Section, a department dedicated to the removal of dangerous space junk between the orbits of the Earth and Moon.\n\nHowever, Ai soon discovers how unappreciated her job is. As the laughingstock of Technora, the Debris Section is severely understaffed, poorly funded, and is forced to use a dilapidated spaceship nicknamed the \"Toy Box\" for debris retrieval. Undeterred, Ai perseveres and gradually becomes acquainted with the strange personalities that make up the Debris Section's staff, such as the bumbling but good-natured chief clerk Philippe Myers; the mysterious and tight-lipped temp worker Edelgard Rivera; and the hotheaded and passionate Hachirouta Hoshino, who longs for a spaceship to call his own.\n\nPlanetes is an unconventional sci-fi series that portrays the vastness of space as a backdrop for the personal lives of ordinary people—people who may have been born on Earth, but whose hopes and dreams lie amongst the stars.\n\n[Written by MAL Rewrite]"}, {"id": 45577, "name_en": "IDOLISH7 Third BEAT!", "name_jp": "アイドリッシュセブン Third BEAT!", "image_url": "https://cdn.myanimelist.net/images/anime/1646/115723.jpg", "genres": ["Drama"], "themes": ["Idols (Male)", "Music"], "demographics": [], "studios": ["TROYCA"], "year": 2021, "episodes": 13, "source": "Game", "score": 8.24, "synopsis": "Nearing their first debut anniversary and with their popularity still on the rise, IDOLiSH7 set a date for a live tour to express their gratitude toward their fans. Meanwhile, the Black or White event draws closer—and as the previous year's winners—the group must prepare to take on the challengers. Looking to solidify their reputation in the world of idols, IDOLiSH7 are determined to come out victorious and settle the score once and for all with their rival, TRIGGER.\n\nWhile working around their packed schedules, IDOLiSH7, TRIGGER, and Re:vale meet powerful figures in the entertainment industry. In the midst of the hectic period, some members catch wind of a scandalous rumor regarding IDOLiSH7. As the members of IDOLiSH7 begin to worry about their image, their bonds are put to the test, all while a spiteful figure begins to carry out a certain plan behind the scenes.\n\n[Written by MAL Rewrite]"}, {"id": 35838, "name_en": "Girls' Last Tour", "name_jp": "少女終末旅行", "image_url": "https://cdn.myanimelist.net/images/anime/12/88321.jpg", "genres": ["Adventure", "Mystery", "Sci-Fi", "Slice of Life"], "themes": ["Iyashikei"], "demographics": [], "studios": ["White Fox"], "year": 2017, "episodes": 12, "source": "Web manga", "score": 8.24, "synopsis": "Amid the desolate remains of a once-thriving city, only the rumbling of a motorbike breaks the cold winter silence. Its riders, Chito and Yuuri, are the last survivors in the war-torn city. Scavenging old military sites for food and parts, the two girls explore the wastelands and speculate about the old world to pass the time. Chito and Yuuri each occasionally struggle with the looming solitude, but when they have each other, sharing the weight of being two of the last humans becomes a bit more bearable. Between Yuuri's clumsy excitement and Chito's calm composure, their dark days get a little brighter with shooting practice, new books, and snowball fights on the frozen battlefield. \n\nAmong a scenery of barren landscapes and deserted buildings, Shoujo Shuumatsu Ryokou tells the uplifting tale of two girls and their quest to find hope in a bleak and dying world.\n\n[Written by MAL Rewrite]"}, {"id": 34036, "name_en": "Detective Conan: Episode One - The Great Detective Turned Small", "name_jp": "名探偵コナン エピソードONE 小さくなった名探偵", "image_url": "https://cdn.myanimelist.net/images/anime/5/81968.jpg", "genres": ["Adventure", "Comedy", "Mystery"], "themes": ["Detective"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2016, "episodes": 1, "source": "Manga", "score": 8.24, "synopsis": "The \"real\" episode 1 of Meitantei Conan written by the Mangaka Gosho Aoyama."}, {"id": 31240, "name_en": "Re:ZERO -Starting Life in Another World-", "name_jp": "Re:ゼロから始める異世界生活", "image_url": "https://cdn.myanimelist.net/images/anime/1522/128039.jpg", "genres": ["Drama", "Fantasy", "Suspense"], "themes": ["Isekai", "Psychological", "Time Travel"], "demographics": [], "studios": ["White Fox"], "year": 2016, "episodes": 25, "source": "Light novel", "score": 8.24, "synopsis": "When Subaru Natsuki leaves the convenience store, the last thing he expects is to be wrenched from his everyday life and dropped into a fantasy world. Things are not looking good for the bewildered teenager; however, not long after his arrival, he is attacked by some thugs. Armed with only a bag of groceries and a now useless cell phone, he is quickly beaten to a pulp. Fortunately, a mysterious beauty named Satella, in hot pursuit after the one who stole her insignia, happens upon Subaru and saves him. In order to thank the honest and kindhearted girl, Subaru offers to help in her search, and later that night, he even finds the whereabouts of that which she seeks. But unbeknownst to them, a much darker force stalks the pair from the shadows, and just minutes after locating the insignia, Subaru and Satella are brutally murdered.\n\nHowever, Subaru immediately reawakens to a familiar scene—confronted by the same group of thugs, meeting Satella all over again—the enigma deepens as history inexplicably repeats itself.\n\n[Written by MAL Rewrite]"}, {"id": 59062, "name_en": "Gachiakuta", "name_jp": "ガチアクタ", "image_url": "https://cdn.myanimelist.net/images/anime/1682/150432.jpg", "genres": ["Action", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Bones Film"], "year": 2025, "episodes": 24, "source": "Manga", "score": 8.23, "synopsis": "The inhabitants of a certain wealthy town think nothing of throwing objects away. However, their waste is priceless to Rudo, a resident of the town's slums. Despite the constant warnings from his adoptive father Regto, Rudo spends his days searching for reusable materials that would otherwise be sent to the giant disposal area known as the Pit. Due to its vastness, the Pit doubles as a means of criminal punishment; those dropped in are never to return again.\n\nWhen Regto is murdered by a mysterious assailant, Rudo is falsely accused of the crime and thrown into the Pit. To his surprise, he awakens in a trash-filled area inhabited by enormous monsters formed from the junk. As the toxic air and Trash Beasts push Rudo to the brink of death, he is saved by Enjin, one of the Cleaners who wield weapons known as Vital Instruments to fight the monstrosities. Having gained his own Vital Instrument, Rudo soon joins the Cleaners in the hopes of finding a way to escape the Pit and avenge his father.\n\n[Written by MAL Rewrite]"}, {"id": 58788, "name_en": "Journal with Witch", "name_jp": "違国日記", "image_url": "https://cdn.myanimelist.net/images/anime/1791/154233.jpg", "genres": ["Drama"], "themes": [], "demographics": ["Josei"], "studios": ["Shuka"], "year": 2026, "episodes": 13, "source": "Manga", "score": 8.23, "synopsis": "Thirty-five-year-old novelist Makio Koudai never had a good relationship with her older sister Minori, who always berated her for being different. Due to this, Makio is not stricken with grief upon hearing the news that Minori and her husband die in a car crash. But when Makio is asked to identify their bodies, she runs into her 15-year-old niece, Asa Takumi, whom she has not seen in years.\n\nStruggling to process her parents' death, Makio reassures Asa that her complicated feelings are valid and suggests that the teenager start writing in a diary as a way to cope with the loss. Upon learning that no other relatives wanted to take in Asa, Makio decides to become her guardian despite her lack of experience. In a world full of uncertainty, the novelist and teenager must learn to live with each other while figuring themselves out.\n\n[Written by MAL Rewrite]"}, {"id": 52684, "name_en": "Throne of Seal 2nd Season", "name_jp": "神印王座 第二季", "image_url": "https://cdn.myanimelist.net/images/anime/1897/150028.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": [], "studios": ["Shenman Entertainment"], "year": 2022, "episodes": 0, "source": "Web novel", "score": 8.23, "synopsis": "Revealing himself to be a Dual Knight, Long Hao Chen triumphs in his match within his temple and advances to the finals. But before the grand finals can begin, ranking matches are conducted in each temple to determine the strongest within. During the rankings, Hao Chen is injured, enraging Sheng Cai'er and driving her to seek vengeance for what happened to him. Her actions cause a fight between the Knight Temple and Assassin Temple, only to be subdued after a shocking revelation—that Sheng Cai'er is a Reincarnated Saint.\n\nThe finals for the Demon Hunting Team Competition begin with the division of the sixty remaining participants into six groups. The top sixteen participants are then further pitted against each other to determine the top three. Each of the top three will be rewarded with a spiritual stove and the right to pick their teammates.\n\nIn order to become a knight that is capable of defeating the Demon Lord, Long Hao Chen must strive to be first in this competition so he can select the best teammates to give him a fighting chance.\n\n[Written by MAL Rewrite]"}, {"id": 52588, "name_en": "Kaiju No. 8", "name_jp": "怪獣8号", "image_url": "https://cdn.myanimelist.net/images/anime/1370/140362.jpg", "genres": ["Action", "Fantasy", "Sci-Fi"], "themes": ["Adult Cast", "Military", "Urban Fantasy"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2024, "episodes": 12, "source": "Manga", "score": 8.23, "synopsis": "After the destruction of their hometown, childhood friends Kafka Hibino and Mina Ashiro make a pact to become officers in the Defense Force—a militarized organization tasked with protecting Japan from colossal monsters known as \"kaijuu.\" Decades later, the 32-year-old Kafka has all but given up on his dreams of heroism. Instead, he cleans up the remains of the slaughtered kaijuu after they are defeated by valiant soldiers—including Mina, who has successfully achieved their shared goal.\n\nUpon meeting his new coworker, Reno Ichikawa, Kafka faces a mirror of his past self: an ambitious young man whose one desire is to fight as a member of the Defense Force. Unfortunately, the two are soon involved in a freak encounter with a rogue kaijuu. Though Kafka demonstrates his innate heroic nature and rescues Reno from certain doom, he is left gravely injured.\n\nWhile both men recover in a hospital, Kafka is seemingly attacked by another one of the beasts. As a result, he gains the ability to transform into a humanoid kaijuu with the strength and powers of the massive monsters menacing Japan. Dubbed \"Kaijuu No. 8\" by the military, Kafka resolves to use his newfound gifts for the greater good. Tied together by mutual respect, Kafka and Reno set out to join warriors like Mina at the forefront of the Defense Force.\n\n[Written by MAL Rewrite]"}, {"id": 37055, "name_en": "Saga of Tanya the Evil: The Movie", "name_jp": "劇場版 幼女戦記", "image_url": "https://cdn.myanimelist.net/images/anime/1249/117182.jpg", "genres": ["Action", "Fantasy"], "themes": ["Isekai", "Military", "Reincarnation"], "demographics": [], "studios": ["Nut"], "year": 2019, "episodes": 1, "source": "Light novel", "score": 8.23, "synopsis": "With its armies sweeping across the continent, the Empire seems unstoppable. After securing victory over the remnants of the Republic's army, the Empire's ultimate victory is finally within reach. However, dark clouds are gathering in the East. The communist-led Russy Federation is mustering troops on its western border, preparing to enter the war. Supported by a detachment of Allied volunteer magicians—among whom is Mary Sioux, the daughter of a soldier killed by Tanya von Degurechaff—the Federation is determined to spread the communist creed and bring the Empire to its knees.\n\nMeanwhile, Tanya and her battalion return to the imperial capital from the southern front. Upon their arrival, they are tasked with investigating troop movements on the border with the Federation. Any escalation of violence at this point may lead to new conflicts, plunging the world into a devastating global war.\n\nWill the Empire eventually emerge victorious from its struggle, or will it crumble in the face of superior enemies and radically different ideologies?\n\n[Written by MAL Rewrite]"}, {"id": 36990, "name_en": "Non Non Biyori Movie: Vacation", "name_jp": "劇場版 のんのんびより ばけーしょん", "image_url": "https://cdn.myanimelist.net/images/anime/1044/93575.jpg", "genres": ["Slice of Life"], "themes": ["CGDCT", "Iyashikei"], "demographics": ["Seinen"], "studios": ["SILVER LINK."], "year": 2018, "episodes": 1, "source": "Manga", "score": 8.23, "synopsis": "With summer vacation coming to an end, the girls are having as much fun as they can with their remaining time. However, their daily shenanigans are cut short when Suguru Koshigaya wins the grand prize of a lottery—tickets to Okinawa! After hasty preparations, the Asahigaoka group embarks on a three-day trip for their final summer getaway.\n\nUpon arriving in Okinawa and checking into an inn, the group comes across Aoi Niizato—the young daughter of the inn's hostess. Despite being of similar age, her mature demeanor leaves Natsumi Koshigaya reflecting upon her own childish nature. With the sign of an unexpected friendship blooming on the horizon, the girls waste no time diving into their ambitious sightseeing plans and regional activities!\n\n[Written by MAL Rewrite]"}, {"id": 15227, "name_en": "In This Corner of the World", "name_jp": "この世界の片隅に", "image_url": "https://cdn.myanimelist.net/images/anime/2/87704.jpg", "genres": ["Award Winning", "Drama"], "themes": ["Historical"], "demographics": ["Seinen"], "studios": ["MAPPA"], "year": 2016, "episodes": 1, "source": "Manga", "score": 8.23, "synopsis": "Suzu Urano is a pure and kindhearted girl who loves to draw and keep her head in the clouds. Growing up in the outskirts of Hiroshima with her family, she is more than happy to help with her grandmother's nori business.\n\nHowever, when she becomes of age, Suzu leaves her beloved home to marry Shuusaku Houjou, a man she barely knows. As she integrates into her new husband's household, the homesick bride struggles to adjust to the unfamiliar environment as the war effort extends far beyond its point of no return. When the war reaches Suzu's own backyard and peace gives way to brutality, how will she support herself and those she comes to love along the way?\n\nKono Sekai no Katasumi ni paints a colorful yet haunting depiction of everyday life in the years before and after World War II, showcasing the perseverance and fortitude of ordinary Japanese during one of the darkest periods of modern history.\n\n[Written by MAL Rewrite]"}, {"id": 33051, "name_en": "Mobile Suit Gundam: Iron-Blooded Orphans 2nd Season", "name_jp": "機動戦士ガンダム 鉄血のオルフェンズ 第2期", "image_url": "https://cdn.myanimelist.net/images/anime/6/80899.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["Mecha", "Space"], "demographics": [], "studios": ["Sunrise"], "year": 2016, "episodes": 25, "source": "Original", "score": 8.23, "synopsis": "Tekkadan has now become a direct affiliate of Teiwaz after procuring a new trade agreement with Arbrau. With its newfound funds and prestige, Tekkadan finds both its list of allies and enemies growing. Meanwhile, the flames of the Gjallarhorn power struggle continue to rage in full force. As a part of her efforts to make Mars financially independent from Earth, Kudelia Aina Bernstein founds the Admoss Company and enlists Tekkadan as her business partner. \n\nThe stakes are getting higher as the Tekkadan family continues to grow. Will Orga, Mikazuki, and the rest of the Tekkadan faction be able to keep up, or will Kudelia's dream of Martian independence die out?\n\n[Written by MAL Rewrite]"}, {"id": 11843, "name_en": "Daily Lives of High School Boys", "name_jp": "男子高校生の日常", "image_url": "https://cdn.myanimelist.net/images/anime/3/33257.jpg", "genres": ["Comedy"], "themes": ["Gag Humor", "School"], "demographics": [], "studios": ["Sunrise"], "year": 2012, "episodes": 12, "source": "Web manga", "score": 8.23, "synopsis": "Roaming the halls of the all-boys Sanada North High School are three close comrades: the eccentric ringleader with a hyperactive imagination Hidenori, the passionate Yoshitake, and the rational and prudent Tadakuni. Their lives are filled with giant robots, true love, and intense drama... in their colorful imaginations, at least. In reality, they are just an everyday trio of ordinary guys trying to pass the time, but who said everyday life couldn't be interesting? Whether it's an intricate RPG reenactment or an unexpected romantic encounter on the riverbank at sunset, Danshi Koukousei no Nichijou is rife with bizarre yet hilariously relatable situations that are anything but mundane.\n\n[Written by MAL Rewrite]"}, {"id": 37450, "name_en": "Rascal Does Not Dream of Bunny Girl Senpai", "name_jp": "青春ブタ野郎はバニーガール先輩の夢を見ない", "image_url": "https://cdn.myanimelist.net/images/anime/1301/93586.jpg", "genres": ["Drama", "Romance", "Supernatural"], "themes": ["School"], "demographics": [], "studios": ["CloverWorks"], "year": 2018, "episodes": 13, "source": "Light novel", "score": 8.23, "synopsis": "The rare and inexplicable Puberty Syndrome is thought of as a myth. It is a rare disease which only affects teenagers, and its symptoms are so supernatural that hardly anyone recognizes it as a legitimate occurrence. However, high school student Sakuta Azusagawa knows from personal experience that it is very much real, and happens to be quite prevalent in his school.\n\nMai Sakurajima is a third-year high school student who gained fame in her youth as a child actress, but recently halted her promising career for reasons unknown to the public. With an air of unapproachability, she is well known throughout the school, but none dare interact with her—that is until Sakuta sees her wandering the library in a bunny girl costume. Despite the getup, no one seems to notice her, and after confronting her, he realizes that she is another victim of Puberty Syndrome. As Sakuta tries to help Mai through her predicament, his actions bring him into contact with more girls afflicted with the elusive disease.\n\n[Written by MAL Rewrite]"}, {"id": 48316, "name_en": "The Eminence in Shadow", "name_jp": "陰の実力者になりたくて！", "image_url": "https://cdn.myanimelist.net/images/anime/1091/128729.jpg", "genres": ["Action", "Comedy", "Fantasy"], "themes": ["Isekai", "Reincarnation"], "demographics": [], "studios": ["Nexus"], "year": 2022, "episodes": 20, "source": "Light novel", "score": 8.23, "synopsis": "For as long as he can remember, Minoru Kagenou has been fixated on becoming as strong as possible, which has led him to undertake all kinds of rigorous training. This wish, however, does not stem from a desire to be recognized by others; rather, Minoru does everything he can to blend in with the crowd. So, while pretending to be a completely average student during the day, he arms himself with a crowbar and ruthlessly thrashes local biker gangs at night. Yet when Minoru finds himself in a truck accident, his ambitions seemingly come to a sudden end. In his final moments, he laments his powerlessness—no matter how much he trained, there was nothing he could do to overcome his human limitations.\n\nBut instead of dying, Minoru reawakens as Cid, the second child of the noble Kagenou family, in another world—one where magic is commonplace. With the power he so desired finally within his grasp, he dons the moniker \"Shadow\" and establishes Shadow Garden: a group whose sole purpose is to combat the enigmatic Cult of Diablos, an organization born from Cid's imagination. However, as Shadow Garden grows in both membership and influence, it becomes increasingly apparent that the Cult of Diablos is not as fictional as Cid had intended.\n\n[Written by MAL Rewrite]"}, {"id": 59027, "name_en": "Spy x Family Season 3", "name_jp": "SPY×FAMILY Season 3", "image_url": "https://cdn.myanimelist.net/images/anime/1697/151793.jpg", "genres": ["Action", "Comedy"], "themes": ["Childcare", "Super Power"], "demographics": ["Shounen"], "studios": ["Wit Studio", "CloverWorks"], "year": 2025, "episodes": 13, "source": "Manga", "score": 8.22, "synopsis": "Despite occasional setbacks, Operation Strix has remained on track to extract sensitive information from the enigmatic politician Donovan Desmond. To this end, spy Loid Forger has managed to maintain his pretend family consisting of his telepathic daughter, Anya; assassin wife, Yor; and their clairvoyant dog, Bond. Still the only one aware of everyone's true identities, Anya tries to sustain the family's cohesion while working to befriend Donovan's son Damian, her Eden Academy classmate.\n\nHowever, Anya's efforts instead earn her another Tonitrus Bolt, bringing her closer to getting expelled and jeopardizing the operation's success. Hearing the news, Loid faints from shock. In his unconsciousness, he reminisces about his tragic past and his journey to becoming a spy. Realizing what Operation Strix means to him and the world, Loid finds renewed motivation. Although they each work toward their own covert goals, the Forgers continue to cherish their chaotic yet blissful family life.\n\n[Written by MAL Rewrite]"}, {"id": 54959, "name_en": "BanG Dream! It's MyGO!!!!!", "name_jp": "バンドリ！It's MyGO!!!!!", "image_url": "https://cdn.myanimelist.net/images/anime/1891/136948.jpg", "genres": ["Drama"], "themes": ["Music"], "demographics": [], "studios": ["SANZIGEN"], "year": 2023, "episodes": 13, "source": "Mixed media", "score": 8.22, "synopsis": "In their last year of middle school, the five band members of CRYCHIC went their separate ways after an unpleasant disbandment. Each wrestling with their own regrets, the girls enrolled in different high schools, hoping never to hear from one another again.\n\nMeanwhile, Anon Chihaya transfers to Haneoka Girls' Academy in the middle of the school year. As most students play in a band as a hobby, Anon desires to fit in and spends her first days trying to recruit members for her own band, envisioning herself as the group's vocalist. She befriends Tomori Takamatsu, a shy girl who seems interested but hesitant about joining. As it turns out, Tomori used to be part of CRYCHIC, an experience that has scarred her deeply.\n\nThrough Tomori, Anon meets Taki Shiina and Soyo Nagasaki, both former members of the same band. Although Taki detests the idea of playing with Anon, Soyo is delighted to help her out. Now with Soyo by her side, Anon's dream of forming a band is within reach—if only she could find a few more members.\n\n[Written by MAL Rewrite]"}, {"id": 35677, "name_en": "Liz and the Blue Bird", "name_jp": "リズと青い鳥", "image_url": "https://cdn.myanimelist.net/images/anime/1638/93032.jpg", "genres": ["Award Winning", "Drama"], "themes": ["Music", "Performing Arts", "School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2018, "episodes": 1, "source": "Novel", "score": 8.22, "synopsis": "Liz's days of solitude come to an end when she meets a blue bird in the form of a young girl. Although their relationship blossoms, Liz must make a heart-wrenching decision in order to truly realize her love for Blue Bird.\n\nHigh school seniors and close friends Mizore Yoroizuka and Nozomi Kasaki are tasked to play the lead instruments in the third movement of Liz and the Blue Bird, a concert band piece inspired by this fairy tale. The introverted and reserved Mizore plays the oboe, representing the kind and gentle Liz. Meanwhile, the radiant and popular Nozomi plays the flute, portraying the cheerful and energetic Blue Bird.\n\nHowever, as they rehearse, the distance between Mizore and Nozomi seems to grow. Their disjointed duet disappoints the band, and with graduation on the horizon, uncertainty about the future spurs complicated emotions. With little time to improve as their performance draws near, they desperately attempt to connect with their respective characters. But when Mizore and Nozomi consider the story from a brand-new perspective, will the girls find the strength to face harsh realities?\n\nA spin-off film adaptation of the Hibike Euphonium! series, Liz to Aoi Tori dances between the parallels of a charming fairy tale, a moving musical piece, and a delicate high school friendship.\n\n[Written by MAL Rewrite]"}, {"id": 28805, "name_en": "The Boy and the Beast", "name_jp": "バケモノの子", "image_url": "https://cdn.myanimelist.net/images/anime/11/73540.jpg", "genres": ["Fantasy"], "themes": ["Anthropomorphic", "Isekai"], "demographics": [], "studios": ["Studio Chizu"], "year": 2015, "episodes": 1, "source": "Original", "score": 8.22, "synopsis": "Two souls, living very different lives, wander alone and isolated in their respective worlds. For nine-year-old Ren, the last person who treated him with any form of kindness has been killed, and he is shunned by what is left of his family. With no parents, no real family, and no place to go, Ren escapes into the confusing streets and alleyways of Shibuya. Through the twists and turns of the alleys, Ren stumbles into the intimidating Kumatetsu, who leads him to the beast realm of Shibuten.\n\nFor Kumatetsu, the boy represents a chance for him to become a candidate to replace the lord of the realm once he retires. While nearly unmatched in combat, Kumatetsu's chilly persona leaves him with no disciples to teach and no way to prove he is worthy of becoming the lord's successor. \n\nWhile the two share different goals, they agree to help each other in order to reach them. Kumatetsu searches for recognition; Ren, now known as Kyuuta, searches for the home he never had. As the years pass by, it starts to become apparent that the two are helping each other in more ways than they had originally thought. Perhaps there has always been less of a difference between them, a boy and a beast, than either of the two ever realized.\n\n[Written by MAL Rewrite]"}, {"id": 5690, "name_en": "Nodame Cantabile Finale", "name_jp": "のだめカンタービレ フィナーレ", "image_url": "https://cdn.myanimelist.net/images/anime/1084/119096.jpg", "genres": ["Comedy", "Romance"], "themes": ["Adult Cast", "Music", "Performing Arts"], "demographics": ["Josei"], "studios": ["J.C.Staff"], "year": 2010, "episodes": 11, "source": "Manga", "score": 8.22, "synopsis": "Shinichi Chiaki is quickly making a name for himself as the principal conductor of the revitalized Roux-Marlet Orchestra, and Megumi \"Nodame\" Noda has made leaps and bounds as a pianist at the Conservatoire de Paris. However, tensions mount between the two as Nodame feels left behind by Chiaki's growing success and his close friendship with legendary piano prodigy Rui Son. Disregarding her teacher Professor Charles Auclair's advice, Nodame enters another piano competition in an attempt to jumpstart her own performance career. \n\nMeanwhile, those around Chiaki and Nodame are at their own crossroads. Rui begins to doubt herself after hearing Nodame's playing and being denied tutelage from Auclair; Maestro Franz von Stresemann faces the reality of his mortality; pianists Yunlong Li and Tatiana Vishneva feverishly prepare for a competition, while the latter also struggles with her growing feelings for oboist and fellow student Yasunori Kuroki. \n\nAs Chiaki, Nodame, and their friends continue on their respective journeys, they must not only strive to stay true to themselves, but also remember where it all started.\n\n[Written by MAL Rewrite]"}, {"id": 47794, "name_en": "MILGRAM", "name_jp": "ミルグラム", "image_url": "https://cdn.myanimelist.net/images/anime/1497/112023.jpg", "genres": ["Suspense"], "themes": ["Music", "Psychological"], "demographics": [], "studios": ["OTOIRO"], "year": 2020, "episodes": 0, "source": "Other", "score": 8.22, "synopsis": "Es awakens in the facility Milgram to find a strange talking animal named Jackalope staring them down. Soon, they learn their purpose: as the prison guard, Es must delve into the psyches of 10 inmates, each of them imprisoned for causing the loss of life in some form or another. Their fates rest in Es' hands. Do the means justify an end, and how much does intent matter if the result is death? No matter how the lines between good and evil, right and wrong, and just and cruel are blurred, there are only two options Es can choose between: innocent or guilty.\n\n[Written by MAL Rewrite]"}, {"id": 40784, "name_en": "Scissor Seven: The Strongest Hairstylist", "name_jp": "伍六七之最强发型师", "image_url": "https://cdn.myanimelist.net/images/anime/1962/153860.jpg", "genres": ["Action", "Comedy", "Drama", "Mystery"], "themes": ["Martial Arts", "Super Power"], "demographics": [], "studios": ["Sharefun Studio"], "year": 2019, "episodes": 10, "source": "Original", "score": 8.22, "synopsis": "Seeking to recover his memories, a scissor-wielding hairdresser, bungling quasi-assassin stumbles into a struggle for power among two feuding factions.\n\n(Source: IMDb)"}, {"id": 11977, "name_en": "Puella Magi Madoka Magica the Movie Part 1: Beginnings", "name_jp": "劇場版 魔法少女まどか☆マギカ 始まりの物語", "image_url": "https://cdn.myanimelist.net/images/anime/6/42331.jpg", "genres": ["Drama", "Suspense"], "themes": ["Mahou Shoujo", "Psychological"], "demographics": [], "studios": ["Shaft"], "year": 2012, "episodes": 1, "source": "Original", "score": 8.22, "synopsis": "Madoka Kaname and her best friend Sayaka Miki are ordinary middle school students in the city of Mitakihara. But one day, they encounter a strange cat-like creature named Kyuubey, who claims he can grant them one wish. In exchange, they would become magical girls and fight against evil perpetrated by witches. A veteran magical girl in the area, Mami Tomoe, decides to show them how to hunt witches, while the mysterious transfer student Homura Akemi warns them to not take Kyuubey's deal, though she refuses to say why.\n\nHowever, after witnessing the brutal reality of fighting witches, the girls decide it may be safer to decline Kyuubey's offer. But when another magical girl arrives in the city and Sayaka decides to make a wish to help the one she loves, things quickly escalate as they are confronted with the harsh truth behind their powers and the ultimate price of their wishes.\n\n[Written by MAL Rewrite]"}, {"id": 49918, "name_en": "My Hero Academia Season 6", "name_jp": "僕のヒーローアカデミア", "image_url": "https://cdn.myanimelist.net/images/anime/1483/126005.jpg", "genres": ["Action"], "themes": ["School", "Super Power"], "demographics": ["Shounen"], "studios": ["Bones"], "year": 2022, "episodes": 25, "source": "Manga", "score": 8.22, "synopsis": "With Tomura Shigaraki at its helm, the former Liberation Army is now known as the Paranormal Liberation Front. This organized criminal group poses an immense threat to the Hero Association, not only because of its sheer size and strength, but also the overpowering quirks of Jin \"Twice\" Bubaigawara and Gigantomachia.\n\nAs new intel from the covert hero Keigo \"Hawks\" Takami confirms that Shigaraki is nowhere to be seen, the Hero Association decides to strike the enemy headquarters with a surprise attack using the entirety of its assets—and the UA students find themselves on the battlefield once again. As the fight rages on, the unsuspecting villains must regroup and push back, but the brave heroes are determined to eradicate every last one of them.\n\n[Written by MAL Rewrite]"}, {"id": 53393, "name_en": "Heavenly Delusion", "name_jp": "天国大魔境", "image_url": "https://cdn.myanimelist.net/images/anime/1121/133132.jpg", "genres": ["Adventure", "Mystery", "Sci-Fi"], "themes": [], "demographics": ["Seinen"], "studios": ["Production I.G"], "year": 2023, "episodes": 13, "source": "Manga", "score": 8.21, "synopsis": "Fifteen years ago, disaster struck human civilization, and now dangerous man-eating monsters roam the ravaged lands, posing an existential threat to the remaining survivors. Amid this turmoil, an isolated facility shelters children and nurtures them in peace. However, as a few among them find out about the world beyond the narrow periphery of their nursery's walls, their curiosity about it slowly grows.\n\nMeanwhile, in the outside world, young survivors Maru and Kiruko band together to search for a special place called Heaven, each for their own reasons. Carrying past burdens and tragic secrets, the two hope to find answers to the cruelty they have experienced in their lives and in the world, which still remains in tatters.\n\n[Written by MAL Rewrite]"}, {"id": 52578, "name_en": "The Dangers in My Heart", "name_jp": "僕の心のヤバイやつ", "image_url": "https://cdn.myanimelist.net/images/anime/1545/133887.jpg", "genres": ["Comedy", "Romance"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["Shin-Ei Animation"], "year": 2023, "episodes": 12, "source": "Manga", "score": 8.21, "synopsis": "Kyoutarou Ichikawa may look like a shy and reserved middle school student, but deep within his heart is a bloodthirsty killer. His ultimate desire is to see his classmate Anna Yamada's beautiful face writhing in pain before he ends her life. But this fantasy may never come to fruition, as Ichikawa starts to see an entirely different side to Yamada.\n\nOften seeking refuge in the library, Ichikawa frequently runs into Yamada. It is during these encounters that Ichikawa realizes his model classmate is actually an airhead who can never read the room. As they spend more time together, the boy cannot help but feel not only a sense of endearment toward the very girl he wishes to murder but also a desire to protect her at all costs. Is it possible that this sudden change in Ichikawa's perspective could lead to something more?\n\n[Written by MAL Rewrite]"}, {"id": 51179, "name_en": "Mushoku Tensei: Jobless Reincarnation Season 2", "name_jp": "無職転生 II ～異世界行ったら本気だす～", "image_url": "https://cdn.myanimelist.net/images/anime/1898/138005.jpg", "genres": ["Adventure", "Drama", "Fantasy", "Ecchi"], "themes": ["Isekai", "Reincarnation"], "demographics": [], "studios": ["Studio Bind"], "year": 2023, "episodes": 12, "source": "Light novel", "score": 8.21, "synopsis": "After his relationship with Eris Boreas Greyrat reaches new heights, Rudeus Greyrat is ecstatic. Unfortunately, his joy is short-lived, as Eris suddenly abandons him to embark on her own journey. Believing that Eris has lost all interest in him, a heartbroken and depressed Rudeus sets forth to the Northern Territories. With his sole goal being to locate his mother on the vast continent, Rudeus wonders if persisting through daily life is worth the pain, falling into a robotic routine as he endlessly ruminates on his lost love.\n\nHowever, the dangers of the North soon prove that one cannot survive with a dulled mind. While on a quest with the party Counter Arrow, with whom he recently became acquainted, Rudeus has a brush with death—an experience that forces him to finally snap out of his despair. With his newfound teammates, Rudeus rediscovers the pleasure of daily adventuring and moves forward with his original goal of living his second lease on life to the fullest.\n\n[Written by MAL Rewrite]"}, {"id": 35110, "name_en": "Haikyu!! The Movie: Talent and Sense", "name_jp": "ハイキュー!! 才能とセンス", "image_url": "https://cdn.myanimelist.net/images/anime/1034/147253.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2017, "episodes": 1, "source": "Manga", "score": 8.21, "synopsis": "Following the events of the Inter-High, the Karasuno High School volleyball team decides to regroup by attending an intensive boot camp organized by Nekoma High School in Tokyo. At the camp, the team continues to suffer loss upon loss to the other schools in attendance. Reaching an impasse, the players each struggle to break out of their routine to hone their skills. \n\nFor the team's setter-and-ace duo Shouyou Hinata and Tobio Kageyama, the frustrations cause them to falter more than anyone else. While Hinata develops his own playstyle, his recklessness leads him to fall out of sync with Kageyama's sets and earns his partner's ire instead. As they search for an answer to their woes, the two find what they are lacking from two unlikely people. Will it be enough for the team to overcome their rut?\n\n[Written by MAL Rewrite]"}, {"id": 12115, "name_en": "Berserk: The Golden Age Arc III - The Advent", "name_jp": "ベルセルク 黄金時代篇Ⅲ 降臨", "image_url": "https://cdn.myanimelist.net/images/anime/12/41305.jpg", "genres": ["Action", "Adventure", "Drama", "Fantasy", "Horror"], "themes": ["Gore", "Military", "Psychological"], "demographics": ["Seinen"], "studios": ["Studio 4°C"], "year": 2013, "episodes": 1, "source": "Manga", "score": 8.21, "synopsis": "The Band of the Hawk has dwindled in the year since Guts left them on his journey to forge his own destiny. Unaware of their fate, Guts returns to the Hawks—now being led by his former ally Casca—after a rumor about them passes his way. Once the saviors of the kingdom of Midland, the Band of the Hawk are now hunted as they desperately fight for their lives while plotting to free their leader, Griffith, after he was imprisoned for committing treason. But the man they save is far from the Griffith they remember.\n\nGriffith is a shell of his former charismatic self after a year of continuous, horrific torture. No longer able to walk, speak, or even hold a sword, he has nothing but the small, strange trinket, the Crimson Behelit, that will not leave him. The entire Band of the Hawk want to rise to greatness once more, but how much are they willing to sacrifice to return to their past glory? It doesn't seem possible, but when Griffith's heart darkens and a solar eclipse blackens the sky, the Behelit offers a choice that will leave the Band of the Hawk with a blood-soaked fate that will haunt them for the rest of their days.\n\n[Written by MAL Rewrite]"}, {"id": 38249, "name_en": "The Disastrous Life of Saiki K. Final Arc", "name_jp": "斉木楠雄のΨ難 完結編", "image_url": "https://cdn.myanimelist.net/images/anime/1159/95661.jpg", "genres": ["Comedy"], "themes": ["Gag Humor", "School", "Super Power"], "demographics": ["Shounen"], "studios": ["J.C.Staff", "Egg Firm"], "year": 2018, "episodes": 1, "source": "Manga", "score": 8.21, "synopsis": "The ground shakes, a rumble follows, and before anyone in Japan can prepare themselves, a cataclysmic volcanic eruption engulfs the country in molten lava and suffocating ashes. Of course, high school student Kusou Saiki repeatedly uses his psychic powers to prevent this harrowing catastrophe.\n\nBut between restoring the planet and watching his friends discuss their aspirations, Saiki finally resolves to stop the eruption and allow the world to continue spinning. However, this straightforward task is challenging even for the most powerful of psychics, let alone one who must deal with the usual shenanigans his ludicrous friends present.\n\n[Written by MAL Rewrite]"}, {"id": 2966, "name_en": "Spice and Wolf", "name_jp": "狼と香辛料", "image_url": "https://cdn.myanimelist.net/images/anime/5/59401.jpg", "genres": ["Adventure", "Drama", "Romance", "Supernatural"], "themes": ["Adult Cast"], "demographics": [], "studios": ["Imagin"], "year": 2008, "episodes": 13, "source": "Light novel", "score": 8.21, "synopsis": "Holo is a powerful wolf deity who is revered in the small town of Pasloe for blessing the annual harvest. Yet as years go by and the villagers become more self-sufficient, Holo has been reduced to a mere folktale. When a traveling merchant named Kraft Lawrence stops at the settlement, Holo offers to become his business partner if he takes her to her northern home of Yoitsu. The savvy trader recognizes Holo's unusual ability to evaluate a person's character and accepts her proposition. \n\nNow in the possession of both sharp bargaining skills and a charismatic negotiator, Lawrence inches closer to his goal of opening his own shop. However, as Lawrence travels the countryside with Holo in search of economic opportunities, he begins to realize that his aspirations are slowly morphing into something unexpected.\n\n[Written by MAL Rewrite]"}, {"id": 35466, "name_en": "ReLIFE: Final Arc", "name_jp": "ReLIFE 完結編", "image_url": "https://cdn.myanimelist.net/images/anime/1566/91061.jpg", "genres": ["Drama", "Romance"], "themes": ["School"], "demographics": [], "studios": ["TMS Entertainment"], "year": 2018, "episodes": 4, "source": "Web manga", "score": 8.21, "synopsis": "After reliving the life of a high school student through the ReLIFE experiment, 27-year-old Arata Kaizaki cannot believe how quickly it has changed him. He has begun to see the world through a different perspective that he had completely forgotten as an adult. He has made friends and formed deep relationships with each one of them. However his support, Ryou Yoake, reminds him that the experiment is all an illusion; after his experiment ends, he will be forgotten by all of them.\n\nThe experiment of another ReLIFE subject is also coming to an end. After spending two years with ReLIFE, Chizuru Hishiro has developed into a more open, more thoughtful person than she could have ever imagined. She has met people who have changed her life, her perspective, and ultimately her. However, now that their ReLIFE is coming to an end, will they be able to let go of the memories they have made?\n\n[Written by MAL Rewrite]"}, {"id": 38680, "name_en": "Fruits Basket 1st Season", "name_jp": "フルーツバスケット", "image_url": "https://cdn.myanimelist.net/images/anime/1447/99827.jpg", "genres": ["Drama", "Romance", "Supernatural"], "themes": ["Love Polygon", "School"], "demographics": ["Shoujo"], "studios": ["TMS Entertainment"], "year": 2019, "episodes": 25, "source": "Manga", "score": 8.21, "synopsis": "Tooru Honda has always been fascinated by the story of the Chinese zodiac that her beloved mother told her as a child. However, a sudden family tragedy changes her life, and subsequent circumstances leave her all alone. Tooru is now forced to live in a tent, but little does she know that her temporary home resides on the private property of the esteemed Souma family. Stumbling upon their home one day, she encounters Shigure, an older Souma cousin, and Yuki, the \"prince\" of her school. Tooru explains that she lives nearby, but the Soumas eventually discover her well-kept secret of being homeless when they see her walking back to her tent one night.\n\nThings start to look up for Tooru as they kindly offer to take her in after hearing about her situation. But soon after, she is caught up in a fight between Yuki and his hot-tempered cousin, Kyou. While trying to stop them, she learns that the Souma family has a well-kept secret of their own: whenever they are hugged by a member of the opposite sex, they transform into the animals of the Chinese zodiac.\n\nWith this new revelation, Tooru will find that living with the Soumas is an unexpected adventure filled with laughter and romance.\n\n[Written by MAL Rewrite]"}, {"id": 1842, "name_en": "Major S3", "name_jp": "メジャー (第3シリーズ)", "image_url": "https://cdn.myanimelist.net/images/anime/9/73971.jpg", "genres": ["Sports"], "themes": ["Team Sports"], "demographics": ["Shounen"], "studios": ["Studio Hibari"], "year": 2007, "episodes": 26, "source": "Manga", "score": 8.21, "synopsis": "Having bid farewell to his former teammates at Kaido High School's baseball club, Gorou Honda returns to his hometown eager to continue playing. Attending a new high school proves to be more difficult than he initially expected, as Tetsufumi Egashira's merciless slandering of his name prevents Gorou from being accepted into any school with an existing baseball team. \n\nLeft with no other options, Gorou joins Seishuu High School, where his childhood friend Kaoru Shimizu also attends. However, Seishuu has always been an all-girls school until two years ago, so there is not yet a dedicated men’s baseball team.\n\nBut nothing will stop Gorou in his pursuit of Koshien. He must now create a baseball team from scratch with the seven male students at Seishuu, but all of them are complete amateurs! He decides to train the incompetent team up to nationally competitive levels, but such a feat is easier said than done. Will Gorou once again perform miracles? Or will he give up on achieving his goal of defeating Kaido with his own team? \n\n[Written by MAL Rewrite]"}, {"id": 39247, "name_en": "Miss Kobayashi's Dragon Maid S", "name_jp": "小林さんちのメイドラゴンS", "image_url": "https://cdn.myanimelist.net/images/anime/1252/115539.jpg", "genres": ["Slice of Life", "Supernatural"], "themes": [], "demographics": [], "studios": ["Kyoto Animation"], "year": 2021, "episodes": 12, "source": "Manga", "score": 8.21, "synopsis": "As Tooru continues on her quest to become the greatest maid and Kanna Kamui fully immerses in her life as an elementary school student, there is not a dull day in the Kobayashi household with mischief being a daily staple. On one such day, however, a massive landslide is spotted on the hill where Kobayashi and Tooru first met—a clear display of a dragon's might. When none of the dragons they know claim responsibility, the perpetrator herself descends from the skies: Ilulu, the radical Chaos Dragon with monstrous power rivaling that of Tooru.\n\nSickened by Tooru's involvement with humans, Ilulu resorts to the typical dragon method of resolving conflict—a battle to the death. Despite such behavior, she becomes intrigued by Kobayashi's ability to befriend dragons and decides instead to observe just what makes Kobayashi so special. With a new troublesome dragon in town, Kobayashi's eccentric life with a dragon maid is only getting merrier.\n\n[Written by MAL Rewrite]"}, {"id": 16664, "name_en": "The Tale of the Princess Kaguya", "name_jp": "かぐや姫の物語", "image_url": "https://cdn.myanimelist.net/images/anime/1935/93606.jpg", "genres": ["Award Winning", "Fantasy"], "themes": ["Historical"], "demographics": [], "studios": ["Studio Ghibli"], "year": 2013, "episodes": 1, "source": "Other", "score": 8.21, "synopsis": "Deep in the countryside, a man named Okina works as a bamboo cutter in a forest, chopping away at the hollow plants day after day. One day, he discovers a small baby inside a glowing shoot. He immediately takes her home, convinced that she is a princess sent to Earth as a divine blessing from heaven. Okina and his wife Ouna take it upon themselves to raise the infant as their own, watching over her as she quickly grows into an energetic young girl. Given the name Kaguya, she fits right in with the village she has come to call home, going on adventures with the other children and enjoying what youth has to offer.\n\nBut when Okina finds a large fortune of gold and treasure in the forest, Kaguya's life is completely changed. Believing this to be yet another gift from heaven, he takes it upon himself to turn his daughter into a real princess using the wealth he has just obtained, relocating the family to a mansion in the capital. As she leaves her friends behind to enter into an unwanted life of royalty, Kaguya's origins and purpose slowly come to light.\n\n[Written by MAL Rewrite]"}, {"id": 372, "name_en": "Cardcaptor Sakura Movie 2: The Sealed Card", "name_jp": "劇場版 カードキャプターさくら 封印されたカード", "image_url": "https://cdn.myanimelist.net/images/anime/1652/90326.jpg", "genres": ["Award Winning", "Comedy", "Drama", "Romance"], "themes": ["Mahou Shoujo"], "demographics": ["Shoujo"], "studios": ["Madhouse"], "year": 2000, "episodes": 1, "source": "Manga", "score": 8.21, "synopsis": "For this year's Nadeshiko Festival, Sakura Kinomoto's elementary school class is presenting a play. She will portray a princess who struggles to respond to the love confession of the neighboring country's prince. Sakura empathizes with her character all too well, since she herself still owes an answer to the boy who confessed his love for her four months ago.\n\nWhen cousins Shaoran and Meiling Li return from Hong Kong to pay a surprise visit to their friends in Japan, Sakura receives further encouragement to finally declare her feelings. However, she is repeatedly distracted by a presence reminiscent of a Clow Card as well as unexplained disappearances around town.\n\nEventually, Sakura learns of another of Clow Reed's creations—the \"Nothing\"—which was formerly sealed away beneath the magician's old house. It has power equal to all 52 cards Sakura possesses, and furthermore, it wants to take those cards away from her! Objects, space, and people disappear from Tomoeda with each card that is stolen. Sakura sets out to capture the Nothing so everything will return to normal, but what must she sacrifice in the process?\n\n[Written by MAL Rewrite]"}, {"id": 60420, "name_en": "The Demon Hunter 2", "name_jp": "沧元图2", "image_url": "https://cdn.myanimelist.net/images/anime/1530/146755.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Shenman Entertainment", "Tianshi Wenhua"], "year": 2024, "episodes": 36, "source": "Web novel", "score": 8.2, "synopsis": "The second season of Cang Yuan Tu."}, {"id": 55866, "name_en": "A Sign of Affection", "name_jp": "ゆびさきと恋々", "image_url": "https://cdn.myanimelist.net/images/anime/1478/140828.jpg", "genres": ["Romance"], "themes": ["Adult Cast"], "demographics": ["Shoujo"], "studios": ["Ajia-do"], "year": 2024, "episodes": 12, "source": "Manga", "score": 8.2, "synopsis": "For hearing-impaired university student Yuki Itose, silence has been a natural part of life since birth. Her world is small and isolated; she commutes to campus, interacts with her best friend Rin Fujishiro, and communicates through writing and text messages—a lifestyle that offers little to no change. One day, during her commute, Yuki meets fellow student Itsuomi Nagi, a multilingual travel enthusiast and friend of Rin. When Itsuomi learns of Yuki's condition, he takes it in stride, moving Yuki's heart. From this one simple gesture, Yuki and Itsuomi's lives start changing day by day as they let each other into their own worlds.\n\n[Written by MAL Rewrite]"}, {"id": 53065, "name_en": "My Dress-Up Darling Season 2", "name_jp": "その着せ替え人形は恋をする Season 2", "image_url": "https://cdn.myanimelist.net/images/anime/1024/150787.jpg", "genres": ["Romance"], "themes": ["Otaku Culture", "School"], "demographics": ["Seinen"], "studios": ["CloverWorks"], "year": 2025, "episodes": 12, "source": "Manga", "score": 8.2, "synopsis": "After Marin Kitagawa introduced Wakana Gojou to the world of cosplay, he has been creating her outfits with ease. Even so, he still has a lot to learn, and every new lesson seems to strengthen his love for sewing and for the hina dolls his grandfather taught him to make.\n\nMeanwhile, Kitagawa finds it harder and harder to hide her feelings for Gojou. Friends and strangers alike constantly mistake them for a couple, much to Gojou's embarrassment. After all, in his eyes, their worlds are far too different for anyone to think they could be romantically linked. However, as Gojou spends more time with Kitagawa and realizes that his passions are not ridiculed by other people, a relationship between the two no longer seems so far-fetched.\n\n[Written by MAL Rewrite]"}, {"id": 52299, "name_en": "Solo Leveling", "name_jp": "俺だけレベルアップな件", "image_url": "https://cdn.myanimelist.net/images/anime/1801/142390.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Adult Cast", "Urban Fantasy"], "demographics": [], "studios": ["A-1 Pictures"], "year": 2024, "episodes": 12, "source": "Web manga", "score": 8.2, "synopsis": "Humanity was caught at a precipice a decade ago when the first gates—portals linked with other dimensions that harbor monsters immune to conventional weaponry—emerged around the world. Alongside the appearance of the gates, various humans were transformed into hunters and bestowed superhuman abilities. Responsible for entering the gates and clearing the dungeons within, many hunters chose to form guilds to secure their livelihoods.\n\nSung Jin-Woo is an E-rank hunter dubbed as the weakest hunter of all mankind. While exploring a supposedly safe dungeon, he and his party encounter an unusual tunnel leading to a deeper area. Enticed by the prospect of treasure, the group presses forward, only to be confronted with horrors beyond their imagination. Miraculously, Jin-Woo survives the incident and soon finds that he now has access to an interface visible only to him. This mysterious system promises him the power he has long dreamed of—but everything comes at a price.\n\n[Written by MAL Rewrite]"}, {"id": 38003, "name_en": "Bungo Stray Dogs 3", "name_jp": "文豪ストレイドッグス 第3期", "image_url": "https://cdn.myanimelist.net/images/anime/1037/100463.jpg", "genres": ["Action", "Mystery"], "themes": ["Adult Cast", "Detective", "Organized Crime", "Super Power"], "demographics": ["Seinen"], "studios": ["Bones"], "year": 2019, "episodes": 12, "source": "Manga", "score": 8.2, "synopsis": "Following the conclusion of the three-way organizational war, government bureaucrat Ango Sakaguchi recalls an event that transpired years ago, after the death of the former Port Mafia boss. Osamu Dazai, still a new recruit at the time, was tasked with investigating rumors related to a mysterious explosion that decimated part of the city years ago—and its connection to the alleged reappearance of the former boss.\n\nDue to circumstances out of his control, he is partnered with Chuuya Nakahara, the gifted yet impulsive leader of a rival clan known as the Sheep, to uncover the truth behind the case and shine a light on the myth of Arahabaki—the god of fire who might just lead Dazai to the case's solution.\n\nMeanwhile, in the present day, it is business as usual once again for the Armed Detective Agency. Their peaceful break will not last for long, however, as enemies old and new gather their strength and prepare for another face-off.\n\n[Written by MAL Rewrite]"}, {"id": 5460, "name_en": "Detective Conan Movie 13: The Raven Chaser", "name_jp": "名探偵コナン漆黒の追跡者(チェイサー)", "image_url": "https://cdn.myanimelist.net/images/anime/2/22558.jpg", "genres": ["Action", "Mystery"], "themes": ["Detective"], "demographics": ["Shounen"], "studios": ["Tokyo Movie Shinsha", "TMS Entertainment"], "year": 2009, "episodes": 1, "source": "Manga", "score": 8.2, "synopsis": "Kudou Shinichi is living his life as Edogawa Conan, but those days seem like they might end pretty soon. The Black Syndicate is coming dangerously close to learning the truth about Shinichi having survived. Conan and everybody around him may end up dead if he doesn't manage to find Irish—a member of the Black Organization who has infiltrated the police forces, currently investigating a big serial murder case."}, {"id": 23847, "name_en": "My Teen Romantic Comedy SNAFU TOO!", "name_jp": "やはり俺の青春ラブコメはまちがっている。続", "image_url": "https://cdn.myanimelist.net/images/anime/11/75376.jpg", "genres": ["Comedy", "Romance"], "themes": ["Love Polygon", "School"], "demographics": [], "studios": ["feel."], "year": 2015, "episodes": 13, "source": "Light novel", "score": 8.2, "synopsis": "With the Volunteer Service Club now firmly established, it is receiving more requests from students in search of solutions to their various issues. However, the club members often struggle to see eye to eye when it comes to their problem-solving ideals. The suggestions that Hachiman Hikigaya recommends frequently clash with those of Yukino Yukinoshita, fracturing the relationships within the club. \n\nDespite their differences, with the trio constantly trying to find common ground, they may soon reach a point where they discover something genuine.\n\n[Written by MAL Rewrite]"}, {"id": 6811, "name_en": "InuYasha: The Final Act", "name_jp": "犬夜叉 完結編", "image_url": "https://cdn.myanimelist.net/images/anime/7/75570.jpg", "genres": ["Action", "Adventure", "Fantasy", "Romance"], "themes": ["Historical", "Love Polygon", "Mythology", "Time Travel"], "demographics": ["Shounen"], "studios": ["Sunrise"], "year": 2009, "episodes": 26, "source": "Manga", "score": 8.2, "synopsis": "Thwarted again by Naraku, Inuyasha, Kagome Higurashi, and their friends must continue their hunt for the few remaining Shikon Jewel shards, lest they fully form into a corrupted jewel at the hands of Naraku. But Naraku has plans of his own to acquire them, and will destroy anyone and anything standing in his way—even his own underlings.\n\nThe persistent, unyielding danger posed by Naraku forces Sango and Miroku to decide what is most important to them—each other or their duty in battle. Meanwhile, Inuyasha must decide whether his heart lies with Kikyou or Kagome, before fate decides for him. Amid the race to find the shards, Inuyasha and his brother Sesshoumaru must also resolve their feud and cooperate for their final confrontation with Naraku, as it is a battle they must win in order to put a stop to his evil and cruelty once and for all.\n\n[Written by MAL Rewrite]"}, {"id": 16894, "name_en": "Kuroko's Basketball 2", "name_jp": "黒子のバスケ", "image_url": "https://cdn.myanimelist.net/images/anime/9/56155.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2013, "episodes": 25, "source": "Manga", "score": 8.2, "synopsis": "With the Interhigh Championship finally over, Seirin's basketball team refocuses their efforts, training harder than ever to get the chance to participate in the Winter Cup. Both Tetsuya Kuroko and Taiga Kagami see old friends walk back into their lives, providing a challenge both on and off the court.\n\nAs new skills are developed and new alliances created, enemies from various teams—giants of high school basketball such as Yousen, Shuutoku, and Touou—stand in the way of Seirin's steadfast attempts to get to the top. All of these schools prove to be formidable foes whose abilities progress exponentially, while Kuroko struggles to find a balance between his resolve to play as part of a team and his desire to win. \n\nWith old wounds reopening, new challenges to face on the court, and a new set of foes—the \"Uncrowned Kings\"—vowing to defeat the new hopefuls, will Seirin ever be able to achieve their dream of beating the Generation of Miracles?\n\n[Written by MAL Rewrite]"}, {"id": 3091, "name_en": "xxxHOLiC◆Kei", "name_jp": "×××HOLiC◆継", "image_url": "https://cdn.myanimelist.net/images/anime/9/8626.jpg", "genres": ["Comedy", "Drama", "Mystery", "Supernatural"], "themes": [], "demographics": ["Seinen"], "studios": ["Production I.G"], "year": 2008, "episodes": 13, "source": "Manga", "score": 8.2, "synopsis": "Kimihiro Watanuki's life was never really normal. But in addition to his ability to see spirits, this sequel to xxxHOLiC finds him still slaving away for Yuuko, the bizarre owner of a strange shop, who promised to rid him of this ability. However, this otherworldly woman can only do so when he has worked enough to earn his wish. Such is the way for anyone who finds their way into the shop to have their request granted: a compensation equal to their wish must be paid. \n\nIn this odd shop that straddles the world of the living and the dead, Watanuki finds himself doing household chores for the seemingly lazy Yuuko and her companions, while also helping out clients. Along with his classmates, supposed romantic rival Shizuka Doumeki and his crush Himawari Kunogi, Watanuki deals with the many misfortunes surrounding Yuuko's customers, as well as those that closely follow him and his friends.\n\n[Written by MAL Rewrite]"}, {"id": 627, "name_en": "Major S1", "name_jp": "メジャー （第1シリーズ）", "image_url": "https://cdn.myanimelist.net/images/anime/13/73973.jpg", "genres": ["Sports"], "themes": ["Team Sports"], "demographics": ["Shounen"], "studios": ["Studio Hibari"], "year": 2004, "episodes": 26, "source": "Manga", "score": 8.2, "synopsis": "Gorou Honda, a little boy obsessed with baseball, has always admired his father, Shigeharu. Wishing to follow in his father's footsteps, Gorou dreams of becoming a professional baseball player. In turn, his son's starry-eyed admiration encourages Shigeharu to keep persevering, despite his late wife's death and his unsatisfying position on the second-string team Blue Ocean.\n\nUnfortunately, an elbow injury forces Shigeharu off the team, and he falls into despair. However, after an offhand joke from his childhood friend, Shigeharu reevaluates his choices and decides to keep playing, leaving behind his prime position as pitcher and taking up the bat. Now motivated more than ever, Gorou works hard to carve his way in the Japanese Little League.\n\n[Written by MAL Rewrite]"}, {"id": 50602, "name_en": "Spy x Family Part 2", "name_jp": "SPY×FAMILY", "image_url": "https://cdn.myanimelist.net/images/anime/1111/127508.jpg", "genres": ["Action", "Comedy"], "themes": ["Childcare", "Super Power"], "demographics": ["Shounen"], "studios": ["Wit Studio", "CloverWorks"], "year": 2022, "episodes": 13, "source": "Manga", "score": 8.2, "synopsis": "With Anya Forger successfully enrolled at the renowned Eden Academy, Operation Strix advances to its second phase. To investigate Ostanian politician Donovan Desmond, Anya must either befriend his son Damian or collect eight Stella Stars to become an Imperial Scholar. Fortunately, Anya has already acquired her first star. In celebration, her adoptive father, Loid, decides to fulfill her wish to adopt a dog.\n\nDuring their canine search, Loid receives new orders from his superiors, who have found that a band of Berlint University students is plotting to assassinate Westalis' Minister Brantz using bombs worn by trained dogs. While Loid tries to stop their plans, Anya stumbles upon the terrorists' base of operations. There, she befriends a kindhearted, clairvoyant dog who the family later names Bond.\n\nAlthough the Forgers continue to lead their individual lives in secrecy, the family—with a new fluffy addition—remains united through all of the unusual obstacles thrown their way.\n\n[Written by MAL Rewrite]"}, {"id": 9863, "name_en": "SKET Dance", "name_jp": "スケットダンス", "image_url": "https://cdn.myanimelist.net/images/anime/9/73974.jpg", "genres": ["Comedy"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["Tatsunoko Production"], "year": 2011, "episodes": 77, "source": "Manga", "score": 8.2, "synopsis": "At Kaimei High School there is a special club dedicated to helping others known as the SKET Brigade. The brains of the group is Kazuyoshi \"Switch\" Usui, a tech-savvy otaku who speaks through speech synthesis software, while the brawn is provided by Hime \"Himeko\" Onizuka, the hockey stick-wielding girl once known as \"Onihime.\"  And last but not least, their leader is Yuusuke \"Bossun\" Fujisaki, whose latent ability is evoked by his goggles, allowing him to summon the awesome power of extraordinary concentration.  \n\nHowever, most of the school only know them as the club that handles odd jobs. Many of their days are spent in the clubroom slacking off, but when there is something to be done, they give their all to help others—usually in sincere, but unintentionally hilarious, ways. The SKET Brigade do all they can to provide support, kindness, encouragement, and troubleshooting to any students crazy enough to ask for their services.  \n\n[Written by MAL Rewrite]"}, {"id": 18115, "name_en": "Magi: The Kingdom of Magic", "name_jp": "マギ The kingdom of magic", "image_url": "https://cdn.myanimelist.net/images/anime/13/55039.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["A-1 Pictures"], "year": 2013, "episodes": 25, "source": "Manga", "score": 8.2, "synopsis": "After celebrating their victory against Al-Thamen, Aladdin and his friends depart the land of Sindria. With the end of the battle, however, comes the time for each of them to go their separate ways. Hakuryuu and Kougyoku are ordered to go back to their home country, the Kou Empire. Meanwhile Aladdin announces he needs to head for Magnostadt—a mysterious country ruled by magicians—to investigate the mysterious events occurring in this new kingdom and become more proficient in magic. For their part, encouraged by Aladdin's words, Alibaba and Morgiana also set off in pursuit of their own goals: training and going to her homeland, respectively.\n\nMagi: The Kingdom of Magic follows these friends as they all go about their separate adventures, each facing their own challenges. However, a new threat begins to rise as a great war looms over the horizon...\n\n[Written by MAL Rewrite]"}, {"id": 36946, "name_en": "Dragon Ball Super: Broly", "name_jp": "ドラゴンボール超（スーパー） ブロリー", "image_url": "https://cdn.myanimelist.net/images/anime/1575/93498.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Martial Arts"], "demographics": ["Shounen"], "studios": ["Toei Animation"], "year": 2018, "episodes": 1, "source": "Manga", "score": 8.2, "synopsis": "Forty-one years ago on Planet Vegeta, home of the infamous Saiyan warrior race, King Vegeta noticed a baby named Broly whose latent power exceeded that of his own son. Believing that Broly's power would one day surpass that of his child, Vegeta, the king sends Broly to the desolate planet Vampa. Broly's father Paragus follows after him, intent on rescuing his son. However, his ship gets damaged, causing the two to spend years trapped on the barren world, unaware of the salvation that would one day come from an unlikely ally.\n\nYears later on Earth, Gokuu Son and Prince Vegeta—believed to be the last survivors of the Saiyan race—are busy training on a remote island. But their sparring is interrupted when the appearance of their old enemy Frieza drives them to search for the last of the wish-granting Dragon Balls on a frozen continent. Once there, Frieza shows off his new allies: Paragus and the now extremely powerful Broly. A legendary battle that shakes the foundation of the world ensues as Gokuu and Vegeta face off against Broly, a warrior without equal whose rage is just waiting to be unleashed.\n\n[Written by MAL Rewrite]"}, {"id": 18617, "name_en": "Girls und Panzer der Film", "name_jp": "ガールズ&パンツァー 劇場版", "image_url": "https://cdn.myanimelist.net/images/anime/1776/91467.jpg", "genres": ["Action", "Award Winning"], "themes": ["CGDCT", "Military", "School"], "demographics": [], "studios": ["Actas"], "year": 2015, "episodes": 1, "source": "Original", "score": 8.19, "synopsis": "Having won the 63rd National Sensha-dou tournament, the girls from Ooarai Girls Academy managed to prevent their school from closure by the Japanese Ministry of Education. However, new troubles loom on the horizon: a ministry official argues that the deal was not legally binding and that the Ooarai school carrier is to be decommissioned. Students and their families are frustrated with this injustice and overwhelmed with despair; it seems little can be done to save the school.\n\nTheir last glimmer of hope is a proposed match against the All-Stars University Sensha-dou team, consisting of the most talented students from all over the country. Despite Ooarai being severely outnumbered and clearly outclassed, they accept the challenge.\n\n[Written by MAL Rewrite]"}, {"id": 37171, "name_en": "Asobi Asobase - workshop of fun -", "name_jp": "あそびあそばせ", "image_url": "https://cdn.myanimelist.net/images/anime/1139/95077.jpg", "genres": ["Comedy"], "themes": ["Gag Humor", "School"], "demographics": ["Seinen"], "studios": ["Lerche"], "year": 2018, "episodes": 12, "source": "Manga", "score": 8.19, "synopsis": "During recess, Olivia, a foreign transfer student who doesn't know English, plays a game of \"look-the-other-way\" with Hanako Honda, a loud-mouthed airhead. Their rowdy behavior spurs the ire of Kasumi Nomura, a deadpan loner constantly teased by her older sister for her tendency to lose games. Not willing to compete, Kasumi declines Olivia's offer to join the fun, but eventually gets involved anyway and dispenses her own brand of mischief. Soon, a strange friendship blossoms between the peculiar trio, and they decide to form the \"Pastime Club,\" where they are free to resume their daily hijinks. \n\nWhether it be failing to learn English, trying desperately to become popular, or getting caught by teachers at the wrong time, school life will never be boring when the girls of Asobi Asobase are up to their hilarious antics.\n\n[Written by MAL Rewrite]"}, {"id": 37078, "name_en": "The King's Avatar (2018)", "name_jp": "全职高手特别篇", "image_url": "https://cdn.myanimelist.net/images/anime/1825/91514.jpg", "genres": ["Action"], "themes": ["Video Game"], "demographics": [], "studios": ["Colored Pencil Animation"], "year": 2018, "episodes": 3, "source": "Web novel", "score": 8.19, "synopsis": "The All-Star Tournament is a three-day event that allows e-sports participants from various professional clubs to display their skills, while also giving casual Glory players the chance to interact with pros in the flesh.\n\nRetired top-tier Glory player Ye Xiu agrees to watch the tournament together with Tang Rou and Chen Guo after the latter manages to get ahold of three tickets. It is a strange sensation for Ye Xiu, as unlike previous occasions, this time he attends the tournament as a spectator, not a participant. However, despite his attempts to keep a low profile, events in the All-Star Tournament may force the former Glory legend back into the limelight.\n\n[Written by MAL Rewrite]"}, {"id": 40435, "name_en": "The Master of Diabolism Q", "name_jp": "魔道祖師Q", "image_url": "https://cdn.myanimelist.net/images/anime/1210/122883.jpg", "genres": ["Comedy", "Fantasy"], "themes": ["Historical", "Mythology", "Reincarnation"], "demographics": [], "studios": ["B.CMAY PICTURES"], "year": 2020, "episodes": 30, "source": "Web novel", "score": 8.19, "synopsis": "The cultivation classes in the Cloud Recesses have started, and Wei Wuxian is as mischievous as ever. From getting in trouble for cheating to tempting the ever-so-cold and stoic Lan Wangji into breaking one of the three thousand strict rules of the Gusu Lan Clan—he's done it all. Although almost everything Wei Wuxian does is against Lan Wangji's principles, an unbreakable bond forms between the two. \n\nThis chibi spin-off is a refreshing compilation of the hilarious and adorable moments of Mo Dao Zu Shi that shows not only how impish Wei Wuxian can be, but also the bashful side of the icy Lan Wangji.\n\n[Written by MAL Rewrite]"}, {"id": 40853, "name_en": "Stand By Me Doraemon 2", "name_jp": "STAND BY ME ドラえもん ２", "image_url": "https://cdn.myanimelist.net/images/anime/1842/109657.jpg", "genres": ["Comedy", "Sci-Fi"], "themes": ["Time Travel"], "demographics": ["Kids"], "studios": ["Shirogumi", "Shin-Ei Animation"], "year": 2020, "episodes": 1, "source": "Manga", "score": 8.19, "synopsis": "After reminiscing about his late grandmother, Nobita Nobi wishes to see her again and asks Doraemon to return them to the past. Following the wholesome reunion of the two, Nobita's grandmother confesses that she hopes to meet his future bride. Nobita accepts her request out of filial love and quickly scrambles into the time machine as he and Doraemon journey forward in time.\n\nHowever, when the duo warps to Nobita's wedding day, they discover that the future Nobita is missing from the ceremony! Determined to set things right, Doraemon and Nobita embark on a mission to search for Nobita's future self and encourage him to discover the happiness he deserves.\n\n[Written by MAL Rewrite]"}, {"id": 32843, "name_en": "Symphogear XV", "name_jp": "戦姫絶唱シンフォギアXV", "image_url": "https://cdn.myanimelist.net/images/anime/1899/101966.jpg", "genres": ["Action", "Sci-Fi"], "themes": ["Idols (Female)", "Music"], "demographics": [], "studios": ["Satelight"], "year": 2019, "episodes": 13, "source": "Original", "score": 8.19, "synopsis": "Humanity is finally confronted with the threat of the Custodians—the ancient, sentient species held responsible for cursing humanity to speak different languages thousands of years ago. The Symphogear wielders—Hibiki Tachibana, Tsubasa Kazanari, Chris Yukine, Maria Cadenzavna Eve, Kirika Akatsuki, and Shirabe Tsukuyomi—are sent to the Antarctic in order to retrieve an ancient relic. After securing it and rescuing the scientific staff present there from a Coffin, the automated defense mechanism protecting it, the relic is given to American researchers due to international agreements.\n\nThe criminal organization Noble Red, a remnant of the previously fought Bavarian Illuminati, starts targeting the relic. Will the Symphogear wielders and their supporting organization S.O.N.G. be able to foil the plans of the organizations conspiring against them?\n\n[Written by MAL Rewrite]"}, {"id": 49574, "name_en": "A Will Eternal 2nd Season", "name_jp": "​一念永恒 第2季", "image_url": "https://cdn.myanimelist.net/images/anime/1902/137109.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["B.CMAY PICTURES"], "year": 2022, "episodes": 54, "source": "Web novel", "score": 8.19, "synopsis": "To avoid a war between the two sects, Bai Xiaochun disguises himself as \"Ye Zang\" and infiltrates the rival sect, Blood Stream Sect. He climbs his way up and obtains the identity of Blood Child. However, how can he avoid the impending war between the two sects...?\n\n(Source: Tencent, translated)"}, {"id": 40052, "name_en": "Great Pretender", "name_jp": "GREAT PRETENDER", "image_url": "https://cdn.myanimelist.net/images/anime/1418/107954.jpg", "genres": ["Action", "Adventure", "Mystery"], "themes": ["Adult Cast", "Organized Crime"], "demographics": [], "studios": ["Wit Studio"], "year": 2020, "episodes": 23, "source": "Original", "score": 8.19, "synopsis": "A series of unfortunate events has led Makoto Edamura to adopt the life of crime—pickpocketing and scamming others for a living. However, after swindling a seemingly clueless tourist, Makoto discovers that he was the one tricked and, to make matters worse, that the police are now after him. \n\nWhile making his escape, he runs into the tourist once again, who turns out to be a fellow con man named Laurent Thierry, and ends up following him to Los Angeles. In an attempt to defend his self-proclaimed title of \"Japan's Greatest Swindler,\" Makoto challenges his rival to determine the better scammer. Accepting the competition, Laurent drops them off outside a huge mansion and claims that their target will be the biggest mafia boss on the West Coast.\n\nAs Makoto becomes increasingly involved with the cunning Laurent, his colorful associates, and the world of international high-stakes fraud, he soon realizes that he got more than what he bargained for as his self-declared skills are continually put to the test.\n\n[Written by MAL Rewrite]"}, {"id": 42897, "name_en": "Horimiya", "name_jp": "ホリミヤ", "image_url": "https://cdn.myanimelist.net/images/anime/1695/111486.jpg", "genres": ["Romance"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["CloverWorks"], "year": 2021, "episodes": 13, "source": "Manga", "score": 8.19, "synopsis": "On the surface, the thought of Kyouko Hori and Izumi Miyamura getting along would be the last thing in people's minds. After all, Hori has a perfect combination of beauty and brains, while Miyamura appears meek and distant to his fellow classmates. However, a fateful meeting between the two lays both of their hidden selves bare. Even though she is popular at school, Hori has little time to socialize with her friends due to housework. On the other hand, Miyamura lives under the noses of his peers, his body bearing secret tattoos and piercings that make him look like a gentle delinquent.\n\nHaving opposite personalities yet sharing odd similarities, the two quickly become friends and often spend time together in Hori's home. As they both emerge from their shells, they share with each other a side of themselves concealed from the outside world.\n\n[Written by MAL Rewrite]"}, {"id": 49114, "name_en": "The Case Study of Vanitas Part 2", "name_jp": "ヴァニタスの手記", "image_url": "https://cdn.myanimelist.net/images/anime/1247/120579.jpg", "genres": ["Action", "Fantasy", "Mystery"], "themes": ["Historical", "Urban Fantasy", "Vampire"], "demographics": ["Shounen"], "studios": ["Bones"], "year": 2022, "episodes": 12, "source": "Manga", "score": 8.19, "synopsis": "Vanitas and Noé Archiviste head out to the town of Gévaudan in search of the \"Beast,\" an enormous wolf-like creature that has slaughtered hundreds of people. Suspecting that the Beast is a curse-bearing vampire, Vanitas primarily aims to heal it using the powers of his grimoire.\n\nAlong the way, the two get separated and suddenly travel back to the past—to the exact moment the Beast is lurking in the woods. After a battle against the gigantic wolf and a vampire hunter, Vanitas decides to team up with Jeanne in order to find Noé. Despite being allies, Jeanne's goal is the opposite of Vanitas', as she was tasked to kill the Beast—suspecting it may be someone she used to know.\n\nMeanwhile, a severely wounded Noé is picked up by the mysterious Chloé d'Apchier and her servant. Like Noé, Chloé is a vampire whose existence was erased from the public's knowledge. She has been a guardian for future generations and once tried to find a way to become human again. While Noé is grateful to Chloé for her hospitality, little does he know that she might be siding with forces far more dangerous than the Beast itself.\n\n[Written by MAL Rewrite]"}, {"id": 44055, "name_en": "Sasaki and Miyano", "name_jp": "佐々木と宮野", "image_url": "https://cdn.myanimelist.net/images/anime/1182/119308.jpg", "genres": ["Boys Love"], "themes": ["Otaku Culture", "School"], "demographics": [], "studios": ["Studio Deen"], "year": 2022, "episodes": 12, "source": "Web manga", "score": 8.19, "synopsis": "Yoshikazu Miyano's troubles first start one hot summer day when Shuumei Sasaki steps into his life. Sasaki saves Miyano's classmate from a group of bullies, and after that, Miyano cannot seem to shake off his eccentric upperclassman. His silent admiration for Sasaki gradually sours into annoyance each time the so-called delinquent refuses to leave him alone. Constantly being called by cute nicknames and having his boundaries ignored, Miyano wonders why Sasaki wants to get close to him.\n\nThe shy and easily flustered Miyano harbors an embarrassing secret—he is a \"fudanshi,\" a boy who likes boys' love (BL) manga. The last thing he wants is for other students to find out, but through a slip of the tongue, he reveals the truth to Sasaki. Intrigued, the clueless Sasaki asks to borrow a book to read, which he is given very reluctantly. To Miyano's surprise, Sasaki enjoys the BL that he receives and asks for more, marking a shift in their strange dynamic.\n\nAlthough Sasaki appears to possess some personal agenda, his feelings for Miyano become complicated the more time they spend together. As they now share a common interest, their relationship is poised to change and further develop.\n\n[Written by MAL Rewrite]"}, {"id": 55772, "name_en": "Golden Kamuy Final Season", "name_jp": "ゴールデンカムイ 最終章", "image_url": "https://cdn.myanimelist.net/images/anime/1288/154763.jpg", "genres": ["Action", "Adventure"], "themes": ["Adult Cast", "Historical", "Military"], "demographics": ["Seinen"], "studios": ["Brain's Base"], "year": 2026, "episodes": 13, "source": "Manga", "score": 8.18, "synopsis": "Final chapter of Golden Kamuy."}, {"id": 55655, "name_en": "The Demon Hunter", "name_jp": "沧元图", "image_url": "https://cdn.myanimelist.net/images/anime/1785/136462.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Shenman Entertainment"], "year": 2023, "episodes": 26, "source": "Web novel", "score": 8.18, "synopsis": "Meng Chuan witnessed his mother killed before his eyes, so he trained very hard hoping to one day avenge her death. But his peaceful days were broken as his wedding engagement was called off, an invasion by foreign forces, the sanctuary fell into enemy hands... In order to protect the people of Ning City, he picked up his sword and vowed to be the strongest. This is a heavy responsibility and a long journey."}, {"id": 36370, "name_en": "Legend of the Galactic Heroes: Die Neue These - Stellar War Part 2", "name_jp": "銀河英雄伝説 Die Neue These 星乱 第2章", "image_url": "https://cdn.myanimelist.net/images/anime/1215/103351.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["Adult Cast", "Military", "Space"], "demographics": [], "studios": ["Production I.G"], "year": 2019, "episodes": 4, "source": "Novel", "score": 8.18, "synopsis": "Marquis Reinhard von Lohengramm's plot to destabilize the Free Planets Alliance succeeds when the treacherous former Rear Admiral Arthur Lynch instigates a coup on Heinessen. Equipped with a plan crafted by Reinhard himself, Lynch encourages his longtime friend Admiral Dwight Greenhill to supervise the National Salvation Military Council's toppling of the civilian government. Seeking to defend democracy and restore the Alliance constitution, Admiral Yang Wen-li faces off against his fellow citizens—and, regrettably, the father of his devoted adjutant Lieutenant Frederica Greenhill.\n\nNow with the Free Planets Alliance thoroughly occupied with their own internal matters, the forces of the Galactic Empire can safely suppress the newly formed Lippstadt League led by Duke Otto von Braunschweig. However, with his friend and loyal subordinate High Admiral Siegfried Kircheis fighting far away in the noble-controlled frontier regions, Reinhard increasingly relies on the advice of the ruthless Vice Admiral Paul von Oberstein, whose influence within the esteemed Lohengramm admiralty steadily grows.\n\nThough bloodshed is inevitable on both sides of the galaxy, Yang Wen-li of the Alliance and Reinhard von Lohengramm of the Empire each ask themselves the same questions: how will history look back on their actions? Will the ends justify the means?\n\n[Written by MAL Rewrite]"}, {"id": 7791, "name_en": "K-ON! Season 2", "name_jp": "けいおん!!", "image_url": "https://cdn.myanimelist.net/images/anime/12/76121.jpg", "genres": ["Award Winning", "Comedy"], "themes": ["CGDCT", "Music", "School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2010, "episodes": 26, "source": "4-koma manga", "score": 8.18, "synopsis": "It is the new year, which means that the senior members of the Light Music Club are now third-years, with Azusa Nakano being the only second-year. The seniors soon realize that Azusa will be the only member left once they graduate and decide to recruit new members. Despite trying many methods of attracting underclassmen—handing out fliers, bringing people into the clubroom, and performing at the welcoming ceremony—there are no signs of anyone that plans to join.\n\nWhile heading to the clubroom, Azusa overhears Yui Hirasawa say that the club is fine with only five people and that they can do many fun things together. Changing her mind, she decides that they do not need to recruit any members for the time being. \n\nK-On!! revolves around the members of the Light Music Club as they experience their daily high school life. From rehearsing for concerts to just messing around, they are ready to make their last year together an exciting one!\n\n[Written by MAL Rewrite]"}, {"id": 22297, "name_en": "Fate/stay night [Unlimited Blade Works]", "name_jp": "Fate/stay night [Unlimited Blade Works]", "image_url": "https://cdn.myanimelist.net/images/anime/12/67333.jpg", "genres": ["Action", "Fantasy"], "themes": ["Urban Fantasy"], "demographics": [], "studios": ["ufotable"], "year": 2014, "episodes": 12, "source": "Visual novel", "score": 8.18, "synopsis": "The Holy Grail War is a battle royale among seven magi who serve as Masters. Masters, through the use of the command seals they are given when they enter the war, command Heroic Spirits known as Servants to fight for them in battle. In the Fifth Holy Grail War, Rin Toosaka is among the magi entering the competition. With her Servant, Archer, she hopes to obtain the ultimate prize—the Holy Grail, a magical artifact capable of granting its wielder any wish.\n \nOne of Rin's classmates, Shirou Emiya, accidentally enters the competition and ends up commanding a Servant of his own known as Saber. As they find themselves facing mutual enemies, Rin and Shirou decide to form a temporary alliance as they challenge their opponents in the Holy Grail War. \n\n[Written by MAL Rewrite]"}, {"id": 37514, "name_en": "Made in Abyss: Journey's Dawn", "name_jp": "劇場版総集編【前編】メイドインアビス 旅立ちの夜明け", "image_url": "https://cdn.myanimelist.net/images/anime/1173/95167.jpg", "genres": ["Adventure", "Drama", "Fantasy", "Mystery", "Sci-Fi"], "themes": ["Gore"], "demographics": [], "studios": ["Kinema Citrus"], "year": 2019, "episodes": 1, "source": "Web manga", "score": 8.18, "synopsis": "The movie is a compilation of episodes 1-8 of the 2017 television series with new scenes added for the introduction. It covers the period from when Riko descends into the Abyss with her robot companion Reg, reaching the second layer where they meet the White Whistle Ozen who reveals information about Riko's mother.\n\n(Source: Wikipedia)"}, {"id": 40729, "name_en": "Megalobox 2: Nomad", "name_jp": "NOMAD メガロボクス2", "image_url": "https://cdn.myanimelist.net/images/anime/1190/113352.jpg", "genres": ["Sci-Fi", "Sports"], "themes": ["Adult Cast", "Combat Sports"], "demographics": [], "studios": ["TMS Entertainment"], "year": 2021, "episodes": 13, "source": "Original", "score": 8.18, "synopsis": "Megalo Box is an advanced form of boxing where competitors wear metal frames called Gear. When the first ever Megalonia tournament took place, \"Gearless\" Joe became its champion and known to all as a legendary fighter. However, soon after, he lost an exhibition match against the second champion and vanished from the public eye.\n\nSeven years later, Joe now goes by \"Nomad\" and keeps a low profile, occasionally fighting in a few underground matches to get by. He is haunted by hallucinations and relies on a set of painkillers to numb his mind.\n\nDuring a match, Joe wins against an opponent, Chief, who purposefully loses for some extra cash from gamblers. Subsequently, Joe discovers that Chief is from a community of immigrants called the Casa. Chief is gradually trying to make enough money to purchase the land where they live illegally. At first, Joe hesitates to get involved, but eventually decides to lend the Casa a helping hand.\n\n[Written by MAL Rewrite]"}, {"id": 3226, "name_en": "Major S4", "name_jp": "メジャー　（第4シリーズ)", "image_url": "https://cdn.myanimelist.net/images/anime/9/75217.jpg", "genres": ["Sports"], "themes": ["Team Sports"], "demographics": ["Shounen"], "studios": ["SynergySP"], "year": 2008, "episodes": 26, "source": "Manga", "score": 8.18, "synopsis": "Having finished high school, Gorou Honda sets his sights on becoming a professional baseball player. His dreams are much more ambitious than becoming a Japanese Baseball League player, so he instead decides to move to the birthplace of his beloved sport, America, in order to play in the Major League.\n\nHowever, Gorou finds that the Major League players are much faster, stronger, and more driven than he is. Nonetheless, he is eager to catch up with them. In order to do so, Gorou must first conquer the ranks of the Minor League, where numerous skilled players compete in the grueling rise to the Majors.\n\nGorou learns that he will have to adapt to the stark differences of American culture and push himself to new extremes as his race to join the Major League begins.\n\n[Written by MAL Rewrite]"}, {"id": 6864, "name_en": "xxxHOLiC Rou", "name_jp": "xxxHOLiC 籠", "image_url": "https://cdn.myanimelist.net/images/anime/9/25080.jpg", "genres": ["Drama", "Mystery", "Supernatural"], "themes": [], "demographics": ["Seinen"], "studios": ["Production I.G"], "year": 2010, "episodes": 2, "source": "Manga", "score": 8.18, "synopsis": "Ten years after the events of xxxHOLiC Shunmuki, a melancholic Kimihiro Watanuki has taken over the shop formerly run by the mysterious Yuuko due to a promise he made to her. His companions Maru, Moro, and Mokona live together with him in relative contentment, and some familiar faces arrive every now and then: former rival turned-steadfast friend Shizuka Doumeki, and the former psychic prodigy Kohane Tsuyuri. While Kohane studies folklore under Doumeki at university, they encounter a case that is perfect for the master of the shop.\n\nEventually, Watanuki receives a visit from Doumeki's grandfather, Haruka. He requests that Watanuki investigates his grandson's dreams. While inside the dream world, he discovers how their past adventures played out from Doumeki's perspective of view, leading Watanuki to discover truths from 10 years ago and secrets about the ties that bind their friendship.\n\n[Written by MAL Rewrite]"}, {"id": 19123, "name_en": "One Piece: Episode of Merry - The Tale of One More Friend", "name_jp": "ワンピース エピソード・オブ・メリー ～もうひとりの仲間の物語～", "image_url": "https://cdn.myanimelist.net/images/anime/9/61015.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Toei Animation"], "year": 2013, "episodes": 1, "source": "Manga", "score": 8.18, "synopsis": "After Brook questions the origin of the Mini Merry name, Usopp and Chopper Tony Tony recall the adventures of the Straw Hat Crew's beloved ship, friend, and crew member—the Going Merry. Usopp abandons the crew for a fight over the Going Merry while Robin Nico leaves with the government's top assassin group, CP9. In order to bring the crew back together, Monkey D. Luffy and the remaining Straw Hats follow Robin to rescue her from the claws of the World Government.\n\n[Written by MAL Rewrite]"}, {"id": 1142, "name_en": "Honey and Clover II", "name_jp": "ハチミツとクローバー II", "image_url": "https://cdn.myanimelist.net/images/anime/6/11040.jpg", "genres": ["Drama", "Romance"], "themes": ["Adult Cast", "Love Polygon", "Visual Arts"], "demographics": ["Josei"], "studios": ["J.C.Staff"], "year": 2006, "episodes": 12, "source": "Manga", "score": 8.18, "synopsis": "Back from his journey across Japan, Yuuta Takemoto reminisces about his college life so far. He has matured significantly since his second year and is motivated to move forward.\n\nFeeling more confident than ever before, he finally confesses to Hagumi Hanamoto, the girl he has been in love with since their first encounter. However, Hagumi has been confused by her attempts to understand the mysterious Shinobu Morita. Hiding behind a playful demeanor, Morita may be the most burdened by his own potential. Meanwhile, Takumi Mayama has become a full-fledged working adult and has landed Ayumi Yamada several pottery orders through his company in an act of friendship.\n\nThe five youths continue to face individual hardships in academics, work, love, and friendship as they push each other toward the paths that they are destined to walk.\n\n[Written by MAL Rewrite]"}, {"id": 138, "name_en": "Hunter x Hunter: Greed Island", "name_jp": "HUNTER×HUNTER Greed Island", "image_url": "https://cdn.myanimelist.net/images/anime/1902/122320.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Nippon Animation"], "year": 2003, "episodes": 8, "source": "Manga", "score": 8.18, "synopsis": "After the battle with the Spiders, the search for Ging continues as Gon and Killua decided to once again attempt to purchase the rare game \"Greed Island.\" However, Gon came up with a better plan, which was to volunteer to complete the game for the billionaire who owned it. But some training must be done, as Gon and Killua's abilities are still at a developing stage and \"Greed Island\" is no simple game for anyone to play with. It is the infamous game that could easily lead to one's death. Based on the manga by Togashi Yoshihiro."}, {"id": 38234, "name_en": "One Piece: Stampede", "name_jp": "劇場版『ONE PIECE STAMPEDE』（スタンピード）", "image_url": "https://cdn.myanimelist.net/images/anime/1221/100550.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Toei Animation"], "year": 2019, "episodes": 1, "source": "Manga", "score": 8.18, "synopsis": "Monkey D. Luffy and the Straw Hats arrive aboard the Sunny to the Pirates Festival, the world's largest celebration created by and for pirates. Buena Festa, the festival organizer, invites the Straw Hats and all Worst Generation crews to partake in the festivities. Luring even Shichibukai and Marines to its shores, it seems that no pirate or sailor can resist the enticing secrets that the event hides behind its glamor.\n\nThe festival's contest is simple: find one of the treasures Gol D. Roger left behind. As the competition progresses, the various pirate crews fight each other in a free-for-all battle royale—that is, until the sudden appearance of an unexpected pirate drastically changes the game.\n\n[Written by MAL Rewrite]"}, {"id": 57433, "name_en": "Rascal Does Not Dream of Santa Claus", "name_jp": "青春ブタ野郎はサンタクロースの夢を見ない", "image_url": "https://cdn.myanimelist.net/images/anime/1823/149858.jpg", "genres": ["Drama", "Romance", "Supernatural"], "themes": ["School"], "demographics": [], "studios": ["CloverWorks"], "year": 2025, "episodes": 13, "source": "Light novel", "score": 8.17, "synopsis": "After surviving the entrance exams, Sakuta Azusagawa has finally enrolled in the same university as his girlfriend, Mai Sakurajima. He is gradually getting used to the unfamiliar environment as he attends student gatherings and makes new acquaintances. Everything seems to be going smoothly until Sakuta's friend and Sweet Bullet idol group member Uzuki Hirokawa begins acting out.\n\nWith fellow Sweet Bullet member Nodoka Toyohama pleading for his help, Sakuta realizes that he has stumbled upon another case of the inexplicable Puberty Syndrome—Uzuki is feeling pressured by the sudden surge of popularity and rumors of a potential solo career. While Sakuta searches for a solution, another occurrence of Puberty Syndrome gradually emerges from the shadows.\n\n[Written by MAL Rewrite]"}, {"id": 57181, "name_en": "Blue Box", "name_jp": "アオのハコ", "image_url": "https://cdn.myanimelist.net/images/anime/1496/147108.jpg", "genres": ["Romance", "Sports"], "themes": ["Love Polygon", "School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Telecom Animation Film"], "year": 2024, "episodes": 25, "source": "Manga", "score": 8.17, "synopsis": "Every morning, incoming first-year Taiki Inomata hurries to his high school gym in order to further refine his badminton skills. However, his true motivation stems from sharing the otherwise empty gym with second-year Chinatsu Kano, Taiki's crush and the star player of the girls' basketball team. Although Chinatsu seems unapproachable, Taiki gradually finds opportunities to get to know her little by little.\n\nUnbeknownst to Taiki, his tireless work ethic and admiration motivate Chinatsu to work harder and strive to achieve her greatest ambitions. When her family must suddenly move overseas for work, Chinatsu decides to remain in Japan and shoot for victory at the national level. With nowhere to stay, she is taken in by Taiki's mother, who is longtime friends with Chinatsu's own. Overwhelmed with the new reality of living alongside the girl he loves, Taiki resolves to join Chinatsu at the national level in his own sport—and grow closer to her in the process.\n\nStill, despite being good enough to catch his coach's eye, Taiki must fight an uphill battle to qualify for a spot on the starting team. Cheered on by both Chinatsu and gymnast Hina Chouno, his childhood friend, Taiki aims to make a name for himself among his powerful upperclassmen.\n\n[Written by MAL Rewrite]"}, {"id": 34537, "name_en": "Night Is Short, Walk on Girl", "name_jp": "夜は短し歩けよ乙女", "image_url": "https://cdn.myanimelist.net/images/anime/2/86940.jpg", "genres": ["Comedy", "Romance"], "themes": ["Adult Cast"], "demographics": [], "studios": ["Science SARU"], "year": 2017, "episodes": 1, "source": "Novel", "score": 8.17, "synopsis": "On a mysterious night that seems to last for a year, an ordinary college student continues to chase one of his underclassmen, a girl with black hair—the girl of his dreams. Up until now, he has been relying on a simple plan, which is to calculatingly bump into her every day while making it seem like a meaningful coincidence. However, his efforts remain futile as their relationship is not progressing at all.\n\nMeanwhile, the black-haired girl believes that everything is connected by fate and endeavors to experience as many new things as possible, leaving it all for destiny to decide. While strolling along the lively streets of Kyoto, she discovers that the very beginning of her fateful journey—a book she had as a child—is currently being sold in a second-hand bookstore. Upon knowing this, the college student eyes another opportunity to run into her \"by chance\": this time, he hopes to get the book before she does and finally grasp the thread of fate that could connect their hearts.\n\n[Written by MAL Rewrite]"}, {"id": 10800, "name_en": "Chihayafuru", "name_jp": "ちはやふる", "image_url": "https://cdn.myanimelist.net/images/anime/3/35749.jpg", "genres": ["Drama", "Romance", "Sports"], "themes": ["Love Polygon", "School", "Strategy Game"], "demographics": ["Josei"], "studios": ["Madhouse"], "year": 2011, "episodes": 25, "source": "Manga", "score": 8.17, "synopsis": "As a child, Chihaya Ayase had only one dream: to see her elder sister Chitose become Japan's most successful model. However, upon defending her ostracised classmate Arata Wataya from his bully—Chihaya's childhood friend Taichi Mashima—she discovers the world of competitive karuta and soon becomes enamoured with the sport.\n\nBased on the Ogura Hundred Poets anthology, this card game where poems are studied requires excellent memory, agility, and a tremendous endurance from the players. Full of hope, Chihaya joins the Shiranami Society together with the newly reconciled Arata and Taichi, embarking on an exciting journey for the title awarded to the top-ranked female player—Queen of Karuta.\n\nSince middle school, Chihaya grew distant from a dispassionate Taichi and separated from Arata. However, in order to improve her skills, Chihaya decides to create a karuta club in her high school. With the help of Taichi, another veteran player, and a few spirited newcomers, Chihaya's new-founded Mizusawa Karuta Club aims for victory in the Omi Shrine's national championship.\n\n[Written by MAL Rewrite]"}, {"id": 32005, "name_en": "Detective Conan Movie 20: The Darkest Nightmare", "name_jp": "劇場版　名探偵コナン　純黒の悪夢（ナイトメア）", "image_url": "https://cdn.myanimelist.net/images/anime/10/78894.jpg", "genres": ["Action", "Mystery"], "themes": ["Detective"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2016, "episodes": 1, "source": "Manga", "score": 8.17, "synopsis": "On a dark night, the Japanese police is raided by a spy. Different countries' intelligence agencies—such as England's MI6, Germany's BND, and America's CIA—as well as the FBI's secret files are going to be taken, but public safety officers lead by Tooru Amuro arrive just in time. The spy steals a car and escapes. The spy and Amuro are then locked in a dead heat on the highway, and just as it is about to cause an accident with multiple cars, the spy's car is hit by FBI agent Shuichi Akai's rifle bullet and falls of the roadway.\nThe next day, Conan and his friends go to a newly-remodeled aquarium in Tokyo. Under the main attraction, a Ferris wheel, Conan finds an attractive woman alone and injured. Her left and right eyes are different colors.\n\nBut the woman is in a state of amnesia where she doesn't even remember her own name, and the cellphone she's carrying is broken. Conan and his friends promise to help her regain her memory, so they stay with her.\n\nThroughout all this, Vermouth is watching behind the scenes. Afterwards, she pulls out a silencer and speaks into an attached intercom, \"It's as planned, Gin.\""}, {"id": 39166, "name_en": "Girls und Panzer das Finale – Part 3", "name_jp": "『ガールズ＆パンツァー 最終章』第3話", "image_url": "https://cdn.myanimelist.net/images/anime/1894/119214.jpg", "genres": [], "themes": ["CGDCT", "Military", "School"], "demographics": [], "studios": ["Actas"], "year": 2021, "episodes": 1, "source": "Original", "score": 8.17, "synopsis": "The quarterfinal round of the Winter Continuous Track Cup is in full swing, and each school is going all out. What started as a furious back-and-forth firefight between Ooarai Girls Academy and Chi-Ha-Tan Academy has become a 12-hour jungle slugfest with neither team having the advantage and almost everyone running on fumes. In spite of this, Miho Nishizumi and her teammates continue to fight into the morning, hoping to land a decisive blow before their rivals.\n\nMeanwhile, Kuromorimine Girls Academy—now led by Erika Itsumi—is struggling in its match against Pravda High School. Taking Maho Nishizumi's words of advice to heart, Erika manages to break free of Kuromorimine's rigid, traditional tactics and turn the tide in her favor. At the same time, St. Gloriana Girls College faces off against Anzio High School, while Saunders University High School has a tough fixture with underdog Continuation High School and its mysterious sharpshooter, Jouko. \n\nWith only four semifinal spots up for grabs, the members of each team must give their absolute best performance if they want to survive and successfully make it to the next round!\n\n[Written by MAL Rewrite]"}, {"id": 33323, "name_en": "Kamisama Hajimemashita: Kamisama, Shiawase ni Naru", "name_jp": "神様はじめました「神様、幸せになる」", "image_url": "https://cdn.myanimelist.net/images/anime/1542/148441.jpg", "genres": ["Comedy", "Romance", "Supernatural"], "themes": ["Mythology"], "demographics": ["Shoujo"], "studios": ["TMS Entertainment"], "year": 2016, "episodes": 1, "source": "Manga", "score": 8.17, "synopsis": "Nanami Momozono, current land god of Mikage Shrine, and her fox familiar Tomoe have faced many obstacles during their time together, but none so challenging as the one posed by the wealth god Ookuninushi—if Tomoe's wish to be human is granted, he must learn to live as one, and Nanami will have to return to being a human.\n\nAs the couple look to the future and reflect on their former adventures, Nanami tries to figure out their new living situation as her high school graduation approaches. But no matter the path they choose to walk, Tomoe and Nanami's love will endure.\n\n[Written by MAL Rewrite]"}, {"id": 4107, "name_en": "Gurren Lagann The Movie: Childhood's End", "name_jp": "劇場版 天元突破グレンラガン 紅蓮篇", "image_url": "https://cdn.myanimelist.net/images/anime/1270/115752.jpg", "genres": ["Sci-Fi"], "themes": ["Mecha"], "demographics": [], "studios": ["Gainax"], "year": 2008, "episodes": 1, "source": "Original", "score": 8.17, "synopsis": "Animation studio Gainax presented a website for the release of a movie adaptation of the Tengen Toppa Gurren Lagann TV series in the fall of 2008. Both the original director and series script editor will return to work on the new project. Gainax will also be hosting four events to celebrate this occasion at this year's Tokyo International Anime Fair, featuring voice actors from the anime."}, {"id": 18195, "name_en": "Little Busters! ~Refrain~", "name_jp": "リトルバスターズ！～Refrain～", "image_url": "https://cdn.myanimelist.net/images/anime/10/55049.jpg", "genres": ["Comedy", "Drama", "Romance"], "themes": ["School"], "demographics": [], "studios": ["J.C.Staff"], "year": 2013, "episodes": 13, "source": "Visual novel", "score": 8.17, "synopsis": "Following the Little Busters after they lost their first baseball game, the team decides to have a pancake party. It has been almost one semester since the return of Kyousuke Natsume. As usual, Riki Naoe continues to help the Little Busters' members—both old and new—with confronting their inner struggles. However, strange happenings begin to occur, leading Rin Natsume and Riki closer to unraveling the truth behind the \"secret of this world.\"\n\nLittle do Rin and Rikki know, their discovery will end up changing the peaceful everyday lives created by the Little Busters once and for all. Little Busters!: Refrain brings a conclusion to the colorful stories of its ensemble cast—forged with the weight of emotions and strengthened with the bond of friendship—as they come to terms with their regrets and weaknesses.\n\n[Written by MAL Rewrite]"}, {"id": 7674, "name_en": "Bakuman.", "name_jp": "バクマン。", "image_url": "https://cdn.myanimelist.net/images/anime/6/26138.jpg", "genres": ["Comedy", "Drama", "Romance"], "themes": ["Otaku Culture"], "demographics": ["Shounen"], "studios": ["J.C.Staff"], "year": 2010, "episodes": 25, "source": "Manga", "score": 8.17, "synopsis": "As a child, Moritaka Mashiro dreamt of becoming a mangaka, just like his childhood hero and uncle, Tarou Kawaguchi, creator of a popular gag manga. But when tragedy strikes, he gives up on his dream and spends his middle school days studying, aiming to become a salaryman instead.\n\nOne day, his classmate Akito Takagi, the school's top student and aspiring writer, notices the detailed drawings in Moritaka's notebook. Seeing the vast potential of his artistic talent, Akito approaches Moritaka, proposing that they become mangaka together. After much convincing, Moritaka realizes that if he is able to create a popular manga series, he may be able to get the girl he has a crush on, Miho Azuki, to take part in the anime adaptation as a voice actor. Thus the pair begins creating manga under the pen name Muto Ashirogi, hoping to become the greatest mangaka in Japan, the likes of which no one has ever seen.\n\n[Written by MAL Rewrite]"}, {"id": 38088, "name_en": "Digimon Adventure: Last Evolution Kizuna", "name_jp": "デジモンアドベンチャー LAST EVOLUTION 絆", "image_url": "https://cdn.myanimelist.net/images/anime/1654/104884.jpg", "genres": ["Action", "Adventure", "Comedy", "Drama"], "themes": [], "demographics": [], "studios": ["Yumeta Company"], "year": 2020, "episodes": 1, "source": "Original", "score": 8.17, "synopsis": "As the Chosen Children and their partner Digimon live happily together in the human world, Taichi Yagami and Yamato Ishida, alongside their friends, dedicate themselves to maintaining this hard-earned peace. Though united by this innate responsibility, each one has already started to take their first steps toward a future beyond being a Chosen Child. \n\nHowever, this new journey is interrupted by the appearance of Menoa Bellucci, an American professor specializing in Digimon research. She bears news of several Chosen Children from around the world being found comatose, with their partner Digimon nowhere to be found. Menoa's investigations indicate that a new breed of Digimon is behind the alarming phenomenon: Eosmon, who hides within the internet's depths. \n\nTo succeed in this mission, the team must endeavor through the growing distance between them and band together one last time.\n\n[Written by MAL Rewrite]"}, {"id": 1519, "name_en": "Black Lagoon: The Second Barrage", "name_jp": "BLACK LAGOON The Second Barrage", "image_url": "https://cdn.myanimelist.net/images/anime/1701/152849.jpg", "genres": ["Action"], "themes": ["Adult Cast", "Organized Crime"], "demographics": ["Seinen"], "studios": ["Madhouse"], "year": 2006, "episodes": 12, "source": "Manga", "score": 8.17, "synopsis": "Rokurou \"Rock\" Okajima has joined the Lagoon Company, a pirate mercenary group which operates out of Roanapur, Thailand. Despite his initial protests, this filthy slum of depraved souls and merciless criminals now serves as the former salaryman's home. Stranded, with nothing left of his past life but the clothes on his back and his inner morality, Rock is forced to perform jobs alongside the other members of the Lagoon crew. Berated for his lack of spine as he wades through the underbelly of society, he must decide whether to continue on amidst the gunfire and ruthlessness or risk everything he has in an attempt to be free. Whether he chooses the comfort of a familiar land or the freedom of being an outlaw, his decision will have lasting consequences on the crew who gave him a home.\n\n[Written by MAL Rewrite]"}, {"id": 40911, "name_en": "Moriarty the Patriot", "name_jp": "憂国のモリアーティ", "image_url": "https://cdn.myanimelist.net/images/anime/1464/108330.jpg", "genres": ["Mystery", "Suspense"], "themes": ["Adult Cast", "Historical", "Organized Crime", "Psychological"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2020, "episodes": 11, "source": "Manga", "score": 8.17, "synopsis": "During the late 19th century, Great Britain has become the greatest empire the world has ever known. Hidden within its success, the nation's rigid economic hierarchy dictates the value of one's life solely on status and wealth. To no surprise, the system favors the aristocracy at the top and renders it impossible for the working class to ascend the ranks.\n\nWilliam James Moriarty, the second son of the Moriarty household, lives as a regular noble while also being a consultant for the common folk to give them a hand and solve their problems. However, deep inside him lies a desire to destroy the current structure that dominates British society and those who benefit from it. \n\nAlongside his brothers Albert and Louis, William will do anything it takes to change the filthy world he lives in—even if blood must be spilled.\n\n[Written by MAL Rewrite]"}, {"id": 558, "name_en": "Major S2", "name_jp": "メジャー (第2シリーズ)", "image_url": "https://cdn.myanimelist.net/images/anime/6/73972.jpg", "genres": ["Sports"], "themes": ["Team Sports"], "demographics": ["Shounen"], "studios": ["Studio Hibari"], "year": 2006, "episodes": 26, "source": "Manga", "score": 8.17, "synopsis": "Gorou Honda has finally returned to Mifune East Junior High School, surprising his friends upon arrival. Now, Gorou is once again surrounded by those he holds closest, and he strives to continue playing and enjoying the game he loves most.\n\nHowever, things do not go as planned, as Gorou is reminded of the harsh realities of baseball as he copes with an injury he sustained while playing baseball at Hitaka Little. Between new rivals, old friends, and mending broken relationships, Gorou must overcome challenges he has never faced before. \n\nHis goal of attending elite baseball high school Kaido may not be far from his reach, but complicated circumstances may stop him in his tracks once again. How will he deal with the immense pressures of the game? And will he ever be able to find the type of baseball he loves most?\n\n[Written by MAL Rewrite]"}, {"id": 49590, "name_en": "The Tatami Time Machine Blues", "name_jp": "四畳半タイムマシンブルース", "image_url": "https://cdn.myanimelist.net/images/anime/1583/126128.jpg", "genres": ["Comedy", "Mystery", "Romance", "Suspense"], "themes": ["Adult Cast", "Psychological", "Time Travel"], "demographics": [], "studios": ["Science SARU"], "year": 2022, "episodes": 6, "source": "Novel", "score": 8.17, "synopsis": "On a hot summer day in Kyoto, a young man walks toward his apartment, dreaming of turning on the air conditioner and cooling down. But before he can turn his dream into reality, disaster naturally strikes in the form of Ozu, who spills soda all over the remote. With the air conditioner rendered completely unusable, the man and his friends are left to suffer in the terrible August heat.\n\nJust when all hope seems lost, the group finds a true-to-life time machine in their storage room. Not knowing the dire consequences of doing so, they decide to travel back to the past and grab the remote before Ozu even has a chance to destroy it. What awaits them on the other side is a staggering adventure none of them are prepared for.\n\n[Written by MAL Rewrite]"}, {"id": 42894, "name_en": "Natsume's Book of Friends: The Waking Rock and the Strange Visitor", "name_jp": "夏目友人帳 石起こしと怪しき来訪者", "image_url": "https://cdn.myanimelist.net/images/anime/1992/111560.jpg", "genres": ["Slice of Life", "Supernatural"], "themes": ["Iyashikei", "Mythology"], "demographics": ["Shoujo"], "studios": ["Shuka"], "year": 2021, "episodes": 1, "source": "Manga", "score": 8.17, "synopsis": "Ishi Okoshi\nWhile searching for Nyanko-sensei, Takashi Natsume encounters meek and small-looking youkai Mitsumi. Mitsumi was entrusted by his leader Gantetsu, a powerful and strong ayakashi, to be the year's \"Ishi Okoshi,\" whose role consists of waking him from his deep slumber. Seeing how much Mitsumi wants to prove himself as Gantetsu's worthy servant, Natsume decides to help him fulfill his task. However, time is running out—for they are not the only ones who want to awaken the divine being.\n\nAyashiki Raihousha\nOne day, Natsume's friend Kaname Tanuma is visited by a strange man. In the subsequent days, the visitor briefly talks with Tanuma and leaves. Natsume soon discovers that the outsider is a youkai disguised as a human, who claims to bear no danger. Despite trusting the youkai, Tanuma's health slowly begins to deteriorate, causing Natsume to become more suspicious of the interloper. What is the truth of his sudden appearance and his connection with Tanuma?\n\n[Written by MAL Rewrite]"}, {"id": 23623, "name_en": "Non Non Biyori Repeat", "name_jp": "のんのんびより りぴーと", "image_url": "https://cdn.myanimelist.net/images/anime/9/75105.jpg", "genres": ["Slice of Life"], "themes": ["CGDCT", "Iyashikei", "School"], "demographics": ["Seinen"], "studios": ["SILVER LINK."], "year": 2015, "episodes": 12, "source": "Manga", "score": 8.17, "synopsis": "Far from the hustle and bustle of urban life, and with only a single candy store and bus route to its name, the rural Asahigaoka is certainly not a place for everyone. Nevertheless, the village's children still manage to cheerfully spend their days exploring and having fun in the wilderness around them. One such child, Renge Miyauchi, the youngest of the group, looks forward to the entrance ceremony of the upcoming school year, signalling her entry into first grade and the beginning of her elementary school life. Attending the only school in town, Renge and her friends, seventh grader Natsumi Koshigaya and her eighth grade sister Komari, make the most out of their rural lifestyle, playing and studying everyday.\n\nMeanwhile, fifth grader Hotaru Ichijou has just moved to Asahigaoka from Tokyo, unaware of the numerous adventures and memories that await her. \n\n[Written by MAL Rewrite]"}, {"id": 18, "name_en": "Initial D Fourth Stage", "name_jp": "頭文字〈イニシャル〉D FOURTH STAGE", "image_url": "https://cdn.myanimelist.net/images/anime/9/10521.jpg", "genres": ["Action", "Drama"], "themes": ["Racing"], "demographics": ["Seinen"], "studios": ["A.C.G.T."], "year": 2004, "episodes": 24, "source": "Manga", "score": 8.17, "synopsis": "Takumi Fujiwara finally joins Ryousuke and Keisuke Takahashi to create \"Project D.\" Their goal is twofold: Ryousuke wants to develop his \"High-Speed Street Racing Theory,\" while Keisuke and Takumi aim at improving their driving skills by facing powerful opponents on dangerous roads. The idea of Project D is to challenge street racing teams from other prefectures to improve both their uphill and downhill records. In order to attract the attention of the best racing teams, Ryousuke creates a dedicated website to announce the future battles of Project D and post the team's results.\n\nThe fourth season of Initial D details the hardships and successes of the members of Project D as they try to become the best street racing team outside of Gunma Prefecture.\n\n[Written by MAL Rewrite]"}, {"id": 34636, "name_en": "Welcome to the Ballroom", "name_jp": "ボールルームへようこそ", "image_url": "https://cdn.myanimelist.net/images/anime/5/86739.jpg", "genres": ["Sports"], "themes": ["Performing Arts"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2017, "episodes": 24, "source": "Manga", "score": 8.17, "synopsis": "Tatara Fujita is a shy middle schooler who has no particular plan for the future. He has gotten through life by avoiding any kind of confrontation and blending in with the crowd. But blending in isn't enough to get out of trouble, as some bullies harass him for money. Luckily, he is saved by a man named Kaname Sengoku.\n\nKaname invites Tatara to his dance studio. Although he would normally never set foot in such a place, Tatara is captivated by Sengoku's commanding presence. Granted an opportunity to dance with fellow classmate Shizuku Hanaoka—who often practices at the studio—Tatara realizes there's something about the idea of being put in the limelight and dancing where people will see him that keeps him coming back. With an earnest, passionate drive to improve, Tatara begins his journey into the world of competitive dance.\n\n[Written by MAL Rewrite]"}, {"id": 59228, "name_en": "Black Butler: Emerald Witch Arc", "name_jp": "黒執事 -緑の魔女編-", "image_url": "https://cdn.myanimelist.net/images/anime/1517/148292.jpg", "genres": ["Action", "Mystery", "Supernatural"], "themes": ["Historical", "Mythology"], "demographics": ["Shounen"], "studios": ["CloverWorks"], "year": 2025, "episodes": 13, "source": "Manga", "score": 8.16, "synopsis": "Queen Victoria of England has ordered her loyal watchdog, 13-year-old Earl Ciel Phantomhive, to investigate a series of suspicious deaths in southern Germany. Across multiple villages, all the victims have one thing in common: they had entered the so-called Werewolves' Forest right before they developed strange deformities and subsequently died. The rumors put blame on a \"witch's curse,\" the nearby villagers' firm belief based on old folklore.\n\nUnconvinced that anything truly supernatural is at play, Ciel and his contracted demonic butler, Sebastian Michaelis—along with the rest of the Phantomhive servants—come across an isolated village in the Werewolves' Forest. Its residents are only women, hostile toward the outsiders and serving a young girl with emerald eyes and bound feet, Sieglinde \"Green Witch\" Sullivan. Ciel and Sebastian investigate to uncover the truth, but as they soon fall victim to the curse themselves, it appears that there is more to the local myths than meets the eye.\n\n[Written by MAL Rewrite]"}, {"id": 55825, "name_en": "Hell's Paradise Season 2", "name_jp": "地獄楽第二期", "image_url": "https://cdn.myanimelist.net/images/anime/1772/154456.jpg", "genres": ["Action", "Adventure", "Supernatural"], "themes": ["Gore", "Historical", "Samurai"], "demographics": ["Shounen"], "studios": ["MAPPA"], "year": 2026, "episodes": 12, "source": "Manga", "score": 8.16, "synopsis": "Second season of Jigokuraku."}, {"id": 53127, "name_en": "Fate/strange Fake: Whispers of Dawn", "name_jp": "Fate/strange Fake -Whispers of Dawn-", "image_url": "https://cdn.myanimelist.net/images/anime/1220/136619.jpg", "genres": ["Action", "Fantasy"], "themes": ["Urban Fantasy"], "demographics": [], "studios": ["A-1 Pictures"], "year": 2023, "episodes": 1, "source": "Light novel", "score": 8.16, "synopsis": "In the American city of Snowfield, a new Holy Grail War is about to erupt. Such ruthless confrontations pit against each other magic experts known as magi, who ascend to the status of Master by summoning Servants—heroic spirits of the past. The winner is thought to have any one of their wishes granted.\n\nHowever, it appears that the organization behind the present situation has merely managed to create an imperfect copy of the Third Holy Grail War. As the unusual ritual catches the attention of the Mage's Association in London, Flatt Escardos, the young apprentice of Lord El-Melloi II—a former participant of the Fourth Holy Grail War—travels to Snowfield and, to the great dismay of his teacher, joins the ranks of the Masters.\n\nIn this fake conflict where the line between Masters and Servants is blurred, the different factions will have a hard time preventing the city of Snowfield from plunging into total chaos.\n\n[Written by MAL Rewrite]"}, {"id": 32867, "name_en": "Bungo Stray Dogs 2", "name_jp": "文豪ストレイドッグス", "image_url": "https://cdn.myanimelist.net/images/anime/1572/133096.jpg", "genres": ["Action", "Mystery"], "themes": ["Adult Cast", "Detective", "Organized Crime", "Super Power"], "demographics": ["Seinen"], "studios": ["Bones"], "year": 2016, "episodes": 12, "source": "Manga", "score": 8.16, "synopsis": "Despite their differences in position, three men—the youngest senior executive of the Port Mafia, Osamu Dazai; the lowest ranking member, Sakunosuke Oda; and the intelligence agent, Ango Sakaguchi—gather at the Lupin Bar at the end of the day to relax and take delight in the company of friends.\n\nHowever, one night, Ango disappears. A photograph taken at the bar is all that is left of the three together.\n\nFast forward to the present, and Dazai is now a member of the Armed Detective Agency. The Guild, an American gifted organization, has entered the fray and is intent on taking the Agency's work permit. They must now divide their attention between the two groups, the Guild and the Port Mafia, who oppose their very existence.   \n\n[Written by MAL Rewrite]"}, {"id": 33674, "name_en": "No Game, No Life: Zero", "name_jp": "ノーゲーム・ノーライフ ゼロ", "image_url": "https://cdn.myanimelist.net/images/anime/1085/90759.jpg", "genres": ["Drama", "Fantasy", "Romance"], "themes": ["Isekai"], "demographics": [], "studios": ["Madhouse"], "year": 2017, "episodes": 1, "source": "Light novel", "score": 8.16, "synopsis": "In ancient Disboard, Riku is an angry, young warrior intent on saving humanity from the warring Exceed, the 16 sentient species, fighting to establish the One True God among the Old Deus. In a lawless land, humanity's lack of magic and weak bodies have made them easy targets for the other Exceed, leaving the humans on the brink of extinction. \n\nOne day, however, hope returns to humanity when Riku finds a powerful female Ex-Machina, whom he names Schwi, in an abandoned elf city. Exiled from her Cluster because of her research into human emotions, Schwi is convinced that humanity has only survived due to the power of these feelings and is determined to understand the human heart. Forming an unlikely partnership in the midst of the overwhelming chaos, Riku and Schwi must now find the answers to their individual shortcomings in each other, and discover for themselves what it truly means to be human as they fight for their lives together against all odds. Each with a powerful new ally in tow, it is now up to them to prevent the extinction of the human race and establish peace throughout Disboard.\n\n[Written by MAL Rewrite]"}, {"id": 962, "name_en": "Aria the Natural", "name_jp": "ARIA The NATURAL", "image_url": "https://cdn.myanimelist.net/images/anime/2/77621.jpg", "genres": ["Sci-Fi", "Slice of Life"], "themes": ["Iyashikei"], "demographics": ["Shounen"], "studios": ["HAL Film Maker"], "year": 2006, "episodes": 26, "source": "Manga", "score": 8.16, "synopsis": "Akari Mizunashi continues her training to become a Prima Undine (a professional tour guide gondolier) along with her friends Aika and Alice in the peaceful city of Neo Venezia. Despite the fact that these three girls are from competing companies, they are constantly together, learning more about how to become better tour guides and more about the mysteries of Neo Venezia.\n\nAs the group continues to meet interesting and unforgettable people through their daily routines, they will also come closer to the secrets that make the enigmatic and ever beautiful city of Neo Venezia so warm and alive.\n\nWelcome back to Neo Venezia: the city where miracles can be created by hand.\n\n[Written by MAL Rewrite]"}, {"id": 853, "name_en": "Ouran High School Host Club", "name_jp": "桜蘭高校ホスト部", "image_url": "https://cdn.myanimelist.net/images/anime/2/71992.jpg", "genres": ["Comedy", "Romance"], "themes": ["Crossdressing", "Reverse Harem", "School"], "demographics": ["Shoujo"], "studios": ["Bones"], "year": 2006, "episodes": 26, "source": "Manga", "score": 8.16, "synopsis": "Haruhi Fujioka is a studious girl who has recently enrolled at the prestigious Ouran Academy. One day, while looking for a quiet and peaceful place to study, she stumbles across a seemingly unused music room. Upon entering, Haruhi is welcomed by the members of the well-known Host Club: a club in which attractive boys amuse girls from across the entire school. However, when Tamaki Suou—the founder and president of the club—startles the bright scholarship student, she accidentally breaks an expensive vase.\n\nWith repayment looking difficult for Haruhi, the Host Club members come up with the perfect solution to the girl's problem: work for the club and ultimately become a Host herself! Mistaken for a boy by her peers, Haruhi has to entertain various female students while coping with her fellow Hosts' extravagant personalities.\n\n[Written by MAL Rewrite]"}, {"id": 1889, "name_en": "Higurashi: When They Cry – Kai", "name_jp": "ひぐらしのなく頃に解", "image_url": "https://cdn.myanimelist.net/images/anime/12/14114.jpg", "genres": ["Horror", "Mystery", "Suspense"], "themes": ["Gore", "Psychological"], "demographics": [], "studios": ["Studio Deen"], "year": 2007, "episodes": 24, "source": "Visual novel", "score": 8.16, "synopsis": "In the small village of Hinamizawa, Rika Furude and her friends live together in relative harmony. Beneath this mask of peace, however, lies a hidden darkness, one that Rika understands all too well. In this eternal summer, she has been witness to unspeakable horrors. Every time the clock resets, Rika must try to find the villain behind these senseless deaths before tragedy strikes again. However, trying to decipher a mystery when only half the clues are present proves time and again to be disastrous, and each time she believes that things will change, she inevitably fails. As the annual festival approaches, Rika begins her descent from hope to despair in her struggle to break the chains of fate entangling them all.\n\n[Written by MAL Rewrite]"}, {"id": 51019, "name_en": "Demon Slayer: Kimetsu no Yaiba Swordsmith Village Arc", "name_jp": "鬼滅の刃 刀鍛冶の里編", "image_url": "https://cdn.myanimelist.net/images/anime/1765/135099.jpg", "genres": ["Action", "Supernatural"], "themes": ["Historical"], "demographics": ["Shounen"], "studios": ["ufotable"], "year": 2023, "episodes": 11, "source": "Manga", "score": 8.16, "synopsis": "For centuries, the Demon Slayer Corps has sacredly kept the location of Swordsmith Village a secret. As the village of the greatest forgers, it provides Demon Slayers with the finest weapons, which allow them to fight night-crawling fiends and ensure the safety of humans. After his sword was chipped and deemed useless, Tanjirou Kamado, along with his precious little sister Nezuko, is escorted to the village to receive a new one.\n\nMeanwhile, the death of an Upper Rank Demon disturbs the idle order in the demon world. As Tanjirou becomes acquainted with Mist Hashira Muichirou Tokitou and Love Hashira Mitsuri Kanroji, ferocious powers creep from the shadows and threaten to shatter the Demon Slayers' greatest line of defense.\n\n[Written by MAL Rewrite]"}, {"id": 59833, "name_en": "KonoSuba: God's Blessing on This Wonderful World! 3 OVA", "name_jp": "この素晴らしい世界に祝福を！３ーBONUS STAGEー", "image_url": "https://cdn.myanimelist.net/images/anime/1482/146928.jpg", "genres": ["Adventure", "Comedy", "Fantasy"], "themes": ["Isekai", "Parody"], "demographics": [], "studios": ["Drive"], "year": 2025, "episodes": 2, "source": "Light novel", "score": 8.15, "synopsis": "Original video anime episodes of Kono Subarashii Sekai ni Shukufuku wo!."}, {"id": 44042, "name_en": "Holo Graffiti", "name_jp": "ホロのぐらふぃてぃ", "image_url": "https://cdn.myanimelist.net/images/anime/1259/110227.jpg", "genres": ["Comedy"], "themes": [], "demographics": [], "studios": ["Hololive Production"], "year": 2019, "episodes": 0, "source": "Other", "score": 8.15, "synopsis": "From unraveling the secrets of opening and closing doors to defusing surprise packages more commonly known as bombs, there is never a dull day at the Hololive Production office! Holo no Graffiti follows an eccentric cast of Virtual YouTubers, also known as \"VTubers,\" going about their absurd yet hilarious daily lives, detailing all their cute moments and mishaps.\n\n[Written by MAL Rewrite]"}, {"id": 25537, "name_en": "Fate/stay night: Heaven's Feel - I. Presage Flower", "name_jp": "劇場版「Fate/stay night [Heaven's Feel] Ⅰ.presage flower」", "image_url": "https://cdn.myanimelist.net/images/anime/1274/102213.jpg", "genres": ["Action", "Fantasy"], "themes": ["Urban Fantasy"], "demographics": [], "studios": ["ufotable"], "year": 2017, "episodes": 1, "source": "Visual novel", "score": 8.15, "synopsis": "The Holy Grail War: a violent battle between mages in which seven masters and their summoned servants fight for the Holy Grail, a magical artifact that can grant the victor any wish. Nearly 10 years ago, the final battle of the Fourth Holy Grail War wreaked havoc on Fuyuki City and took over 500 lives, leaving the city devastated. \n\nShirou Emiya, a survivor of this tragedy, aspires to become a hero of justice like his rescuer and adoptive father, Kiritsugu Emiya. Despite only being a student, Shirou is thrown into the Fifth Holy Grail War when he accidentally sees a battle between servants at school and summons his own servant, Saber. \n\nWhen a mysterious shadow begins a murderous spree in Fuyuki City, Shirou aligns himself with Rin Toosaka, a fellow participant in the Holy Grail War, in order to stop the deaths of countless people. However, Shirou's feelings for his close friend Sakura Matou lead him deeper into the dark secrets surrounding the war and the feuding families involved.\n\n[Written by MAL Rewrite]"}, {"id": 48549, "name_en": "Dr. Stone: New World", "name_jp": "Dr.STONE NEW WORLD", "image_url": "https://cdn.myanimelist.net/images/anime/1316/136268.jpg", "genres": ["Adventure", "Comedy"], "themes": [], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2023, "episodes": 11, "source": "Manga", "score": 8.15, "synopsis": "With the ambitious Ryuusui Nanami on board, Senkuu Ishigami and his team are almost ready to sail the seas and reach the other side of the world—where the bizarre green light that petrified humanity originated. Thanks to the revival of a skillful chef, enough food is being prepared for the entire crew, and the incredible reinvention of the GPS promises to ensure safety on the open sea.\n\nPreparations for the upcoming journey progress swimmingly until Senkuu receives an eerie message from a mysterious source. More driven than ever, the scientist sets out to explore the new world and discover what it can offer for his scientific cause. Though the uncharted territories may hide unkind surprises, Senkuu, with a little help from science, is ready to take on any challenge.\n\n[Written by MAL Rewrite]"}, {"id": 49052, "name_en": "Aoashi", "name_jp": "アオアシ", "image_url": "https://cdn.myanimelist.net/images/anime/1731/120871.jpg", "genres": ["Sports"], "themes": ["Team Sports"], "demographics": ["Seinen"], "studios": ["Production I.G"], "year": 2022, "episodes": 24, "source": "Manga", "score": 8.15, "synopsis": "In a quiet rural town, the spotlight of a local junior high school football team rests on one player: Ashito Aoi. Known for his unpredictable moves and self-centered playing style, Ashito is the sole powerhouse pushing his team through an important high school preliminary tournament. However, their win streak is short-lived—an opponent causes Ashito to lose his temper and act violently, resulting in his removal from the rest of the game.\n\nWithout their star player, the team is quickly eliminated from the tournament. Just as he believes all hope is lost, Ashito is approached by a youth team coach named Tatsuya Fukuda who senses potential in him, and Fukuda invites him for tryouts in Tokyo. In an unfamiliar setting surrounded by talent, Ashito must bring out the best of his ability to prove himself and secure what could be a life-changing career.\n\n[Written by MAL Rewrite]"}, {"id": 49521, "name_en": "Non Non Biyori Nonstop: Our Club Worked Hard", "name_jp": "のんのんびより のんすとっぷ 部活をがんばった", "image_url": "https://cdn.myanimelist.net/images/anime/1984/120788.jpg", "genres": ["Slice of Life"], "themes": ["CGDCT", "Iyashikei"], "demographics": ["Seinen"], "studios": ["SILVER LINK."], "year": 2022, "episodes": 1, "source": "Manga", "score": 8.15, "synopsis": "Beginning her academic journey as a first grader, Shiori comes face to face with the aspect of school she has heard alarming rumors about—homework! However, her worries quickly turn into excitement as the Asahigaoka girls agree to work together to complete their assignments at Komari and Natsumi Koshigaya's house. Their plan appears to be successful until an all-too-familiar feeling of procrastination creates a rowdy distraction for Natsumi.\n\nOn another day, Natsumi asks her teacher Kazuho Miyauchi if she can form and lead a new badminton school club. While the lack of funding and equipment proves to be a major setback, it does not stop Natsumi and the other girls from asking the candy store owner Kaede Kagayama for some help. During their spontaneous badminton practice, a minor accident leads Kaede to reminisce about a memorable day when she babysat Renge Miyauchi.\n\n[Written by MAL Rewrite]"}, {"id": 49909, "name_en": "Kotaro Lives Alone", "name_jp": "コタローは1人暮らし", "image_url": "https://cdn.myanimelist.net/images/anime/1862/121020.jpg", "genres": ["Slice of Life"], "themes": ["Childcare"], "demographics": ["Seinen"], "studios": ["LIDENFILMS"], "year": 2022, "episodes": 10, "source": "Manga", "score": 8.15, "synopsis": "One day, manga author Shin Karino is greeted by four-year-old Kotarou Satou, his new next-door neighbor, who gifts him a box of tissues. In Karino's eyes, Kotarou is an odd kid: he speaks in an extremely formal manner, and he lives alone in his apartment—no parents or relatives in sight. But Kotarou neither seems to mind, nor wants to rely on people.\n\nFeeling sympathetic toward Kotarou's circumstances, Karino decides to follow Kotarou to a bathhouse in case something might happen to him and there he comes to understand the little boy is not so different from him. Little by little, Karino—along with the other residents of the apartment complex—grows fond of Kotarou and his antics. At the same time, Kotarou himself might have found something akin to a family in his unique neighbors.\n\n[Written by MAL Rewrite]"}, {"id": 40852, "name_en": "Dr. Stone: Stone Wars", "name_jp": "ドクターストーン STONE WARS", "image_url": "https://cdn.myanimelist.net/images/anime/1711/110614.jpg", "genres": ["Adventure", "Comedy"], "themes": [], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2021, "episodes": 11, "source": "Manga", "score": 8.15, "synopsis": "Senkuu has made it his goal to bring back two million years of human achievement and revive the entirety of those turned to statues. However, one man stands in his way: Tsukasa Shishiou, who believes that only the fittest of those petrified should be revived.\n\nAs the snow melts and spring approaches, Senkuu and his allies in Ishigami Village finish the preparations for their attack on the Tsukasa Empire. With a reinvented cell phone model now at their disposal, the Kingdom of Science is ready to launch its newest scheme to recruit the sizable numbers of Tsukasa's army to their side. However, it is a race against time; for every day the Kingdom of Science spends perfecting their inventions, the empire rapidly grows in number.\n\nReuniting with old friends and gaining new allies, Senkuu and the Kingdom of Science must stop Tsukasa's forces in order to fulfill their goal of restoring humanity and all its creations. With the two sides each in pursuit of their ideal world, the Stone Wars have now begun!\n\n[Written by MAL Rewrite]"}, {"id": 59897, "name_en": "Miss Kobayashi's Dragon Maid: A Lonely Dragon Wants to be Loved", "name_jp": "小林さんちのメイドラゴン さみしがりやの竜", "image_url": "https://cdn.myanimelist.net/images/anime/1134/148744.jpg", "genres": ["Slice of Life", "Supernatural"], "themes": [], "demographics": [], "studios": ["Kyoto Animation"], "year": 2025, "episodes": 1, "source": "Manga", "score": 8.14, "synopsis": "Miss Kobayashi, Tohru, Kanna, and Iruru's peaceful lives are shattered when Kanna's father, Kamun Kamui, leader of the Chaos Forces, arrives and forces her to return to the Dragon World to fix a past mistake. With a war brewing between Chaos and Harmony, Kanna must find a way to unite the two worlds but is torn between her loyalty to her friends and her father's demands. Miss Kobayashi, however, refuses to let her go and tries to mend Kanna and Kamui's broken bond. As dragons clash, Iruru uncovers a hidden force manipulating the war. Can they stop the war before it consumes both worlds?\n\n(Source: Crunchyroll)"}, {"id": 55911, "name_en": "The Fable", "name_jp": "ザ・ファブル", "image_url": "https://cdn.myanimelist.net/images/anime/1254/141155.jpg", "genres": ["Comedy", "Suspense"], "themes": ["Adult Cast", "Organized Crime"], "demographics": ["Seinen"], "studios": ["Tezuka Productions"], "year": 2024, "episodes": 25, "source": "Manga", "score": 8.14, "synopsis": "Fable, an assassin who works in organized crime, has been raised to the legendary status of a ruthless killer capable of murdering any man within six seconds. Six years after his debut, his boss fears that Fable has started to attract too much attention from the legal authorities, and he orders him to hide in Osaka for a year. Accompanied by his driver who assumes the identity of his sister, Fable is bound by a special condition: he must act as a civilian and not murder anyone lest he and his partner lose their lives.\n\nHowever, the seemingly well-deserved holiday soon takes a grim turn. Takeshi Ebihara, the second-in-command of the Osaka mafia that officially protects the assassin, sees Fable's presence as a threat and wants to remove him. Constantly monitored by the yakuza and regularly put in life-or-death situations, Fable has no other choice but to outwit Takeshi's schemes if he wants to stay alive.\n\n[Written by MAL Rewrite]"}, {"id": 38481, "name_en": "A Certain Scientific Railgun T", "name_jp": "とある科学の超電磁砲[レールガン]T", "image_url": "https://cdn.myanimelist.net/images/anime/1819/103287.jpg", "genres": ["Action", "Fantasy", "Sci-Fi"], "themes": ["Super Power", "Urban Fantasy"], "demographics": [], "studios": ["J.C.Staff"], "year": 2020, "episodes": 25, "source": "Manga", "score": 8.14, "synopsis": "The Daihasei Festival has begun, and that of course means that Tokiwadai Middle School—a prestigious all-girls' middle school—is competing too. Despite the participation of the \"Ace of Tokiwadai,\" Mikoto Misaka, the other students who are participating are still putting their utmost effort into winning, no matter how impossible the feat may seem against her overwhelming might.\n\nHowever, not all is fun and games. Due to the festival, Academy City opens to the outside world, and various factions have begun plotting ways to infiltrate the city. Misaka appears to be on their radar, and as the festival proceeds, people lurking from the shadows begin to emerge...\n\nToaru Kagaku no Railgun T brings back the Tokiwadai Ace and her friends as they dive deeper into the dark side of Academy City. From terrorist attacks to ruthless underground projects, anything is possible in this city.\n\n[Written by MAL Rewrite]"}, {"id": 3701, "name_en": "Kaiba", "name_jp": "カイバ", "image_url": "https://cdn.myanimelist.net/images/anime/1959/147908.jpg", "genres": ["Adventure", "Avant Garde", "Award Winning", "Mystery", "Romance", "Sci-Fi"], "themes": ["Psychological"], "demographics": [], "studios": ["Madhouse"], "year": 2008, "episodes": 12, "source": "Original", "score": 8.14, "synopsis": "In a world where memories exist in memory chips separate from the body, death of the body no longer means death of the soul. It is possible for memories to be viewed, altered, and transferred between bodies. These memory chips are used by the rich to obtain eternal lives in carefully selected bodies, while for the poor, selling their own bodies and conserving their souls in the chips often become the only way to earn a living. An electrolytic cloud in the sky serves as a barrier between the heavens of the fortunate and the underworld of the destitute, making this social division impregnable. \n\nOne day, a man named Kaiba wakes up in an empty room with no memories, a mysterious hole in his chest, and a locket holding the picture of an unknown woman. After escaping an attack and stumbling upon a decrepit village of underworld residents, he begins his adventure across the different planets of this strange universe to find out more about his own identity and the woman he once knew.\n\nThrough a journey of self-discovery and acceptance, Kaiba weaves together tales of souls and spirits and explores the importance of memories.\n\n[Written by MAL Rewrite]"}, {"id": 30503, "name_en": "Noragami Aragoto", "name_jp": "ノラガミ ARAGOTO", "image_url": "https://cdn.myanimelist.net/images/anime/1689/94850.jpg", "genres": ["Action", "Supernatural"], "themes": ["Mythology"], "demographics": ["Shounen"], "studios": ["Bones"], "year": 2015, "episodes": 13, "source": "Manga", "score": 8.14, "synopsis": "Yato and Yukine have finally mended their relationship as god and Regalia. As a minor and unknown deity, Yato continues to take odd jobs for five yen apiece in hopes of one day having millions of worshippers and his own grand shrine. He has yet to fix Hiyori Iki's loose soul, but she cheerily prepares for high school nonetheless.\n\nWhile things are seemingly back to normal, the complicated history between Yato and Bishamon—goddess of war and warriors—resurfaces. Bishamon holds a mysterious old grudge against Yato, which results in violent clashes between them. To further complicate matters, Bishamon's most trusted Regalia, Kazuma, is indebted to Yato. When lives are on the line, unraveling these secrets is possibly the only way to correct mistakes of the past.\n\n[Written by MAL Rewrite]"}, {"id": 721, "name_en": "Princess Tutu", "name_jp": "プリンセスチュチュ", "image_url": "https://cdn.myanimelist.net/images/anime/1145/93646.jpg", "genres": ["Drama", "Mystery", "Romance"], "themes": ["Anthropomorphic", "Love Polygon", "Mahou Shoujo", "Performing Arts"], "demographics": [], "studios": ["HAL Film Maker"], "year": 2002, "episodes": 38, "source": "Original", "score": 8.14, "synopsis": "In a fairy tale come to life, the clumsy, sweet, and gentle Ahiru seems like an unlikely protagonist. In reality, Ahiru is just as magical as the talking cats and crocodiles that inhabit her town—for Ahiru really is a duck! Transformed by the mysterious Drosselmeyer into a human girl, Ahiru soon learns the reason for her existence. Using her magical egg-shaped pendant, Ahiru can transform into Princess Tutu—a beautiful and talented ballet dancer whose dances relieve people of the turmoil in their hearts. With her newfound ability, Ahiru accepts the challenge of collecting the lost shards of her prince's heart, for long ago he had shattered it in order to seal an evil raven away for all eternity.\n\nPrincess Tutu is a tale of heroes and their struggle against fate. Their beliefs, their feelings, and ultimately their actions will determine whether this fairy tale can reach its \"happily ever after.\"\n\n[Written by MAL Rewrite]"}, {"id": 50709, "name_en": "Lycoris Recoil", "name_jp": "リコリス・リコイル", "image_url": "https://cdn.myanimelist.net/images/anime/1261/127311.jpg", "genres": ["Action"], "themes": [], "demographics": [], "studios": ["A-1 Pictures"], "year": 2022, "episodes": 13, "source": "Original", "score": 8.14, "synopsis": "The number of terrorist acts in Japan has never been lower, thanks to the efforts of a syndicate called Direct Attack (DA). The organization raises orphaned girls as killers to carry out assassinations under their \"Lycoris\" program. Takina Inoue is an exceptional Lycoris with a strong sense of purpose and a penchant for perfection. Unfortunately, a hostage situation tests her patience, and the resulting act of insubordination leads to her transfer out of DA. Not thrilled about losing the only place she belonged to, she reluctantly arrives at her new base of operations—LycoReco, a cafe in disguise.\n\nTakina's new partner, however, turns out to be quite different from what she imagined. Despite being the famed Lycoris prodigy, Chisato Nishikigi appears almost unconcerned with her duties. She drags Takina along on all kinds of odd jobs under the simple explanation of helping people in need. Takina is even more puzzled when Chisato takes down a group of armed assailants without killing any of them. Feeling like a fish out of water, Takina itches to get reinstated into DA—but Chisato is determined to prove to her that there is more to a life than just taking them.\n\n[Written by MAL Rewrite]"}, {"id": 34572, "name_en": "Black Clover", "name_jp": "ブラッククローバー", "image_url": "https://cdn.myanimelist.net/images/anime/2/88336.jpg", "genres": ["Action", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Studio Pierrot"], "year": 2017, "episodes": 170, "source": "Manga", "score": 8.14, "synopsis": "Asta and Yuno were abandoned at the same church on the same day. Raised together as children, they came to know of the \"Wizard King\"—a title given to the strongest mage in the kingdom—and promised that they would compete against each other for the position of the next Wizard King. However, as they grew up, the stark difference between them became evident. While Yuno is able to wield magic with amazing power and control, Asta cannot use magic at all and desperately tries to awaken his powers by training physically.\n\nWhen they reach the age of 15, Yuno is bestowed a spectacular Grimoire with a four-leaf clover, while Asta receives nothing. However, soon after, Yuno is attacked by a person named Lebuty, whose main purpose is to obtain Yuno's Grimoire. Asta tries to fight Lebuty, but he is outmatched. Though without hope and on the brink of defeat, he finds the strength to continue when he hears Yuno's voice. Unleashing his inner emotions in a rage, Asta receives a five-leaf clover Grimoire, a \"Black Clover\" giving him enough power to defeat Lebuty. A few days later, the two friends head out into the world, both seeking the same goal—to become the Wizard King!\n\n[Written by MAL Rewrite]"}, {"id": 48736, "name_en": "My Dress-Up Darling", "name_jp": "その着せ替え人形は恋をする", "image_url": "https://cdn.myanimelist.net/images/anime/1179/119897.jpg", "genres": ["Romance"], "themes": ["Otaku Culture", "School"], "demographics": ["Seinen"], "studios": ["CloverWorks"], "year": 2022, "episodes": 12, "source": "Manga", "score": 8.14, "synopsis": "High school student Wakana Gojou spends his days perfecting the art of making hina dolls, hoping to eventually reach his grandfather's level of expertise. While his fellow teenagers busy themselves with pop culture, Gojou finds bliss in sewing clothes for his dolls. Nonetheless, he goes to great lengths to keep his unique hobby a secret, as he believes that he would be ridiculed were it revealed. \n\nEnter Marin Kitagawa, an extraordinarily pretty girl whose confidence and poise are in stark contrast to Gojou's meekness. It would defy common sense for the friendless Gojou to mix with the likes of Kitagawa, who is always surrounded by her peers. However, the unimaginable happens when Kitagawa discovers Gojou's prowess with a sewing machine and brightly confesses to him about her own hobby: cosplay. Because her sewing skills are pitiable, she decides to enlist his help.\n\nAs Gojou and Kitagawa work together on one cosplay outfit after another, they cannot help but grow close—even though their lives are worlds apart.\n\n[Written by MAL Rewrite]"}, {"id": 59654, "name_en": "Jujutsu Kaisen: Hidden Inventory/Premature Death", "name_jp": "呪術廻戦 懐玉・玉折", "image_url": "https://cdn.myanimelist.net/images/anime/1999/147023.jpg", "genres": ["Action", "Supernatural"], "themes": ["Gore", "School"], "demographics": ["Shounen"], "studios": ["MAPPA"], "year": 2025, "episodes": 1, "source": "Manga", "score": 8.13, "synopsis": "A compilation movie of Jujutsu Kaisen's Hidden Inventory and Premature Death arc."}, {"id": 54856, "name_en": "Horimiya: The Missing Pieces", "name_jp": "ホリミヤ -piece-", "image_url": "https://cdn.myanimelist.net/images/anime/1007/136277.jpg", "genres": ["Romance"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["CloverWorks"], "year": 2023, "episodes": 13, "source": "Manga", "score": 8.13, "synopsis": "As the graduation ceremony at Katagiri High School comes to an end, Kyouko Hori, her boyfriend Izumi Miyamura, and their friends begin to look back on their time as students. The moments they shared together may be fleeting, but each one is a colorful piece of their precious memories.\n\n[Written by MAL Rewrite]"}, {"id": 51335, "name_en": "Throne of Seal", "name_jp": "神印王座", "image_url": "https://cdn.myanimelist.net/images/anime/1776/122162.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": [], "studios": ["Shenman Entertainment"], "year": 2022, "episodes": 26, "source": "Web novel", "score": 8.13, "synopsis": "Six thousand years ago, mankind's glorious reign ended as a result of an invasion of the demon race, brought about by the emergence of 72 demon gods. To safeguard the remnants of humanity, the Alliance of Temples appeared to stop the demons' progress and temporarily stabilize the situation.\n\nAt present, nine-year-old Long Hao Chen is training to become a part of the Knight Temple Hall. Through sheer dedication and the support of his companions, he passes the knight exam. Now, under the tutelage of his teacher Long Xing Yu, Long Hao Chen endures the Holy Resurrection Ritual to ascertain his capability. The ritual reveals that Long Hao Chen is a Son of Light—an individual who possesses a vast amount of spiritual energy.\n\nRecognizing his disciple's promising potential, Long Xing Yu urges Long Hao Chen to become a strong knight capable of defeating the Demon Lord alongside him. With countless challenges awaiting him, Long Hao Chen may be the warrior that can end the era of darkness.\n\n[Written by MAL Rewrite]"}, {"id": 9890, "name_en": "Major: Message", "name_jp": "メジャー・メッセージ", "image_url": "https://cdn.myanimelist.net/images/anime/11/75219.jpg", "genres": ["Sports"], "themes": ["Team Sports"], "demographics": ["Shounen"], "studios": ["SynergySP"], "year": 2010, "episodes": 1, "source": "Manga", "score": 8.13, "synopsis": "Thirteen years after achieving major success with the Indiana Hornets, Gorou Honda's career as a pitcher ended abruptly after sustaining a horrible injury. Inspired by his father, who continued playing baseball despite major setbacks, Gorou plans to restart his baseball career and play in a new position. However, his strong dedication to train has puzzled his family, especially his young daughter Izumi, who is frustrated with the absence of her father from her lives. Faced by another tough obstacle, Gorou once again decides to prove his resolve through his beloved sport.\n\n[Written by MAL Rewrite]"}, {"id": 31715, "name_en": "Working!!! Lord of the Takanashi", "name_jp": "WORKING!!! 第14話「ロード・オブ・ザ・小鳥遊」", "image_url": "https://cdn.myanimelist.net/images/anime/10/79375.jpg", "genres": ["Comedy", "Romance"], "themes": ["Love Status Quo", "Workplace"], "demographics": ["Seinen"], "studios": ["A-1 Pictures"], "year": 2016, "episodes": 1, "source": "4-koma manga", "score": 8.13, "synopsis": "The light-hearted Working!! franchise comes to a close in this final episode of the concluding season. Will Aoi Yamada get the closure she so desperately needs with her distant mother? Will the relationship between Yachiyo Todoroki and Jun Satou continue to remain stagnant and awkward? Perhaps even Souta Takanashi and Mahiru Inami will take a step forward in deepening their eccentric friendship. And in the end, Wagnaria Restaurant might finally get a new chief!\n\nBeautifully connected, all these events intertwine to give these characters a final ending to their stories.\n\n[Written by MAL Rewrite]"}, {"id": 37962, "name_en": "IDOLiSH7 Second BEAT!", "name_jp": "アイドリッシュセブン Second BEAT!", "image_url": "https://cdn.myanimelist.net/images/anime/1962/106361.jpg", "genres": ["Drama"], "themes": ["Idols (Male)", "Music"], "demographics": [], "studios": ["TROYCA"], "year": 2020, "episodes": 15, "source": "Game", "score": 8.13, "synopsis": "Following their performance at the Black or White event, the idol group IDOLiSH7 is at the height of its popularity, with offers for various concerts and work coming in every day, much to the joy of the seven boys and their manager. However, as their fame continues to rise, Riku Nanase's health begins worsening due to the heavy workload.\n\nWith the gigantic idol duo Re:vale showing interest in the group and the reopening event of the Zero Arena coming up, IDOLiSH7: Second Beat follows IDOLiSH7 as they not only struggle to adapt to the challenges of the idol life but also to face their personal troubles, all while continuously meeting the expectations of their treasured fans.\n\n[Written by MAL Rewrite]"}, {"id": 28675, "name_en": "Beyond the Boundary: I'll Be Here - Future", "name_jp": "劇場版 境界の彼方 I'LL BE HERE 未来篇", "image_url": "https://cdn.myanimelist.net/images/anime/9/72614.jpg", "genres": ["Action", "Romance", "Supernatural"], "themes": ["School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2015, "episodes": 1, "source": "Light novel", "score": 8.13, "synopsis": "After Akihito Kanbara reunites with Mirai Kuriyama—whom he believed had vanished after defeating Beyond the Boundary—he discovers a heartbreaking fact: Mirai has lost all memory of him, their friends, and her past as a Spirit Warrior. Akihito is utterly devastated, but realizes that she has a unique opportunity. Mirai can finally live the life of a normal girl—where she'll be completely devoid of the supernatural society that both shunned and used her. While it's all for the sake of Mirai's happiness, the price is costly—Akihito and his friends must keep her true origins a secret from her, and as a result avoid befriending her. \n\nHowever, the troubling memories of Mirai's old life gradually begin to resurface, and a mysterious new evil leads a group of shadow-like creatures into the city with the goal of seeking her out. As the situations become dire, Akihito must fight to protect himself, his closest friends, and Mirai—the bespectacled beauty he holds most dear. \n\n[Written by MAL Rewrite]"}, {"id": 39592, "name_en": "Mobile Suit Gundam: The Origin - Advent of the Red Comet", "name_jp": "機動戦士ガンダム THE ORIGIN 前夜 赤い彗星", "image_url": "https://cdn.myanimelist.net/images/anime/1897/100015.jpg", "genres": ["Sci-Fi"], "themes": ["Mecha", "Military", "Space"], "demographics": ["Shounen"], "studios": ["Sunrise"], "year": 2019, "episodes": 13, "source": "Manga", "score": 8.13, "synopsis": "What was the tragedy that decided the fates of Char Aznable, the man later nicknamed the \"Red Comet\" as an ace pilot of the Zeon forces, and his sister Sayla Mass?\n\nThe two siblings' journeys, brought on by the sudden death of their father Zeon Zum Deikun who was a leader of the Spacenoids, are depicted in the four episodes of \"Chronicle of Char and Sayla.\"\n\nThe Zabi family who seize control of Side 3 and lead the Principality of Zeon, the early days of many renowned Zeon ace pilots who later fight in the One Year War, the secrets of mobile suit development, conflicts with the Earth Federation Forces, and the road leading to the outbreak of war—all will be revealed.\n\n(Source: Official Website)"}, {"id": 39792, "name_en": "Keep Your Hands Off Eizouken!", "name_jp": "映像研には手を出すな!", "image_url": "https://cdn.myanimelist.net/images/anime/1680/110451.jpg", "genres": ["Adventure", "Award Winning", "Comedy"], "themes": ["Otaku Culture", "School"], "demographics": ["Seinen"], "studios": ["Science SARU"], "year": 2020, "episodes": 12, "source": "Manga", "score": 8.13, "synopsis": "Midori Asakusa sees the world a bit differently. Always having her nose in a sketchbook, Asakusa draws detailed landscapes and backgrounds of both the world around her and the one within her boundless imagination. Even the simple act of doodling on a wall evolves into an emergency repair on the outer hull of her spaceship. She is only brought back to reality by her best friend Sayaka Kanamori. The pair are stark opposites, with Asakusa's childlike wonder contrasted by Kanamori's calculated approach to life. \n\nAfter a chance encounter where the two \"save\" the young model Tsubame Misuzaki from her overprotective bodyguard, a connection instantly sparks between Asakusa and Misuzaki, as both share an intense passion for art and animation. Whereas Asakusa is interested in backgrounds and settings, Misuzaki loves drawing the human form. Sensing a money-making opportunity, Kanamori suggests that they start an animation club, which they disguise as a motion picture club since the school already has an anime club. Thus begins the trio's journey of producing animation that will awe the world. \n\nFrom the brilliant mind of Masaaki Yuasa, Eizouken ni wa Te wo Dasu na! is a love letter to animation, wildly creative in its approach, and a testament to the potential of the medium.\n\n[Written by MAL Rewrite]"}, {"id": 4477, "name_en": "Nodame Cantabile: Paris-hen", "name_jp": "のだめカンタービレ 巴里編", "image_url": "https://cdn.myanimelist.net/images/anime/1896/147906.jpg", "genres": ["Comedy", "Romance"], "themes": ["Adult Cast", "Music", "Performing Arts"], "demographics": ["Josei"], "studios": ["J.C.Staff"], "year": 2008, "episodes": 11, "source": "Manga", "score": 8.13, "synopsis": "Shinichi Chiaki conquers his fear of flying, and Megumi \"Nodame\" Noda's exceptional performance at a piano competition earns her an invitation to study at the prestigious Conservatoire de Paris. The pair go to Paris together to take the next step in their careers: Chiaki as a new rising conductor under the wing of the great maestro Franz von Stresemann, and Nodame as a pupil of the esteemed piano professor Charles Auclair.\n\nBut, of course, the music world is much bigger than the two of them could have ever imagined. Chiaki and Nodame, alongside old friends and new rivals, must fight and persevere to reach the dazzling musical heights that await them while never losing sight of what matters most.\n\n[Written by MAL Rewrite]"}, {"id": 16662, "name_en": "The Wind Rises", "name_jp": "風立ちぬ", "image_url": "https://cdn.myanimelist.net/images/anime/8/52353.jpg", "genres": ["Award Winning", "Drama", "Romance"], "themes": ["Historical"], "demographics": [], "studios": ["Studio Ghibli"], "year": 2013, "episodes": 1, "source": "Manga", "score": 8.13, "synopsis": "Although Jirou Horikoshi's nearsightedness prevents him from ever becoming a pilot, he leaves his hometown to study aeronautical engineering at Tokyo Imperial University for one simple purpose: to design and build planes just like his hero, Italian aircraft pioneer Giovanni Battista Caproni. His arrival in the capital coincides with the Great Kanto Earthquake of 1923, during which he saves a maid serving the family of a young girl named Naoko Satomi; this disastrous event marks the beginning of over two decades of social unrest and malaise leading up to Japan's eventual surrender in World War II.\n\nFor Jirou, the years leading up to the production of his infamous Mitsubishi A6M Zero fighter aircraft will test every fiber of his being. His many travels and life experiences only urge him onward⁠—even as he realizes both the role of his creations in the war and the harsh realities of his personal life. As time marches on, he must confront an impossible question: at what cost does he chase his beautiful dream?\n\n[Written by MAL Rewrite]"}, {"id": 41361, "name_en": "Pompo: The Cinéphile", "name_jp": "映画大好きポンポさん", "image_url": "https://cdn.myanimelist.net/images/anime/1210/119193.jpg", "genres": ["Comedy", "Drama"], "themes": ["Showbiz"], "demographics": [], "studios": ["CLAP"], "year": 2021, "episodes": 1, "source": "Web manga", "score": 8.13, "synopsis": "Joelle Davidovich Pomponette, better known as simply \"Pompo,\" is an extremely talented movie producer, having inherited her grandfather's connections and cinematic eye. Despite her promising outlook, Pompo refuses to produce anything other than trashy B-movies. That is, until she hands her assistant, Gene Fini, a script for an ambitious screenplay about the life of a composer and announces that he has earned his first directorial credit.\n\nDetermined to make the film shine, Gene throws himself completely into his work. However, with an overwhelming workload and a deadline only days away, the aspiring director struggles with what must be sacrificed in the name of creation.\n\n[Written by MAL Rewrite]"}, {"id": 731, "name_en": "Interstella5555: The 5tory of The 5ecret 5tar 5ystem", "name_jp": "インターステラ5555", "image_url": "https://cdn.myanimelist.net/images/anime/1618/146985.jpg", "genres": ["Adventure", "Drama", "Sci-Fi"], "themes": ["Adult Cast", "Music"], "demographics": [], "studios": ["Toei Animation"], "year": 2003, "episodes": 1, "source": "Music", "score": 8.13, "synopsis": "This musical film is the visual realization of \"Discovery\", an album by Daft Punk. It tells the story of a band of four extraterrestrial musicians who are kidnapped and brought back to Earth by an evil manager. Their only hopes lie with a space pilot from their home planet who, after receiving a distress call broadcasted during the abduction, seeks out to rescue them."}, {"id": 49596, "name_en": "Blue Lock", "name_jp": "ブルーロック", "image_url": "https://cdn.myanimelist.net/images/anime/1258/126929.jpg", "genres": ["Sports"], "themes": ["Team Sports"], "demographics": ["Shounen"], "studios": ["8bit"], "year": 2022, "episodes": 24, "source": "Manga", "score": 8.13, "synopsis": "Yoichi Isagi was mere moments away from scoring a goal that would have sent his high school soccer team to the nationals, but a split-second decision to pass the ball to his teammate cost him that reality. Bitter, confused, and disappointed, Isagi wonders if the outcome would have been different had he not made the pass. When the young striker returns home, an invitation from the Japan Football Union awaits him. Through an arbitrary and biased decision-making process, Isagi is one of three hundred U-18 strikers selected for a controversial project named Blue Lock. \n\nThe project's ultimate goal is to turn one of the selected players into the star striker for the Japanese national team. To find the best participant, each diamond in the rough must compete against others through a series of solo and team competitions to rise to the top. Putting aside his ethical objections to the project, Isagi feels compelled to fight his way to the top, even if it means ruthlessly crushing the dreams of 299 aspiring young strikers.\n\n[Written by MAL Rewrite]"}, {"id": 28223, "name_en": "Death Parade", "name_jp": "デス・パレード", "image_url": "https://cdn.myanimelist.net/images/anime/5/71553.jpg", "genres": ["Drama", "Fantasy", "Suspense"], "themes": ["Adult Cast", "High Stakes Game", "Psychological"], "demographics": [], "studios": ["Madhouse"], "year": 2015, "episodes": 12, "source": "Original", "score": 8.13, "synopsis": "After death, either Heaven or Hell awaits most humans. But for a select few, death brings them to Quindecim—a bar where only pairs of people who die at the same time can enter. Attending the bar is an enigmatic figure known as Decim, who also acts as the arbiter. He passes judgment on those who wind up at Quindecim by challenging them to a life-threatening game. These games determine if the patron's soul will reincarnate into a new life, or be sent into the void, never to be seen again.\n\nFrom darts and bowling to fighting games, the true nature of each patron slowly comes to light as they wager their souls. Though his methods remain unchanged, the sudden appearance of a black-haired amnesiac causes Decim to reevaluate his own rulings.\n\n[Written by MAL Rewrite]"}, {"id": 50325, "name_en": "Kaguya-sama: Love is War - Ultra Romantic \"Yu Ishigami Wants to Chat\"", "name_jp": "第３期『かぐや様は告らせたい-ウルトラロマンティック-』 / ティザーPV 「石上優は語りたい」", "image_url": "https://cdn.myanimelist.net/images/anime/1265/119435.jpg", "genres": ["Comedy"], "themes": ["School"], "demographics": ["Seinen"], "studios": ["A-1 Pictures"], "year": 2021, "episodes": 1, "source": "Manga", "score": 8.13, "synopsis": "Every Thursday, Shuchiin Academy student council secretary Yuu Ishigami can be found in a state of anxiety. He counts down the hours until he can escape to the student council chamber to read the latest chapter of the popular seinen manga series, \"Momo Doesn't Think.\" Joining Ishigami in his weekly reading, student council president Miyuki Shirogane swiftly grows excited at the news of a third season of the manga's anime adaptation.\n\nHowever, it is only a matter of time before vice president Kaguya Shinomiya discovers what has been engrossing the two boys. With the stigma of being labeled an otaku looming over his head, Shirogane must do everything he can to keep his embarrassing secret from coming to light.\n\n[Written by MAL Rewrite]"}, {"id": 50528, "name_en": "Golden Kamuy Season 4", "name_jp": "ゴールデンカムイ", "image_url": "https://cdn.myanimelist.net/images/anime/1855/128059.jpg", "genres": ["Action", "Adventure"], "themes": ["Adult Cast", "Historical", "Military"], "demographics": ["Seinen"], "studios": ["Brain's Base"], "year": 2022, "episodes": 13, "source": "Manga", "score": 8.13, "synopsis": "The race to obtain the lost Ainu gold is nearing its peak, as even more formidable ex-prisoners are set on keeping their tattooed skins intact. Saichi \"The Immortal\" Sugimoto and Asirpa can finally move forward together, but Asirpa's time with Kiroranke and Ogata Hyakunosuke has slightly altered her ambition and strengthened her determination. As their party leaves the Russian territory, they gain a peculiar follower.\n\nMeanwhile, Toshizou Hijikata's crew makes major moves to get invaluable skins, falling into the poisonous trap of a dangerous convict. Although each group possesses a strong advantage over the others, even First Lieutenant Tokushirou Tsurumi—the most powerful and ruthless contender—cannot advance without Asirpa's help. However, his ominous plans for when they will inevitably meet remain a mystery.\n\n[Written by MAL Rewrite]"}, {"id": 49053, "name_en": "given - on the other hand", "name_jp": "ギヴン うらがわの存在", "image_url": "https://cdn.myanimelist.net/images/anime/1031/115154.jpg", "genres": ["Boys Love", "Drama"], "themes": ["Music", "School"], "demographics": [], "studios": ["Lerche"], "year": 2021, "episodes": 1, "source": "Manga", "score": 8.13, "synopsis": "Since the night of \"given's\" first live performance, the relationship between Mafuyu Satou and Ritsuka Uenoyama has been smooth sailing. Their shared love of music reinforces their bond, and every moment spent together makes them more inseparable. Nevertheless, there will come times when they will suffer setbacks, with both of them consumed by their inner turmoil.\n\n[Written by MAL Rewrite]"}, {"id": 37430, "name_en": "That Time I Got Reincarnated as a Slime", "name_jp": "転生したらスライムだった件", "image_url": "https://cdn.myanimelist.net/images/anime/1069/123309.jpg", "genres": ["Action", "Comedy", "Fantasy"], "themes": ["Isekai", "Reincarnation"], "demographics": ["Shounen"], "studios": ["8bit"], "year": 2018, "episodes": 24, "source": "Manga", "score": 8.13, "synopsis": "Thirty-seven-year-old Satoru Mikami is a typical corporate worker, who is perfectly content with his monotonous lifestyle in Tokyo, other than failing to nail down a girlfriend even once throughout his life. In the midst of a casual encounter with his colleague, he falls victim to a random assailant on the streets and is stabbed. However, while succumbing to his injuries, a peculiar voice echoes in his mind, and recites a bunch of commands which the dying man cannot make sense of.\n\nWhen Satoru regains consciousness, he discovers that he has reincarnated as a goop of slime in an unfamiliar realm. In doing so, he acquires newfound skills—notably, the power to devour anything and mimic its appearance and abilities. He then stumbles upon the sealed Catastrophe-level monster \"Storm Dragon\" Veldora who had been sealed away for the past 300 years for devastating a town to ashes. Sympathetic to his predicament, Satoru befriends him, promising to assist in destroying the seal. In return, Veldora bestows upon him the name Rimuru Tempest to grant him divine protection. \n\nNow, liberated from the mundanities of his past life, Rimuru embarks on a fresh journey with a distinct goal in mind. As he grows accustomed to his new physique, his gooey antics ripple throughout the world, gradually altering his fate.\n\n[Written by MAL Rewrite]"}, {"id": 56738, "name_en": "The Duke of Death and His Maid Season 3", "name_jp": "死神坊ちゃんと黒メイド 第3期", "image_url": "https://cdn.myanimelist.net/images/anime/1988/142714.jpg", "genres": ["Comedy", "Fantasy", "Romance"], "themes": ["Love Status Quo"], "demographics": [], "studios": ["J.C.Staff"], "year": 2024, "episodes": 12, "source": "Web manga", "score": 8.12, "synopsis": "Although everything the young duke touches perishes, his existence is far from lonely. His spirited siblings Viola and Walter, loyal butler Rob, and the eccentric witches Cuff and Zain are regularly livening up his secluded mansion. But it is the duke's flirty maid, Alice Lendrott, who fills him with the promise of a beautiful tomorrow and the determination to break this hellish spell cast upon him.\n\nThe identity of the witch who cursed the duke has been uncovered thanks to Zain's unique magic and the help of Daleth, the leader of the underworld. However, the villain is a notoriously fearsome and powerful witch, who, enraged by the duke and his companions' trip back in time, torments them even from the afterlife. Moreover, an unexpected loved one reappears, strengthening the duke and Alice's hope to break the curse and finally close the distance between them.\n\n[Written by MAL Rewrite]"}, {"id": 54789, "name_en": "My Hero Academia Season 7", "name_jp": "僕のヒーローアカデミア 第7期", "image_url": "https://cdn.myanimelist.net/images/anime/1529/140306.jpg", "genres": ["Action"], "themes": ["School", "Super Power"], "demographics": ["Shounen"], "studios": ["Bones"], "year": 2024, "episodes": 21, "source": "Manga", "score": 8.12, "synopsis": "Following an all-out battle with the Paranormal Liberation Front, it is difficult for the people of Japan to continue placing faith in their heroes. To combat the combined power of Tomura Shigaraki and All For One, All Might calls for his ally from the West—the strongest woman on the planet, Star and Stripe.\n\nHowever, All For One decides to intercept Star and her fleet to get his hands on her overpowered quirk before she can enter Japanese airspace. Although Endeavor, Hawks, and Best Jeanist are headed to the rendezvous point, Star makes a gamble in the present to save her comrades.\n\n[Written by MAL Rewrite]"}, {"id": 25313, "name_en": "Gintama': Futon ni Haitte kara Buki Nokoshi ni Kizuite Neru ni Nerenai Toki mo Aru", "name_jp": "銀魂 〜布団に入ってから拭き残しに気付いて寝るに寝れない時もある〜", "image_url": "https://cdn.myanimelist.net/images/anime/1327/110925.jpg", "genres": ["Action", "Comedy", "Sci-Fi"], "themes": ["Gag Humor", "Historical", "Parody", "Samurai"], "demographics": ["Shounen"], "studios": ["Sunrise"], "year": 2015, "episodes": 1, "source": "Manga", "score": 8.12, "synopsis": "Bundled with limited edition of the 58th Gintama manga volume.\n\nThe tagline for the bundled anime reads, \"It's time for all the Yorozuya members ...to wake up just one more time.\"\n\n(Source: MAL News, edited)"}, {"id": 35857, "name_en": "Lupin the Third Part 5", "name_jp": "ルパン三世 PART5", "image_url": "https://cdn.myanimelist.net/images/anime/1235/91028.jpg", "genres": ["Action", "Adventure", "Comedy", "Mystery"], "themes": ["Adult Cast"], "demographics": ["Seinen"], "studios": ["Telecom Animation Film"], "year": 2018, "episodes": 24, "source": "Manga", "score": 8.12, "synopsis": "Genius thief Arsene Lupin III—along with the usual crew of Goemon Ishikawa XIII, Fujiko Mine, and Daisuke Jigen—finds himself in modern-day France, where he encounters both new and old adversaries, with Inspector Kouichi Zenigata still hot on his trail. As they steal from darker, more sinister entities, they will also have to find a way to deal with the newest technology in their escapades, as well as face the ghosts of their pasts. However, this time, Lupin's choices begin to catch up with him as his pursuers use every tool at their disposal to take him down once and for all.\n\n[Written by MAL Rewrite]"}, {"id": 14713, "name_en": "Kamisama Kiss", "name_jp": "神様はじめました", "image_url": "https://cdn.myanimelist.net/images/anime/3/85429.jpg", "genres": ["Comedy", "Romance", "Supernatural"], "themes": ["Mythology"], "demographics": ["Shoujo"], "studios": ["TMS Entertainment"], "year": 2012, "episodes": 13, "source": "Manga", "score": 8.12, "synopsis": "High schooler Nanami Momozono has quite a few problems of late, beginning with her absentee father being in such extreme debt that they lose everything. Downtrodden and homeless, she runs into a man being harassed by a dog. After helping him, she explains her situation, and to her surprise, he offers her his home in gratitude. But when she discovers that said home is a rundown shrine, she tries to leave; however, she is caught by two shrine spirits and a fox familiar named Tomoe. They mistake her for the man Nanami rescued—the land god of the shrine, Mikage. Realizing that Mikage must have sent her there as a replacement god, Tomoe leaves abruptly, refusing to serve a human.\n\nRather than going back to being homeless, Nanami immerses herself in her divine duties. But if she must keep things running smoothly, she will need the help of a certain hot-headed fox. In her fumbling attempt to seek out Tomoe, she lands in trouble and ends up sealing a contract with him. Now the two must traverse the path of godhood together as god and familiar; but it will not be easy, for new threats arise in the form of a youkai who wants to devour the girl, a snake that wants to marry her, and Nanami's own unexpected feelings for her new familiar.\n\n[Written by MAL Rewrite]"}, {"id": 21469, "name_en": "Stand By Me Doraemon", "name_jp": "STAND BY ME ドラえもん", "image_url": "https://cdn.myanimelist.net/images/anime/9/65571.jpg", "genres": ["Comedy", "Sci-Fi"], "themes": [], "demographics": ["Kids", "Shounen"], "studios": ["Shirogumi", "Shin-Ei Animation"], "year": 2014, "episodes": 1, "source": "Manga", "score": 8.12, "synopsis": "Nobita Nobi is an elementary student who hates studying, is bad at sports, and does everything half-heartedly. He is a pushover, unlucky, and fearful of many things. His personality makes him a failure in life, even affecting his progeny. This causes his great-great-grandchild, Sewashi, to take control of the situation.\n\nSewashi travels back in time from the 22nd century to the 20th century to meet Nobita, who is shocked to see him appear out of his drawer alongside a blue robotic cat. The robotic cat calls himself Doraemon, who claims to have been pressured by Sewashi to assist Nobita, with their ultimate goal being to provide Nobita happiness. Frustrated after seeing Nobita's hopeless state, Doraemon decides to go back to the future. However, Sewashi activates a program within Doraemon that prevents him from doing so. \n\nForced to stay, Doraemon helps Nobita using futuristic gadgets through his four-dimensional pocket—a bag containing anything inside it. Can Doraemon bring Nobita happiness and return to the future?\n\n[Written by MAL Rewrite]"}, {"id": 1827, "name_en": "Moribito - Guardian of the Spirit", "name_jp": "精霊の守り人", "image_url": "https://cdn.myanimelist.net/images/anime/4/50337.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": [], "studios": ["Production I.G"], "year": 2007, "episodes": 26, "source": "Novel", "score": 8.12, "synopsis": "On the precipice of a cataclysmic drought, the Star Readers of the Shin Yogo Empire must devise a plan to avoid widespread famine. It is written in ancient myths that the first emperor, along with eight warriors, slew a water demon to avoid a great drought and save the land that was to become Shin Yogo. If a water demon was to appear once more, its death could bring salvation. However, the water demon manifests itself within the body of the emperor's son, Prince Chagum—by the emperor's order, Chagum is to be sacrificed to save the empire.\n\nMeanwhile, a mysterious spear-wielding mercenary named Balsa arrives in Shin Yogo on business. After saving Chagum from a thinly veiled assassination attempt, she is tasked by Chagum's mother to protect him from the emperor and his hunters. Bound by a sacred vow she once made, Balsa accepts.\n\nSeirei no Moribito follows Balsa as she embarks on her journey to protect Chagum, exploring the beauty of life, nature, family, and the bonds that form between strangers.\n\n[Written by MAL Rewrite]"}, {"id": 1604, "name_en": "Reborn!", "name_jp": "家庭教師ヒットマンREBORN!", "image_url": "https://cdn.myanimelist.net/images/anime/1531/142751.jpg", "genres": ["Action", "Comedy"], "themes": ["Organized Crime", "Super Power"], "demographics": ["Shounen"], "studios": ["Artland"], "year": 2006, "episodes": 203, "source": "Manga", "score": 8.12, "synopsis": "There is no putting it lightly—Tsunayoshi Sawada is just no good. He is clumsy, talentless, and desperately in love with the school idol Kyouko Sasagawa, a girl so completely out of his league. Dubbed \"Loser Tsuna\" by his classmates, he seems to be the very personification of failure in the guise of a middle-schooler.\n\nTsuna's boring life takes an extraordinary twist when he encounters the mysterious Reborn, who happens to be a hitman... and shockingly, a baby! Sent from the strongest Mafia family in Italy, Reborn is assigned the daunting mission of preparing the dull middle schooler to succeed the ninth boss of the notorious Vongola family, who is on the brink of retirement. The dull boy has a grueling road ahead, but with the help of his new criminal affiliates and his peculiar home tutor, perhaps even Loser Tsuna can achieve greatness.\n\n[Written by MAL Rewrite]"}, {"id": 11597, "name_en": "Nisemonogatari", "name_jp": "偽物語", "image_url": "https://cdn.myanimelist.net/images/anime/1044/103654.jpg", "genres": ["Comedy", "Mystery", "Supernatural", "Ecchi"], "themes": [], "demographics": [], "studios": ["Shaft"], "year": 2012, "episodes": 11, "source": "Light novel", "score": 8.12, "synopsis": "Koyomi Araragi has recently survived a vampire attack and met several girls plagued by supernatural entities. On top of all this, he wakes up one morning to find himself kidnapped and tied up by his girlfriend Hitagi Senjougahara. Araragi's sister Karen has run afoul of Deishuu Kaiki, a swindler who once conned Senjougahara's family. As a result, Senjougahara has imprisoned Araragi to keep him safe from the devious Kaiki. But when Araragi receives a frantic message from Karen, he sets out to rescue her.\n\nAlong with Karen's troubles, Tsukihi, Araragi's other sister, faces issues of her own. And when two mysterious women step into their lives, not even Araragi can anticipate their true goals, nor the catastrophic truths about his family soon to be revealed.\n\n[Written by MAL Rewrite]"}, {"id": 50612, "name_en": "Dr. Stone: Ryusui", "name_jp": "Dr.STONE 龍水", "image_url": "https://cdn.myanimelist.net/images/anime/1071/124921.jpg", "genres": ["Adventure", "Comedy"], "themes": [], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2022, "episodes": 1, "source": "Manga", "score": 8.12, "synopsis": "Now that brains and brawns have united forces, the next step in Senkuu's plan to unravel the mystery behind the green light that once petrified humanity is to go to the other side of the Earth and investigate its origin. However, to achieve this, Senkuu must first build a ship.\n\nWith the help of Tsukasa Shishiou's former underlings, the base of the ship is rapidly assembled, but there is one missing piece: a skilled captain. During their search, Senkuu and his crew come across a petrified Ryuusui Nanami—the heir of the biggest maritime conglomerate, known for his vast knowledge of sailboats but unpleasant personality. Despite this, Senkuu takes the risk and revives Ryuusui.\n\nRyuusui, upon realizing there are no ownership rights in this new civilization, is excited at the prospect of claiming everything for himself. But before he agrees to play a crucial part in their upcoming journey and command the vessel through the restless seas, he and Senkuu must find the king of fuels to power the ship—oil.\n\n[Written by MAL Rewrite]"}, {"id": 28171, "name_en": "Food Wars! Shokugeki no Soma", "name_jp": "食戟のソーマ", "image_url": "https://cdn.myanimelist.net/images/anime/1444/148976.jpg", "genres": ["Gourmet", "Ecchi"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["J.C.Staff"], "year": 2015, "episodes": 24, "source": "Manga", "score": 8.12, "synopsis": "Souma Yukihira has been cooking alongside his father Jouichirou for as long as he can remember. As a sous chef at his father's restaurant, he has spent years developing his culinary expertise and inventing new dishes to amaze their customers. He aspires to exceed his father's skill and take over the restaurant one day, but he is shocked to learn that Jouichirou is closing up the shop to take a job in New York.\n\nRather than tagging along with his father, Souma finds himself enrolling at the prestigious Tootsuki Culinary Academy, where only 10 percent of its students end up graduating. The school is famous for its \"Shokugeki\"—intense cooking competitions between students often used to settle debates and arguments. Jouichirou tells Souma that to surpass him and survive the next three years at Tootsuki and graduate there.\n\nThe academy's brutal curriculum and fiercely competitive student body await the young chef, who must learn to navigate the treacherous environment if he wants to stand a chance at realizing his dreams. But is skill alone enough to let him rise to the top?\n\n[Written by MAL Rewrite]"}, {"id": 51122, "name_en": "Spice and Wolf: Merchant Meets the Wise Wolf", "name_jp": "狼と香辛料 MERCHANT MEETS THE WISE WOLF", "image_url": "https://cdn.myanimelist.net/images/anime/1059/142414.jpg", "genres": ["Adventure", "Drama", "Romance", "Supernatural"], "themes": ["Adult Cast"], "demographics": [], "studios": ["Passione"], "year": 2024, "episodes": 25, "source": "Light novel", "score": 8.12, "synopsis": "With a cartload of fur pelts in tow, traveling merchant Kraft Lawrence stops by the village of Pasloe. According to local folklore, centuries ago, one of the villagers made a promise with the wolf deity Holo, who swore to bless Pasloe with bountiful harvests of wheat. Yet, as time passed, such stories became little more than relics of the past.\n\nAfter quickly finishing his business in the village, Lawrence sets out to his next destination. His journey, however, takes an unexpected turn when he discovers a nude, animal-eared girl sleeping among his pelts. Even more surprisingly, the youthful-looking woman claims to be Holo—the wolf of legend.\n\nHolo wishes to return to her hometown in the north, and though their first encounter is rocky, she convinces Lawrence to accompany her on her travels. In return, she vows to earn her keep, using her quick wits and lifetime of experience to help her newfound companion in his dealings. As they continue their journey, Lawrence and Holo take advantage of whatever economic opportunities they come across, often landing in situations that put both their business skills and their relationship to the test.\n\n[Written by MAL Rewrite]"}, {"id": 62543, "name_en": "There's No Freaking Way I'll Be Your Lover! Unless…: Next Shine", "name_jp": "わたしが恋人になれるわけないじゃん、ムリムリ! (※ムリじゃなかった!?) ～ネクストシャイン！～", "image_url": "https://cdn.myanimelist.net/images/anime/1129/152182.jpg", "genres": ["Comedy", "Girls Love"], "themes": ["Harem", "School"], "demographics": [], "studios": ["studio MOTHER"], "year": 2026, "episodes": 5, "source": "Light novel", "score": 8.11, "synopsis": "Episodes 13-17 of Watashi ga Koibito ni Nareru Wake Nai jan, Muri Muri! (※Muri ja Nakatta!?)\n\n\"I want to learn to love myself—just the way I am!\"\n\nRenako Amaori, a former gloomy and socially awkward girl, reinvented herself for high school and successfully made her debut. She even managed to join the top-tier popular group... only to be suddenly confessed to by two of its brightest stars?! One is the school's ultimate prince, Mai Ouzuka. The other, the beloved angel of the campus, Ajisai Sena. There's no way she can handle both their affections—no freaking way!\n\nWhile still struggling to give them an answer, Renako agrees to help her friend Kaho by joining a cosplay event. It's the perfect excuse to delay her reply... or so she thought. But as she witnesses Kaho's passion firsthand, something begins to stir inside her.\n\nWhat is it that she truly wants? What is she truly searching for? Can she really not become someone's lover? Or has that \"no freaking way\"... not been so impossible after all? \n\n(Source: REMOW, edited)"}, {"id": 62543, "name_en": "There's No Freaking Way I'll Be Your Lover! Unless…: Next Shine", "name_jp": "わたしが恋人になれるわけないじゃん、ムリムリ! (※ムリじゃなかった!?) ～ネクストシャイン！～", "image_url": "https://cdn.myanimelist.net/images/anime/1129/152182.jpg", "genres": ["Comedy", "Girls Love"], "themes": ["Harem", "School"], "demographics": [], "studios": ["studio MOTHER"], "year": 2026, "episodes": 5, "source": "Light novel", "score": 8.11, "synopsis": "Episodes 13-17 of Watashi ga Koibito ni Nareru Wake Nai jan, Muri Muri! (※Muri ja Nakatta!?)\n\n\"I want to learn to love myself—just the way I am!\"\n\nRenako Amaori, a former gloomy and socially awkward girl, reinvented herself for high school and successfully made her debut. She even managed to join the top-tier popular group... only to be suddenly confessed to by two of its brightest stars?! One is the school's ultimate prince, Mai Ouzuka. The other, the beloved angel of the campus, Ajisai Sena. There's no way she can handle both their affections—no freaking way!\n\nWhile still struggling to give them an answer, Renako agrees to help her friend Kaho by joining a cosplay event. It's the perfect excuse to delay her reply... or so she thought. But as she witnesses Kaho's passion firsthand, something begins to stir inside her.\n\nWhat is it that she truly wants? What is she truly searching for? Can she really not become someone's lover? Or has that \"no freaking way\"... not been so impossible after all? \n\n(Source: REMOW, edited)"}, {"id": 59459, "name_en": "Secrets of the Silent Witch", "name_jp": "サイレント・ウィッチ 沈黙の魔女の隠しごと", "image_url": "https://cdn.myanimelist.net/images/anime/1669/149732.jpg", "genres": ["Fantasy"], "themes": ["School"], "demographics": [], "studios": ["Studio Gokumi"], "year": 2025, "episodes": 13, "source": "Light novel", "score": 8.11, "synopsis": "In the Kingdom of Ridill lives a genius magician known to be the first person to ever use chantless magic. With this amazing skill, Monica Everett joins the Seven Sages as a hero who can defeat powerful dragons, such as the legendary black dragon, all on her own. However, the truth is that the Silent Witch developed chantless magic due to being too shy—preferring to live on a mountain top, far from society.\n\nHer secluded lifestyle changes, though, when a fellow sage tasks her to covertly defend the second prince, Felix Arc Ridill, from attempts on his life. Unfortunately, that means she will have to go undercover as a student at Serendia Academy. Between guard duty and her social anxiety, Monica is facing her most difficult mission to date.\n\n[Written by MAL Rewrite]"}, {"id": 54650, "name_en": "Precure All Stars Movie F", "name_jp": "映画プリキュアオールスターズF", "image_url": "https://cdn.myanimelist.net/images/anime/1959/137024.jpg", "genres": ["Action"], "themes": ["Mahou Shoujo"], "demographics": [], "studios": ["Toei Animation"], "year": 2023, "episodes": 1, "source": "Original", "score": 8.11, "synopsis": "A great Pretty Cure gathering! Or so we thought. They've actually been separated~!?\n\n\"Where am I...?\" --When Sora wakes up, she finds herself in a mysterious world... It seems that she has lost Mashiro and the others.... But there she meets Yui, Manatsu, and Prim! What's more, Prim transforms into Cure Supreme! Mashiro meets Laura, Amane, and Nodoka on a small island in the sea, and rescues Puka from a monster attack! Tsubasa meets Saaya, Ha-chan, and Haruka, where they take a walk in the hanging garden! Ageha meets Asumi, Lala and Yukari, but Lala and Yukari are having trouble getting along! They meet a lot of friends, but for some reason, their memories are all a little hazy... And it seems that the only people in this world are Precure...? We want to go back to our own world! The clue is a castle in the distance-- Onward! Our journey begins here! Let's go on a great adventure to the castle!\n\n(Source: Official Site, translated)"}, {"id": 30364, "name_en": "Haikyuu!! the Movie: The Winner and the Loser", "name_jp": "ハイキュー!! 勝者と敗者", "image_url": "https://cdn.myanimelist.net/images/anime/2/74813.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2015, "episodes": 1, "source": "Manga", "score": 8.11, "synopsis": "The Inter-High has finally arrived for Karasuno High School's volleyball team. Their opponent at the tournament is Date Tech High School—the team that crushed Karasuno the year prior. The loss brings back anxiety and fear for the team's second- and third-years as they were unable to overcome Date's \"read block.\" With the dynamic freshmen duo Shouyou Hinata and Tobio Kageyama on their side, will Karasuno be able to overcome their past and break through the iron wall?\n\nAwaiting Karasuno in the next round is the elite Aoba Jousai High School, led by the cunning captain Tooru Oikawa. Coming from the same middle school as Kageyama, Oikawa is a talented and unpredictable setter with a grudge against his former teammate. It is a battle between genius setters to decide who will continue on in the Inter-High.\n\n[Written by MAL Rewrite]"}, {"id": 29831, "name_en": "Tamayura: Sotsugyou Shashin Part 4 - Ashita", "name_jp": "たまゆら～卒業写真～ 第4部 朝-あした-", "image_url": "https://cdn.myanimelist.net/images/anime/1697/134884.jpg", "genres": ["Slice of Life"], "themes": ["CGDCT", "Iyashikei"], "demographics": [], "studios": ["TYO Animations"], "year": 2016, "episodes": 1, "source": "Original", "score": 8.11, "synopsis": "The final movie of a four-part finale of Tamayura."}, {"id": 29755, "name_en": "Haikyu!! the Movie: The End and the Beginning", "name_jp": "ハイキュー!! 終わりと始まり", "image_url": "https://cdn.myanimelist.net/images/anime/8/76011.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2015, "episodes": 1, "source": "Manga", "score": 8.11, "synopsis": "After watching a volleyball match on TV where the \"Little Giant\" of Karasuno High School helps his team secure a hard-fought victory, young and impressionable Shouyou Hinata begins to play the sport in hopes that he too can soar high one day. Now enrolled at the same school as his idol, he attempts to join the volleyball team despite his short stature. Unfortunately, upon his arrival, Hinata locks eyes with \"King of the Court\" Tobio Kageyama—a genius setter whom he had once suffered a bitter defeat to in middle school.\n\nTheir unfriendly introduction and subsequent rivalry forces the rest of the Karasuno team to bar both of them from joining the club until they can learn to work together as teammates. Though reluctant to cooperate, their desire to play volleyball causes them to return with a proposal that will show the other members that they are capable of teamwork. As the two freshmen form an odd setter-and-ace relationship, an old rival school invites Karasuno for practice matches, reigniting competition between the two schools once more.\n\n[Written by MAL Rewrite]"}, {"id": 10153, "name_en": "Magical Girl Lyrical Nanoha: The Movie 2nd A's", "name_jp": "魔法少女リリカルなのは The MOVIE 2nd A's", "image_url": "https://cdn.myanimelist.net/images/anime/1511/94432.jpg", "genres": ["Action", "Comedy", "Drama", "Sci-Fi"], "themes": ["Mahou Shoujo"], "demographics": [], "studios": ["Seven Arcs"], "year": 2012, "episodes": 1, "source": "Original", "score": 8.11, "synopsis": "Several months have passed since the Jewel Seed incident, and Nanoha Takamachi is getting ready to welcome Fate Testarossa back from her trial with the Administrative Bureau. Having received conditional clemency for her role in preventing the crisis, Fate has been allowed to attend school with Nanoha. However, before the two girls can settle into their daily lives together, a group of dark mages suddenly descend upon them. Overpowered by their unknown assailants, Nanoha and Fate have their mana absorbed by an ominous grimoire, and both Raising Heart and Bardiche are damaged in the process.\n\nThe ones behind this ambush are known as the Wolkenritter, and its four members—Vita, Signum, Shamal, and the familiar Zafila—were summoned by the Book of Darkness to carry out its malignant bidding. The book's current owner is the young girl Hayate Yagami, oblivious to its true nature while the Wolkenritter, charging its power with the forcibly stolen mana, is duty-bound to seek the tome's completion.\n\nLeft drained and weaponless, Nanoha and Fate must race against time to recover their strength and confront the Wolkenritter, with the aim of preventing the quartet from finishing the Book of Darkness and unleashing its devastating magic upon the world.\n\n[Written by MAL Rewrite]"}, {"id": 20899, "name_en": "JoJo's Bizarre Adventure: Stardust Crusaders", "name_jp": "ジョジョの奇妙な冒険 スターダストクルセイダース", "image_url": "https://cdn.myanimelist.net/images/anime/11/55267.jpg", "genres": ["Action", "Adventure"], "themes": ["Super Power"], "demographics": ["Shounen"], "studios": ["David Production"], "year": 2014, "episodes": 24, "source": "Manga", "score": 8.11, "synopsis": "Years after an ancient evil was salvaged from the depths of the sea, Joutarou Kuujou sits peacefully within a Japanese jail cell. He's committed no crime yet demands he not be released, believing he's been possessed by an evil spirit capable of harming those around him. Concerned for her son, Holy Kuujou asks her father, Joseph Joestar, to convince Joutarou to leave the prison. Joseph informs his grandson that the \"evil spirit\" is in fact something called a \"Stand,\" the physical manifestation of one's fighting spirit which can adopt a variety of deadly forms. After a fiery brawl with Joseph's friend Muhammad Avdol, Joutarou is forced out of his cell and begins learning how to control the power of his Stand.\n\nHowever, when a Stand awakens within Holy and threatens to consume her in 50 days, Joutarou, his grandfather, and their allies must seek out and destroy the immortal vampire responsible for her condition. They must travel halfway across the world to Cairo, Egypt and along the way, do battle with ferocious Stand users set on thwarting them. If Joutarou and his allies fail in their mission, humanity is destined for a grim fate.\n\n[Written by MAL Rewrite]"}, {"id": 6467, "name_en": "Detective Conan Movie 14: The Lost Ship in the Sky", "name_jp": "名探偵コナン 天空の難破船", "image_url": "https://cdn.myanimelist.net/images/anime/7/22312.jpg", "genres": ["Action", "Mystery"], "themes": ["Detective"], "demographics": ["Shounen"], "studios": ["Tokyo Movie Shinsha", "TMS Entertainment"], "year": 2010, "episodes": 1, "source": "Manga", "score": 8.11, "synopsis": "One night, the Tokyo National Institute of Microorganisms is attacked by a mysterious organization calling themselves the \"Red Siamese Cats.\" The group causes the explosion of a high-security lab storing a virus—said to have a mortality rate of 80% with no known cure. This act of terror dominates the headlines, overshadowing the unveiling of the world's biggest airship developed under Jirokichi Suzuki. Peculiarly, the unveiling has a note attached for Kaito \"Kaitou Kid\" Kuroba, challenging him to steal the jewel on display—the illustrious Lady Sky.\n\nConan Edogawa is permitted to board the airship along with Kogorou Mouri, Ran Mouri, Sonoko Suzuki, Professor Agasa, and the Detective Boys. However, their fun comes to a halt when an unknown caller contacts Jirokichi and declares that they have released a certain virus in the smoking room. Soon after, symptoms begin to appear among the passengers and chaos ensues. Seizing the opportunity, the Red Siamese Cats suddenly appear and hijack the airship! \n\nTo stop the assailants, Conan and Kaitou Kid, along with their allies, must work together to decipher the clues and discover the Red Siamese Cats' real objective before time runs out.\n\n[Written by MAL Rewrite]"}, {"id": 36296, "name_en": "Hinamatsuri", "name_jp": "ヒナまつり", "image_url": "https://cdn.myanimelist.net/images/anime/1580/93526.jpg", "genres": ["Comedy"], "themes": ["Childcare", "Organized Crime", "Super Power"], "demographics": ["Seinen"], "studios": ["feel."], "year": 2018, "episodes": 12, "source": "Manga", "score": 8.11, "synopsis": "While reveling in the successful clinching of a prized vase for his collection, Yoshifumi Nitta, a yakuza member, is rudely interrupted when a large, peculiar capsule suddenly materializes and falls on his head. He opens the capsule to reveal a young, blue-haired girl, who doesn't divulge anything about herself but her name—Hina—and the fact that she possesses immense powers. As if things couldn't get any worse, she loses control and unleashes an explosion if her powers remain unused. Faced with no other choice, Nitta finds himself becoming her caregiver. \n\nTo let her use her powers freely, Nitta asks Hina to help out with a construction deal, which goes smoothly. But while this is happening, a rival yakuza group covertly attacks his boss. To Nitta's shock, his colleagues later pin the blame on him! Tasked with attacking the rival group in retaliation, Nitta steels himself and arrives at their hideout. But suddenly, Hina unexpectedly steps in and helps him wipe out the entire group. As it turns out, Hina might just become a valuable asset to Nitta and his yakuza business, provided she does not use her powers on him first! And so the strange life of this unusual duo begins.\n\n[Written by MAL Rewrite]"}, {"id": 40542, "name_en": "The Disastrous Life of Saiki K.: Reawakened", "name_jp": "斉木楠雄のΨ難 Ψ始動編", "image_url": "https://cdn.myanimelist.net/images/anime/1993/119022.jpg", "genres": ["Comedy"], "themes": ["Gag Humor", "School", "Super Power"], "demographics": ["Shounen"], "studios": ["J.C.Staff", "Egg Firm"], "year": 2019, "episodes": 6, "source": "Manga", "score": 8.11, "synopsis": "Kusuo Saiki is a high school student who possesses a wide range of psychic abilities. While many may believe these abilities to be a gift, to Kusuo, they are a curse as he must fight strange odds in order to try to live a normal life. Forced to use his psychic powers to protect his secret or to make up for his father's incompetence at work, will Saiki eventually come to realize how his powers can actually help his friends and family?\n\n[Written by MAL Rewrite]"}, {"id": 40421, "name_en": "given The Movie", "name_jp": "映画 ギヴン", "image_url": "https://cdn.myanimelist.net/images/anime/1648/111422.jpg", "genres": ["Boys Love", "Drama"], "themes": ["Music"], "demographics": [], "studios": ["Lerche"], "year": 2020, "episodes": 1, "source": "Manga", "score": 8.11, "synopsis": "The band \"given\"—comprised of Ritsuka Uenoyama, Mafuyu Satou, Haruki Nakayama, and Akihiko Kaji—has advanced to the final screening of the Countdown-fes Amateur Contest, in which they will be judged on their live act. Although enthusiastic, they worry about having only one original song to perform.\n\nMafuyu embraces the idea of learning more about music in order to create new, emotionally resonant songs. In this regard, he unexpectedly receives help from Ugetsu Murata, Akihiko's on-again, off-again lover. Ugetsu has unsuccessfully tried to let go of Akihiko, who himself is torn between lingering feelings for his past and an uncertain resolve for the future.\n\nAs the competition draws near, Haruki uncharacteristically begins to doubt his place in the band and the trust he shares with Akihiko. It is a given that not all attachments last forever, but it remains to be seen what can be salvaged from the ruins of heartbreak—or if only regrets will endure.\n\n[Written by MAL Rewrite]"}, {"id": 8129, "name_en": "Princess Jellyfish", "name_jp": "海月姫", "image_url": "https://cdn.myanimelist.net/images/anime/1764/133575.jpg", "genres": ["Comedy"], "themes": ["Adult Cast", "Crossdressing", "Visual Arts"], "demographics": ["Josei"], "studios": ["Brain's Base"], "year": 2010, "episodes": 11, "source": "Manga", "score": 8.11, "synopsis": "Ever since her late mother took her to an aquarium when she was young, Tsukimi Kurashita has been obsessed with jellyfish, comparing their flowing tentacles to a princess's white dress. Now living with five other unemployed otaku women, 19-year-old Tsukimi spends her days as a social outcast dreaming of becoming an illustrator.\n\nHowever, her life changes forever when one day, a beautiful woman unexpectedly helps her save a jellyfish in a local pet store. From then on, the stranger—confident, fashionable, and the complete opposite of Tsukimi and her roommates—begins to regularly visit the girls' building. This trendy hipster, though appearing shallow at first, harbors some secrets of her own, starting with the fact that \"she\" isn't really a girl at all, but a wealthy male college student named Kuranosuke Koibuchi!\n\n[Written by MAL Rewrite]"}, {"id": 18689, "name_en": "Ace of Diamond", "name_jp": "ダイヤのA[エース]", "image_url": "https://cdn.myanimelist.net/images/anime/5/54235.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G", "Madhouse"], "year": 2013, "episodes": 75, "source": "Manga", "score": 8.11, "synopsis": "With a stray pitch that completely missed the batter, Eijun Sawamura loses his final middle school baseball game. Frustrated by this defeat, Eijun and his teammates vow to reach the national tournament once they are in high school. But everything changes when a scout unexpectedly invites him to Tokyo's prestigious Seidou High School after seeing the potential in his unusual pitching style. Encouraged by his teammates, Eijun accepts the offer, ready to improve his skills and play at a much more competitive level of baseball.\n\nHowever, now surrounded by a large number of skilled players, Eijun struggles to find his place on the team. He declares that he will one day become the team's ace, but that's only if fellow first year Satoru Furuya doesn't take the title first, with his breakneck fastballs that earn him a coveted spot on the starting roster. With the addition of these talented new players to an already powerful lineup, the Seidou baseball team aims to become the best in Japan, facing off against a number of formidable foes that stand in their way.\n\n[Written by MAL Rewrite]"}, {"id": 15059, "name_en": "Initial D Fifth Stage", "name_jp": "頭文字 D Fifth Stage", "image_url": "https://cdn.myanimelist.net/images/anime/1563/119898.jpg", "genres": ["Action", "Drama"], "themes": ["Racing"], "demographics": ["Seinen"], "studios": ["SynergySP"], "year": 2012, "episodes": 14, "source": "Manga", "score": 8.11, "synopsis": "After earning a series of difficult victories in the prefectures of Tochigi, Saitama, and Ibaraki, the drivers of Project D—an amateur street driving group led by ex-street driver and expert tactician Ryousuke Takahashi—Takumi Fujiwara and Keisuke Takahashi now have to take Project D to the next level: the Kanagawa prefecture, commonly known as the holy land of street racing. Their opponents, members of the three best street racing teams of Kanagawa, design an elaborate strategy called the \"Four Lines of Defense\" to put a definitive end to the ambitions of Project D.\n\nMeanwhile, Takumi feels he needs to refine his driving skills to overcome the last victory against Toshiya Joushima that he only just managed to snatch. The moment of truth has now come for Project D, which carries with it the hopes of the Gunma Prefecture; will they manage to thwart the surprising tactics of the skilled street racers of Kanagawa?\n\n[Written by MAL Rewrite]"}, {"id": 49709, "name_en": "To Your Eternity Season 2", "name_jp": "不滅のあなたへ Season2", "image_url": "https://cdn.myanimelist.net/images/anime/1271/127700.jpg", "genres": ["Adventure", "Drama", "Supernatural"], "themes": [], "demographics": ["Shounen"], "studios": ["Drive"], "year": 2022, "episodes": 20, "source": "Manga", "score": 8.11, "synopsis": "After seeing enough death and tragedy, the immortal Fushi secludes himself on an island, defending himself from enemy Nokkers. However, instead of attacking Fushi in isolation, Nokkers begin targeting the settlements outside of his reach in hopes of luring him out. Soon, a group known as the Guardians—led by Hisame, the descendant of the deceased warrior Hayase—finds Fushi.\n\nInspired by how Fushi protected Janada Island from the Nokkers years ago, the Guardians have grown a considerable following and are recognized throughout the world. Initially reluctant, Fushi allows the Guardians to accompany him to the site of the Nokkers' recent attack. In their village, Fushi meets a few valuable allies, both new and old. But as the conflict with the Nokkers only leads to more loss, Fushi must find the inner strength to face his inevitable sorrow.\n\n[Written by MAL Rewrite]"}, {"id": 239, "name_en": "Gankutsuou: The Count of Monte Cristo", "name_jp": "巌窟王", "image_url": "https://cdn.myanimelist.net/images/anime/1910/138560.jpg", "genres": ["Award Winning", "Drama", "Mystery", "Sci-Fi", "Supernatural", "Suspense"], "themes": [], "demographics": [], "studios": ["Gonzo"], "year": 2004, "episodes": 24, "source": "Novel", "score": 8.11, "synopsis": "In the year 5053, French aristocrats Viscount Albert de Morcerf and Baron Franz d'Epinay attend the festival of Carnival on the moon city of Luna. While Franz is just looking to have fun, Albert is seeking something more to fill his life—but he finds more than he bargained for in The Count of Monte Cristo, a mysterious and charming self-made nobleman who meets his gaze during an opera performance.\n \nThrough a few twists and turns, Albert befriends the Count and introduces him into French society. The Count, however, has more on his mind than just friendship; he plots to finally unleash his vengeance on those who wronged him years earlier. Gankutsuou follows Albert and the Count's intertwined destinies and the ultimate price paid for enacting revenge.\n\n[Written by MAL Rewrite]"}, {"id": 50416, "name_en": "Skip and Loafer", "name_jp": "スキップとローファー", "image_url": "https://cdn.myanimelist.net/images/anime/1518/138730.jpg", "genres": ["Drama"], "themes": ["School"], "demographics": ["Seinen"], "studios": ["P.A. Works"], "year": 2023, "episodes": 12, "source": "Manga", "score": 8.11, "synopsis": "In order to pursue her dream of bringing positive changes to Japan, Mitsumi Iwakura leaves her countryside town to attend a prestigious high school in the hustle and bustle of Tokyo. As she has already mapped a clear life plan, she has absolute confidence that there will be zero mishaps from then onwards.\n\nDespite her ambitious promise, the country girl ends up running late on the first day when she gets lost on her way to school. Fortunately, she meets a fellow first-year student, Sousuke Shima, who is in the same situation and offers to go with her. They eventually make it to school, but the misfortunes do not end there, as Mitsumi leaves an unfavorable first impression in front of her classmates.\n\nNevertheless, the class soon takes notice of her friendship with Sousuke despite their opposing personalities. Only time will tell whether Mitsumi will be able to forge fruitful relationships with her classmates, and she will certainly not be alone.\n\n[Written by MAL Rewrite]"}, {"id": 121, "name_en": "Fullmetal Alchemist", "name_jp": "鋼の錬金術師", "image_url": "https://cdn.myanimelist.net/images/anime/10/75815.jpg", "genres": ["Action", "Adventure", "Award Winning", "Drama", "Fantasy"], "themes": ["Military"], "demographics": ["Shounen"], "studios": ["Bones"], "year": 2003, "episodes": 51, "source": "Manga", "score": 8.11, "synopsis": "Edward Elric, a young, brilliant alchemist, has lost much in his twelve-year life: when he and his brother Alphonse try to resurrect their dead mother through the forbidden act of human transmutation, Edward loses his brother as well as two of his limbs. With his supreme alchemy skills, Edward binds Alphonse's soul to a large suit of armor.\n\nA year later, Edward, now promoted to the fullmetal alchemist of the state, embarks on a journey with his younger brother to obtain the Philosopher's Stone. The fabled mythical object is rumored to be capable of amplifying an alchemist's abilities by leaps and bounds, thus allowing them to override the fundamental law of alchemy: to gain something, an alchemist must sacrifice something of equal value. Edward hopes to draw into the military's resources to find the fabled stone and restore his and Alphonse's bodies to normal. However, the Elric brothers soon discover that there is more to the legendary stone than meets the eye, as they are led to the epicenter of a far darker battle than they could have ever imagined.\n\n[Written by MAL Rewrite]"}, {"id": 1364, "name_en": "Case Closed Movie 05: Countdown to Heaven", "name_jp": "名探偵コナン 天国へのカウントダウン", "image_url": "https://cdn.myanimelist.net/images/anime/1144/92654.jpg", "genres": ["Adventure", "Comedy", "Mystery"], "themes": ["Detective"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2001, "episodes": 1, "source": "Manga", "score": 8.11, "synopsis": "Conan Edogawa, the Detective Boys, and Professor Hiroshi Agasa decide to visit the Nishitamashi Twin Towers. There they run into Ran Mouri; her closest friend, Sonoko Suzuki; and Ran's father, the famous Kogorou. Learning the trio are attending the towers' grand opening, Conan and company tag along for a private tour of its floors.\n\nHowever, as preparations are finalized for the opening ceremony, their visit takes an unexpected turn—three brutal murders occur, seemingly linked to a mysterious Porsche 356A. Soon after, as Conan and the detectives dive deeper into the case, the towers are rocked by an explosion. With fire rapidly spreading and lives in danger, police desperately seek to evacuate everyone. But when the elevator, their only means to escape goes down, Conan and company are left behind. With help on its way, they frantically try to keep everyone safe, but time is running out if they want to bring the perpetrators to justice.\n\n[Written by MAL Rewrite]"}, {"id": 62405, "name_en": "Tatsuki Fujimoto 17-26", "name_jp": "藤本タツキ17-26", "image_url": "https://cdn.myanimelist.net/images/anime/1864/151837.jpg", "genres": ["Action", "Comedy", "Drama", "Romance", "Supernatural"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["P.A. Works", "Zexcs", "Lapin Track", "Studio Kafka", "100studio", "Studio Graph77"], "year": 2025, "episodes": 8, "source": "Manga", "score": 8.1, "synopsis": "1. Niwa ni wa Niwa Niwatori ga Ita. (A Couple Clucking Chickens Were Still Kickin' in the Schoolyard)\n2. Sasaki-kun ga Juudan Tometa (Sasaki Stopped a Bullet)\n3. Koi wa Moumoku (Love Is Blind)\n4. Shikaku\n5. Ningyo Rhapsody (Mermaid Rhapsody)\n6. Me ga Sametara Onnanoko ni Natteita Yamai (Woke-Up-as-a-Girl Syndrome)\n7. Yogen no Nayuta (Nayuta of the Prophecy)\n8. Imouto no Ane (Sisters)"}, {"id": 60371, "name_en": "You and I Are Polar Opposites", "name_jp": "正反対な君と僕", "image_url": "https://cdn.myanimelist.net/images/anime/1140/154457.jpg", "genres": ["Comedy", "Romance"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["Lapin Track"], "year": 2026, "episodes": 12, "source": "Manga", "score": 8.1, "synopsis": "Next to Miyu Suzuki, a trendy and bubbly high school girl, sits Yuusuke Tani, a quiet and frank boy. Their interactions are brief, limited to Suzuki's unprompted random questions and Tani's succinct answers. Yet, beneath these simple exchanges, feelings of love are quietly blossoming.\n\nSuzuki sees Tani for who he is: a straightforward boy who respects everyone equally, completely unconcerned about fitting in. Unlike Tani, she is too hung up on what everyone else thinks, desperate to keep her crush a secret. However, a slight misunderstanding leads to Suzuki confessing her feelings—and to her delight, Tani feels the same. Jittery with excitement, the young couple starts dating, embarking on a wonderful journey of friendship and love.\n\n[Written by MAL Rewrite]"}, {"id": 59306, "name_en": "Slay The Gods", "name_jp": "斩神之凡尘神域", "image_url": "https://cdn.myanimelist.net/images/anime/1514/144274.jpg", "genres": ["Action", "Fantasy", "Suspense"], "themes": ["Psychological"], "demographics": [], "studios": ["Shenman Entertainment"], "year": 2024, "episodes": 15, "source": "Web novel", "score": 8.1, "synopsis": "It follows the journey of Lin Qiye, a blind high school student who unexpectedly becomes a night watchman, tasked with protecting the nation from \"mystical\" invasions as the mist descends and gods reawaken. Leading his team, he tirelessly eliminates mythical creatures that emerge in modern society and eventually sets out on the path of god-slaying, evolving from guarding his small family to defending the nation.\n\n(Source: WeTV)"}, {"id": 53888, "name_en": "Spy x Family Code: White", "name_jp": "SPY×FAMILY CODE: White", "image_url": "https://cdn.myanimelist.net/images/anime/1426/139388.jpg", "genres": ["Action", "Comedy"], "themes": ["Childcare", "Super Power"], "demographics": ["Shounen"], "studios": ["Wit Studio", "CloverWorks"], "year": 2023, "episodes": 1, "source": "Manga", "score": 8.1, "synopsis": "Loid Forger, an elite spy, is warned by his handler that he may potentially be reassigned from his ongoing mission, Operation Strix. To maintain his position, he must make significant progress toward the operation's objectives, which involves having his adoptive daughter Anya earn sufficient Stella Stars to become an Imperial Scholar at Eden Academy.\n\nAfter learning of a cooking contest that awards the winning student with a Stella Star, Loid researches the judge's preferred dessert to help increase Anya's odds. However, perfectly recreating the judge's favorite meremere requires more than just following a recipe. Thus, the Forgers embark on a vacation to the Frigis region to try an authentic meremere. Not all goes smoothly on the trip, as the Forger family ends up entwined in a sinister plot to reignite war between the countries of Ostania and Westalis.\n\n[Written by MAL Rewrite]"}, {"id": 53411, "name_en": "Buddy Daddies", "name_jp": "Buddy Daddies", "image_url": "https://cdn.myanimelist.net/images/anime/1553/133767.jpg", "genres": ["Action", "Comedy"], "themes": ["Childcare", "Organized Crime"], "demographics": [], "studios": ["P.A. Works"], "year": 2023, "episodes": 12, "source": "Original", "score": 8.1, "synopsis": "On Christmas Eve, four-year-old Miri Unasaka arrives in Tokyo completely alone, in search of her father. The bright lights and merry atmosphere guide Miri to a big hotel and a man with a delicious-looking cake. However, the child has just unknowingly walked into the center of an elaborate, foolproof plan for assassinating a dangerous mafia boss.\n\nProfessional assassins Kazuki Kurusu and Rei Suwa live together, fleeing memories of their grim childhoods and avoiding emotional connections. When their mission goes awry and they end up bringing Miri home, there is only one reasonable thing to do: return Miri to her mother. But the girl's innocent laugh and pure worldview quickly enamor her to Kazuki, who secretly enjoys playing a parent, and it is not long before Rei's impenetrable heart makes room for Miri.\n\nMiri's every move is unpredictable, and Kazuki and Rei find raising an energetic child harder than any of their missions. Although it presents a great risk to their careers, Kazuki and Rei—both assuming the affectionate nickname \"papa\"—decide to provide Miri with a normal childhood despite all the odds stacked against them.\n\n[Written by MAL Rewrite]"}, {"id": 52347, "name_en": "Shangri-La Frontier", "name_jp": "シャングリラ・フロンティア～クソゲーハンター、神ゲーに挑まんとす～", "image_url": "https://cdn.myanimelist.net/images/anime/1500/139931.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Video Game"], "demographics": ["Shounen"], "studios": ["C2C"], "year": 2023, "episodes": 25, "source": "Manga", "score": 8.1, "synopsis": "High school student Rakurou Hizutome has a peculiar hobby of playing poorly made games—ones that are unbalanced or are filled with so many bugs that make them borderline unplayable. The few who share his hobby might recognize him by his in-game name, Sunraku. For his next game, Rakurou is recommended Shangri-La Frontier, a popular and well-received virtual reality game as a breather from the terrible games he has been playing recently.\n\nOnce he boots up the game, Rakurou decides to sell off most of his starting gear to gain extra money, leaving himself with only a pair of boxers, a bird mask, and some weapons. He is instantly hooked as he meticulously levels up his avatar. However, after encountering some intimidating monsters, he realizes that he may have underestimated the challenge that a mainstream game can offer. As Rakurou progresses, he must draw on all the skills he has perfected from his previous gaming experience. Before long, Sunraku's eccentric playstyle takes Shangri-La Frontier by storm.\n\n[Written by MAL Rewrite]"}, {"id": 15323, "name_en": "One Piece: Episode of Nami - Tears of a Navigator and the Bonds of Friends", "name_jp": "ワンピースエピソード・オブ・ナミ～航海士の涙と仲間の絆～", "image_url": "https://cdn.myanimelist.net/images/anime/5/41415.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Toei Animation"], "year": 2012, "episodes": 1, "source": "Manga", "score": 8.1, "synopsis": "Nami betrays the Straw Hat crew and steals their ship and treasures, escaping to Arlong Park. Luffy and his crew follow her, only to come face to face with the infamous pirate Arlong, a ruthless fish-man who has taken over Nami's home island with force and Naval bribes. As opinions clash and the Straw Hats attempt to convince Nami to rejoin the team, her true motivation behind the deception will be revealed at last. \n\n[Written by MAL Rewrite]"}, {"id": 9130, "name_en": "Saint Seiya: The Lost Canvas 2", "name_jp": "聖闘士星矢 THE LOST CANVAS 冥王神話 2", "image_url": "https://cdn.myanimelist.net/images/anime/12/29597.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Mythology"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2011, "episodes": 13, "source": "Manga", "score": 8.1, "synopsis": "The sequel to Saint Seiya: The Lost Canvas - Meiou Shinwa."}, {"id": 30370, "name_en": "Yona of the Dawn OVA", "name_jp": "暁のヨナ", "image_url": "https://cdn.myanimelist.net/images/anime/8/75974.jpg", "genres": ["Adventure", "Fantasy"], "themes": [], "demographics": ["Shoujo"], "studios": ["Studio Pierrot"], "year": 2015, "episodes": 3, "source": "Manga", "score": 8.1, "synopsis": "Sono Se ni wa\nAt a remote hot springs lodge, Yona and the team rest. The boys are taking a bath with the exception of Ki-ja, who looks troubled. Jae-ha takes this chance to tease him, unwittingly finding a mark linked to the previous White Dragon—whom coincidentally is also Ki-ja's father—prompting Ki-ja to recall their complicated relationship.\n\nKou Ryuu Zeno Kako-hen\nIn another time and place, Son Hak and the dragons banter as they hold a drinking contest. As they start to doze off one-by-one, Zeno dreams of his past, where his abilities and experiences, both as a person and as the Yellow Dragon, are revealed.\n\n[Written by MAL Rewrite]"}, {"id": 1566, "name_en": "Ghost in the Shell: Stand Alone Complex - Solid State Society", "name_jp": "攻殻機動隊 STAND ALONE COMPLEX Solid State Society", "image_url": "https://cdn.myanimelist.net/images/anime/3/85128.jpg", "genres": ["Award Winning", "Mystery", "Sci-Fi"], "themes": ["Adult Cast", "Detective", "Mecha", "Military"], "demographics": ["Seinen"], "studios": ["Production I.G"], "year": 2006, "episodes": 1, "source": "Manga", "score": 8.1, "synopsis": "A.D. 2034. It has been two years since Motoko Kusanagi left Section 9. Togusa is now the new leader of the team, that has considerably increased its appointed personnel. The expanded new Section 9 confronts a rash of complicated incidents, and investigations reveal that an ultra-wizard hacker named the Puppeteer is behind the entire series of events.\n\nIn the midst of all, Batou, who was stalking the case on a separate track, encounters Motoko. She goes away after saying, \"Stay away from the Solid State Society.\" Batou is left with a doubt in his mind. Could Motoko be the Puppeteer?\n\nThe series of intriguing incidents that Section 9 faces gradually link together almost artistically. Who is the Puppeteer? What will happen to Batou's relationship with Motoko? What is the full truth behind this carefully planned perfect crime? And what will the outcome be? Mysteries surround the Solid State Society...\n\n(Source: Production I.G.)"}, {"id": 12859, "name_en": "One Piece Film: Z", "name_jp": "ワンピース　フィルム　﻿Ｚ", "image_url": "https://cdn.myanimelist.net/images/anime/6/44297.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Toei Animation"], "year": 2012, "episodes": 1, "source": "Manga", "score": 8.1, "synopsis": "The Straw Hat Pirates enter the rough seas of the New World in search of the hidden treasures of the Pirate King, Gol D. Roger－One Piece. On their voyage, the pirates come across a terrifying, powerful man, former Marine Admiral Z.\n\nZ is accused of having stolen the \"Dyna Stones\", weapons believed to have the power to shake up the New World. The Marine Headquarters believes Z is about to use it to end the pirate era, and with it, the lives of many innocent people. In fear of such a phenomenal event, marines start to take action against the former admiral.\n\nEven if it means stumbling upon marines and the navy, the Straw Hat Pirates decided to chase after Z and stop him from causing havoc. As they continue to embark on their ventures, the pirates bump into new and familiar acquaintances."}, {"id": 50380, "name_en": "Ya Boy Kongming!", "name_jp": "パリピ孔明", "image_url": "https://cdn.myanimelist.net/images/anime/1970/122297.jpg", "genres": ["Comedy"], "themes": ["Adult Cast", "Music", "Reincarnation", "Showbiz"], "demographics": [], "studios": ["P.A. Works"], "year": 2022, "episodes": 12, "source": "Web manga", "score": 8.1, "synopsis": "Zhuge Kongming earned a reputation as one of the greatest tacticians of the Three Kingdoms period of third-century China. Having led his army through countless grueling victories, Kongming falls gravely ill during the Battle of Wuzhang Plains. The weariness and regret stemming from the seemingly never-ending war catch up to him, and as he draws his final breath, Kongming wishes that if he were to reincarnate, he would be reborn in a more peaceful era.\n\nHis wish comes true, and Kongming wakes up in modern-day Tokyo with a younger body and his memories intact. Thrust into an unfamiliar world, he finds his way into a nightclub and meets Eiko Tsukimi, an aspiring singer whose performance immediately captivates him. Pitying his confusion, Eiko takes Kongming under her wing and teaches him about the current world, which leads to Kongming's interest in contemporary music. Seeing Eiko's immense musical potential, Kongming vows to make the world recognize her and soon takes on the role of her manager.\n\nUnsurprisingly, the music industry is unforgiving to those who make even the slightest mistakes. Still, Kongming is determined to accomplish his goals—even if he must utilize the war stratagems he famously used in his previous life!\n\n[Written by MAL Rewrite]"}, {"id": 2581, "name_en": "Mobile Suit Gundam 00", "name_jp": "機動戦士ガンダム00", "image_url": "https://cdn.myanimelist.net/images/anime/3/13200.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["Mecha", "Military", "Space"], "demographics": [], "studios": ["Sunrise"], "year": 2007, "episodes": 25, "source": "Original", "score": 8.1, "synopsis": "In the distant future, mankind's dependence on fossil fuels will lead to their complete depletion, an energy crisis unlike anything the world witnessed. Out of retaliation and fear, humanity began focusing at an alternative source of energy: solar power. Different nations have united together to form three major factions—the Union of Solar Energy and Free Nations, the Advanced European Union, and the Human Reform League. Each of these sectors has access to a solar power generator, which gives them limitless energy.\n \nAs a result, countries that were once dependent on the sale of fossil fuels are now plunged in poverty, leading to years of warfare and internal strife over the control of solar energy. Amid this chaos, an unknown paramilitary organization appeared identifying themselves as \"Celestial Being,\" aspire to end all warfare through armed intervention by using mysterious and technologically advanced Mobile Suits known as Gundams.\n\nMobile Suit Gundam 00 follows the story of Celestial Being's Gundam Meisters Setsuna F. Seiei, Lockon Stratos, Allelujah Haptism, and Tieria Erde. These four dive into the devastating battle between the three superpowers to accomplish their goal of changing the world.\n\n[Written by MAL Rewrite]"}, {"id": 35608, "name_en": "Love, Chunibyo & Other Delusions!: Take On Me", "name_jp": "映画 中二病でも恋がしたい！-Take On Me-", "image_url": "https://cdn.myanimelist.net/images/anime/2/89974.jpg", "genres": ["Comedy", "Drama", "Romance"], "themes": ["School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2018, "episodes": 1, "source": "Light novel", "score": 8.1, "synopsis": "Although already a third-year high school student, Rikka Takanashi remains a chuunibyou—a \"disease\" that causes people to fantasize about themselves and their surroundings. Her relationship with Yuuta Togashi has also gone unchanged for the past six months, and with entrance exams right around the corner, both of them strive to enroll at the same college. However, Tooka—Rikka's elder sister—decides to take Rikka to Italy as she has found a stable job there. This unforeseen turn of events causes a commotion between the couple as neither of them want to be separated from each other. Desperate for ideas, they seek assistance from their friends, and after a brief conversation, they come up with a plan—to elope.\n\nChuunibyou demo Koi ga Shitai! Movie: Take On Me is a sensational drama featuring the couple—Yuuta and Rikka—as they journey across Japan. The two attempt to prevent Rikka from being taken to Italy, but will they be able to succeed in doing so?\n\n[Written by MAL Rewrite]"}, {"id": 60048, "name_en": "Bleach 20th Anime Anniversary", "name_jp": "Bleach 20th PV", "image_url": "https://cdn.myanimelist.net/images/anime/1357/145984.jpg", "genres": ["Action", "Supernatural"], "themes": [], "demographics": ["Shounen"], "studios": ["Pierrot Films"], "year": 2024, "episodes": 1, "source": "Manga", "score": 8.09, "synopsis": "In celebration of 20 years of Bleach, the PV is a compilation of newly reworked scenes from Bleach containing new animation."}, {"id": 59898, "name_en": "City The Animation", "name_jp": "CITY THE ANIMATION", "image_url": "https://cdn.myanimelist.net/images/anime/1829/145678.jpg", "genres": ["Comedy"], "themes": ["Gag Humor"], "demographics": ["Seinen"], "studios": ["Kyoto Animation"], "year": 2025, "episodes": 13, "source": "Manga", "score": 8.09, "synopsis": "Aimless daydreaming in high school is an ordinary part of growing up. But once graduation comes, suddenly there are bills to pay and a higher education to tackle. Even so, Midori Nagumo never quite stopped dreaming—even after entering college—as she still wants to have fun while ignoring her responsibilities. Contrasting Midori's recklessness is her supposed best friend Niikura, who is the complete opposite of Midori and has a goal of becoming a photographer after college.\n\nHowever, Niikura is often pulled into Midori's schemes, which leads them to meet Wako Izumi, a tenacious, yet airheaded classmate who is always taking pictures of things she finds fun. The trio ends up living together in the same apartment and attempt to navigate their lives in the city of City, where unexpected and out-of-the-ordinary things occur daily.\n\n[Written by MAL Rewrite]"}, {"id": 58689, "name_en": "Genshin Impact: The Song Burning in the Embers", "name_jp": "《原神》「烬中歌」", "image_url": "https://cdn.myanimelist.net/images/anime/1229/142648.jpg", "genres": ["Action", "Drama", "Fantasy"], "themes": [], "demographics": [], "studios": [], "year": 2024, "episodes": 1, "source": "Game", "score": 8.09, "synopsis": "Animated short film about the backstory of Arlecchino from Genshin Impact."}, {"id": 57524, "name_en": "Makeine: Too Many Losing Heroines!", "name_jp": "負けヒロインが多すぎる！", "image_url": "https://cdn.myanimelist.net/images/anime/1332/143513.jpg", "genres": ["Comedy", "Romance"], "themes": ["School"], "demographics": [], "studios": ["A-1 Pictures"], "year": 2024, "episodes": 12, "source": "Light novel", "score": 8.09, "synopsis": "Despite not understanding much about fleeting teen romance, first-year high school student Kazuhiko Nukumizu still wonders how he would react if his life were to be turned into a love story. Regardless, as a self-proclaimed \"background character,\" Nukumizu is satisfied continuing his life as an introvert with a negligible social life. However, he suddenly finds himself too close to the spotlight when he witnesses his popular classmate Anna Yanami be rejected by her childhood friend in the middle of a family restaurant. \n\nWhile Nukumizu wishes he could just forget what he saw and move on, Anna ends up forcefully confiding herself in Nukumizu, lamenting her status as a childhood friend fated to have her beloved stolen. As he becomes dragged into Anna's situation, Nukumizu soon gets caught up in the relationship drama of two more girls: Lemon Yakishio, an outgoing member of the track and field club; and Chika Komari, a shy member of the literature club. Now thrust out of his comfort zone, Nukumizu finds himself a major character in the lives of too many losing heroines. \n\n[Written by MAL Rewrite]"}, {"id": 52826, "name_en": "Tsurune - The Linking Shot -", "name_jp": "ツルネ ―つながりの一射―", "image_url": "https://cdn.myanimelist.net/images/anime/1803/132337.jpg", "genres": ["Sports"], "themes": ["School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2023, "episodes": 13, "source": "Light novel", "score": 8.09, "synopsis": "Despite the prefectural tournament's breathtaking conclusion, the Kazemai High School Kyudo Club has no time to celebrate. With the national competition right around the corner, the club is determined to prove that their initial victory was not a mere fluke.\n\nWith his passion for the sport reignited, Minato Narumiya earnestly prepares for the upcoming competition with his teammates, who are equally motivated to win. As the much-anticipated tournament begins, the members of Kazemai's kyudo team fight to stay on target with their ambitious goal. But when old acquaintances reappear as difficult opponents, the boys find that winning the nationals will require more dedication and effort than ever before.\n\n[Written by MAL Rewrite]"}, {"id": 17739, "name_en": "Hidamari Sketch: Sae & Hiro Graduation", "name_jp": "ひだまりスケッチ 沙英ヒロ 卒業編", "image_url": "https://cdn.myanimelist.net/images/anime/1913/146300.jpg", "genres": ["Slice of Life"], "themes": ["CGDCT", "Iyashikei", "School", "Visual Arts"], "demographics": [], "studios": ["Shaft"], "year": 2013, "episodes": 2, "source": "4-koma manga", "score": 8.09, "synopsis": "The day has come for Sae and Hiro to graduate, who are now in their last days as High School students. The first episode centers on the final exams and the university entrance tests, while the second episode centers on the graduation of Sae and Hiro."}, {"id": 32122, "name_en": "Gintama°: Umai-mono wa Atomawashi ni Suru to Yokodorisareru kara Yappari Saki ni Kue", "name_jp": "銀魂 〜美味いモノは後回しにすると横取りされるからやっぱり先に食え〜", "image_url": "https://cdn.myanimelist.net/images/anime/1720/110926.jpg", "genres": ["Action", "Comedy"], "themes": ["Gag Humor", "Historical", "Parody", "Samurai"], "demographics": ["Shounen"], "studios": ["Bandai Namco Pictures"], "year": 2015, "episodes": 1, "source": "Manga", "score": 8.09, "synopsis": "After overcoming monumental obstacles in their previous adventures, Gintoki Sakata, Shinpachi Shimura, and Kagura gather at the Yorozuya office to discuss important matters. The trio, accompanied by their hilarious and eccentric friends, reveals a long-awaited notice: the biggest Gintama arc is coming to the big screens!\n\n[Written by MAL Rewrite]"}, {"id": 6746, "name_en": "Durarara!!", "name_jp": "デュラララ!!", "image_url": "https://cdn.myanimelist.net/images/anime/10/71772.jpg", "genres": ["Action", "Mystery", "Supernatural"], "themes": [], "demographics": [], "studios": ["Brain's Base"], "year": 2010, "episodes": 24, "source": "Light novel", "score": 8.09, "synopsis": "In Tokyo's downtown district of Ikebukuro, amidst many strange rumors and warnings of anonymous gangs and dangerous occupants, one urban legend stands out above the rest—the existence of a headless \"Black Rider\" who is said to be seen driving a jet-black motorcycle through the city streets.\n\nMikado Ryuugamine has always longed for the excitement of the city life, and an invitation from a childhood friend convinces him to move to Tokyo. Witnessing the Black Rider on his first day in the city, his wishes already seem to have been granted. But as supernatural events begin to occur, ordinary citizens like himself, along with Ikebukuro's most colorful inhabitants, are mixed up in the commotion breaking out in their city.\n\n[Written by MAL Rewrite]"}, {"id": 16918, "name_en": "Silver Spoon", "name_jp": "銀の匙", "image_url": "https://cdn.myanimelist.net/images/anime/6/49237.jpg", "genres": ["Comedy"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["A-1 Pictures"], "year": 2013, "episodes": 11, "source": "Manga", "score": 8.09, "synopsis": "Yuugo Hachiken is studious, hard-working, and tired of trying to live up to expectations he just cannot meet. With the ushering in of a brand new school year, he decides to enroll in Ooezo Agricultural High School, a boarding school located in the Hokkaido countryside, as a means to escape from the stress brought upon by his parents.\n \nInitially convinced that he would do well at this institution, Hachiken is quickly proven wrong by his talented classmates, individuals who have been living on farms their entire lives and know just about everything when it comes to food, vegetables, and even the physiology of livestock! Whether it be waking up at five in the morning for strenuous labor or to take care of farm animals, Hachiken is a complete amateur when it comes to the harsh agricultural life.\n \nGin no Saji follows the comedic story of a young student as he tries to fit into a completely new environment, meeting many unique people along the way. As he struggles to appreciate his surroundings, Hachiken hopes to discover his dreams, so that he may lead a fulfilling life on his own terms.\n\n[Written by MAL Rewrite]"}, {"id": 31051, "name_en": "Kuroko’s Basketball: It is the Best Present", "name_jp": "黒子のバスケ〜最高のプレゼントです〜", "image_url": "https://cdn.myanimelist.net/images/anime/13/77323.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2015, "episodes": 1, "source": "Manga", "score": 8.09, "synopsis": "It is Tetsuya Kuroko's birthday. In celebration, Satsuki Momoi has invited the other members of the Generation of Miracles to play basketball together and rekindle the memories from their nostalgic middle school days. Meanwhile, Taiga Kagami and his fellow teammates of Seirin High are doing their best to prepare for Kuroko's birthday party.\n\nSometimes, the best gift one can receive is not a material one but, rather, the feeling of having fun with everyone!\n\n[Written by MAL Rewrite]"}, {"id": 1382, "name_en": "GaoGaiGar Final", "name_jp": "勇者王ガオガイガーFINAL", "image_url": "https://cdn.myanimelist.net/images/anime/1187/132740.jpg", "genres": ["Adventure", "Drama", "Sci-Fi"], "themes": ["Mecha", "Super Power"], "demographics": [], "studios": ["Sunrise"], "year": 2000, "episodes": 8, "source": "Original", "score": 8.09, "synopsis": "Following Gutsy Galaxy Guard's victory over the Zonder Empire and the 31 Primevals, a new threat makes their appearance on Earth. GGG—with Guy Shishio and the newly-constructed GaoFighGar—team up with their French counterpart Chasseur to battle the evil organization BioNet. Among Chasseur's ranks is Renais Cardiff Shishioh, a former BioNet cyborg with the relentless pursuit of destroying those who took away her humanity. But as GGG and Chasseur fight the BioNet, GGG's bases around the world are suddenly attacked, and the recently-discovered \"Q-Parts\" and the original Gao Machines are stolen by Mamoru Amami, who has been abducted and cloned by an even greater threat known as the \"Eleven Kings of Sol.\" Evicted from Earth by the United Nations, GGG must now travel to the far reaches of the galaxy to battle the Eleven Kings of Sol and save Mamoru before it's too late.\n\n(Source: ANN, edited)"}, {"id": 3702, "name_en": "Detroit Metal City", "name_jp": "デトロイト・メタル・シティ", "image_url": "https://cdn.myanimelist.net/images/anime/3/9853.jpg", "genres": ["Comedy"], "themes": ["Adult Cast", "Gag Humor", "Music", "Parody", "Showbiz"], "demographics": ["Seinen"], "studios": ["Studio 4°C"], "year": 2008, "episodes": 12, "source": "Manga", "score": 8.09, "synopsis": "Dominating the world of indie music, Detroit Metal City (DMC) is a popular death metal band known for its captivatingly dark and crude style. Its extravagant lead singer, Johannes Krauser II, is especially infamous as a demonic being who has risen from the fiery pits of hell itself in order to bring the world to its knees and lord over all mortals—or at least that's what he's publicized to be.\n\nUnbeknownst to his many worshippers, Krauser II is just the alter ego of an average college graduate named Souichi Negishi. Although he is soft-spoken, peace-loving, and would rather listen to Swedish pop all day, he must participate in DMC's garish concerts in order to make ends meet. Detroit Metal City chronicles Negishi's hilarious misadventures as he attempts to juggle his hectic band life, a seemingly budding romance, and dealing with his incredibly obsessive and dedicated fans.\n\n[Written by MAL Rewrite]"}, {"id": 32902, "name_en": "The Ancient Magus' Bride: Those Awaiting a Star", "name_jp": "魔法使いの嫁 星待つひと", "image_url": "https://cdn.myanimelist.net/images/anime/4/80587.jpg", "genres": ["Drama", "Fantasy"], "themes": ["Urban Fantasy"], "demographics": ["Shounen"], "studios": ["Wit Studio"], "year": 2016, "episodes": 3, "source": "Manga", "score": 8.09, "synopsis": "Angelica sends Chise some magic supplies including a present. The present turns out to be a book Chise read in her childhood. Elias asks Chise to tell him the story behind the book. Chise tells him about Miura-san and the mysterious library she found in the forest as a child.\n\n[Written by MAL Rewrite]"}, {"id": 46569, "name_en": "Hell's Paradise", "name_jp": "地獄楽", "image_url": "https://cdn.myanimelist.net/images/anime/1075/131925.jpg", "genres": ["Action", "Adventure", "Supernatural"], "themes": ["Gore", "Historical", "Samurai"], "demographics": ["Shounen"], "studios": ["MAPPA"], "year": 2023, "episodes": 13, "source": "Manga", "score": 8.09, "synopsis": "Sentenced to death, ninja Gabimaru the Hollow finds himself apathetic. After leading a blood-soaked life, Gabimaru believes he deserves to die. However, every attempt to execute him inexplicably fails. Finally, Sagiri Yamada Asaemon, a fledgling member of a famed executioner clan, is asked to take Gabimaru's life; yet Sagiri makes no move to kill him as requested.\n\nInsisting that Gabimaru will not die because of his love for his wife, Sagiri instead offers him the chance to obtain a full pardon for his crimes. If he can travel to the island of Shinsekyo and obtain the Elixir of Life—which supposedly grants immortality—and bring it back for the shogun, then his freedom will be assured.\n\nBut of the many who have traveled to Shinsekyo in search of the mythical Elixir, not a single person has returned sound of mind, if at all. Though unaware of the numerous dangers ahead, Gabimaru decides to accept the offer—alongside ten other death row convicts—in hope that he and his wife may finally live in peace.\n\n[Written by MAL Rewrite]"}, {"id": 30831, "name_en": "KonoSuba: God's Blessing on This Wonderful World!", "name_jp": "この素晴らしい世界に祝福を！", "image_url": "https://cdn.myanimelist.net/images/anime/1895/142748.jpg", "genres": ["Adventure", "Comedy", "Fantasy"], "themes": ["Isekai", "Parody"], "demographics": [], "studios": ["Studio Deen"], "year": 2016, "episodes": 10, "source": "Light novel", "score": 8.09, "synopsis": "After dying a laughable and pathetic death on his way back from buying a game, high school student and recluse Kazuma Satou finds himself sitting before a beautiful but obnoxious goddess named Aqua. She provides the NEET with two options: continue on to heaven or reincarnate in every gamer's dream—a real fantasy world! Choosing to start a new life, Kazuma is quickly tasked with defeating a Demon King who is terrorizing villages. But before he goes, he can choose one item of any kind to aid him in his quest, and the future hero selects Aqua. But Kazuma has made a grave mistake—Aqua is completely useless!\n\nUnfortunately, their troubles don't end here; it turns out that living in such a world is far different from how it plays out in a game. Instead of going on a thrilling adventure, the duo must first work to pay for their living expenses. Indeed, their misfortunes have only just begun!\n\n[Written by MAL Rewrite]"}, {"id": 2236, "name_en": "The Girl Who Leapt Through Time", "name_jp": "時をかける少女", "image_url": "https://cdn.myanimelist.net/images/anime/1/2432.jpg", "genres": ["Award Winning", "Drama", "Romance", "Sci-Fi"], "themes": ["School", "Time Travel"], "demographics": [], "studios": ["Madhouse"], "year": 2006, "episodes": 1, "source": "Novel", "score": 8.09, "synopsis": "Makoto Konno is in her last year of high school, but is having a hard time deciding what to do with her future. In between enduring the pressure of her teachers and killing time with her best friends, Makoto's life suddenly changes when she accidentally discovers that she is capable of literally leaping through time.\n\nToki wo Kakeru Shoujo follows Makoto as she plays around with her newfound power. However, she soon learns the hard way that every choice has a consequence, and time is a lot more complicated than it may seem.\n\n[Written by MAL Rewrite]"}, {"id": 18661, "name_en": "Kamisama Kiss OVA", "name_jp": "神様はじめました", "image_url": "https://cdn.myanimelist.net/images/anime/6/56425.jpg", "genres": ["Comedy", "Romance", "Supernatural"], "themes": ["Mythology"], "demographics": ["Shoujo"], "studios": ["TMS Entertainment"], "year": 2013, "episodes": 2, "source": "Manga", "score": 8.08, "synopsis": "Kamisama, Suterareru\nRiding the high of a heartfelt kiss she shared with her familiar Tomoe, Nanami Momozono seems like nothing can bring her down. However, that changes when she witnesses Tomoe receive a mysterious black box and claim that possessing it, he no longer needs Nanami. By creating the perfect diversion, Nanami is determined to find out what this box contains.\n\nKamisama, Onsen ni Iku\nAfter Nanami overhears her familiar Mizuki talk about her possibly being too spoiled by Tomoe, she becomes hell-bent on proving her capability to be independent. During her trip with Tomoe to a hot spring hotel, Nanami learns about a stone that allegedly grants a lifetime of happiness to couples who touch it together. However, the long path to the stone may prove a bit overwhelming as long as Nanami refuses Tomoe's help.\n\n[Written by MAL Rewrite]"}, {"id": 31658, "name_en": "Kuroko's Basketball the Movie: Last Game", "name_jp": "劇場版 黒子のバスケ LAST GAME", "image_url": "https://cdn.myanimelist.net/images/anime/2/83106.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2017, "episodes": 1, "source": "Manga", "score": 8.08, "synopsis": "Hailing from America, Jabberwock—a street basketball team with skills comparable to those of the NBA—has come to Japan to play an exhibition match against Strky, a team of former third-year students who once played in the Interhigh and Winter Cup. However, due to the vast difference in skill, Jabberwock easily wins. Their captain, Nash Gold Jr., mocks the basketball style of all players in Japan by comparing them to monkeys.\n \nInfuriated by the nasty comment, Kagetora Aida challenges them to a revenge match. Because of pride and the belief that the results will be no different, Nash accepts the challenge. Kagetora then assembles Vorpal Swords, a team composed of the Generation of Miracles, including Kuroko Tetsuya and Kagami Taiga, for they are the only ones who stand a chance against a foe that seems unbeatable from every angle.\n\n[Written by MAL Rewrite]"}, {"id": 40815, "name_en": "Ascendance of a Bookworm Season 2", "name_jp": "本好きの下剋上 ～司書になるためには手段を選んでいられません～ 第2期", "image_url": "https://cdn.myanimelist.net/images/anime/1464/107998.jpg", "genres": ["Fantasy"], "themes": ["Isekai", "Reincarnation"], "demographics": [], "studios": ["Ajia-do"], "year": 2020, "episodes": 12, "source": "Light novel", "score": 8.08, "synopsis": "When Myne learns that the Holy Church is in need of mana for their relics, she sees it as her chance to be cured of her life-threatening mana disorder. After seeing their bountiful library, she throws herself headfirst into the Church's grasp and begs to join their order. In exchange for her service and her unusually bountiful supply of mana, Myne is given the blue robes of a noble-born apprentice priestess, despite being a commoner. To Myne, all this talk of mana and nobility is trivial, as she now has access to an unlimited supply of books!\n\nAs Myne transitions into the next phase of her life in this new world, she soon learns that achieving her dream has come at a heavy cost. Noble society is severe, unforgiving, and fueled by politics and neglect. She must now deal with the class conflict between the noble-born blue robes and the common-born grey robes, the High Priest's attempts to oust her, and constant behavioral issues from her new retainers. With the help of her family, friends, and the enigmatic Head Priest whose loyalties and motives remain unknown, Myne seeks to overcome these obstacles and continue on the path to becoming her ideal self—the ultimate librarian!\n\n[Written by MAL Rewrite]"}, {"id": 23283, "name_en": "Terror in Resonance", "name_jp": "残響のテロル", "image_url": "https://cdn.myanimelist.net/images/anime/1417/117422.jpg", "genres": ["Mystery", "Suspense"], "themes": ["Detective", "Psychological"], "demographics": [], "studios": ["MAPPA"], "year": 2014, "episodes": 11, "source": "Original", "score": 8.08, "synopsis": "Painted in red, the word \"VON\" is all that is left behind after a terrorist attack on a nuclear facility in Japan. The government is shattered by their inability to act, and the police are left frantically searching for ways to crack down the perpetrators. The public are clueless—until, six months later, a strange video makes its way onto the internet. In it, two teenage boys who identify themselves only as \"Sphinx\" directly challenge the police, threatening to cause destruction and mayhem across Tokyo. Unable to stop the mass panic quickly spreading through the city and desperate for any leads in their investigation, the police struggle to act effectively against these terrorists, with Detective Kenjirou Shibazaki caught in the middle of it all.\n\nZankyou no Terror tells the story of Nine and Twelve, the two boys behind the masked figures of Sphinx. They should not exist, yet they stand strong in a world of deception and secrets while they make the city fall around them, all in the hopes of burying their own tragic truth.\n\n[Written by MAL Rewrite]"}, {"id": 6336, "name_en": "Mobile Suit Gundam Unicorn", "name_jp": "機動戦士ガンダムUC（ユニコーン）", "image_url": "https://cdn.myanimelist.net/images/anime/12/50459.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["Mecha", "Military", "Space"], "demographics": [], "studios": ["Sunrise"], "year": 2010, "episodes": 7, "source": "Novel", "score": 8.08, "synopsis": "In the year Universal Century 0096, three years after Char Aznable's failed attempt to force human migration into space, life continues in the colonies orbiting Earth. One such colony, at Side 4, is home to Banagher Links, a 16-year-old who lives a quiet life among his classmates.\n\nAudrey Burne, the last descendant of a great tyrannical family, takes it upon herself to steal the key to a mysterious device known as \"Laplace's Box.\" It is said that the Box has the power to shape the course of the universe, and Audrey travels to Side 4 in an attempt to take it from its current holder and keep it from the Sleeves, the surviving remnant of Char Aznable's  Neo-Zeon. In her search, she stumbles across Banagher and changes his life forever.\n\nWhen Side 4 comes under the attack of the Sleeves and its prolific fighters Marida Cruz and Full Frontal, Banagher takes control of the newly built Gundam Unicorn to defend his friends and protect the fate of humankind.\n\n[Written by MAL Rewrite]"}, {"id": 2951, "name_en": "Gintama: Jump Festa 2005 Special", "name_jp": "銀魂〜何事も最初が肝心なので多少背伸びするくらいが丁度良い〜", "image_url": "https://cdn.myanimelist.net/images/anime/1897/139500.jpg", "genres": ["Comedy", "Sci-Fi"], "themes": ["Historical", "Mecha", "Parody", "Samurai"], "demographics": ["Shounen"], "studios": ["Sunrise"], "year": 2005, "episodes": 1, "source": "Manga", "score": 8.08, "synopsis": "Due to the arrival of aliens called the \"Amanto,\" the samurai of feudal Japan have fallen into misery and despair. Denied their swords and stature, they are now treated as an object of pity and utter disregard while the Amanto mercilessly terrorizes the country.\n\nTo restore the reputation of the samurai, the Anti-Foreigner Faction leader Kotarou Katsura resolves to eradicate the Amanto—even if he must resort to violent methods. With a giant mecha at his disposal, he plans to initiate an attack against the Shinsengumi, an army of skillful samurai dedicated to protecting the peace of Edo.\n\nMeanwhile, Yorozuya owner Gintoki Sakata and his friends are out to see the cherry blossoms. However, they are interrupted when the Shinsengumi show up, claiming Gintoki has stolen their spot for flower-viewing. Quickly spiraling into a frenzied quarrel, the two groups settle the fight through a game of rock-paper-scissors—albeit with rather brutal methods.\n\n[Written by MAL Rewrite]"}, {"id": 12069, "name_en": "Fighting Spirit Special", "name_jp": "はじめの一歩 THE FIGHTING! ボクサーの拳", "image_url": "https://cdn.myanimelist.net/images/anime/2/84664.jpg", "genres": ["Sports"], "themes": ["Combat Sports"], "demographics": ["Shounen"], "studios": ["Madhouse"], "year": 2003, "episodes": 1, "source": "Manga", "score": 8.08, "synopsis": "A special episode included in the DVDs. It tells the story of how Takamura started boxing."}, {"id": 28025, "name_en": "Tsukimonogatari", "name_jp": "憑物語", "image_url": "https://cdn.myanimelist.net/images/anime/6/68259.jpg", "genres": ["Comedy", "Mystery", "Supernatural", "Ecchi"], "themes": ["Vampire"], "demographics": [], "studios": ["Shaft"], "year": 2014, "episodes": 4, "source": "Light novel", "score": 8.08, "synopsis": "Koyomi Araragi is studying hard in preparation for his college entrance exams when he begins to notice something very strange: his reflection no longer appears in a mirror, a characteristic of a true vampire. Worried about the state of his body, he enlists the help of the human-like doll Yotsugi Ononoki and her master Yozuru Kagenui, an immortal oddity specialist.\n\nQuickly realizing what is wrong with him, Kagenui gives Araragi two choices: either abstain from using the vampiric abilities he received from Shinobu Oshino, or lose his humanity forever.\n\n[Written by MAL Rewrite]"}, {"id": 59841, "name_en": "Solo Leveling: ReAwakening", "name_jp": "俺だけレベルアップな件 -ReAwakening-", "image_url": "https://cdn.myanimelist.net/images/anime/1983/146190.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Adult Cast", "Urban Fantasy"], "demographics": [], "studios": ["A-1 Pictures"], "year": 2024, "episodes": 1, "source": "Web manga", "score": 8.07, "synopsis": "Ever since dangerous gates leading to dungeons filled with monsters appeared on Earth a decade ago, humans gained a new way to provide for themselves: certain individuals designated as hunters received otherworldly powers to fight the dangers within. Sung Jin-Woo works desperately as a hunter to keep his family afloat. However, due to his lack of power, he is known as the weakest hunter of all mankind.\n\nDuring a dungeon expedition with his party, an unexpected disaster occurs, leaving Jin-Woo on the brink of death. Inexplicably surviving the grave situation, he wakes up in a hospital after the incident with a surprise—he has been selected as a player by a mysterious system, offering him possibilities to improve his skills. As he is seemingly the only one who has been chosen, Jin-Woo begins to level up at a fast pace, and he might just be able to become the world's strongest hunter with his reawakening.\n\n[Written by MAL Rewrite]"}, {"id": 36369, "name_en": "Legend of the Galactic Heroes: Die Neue These - Stellar War Part 1", "name_jp": "銀河英雄伝説 Die Neue These 星乱", "image_url": "https://cdn.myanimelist.net/images/anime/1772/102667.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["Adult Cast", "Military", "Space"], "demographics": [], "studios": ["Production I.G"], "year": 2019, "episodes": 4, "source": "Novel", "score": 8.07, "synopsis": "The Free Planets Alliance's invasion of the Galactic Empire has failed after supply mismanagement led to heavy losses. Unwilling to abort the expedition despite being greatly outnumbered, Fleet Admiral Lazar Lobos orders the remaining Alliance fleets to gather in the Amritsar Starzone for a final stand against the incoming counterattack.\n\nStrategic genius Fleet Admiral Reinhard von Lohengramm leads the Imperial forces into this decisive battle with an overwhelming advantage. However, his rival, tactical magician Vice Admiral Yang Wen-li of the Alliance, once again makes up for the incompetence of his superiors and stages a narrow escape for himself and his fellow soldiers. Though both Reinhard and Yang are displeased with the outcome—the former for his imperfect victory and the latter for the tremendous casualties—their attention must turn to the bubbling political unrest on both sides of the galaxy.\n\n[Written by MAL Rewrite]"}, {"id": 4722, "name_en": "Skip Beat!", "name_jp": "スキップ・ビート！", "image_url": "https://cdn.myanimelist.net/images/anime/11/84022.jpg", "genres": ["Comedy", "Drama", "Romance"], "themes": ["Love Polygon", "Showbiz"], "demographics": ["Shoujo"], "studios": ["HAL Film Maker"], "year": 2008, "episodes": 25, "source": "Manga", "score": 8.07, "synopsis": "Day in and day out, Kyouko Mogami works multiple jobs to support her childhood friend, Shoutarou \"Shou\" Fuwa, in his rise to stardom as an idol. She never complains about her way of life, as she is deeply infatuated with Shou and would go to the ends of the world for him. However, her heart is broken when she overhears Shou talk about how he views her as nothing more than a maid he cares little for. \n\nAngered over being used, Kyouko flies into a rage and swears to get revenge on her former crush by entering the world of showbiz to surpass him. Joining the relentless and unforgiving entertainment industry proves to be a challenge, as she continuously meets people who force her out of her comfort zone. But for the sake of vengeance, Kyouko is determined to push herself and rise to the top.\n\n[Written by MAL Rewrite]"}, {"id": 3927, "name_en": "Mobile Suit Gundam 00: Second Season", "name_jp": "機動戦士ガンダム00 セカンドシーズン", "image_url": "https://cdn.myanimelist.net/images/anime/9/12782.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["Mecha", "Military", "Space"], "demographics": [], "studios": ["Sunrise"], "year": 2008, "episodes": 25, "source": "Original", "score": 8.07, "synopsis": "In the year 2311 AD, a world that once saw itself full of inter-continental conflict now stands unified, led by the Earth Sphere Federation (ESF). The ESF established a preventative military task force known as the A-Laws, tasking them with shutting down violent terrorist organizations. As they gain more and more legal authority, the A-Laws begin to twist the law to fit their own agenda, ruling the citizens of Earth with a heavy hand.\n\nIn response to the fascistic behavior of the A-Laws, the anti-terrorist group Celestial Being reappears. Led by state-of-the-art mobile suits known as Gundam, the pilots of Celestial Being wage a new war with the A-Laws, aiming to stop their tyrannical abuse of power.\n\nSetsuna F. Seiei, pilot of the Gundam Exia, helps to lead the charge along with his fellow Gundam Meisters Lockon Stratos, Allelujah Haptism, and Tieria Erde. But in the process, Setsuna stumbles upon a conspiratorial plot spearheaded by a new faction, the Innovators, and must contend with his own old wounds and ghosts of the past in order to save a world that despises him.\n\n[Written by MAL Rewrite]"}, {"id": 38081, "name_en": "Girls und Panzer das Finale – Part 2", "name_jp": "『ガールズ＆パンツァー 最終章』第2話", "image_url": "https://cdn.myanimelist.net/images/anime/1240/119215.jpg", "genres": [], "themes": ["CGDCT", "Military", "School"], "demographics": [], "studios": ["Actas"], "year": 2019, "episodes": 1, "source": "Original", "score": 8.07, "synopsis": "Despite having the upper hand, BC Freedom High School suffers a thorough defeat at the hands of Ooarai Girls Academy. Following their victory, the Ooarai girls decide to take a break before the next round. After discovering that Momo Kawashima is taking care of her family while studying for the university entrance exams, the rest of the Ooarai team become more determined to win the Winter Continuous Track Cup for the sake of Momo and her family.\n\nMeanwhile, the second round of the tournament has started, and Ooarai now has to face Chi-Ha-Tan Academy. Having learned from the first round to not underestimate their enemies, Ooarai slowly approaches their opponent on the thick jungle battlefield.\n\nDespite their best attempts to be cautious, Ooarai is immediately put in a difficult situation as Chi-Ha-Tan employs several new strategies devised by Haru Fukuda. Finding themselves between a rock and a hard place, Ooarai must claw their way out to claim another comeback victory and keep their championship hopes alive.\n\n[Written by MAL Rewrite]"}, {"id": 66, "name_en": "Azumanga Daioh: The Animation", "name_jp": "あずまんが大王 THE ANIMATION", "image_url": "https://cdn.myanimelist.net/images/anime/1066/117358.jpg", "genres": ["Comedy"], "themes": ["CGDCT", "Gag Humor", "School"], "demographics": [], "studios": ["J.C.Staff"], "year": 2002, "episodes": 26, "source": "4-koma manga", "score": 8.07, "synopsis": "Chiyo Mihama begins her high school career as one of the strangest students in her freshman class—a tiny, 10-year-old academic prodigy with a fondness for plush dolls and homemade cooking. But her homeroom teacher, Yukari Tanizaki, is the kind of person who would hijack a student's bike to avoid being late, so \"strange\" is a relative word.\n\nThere certainly isn't a shortage of peculiar girls in Yukari-sensei's homeroom class. Accompanying Chiyo are students like Tomo Takino, an energetic tomboy with more enthusiasm than brains; Koyomi Mizuhara, Tomo's best friend whose temper has a fuse shorter than Chiyo; and Sakaki, a tall, athletic beauty whose intimidating looks hide a gentle personality and a painful obsession with cats. In addition, transfer student Ayumu Kasuga, a girl with her head stuck in the clouds, fits right in with the rest of the girls—and she has a few interesting theories about Chiyo's pigtails!\n\nTogether, this lovable group of girls experience the ups and downs of school life, their many adventures filled with constant laughter, surreal absurdity, and occasionally even touching commentary on the bittersweet, temporal nature of high school.\n\n[Written by MAL Rewrite]"}, {"id": 42429, "name_en": "Ascendance of a Bookworm Season 3", "name_jp": "本好きの下剋上 ～司書になるためには手段を選んでいられません～ 第3期", "image_url": "https://cdn.myanimelist.net/images/anime/1976/122302.jpg", "genres": ["Fantasy"], "themes": ["Isekai", "Reincarnation"], "demographics": [], "studios": ["Ajia-do"], "year": 2022, "episodes": 10, "source": "Light novel", "score": 8.07, "synopsis": "Winter is approaching, and Myne—now an apprentice priestess—must prepare for her stay at the church and the upcoming Dedication Ceremony. However, due to her immense knowledge and extraordinary amount of mana, she has garnered the attention of many dangerous people, who are willing to do anything to get their hands on Myne.\n\nTo keep her safe, the Head Priest assigns Myne a bodyguard and advises her to be adopted by a noble, a decision that will force her to leave her family behind. As Myne is opposed to the idea, the Head Priest gives her an ultimatum: she can be with her family until she turns 10, but if she is deemed too unstable, she will immediately be dealt with.\n\nPlaced in a tough position, Myne is uncertain about her future. Despite the twists that may lie ahead, she will do whatever she can to protect those that she loves—even if it means giving up on her dream.\n\n[Written by MAL Rewrite]"}, {"id": 39198, "name_en": "Astra Lost in Space", "name_jp": "彼方のアストラ", "image_url": "https://cdn.myanimelist.net/images/anime/1784/106428.jpg", "genres": ["Adventure", "Award Winning", "Mystery", "Sci-Fi", "Suspense"], "themes": ["Space", "Survival"], "demographics": ["Shounen"], "studios": ["Lerche"], "year": 2019, "episodes": 12, "source": "Manga", "score": 8.07, "synopsis": "In the year 2063, space travel is feasible and commercially available. As the cheerful Aries Spring arrives at the spaceport to attend a camp on the distant planet McPa, her purse is suddenly snatched by a reckless thief. Luckily, the athletic Kanata Hoshijima is able to retrieve it for her, and Aries soon discovers that he is among the group of teenagers who will be traveling with her on the excursion as team B-5.\n\nUpon arriving at their campsite, the group's trip takes a turn for the worse when a strange sphere of black light sucks them into the vast reaches of outer space. Stranded with seemingly no hope, they find an abandoned ship nearby that provides them with the means to return home. However, they soon discover that they are not as close to their campsite as they initially thought, but are in fact thousands of light-years away from home.\n\nWith this realization, the nine members must cautiously manage their resources, maintain their strength, and unite as one to conquer the darkness of space together. While the reason behind their trip's sudden obstruction remains unknown, they nevertheless embark on the treacherous voyage back home aboard their new ship, the Astra.\n\n[Written by MAL Rewrite]"}, {"id": 4938, "name_en": "Tsubasa RESERVoir CHRoNiCLE: Spring Thunder Chronicle", "name_jp": "ツバサ 春雷記", "image_url": "https://cdn.myanimelist.net/images/anime/9/33841.jpg", "genres": ["Action", "Adventure", "Drama", "Fantasy", "Romance"], "themes": [], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2009, "episodes": 2, "source": "Manga", "score": 8.07, "synopsis": "After the battle in Celes, Fai escapes with Kurogane's aid while helping Syaoran and Sakura as well. Now in the land of Nihon, Kurogane meets with Tomoyo once more. Seishirou appears and Syaoran battles him for the feather. After finally gaining the feather, he enters the Dream World where he meets Sakura, Watanuki and the Clone.\n\n(Source: Metacritic)"}, {"id": 3901, "name_en": "Baccano! Specials", "name_jp": "バッカーノ!", "image_url": "https://cdn.myanimelist.net/images/anime/7/9129.jpg", "genres": ["Action", "Mystery", "Supernatural"], "themes": ["Adult Cast", "Historical", "Organized Crime"], "demographics": [], "studios": ["Brain's Base"], "year": 2008, "episodes": 3, "source": "Light novel", "score": 8.07, "synopsis": "In 1929, Ladd Russo spared Graham Specter's life and earned his unwavering loyalty. Three years later, Graham is infuriated upon learning that Ladd was pushed off the train after the events aboard the Flying Pussyfoot. As a man of bizarre yet passionate philosophies, he plans to make an offering to Ladd by kidnapping Eve Genoard and subsequently capturing Jacuzzi Splot, who has been living in the Genoard mansion with his gang.\n\nMeanwhile, Jacuzzi and his gang take in an unfamiliar woman, Elmer C. Albatross pays a visit to the prison to meet an old friend, and Czeslaw Meyer runs into the man he hoped he would never see again.\n\nThese seemingly separate storylines merge, tying up several loose ends and revealing the whole truth of the anomaly that occurred aboard the Advena Avis in 1711.\n\n[Written by MAL Rewrite]"}, {"id": 50796, "name_en": "Insomniacs After School", "name_jp": "君は放課後インソムニア", "image_url": "https://cdn.myanimelist.net/images/anime/1402/134007.jpg", "genres": ["Romance"], "themes": ["School"], "demographics": ["Seinen"], "studios": ["LIDENFILMS"], "year": 2023, "episodes": 13, "source": "Manga", "score": 8.07, "synopsis": "High schooler Ganta Nakami has trouble falling asleep most nights. As a result, he is irritable at school, always searching for an opportunity to find a secluded place to doze off. On the other hand, Isaki Magari is a free spirit who is well liked by her friends, but no one is aware of her sleep disorder. She makes use of the school's abandoned astronomy club observatory as her secret sleeping bunker when she needs to get some shut-eye. \n\nAs fate would have it, Nakami finds Magari napping in the observatory. When Magari discovers that she and Nakami have something in common, she offers to share her secret sleeping spot with her fellow insomniac. As the two find warmth in each other's company, the struggles they face start to become easier to confront.\n\n[Written by MAL Rewrite]"}, {"id": 34076, "name_en": "The Eccentric Family 2", "name_jp": "有頂天家族2", "image_url": "https://cdn.myanimelist.net/images/anime/8/85354.jpg", "genres": ["Drama", "Supernatural"], "themes": ["Anthropomorphic", "Mythology"], "demographics": [], "studios": ["P.A. Works"], "year": 2017, "episodes": 12, "source": "Novel", "score": 8.07, "synopsis": "After uncovering the truth behind their father's untimely death, life for the four Shimogamo brothers returns to relative peace with each trying to live up to their father's greatness in their own way. For the eldest, Yaichirou, who aims to become the next Trick Magister and leader of the tanuki society, it starts with reinstating the popular shogi tournament. For Yajirou, it is restoring his former shapeshifting abilities, whilst little Yashirou is content with continuing his work at the family’s factory. But for the third son, Yasaburou, it simply means embracing the \"fool's blood\" he inherited from his father and living a carefree but interesting life. This, of course, includes hunting for the mysterious and elusive snake-like creature known as a tsuchinoko, and causing ripples of trouble at every turn.\n\nHowever, these ripples threaten to turn into waves with the return of Nidaime, the estranged son of the brothers' tengu teacher, Professor Akadama. Nidaime bears a grudge against not only his father, but his father's apprentice Benten as well. His loyalties suddenly brought into question, Yasaburou must use his tanuki wit to appease all sides without getting caught in the crossfire, before the delicate balance between human, tengu, and tanuki is overthrown and all hell breaks loose.\n\n[Written by MAL Rewrite]"}, {"id": 135, "name_en": "Hikaru no Go", "name_jp": "ヒカルの碁", "image_url": "https://cdn.myanimelist.net/images/anime/12/78317.jpg", "genres": ["Comedy", "Drama", "Supernatural"], "themes": ["School", "Strategy Game"], "demographics": ["Shounen"], "studios": ["Studio Pierrot"], "year": 2001, "episodes": 75, "source": "Manga", "score": 8.07, "synopsis": "While searching through his grandfather's attic, Hikaru Shindou stumbles upon an old go board. Touching it, he is greeted by a mysterious voice, and soon after falls unconscious. When he regains his senses, he discovers that the voice is still present and belongs to Fujiwara no Sai, the spirit of an ancient go expert. A go instructor for the Japanese Emperor in the Heian Era, Sai's passion for the game transcends time and space, allowing him to continue playing his beloved game as a ghostly entity. Sai's ultimate goal is to master a divine go technique that no player has achieved so far, and he seeks to accomplish this by playing the board game through Hikaru.\n\nDespite having no interest in board games, Hikaru reluctantly agrees to play, executing moves as instructed by Sai. However, when he encounters the young go prodigy Akira Touya, a passion for the game is slowly ignited within him. Inspired by his newfound rival, Hikaru's journey into the world of go is just beginning.\n\n[Written by MAL Rewrite]"}, {"id": 24833, "name_en": "Assassination Classroom", "name_jp": "暗殺教室", "image_url": "https://cdn.myanimelist.net/images/anime/5/75639.jpg", "genres": ["Action", "Comedy"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["Lerche"], "year": 2015, "episodes": 22, "source": "Manga", "score": 8.07, "synopsis": "Tucked in the mountains near the elite Kunugigaoka Middle School lies a small derelict building that houses the delinquents and dropouts of Class 3-E. Looked down upon by their peers, the students in this class appear to have little hope in advancing their academic careers. That is, until the national government tasks them with eliminating the greatest threat to their planet: their new teacher. \n\nHaving already destroyed the moon, the octopus-like professor—dubbed \"Koro-sensei\"—has now threatened to destroy the Earth by March of the following year. In light of their mission, the students have found that killing him is easier said than done. Not only can Koro-sensei move at speeds of up to Mach 20, but he can also resist almost every earthly weapon. Ironically, he also proves to be one of the best teachers Class 3-E has ever had. Training the class to excel in both their studies as students and skills as assassins, Koro-sensei is confident that his students' ingenuity and indomitable will could return them to the main campus. \n\nThrough trial and error, Nagisa Shiota, as well as the other students of Class 3-E, must figure out Koro-sensei's weaknesses—and fast, for the very fate of the world depends upon it.\n\n[Written by MAL Rewrite]"}, {"id": 59828, "name_en": "Shoshimin: How to Become Ordinary Season 2", "name_jp": "小市民シリーズ 第2期", "image_url": "https://cdn.myanimelist.net/images/anime/1224/148321.jpg", "genres": ["Mystery"], "themes": ["School"], "demographics": [], "studios": ["Lapin Track"], "year": 2025, "episodes": 12, "source": "Novel", "score": 8.06, "synopsis": "First-year student Takahiko Urino aspires to write something truly original for the school newspaper, Furnado Monthly. Encouraged by his girlfriend, Yuki Osanai—a seemingly average second-year student—Urino convinces the newspaper club to let him pursue an unusual story. A case of small-scale fires has escalated into serial arson, and Urino intends to predict the locations of future fires and identify the culprit. But his obsession with the case does not always reflect well on the club, and Osanai may not be so fond of her boyfriend's growing arrogance.\n\nMeanwhile, another supposedly unexceptional student, Jougorou Kobato, spends uneventful days with his girlfriend, Tokiko Nakamaru. However, when he witnesses a fire tied to a past case and Osanai herself, Kobato cannot help but slip back into his old, less-than-ordinary habits in search of the truth.\n\n[Written by MAL Rewrite]"}, {"id": 56980, "name_en": "Yatagarasu: The Raven Does Not Choose Its Master", "name_jp": "烏は主を選ばない", "image_url": "https://cdn.myanimelist.net/images/anime/1084/142685.jpg", "genres": ["Drama", "Fantasy"], "themes": ["Historical", "Mythology"], "demographics": [], "studios": ["Studio Pierrot"], "year": 2024, "episodes": 20, "source": "Novel", "score": 8.06, "synopsis": "In the Kingdom of Yamauchi, people have the ability to transform into three-legged crows known as yatagarasu. While the men plot over daily governance, women compete for the hand of Wakamiya, the crown prince who was selected as the Kinu, the servant to the Mountain God. Four young women arrive at the court to join the competition, including one named Asebi.\n\nWhile the kind and innocent Asebi sticks out like a sore thumb compared to the other women, the crown prince has to fight for his place in the clan. Despite being born both to a concubine and as a second son, the royal priests still chose Wakamiya to be the Kinu over his older brother. This decision created a rift between the siblings while enraging Wakamiya's stepmother in the process. \n\nWhen Yukiya, one of the teenage sons of the village chief, is sent to the palace to become Wakamiya's new attendant, the prince  is not pleased in the slightest. However, Yukiya's presence at the imperial courts is no mere coincidence.\n\n[Written by MAL Rewrite]"}, {"id": 56653, "name_en": "Ave Mujica: The Die is Cast", "name_jp": "BanG Dream! Ave Mujica", "image_url": "https://cdn.myanimelist.net/images/anime/1181/148235.jpg", "genres": ["Drama"], "themes": ["Music"], "demographics": [], "studios": ["SANZIGEN"], "year": 2025, "episodes": 13, "source": "Mixed media", "score": 8.06, "synopsis": "The day Sakiko Togawa's privileged life crumbled, she painfully broke her vow to CRYCHIC and left the band behind—an act that tore her heart in two. As days fade into nights, she struggles to make ends meet until a certain cherished memory is tainted, pushing her to the edge.\n\nDriven by a fractured sense of purpose and the need to reclaim what she lost, Sakiko forms Ave Mujica, a masked symphonic metal band. Its members are bound not by a shared goal but by the fragile threads of their divergent desires. Together, they weave a haunting masquerade of loss and rebirth, captivating audiences with a gothic musical stage play. But as the girls' ideals collide, the scars of their pasts rear their ugly heads. The brittle facade the girls have built threatens to collapse beneath them, bringing Sakiko to the verge of falling apart.\n\n[Written by MAL Rewrite]"}, {"id": 51836, "name_en": "Soul Land 2: The Peerless Tang Clan", "name_jp": "斗罗大陆Ⅱ绝世唐门", "image_url": "https://cdn.myanimelist.net/images/anime/1357/154563.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Martial Arts"], "demographics": [], "studios": ["Sparkly Key Animation Studio"], "year": 2023, "episodes": 156, "source": "Web novel", "score": 8.06, "synopsis": "A strange continent in another world.\n\nNo magic, no battle qi, no martial arts, only martial spirits. Ten thousand years after the Tang clan was founded on the Douluo continent, the Tang clan has declined to the verge of extinction.\n\nA whole new generation of proud talents has been born. Will the new generation of the Seven monsters of Shrek be able to reestablish the Tang Clan and compose a new tale of greatness for the unrivaled Tang Clan?\n\n(Source: IMDB)"}, {"id": 53887, "name_en": "Spy x Family Season 2", "name_jp": "SPY×FAMILY Season 2", "image_url": "https://cdn.myanimelist.net/images/anime/1506/138982.jpg", "genres": ["Action", "Comedy"], "themes": ["Childcare", "Super Power"], "demographics": ["Shounen"], "studios": ["Wit Studio", "CloverWorks"], "year": 2023, "episodes": 12, "source": "Manga", "score": 8.06, "synopsis": "With her ability to read minds, Anya Forger is the only one who knows the true identities of her unconventional family. Her pretend father Loid operates as an elite spy code-named Twilight; her mother Yor kills on demand as the assassin Thorn Princess; and their dog, Bond, possesses the gift of precognition. Although they hide the truth from each other, this pretense of a perfectly ordinary family provides Anya with the genuine love and warmth that she longed for as an orphan.\n\nOperation Strix—Loid's special mission to avoid potential war by gathering vital information and getting close to the powerful political figure, Donovan Desmond—is only possible if Anya plays her part right. She can either excel academically and become an Imperial Scholar at her prestigious school or make friends with Donovan's son, Damian. Neither is exactly easy, but with her adventurous attitude, Anya throws herself wholeheartedly into her mission as a Forger—all for the sake of international peace.\n\n[Written by MAL Rewrite]"}, {"id": 31149, "name_en": "Persona 3 the Movie: #4 Winter of Rebirth", "name_jp": "PERSONA3 THE MOVIE —#4 Winter of Rebirth—", "image_url": "https://cdn.myanimelist.net/images/anime/9/77828.jpg", "genres": ["Action", "Supernatural"], "themes": ["Super Power"], "demographics": [], "studios": ["A-1 Pictures"], "year": 2016, "episodes": 1, "source": "Game", "score": 8.06, "synopsis": "\"The time of destruction is coming ever closer without a doubt. It cannot be stopped.\"\nTo know death, to gaze at death, to face death.\n\nMakoto and his allies have grown through the many meetings and partings they have experienced.\n\nTheir fight might not be for the sake of the world, but for themselves. Even so, they have continued to fight, believing that there are peaceful days waiting at the end of the battle.\n\nHowever, a boy says that destruction is the fate that humanity must shoulder. The boy they believed to be their friend tells them the truth of the situation without an emotion on his face.\n\nThe season changes to winter. Makoto makes a decision in order to greet the spring that is waiting beyond...\n\n(Source: ANN)"}, {"id": 31251, "name_en": "Mobile Suit Gundam: Iron-Blooded Orphans", "name_jp": "機動戦士ガンダム 鉄血のオルフェンズ", "image_url": "https://cdn.myanimelist.net/images/anime/6/75879.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["Mecha", "Space"], "demographics": [], "studios": ["Sunrise"], "year": 2015, "episodes": 25, "source": "Original", "score": 8.06, "synopsis": "Over three hundred years have passed since the Calamity War, the great conflict between Earth and its outer space colonies. Now Earth is ruled over by four economic blocs, and the military organization Gjallarhorn is responsible for keeping the peace. Mars, on the other hand, depends heavily on Earth's economy.\n\nHorrified by the appalling living conditions that Mars' inhabitants have to bear, Kudelia Aina Bernstein, a young aristocrat from the Chryse Autonomous Region, gets involved in the Red Planet's independence movement. She hires the services of a local company, Chryse Guard Security (CGS), to escort her on the journey to Earth to negotiate economic conditions with the earthly bloc that controls the region. The Third Army Division—consisting of Mikazuki Augus, Orga Itsuka, and many other child soldiers—are chosen to protect her.\n\nWhen Gjallarhorn attacks the CGS facilities to assassinate the young revolutionary threatening their interests, Orga and his comrades must not let the attackers accomplish their goal—in fact, Gjallarhorn's actions might turn out to be the unintentional catalyst that leads the children to be the forgers of their own destiny.\n\n[Written by MAL Rewrite]"}, {"id": 40080, "name_en": "The King's Avatar: For the Glory", "name_jp": "全职高手之巅峰荣耀", "image_url": "https://cdn.myanimelist.net/images/anime/1960/102480.jpg", "genres": ["Action"], "themes": ["Video Game"], "demographics": [], "studios": ["Colored Pencil Animation"], "year": 2019, "episodes": 1, "source": "Web novel", "score": 8.06, "synopsis": "Two years ago, Glory started out as just another MMORPG, but its real-world influence has grown as more countries begin to recognize esports as an official sport. Now, in China, arrangements are being made to hold the first professional league of Glory, culminating in the Glory Championship.\n\nFriends Ye Qiu and Su Muqiu both aspire to make a career out of esports. However, due to personal reasons, Ye Qiu has been hesitant until now to chase his dream onto the national stage. When the two boys finally agree to form their own team, they are supported by Tao Xuan, their boss and the owner of Excellent Era internet cafe, after which they name their team. And so, Team Excellent Era sets their sights on defeating all opponents who stand in their way of reaching the ultimate summit of Glory.\n\n[Written by MAL Rewrite]"}, {"id": 22145, "name_en": "Black Butler: Book of Circus", "name_jp": "黒執事 Book of Circus", "image_url": "https://cdn.myanimelist.net/images/anime/6/64811.jpg", "genres": ["Action", "Mystery", "Supernatural"], "themes": ["Historical", "Mythology"], "demographics": ["Shounen"], "studios": ["A-1 Pictures"], "year": 2014, "episodes": 10, "source": "Manga", "score": 8.06, "synopsis": "Full of wonder and excitement, the Noah's Arc Circus troupe has captured audiences with their dazzling performances. Yet these fantastic acts do not come without a price. Children have mysteriously gone missing around London, correlating to that of the group's movements. Unsettled by these kidnappings, Queen Victoria sends in her notorious guard dog, Ciel Phantomhive; and his ever-faithful demon butler, Sebastian Michaelis, on an undercover mission to find these missing children.\n\nTrying to balance their new circus acts with their covert investigation under the big top, however, proves to be quite a challenge. With the other performers growing suspicious and the threat of the circus' mysterious benefactor looming overhead, what the two discover will shake Ciel to his very core.\n\n[Written by MAL Rewrite]"}, {"id": 44218, "name_en": "Swallowed Star", "name_jp": "吞噬星空", "image_url": "https://cdn.myanimelist.net/images/anime/1094/153667.jpg", "genres": ["Action", "Adventure", "Fantasy", "Sci-Fi"], "themes": ["Martial Arts"], "demographics": [], "studios": ["Sparkly Key Animation Studio"], "year": 2020, "episodes": 26, "source": "Web novel", "score": 8.06, "synopsis": "One day, an unknown RR virus appeared on earth, and the world fell into disaster. Infected animals mutate into frightening and massively attacking monsters. When humans face destruction, they build walls and build base cities as the last bastion of man. The trials experienced by mankind during this period of time are called the \"Period of Nirvana.\" In extreme living environments, human power is also gradually advancing and evolving, and martial arts are emerging, and human power is qualitatively increasing compared to before. And the best of them is called the Warriors. Luo Feng, 18, also aspires to be one of them. Currently, he will take the college entrance exam and face a choice at a crossroads in his life, but suddenly a monster attack affects the trajectory of his life. \n\nUnder the threat of powerful monsters, the inhabitants of the city are in danger, but the military is powerless. Only one soldier stood up and maintained the security of the base city. Luo Feng is infected by a warrior force, and secretly he decides to become a fighter to protect his loved ones. This is the beginning of everything, the starting point of Luo Feng's journey as a martial artist, and also the beginning of his legendary life. Luo Feng aspires to be a martial artist, but the road ahead is not smooth. The first thing he has to deal with is the influence of the outside environment that is not visible to him. Luo Feng's family's condition was alarming and life was difficult, his parents could not provide more help and could only rely on his own efforts. \n\nUltimately, under constant hard work, Luo Feng continues to explore his own potential and is recognized by his increased abilities and self-improvement. Not only that, Luo Feng not only shouldered the burden of supporting the family, but also joined other justice fighters to face evil monsters to protect the homeland of mankind and for the better survival and development of mankind. In a desperate doomsday situation, can Luo Feng and the other soldiers drive away the monsters and successfully protect the human world?\n\n(Source: Animexin)"}, {"id": 1257, "name_en": "Saint Seiya: The Hades Chapter - Sanctuary", "name_jp": "聖闘士星矢 冥王ハーデス十二宮編", "image_url": "https://cdn.myanimelist.net/images/anime/1182/122016.jpg", "genres": ["Action", "Adventure", "Fantasy", "Sci-Fi"], "themes": ["Mythology"], "demographics": ["Shounen"], "studios": ["Toei Animation"], "year": 2002, "episodes": 13, "source": "Manga", "score": 8.06, "synopsis": "Hades is planning to take over the world, to achieve that goal, he sends out deceased Gold Saints to take Athena's head. Seiya and the other Bronze Saints come to help but their help isn't appreciated by the remaining Gold Saints that are still alive. \n\n(Source: ANN)"}, {"id": 48895, "name_en": "Overlord IV", "name_jp": "オーバーロード IV", "image_url": "https://cdn.myanimelist.net/images/anime/1530/120110.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Isekai"], "demographics": [], "studios": ["Madhouse"], "year": 2022, "episodes": 13, "source": "Light novel", "score": 8.06, "synopsis": "E-Rantel, the capital city of the newly established Sorcerer Kingdom, suffers from a dire shortage of goods. Once a prosperous city known for its trade, it now faces a crisis due to its caution—or even fear—of its king, Ainz Ooal Gown. To make amends, Ainz sends Albedo to the city as a diplomatic envoy.\n\nMeanwhile, the cardinals of the Slane Theocracy discuss how to retaliate against Ainz after his attack crippled the Re-Estize Kingdom's army, plotting for the Baharuth Empire to take over the Sorcerer Kingdom. However, when Emperor Jircniv Rune Farlord El Nix arranges a meeting with the Theocracy's messengers at a colosseum, he is confronted by none other than Ainz himself.\n\nWith their secret gathering now out in the open, the emperor and his guests learn that Ainz has challenged the Warrior King, the empire's greatest fighter, to a duel. With Ainz's motivations beyond his comprehension, Jircniv can do nothing but watch as humanity's future changes before his very eyes.\n\n[Written by MAL Rewrite]"}, {"id": 2449, "name_en": "Ghost in the Shell: Stand Alone Complex - The Laughing Man", "name_jp": "攻殻機動隊 Stand Alone Complex - The Laughing Man", "image_url": "https://cdn.myanimelist.net/images/anime/1127/141116.jpg", "genres": ["Mystery", "Sci-Fi", "Suspense"], "themes": ["Adult Cast", "Detective", "Mecha", "Psychological"], "demographics": ["Seinen"], "studios": ["Production I.G"], "year": 2005, "episodes": 1, "source": "Manga", "score": 8.06, "synopsis": "In 2024, the terrorist incident known as \"The Laughing Man Incident\" occurred in which Ernest Serano, president of the groundbreaking micromachine company, Serano Genomics, was kidnapped and ransomed. One day, the case having remained unsolved for six years, Detective Yamaguchi, who has been investigating \"The Laughing Man Incident,\" sends word that he wants to meet with Togusa from Public Safety Section 9. However, soon after sending this message, Yamaguchi, crucial to the success of the case, dies in an accident. Many days pass and in the midst of a police interview relay concerning suspicions behind interceptors, a forewarning is received from \"The Laughing Man\" of his next crime. The incorporeal hacker begins to move once again.\n\n(Source: ANN)"}, {"id": 48661, "name_en": "JoJo's Bizarre Adventure: Stone Ocean", "name_jp": "ジョジョの奇妙な冒険 ストーンオーシャン", "image_url": "https://cdn.myanimelist.net/images/anime/1896/119844.jpg", "genres": ["Action", "Adventure"], "themes": ["Super Power"], "demographics": ["Shounen"], "studios": ["David Production"], "year": 2021, "episodes": 12, "source": "Manga", "score": 8.06, "synopsis": "Conspiring forces frame Jolyne Kuujou for a reckless crime, landing her in the infamous Green Dolphin Street Jail. Much like her father Joutarou, Jolyne is brash, brave, and just; she rails against her unfair sentence and quickly discovers the sinister circumstances that led to her incarceration.\n\nA gift from her absent father grants Jolyne the power of Stone Free, a supernatural ability known as a Stand that allows her to unravel her body into string. Jolyne uses Stone Free to battle her way through the prison, recruiting new allies—Ermes Costello and Foo Fighters—to assist in her investigation. Together, the fearless women fight to uncover the menace behind Whitesnake, an enemy Stand responsible for the increasingly dangerous prisoners who are after Jolyne's life.\n\nThrough Jolyne, the Joestar lineage confronts the legacy of its one true enemy. Jolyne and her friends race to stop a disastrous plot and put an end to a culminating evil.\n\n[Written by MAL Rewrite]"}, {"id": 10643, "name_en": "Gintama: Dai Hanseikai", "name_jp": "アニメ銀魂　大反省会", "image_url": "https://cdn.myanimelist.net/images/anime/1725/110927.jpg", "genres": ["Action", "Comedy"], "themes": ["Parody", "Samurai"], "demographics": ["Shounen"], "studios": ["Sunrise"], "year": 2010, "episodes": 1, "source": "Manga", "score": 8.06, "synopsis": "Some of the characters get together and talk about \"regrets\" they have after 4 years of anime Gintama. Soon they fight over who gets more screen time. Special animation shown at the Gintama Haru Matsuri 2010 live event."}, {"id": 32282, "name_en": "Food Wars! The Second Plate", "name_jp": "食戟のソーマ 弍ノ皿", "image_url": "https://cdn.myanimelist.net/images/anime/8/79353.jpg", "genres": ["Gourmet", "Ecchi"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["J.C.Staff"], "year": 2016, "episodes": 13, "source": "Manga", "score": 8.06, "synopsis": "The qualifiers of the Autumn Elections are now over, and only eight talented chefs remain. Now, they face off in one-on-one food wars, each with their own unique themes. Met with both new judges and new opponents all with their own specialties, Souma must stay on his toes if he hopes to make it to the top of both the Autumn Elections and Tootsuki Culinary Academy.\n\n[Written by MAL Rewrite]"}, {"id": 51096, "name_en": "Classroom of the Elite II", "name_jp": "ようこそ実力至上主義の教室へ 2nd Season", "image_url": "https://cdn.myanimelist.net/images/anime/1010/124180.jpg", "genres": ["Drama", "Suspense"], "themes": ["Psychological", "School"], "demographics": [], "studios": ["Lerche"], "year": 2022, "episodes": 13, "source": "Light novel", "score": 8.06, "synopsis": "Life back on the cruise following the Island Special Examination is anything but smooth sailing. Almost immediately after their return, the first-year students of Tokyo Metropolitan Advanced Nurturing High School face yet another special exam, with both class and individual points on the line.\n\nIn addition to the complicated ruleset, more issues arise in the form of Kakeru Ryuuen and Kei Karuizawa. Angered by the previous test's outcome, Ryuuen is dead set on outdoing every class in the new challenge using any means necessary. Meanwhile, Karuizawa, a crucial pillar of Class D, is close to crumbling under the pressure of her past.\n\nThe stage is now set for Kiyotaka Ayanokouji to once again—using the full extent of his planning, foresight, and ruthless manipulation—steer Class D to victory as dangerously close enemy forces try to bring it down.\n\n[Written by MAL Rewrite]"}, {"id": 56063, "name_en": "NieR:Automata Ver1.1a (Cour 2)", "name_jp": "NieR:Automata Ver1.1a 第2クール", "image_url": "https://cdn.myanimelist.net/images/anime/1364/143539.jpg", "genres": ["Action", "Fantasy", "Sci-Fi"], "themes": [], "demographics": [], "studios": ["A-1 Pictures"], "year": 2024, "episodes": 12, "source": "Game", "score": 8.05, "synopsis": "As the machine war progresses, YoRHa Command and the Resistance prepare for a final decisive assault against the \"Machine Lifeforms,\" the militarized robots who roam the earth's surface. While preparations are underway, YoRHa 9-gou S-gata \"9S\" and YoRHa 2-gou B-gata \"2B\" continue their advanced scouting mission. However, 9S and 2B struggle with their respective roles, their loyalty to each other, and their loyalty toward Command.\n\nAlthough the nefarious Adam and Eve have been defeated, it appears the machines are not down for the count. An ominous presence in the form of the \"Red Girl\" continues to observe the androids from afar, waiting for the perfect opportunity to attack them.\n\n[Written by MAL Rewrite]"}, {"id": 54791, "name_en": "given The Movie: Hiiragi Mix", "name_jp": "映画 ギヴン 柊mix", "image_url": "https://cdn.myanimelist.net/images/anime/1095/140582.jpg", "genres": ["Boys Love", "Drama"], "themes": ["Music"], "demographics": [], "studios": ["Lerche"], "year": 2024, "episodes": 1, "source": "Manga", "score": 8.05, "synopsis": "Hiiragi Kashima and Shizusumi \"Shizu\" Yagi's band \"syh\" is set to debut, but they run into trouble when their support guitarist leaves. Although Hiiragi often clashes with Ritsuka Uenoyama—the smug boyfriend of his childhood friend Mafuyu Satou—Hiiragi convinces him to perform as their temporary member.\n\nDespite their friction, Hiiragi acknowledges Uenoyama's talent and respects his hard work—to the point of asking him to complete an old song that Mafuyu's ex-boyfriend, Yuki Yoshida, left behind. In between writing, recording, and filming a music video, Hiiragi comes to terms with his love for the aloof Shizu and wrestles with how to convey his feelings.\n\nMeanwhile, \"given\"—composed of Uenoyama, Mafuyu, and their bandmates Haruki Nakayama and Akihiko Kaji—receives an offer to debut as well. The members are ready and beyond excited, save for Mafuyu, who has never before thought about playing music professionally. As Uenoyama attempts to understand Yuki and replicate his unique sound, Mafuyu steps back to ponder over the looming changes in his life, fearing what the uncertain future may bring.\n\n[Written by MAL Rewrite]"}, {"id": 21419, "name_en": "Detective Conan Movie 18: Dimensional Sniper", "name_jp": "名探偵コナン　異次元の狙撃手（スナイパー）", "image_url": "https://cdn.myanimelist.net/images/anime/11/60289.jpg", "genres": ["Action", "Mystery"], "themes": ["Detective"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2014, "episodes": 1, "source": "Manga", "score": 8.05, "synopsis": "One morning, at the observation deck of the Bell Tree Tower, a man is suddenly shot dead by an unknown sniper. Conan Edogawa, who happened to be visiting as the murder occurred, rushes to apprehend the sniper but fails to do so. He later learns that the suspect for the incident, Timothy Hunter, is a former member of the Navy Special Ops squad and was already being investigated for a previous murder case. The FBI believes that there are at least three people Timothy wants dead. However, after the deaths of two of them, Timothy himself is killed, throwing the investigation into disarray.\n\nAs they lose their only lead, news about the assassinations spreads across Tokyo, leaving the city in a state of panic. It is up to Conan to find and catch the culprit in the hope of putting an end to this sniping spree terror.\n\n[Written by MAL Rewrite]"}, {"id": 6547, "name_en": "Angel Beats!", "name_jp": "Angel Beats!（エンジェルビーツ!）", "image_url": "https://cdn.myanimelist.net/images/anime/1244/111115.jpg", "genres": ["Drama", "Fantasy"], "themes": ["School"], "demographics": [], "studios": ["P.A. Works"], "year": 2010, "episodes": 13, "source": "Original", "score": 8.05, "synopsis": "Death is one of many mysteries that has left humanity in the dark since the dawn of time. However, the burning question of what happens to the soul after one dies is soon answered to 17-year-old Yuzuru Otonashi. Waking up with no previous memories in a dimension between life and death, he discovers the unsettling truth of the afterlife.  \n\nTaking the form of a high school, this bizarre dimension is designated to shelter those who died unwanted deaths. Feeling wronged by God during their earthly lives, the school's residents have decided to form the Afterlife Battlefront—a rebellious faction determined to oppose their god-like student council president, Kanade \"Angel\" Tachibana. The group's leader, Yuri Nakamura, recruits Otonashi in their fight against Angel in order to take control of their own lives. However, questioning the morality behind their actions, Otonashi takes a step behind the enemy lines to understand the opposing side of their common fate.\n\n[Written by MAL Rewrite]"}, {"id": 35062, "name_en": "The Ancient Magus' Bride", "name_jp": "魔法使いの嫁", "image_url": "https://cdn.myanimelist.net/images/anime/3/88476.jpg", "genres": ["Drama", "Fantasy", "Romance"], "themes": ["Mythology", "Urban Fantasy"], "demographics": ["Shounen"], "studios": ["Wit Studio"], "year": 2017, "episodes": 24, "source": "Manga", "score": 8.05, "synopsis": "Chise Hatori, a 15-year-old Japanese girl, was sold for five million pounds at an auction to a tall masked gentleman. Abandoned at a young age and ridiculed by her peers for her unconventional behavior, she was ready to give herself to any buyer if it meant having a place to go home to. In chains and on her way to an unknown fate, she hears whispers from robed men along her path, gossiping and complaining that such a buyer got his hands on a rare Sleigh Beggy.\n\nIgnoring the murmurs, the mysterious man leads the girl to a study, where he reveals himself to be Elias Ainsworth—a magus. After a brief confrontation and a bit of teleportation magic, the two open their eyes to Elias' picturesque cottage in rural England. Greeted by fairies and surrounded by weird and wonderful beings upon her arrival, these events mark the beginning of Chise's story as the apprentice and supposed bride of the ancient magus.\n\n[Written by MAL Rewrite]"}, {"id": 27833, "name_en": "Durarara!! x2 Ketsu", "name_jp": "デュラララ!!×2 結", "image_url": "https://cdn.myanimelist.net/images/anime/6/77838.jpg", "genres": ["Action", "Mystery", "Supernatural"], "themes": [], "demographics": [], "studios": ["Shuka"], "year": 2016, "episodes": 12, "source": "Light novel", "score": 8.05, "synopsis": "As Mikado Ryuugamine continues to purge the Dollars from within in accordance with his warped sense of justice, Masaomi Kida hopes to bring his friend back to his senses by bringing the Yellow Scarves together once more. Little do they know that a far more dominant force is about to enter their struggle for power, one that their friend Anri Sonohara is all too familiar with.\n\nMeanwhile, the group that has gathered at Shinra Kishitani's apartment realizes that they are on the brink of something life-changing, an event that will throw Ikebukuro into a spiral of confusion. Their anxiety is realized when reports of Celty's head being found in public start to appear all over the news as Kasane Kujiragi begins to make her move.\n\nGone are the brief periods of tranquility as the current turmoil sets the stage for one final performance in this thrilling conclusion to the story of Ikebukuro's finest.\n\n[Written by MAL Rewrite]"}, {"id": 33486, "name_en": "My Hero Academia Season 2", "name_jp": "僕のヒーローアカデミア", "image_url": "https://cdn.myanimelist.net/images/anime/12/85221.jpg", "genres": ["Action"], "themes": ["School", "Super Power"], "demographics": ["Shounen"], "studios": ["Bones"], "year": 2017, "episodes": 25, "source": "Manga", "score": 8.05, "synopsis": "At UA Academy, not even a violent attack can disrupt their most prestigious event: the school sports festival. Renowned across Japan, this festival is an opportunity for aspiring heroes to showcase their abilities, both to the public and potential recruiters.\n\nHowever, the path to glory is never easy, especially for Izuku Midoriya—whose quirk possesses great raw power but is also cripplingly inefficient. Pitted against his talented classmates, such as the fire and ice wielding Shouto Todoroki, Izuku must utilize his sharp wits and master his surroundings to achieve victory and prove to the world his worth.\n\n[Written by MAL Rewrite]"}, {"id": 12189, "name_en": "Hyouka", "name_jp": "氷菓", "image_url": "https://cdn.myanimelist.net/images/anime/13/50521.jpg", "genres": ["Mystery", "Slice of Life"], "themes": ["School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2012, "episodes": 22, "source": "Novel", "score": 8.05, "synopsis": "High school freshman Houtarou Oreki has but one goal: to lead a gray life while conserving as much energy as he can. Unfortunately, his peaceful days come to an end when his older sister, Tomoe, forces him to save the memberless Classics Club from disbandment.\n\nLuckily, Oreki's predicament seems to be over when he heads to the clubroom and discovers that his fellow first-year, Eru Chitanda, has already become a member. However, despite his obligation being fulfilled, Oreki finds himself entangled by Chitanda's curious and bubbly personality, soon joining the club of his own volition.\n\nSoon enough, the club's membership grows to four, as Oreki's friends Satoshi Fukube and Mayaka Ibara join. Driven by Chitanda's insatiable curiosity, the members of the Classics Club solve the trivial yet intriguing mysteries that permeate their daily lives.\n\n[Written by MAL Rewrite]"}, {"id": 1367, "name_en": "Detective Conan Movie 08: Magician of the Silver Sky", "name_jp": "名探偵コナン 銀翼の奇術師[マジシャン]", "image_url": "https://cdn.myanimelist.net/images/anime/2/20982.jpg", "genres": ["Adventure", "Comedy", "Mystery"], "themes": ["Detective"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2004, "episodes": 1, "source": "Manga", "score": 8.05, "synopsis": "After receiving a letter from the thief Kaitou Kid, famous actress Juri Maki seeks the help of private detective Kogorou Mouri to protect the Star Sapphire—the \"Jewel of Destiny,\" said to represent faith, fate, and hope. Thinking he has deciphered Kid's riddle, Kogorou personally shows up to the newly constructed space theater where Juri is acting in the play \"Josephine\" in order to catch Kid in the act.\n \nThe next day, Conan and the gang are invited by Juri to her holiday home, to celebrate the thwarting of Kid's plan and the success of the play. However, their triumph crumbles when a murder occurs during the flight there. Although unintentional, this sets off a series of events that escalate to catastrophic results. Conan and Kid, unlikely allies that they are, must work together to save both their friends and every other passenger aboard the plane.\n \n[Written by MAL Rewrite]"}, {"id": 61339, "name_en": "Toilet-Bound Hanako-kun Season 2 Part 2", "name_jp": "地縛少年花子くん2 後編", "image_url": "https://cdn.myanimelist.net/images/anime/1484/150617.jpg", "genres": ["Supernatural"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["Lerche"], "year": 2025, "episodes": 12, "source": "Manga", "score": 8.04, "synopsis": "Due to frequent misadventures with the supernatural and her hectic school life, Nene Yashiro can barely catch a break until Kamome Academy's annual sleepover event comes around. Nene is excited to blow off some steam with her best friend Aoi Akane and the rest of her class at the get-together. Unbeknownst to her, the sleepover falls on the same date as Urabon, when the line between the dead and the living gets distorted, allowing wicked spirits to come out to the surface.\n\nWhen an evil ghost is about to attack her, Nene is rescued by a mysterious figure: Shinigami, the Sixth Wonder that can trap souls using his flute. In that moment, Shinigami takes notice of Aoi and abducts her to use her as a sacrifice that will close the barrier for good. Joined by Hanako-kun and Akane Aoi, Nene must embark on a journey into the deepest confines of the world of the dead to save her best friend before it is too late.\n\n[Written by MAL Rewrite]"}, {"id": 60602, "name_en": "MF Ghost Season 3", "name_jp": "MFゴースト 3rd Season", "image_url": "https://cdn.myanimelist.net/images/anime/1419/154458.jpg", "genres": ["Drama"], "themes": ["Racing"], "demographics": ["Seinen"], "studios": ["Felix Film"], "year": 2026, "episodes": 0, "source": "Manga", "score": 8.04, "synopsis": "Third season of MF Ghost."}, {"id": 59229, "name_en": "Fire Force Season 3 Part 2", "name_jp": "炎炎ノ消防隊 参ノ章 第2クール", "image_url": "https://cdn.myanimelist.net/images/anime/1508/152472.jpg", "genres": ["Action", "Fantasy", "Sci-Fi"], "themes": ["Urban Fantasy"], "demographics": ["Shounen"], "studios": ["David Production"], "year": 2026, "episodes": 13, "source": "Manga", "score": 8.04, "synopsis": "Second part of Enen no Shouboutai: San no Shou."}, {"id": 55701, "name_en": "Demon Slayer: Kimetsu no Yaiba Hashira Training Arc", "name_jp": "鬼滅の刃 柱稽古編", "image_url": "https://cdn.myanimelist.net/images/anime/1565/142711.jpg", "genres": ["Action", "Supernatural"], "themes": ["Historical"], "demographics": ["Shounen"], "studios": ["ufotable"], "year": 2024, "episodes": 8, "source": "Manga", "score": 8.04, "synopsis": "After a series of mighty clashes with Upper Rank Demons, the Ubuyashiki clan prepares for one last battle with the hellish forces of Muzan Kibutsuji. In order to finally defeat the Demon leader once and for all, the clan devises a training camp for the Demon Slayer Corps, one led by the remaining Hashira—the most elite warriors in the organization. Each Hashira forms a specialized exercise that will hone both their own abilities and the skills of the ordinary soldiers.\n\nTanjirou Kamado, a boy at the heart of the brewing conflict, recovers from wounds received in a recent fight. While his half-Demon sister Nezuko is studied by researchers like Shinobu Kochou, Tanjirou embarks to train with the Hashira, seeking mastery in each of their assigned areas of expertise to be best prepared for the coming war—skills vital to Tanjirou, as he has vowed to be the very warrior who will eliminate Muzan for good.\n\n[Written by MAL Rewrite]"}, {"id": 52846, "name_en": "Tot Musica", "name_jp": "Tot Musica", "image_url": "https://cdn.myanimelist.net/images/anime/1816/127267.jpg", "genres": [], "themes": ["Music"], "demographics": [], "studios": [], "year": 2022, "episodes": 1, "source": "Original", "score": 8.04, "synopsis": "Music video for the song Tot Musica by Ado."}, {"id": 51367, "name_en": "JoJo's Bizarre Adventure: Stone Ocean Part 2", "name_jp": "ジョジョの奇妙な冒険 ストーンオーシャン", "image_url": "https://cdn.myanimelist.net/images/anime/1051/121959.jpg", "genres": ["Action", "Adventure"], "themes": ["Super Power"], "demographics": ["Shounen"], "studios": ["David Production"], "year": 2022, "episodes": 12, "source": "Manga", "score": 8.04, "synopsis": "Thrown into solitary confinement after a daring rescue attempt, Jolyne Kuujou fights to uncover the sinister plot of Whitesnake, an enemy Stand whose mysterious wielder remains unknown to her.\n\nWhitesnake's user is actually Enrico Pucci, priest and chaplain of Green Dolphin Street Prison. Pucci shares a deep connection with a villain who once plagued the Joestar bloodline, making his feud with Jolyne and her father, Joutarou, a personal one. Intent on executing the master plan of his deceased friend, Pucci uses the prison and its inmates to fulfill his nefarious schemes.\n\nAssisted by fellow prisoners, like Ermes Costello and Foo Fighters, Jolyne battles several enemy Stand users. As the ramifications of Pucci's plot spread beyond the confines of the prison and begin to threaten the rest of the world, Jolyne embraces her family lineage and risks her life to put an end to evil.\n\n[Written by MAL Rewrite]"}, {"id": 13759, "name_en": "The Pet Girl of Sakurasou", "name_jp": "さくら荘のペットな彼女", "image_url": "https://cdn.myanimelist.net/images/anime/4/43643.jpg", "genres": ["Drama", "Romance"], "themes": ["School"], "demographics": [], "studios": ["J.C.Staff"], "year": 2012, "episodes": 24, "source": "Light novel", "score": 8.04, "synopsis": "At Suimei High, the Sakura-sou dormitory is infamous for housing the school's most notorious delinquents. Thus, when the relatively tame Sorata Kanda is transferred to the dorm, escaping this insane asylum becomes his foremost goal. Trapped there for the time being, he must learn how to deal with his fellow residents, including bubbly animator Misaki Kamiigusa, charming playboy writer Jin Mitaka, and the ever-reclusive Ryuunosuke Akasaka. Surrounded by weirdness, Sorata frequently finds respite in his interactions with his one \"normal\" friend, aspiring voice actress Nanami Aoyama.\n\nWhen Mashiro Shiina—a new foreign exchange student—joins the dormitory, Sorata is instantly enraptured by her beauty. Underneath her otherworldly appearance, Mashiro is an autistic savant, capable of world-renowned brilliance in her art, yet unable to perform simple daily tasks. After Sorata ends up in charge of taking care of Mashiro, the two inevitably grow closer, with Sorata's initial desire to escape the dormitory becoming a forgotten goal.\n\nDespite their eccentricities, every resident is incredible in their own field, leaving Sorata to contend with his own lack of any particular skill. With brilliance all around him, he thus strives to become an equal to their talent. Revolving around the hardships and joys of its colorful cast, Sakura-sou no Pet na Kanojo is a heartwarming coming-of-age tale of friendship, love, ambition, and heartbreak—through the lens of an ordinary person surrounded by the extraordinary.\n\n[Written by MAL Rewrite]"}, {"id": 21557, "name_en": "When Marnie Was There", "name_jp": "思い出のマーニー", "image_url": "https://cdn.myanimelist.net/images/anime/7/64293.jpg", "genres": ["Drama", "Mystery", "Suspense"], "themes": ["Psychological"], "demographics": [], "studios": ["Studio Ghibli"], "year": 2014, "episodes": 1, "source": "Novel", "score": 8.04, "synopsis": "Suffering from frequent asthma attacks, young Anna Sasaki is quiet, unsociable, and isolated from her peers, causing her foster parent endless worry. Upon recommendation by the doctor, Anna is sent to the countryside, in hope that the cleaner air and more relaxing lifestyle will improve her health and help clear her mind. Engaging in her passion for sketching, Anna spends her summer days living with her aunt and uncle in a small town near the sea.\n\nOne day while wandering outside, Anna discovers an abandoned mansion known as the Marsh House. However, she soon finds that the residence isn't as vacant as it appears to be, running into a mysterious girl named Marnie. Marnie's bubbly demeanor slowly begins to draw Anna out of her shell as she returns night after night to meet with her new friend. But it seems there is more to the strange girl than meets the eye—as her time in the town nears its end, Anna begins to discover the truth behind the walls of the Marsh House.\n\nOmoide no Marnie tells the touching story of a young girl's journey through self-discovery and friendship, and the summer that she will remember for the rest of her life.\n\n[Written by MAL Rewrite]"}, {"id": 25013, "name_en": "Yona of the Dawn", "name_jp": "暁のヨナ", "image_url": "https://cdn.myanimelist.net/images/anime/9/64225.jpg", "genres": ["Adventure", "Fantasy", "Romance"], "themes": [], "demographics": ["Shoujo"], "studios": ["Studio Pierrot"], "year": 2014, "episodes": 24, "source": "Manga", "score": 8.04, "synopsis": "The kingdom of Kouka is blessed with a beautiful princess whose childlike innocence charms all who come across her. Named Yona, she has grown up sheltered in the royal palace, shielded from any danger that may befall her. However, all good things must come to an end.\n\nYona's perfect world comes crashing down when a heinous act of treason threatens to erase all that she holds dear, including her birthright as the princess of Kouka. Left with no one to trust but her childhood friend and loyal bodyguard Son Hak, she is forced to flee the palace. Faced with the perils of surviving in the wild with a target on her back, Yona realizes that her kingdom is no longer the safe haven it once was.\n\nFree from the shackles of naivety, Yona vows to do everything in her power to become strong enough to crush her enemies. With Hak by her side, she must piece together the remains of an ancient legend that might be the key to reclaiming her kingdom from those who conspired to steal it from her.\n\n[Written by MAL Rewrite]"}, {"id": 23317, "name_en": "Black Butler: Book of Murder", "name_jp": "黒執事 Book of Murder", "image_url": "https://cdn.myanimelist.net/images/anime/12/74392.jpg", "genres": ["Action", "Mystery", "Supernatural"], "themes": ["Historical", "Mythology"], "demographics": ["Shounen"], "studios": ["A-1 Pictures"], "year": 2015, "episodes": 2, "source": "Manga", "score": 8.04, "synopsis": "At the behest of the Queen, Earl Ciel Phantomhive hosts a lavish dinner party attended by several of the finest members of polite society—as well as struggling author, Arthur. But as the party reaches its high, a terrible murder takes place and none other than the Earl himself is suspected of the crime.\n\nAs a violent storm rages on outside, the death count continues to climb. The Phantomhive household and their eminent guests find they must cooperate in order to solve this mystery before they too fall prey to the mysterious murderer. However, it seems that not even the perfect butler, Sebastian Michaelis, is safe from this horror.\n\n[Written by MAL Rewrite]"}, {"id": 11771, "name_en": "Kuroko's Basketball", "name_jp": "黒子のバスケ", "image_url": "https://cdn.myanimelist.net/images/anime/11/50453.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2012, "episodes": 25, "source": "Manga", "score": 8.04, "synopsis": "For the last three years, Teikou Middle School has dominated the national basketball scene with its legendary lineup: the \"Generation of Miracles.\" It consisted of five prodigies who excelled at the sport, but a \"Phantom Sixth Man\" lurked in the shadows and helped earn the team their revered status. Eventually, their monstrous growth jaded them from the sport they loved and made them go their separate ways in high school.\n\nIn search of new members, the Seirin High School basketball team recruits Taiga Kagami and Tetsuya Kuroko, two freshmen who seem to have significant differences in abilities. Having returned recently from America, Kagami has both a natural aptitude and relentless love for the sport. Meanwhile, Kuroko lacks presence and exhibits no outstanding athletic talent. However, it is later revealed that he is Teikou's Phantom Sixth Man—the player once part of the Generation of Miracles.\n\nKuroko wants to prove to the Seirin team that he is strong in his own way. Seeing his conviction, Kagami forms a dynamic partnership with Kuroko, the latter promising to support Kagami's \"light\" as his \"shadow.\" Alongside their new Seirin teammates, they aim to conquer the upcoming Interhigh championship, but the reappearance of Kuroko's former teammates complicates their plan.\n\n[Written by MAL Rewrite]"}, {"id": 4155, "name_en": "One Piece Film: Strong World", "name_jp": "ワンピース　フィルム　ストロングワールド", "image_url": "https://cdn.myanimelist.net/images/anime/1192/116784.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Toei Animation"], "year": 2009, "episodes": 1, "source": "Manga", "score": 8.04, "synopsis": "Upon hearing news that islands in East Blue are being destroyed, Monkey D. Luffy and his crew go to investigate. On their way, however, an outlandish pirate ship appears out of the sky, helmed by the infamous pirate Shiki \"the Golden Lion\"—a man who ate the Float-Float Fruit and the first ever prisoner to escape from Impel Down. In his quest to defeat the World Government, Shiki kidnaps Nami to be his own navigator and sends the rest of the Straw Hat Pirates to his floating islands as hostages, leaving her in a dilemma. Separated in a land under Shiki's absolute control, Luffy and his crew must survive the mystifying terrain in order to bring back their navigator and friend.\n\n[Written by MAL Rewrite]"}, {"id": 4224, "name_en": "Toradora!", "name_jp": "とらドラ！", "image_url": "https://cdn.myanimelist.net/images/anime/13/22128.jpg", "genres": ["Drama", "Romance"], "themes": ["Love Polygon", "School"], "demographics": [], "studios": ["J.C.Staff"], "year": 2008, "episodes": 25, "source": "Light novel", "score": 8.04, "synopsis": "Ryuuji Takasu is a gentle high school student with a love for housework; but in contrast to his kind nature, he has an intimidating face that often gets him labeled as a delinquent. On the other hand is Taiga Aisaka, a small, doll-like student, who is anything but a cute and fragile girl. Equipped with a wooden katana and feisty personality, Taiga is known throughout the school as the \"Palmtop Tiger.\"\n\nOne day, an embarrassing mistake causes the two students to cross paths. Ryuuji discovers that Taiga actually has a sweet side: she has a crush on the popular vice president, Yuusaku Kitamura, who happens to be his best friend. But things only get crazier when Ryuuji reveals that he has a crush on Minori Kushieda—Taiga's best friend!\n\nToradora! is a romantic comedy that follows this odd duo as they embark on a quest to help each other with their respective crushes, forming an unlikely alliance in the process.\n\n[Written by MAL Rewrite]"}, {"id": 8425, "name_en": "Gosick", "name_jp": "GOSICK -ゴシック-", "image_url": "https://cdn.myanimelist.net/images/anime/11/27906.jpg", "genres": ["Drama", "Mystery", "Romance"], "themes": ["Historical"], "demographics": [], "studios": ["Bones"], "year": 2011, "episodes": 24, "source": "Light novel", "score": 8.04, "synopsis": "Kazuya Kujou is a foreign student at Saint Marguerite Academy, a luxurious boarding school in the Southern European country of Sauville. Originally from Japan, his jet-black hair and dark brown eyes cause his peers to shun him and give him the nickname \"Black Reaper,\" based on a popular urban legend about the traveler who brings death in the spring.\n\nOn a day like any other, Kujou visits the school's extravagant library in search of ghost stories. However, his focus soon changes as he becomes curious about a golden strand of hair on the stairs. The steps lead him to a large garden and a beautiful doll-like girl known as Victorique de Blois, whose complex and imaginative foresight allows her to predict their futures, now intertwined.\n\nWith more mysteries quickly developing—including the appearance of a ghost ship and an alchemist with the power of transmutation—Victorique and Kujou, bound by fate and their unique skills, have no choice but to rely on each other.\n\n[Written by MAL Rewrite]"}, {"id": 24277, "name_en": "Yowamushi Pedal: Grande Road", "name_jp": "弱虫ペダル GRANDE ROAD", "image_url": "https://cdn.myanimelist.net/images/anime/12/67799.jpg", "genres": ["Sports"], "themes": ["Racing"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2014, "episodes": 24, "source": "Manga", "score": 8.04, "synopsis": "Second season of the Yowamushi Pedal series."}, {"id": 1943, "name_en": "Paprika", "name_jp": "パプリカ", "image_url": "https://cdn.myanimelist.net/images/anime/1929/93629.jpg", "genres": ["Avant Garde", "Award Winning", "Mystery", "Sci-Fi", "Suspense"], "themes": ["Adult Cast", "Psychological"], "demographics": [], "studios": ["Madhouse"], "year": 2006, "episodes": 1, "source": "Novel", "score": 8.04, "synopsis": "The world of dreams can be an incredible window into the psyche, showing one's deepest desires, aspirations, and repressed memories. One hopeful tech lab has been developing the \"DC Mini,\" a device with the power to delve into the dreams of others. Atsuko Chiba and Kosaku Tokita have been tirelessly working to develop this technology with the hopes of using it to deeply explore patients' minds and help cure them of their psychological disorders.\n\nHowever, having access to the deepest corners of a person's mind comes with a tremendous responsibility. In the wrong hands, the DC Mini could be used as a form of psychological terrorism and cause mental breakdowns in the minds of targets. When this technology is stolen and people around them start acting strangely, Atsuko and Kosaku know they have a serious problem on their hands. Enlisting the help of Officer Konakawa, who has been receiving this experimental therapy, they search both the real and dream worlds for their mental terrorist.\n\n[Written by MAL Rewrite]"}, {"id": 40907, "name_en": "World Trigger 2nd Season", "name_jp": "ワールドトリガー", "image_url": "https://cdn.myanimelist.net/images/anime/1598/110462.jpg", "genres": ["Action", "Sci-Fi"], "themes": [], "demographics": ["Shounen"], "studios": ["Toei Animation"], "year": 2021, "episodes": 12, "source": "Manga", "score": 8.04, "synopsis": "After successfully holding off the invasion by Aftokrator, the Border Defense Agency prepares an away mission into the Neighbor's dimension. However, like in previous scouting expeditions, only A-rank teams are certain to secure a spot. As the B-rank wars continue, Osamu Mikumo and the rest of Tamakoma-2 quickly fight to the top in an attempt to obtain a promotion before the operation begins.\n\nMeanwhile, a new Neighbor ship approaches Border Headquarters. Noticing that the attackers are targeting the Border Expedition Ship, forces are hastily sent to combat them. However, with fewer squads available due to the proceeding rank wars, the organization is sent into disarray. This latest offensive from the Neighbors shrouds the fate of the all-important expedition ship in uncertainty.\n\n[Written by MAL Rewrite]"}, {"id": 19815, "name_en": "No Game, No Life", "name_jp": "ノーゲーム・ノーライフ", "image_url": "https://cdn.myanimelist.net/images/anime/1074/111944.jpg", "genres": ["Comedy", "Fantasy", "Ecchi"], "themes": ["Isekai", "Strategy Game"], "demographics": [], "studios": ["Madhouse"], "year": 2014, "episodes": 12, "source": "Light novel", "score": 8.04, "synopsis": "Sixteen sentient races inhabit Disboard, a world overseen by Tet, the One True God. The lowest of the sixteen—Imanity—consists of humans, a race with no affinity for magic. In a place where everything is decided through simple games, humankind seems to have no way out of their predicament—but the arrival of two outsiders poses a change.\n\nOn Earth, stepsiblings Sora and Shiro are two inseparable shut-ins who dominate various online games under the username \"Blank.\" While notorious on the internet, the pair believe that life is merely another dull game. However, after responding to a message from an unknown user, they are suddenly transported to Disboard. The mysterious sender turns out to be Tet, who informs them about the world's absolute rules. After Tet leaves, Sora and Shiro begin their search for more information and a place to stay, taking them to Elkia—Imanity's only remaining kingdom.\n\nThere, the duo encounters Stephanie Dola, an emotional girl vying for the kingdom's sovereignty. In desperation, she attempts to regain her father's throne, but her foolhardiness makes her goal unachievable. Inspired by the girl's motivation and passion, Sora and Shiro decide to aid Stephanie in getting Elkia back on its feet, ultimately aiming to become the new rulers of the enigmatic realm.\n\n[Written by MAL Rewrite]"}, {"id": 49320, "name_en": "Detective Conan Movie 25: The Bride of Halloween", "name_jp": "名探偵コナン ハロウィンの花嫁", "image_url": "https://cdn.myanimelist.net/images/anime/1744/120789.jpg", "genres": ["Action", "Mystery"], "themes": ["Detective"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2022, "episodes": 1, "source": "Manga", "score": 8.04, "synopsis": "Shibuya, Tokyo, is bustling with the Halloween season. A wedding is being held at Shibuya Hikarie, where Sato is dressed in a wedding dress. While Conan and the other invited guests are watching, an assailant suddenly bursts in, and Takagi, who was trying to protect Sato, is injured. Takagi survived and the situation was settled, but in Sato's eyes, the image of the grim reaper that she had seen when Jinpei Matsuda, the man she had been in love with, had been killed in a series of bombings three years ago, overlapped with Takagi's.\n\nAt the same time, the perpetrator of the bombings escapes from prison. Rei Furuya/Toru Amuro, a member of the public safety police, is hunting down the man who killed his classmate, but a mysterious person in disguise suddenly appears and puts a collar bomb on him.\n\nConan visits the underground shelter where Amuro is hiding out to disarm the collar bomb, and hears about an incident three years ago when he and his now deceased classmates from the police academy encountered an unidentified virtual bomber named \"Plamya\" in Shibuya. As Conan and his team investigate, a disturbing shadow begins to loom over them.\n\n(Source: Detective Conan World)"}, {"id": 237, "name_en": "Eureka Seven", "name_jp": "交響詩篇エウレカセブン", "image_url": "https://cdn.myanimelist.net/images/anime/12/34443.jpg", "genres": ["Adventure", "Drama", "Romance", "Sci-Fi"], "themes": ["Mecha"], "demographics": [], "studios": ["Bones"], "year": 2005, "episodes": 50, "source": "Original", "score": 8.04, "synopsis": "In the backwater town of Bellforest lives a 14-year-old boy named Renton Thurston. He desires to leave his home behind and join the mercenary group known as Gekkostate, hoping to find some adventure to brighten up his mundane life. However, stuck between his grandfather's insistence to become a mechanic like him and the pressure of his deceased father's legacy, the only excitement Renton finds is in his pastime of riding the Trapar wave particles that are dispersed throughout the air, an activity akin to surfing.\n\nEverything changes when an unknown object crashes through Renton's garage, discovered to be a Light Finding Operation—a robot capable of riding the Trapar waves—specifically known as the Nirvash typeZERO. Its pilot is a young girl named Eureka, a member of the Gekkostate, who requests a tune-up for the Nirvash. Their meeting sparks the beginning of Renton's involvement with the Gekkostate as he takes off alongside Eureka as the co-pilot of the Nirvash.\n\n[Written by MAL Rewrite]"}, {"id": 227, "name_en": "FLCL", "name_jp": "フリクリ", "image_url": "https://cdn.myanimelist.net/images/anime/7/77356.jpg", "genres": ["Avant Garde", "Comedy", "Sci-Fi", "Suspense"], "themes": ["Mecha", "Parody", "Psychological"], "demographics": [], "studios": ["Gainax", "Production I.G"], "year": 2000, "episodes": 6, "source": "Original", "score": 8.04, "synopsis": "Naota Nandaba is an ordinary sixth grader living in a city where nothing amazing ever seems to happen. After his brother Tasuku leaves town to play baseball in America, Naota takes it upon himself to look after everything Tasuku left behind—from his top bunk bed to his ex-girlfriend Mamimi Samejima, who hasn't stopped clinging to Naota since Tasuku left. \n\nLittle does Naota know, however, that his mundane existence is on the verge of being changed forever: enter Haruko Haruhara, a Vespa-riding, bass guitar-wielding, pink-haired psychopath whose first encounter with Naota leaves him with tire tracks on his back and a giant horn on his head. Though all he wants is some peace and quiet, when Haruko takes up residence at his parents' home, Naota finds himself dragged into the heart of the greatest battle for supremacy that Earth—and quite possibly the entire universe—has ever seen.\n\n[Written by MAL Rewrite]"}, {"id": 38668, "name_en": "Dorohedoro", "name_jp": "ドロヘドロ", "image_url": "https://cdn.myanimelist.net/images/anime/1230/119278.jpg", "genres": ["Action", "Comedy", "Fantasy", "Horror"], "themes": ["Gore"], "demographics": ["Seinen"], "studios": ["MAPPA"], "year": 2020, "episodes": 12, "source": "Manga", "score": 8.04, "synopsis": "Hole—a dark, decrepit, and disorderly district where the strong prey on the weak and death is an ordinary occurrence—is all but befitting of the name given to it. A realm separated from law and ethics, it is a testing ground to the magic users who dominate it. As a race occupying the highest rungs of their society, the magic users think of the denizens of Hole as no more than insects. Murdered, mutilated, and made experiments without a second thought, the powerless Hole dwellers litter the halls of Hole's hospital on a daily basis.\n\nPossessing free access to and from the cesspool, and with little challenge to their authority, the magic users appear indomitable to most—aside for a few. Caiman, more reptile than man, is one such individual. He hunts them on a heedless quest for answers with only a trusted pair of bayonets and his immunity to magic. Cursed by his appearance and tormented by nightmares, magic users are his only clue to restoring his life to normal. With his biggest obstacle being his stomach, his female companion Nikaidou, who runs the restaurant Hungry Bug, is his greatest ally.\n\n[Written by MAL Rewrite]"}, {"id": 2025, "name_en": "Darker than Black", "name_jp": "Darker than BLACK -黒の契約者-", "image_url": "https://cdn.myanimelist.net/images/anime/5/19570.jpg", "genres": ["Action", "Mystery"], "themes": ["Super Power"], "demographics": [], "studios": ["Bones"], "year": 2007, "episodes": 25, "source": "Original", "score": 8.04, "synopsis": "It has been 10 years since Heaven's Gate appeared in South America and Hell's Gate appeared in Japan, veiling the once familiar night sky with an oppressive skyscape. Their purposes unknown, these Gates are spaces in which the very laws of physics are ignored. With the appearance of the Gates emerged Contractors, who, in exchange for their humanity, are granted supernatural abilities.\n\nIn the Japanese city surrounding Hell's Gate, Section 4 Chief Misaki Kirihara finds herself at odds with an infamous Contractor codenamed Hei. Called \"Black Reaper\" in the underground world, Hei, like his associates, undertakes missions for the mysterious and ruthless Syndicate while slowly peeling back the dark layers covering a nefarious plot that threatens the very existence of Contractors.\n\nFrom the mind of Tensai Okamura comes a sci-fi thriller taking the form of a subtle exposé on a war in which political positions and justice have no sway—a war waged exclusively in the shadows.\n\n[Written by MAL Rewrite]"}, {"id": 27821, "name_en": "Fate/stay night [Unlimited Blade Works] - Prologue", "name_jp": "Fate/stay night [Unlimited Blade Works] プロローグ", "image_url": "https://cdn.myanimelist.net/images/anime/9/67425.jpg", "genres": ["Action", "Fantasy"], "themes": ["Urban Fantasy"], "demographics": [], "studios": ["ufotable"], "year": 2014, "episodes": 1, "source": "Visual novel", "score": 8.03, "synopsis": "In Fuyuki City, a long-lived ritual involving battles between seven magi and their servants is taking place. This ritual is known as the Holy Grail War and it promises to grant the victor any wish. With the war now entering its fifth iteration, the stage is set for Rin Toosaka to succeed her father's legacy.\n\nRin wishes to summon Saber, said to be the most powerful class. But when she miscalculates and summons Archer instead, how will she fare in the battles that lie ahead of her?\n\n[Written by MAL Rewrite]"}, {"id": 2034, "name_en": "Lovely Complex", "name_jp": "ラブ★コン", "image_url": "https://cdn.myanimelist.net/images/anime/11/75563.jpg", "genres": ["Comedy", "Romance"], "themes": ["School"], "demographics": ["Shoujo"], "studios": ["Toei Animation"], "year": 2007, "episodes": 24, "source": "Manga", "score": 8.03, "synopsis": "Love is unusual for Risa Koizumi and Atsushi Ootani, who are both striving to find their ideal partner in high school—172 cm tall Koizumi is much taller than the average girl, and Ootani is much shorter than the average guy at 156 cm. To add to their plights, their crushes fall in love with each other, leaving Koizumi and Ootani comically flustered and heartbroken. To make matters worse, they're even labeled as a comedy duo by their homeroom teacher due to their personalities and the stark difference in their heights, and their classmates even think of their arguments as sketches.\n\nLovely★Complex follows Koizumi and Ootani as they encourage each other in finding love and become close friends. Apart from their ridiculous antics, they soon find out an unexpected similarity in their music and fashion tastes. Maybe they possess a chemistry yet unknown, but could love ever bloom between the mismatched pair?\n\n[Written by MAL Rewrite]"}, {"id": 1363, "name_en": "Case Closed Movie 04: Captured In Her Eyes", "name_jp": "瞳の中の暗殺者", "image_url": "https://cdn.myanimelist.net/images/anime/1196/94319.jpg", "genres": ["Adventure", "Comedy", "Mystery"], "themes": ["Detective"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2000, "episodes": 1, "source": "Manga", "score": 8.03, "synopsis": "On a rainy afternoon, the Detective Boys witness a murder across the street. Barred by traffic, the culprit slips away and Conan Edogawa is left a single clue by a dying detective. Days later, another detective is found murdered in a parking lot, leaving the police rattled. Suspicious that the culprit is one of their own, everyone in the police department without an alibi is suspect. But despite being on high alert, they are outmaneuvered and suffer yet another attack—this time with Ran Mouri finding herself in the crossfire.\n\nTraumatized, Ran wakes in the hospital with retrograde amnesia, remembering nothing about her life. Soon released, she struggles to remember her past and grows fearful of not regaining her memories before being targeted by the killer for what she witnessed. As she is guarded by friends and family, it is up to Conan to piece together the clues and find who the murderer is before they strike again.\n\n[Written by MAL Rewrite]"}, {"id": 1559, "name_en": "KenIchi: The Mightiest Disciple", "name_jp": "史上最強の弟子 ケンイチ", "image_url": "https://cdn.myanimelist.net/images/anime/9/75515.jpg", "genres": ["Action", "Comedy"], "themes": ["Martial Arts", "School"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2006, "episodes": 50, "source": "Manga", "score": 8.03, "synopsis": "Whenever he is not getting pummeled in the karate club after school, high schooler Kenichi Shirahama can be found with his head buried in one of the many self-help books he carries with him every day. One day, on the way to school, Kenichi accidentally frightens the new student in his class, leading her to throw him down onto the ground. After apologizing for her violent reflexes, she introduces herself as Miu Furinji and asks Kenichi to be her friend.\n\nLater that day, after karate club practice, Kenichi is given an ultimatum by one of the other club members: they will fight next week and the loser has to quit the club permanently. While lamenting his imminent defeat in the upcoming fight, Kenichi witnesses Miu fend off three thugs easily. Fed up with being perpetually bullied by those more powerful than him, he asks Miu for tips on how to become a better fighter. She directs Kenichi to her grandfather's dojo, Ryouzanpaku, where several skilled—albeit strange—masters of extreme martial arts have gathered to hone their abilities. Miu promises that Kenichi will become significantly stronger, but only if he manages to stay alive.\n\nWith those ominous words looming in his mind and the appearance of a criminal martial arts gang known as \"Ragnarok\" threatening the safety of those around him, Kenichi takes the first step on his journey to become a world-class fighter and a defender of the defenseless.\n\n[Written by MAL Rewrite]"}, {"id": 18679, "name_en": "Kill la Kill", "name_jp": "キルラキル", "image_url": "https://cdn.myanimelist.net/images/anime/1464/111943.jpg", "genres": ["Action", "Comedy", "Fantasy", "Ecchi"], "themes": ["School", "Urban Fantasy"], "demographics": [], "studios": ["Trigger"], "year": 2013, "episodes": 24, "source": "Original", "score": 8.03, "synopsis": "After the murder of her father, Ryuuko Matoi has been wandering the land in search of his killer. Following her only lead—the missing half of his invention, the Scissor Blade—she arrives at the prestigious Honnouji Academy, a high school unlike any other. The academy is ruled by the imposing and cold-hearted student council president Satsuki Kiryuuin alongside her powerful underlings, the Elite Four. In the school's brutally competitive hierarchy, Satsuki bestows upon those at the top special clothes called \"Goku Uniforms,\" which grant the wearer unique superhuman abilities. \n\nThoroughly beaten in a fight against one of the students in uniform, Ryuuko retreats to her razed home where she stumbles across Senketsu, a rare and sentient \"Kamui,\" or God Clothes. After coming into contact with Ryuuko's blood, Senketsu awakens, latching onto her and providing her with immense power. Now, armed with Senketsu and the Scissor Blade, Ryuuko makes a stand against the Elite Four, hoping to reach Satsuki and uncover the culprit behind her father's murder once and for all. \n\n[Written by MAL Rewrite]"}, {"id": 889, "name_en": "Black Lagoon", "name_jp": "BLACK LAGOON", "image_url": "https://cdn.myanimelist.net/images/anime/1906/121592.jpg", "genres": ["Action"], "themes": ["Adult Cast", "Organized Crime"], "demographics": ["Seinen"], "studios": ["Madhouse"], "year": 2006, "episodes": 12, "source": "Manga", "score": 8.03, "synopsis": "Salaryman Rokurou Okajima spends his days trying to climb his company's corporate ladder, until one day when a business negotiation in Thailand goes awry. During the botched deal, he falls hostage to the Lagoon Company—a band of ruthless pirate mercenaries. Left to the whims of his captors after his managers refuse to pay his ransom, Rokurou does the unthinkable: instead of begging for his life, he joins the very crew who kidnapped him.\n\nNow a member of the group, Rokurou must adjust to his new residence in the dissolute hellscape known as Roanapur, a city where corruption and crime run rampant, and even the smallest slipup could cost him his life. If not for one of the many crime syndicates on the island, Rokurou also constantly finds himself at odds with his brash, gunslinging colleague, Revy. \n\nAs Rokurou struggles to abandon his past—and with more than just the profits from the Lagoon Company's illegal trading on the line—he must quickly find the resolve to make tough decisions in high-stress situations while keeping his humanity intact.\n\n[Written by MAL Rewrite]"}, {"id": 50587, "name_en": "Gridman Universe", "name_jp": "グリッドマン ユニバース", "image_url": "https://cdn.myanimelist.net/images/anime/1897/133657.jpg", "genres": ["Sci-Fi"], "themes": ["Mecha"], "demographics": [], "studios": ["Trigger"], "year": 2023, "episodes": 1, "source": "Original", "score": 8.03, "synopsis": "A year has passed since the events of SSSS.Gridman. The world is at peace and no one except for Rikka and Utsumi remembers the kaiju, Gridman, or Akane Shinjou. This includes Yuuta, who despite serving as Gridman's host, has no memory of the most important moments of his life. So when a new Kaiju appears, he leaps at the chance to prove to himself that he too can be the hero by merging with Gridman once more.\n\n(Source: ANN)"}, {"id": 58913, "name_en": "The Summer Hikaru Died", "name_jp": "光が死んだ夏", "image_url": "https://cdn.myanimelist.net/images/anime/1104/148614.jpg", "genres": ["Horror", "Mystery", "Supernatural"], "themes": [], "demographics": [], "studios": ["CygamesPictures"], "year": 2025, "episodes": 12, "source": "Web manga", "score": 8.02, "synopsis": "It has been six months since Yoshiki Tsujinaka's best friend, Hikaru Indou, went missing in the mountains and returned a week later with no recollection of what had transpired. Certain that it is not the Hikaru he knows who came back, Yoshiki finally asks Hikaru about it. Suddenly, \"Hikaru\" reveals his true monstrous form and begs Yoshiki to keep it a secret, as he does not want to kill him.\n\nDespite his paranormal nature, \"Hikaru\" seems almost innocent, full of childlike wonder and eager to experience the summer heat, the countryside, and all kinds of other things in a human body for the first time. And for Yoshiki, it matters little if \"Hikaru\" is a fake relying on stolen memories—as long as he never leaves again. As Yoshiki desperately clings to \"Hikaru\" in his intense grief, it becomes increasingly clear that something dangerous and supernatural may have descended from the mountains into their village.\n\n[Written by MAL Rewrite]"}, {"id": 53410, "name_en": "Laid-Back Camp Season 3", "name_jp": "ゆるキャン△ SEASON3", "image_url": "https://cdn.myanimelist.net/images/anime/1178/142710.jpg", "genres": ["Slice of Life"], "themes": ["CGDCT", "Iyashikei"], "demographics": [], "studios": ["8bit"], "year": 2024, "episodes": 12, "source": "Manga", "score": 8.02, "synopsis": "Within the narrow walls of the Outdoor Activities Club room, the few but lively members carry on the camping spirit as they have fun with DIY club experiments. Exciting experiences such as these have brought Nadeshiko Kagamihara deep into the world of camping, making her want to once again go on a solo trip.\n\nHowever, other plans come first, as Ayano Toki and Rin Shima invite Nadeshiko to camp near Ooi River. Eager to go on their first journey together, the girls set off to forge precious memories that will stick with their hearts for years to come.\n\n[Written by MAL Rewrite]"}, {"id": 27989, "name_en": "Sound! Euphonium", "name_jp": "響け！ユーフォニアム", "image_url": "https://cdn.myanimelist.net/images/anime/1517/142072.jpg", "genres": ["Drama"], "themes": ["Music", "Performing Arts", "School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2015, "episodes": 13, "source": "Novel", "score": 8.02, "synopsis": "Now that Kumiko Oumae has enrolled in Kitauji High School, she hopes to forget about her past. Despite her desire for a fresh start, she gets dragged into the school's band club by her new friends—Sapphire Kawashima and Hazuki Katou—and is once again stuck playing the euphonium. \n\nAs the band currently stands, they won't be able to participate in the local festival, Sunfest, let alone compete at a national level. The band's new advisor, Noboru Taki, gives them a choice: they can relax and have fun, or practice hard and attempt to get into nationals. Not wanting to repeat her mistakes from middle school, Kumiko is doubtful as to whether they should try for nationals. Amidst the chaos, she learns that her old bandmate, Reina Kousaka (who she had a bitter relationship with) has joined Kitauji's band club. Under the pressure of Noboru's strict training, Kumiko and her bandmates must learn to overcome their struggles and find success together.\n\n[Written by MAL Rewrite]"}, {"id": 38993, "name_en": "Teasing Master Takagi-san 2", "name_jp": "からかい上手の高木さん2", "image_url": "https://cdn.myanimelist.net/images/anime/1393/107033.jpg", "genres": ["Comedy", "Romance"], "themes": ["Love Status Quo", "School"], "demographics": ["Shounen"], "studios": ["Shin-Ei Animation"], "year": 2019, "episodes": 12, "source": "Manga", "score": 8.02, "synopsis": "Even after spending a considerable amount of time with Takagi, Nishikata is still struggling to find a perfect plan to defeat the expert teaser. A battle of wits, a contest of physical prowess, a test of courage—any strategy he employs to expose her weaknesses is to no avail. On the contrary, Nishikata's pitiful attempts only reveal more of his own flaws, which Takagi takes advantage of to become increasingly daring in her teasing attempts. To make things worse for Nishikata, rumors about him and Takagi may have spread in class due to the frequent interactions between them.\n\nHowever, the optimistic Nishikata believes that wisdom comes with age and that as the days go by, his experience with her constant teasing will eventually bear fruit, leading him to the awaited moment of victory. Thus, Nishikata continues to strive for the seemingly impossible—to outsmart Takagi and make her blush with embarrassment.\n\n[Written by MAL Rewrite]"}, {"id": 33161, "name_en": "My Teen Romantic Comedy SNAFU TOO! OVA", "name_jp": "やはり俺の青春ラブコメはまちがっている. 続 きっと, 女の子はお砂糖とスパイスと素敵な何かでできている。", "image_url": "https://cdn.myanimelist.net/images/anime/13/84052.jpg", "genres": ["Comedy", "Romance"], "themes": ["Love Polygon", "School"], "demographics": [], "studios": ["feel."], "year": 2016, "episodes": 1, "source": "Light novel", "score": 8.02, "synopsis": "After accepting a weekend invitation, Hachiman Hikigaya accompanies Isshiki Iroha around the Chiba Prefecture to brainstorm ideas suitable for an ideal date with Hayato Hayama. As the duo wanders from place to place without a plan, they seemingly enjoy each other's company. Yet, through these straightforward and sincere interactions, the meaning behind what it means to be genuine continues to intrigue Hachiman and his outlook for the future of the Volunteer Service Club and its members.\n\n[Written by MAL Rewrite]"}, {"id": 4901, "name_en": "Black Lagoon: Roberta's Blood Trail", "name_jp": "BLACK LAGOON Roberta's Blood Trail", "image_url": "https://cdn.myanimelist.net/images/anime/8/75529.jpg", "genres": ["Action"], "themes": ["Adult Cast", "Organized Crime"], "demographics": ["Seinen"], "studios": ["Madhouse"], "year": 2010, "episodes": 5, "source": "Manga", "score": 8.02, "synopsis": "Crime never sleeps in Roanapur, and neither does Roberta—a devoted maid and skilled guerilla soldier in service to the Venezuelan Lovelace family. After the assassination of her superior by the US Secret Service, Roberta returns to the city of debauchery in search of vengeance and his killer's head.\n\nMeanwhile, young Fernando Garcia Lovelace, the new heir to his father's estate, tails Roberta to Thailand alongside his bodyguard, Fabiola Iglesias. He enlists the help of the Lagoon Company, with the objective of deterring Roberta's warpath and bringing her back to the family. However, with the continued bloodshed and mounting tensions between Roberta, island crime factions, and the US military, it will take a significant amount of strategy—and weaponry—to stop her relentless quest for revenge.\n\n[Written by MAL Rewrite]"}, {"id": 9204, "name_en": "Heart and Yummie", "name_jp": "おまえうまそうだな", "image_url": "https://cdn.myanimelist.net/images/anime/8/25517.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Anthropomorphic", "Childcare"], "demographics": ["Kids"], "studios": ["Ajia-do"], "year": 2010, "episodes": 1, "source": "Picture book", "score": 8.02, "synopsis": "By a twist of fate, a herbivorous dinosaur finds a lost egg and brings it back to her nest. When the egg hatches, however, a carnivorous dinosaur emerges. Unable to abandon the child, she names him \"Heart\" and raises him in exile alongside her own child. As Heart comes of age, he struggles to eat the same food as his family and runs away in disgrace when he learns that he cannot live properly without meat. \n\nYears later, the now feared predator Heart encounters a situation similar to his past—he spots a dinosaur egg opposite his kind. As it emerges, Heart remarks that the newborn is delicious-looking. The newborn herbivore thinks that Heart is his father and takes delicious-looking, or \"Umasou,\" as his name. Unable to eat a newborn who loves him, Heart reluctantly decides to raise Umasou as his own. As he nurtures a forbidden child like his mother before him, Heart struggles to deal with an unforgiving world and the true natures of predator and prey.\n\n[Written by MAL Rewrite]"}, {"id": 42640, "name_en": "All Saints Street 2", "name_jp": "万圣街2", "image_url": "https://cdn.myanimelist.net/images/anime/1040/112897.jpg", "genres": ["Comedy", "Fantasy"], "themes": ["Mythology", "Urban Fantasy"], "demographics": [], "studios": ["Fenz", "HMCH"], "year": 2020, "episodes": 12, "source": "Web manga", "score": 8.02, "synopsis": "\"Dear Mom and Dad, it's already been a year since I left home. Don't worry- I'm doing great! I can take care of myself now. And I've learned to take care of others, too. Before, I never could have imagined having so many friends. But now, I can hardly imagine living without them. I think... Here, is my second home.\"\n\nIt was all a lie.\n\nSuspicion. Betrayal. Conspiracy. \n\n(Source: PV)"}, {"id": 2164, "name_en": "Den-noh Coil", "name_jp": "電脳コイル", "image_url": "https://cdn.myanimelist.net/images/anime/5/12844.jpg", "genres": ["Adventure", "Award Winning", "Drama", "Mystery", "Sci-Fi"], "themes": [], "demographics": [], "studios": ["Madhouse"], "year": 2007, "episodes": 26, "source": "Original", "score": 8.02, "synopsis": "In the near future, augmented reality has become a key part of daily life. A gentle middle school girl named Yuuko \"Yasako\" Okonogi and her family have just moved to Daikoku City despite rumors of people disappearing. There, her grandmother, nicknamed \"Mega-baa,\" runs a shop called Megasia that specializes in illegal tools which interact with parts of the virtual world.\n\nMega-baa also hosts an unofficial detective agency called \"Coil,\" a group of children around Yasako's age who find and handle corruption of the virtual world. Yasako gets involved with the group when Fumie Hashimoto, a playful member of Coil, helps rescue her cyberdog Densuke after getting trapped in virtual space while chasing a mysterious virus. Also investigating these corruptions and viruses is an abrasive hacker named Yuuko Amasawa, who the others take to calling Isako.\n\nCan Coil discover the truths behind the mysterious viruses and corruption, and if they can, at what cost?\n\n[Written by MAL Rewrite]"}, {"id": 1974, "name_en": "Glass Mask", "name_jp": "ガラスの仮面 (2005)", "image_url": "https://cdn.myanimelist.net/images/anime/7/75061.jpg", "genres": ["Drama", "Romance"], "themes": ["Performing Arts", "Showbiz"], "demographics": ["Shoujo"], "studios": ["Tokyo Movie Shinsha"], "year": 2005, "episodes": 51, "source": "Manga", "score": 8.02, "synopsis": "Two Girls. One Dream. And the entire world for a stage.\n\nAt 13 years old, Maya Kitajima seems destined to spend the rest of her life toiling in a crowded restaurant alongside her bitter and unstable mother. But when her incredible acting talent is discovered by the legendary diva Chigusa Tsukikage, Maya finds a new future filled with both golden opportunities and terrifying risks. \n\nFor Ayumi Himekawa, success has always been assured, yet she longs to be recognized for her own talents and skills, not her famous parents' connections. \n\nFor both, the ultimate prize is the role of The Crimson Goddess in the play of the same name, a part created by Chigusa. To achieve this goal, both Maya and Ayumi must seek out and conquer every acting challenge, pushing the limits of their talent and endurance to the utmost, until they are worthy of the part…\n\nBut for one to win, the other must fail! \n\n(Source: Sentai Filmworks)"}, {"id": 1506, "name_en": "Detective Conan Movie 10: The Private Eyes' Requiem", "name_jp": "名探偵コナン　探偵たちの鎮魂歌[レクイエム]", "image_url": "https://cdn.myanimelist.net/images/anime/1919/94322.jpg", "genres": ["Adventure", "Comedy", "Mystery"], "themes": ["Detective"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2006, "episodes": 1, "source": "Manga", "score": 8.02, "synopsis": "After receiving a strange invitation, Kogorou Mouri pays a visit to the Miracle Land theme park along with his daughter Ran, Conan Edogawa, and the Detective Boys. Once there, Kogorou and Conan are tasked with finishing an unsolved case by a mysterious stranger. Realizing that the invitations were actually an elaborate trap, the two have just 12 hours to solve the case or face grave danger.\n\nWith the help of familiar faces like Heiji Hattori, Kaitou Kid, and even Saguru Hakuba, the group of detectives must unravel the web of clues surrounding the case in order to find the culprit and bring them to justice before it's too late.\n\n[Written by MAL Rewrite]"}, {"id": 4918, "name_en": "xxxHOLiC Shunmuki", "name_jp": "xxxHOLiC 春夢記", "image_url": "https://cdn.myanimelist.net/images/anime/12/25079.jpg", "genres": ["Comedy", "Drama", "Mystery", "Supernatural"], "themes": [], "demographics": ["Seinen"], "studios": ["Production I.G"], "year": 2009, "episodes": 2, "source": "Manga", "score": 8.02, "synopsis": "For the appropriate price, your dearest wish can be granted at the shop of the peculiar Yuuko. Kimihiro Watanuki wishes to be rid of his ability to see spirits, and so as payment, he works for Yuuko doing whatever she needs him to do, from cleaning to errands to even helping out Yuuko's other clients. In xxxHOLiC Shunmuki, Watanuki and his friend and rival Shizuka Doumeki enjoy a meal with their friend Kohane Tsuyuri and her grandmother, reminiscing about how they have changed since meeting each other.\n\nLater on, Watanuki has a dream in which he is visited by Doumeki's grandfather Haruka, who needs him to find some things in the family temple storeroom. Accompanied by Doumeki, he finds that this task is more akin to a treasure hunt, with each item leading them to another, and another, guiding them to an unexpected yet inevitable ending.\n\n[Written by MAL Rewrite]"}, {"id": 34822, "name_en": "Tsukigakirei", "name_jp": "月がきれい", "image_url": "https://cdn.myanimelist.net/images/anime/2/85592.jpg", "genres": ["Romance"], "themes": ["School"], "demographics": [], "studios": ["feel."], "year": 2017, "episodes": 12, "source": "Original", "score": 8.02, "synopsis": "With a new school year comes a new crowd of classmates, and for their final year of junior high, aspiring writer Kotarou Azumi and track team member Akane Mizuno end up in the same class. Though initially complete strangers, a few chance encounters stir an innocent desire within their hearts. A yearning gaze, a fluttering heart—the hallmarks of young love slip into their lives as fate brings their paths to a cross.\n\nHowever, though love is patient and love is kind, Kotarou and Akane discover it is not always straightforward. Despite the comfort they find in each other's company, heartache and anxiety come hand in hand with pursuing the feelings in their hearts. With the uncertainty of how the other truly feels as well as the competing affections of those around them, the road ahead is unclear. Even so, under the shining light of a beautiful full moon, Kotarou gathers his courage to ask Akane a single question, one that forever changes their quiet relationship.\n\n[Written by MAL Rewrite]"}, {"id": 49782, "name_en": "Shadows House 2nd Season", "name_jp": "シャドーハウス 2nd Season", "image_url": "https://cdn.myanimelist.net/images/anime/1634/124231.jpg", "genres": ["Fantasy", "Mystery"], "themes": [], "demographics": ["Seinen"], "studios": ["CloverWorks"], "year": 2022, "episodes": 12, "source": "Manga", "score": 8.02, "synopsis": "After the resolution of the debut, Kate and her Doll Emilico have officially become residents of the Shadows House. However, they are under constant vigilance by the Star Bearers—an elite group in charge of the children's wing. In order to escape from their surveillance and the morbid methods they use to keep everyone's loyalty in check, Kate and Emilico must be wary of who to trust and aim to become Star Bearers themselves.\n\nMeanwhile, the Star Bearers have encountered their own problems. A mysterious robed figure dubbed \"Master Robe\" has trespassed and roams around the children's wing. At first, Master Robe is deemed harmless, but more incidents start occurring that endanger the Dolls' lives—all of them pointing to the suspicious individual.\n\nTo improve her reputation, Kate decides to solve the mystery herself. Yet, with so few clues and so many suspects, searching for Master Robe and their motive for attacking the mansion proves more challenging than she imagined.\n\n[Written by MAL Rewrite]"}, {"id": 20, "name_en": "Naruto", "name_jp": "ナルト", "image_url": "https://cdn.myanimelist.net/images/anime/1141/142503.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Martial Arts"], "demographics": ["Shounen"], "studios": ["Studio Pierrot"], "year": 2002, "episodes": 220, "source": "Manga", "score": 8.02, "synopsis": "Twelve years ago, a colossal demon fox terrorized the world. During the monster's attack on the Hidden Leaf Village, the Hokage—the village's leader and most powerful ninja—sacrifices himself to seal the beast inside a newborn, relieving civilization from destruction while dooming the baby to a lonely life.\n\nNow, after years of being shunned and bullied, Naruto Uzumaki pesters the village with elaborate pranks and vandalism. Despite these antics, he works hard to achieve his dream: to become the Hokage and earn the acknowledgement of those who have mistreated him for his entire life. Naruto joins Team 7, a ninja squad made up of two of his peers—prodigy Sasuke Uchiha and clever Sakura Haruno.\n\nUnder the aloof Kakashi Hatake's leadership, Team 7 takes on a series of difficult missions, forcing its members to grow in strength and comradery despite their many differences. Naruto strives to stand out in his rivalry with Sasuke and earn the romantic affection of Sakura. But as the trio brush against danger and death, their tragic pasts threaten to tear them apart.\n\n[Written by MAL Rewrite]"}, {"id": 41402, "name_en": "Welcome to Demon School! Iruma-kun Season 2", "name_jp": "魔入りました！入間くん", "image_url": "https://cdn.myanimelist.net/images/anime/1527/113656.jpg", "genres": ["Comedy", "Fantasy"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["Bandai Namco Pictures"], "year": 2021, "episodes": 21, "source": "Manga", "score": 8.02, "synopsis": "After many trials and tribulations, Iruma Suzuki is finally happily living among demons despite having to hide his true identity as a human. Even more so, he has now found his ambition in life: keep ranking up in this world!\n\nHowever, that plan is halted when Iruma's club is temporarily dismissed, and he is forced to be part of the student council, known for its strictness toward rowdy students. Its cold-hearted president is Amelie Azazel, Iruma's friend. Although Iruma is not used to following their rigid schedule and many rules, he still wants to prove himself and help Amelie alongside all of the other members of the council. \n\nBut trouble arises when Amelie's personality completely changes due to strange circumstances, putting the student council's reputation in jeopardy. Will Iruma be able to save them and avoid having the whole school turn into pure chaos?\n\n[Written by MAL Rewrite]"}, {"id": 59675, "name_en": "Apocalypse Hotel", "name_jp": "アポカリプスホテル", "image_url": "https://cdn.myanimelist.net/images/anime/1511/148642.jpg", "genres": ["Comedy", "Sci-Fi"], "themes": [], "demographics": [], "studios": ["CygamesPictures"], "year": 2025, "episodes": 12, "source": "Original", "score": 8.01, "synopsis": "In the year 2157, Gingarou Hotel, a luxury establishment in Ginza, Tokyo, employs advanced hotelier robots to provide best-in-class hospitality. Unfortunately, no one has visited the hotel in decades, as humanity has long abandoned Earth due to its toxic atmosphere.\n\nYachiyo, the robot \"acting acting manager,\" works tirelessly every day alongside her mechanical crew to keep the hotel running in tip-top shape. Over the years, many employees have ceased functioning, with no hope of repair or replacement. Still, Yachiyo continues to fulfill her duties, just as she promised the hotel's owner before his departure. She remains optimistic that humanity will one day return to Earth—and that she will see the owner again.\n\n[Written by MAL Rewrite]"}, {"id": 52107, "name_en": "Mononoke the Movie: Phantom in the Rain", "name_jp": "劇場版モノノ怪 唐傘", "image_url": "https://cdn.myanimelist.net/images/anime/1903/142810.jpg", "genres": ["Horror", "Mystery", "Supernatural", "Suspense"], "themes": ["Adult Cast", "Historical", "Mythology", "Psychological"], "demographics": [], "studios": ["EOTA"], "year": 2024, "episodes": 1, "source": "Original", "score": 8.01, "synopsis": "Within the Inner Chambers—the luxurious garden of women destined only for the ruler of the nation—work burdens everyone from the lowest maid to the highest-ranking concubine. After arriving on the same day to start as servants, Asa and Kame quickly become friends. The two vow to overcome any challenges in their way as they begin their splendid new lives.\n\nIn the midst of intricate power relations, uncanny rituals, and untold secrets, the Inner Chambers are thrown into an uproar when strange incidents disrupt the arrangements for an upcoming ceremony. While shogunate officers are dispatched to investigate the matter, the strange Medicine Seller knocks on the gates and demands access to these forbidden quarters, claiming that their situation is much more dangerous than they imagined.\n\n[Written by MAL Rewrite]"}, {"id": 72, "name_en": "Full Metal Panic? Fumoffu", "name_jp": "フルメタル・パニック? ふもっふ", "image_url": "https://cdn.myanimelist.net/images/anime/4/75260.jpg", "genres": ["Action", "Comedy"], "themes": ["Gag Humor", "School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2003, "episodes": 12, "source": "Light novel", "score": 8.01, "synopsis": "Sergeant Sousuke Sagara returns to Jindai High School to protect the precious war asset, Kaname Chidori, from any threat. However, his lack of social skills and real-life experience result in comical yet dangerous situations, endangering the peaceful school life Kaname longs for. As Sousuke continues to bring a wide range of weapons to school as a means to solve threats—real or fake—Kaname struggles to fulfill her duty as the student council vice president all while keeping him in check. \n\nTo ensure a successful mission, Sousuke is occasionally forced to use the costume of a famous amusement park mascot called Bonta-kun. With his technical expertise, he eventually transforms the exuberant uniform into a cutting-edge exoskeleton that has only one dysfunction: the voice translator can only produce the sound \"mofu.\" \n\nFull Metal Panic? Fumoffu depicts the adventures of Kaname and Sousuke as they try to live their normal school lives despite the chaos they inadvertently cause. \n\n[Written by MAL Rewrite]"}, {"id": 6045, "name_en": "Kimi ni Todoke: From Me to You", "name_jp": "君に届け", "image_url": "https://cdn.myanimelist.net/images/anime/1502/124384.jpg", "genres": ["Drama", "Romance"], "themes": ["School"], "demographics": ["Shoujo"], "studios": ["Production I.G"], "year": 2009, "episodes": 25, "source": "Manga", "score": 8.01, "synopsis": "Known for her semblance to the Sadako character of The Ring series, Sawako Kuronuma is given the nickname \"Sadako\" and misunderstood to be frightening and malicious like her fictional counterpart, despite having a timid and sweet nature. Longing to make friends and live a normal life, Sawako is naturally drawn to the cheerful and friendly Shouta Kazehaya, the most popular boy in her class. From their first meeting, Sawako has admired Kazehaya's ability to be the center of attention and aspires to be like him.\n\nWhen Kazehaya organizes a test of courage for the entire class and encourages her to attend, Sawako sees this as an opportunity to get along with her classmates, starting with Ayane Yano and Chizuru Yoshida. Through each new encounter and emotion she experiences, Sawako believes that meeting Kazehaya has changed her for the better. Little does Sawako know, her presence has also changed Kazehaya.\n\n[Written by MAL Rewrite]"}, {"id": 3783, "name_en": "The Garden of Sinners Chapter 3: Remaining Sense of Pain", "name_jp": "劇場版 空の境界 the Garden of sinners 第三章『痛覚残留』", "image_url": "https://cdn.myanimelist.net/images/anime/4/21735.jpg", "genres": ["Action", "Drama", "Fantasy", "Mystery", "Suspense"], "themes": ["Urban Fantasy"], "demographics": [], "studios": ["ufotable"], "year": 2008, "episodes": 1, "source": "Light novel", "score": 8.01, "synopsis": "On a solemn night in July 1998, teenager Fujino Asagami is mercilessly raped by a street gang in a dilapidated bar. No matter what physical or sexual abuse they deal, however, the girl regards her captors with the same apathetic expression. The next day, mangled bodies are discovered in that same building, so torn apart that investigators find it infeasible to even consider the culprit human.\n\nElsewhere, a client request reaches Touko Aozaki's detective agency, tasking Shiki Ryougi with either capturing or killing the perpetrator of last night's incident. But soon, word spreads that a single survivor escaped the slaughter, and now the murderer is plowing down everything in their path to locate and exterminate him. A brutal race against time begins, pitting Shiki against a dangerous foe imperceptible even to her legendary eyes. \n\n[Written by MAL Rewrite]"}, {"id": 44235, "name_en": "Pui Pui Molcar", "name_jp": "PUI PUI モルカー", "image_url": "https://cdn.myanimelist.net/images/anime/1484/119197.jpg", "genres": ["Comedy"], "themes": ["Anthropomorphic"], "demographics": ["Kids"], "studios": ["Shin-Ei Animation"], "year": 2021, "episodes": 12, "source": "Other", "score": 8.01, "synopsis": "Due to an adorable evolutionary miracle, guinea pigs have become furry automobiles known as \"Molcars.\" Now, drivers can enjoy the fluffy interior of their vehicles while fulfilling their every transportation need. However,  Molcars are not simply lettuce-fueled machines; they are also sentient creatures with their own worries and desires. Whether in their drivers' presence or among each other, these motorized rodents are swept up into many wild and eventful situations—the likes of which only a Molcar could wind up in.\n\n[Written by MAL Rewrite]"}, {"id": 5681, "name_en": "Summer Wars", "name_jp": "サマーウォーズ", "image_url": "https://cdn.myanimelist.net/images/anime/1593/116751.jpg", "genres": ["Award Winning", "Comedy", "Sci-Fi"], "themes": [], "demographics": [], "studios": ["Madhouse"], "year": 2009, "episodes": 1, "source": "Original", "score": 8.01, "synopsis": "OZ, a virtual world connected to the internet, has become extremely popular worldwide as a spot for people to engage in a large variety of activities, such as playing sports or shopping, through avatars created and customized by the user. OZ also possesses a near impenetrable security due to its strong encryption, ensuring that any personal data transmitted through the networks will be kept safe in order to protect those who use it. Because of its convenient applications, the majority of society has become highly dependent on the simulated reality, even going as far as entrusting the system with bringing back the unmanned asteroid explorer, Arawashi.\n \nKenji Koiso is a 17-year-old math genius and part-time OZ moderator who is invited by his crush Natsuki Shinohara on a summer trip. But unbeknownst to him, this adventure requires him to act as her fiancé. Shortly after arriving at Natsuki's family's estate, which is preparing for her great-grandmother's 90th birthday, he receives a strange, coded message on his cell phone from an unknown sender who challenges him to solve it. Kenji is able to crack the code, but little does he know that his math expertise has just put Earth in great danger.\n\n[Written by MAL Rewrite]"}, {"id": 44931, "name_en": "Tonikawa: Over The Moon For You - SNS", "name_jp": "トニカクカワイイ 〜SNS〜", "image_url": "https://cdn.myanimelist.net/images/anime/1765/122768.jpg", "genres": ["Comedy", "Romance"], "themes": [], "demographics": ["Shounen"], "studios": ["Seven Arcs"], "year": 2021, "episodes": 1, "source": "Manga", "score": 8.01, "synopsis": "When Tsukasa Yuzaki receives a brand new smartphone as a surprise gift from her husband Nasa, she is delighted, quickly adding in contacts and mastering the arts of LINE and sticker-sending. However, her joy is short-lived: a former coworker calls in Nasa for a difficult job, and Tsukasa is dismayed to learn that her beloved might have to work through the night.\n\nFaced with her first night alone since her marriage to Nasa, will Tsukasa and her new phone be able to overcome the distance that separates the young couple and defeat the loneliness that threatens to pull her under?\n\n[Written by MAL Rewrite]"}, {"id": 49430, "name_en": "Mignon", "name_jp": "MIGNON 민용", "image_url": "https://cdn.myanimelist.net/images/anime/1054/137743.jpg", "genres": ["Boys Love", "Drama"], "themes": ["Vampire"], "demographics": [], "studios": ["ABJ COMPANY"], "year": 2023, "episodes": 12, "source": "Original", "score": 8.01, "synopsis": "Underneath the bright city lights lies a bloodied arena bursting with frantic screams and roars. Strong men engage in boxing matches for money, while their audience desperately hopes that their chosen participants will bring them fortune. For the young rising star Mignon, there is only one place that is an escape from the chaos of the ring and his tough life. Mysterious underground doctor Oh Young-One offers his services to injured fighters, and the wide-eyed Mignon finds solace in his quiet infirmary.\n\nYoung-One is gentle and caring, always tending to Mignon's wounds that mainly come from the cruel world beyond the boxing ring. However, the doctor hides a taboo secret about himself. In a rare moment of carelessness, he exposes it to Mignon, putting his life in immediate danger. Even though Young-One warns Mignon to keep his distance in order to protect him, Mignon remains stubborn and refuses to abandon the one person who makes him feel as though he truly matters.\n\n[Written by MAL Rewrite]"}, {"id": 49587, "name_en": "All Saints Street 3", "name_jp": "万圣街3", "image_url": "https://cdn.myanimelist.net/images/anime/1995/117071.jpg", "genres": ["Comedy", "Fantasy"], "themes": ["Mythology", "Urban Fantasy"], "demographics": [], "studios": ["Fenz", "HMCH"], "year": 2022, "episodes": 12, "source": "Web manga", "score": 8.01, "synopsis": "Third season of Wan Sheng Jie."}, {"id": 34626, "name_en": "KonoSuba: God's Blessing on This Wonderful World! 2 - God's Blessing on This Wonderful Art!", "name_jp": "この素晴らしい世界に祝福を！2 この素晴らしい芸術に祝福を！", "image_url": "https://cdn.myanimelist.net/images/anime/1115/98517.jpg", "genres": ["Adventure", "Comedy", "Fantasy"], "themes": ["Isekai", "Parody"], "demographics": [], "studios": ["Studio Deen"], "year": 2017, "episodes": 1, "source": "Light novel", "score": 8.01, "synopsis": "On one noteworthy day in the Adventurer's Guild, Kazuma Satou encounters someone unexpected—a fan of his named Ran. Surprised that he even has a fan, Kazuma attempts to play it cool to impress her. Unfortunately for him, the guild's receptionist arrives with a request to defeat a giant golem guarding some ancient ruins, and Kazuma accepts only to keep Ran's admiration.\n\nUpon Kazuma and his party's successful return from the ruins, Kazuma continues to shamelessly brag to Ran. Capitalizing on his desperation, the receptionist approaches him with another quest that requires him to return to the same ruins. Hoping to find valuable treasures, Kazuma once again convinces his party to join him—but this time, he may be biting off more than he can chew.\n\n[Written by MAL Rewrite]"}, {"id": 60570, "name_en": "Ling Cage 2", "name_jp": "灵笼 第二季", "image_url": "https://cdn.myanimelist.net/images/anime/1980/150040.jpg", "genres": ["Action", "Sci-Fi"], "themes": [], "demographics": [], "studios": ["YHKT Entertainment"], "year": 2025, "episodes": 12, "source": "Original", "score": 8, "synopsis": "Second season of Ling Long."}, {"id": 59226, "name_en": "Blue Exorcist: The Blue Night Saga", "name_jp": "青の祓魔師 終夜篇", "image_url": "https://cdn.myanimelist.net/images/anime/1390/147040.jpg", "genres": ["Action", "Supernatural"], "themes": ["Mythology", "School"], "demographics": ["Shounen"], "studios": ["Studio VOLN"], "year": 2025, "episodes": 12, "source": "Manga", "score": 8, "synopsis": "Unlike his twin brother, Yukio, Rin Okumura never wished to know about their parents and the circumstances surrounding his own birth. Yet the master of time, Mephisto Pheles, gives Rin a key that transports him 40 years into the past when his late mother, Yuri Frederick Egin, was merely a child. With Mephisto's guidance, Rin leaps through the years of Yuri's childhood and her adolescence as an exorcist-in-training.\n\nEarly on, Yuri meets Shirou Fujimoto, Rin and Yukio's adoptive father. As Rin follows Yuri and Shirou's lives, he witnesses firsthand the initial appearances of Satan, his true father, and the horrors unfolding at the True Cross Order's special Section 13. Yuri's unique connection to Satan eventually leads Rin to the truth behind the most horrific event in exorcist history and the day he and Yukio were born—the Blue Night.\n\n[Written by MAL Rewrite]"}, {"id": 9963, "name_en": "Detective Conan Movie 15: Quarter of Silence", "name_jp": "名探偵コナン 沈黙の15分", "image_url": "https://cdn.myanimelist.net/images/anime/9/32035.jpg", "genres": ["Adventure", "Comedy", "Mystery"], "themes": ["Detective"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2011, "episodes": 1, "source": "Manga", "score": 8, "synopsis": "The momentous day of the opening of the new Tokyo subway, the Touto Line, has come, but a bombing incident puts all celebrations to a halt. The governor of Tokyo is caught in the blast while onboard the train, but he and everyone else present is fortunately saved by the quick thinking and actions of Conan Edogawa.\n\nIntrigued by the incident, Conan researches the governor's political history and discovers that the man was responsible for the destruction of a village in Niigata to build the Kitanosawa Dam. Believing the attack to be related to the construction of the dam, Conan, accompanied by Ran Mouri, Kogorou Mouri, Professor Agasa, Sonoko Suzuki, and the Detective Boys, decides to visit the village and investigate. \n\nThere, they meet a group of locals who lived in the old village before it was torn down. However, just as one mystery leads to another, one of the locals is murdered. Suspecting that something much more sinister is afoot, Conan vows to uncover the truth behind these two incidents before it is too late.\n\n[Written by MAL Rewrite]"}, {"id": 27663, "name_en": "Baby Steps 2nd Season", "name_jp": "ベイビーステップ 第2シリーズ", "image_url": "https://cdn.myanimelist.net/images/anime/9/73683.jpg", "genres": ["Sports"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["Studio Pierrot"], "year": 2015, "episodes": 25, "source": "Manga", "score": 8, "synopsis": "After having decided to play tennis at the professional level, Eiichirou Maruo now needs to convince his parents to support his decision. To do so, he makes a wager: if he cannot win the All-Japan Junior Tennis Tournament, he will give up on his dream. However, he will need to improve his skills quickly if he wants to qualify for the tournament and have any chance of defeating the best players in the country. For this reason, his new coach Ryuuhei Aoi suggests that Eiichirou travel abroad to train at the Florida Tennis Academy.\n\nBaby Steps 2nd Season takes the action to America as Eiichirou begins his two-week training program, getting a taste of what tennis is like outside of Japan. With this exciting experience awaiting him, Eiichirou hopes that his training will get him closer to his goal of becoming a professional player.\n\n[Written by MAL Rewrite]"}, {"id": 6421, "name_en": "Fullmetal Alchemist: Brotherhood OVA Collection", "name_jp": "鋼の錬金術師", "image_url": "https://cdn.myanimelist.net/images/anime/1493/91571.jpg", "genres": ["Action", "Adventure", "Drama", "Fantasy"], "themes": ["Military"], "demographics": ["Shounen"], "studios": ["Bones"], "year": 2009, "episodes": 4, "source": "Manga", "score": 8, "synopsis": "1. Moumoku no Renkinjutsushi (The Blind Alchemist)\n2. Simple na Hitobito (Simple People)\n3. Sensei Monogatari (The Tale of Teacher)\n4. Sore mo mata Kare no Senjou (Yet Another Man's Battlefield)"}, {"id": 6211, "name_en": "Tokyo Magnitude 8.0", "name_jp": "東京マグニチュード8.0", "image_url": "https://cdn.myanimelist.net/images/anime/1653/152734.jpg", "genres": ["Award Winning", "Drama"], "themes": [], "demographics": [], "studios": ["Bones", "Kinema Citrus"], "year": 2009, "episodes": 11, "source": "Original", "score": 8, "synopsis": "Middle school student Mirai Onozawa is dissatisfied with her family circumstances and, in a moment of frustration, wishes to tear everything apart. Unfortunately, these destructive thoughts seem to come true in the form of a magnitude 8.0 earthquake just a few moments later.\n\nWhen summer vacation begins, Mirai reluctantly takes her younger brother Yuuki to Odaiba, where a robot exhibition that he wanted to go to is being held. However, while they are in the exhibition center, the fury of a major earthquake shakes the Kanto region; helpless, both kids witness the devastating power of this natural disaster as it brings the city to its knees.\n\nIn its aftermath, they stumble upon Mari Kusakabe, a motorcyclist and single mother who decides to help the young siblings. Aiming to return to their homes and reunite with their families, the group sets off on a long and hard journey through the decimated city.\n\n[Written by MAL Rewrite]"}, {"id": 2759, "name_en": "Evangelion: 1.0 You Are (Not) Alone", "name_jp": "ヱヴァンゲリヲン新劇場版:序", "image_url": "https://cdn.myanimelist.net/images/anime/7/74975.jpg", "genres": ["Award Winning", "Drama", "Sci-Fi", "Suspense"], "themes": ["Mecha", "Psychological"], "demographics": [], "studios": ["Khara"], "year": 2007, "episodes": 1, "source": "Original", "score": 8, "synopsis": "In a post-apocalyptic world, the last remaining human settlement in Japan is the heavily fortified city of Tokyo-3. Fourteen-year-old Shinji Ikari is brought to the headquarters of NERV, an underground organization lead by his estranged father, Gendou. He requests that Shinji become a pilot of an \"Evangelion,\" a colossal android built to fight against monstrous and destructive alien creatures known as \"Angels\" that wreak havoc on the planet and threaten the survival of the remaining human race.\n\nAlthough initially reluctant, Shinji is swayed by the idea of reconciling with his father, and agrees to aid in mankind's perilous endeavor against its alien threat, as the pilot of Evangelion Unit-01. Thrust into the midst of a dangerous battlefield, Shinji must find the necessary courage and resolve to face against the Angels' incursions before it is too late.\n\n[Written by MAL Rewrite]"}, {"id": 42923, "name_en": "SK8 the Infinity", "name_jp": "SK∞ エスケーエイト", "image_url": "https://cdn.myanimelist.net/images/anime/1549/119195.jpg", "genres": ["Sports"], "themes": ["Racing"], "demographics": [], "studios": ["Bones"], "year": 2021, "episodes": 12, "source": "Original", "score": 8, "synopsis": "High school student Reki Kyan is passionate about one thing: skateboarding. When night falls, he heads to \"S,\" an illegal underground race inside a mine where skaters compete in highly dangerous situations. After a loss that results in his skateboard being destroyed and his arm being broken, Reki is now incapable of practicing at all.\n\nWhile working, Reki runs into his new classmate, Langa Hasegawa, a half-Canadian and half-Japanese boy with no skateboarding experience whatsoever. Langa is in desperate need of money. After they both visit \"S\" when tasked by Reki's boss, they get into trouble and are forced into a bet that requires Langa to skate in a race. However, the mysterious transfer student holds a trump card that Reki is unaware of, one which might help him win the race in the most unexpected way.\n\n[Written by MAL Rewrite]"}, {"id": 39783, "name_en": "The Quintessential Quintuplets 2", "name_jp": "五等分の花嫁∬", "image_url": "https://cdn.myanimelist.net/images/anime/1775/109514.jpg", "genres": ["Comedy", "Romance"], "themes": ["Harem", "School"], "demographics": ["Shounen"], "studios": ["Bibury Animation Studios"], "year": 2021, "episodes": 12, "source": "Manga", "score": 8, "synopsis": "Through their tutor Fuutarou Uesugi's diligent guidance, the Nakano quintuplets' academic performance shows signs of improvement, even if their path to graduation is still rocky. However, as they continue to cause various situations that delay any actual tutoring, Fuutarou becomes increasingly involved with their personal lives, further complicating their relationship with each other.\n\nOn another note, Fuutarou slowly begins to realize the existence of a possible connection between him and the past he believes to have shared with one of the five girls. With everyone's feelings beginning to develop and overlap, will they be able to keep their bond strictly to that of a teacher and his students—or will it mature into something else entirely?\n\n[Written by MAL Rewrite]"}, {"id": 1482, "name_en": "D.Gray-man", "name_jp": "ディー・グレイマン", "image_url": "https://cdn.myanimelist.net/images/anime/13/75194.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Urban Fantasy"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2006, "episodes": 103, "source": "Manga", "score": 8, "synopsis": "Losing a loved one is so painful that one may sometimes wish to be able to resurrect them—a weakness that the enigmatic Millennium Earl exploits. To make his mechanical weapons known as \"Akuma,\" he uses the souls of the dead that are called back. Once a soul is placed in an Akuma, it is trapped forever, and the only way to save them is to exorcise them from their vessel using the Anti-Akuma weapon, \"Innocence.\"\n \nAfter spending three years as the disciple of General Cross, Allen Walker is sent to the Black Order—an organization comprised of those willing to fight Akuma and the Millennium Earl—to become an official Exorcist. With an arm as his Innocence and a cursed eye that can see the suffering souls within an Akuma, it's up to Allen and his fellow Exorcists to stop the Millennium Earl's ultimate plot: one that can lead to the destruction of the world.\n\n[Written by MAL Rewrite]"}, {"id": 14513, "name_en": "Magi: The Labyrinth of Magic", "name_jp": "マギ The labyrinth of magic", "image_url": "https://cdn.myanimelist.net/images/anime/11/42773.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["A-1 Pictures"], "year": 2012, "episodes": 25, "source": "Manga", "score": 8, "synopsis": "A Magi is a magician whose inclination toward magic is so immense that they can be said to shape the world. With their significant influence, each Magi chooses a worthy candidate to become a king, then helps them conquer strange labyrinths called \"Dungeons\" and acquire the power of mythical djinns within. Above all else, the Magi supervises their elected representative as they build a country that might one day bring the world to its knees.\n\nAladdin is a young Magi wandering the world in search of his true self. However, his journey is not a lonely one, as he is accompanied by his friend and mentor Ugo—a djinn he summons using his flute. In his travels, Aladdin also befriends Alibaba Saluja and guides him to a nearby Dungeon. With this newfound friendship, they begin an epic adventure across the world, witnessing various irregularities that seem more frequent than ever.\n\n[Written by MAL Rewrite]"}, {"id": 16049, "name_en": "A Certain Scientific Railgun S", "name_jp": "とある科学の超電磁砲S", "image_url": "https://cdn.myanimelist.net/images/anime/1429/152870.jpg", "genres": ["Action", "Fantasy", "Sci-Fi"], "themes": ["Super Power", "Urban Fantasy"], "demographics": [], "studios": ["J.C.Staff"], "year": 2013, "episodes": 24, "source": "Manga", "score": 8, "synopsis": "Mikoto Misaka and her friends are back, investigating rumors across Academy City. Soon, Mikoto discovers something terrifying: horrific experiments are taking place throughout the city, involving the murder of thousands of espers. Moreover, these espers are far from just ordinary people: they are clones of Mikoto herself. Feeling responsible for their treatment, she sets off to put an end to the experiments; however, the forces opposing her are much more dangerous than she anticipated, and Mikoto finds herself up against some of the most powerful espers imaginable.\n\nToaru Kagaku no Railgun S continues the story of the Railgun as she desperately fights to put an end to the inhuman experiments that she believes she helped cause, her life dragged deep into despair in the process. There's never a dull moment in Academy City, but no one ever said all of them would be pleasant.\n\n[Written by MAL Rewrite]"}, {"id": 50159, "name_en": "Zombieland Saga Movie: Yumeginga Paradise", "name_jp": "劇場版『ゾンビランドサガ ゆめぎんがパラダイス』", "image_url": "https://cdn.myanimelist.net/images/anime/1552/151187.jpg", "genres": ["Comedy", "Supernatural"], "themes": ["Idols (Female)", "Music", "Parody"], "demographics": [], "studios": ["MAPPA"], "year": 2025, "episodes": 1, "source": "Original", "score": 8, "synopsis": null}, {"id": 153, "name_en": "The Twelve Kingdoms", "name_jp": "十二国記", "image_url": "https://cdn.myanimelist.net/images/anime/6/50859.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Isekai"], "demographics": [], "studios": ["Studio Pierrot"], "year": 2002, "episodes": 45, "source": "Novel", "score": 8, "synopsis": "Youko Nakajima has only ever wanted to be normal. She does what she is asked, gets good grades, is the class president, and even helps her classmates whenever she can—but because of her red hair, she has never fit in. With her pushover attitude, Youko lets classmates take advantage of her, so she has nobody she can really call a friend.\n\nBut on an otherwise ordinary day, a man who claims to be from another world barges into Youko's classroom and bows before her. This elegant blond-haired man, Keiki, claims that Youko is his master and belongs on the throne of his kingdom. However, their first meeting is cut short as Keiki has been followed by otherworldly beasts called youma. He is able to escape with Youko into his own realm, but two other classmates—Ikuya Asano and Yuka Sugimoto—are caught up in the madness as well. Unfortunately, their troubles have only just begun, as the youma attack leaves them separated from Keiki. Alone in this strange new land, these ordinary students must learn to fend for themselves or die.\n\n[Written by MAL Rewrite]"}, {"id": 23777, "name_en": "Attack on Titan: Wings of Freedom", "name_jp": "劇場版「進撃の巨人」後編～自由の翼～", "image_url": "https://cdn.myanimelist.net/images/anime/2/72510.jpg", "genres": ["Action", "Drama", "Suspense"], "themes": ["Gore", "Survival"], "demographics": ["Shounen"], "studios": ["Wit Studio"], "year": 2015, "episodes": 1, "source": "Manga", "score": 8, "synopsis": "Recap of episodes 14-25."}, {"id": 14813, "name_en": "My Teen Romantic Comedy SNAFU", "name_jp": "やはり俺の青春ラブコメはまちがっている。", "image_url": "https://cdn.myanimelist.net/images/anime/1786/120117.jpg", "genres": ["Comedy", "Romance"], "themes": ["Love Polygon", "School"], "demographics": [], "studios": ["Brain's Base"], "year": 2013, "episodes": 13, "source": "Light novel", "score": 8, "synopsis": "Hachiman Hikigaya is an apathetic high school student with narcissistic and semi-nihilistic tendencies. He firmly believes that joyful youth is nothing but a farce, and everyone who says otherwise is just lying to themselves. \n\nIn a novel punishment for writing an essay mocking modern social relationships, Hachiman's teacher forces him to join the Volunteer Service Club, a club that aims to extend a helping hand to any student who seeks their support in achieving their goals. With the only other club member being the beautiful ice queen Yukino Yukinoshita, Hachiman finds himself on the front line of other people's problems—a place he never dreamed he would be. As Hachiman and Yukino use their wits to solve many students' problems, will Hachiman's rotten view of society prove to be a hindrance or a tool he can use to his advantage?\n\n[Written by MAL Rewrite]"}, {"id": 59791, "name_en": "Ruri Rocks", "name_jp": "瑠璃の宝石", "image_url": "https://cdn.myanimelist.net/images/anime/1431/148742.jpg", "genres": ["Slice of Life"], "themes": ["CGDCT", "Educational"], "demographics": ["Seinen"], "studios": ["Studio Bind"], "year": 2025, "episodes": 13, "source": "Manga", "score": 7.99, "synopsis": "While shopping, high school student Ruri Tanigawa is captivated by a crystal necklace. Due to its high price, she can only dream of owning one—until her mother tells her about a mountainous area where Ruri's grandfather used to discover numerous minerals. As she makes her way to the location in the hope of finding materials to make her own accessories, Ruri meets Nagi Arato, a graduate student, who gladly shows her the way.\n\nSoon, Ruri is left smitten when they arrive at an enormous quartz formation. More motivated than ever, she convinces Nagi to teach her more about mineralogy. With each new discovery and Nagi's lessons deepening her love, Ruri learns that there is more to minerals than just their price.\n\n[Written by MAL Rewrite]"}, {"id": 59145, "name_en": "Ranma ½ (2024)", "name_jp": "らんま1/2", "image_url": "https://cdn.myanimelist.net/images/anime/1155/144299.jpg", "genres": ["Action", "Comedy", "Romance", "Ecchi"], "themes": ["Magical Sex Shift", "Martial Arts", "School"], "demographics": ["Shounen"], "studios": ["MAPPA"], "year": 2024, "episodes": 12, "source": "Manga", "score": 7.99, "synopsis": "During their martial arts training expedition in China, Ranma Saotome and his father Genma suffered an accident, which in turn, afflicted them with a curse—whenever they are doused with cold water, Ranma transforms into a girl, while his father turns into a panda! Only hot water can reverse these changes, but any further contact with cold water opens the can of worms once more.\n\nUnfortunately, the trouble does not end there, as Ranma finds out about his betrothal to one of the daughters of Soun Tendou, his father's closest friend. During the families' first meeting, it is decided that Ranma is to be married to Akane, the youngest daughter, a decision that is met with vehement protests from both sides. The two are simply not compatible, yet they are forced to live under one roof. Ranma's status quo further adds to the chaos, leading him to a series of comedic situations and misunderstandings that, in the grand scheme of things, may just be what he needs to work with Akane.\n\n[Written by MAL Rewrite]"}, {"id": 37870, "name_en": "BanG Dream! 3rd Season", "name_jp": "BanG Dream!（バンドリ！）第3期", "image_url": "https://cdn.myanimelist.net/images/anime/1985/104767.jpg", "genres": [], "themes": ["CGDCT", "Music", "School"], "demographics": [], "studios": ["SANZIGEN"], "year": 2020, "episodes": 13, "source": "Mixed media", "score": 7.99, "synopsis": "As the all-girl band Poppin’Party continues to rise in fame, they find a poster for an event at the legendary Nippon Budoukan. Now determined to participate in the \"BanG Dream,\" Kasumi Toyama and the other members of Poppin’Party aim to reach new heights and prove their worth by performing on that renowned stage.\n\nMeanwhile, rival band RAISE A SUILEN dreams of changing the world with \"the strongest music,\" but finds itself in dire straits without a guitarist. Though dozens of potential candidates audition for the role, band leader CHU² believes them to be amateurs and unsuitable for the band. However, there may be one guitarist that lives up to CHU²’s high standards.\n\nBanG Dream! 3rd Season features multiple all-girl bands, including Poppin’Party, Afterglow, Pastel*Palettes, Roselia, Hello Happy World!, and now RAISE A SUILEN, as they work to astound fans throughout Japan with their spectacular performances. As the BanG Dream competition draws near, the band members are prepared to captivate audiences with their heartfelt music!\n\n[Written by MAL Rewrite]"}, {"id": 6171, "name_en": "Saint Seiya: The Lost Canvas", "name_jp": "聖闘士星矢 THE LOST CANVAS 冥王神話", "image_url": "https://cdn.myanimelist.net/images/anime/8/75546.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Mythology"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2009, "episodes": 13, "source": "Manga", "score": 7.99, "synopsis": "A Holy War, from ancient mythology, where the Goddess Athena and Hades have fought against each other while defending the earth repeatedly over the span of 200 years. The story takes place in 18th century Europe, 243 years prior to the original \"Saint Seiya\" Three small children, Tenma, Alone, and Sasha have all shared a very happy childhood together. Tenma who is quite aggressive but upstanding has moved to Sanctuary to become a saint. It is there that he is reunited with Sasha who is the sister of Alone and learns that she is the reincarnation of Goddess Athena. Alone, who is kind, gentle and loves painting was chosen for the body of enemy King Hades. Tenma eventually becomes a saint of Pegasus and engages in a fierce battle with his best friend Alone, the King of Hades. Pegasus Tenma, King Hades, and the Goddess Athena and through the twist of their 3 fates merge together which unfolds a prologue to the original Saint Seiya.\n\n(Source: TMS Entertaiment)"}, {"id": 41781, "name_en": "BanG Dream! Movie: Episode of Roselia - II: Song I Am.", "name_jp": "劇場版 BanG Dream! Episode of Roselia II:Song I am.", "image_url": "https://cdn.myanimelist.net/images/anime/1246/115689.jpg", "genres": [], "themes": ["Music"], "demographics": [], "studios": ["SANZIGEN"], "year": 2021, "episodes": 1, "source": "Mixed media", "score": 7.99, "synopsis": "After overcoming numerous tribulations, the members of Roselia promised to appear on FUTURE WORLD FES.\nIt is now Autumn...\n\nThrough writing the lyrics of \"Promise,\" Lisa has matured.\nWatching the change of color in the autumn leaves, Yukina and Sayo expressed their resolve in a picture letter to their future selves.\nDriven by the wish to do something for Roselia, Rinko has started to compose a new song.\n\"The remaining quests are the contest, the tournament, and FUTURE WORLD FES...\"\nReflecting on their goals, Ako starts to feel anxious.\n\nWhat view will the five of them see beyond FUTURE WORLD FES?\n\n(Source: official site)"}, {"id": 2167, "name_en": "Clannad", "name_jp": "CLANNAD", "image_url": "https://cdn.myanimelist.net/images/anime/1804/95033.jpg", "genres": ["Drama", "Romance"], "themes": ["School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2007, "episodes": 23, "source": "Visual novel", "score": 7.99, "synopsis": "Tomoya Okazaki is a delinquent who finds life dull and believes he'll never amount to anything. Along with his friend Youhei Sunohara, he skips school and plans to waste his high school days away.\n\nOne day while walking to school, Tomoya passes a young girl muttering quietly to herself. Without warning she exclaims \"Anpan!\" (a popular Japanese food) which catches Tomoya's attention. He soon discovers the girl's name is Nagisa Furukawa and that she exclaims things she likes in order to motivate herself. Nagisa claims they are now friends, but Tomoya walks away passing the encounter off as nothing.\n\nHowever, Tomoya finds he is noticing Nagisa more and more around school. Eventually he concedes and befriends her. Tomoya learns Nagisa has been held back a year due to a severe illness and that her dream is to revive the school's drama club. Claiming he has nothing better to do, he decides to help her achieve this goal along with the help of four other girls.\n\nAs Tomoya spends more time with the girls, he learns more about them and their problems. As he attempts to help each girl overcome her respective obstacle, he begins to realize life isn't as dull as he once thought.\n\n[Written by MAL Rewrite]"}, {"id": 2004, "name_en": "Hanada Shounen-shi", "name_jp": "花田少年史", "image_url": "https://cdn.myanimelist.net/images/anime/2/87319.jpg", "genres": ["Comedy", "Drama", "Supernatural"], "themes": [], "demographics": ["Seinen"], "studios": ["Madhouse"], "year": 2002, "episodes": 25, "source": "Manga", "score": 7.99, "synopsis": "Ichiro Hanada is a hyperactive little boy who lives with his parents, sister, and grandfather in a rural town. He is always up to some kind of mischief, often teasing his sister or making rude comments to others. Consequently, his mother constantly scolds him, and even the neighbours express disturbance from time to time on how rowdy he can be.\n\nOne day, after pulling a terrible prank, Ichiro sprints onto the streets as his mother chases him. He steals a nearby bicycle and takes on a dangerous route, eventually being hit by a truck. Miraculously, he survives the crash, requiring nine stitches to the back of his head and balding for the surgery. However, the near-death experience gains him the ability to see ghosts—the last thing he needs in his life.\n\nSince Ichiro is the only one who can communicate with them, several ghosts of people who have recently died come to him, seeking help to fulfill their last wishes before achieving enlightenment. Each adventure with a ghost leaves the young and curious boy with a different lesson that gradually makes him wiser.\n\n[Written by MAL Rewrite]"}, {"id": 1914, "name_en": "Tales of Saiunkoku", "name_jp": "彩雲国物語 第2シリーズ", "image_url": "https://cdn.myanimelist.net/images/anime/1998/139962.jpg", "genres": ["Adventure", "Fantasy", "Romance"], "themes": ["Historical", "Reverse Harem"], "demographics": ["Shoujo"], "studios": ["Madhouse"], "year": 2007, "episodes": 39, "source": "Light novel", "score": 7.99, "synopsis": "Shuurei Kou and her friend Eigetsu To, a boy prodigy of humble origins, have been appointed co-governors of the Sa province, one of the eight provinces in Saiunkoku. Together, they decide to make the province an academic research center in the hopes of bringing a long overdue prosperity to the region.\n\nHowever, while Shuurei goes to the capital to obtain approvals for the ambitious project, the Sa province's recently established tranquility is threatened by a pandemic that brings both death and turmoil as it begins to spread among the people. Counting on Eigetsu to monitor the situation until her return, Shuurei seeks support from her allies to find a suitable treatment. Yet, Eigetsu's past personal conflicts distract him, providing an opportunity for opponents of Shuurei's position to take advantage of the troubles and undermine her authority.\n\nBecoming a government official has been Shuurei's lifelong dream, but it is no easy task for the first woman undertaking such a position. Will she step up and overcome this great challenge or give in to the looming adversities?\n\n[Written by MAL Rewrite]"}, {"id": 5365, "name_en": "La Maison en Petits Cubes", "name_jp": "つみきのいえ", "image_url": "https://cdn.myanimelist.net/images/anime/1063/117013.jpg", "genres": ["Award Winning", "Drama"], "themes": [], "demographics": [], "studios": ["Oh! Production"], "year": 2008, "episodes": 1, "source": "Original", "score": 7.99, "synopsis": "In a flooded town where the waters are ever-rising, an old man must constantly build new floors onto his home in order to keep dry. But when his favorite smoking pipe falls into the watery abyss beneath him, he dives into the depths of not only his house, but memories of years past.\n\nTsumiki no Ie is a short film about the everlasting effect of time on one's life—how it can swallow the past entirely, and how one must learn to continue moving forward despite what has already happened.\n\n[Written by MAL Rewrite]"}, {"id": 387, "name_en": "Haibane Renmei", "name_jp": "灰羽連盟", "image_url": "https://cdn.myanimelist.net/images/anime/9/13134.jpg", "genres": ["Drama", "Fantasy", "Mystery"], "themes": ["Psychological"], "demographics": [], "studios": ["Radix"], "year": 2002, "episodes": 13, "source": "Other", "score": 7.99, "synopsis": "Born from a cocoon in the village of Old Home, a young Haibane—a being with a halo and small gray wings—awakens to a world she does not understand without memories of her past. Named Rakka for the dream of falling she had while inside the cocoon, she soon becomes accustomed to life in the strange town. However, there are strict rules for the Haibane, such as being forbidden to leave the village or go near the walls surrounding it. These, along with mysterious disappearances of their kind on their \"Day of Flight,\" begin to unsettle Rakka and the others since they know almost nothing about their own kind.\n \nAs Rakka and the other Haibane live their lives with no memories of the past, they try to break free from their former pain and ultimately find salvation.\n \n[Written by MAL Rewrite]"}, {"id": 7054, "name_en": "Maid Sama!", "name_jp": "会長はメイド様!", "image_url": "https://cdn.myanimelist.net/images/anime/6/25254.jpg", "genres": ["Comedy", "Romance"], "themes": ["School"], "demographics": ["Shoujo"], "studios": ["J.C.Staff"], "year": 2010, "episodes": 26, "source": "Manga", "score": 7.99, "synopsis": "Misaki Ayuzawa is a unique phenomenon within Seika High School. In a predominantly male institution, she became the first-ever female student council president through her honesty and diligence. Ever since Misaki got promoted to the position, she has been working tirelessly to ensure a better school life for all girls. Despite that, Misaki is very strict with the boys, which has earned her the title \"Demon President.\" \n\nOne day, after hearing a girl cry in the hallway, Misaki encounters Takumi Usui—the most popular boy in the school—as he rejects a love confession. Enraged at what she is seeing, Misaki reprimands him for making the girl cry. However, Usui is indifferent and brushes it off as nothing.\n\nUnexpectedly, Misaki soon runs into Usui again, but this time when she is working at a maid cafe! Embarrassed that someone has found out about her secret occupation, Misaki promises herself not to let Usui destroy her reputation. However, the mysterious boy now begins to visit the same cafe regularly to observe and tease Misaki. When push comes to shove, will Usui still be able to keep the president's secret?\n\n[Written by MAL Rewrite]"}, {"id": 269, "name_en": "Bleach", "name_jp": "BLEACH - ブリーチ -", "image_url": "https://cdn.myanimelist.net/images/anime/1541/147774.jpg", "genres": ["Action", "Adventure", "Supernatural"], "themes": [], "demographics": ["Shounen"], "studios": ["Studio Pierrot"], "year": 2004, "episodes": 366, "source": "Manga", "score": 7.99, "synopsis": "Ichigo Kurosaki is an ordinary high schooler—until his family is attacked by a Hollow, a corrupt spirit that seeks to devour human souls. It is then that he meets a Soul Reaper named Rukia Kuchiki, who gets injured while protecting Ichigo's family from the assailant. To save his family, Ichigo accepts Rukia's offer of taking her powers and becomes a Soul Reaper as a result.\n\nHowever, as Rukia is unable to regain her powers, Ichigo is given the daunting task of hunting down the Hollows that plague their town. However, he is not alone in his fight, as he is later joined by his friends—classmates Orihime Inoue, Yasutora Sado, and Uryuu Ishida—who each have their own unique abilities. As Ichigo and his comrades get used to their new duties and support each other on and off the battlefield, the young Soul Reaper soon learns that the Hollows are not the only real threat to the human world.\n\n[Written by MAL Rewrite]"}, {"id": 58143, "name_en": "The Girl Downstairs Extra", "name_jp": "爱上她的理由", "image_url": "https://cdn.myanimelist.net/images/anime/1569/141456.jpg", "genres": ["Romance"], "themes": ["Adult Cast"], "demographics": [], "studios": ["Red Dog Culture House"], "year": 2024, "episodes": 1, "source": "Web manga", "score": 7.98, "synopsis": "Extra episode of Aishang Ta de Liyou."}, {"id": 48755, "name_en": "Imaizumi Brings All the Gyarus to His House", "name_jp": "今泉ん家はどうやらギャルの溜まり場になってるらしい", "image_url": "https://cdn.myanimelist.net/images/anime/1862/140626.jpg", "genres": ["Hentai"], "themes": ["Harem"], "demographics": [], "studios": ["T-Rex"], "year": 2021, "episodes": 0, "source": "Manga", "score": 7.98, "synopsis": "Imaizumi is a young regular highschooler, but somehow his house ended up as a hangout place for 3 girls from his school. The story starts off when the girls are playing a game in Imaizumi's room, while one of the girls is hugging him from behind as they play. The other girls get thirsty and decide to go to the grocery store to buy some snacks, leaving him and the blonde girl at the house. That's when the blondie takes action. After their arrival from the store, they see the boy and the girl doing it. The girls can't help but notice how exquisite his \"thing\" is and naturally they start playing a different kind of game."}, {"id": 35382, "name_en": "Yuri!!! On ICE: Yuri Plisetsky GPF in Barcelona EX \"Welcome to The Madness\"", "name_jp": "ユーリ!!! on ICE Yuri Plisetsky GPF in Barcelona EX \"Welcome to The Madness\"", "image_url": "https://cdn.myanimelist.net/images/anime/6/85548.jpg", "genres": ["Sports"], "themes": ["Performing Arts"], "demographics": [], "studios": ["MAPPA"], "year": 2017, "episodes": 1, "source": "Original", "score": 7.98, "synopsis": "In his exhibition performance for the Grand Prix of Figure Skating Final in Barcelona, 15-year-old Yuri Plisetsky seeks to defy his \"Russian Fairy\" nickname by showing off wild new choreography, the likes of which have never been seen from him on the ice before.\n\n[Written by MAL Rewrite]"}, {"id": 36456, "name_en": "My Hero Academia Season 3", "name_jp": "僕のヒーローアカデミア", "image_url": "https://cdn.myanimelist.net/images/anime/1319/92084.jpg", "genres": ["Action"], "themes": ["School", "Super Power"], "demographics": ["Shounen"], "studios": ["Bones"], "year": 2018, "episodes": 25, "source": "Manga", "score": 7.98, "synopsis": "As summer arrives for the students at UA Academy, each of these superheroes-in-training puts in their best efforts to become renowned heroes. They head off to a forest training camp run by UA's pro heroes, where the students face one another in battle and go through dangerous tests, improving their abilities and pushing past their limits. However, their school trip is suddenly turned upside down when the League of Villains arrives, invading the camp with a mission to capture one of the students. \n\nBoku no Hero Academia 3rd Season follows Izuku \"Deku\" Midoriya, an ambitious student training to achieve his dream of becoming a hero similar to his role model—All Might. Being one of the students caught up amidst the chaos of the villain attack, Deku must take a stand with his classmates and fight for their survival.\n\n[Written by MAL Rewrite]"}, {"id": 16706, "name_en": "The World God Only Knows: Goddesses", "name_jp": "神のみぞ知るセカイ 女神篇", "image_url": "https://cdn.myanimelist.net/images/anime/6/51949.jpg", "genres": ["Comedy", "Romance", "Supernatural"], "themes": ["Harem", "Otaku Culture"], "demographics": ["Shounen"], "studios": ["Manglobe"], "year": 2013, "episodes": 12, "source": "Manga", "score": 7.98, "synopsis": "Having freed a myriad of women from the runaway spirits possessing their hearts, the \"God of Conquest\" Keima Katsuragi is confronted with a new task: find the Jupiter Sisters, the goddesses that sealed Old Hell in the past. Diana, the goddess that resides inside his childhood friend Tenri Ayukawa, explains that they have taken shelter in the hearts of the girls he had assisted previously. Moreover, once Diana and her sisters are reunited, their power can seal the runaway spirits away for good and relieve Keima of his exorcising duties. Though he is initially reluctant to get involved in yet another chore, everything changes when tragedy befalls one of the hosts.\n\nDiscovering that the goddesses are being targeted by a mysterious organization known as Vintage, Keima is caught in a race against time to reunite the sisters and rescue the girl who has already fallen prey. With deeper resolve than ever before, Keima works together with demons Elsie and Haqua to recapture the hearts of the girls he had charmed in the past. However, the road ahead is a difficult one, as he is soon met with the consequences of his previous conquests.\n\n[Written by MAL Rewrite]"}, {"id": 14807, "name_en": "The Garden of Sinners: Future Gospel", "name_jp": "劇場版 空の境界 未来福音", "image_url": "https://cdn.myanimelist.net/images/anime/6/56621.jpg", "genres": ["Drama", "Mystery", "Supernatural"], "themes": [], "demographics": [], "studios": ["ufotable"], "year": 2013, "episodes": 1, "source": "Light novel", "score": 7.98, "synopsis": "​Shiki Ryougi, Mikiya Kokutou, and Touko Aozaki begin investigating a bomber after they witness a nearby explosion. That same night, Shiki catches a glimpse of the bomber, and as a result, he becomes fixated on her. To get rid of her, the madman plays a game of cat and mouse in attempts to lure her to an empty parking garage. And bombs are not the only thing he has in his arsenal: he also possesses the ability to see the future, and he intends to bring an end to Shiki.\n\nElsewhere a few days prior, a student at Reien Girls' Academy, Shizune Seo, plans to head home for the summer. However, while exiting a bus, she has a vision of the future involving a nearby stranger's death. While trying to warn the stranger, she meets Mikiya—who succeeds in utilizing Shizune's information effectively.\n\nSubsequently, an employee is sent on a job with his employer's 10-year-old daughter in tow. However, the subject of his investigation turns out to be a ghost from both of their pasts.\n\nMirai Fukuin tells the stories set during the main timeline of the Kara no Kyoukai films, as well as one set in the future.\n\n[Written by MAL Rewrite]"}, {"id": 42250, "name_en": "Bungo Stray Dogs Wan!", "name_jp": "文豪ストレイドッグス わん！", "image_url": "https://cdn.myanimelist.net/images/anime/1293/111430.jpg", "genres": ["Comedy"], "themes": [], "demographics": [], "studios": ["Bones", "Nomad"], "year": 2021, "episodes": 12, "source": "Web manga", "score": 7.98, "synopsis": "Atsushi Nakajima has finally grown accustomed to the crazy lifestyle that comes with being part of the Armed Detective Agency. But even during peaceful periods of time, there is bound to be chaos! As Atsushi, his friends, and the agency's rival group—the Port Mafia—go about their days roaming the streets of Yokohama, there are no limits to the strange situations they are unwillingly thrown into. \n\n[Written by MAL Rewrite]"}, {"id": 40174, "name_en": "Zombie Land Saga Revenge", "name_jp": "ゾンビランドサガ リベンジ", "image_url": "https://cdn.myanimelist.net/images/anime/1118/115156.jpg", "genres": ["Comedy", "Supernatural"], "themes": ["Idols (Female)", "Music", "Parody"], "demographics": [], "studios": ["MAPPA"], "year": 2021, "episodes": 12, "source": "Original", "score": 7.98, "synopsis": "The Franchouchou girls are back and better than ever! Unfortunately for them, they are also heavily in debt. After a concert bombs, the undead members are forced to take day jobs to pay off the venue, and their normally eccentric manager, Koutarou Tatsumi, sinks into alcoholism.\n\nBut dark days cannot keep these idols down! Rival idols appear, a famous talk show host retires, and existentialism takes root. Koutarou's legacy will slowly unravel while a journalist closes in on the truth. As Saga plunges into chaos, Franchouchou may realize being dead is the least of their worries—but at least the legendary Tae Yamada gets even more legendary!\n\n[Written by MAL Rewrite]"}, {"id": 4789, "name_en": "ef - a tale of melodies.", "name_jp": "ef - a tale of melodies.", "image_url": "https://cdn.myanimelist.net/images/anime/1675/131993.jpg", "genres": ["Drama", "Mystery", "Romance", "Supernatural"], "themes": [], "demographics": [], "studios": ["Shaft"], "year": 2008, "episodes": 12, "source": "Visual novel", "score": 7.98, "synopsis": "In a story set years in the past, Himura Yuu is a studious and diligent young man intent solely on maintaining his top academic position at Otowa Academy. One day, he meets a mysterious girl named Amamiya Yuuko, who, to his surprise, recognizes him. Memories of a distant childhood, memories rather left forgotten... meeting Yuuko again will force Yuu to confront the regrets and sorrows of their collective pasts and presents.\n\nIn the present, Kuze Shuuichi may seem like a womanizer, but upon closer inspection, is a man who would rather be left alone. Hayama Mizuki, however, is not the type of girl who would let him be, especially after hearing the beautiful sounds of his violin performance. As Mizuki attempts to become closer to him, Kuze attempts to push her away—the tale of their budding relationship is darkened with undertones of an imminent tragedy.\n\n[Written by MAL Rewrite]"}, {"id": 3167, "name_en": "Time of Eve", "name_jp": "イヴの時間", "image_url": "https://cdn.myanimelist.net/images/anime/13/19480.jpg", "genres": ["Sci-Fi", "Slice of Life"], "themes": [], "demographics": [], "studios": ["Studio Rikka"], "year": 2008, "episodes": 6, "source": "Original", "score": 7.98, "synopsis": "In future Japan, in a time when android housekeepers have become commonplace, society strictly abides by the Three Laws of Robotics, which all androids must follow. Under the influence of the Robot Ethics Committee, androids are treated the same way as lesser technology, such as household appliances. However, a minority with an adoration for androids exists, categorized as \"android-holics,\" and are shunned by the general public.\n\nRikuo Sakisaka was raised to accept society's precept about androids, and is perfectly aware that they are not human. That is, until the day he discovers a strange message buried within the activity logs of his household android, Sammy. This leads him to Eve no Jikan, a cafe with only one rule that its patrons must adhere to: there must be no distinction made between humans and androids. Curiosity drives Rikuo to learn more about the shop, and he attempts to unearth the reason behind Sammy's peculiar behavior.\n\n[Written by MAL Rewrite]"}, {"id": 48585, "name_en": "Black Clover: Sword of the Wizard King", "name_jp": "ブラッククローバー 魔法帝の剣", "image_url": "https://cdn.myanimelist.net/images/anime/1337/136363.jpg", "genres": ["Action", "Comedy", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Studio Pierrot"], "year": 2023, "episodes": 1, "source": "Manga", "score": 7.98, "synopsis": "As a lionhearted boy who can’t wield magic strives for the title of Wizard King, four banished Wizard Kings of yore return to crush the Clover Kingdom.\n\n(Source: Netflix)"}, {"id": 2563, "name_en": "Aria the OVA: Arietta", "name_jp": "ARIA The OVA ~ARIETTA~ (アリア ジ オーブイエー ~アリエッタ~)", "image_url": "https://cdn.myanimelist.net/images/anime/11/77622.jpg", "genres": ["Sci-Fi", "Slice of Life"], "themes": ["Iyashikei"], "demographics": ["Shounen"], "studios": ["HAL Film Maker"], "year": 2007, "episodes": 1, "source": "Manga", "score": 7.98, "synopsis": "Akari Mizunashi dreams of life after attaining her goal of becoming a Prima Undine, but upon waking realizes that she might not actually have the confidence to run Aria Company on her own. Wary of the future, she seeks advice from her mentor Alicia Florence—one of the three great \"Water Fairies\" of Neo-Venezia.\n\nMuch to Akari's surprise, Alicia reveals her own struggles of the past, but also shares the steps she took in overcoming her self-doubt of running the company and taking on an apprentice. Finding solace in her teacher's words, Akari prepares to embrace whatever trials she must overcome on her path toward achieving her dreams.\n\n[Written by MAL Rewrite]"}, {"id": 50631, "name_en": "Komi Can't Communicate Season 2", "name_jp": "古見さんは、コミュ症です。 2", "image_url": "https://cdn.myanimelist.net/images/anime/1108/121157.jpg", "genres": ["Comedy"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["OLM"], "year": 2022, "episodes": 12, "source": "Manga", "score": 7.98, "synopsis": "After an exciting and momentous cultural festival, Shouko Komi continues her endeavor to make one hundred friends alongside her friend and classmate Hitohito Tadano. As winter begins, the class is joined by the seemingly delinquent student Makoto Katai, who has been absent since the first week of school. Despite his intimidating appearance, Katai has difficulty communicating with others and just wants to befriend his classmates.\n\nAs new friendships form and current ones deepen, Komi and Tadano’s relationship begins to change—though not necessarily for the worse.\n\n[Written by MAL Rewrite]"}, {"id": 9656, "name_en": "Kimi ni Todoke: From Me to You Season 2", "name_jp": "君に届け 2ND SEASON", "image_url": "https://cdn.myanimelist.net/images/anime/1311/121574.jpg", "genres": ["Drama", "Romance"], "themes": ["School"], "demographics": ["Shoujo"], "studios": ["Production I.G"], "year": 2011, "episodes": 12, "source": "Manga", "score": 7.98, "synopsis": "After a momentous New Year's vacation and with Valentine's Day approaching, Sawako Kuronuma is beginning to get along with her classmates. However, now that Sawako has realized her romantic feelings for the popular Shouta Kazehaya, she grows hesitant toward giving him obligatory chocolates and decides to not give him any. In turn, Kazehaya, who likes Sawako, feels a distance between them.\n\nAs February ends and April arrives, the second year of high school begins for Sawako. Luckily, she ends up in the same class as her friends Ayane Yano and Chizuru Yoshida, along with Kazehaya and his friend Ryuu Sanada, in addition to the newcomer named Kento Miura. When Kento develops an interest in Sawako, Sawako and Kazehaya's feelings for each other are put to the test.\n\n[Written by MAL Rewrite]"}, {"id": 16, "name_en": "Honey and Clover", "name_jp": "ハチミツとクローバー", "image_url": "https://cdn.myanimelist.net/images/anime/1301/133577.jpg", "genres": ["Comedy", "Drama", "Romance"], "themes": ["Adult Cast", "Love Polygon", "Visual Arts"], "demographics": ["Josei"], "studios": ["J.C.Staff"], "year": 2005, "episodes": 24, "source": "Manga", "score": 7.98, "synopsis": "Yuuta Takemoto, a sophomore at an arts college, shares a cheap apartment with two seniors—the eccentric Shinobu Morita, who keeps failing to graduate due to his absenteeism, and the sensible Takumi Mayama, who acts as a proper senior to Takemoto, often looking out for him.\n\nTakemoto had not given much thought to his future until one fine spring day, when he meets the endearing Hagumi Hanamoto and falls in love at first sight. Incredibly gifted in the arts, Hagumi enrolls in Takemoto's university and soon befriends the popular pottery student Ayumi Yamada. Ayumi is already well acquainted with the three flatmates and secretly harbors deep feelings for one of them.\n\nHachimitsu to Clover is a heartwarming tale of youth, love, soul-searching, and self-discovery, intricately woven through the complex relationships between five dear friends.\n\n[Written by MAL Rewrite]"}, {"id": 59001, "name_en": "How Dare You!?", "name_jp": "成何体统", "image_url": "https://cdn.myanimelist.net/images/anime/1198/153843.jpg", "genres": ["Comedy", "Romance"], "themes": ["Historical", "Isekai"], "demographics": [], "studios": ["Shengying Animation"], "year": 2024, "episodes": 24, "source": "Web novel", "score": 7.97, "synopsis": "Yu Wanyin is teleported into a novel about palace struggles. The female protagonist of the novel is also teleported into it. To reverse her fate as a villain, Yu Wanyin chooses to join forces with a tyrant, who also turns out to be a modern man teleported in. Yu Wanyin hopes for the three modern people to put aside the palace struggles and just play cards and eat hotpot together peacefully. However, the female protagonist believes she is the chosen one, and still chooses to go against Yu Wanyin and the male protagonist King Duan's team. Thus, Yu Wanyin has to carry out a series of actions in order to survive and help Xia dynasty prevent the drought crisis...\n\n(Source: iQIYI EN)"}, {"id": 42166, "name_en": "Violet Evergarden CMs", "name_jp": "ヴァイオレット・エヴァーガーデン", "image_url": "https://cdn.myanimelist.net/images/anime/1485/125509.jpg", "genres": ["Drama"], "themes": [], "demographics": [], "studios": ["Kyoto Animation"], "year": 2016, "episodes": 2, "source": "Light novel", "score": 7.97, "synopsis": "Special trailers released on YouTube announcing Violet Evergarden's anime adaptation. Features all original animation not used in the series."}, {"id": 39468, "name_en": "Ascendance of a Bookworm", "name_jp": "本好きの下剋上 ～司書になるためには手段を選んでいられません～", "image_url": "https://cdn.myanimelist.net/images/anime/1582/101697.jpg", "genres": ["Fantasy"], "themes": ["Isekai", "Reincarnation"], "demographics": [], "studios": ["Ajia-do"], "year": 2019, "episodes": 14, "source": "Light novel", "score": 7.97, "synopsis": "Urano Motosu loves books and has an endless desire to read literature, no matter the subject. She almost fulfills her dream job of becoming a librarian before her life is ended in an accident. As she draws her last breath, she wishes to be able to read more books in her next life.\n\nAs if fate was listening to her prayer, she wakes up reincarnated as Myne—a frail five-year-old girl living in a medieval era. What immediately comes to her mind is her passion. She tries to find something to read, only to become frustrated by the lack of books at her disposal.  \n\nWithout the printing press, books have to be written and copied by hand, making them very expensive; as such, only a few nobles can afford them—but this won't stop Myne. She will prove that her will to read is unbreakable, and if there are no books around, she will make them herself! \n\n[Written by MAL Rewrite]"}, {"id": 23199, "name_en": "Durarara!! x2 Shou", "name_jp": "デュラララ!!×２ 承", "image_url": "https://cdn.myanimelist.net/images/anime/12/67743.jpg", "genres": ["Action", "Mystery", "Supernatural"], "themes": [], "demographics": [], "studios": ["Shuka"], "year": 2015, "episodes": 12, "source": "Light novel", "score": 7.97, "synopsis": "Although peace has finally returned to Ikebukuro, many of the odd occurrences have become common sights around the city. One such case is the police's constant pursuit of Celty Sturluson, the Headless Rider. Moreover, someone has placed a large bounty on her, igniting the motivation of gang members all over to begin searching for the supernatural creature as well. Meanwhile, Mikado Ryuugamine is approached by Aoba Kuronuma, a mysterious underclassman with unknown intentions, who reveals that he knows Mikado's true identity.\n\nBut Ikebukuro's state of tranquility is short-lived, as a new threat appears in the form of a murderer who goes by the pseudonym \"Hollywood,\" known for wearing a different mask each time they commit a crime. As the various events taking place prove to be connected yet again, Ikebukuro is thrown into another conflict that threatens to engulf the entire city in chaos.\n\n[Written by MAL Rewrite]"}, {"id": 41611, "name_en": "All Saints Street", "name_jp": "万聖街", "image_url": "https://cdn.myanimelist.net/images/anime/1854/106930.jpg", "genres": ["Comedy", "Fantasy"], "themes": ["Mythology", "Urban Fantasy"], "demographics": [], "studios": ["Fenz", "HMCH"], "year": 2020, "episodes": 12, "source": "Web manga", "score": 7.97, "synopsis": "In this world, angels, demons, and other supernatural beings peacefully coexist with humans. Neil Bowman is a demon who is unlike most; he is mesmerized with human culture, and no matter how hard he tries, he can't do anything evil. Thus, he runs away from Hell and up to Earth.\n\nNeil moves in with his online friend and vampire, Ira Blood, along with Ira's many roommates. Despite coming to Earth to meet humans, Neil discovers that not a single one of his new roommates is actually human. Along with Ira, there's Lynn Angel, the stern angel landlord; Lynn's little sister, Lily, who falls from Heaven; Abu, a mummy who never speaks; and Vladimir Eliot Kirilenko, a werewolf tasked with monitoring Neil.\n\nWhat most of them don't realize—not even Neil himself—is that Neil possesses the powers of the Demon King. What effect will this have on the residents of Apartment 1031 on All Saints Street?\n\n[Written by MAL Rewrite]"}, {"id": 31173, "name_en": "Snow White with the Red Hair 2", "name_jp": "赤髪の白雪姫", "image_url": "https://cdn.myanimelist.net/images/anime/12/77834.jpg", "genres": ["Drama", "Romance"], "themes": ["Medical"], "demographics": ["Shoujo"], "studios": ["Bones"], "year": 2016, "episodes": 12, "source": "Manga", "score": 7.97, "synopsis": "Shirayuki and Zen Wistalia have finally confirmed their romantic feelings for each other, and everyone has resumed their daily lives. Shirayuki remains an apprentice court herbalist at the royal palace of Clarines, and Zen continues his duties alongside his aides.\n\nHowever, their daily routines are disrupted when Crown Prince Izana, Zen’s older brother, receives an invitation from Raji Shenazard, the prince of Tanbarun. The herbalist finds herself ordered to go to Tanbarun for seven days, to build a new friendship with the formerly selfish and haughty ruler who once ordered Shirayuki to become his concubine. Along the way, Shirayuki is bound to run into trouble once again, as she is sought by a mysterious boy named Kazuki, someone she has never met.\n\n[Written by MAL Rewrite]"}, {"id": 1023, "name_en": "Wolf's Rain OVA", "name_jp": "ウルフズ・レイン", "image_url": "https://cdn.myanimelist.net/images/anime/1091/134883.jpg", "genres": ["Action", "Adventure", "Drama", "Fantasy"], "themes": [], "demographics": [], "studios": ["Bones"], "year": 2004, "episodes": 4, "source": "Original", "score": 7.97, "synopsis": "As the world accelerates toward its own destruction, Kiba and Cheza—with the help of Tsume, Hige, Toboe, and Blue—race to reach true paradise before the entire world is rendered uninhabitable. Now reunited, Cher and Hubb decide to accompany the wolves in hopes of seeing the journey through to its end, while a distraught and confused Quent wanders aimlessly into the wasteland with his mind fixated on revenge.\n\nMeanwhile, Lord Darcia the Third has finally put his plot into motion and pursues Cheza, pitting him against the pack. As everything falls apart yet simultaneously falls into place, the wolves struggle to survive in an increasingly dangerous environment. Though the end draws near, paradise seems further away than ever before.\n\n[Written by MAL Rewrite]"}, {"id": 861, "name_en": "xxxHOLiC", "name_jp": "×××HOLiC（ホリック）", "image_url": "https://cdn.myanimelist.net/images/anime/5/21858.jpg", "genres": ["Comedy", "Drama", "Mystery", "Supernatural"], "themes": [], "demographics": ["Seinen"], "studios": ["Production I.G"], "year": 2006, "episodes": 24, "source": "Manga", "score": 7.97, "synopsis": "Kimihiro Watanuki can see spirits and other assorted supernatural creatures, which is quite a bothersome ability he strongly dislikes. On the way home one day, while plagued by some spirits, he is inexplicably compelled to enter a strange house. There, he encounters Yuuko, a mysterious woman who claims to be able to rid him of the ability to see and attract the troublesome creatures—for a price. She demands that he work at her \"store\" that grants wishes to people, and thus begins Watanuki's adventures through weird and wonderful events.\n\n[Written by MAL Rewrite]"}, {"id": 846, "name_en": "School Rumble: 2nd Semester", "name_jp": "スクールランブル 二学期", "image_url": "https://cdn.myanimelist.net/images/anime/5/75490.jpg", "genres": ["Comedy", "Romance"], "themes": ["Gag Humor", "Love Polygon", "School"], "demographics": ["Shounen"], "studios": ["Studio Comet"], "year": 2006, "episodes": 26, "source": "Manga", "score": 7.97, "synopsis": "We still have no idea what's really going on with this show, beyond the fact that the weirdness is piling up!\n\nCurry-fiend Karasama remains oblivious to Tenma's crush, and Harima still hasn't confessed his true feelings to Tenma. But hey, the thug's got an excuse since he's caught up with his comic career and knee-deep in a blizzard of deadlines! Meanwhile, the love triangles are ever-shifting thanks to wannabe cupids and runaway brides.\n\nEven when you throw in a bit of cyborg love, a shipwreck, and a fugitive red panda–seriously, that ain't even scratching the surface. You are encouraged to scratch the pig, though. He likes it! And something rumbles in the next to last episode, no matter what I say next.\n\nSchool Rumble–The absolutely funniest show you’ll ever see that's not about anything that rumbles... Ever!\n\n(Source: Funimation)"}, {"id": 543, "name_en": "Vampire Hunter D: Bloodlust", "name_jp": "バンパイアハンターD", "image_url": "https://cdn.myanimelist.net/images/anime/1571/135153.jpg", "genres": ["Action", "Drama", "Fantasy", "Horror", "Romance", "Sci-Fi"], "themes": ["Vampire"], "demographics": [], "studios": ["Madhouse"], "year": 2000, "episodes": 1, "source": "Light novel", "score": 7.97, "synopsis": "When the soothing comfort of sunlight starts to fade, beings of seraphic beauty become the undisputed masters of the night, instilling fear in every living creature. Water turns into ice before these blood-sucking monsters, and their victims realize that this uncanny chill is the harbinger of their inevitable demise. Fortunately, the number of vampires is dwindling thanks to the dauntless bounty hunters who track down and slay the near-immortal predators.\n\nOne night, tragedy befalls the Elbourne family as their daughter, Charlotte, is abducted by Meier Link, a revered vampire lord protected by an army of monsters. To save their beloved Charlotte, her family recruits the Marcus Brothers, a group of vampire hunters known for their ruthlessness; and D, a sword-wielding dhampir—a half-vampire, half-human who has the strengths of both species and the weaknesses of neither.\n\nThe hunters rush to rescue Charlotte, but if her abductor decides to turn her into a vampire, they should eliminate her. Moreover, their mission must be completed before Meier reaches the impregnable vampire castle of Carmilla and takes Charlotte out of humanity's reach forever.\n\n[Written by MAL Rewrite]"}, {"id": 2448, "name_en": "Ghost in the Shell: Stand Alone Complex 2nd GIG - Individual Eleven", "name_jp": "攻殻機動隊 S.A.C. 2nd GIG Individual Eleven", "image_url": "https://cdn.myanimelist.net/images/anime/5/2829.jpg", "genres": ["Sci-Fi"], "themes": ["Adult Cast", "Mecha"], "demographics": [], "studios": ["Production I.G"], "year": 2006, "episodes": 1, "source": "Manga", "score": 7.97, "synopsis": "Compilation movie of the \"Individual Eleven\" story from Ghost in the Shell: SAC 2nd Gig series.\n\n(Source: AniDB)"}, {"id": 50207, "name_en": "A Record Of Mortal's Journey To Immortality 2nd Season", "name_jp": "凡人修仙传 第二季", "image_url": "https://cdn.myanimelist.net/images/anime/1875/120145.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Original Force", "Wonder Cat Animation"], "year": 2021, "episodes": 51, "source": "Web novel", "score": 7.97, "synopsis": "Second season of Fanren Xiu Xian Zhuan."}, {"id": 35788, "name_en": "Food Wars! The Third Plate", "name_jp": "食戟のソーマ 餐ノ皿", "image_url": "https://cdn.myanimelist.net/images/anime/3/88434.jpg", "genres": ["Gourmet", "Ecchi"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["J.C.Staff"], "year": 2017, "episodes": 12, "source": "Manga", "score": 7.97, "synopsis": "The Moon Festival is Tootsuki Academy's annual gourmet gala, where students compete against each other to earn the most profit through selling their cuisine of choice. But for Souma Yukihira, it is also his first opportunity to challenge the Elite Ten, the supreme council that rules over the academy.  \n\nHowever, this is only the beginning of Souma's war against the Elite Ten; a nefarious plot is underway that will provide Souma with the challenge he desires but will also shake the very foundations of Tootsuki Academy itself.\n\n[Written by MAL Rewrite]"}, {"id": 60726, "name_en": "My Melody & Kuromi", "name_jp": "My Melody & Kuromi", "image_url": "https://cdn.myanimelist.net/images/anime/1964/150532.jpg", "genres": ["Fantasy"], "themes": [], "demographics": [], "studios": ["Wit Studio"], "year": 2025, "episodes": 12, "source": "Other", "score": 7.96, "synopsis": "Get ready for a delightful adventure as My Melody &amp; Kuromi brings the beloved Sanrio characters to life in a charming stop-motion animation series. Celebrating the 50th anniversary of My Melody and the 20th anniversary of Kuromi, the series takes viewers on an adventure in Mariland, their whimsical home.\n\n(Source: Netflix)"}, {"id": 59939, "name_en": "Swallowed Star: Blood Luo Continent", "name_jp": "吞噬星空剧场版 血洛大陆", "image_url": "https://cdn.myanimelist.net/images/anime/1695/145740.jpg", "genres": ["Action", "Adventure", "Fantasy", "Sci-Fi"], "themes": ["Martial Arts"], "demographics": [], "studios": ["Sparkly Key Animation Studio"], "year": 2024, "episodes": 1, "source": "Novel", "score": 7.96, "synopsis": "It's time for battle! It's time for excitement! It's time for thrills! \"This trial has a 98% mortality rate.\" \"Then... I'll eliminate everyone who threatens my life.\" Luo Feng travels to the Blood Continent for the trial to protect Earth. On this continent, he constantly pushes his limits, makes good friends and encounters powerful enemies, but he never retreats. Instead, he perseveres step by step, carrying the burden of his journey. Ultimately, he completes his infinite advancement, gaining the powerful ability to protect Earth.\n\n(Source: Tencent, translated)"}, {"id": 59077, "name_en": "Honkai: Star Rail - The Embers of Glamoth", "name_jp": "《崩坏：星穹铁道》：「格拉默的余烬」", "image_url": "https://cdn.myanimelist.net/images/anime/1367/143582.jpg", "genres": ["Action", "Fantasy", "Sci-Fi"], "themes": [], "demographics": [], "studios": [], "year": 2024, "episodes": 1, "source": "Game", "score": 7.96, "synopsis": "Article 22 of Glamoth's military regulations: Knights must give all they have to Her Majesty, the queen. Including their lives.\n\n(Source: YouTube)"}, {"id": 59047, "name_en": "Tamon's B-Side", "name_jp": "多聞くん今どっち!?", "image_url": "https://cdn.myanimelist.net/images/anime/1474/153320.jpg", "genres": ["Comedy", "Romance"], "themes": ["Idols (Male)"], "demographics": ["Shoujo"], "studios": ["J.C.Staff"], "year": 2026, "episodes": 13, "source": "Manga", "score": 7.96, "synopsis": "Idol group F/ACE has recently risen in popularity all thanks to Tamon Fukuhara—its handsome, wild, and talented leader. Seventeen-year-old Utage Kinoshita is one of the vocal fans who greatly admires F/ACE. As her life revolves around worshiping Tamon, Utage works at a housecleaning company to earn money and continue supporting him.\n\nWhen her coworker gets sick, Utage is assigned to a new client with the same name as Tamon. She believes it is merely a coincidence—until she comes face to face with the real celebrity! Her surprise only grows when she discovers that, unlike the extroverted superstar known to the public, Tamon is actually a gloomy guy with no friends or self-confidence.\n\nGiven that Utage's adoration for Tamon knows no bounds, she vows to keep his secret and stay by his side—but dealing with Tamon's contrasting behaviors in and out of work might be the toughest job out there.\n\n[Written by MAL Rewrite]"}, {"id": 34440, "name_en": "Code Geass: Lelouch of the Rebellion III - Glorification", "name_jp": "コードギアス 反逆のルルーシュⅢ 皇道", "image_url": "https://cdn.myanimelist.net/images/anime/1126/91344.jpg", "genres": ["Sci-Fi"], "themes": ["Mecha", "Military", "School", "Super Power"], "demographics": [], "studios": ["Sunrise"], "year": 2018, "episodes": 1, "source": "Original", "score": 7.96, "synopsis": "Lelouch Lamperouge's dreams of destroying the Holy Britannian Empire are slowly crumbling. His sister, Nunnally, has been kidnapped by Schneizel el Britannia, and Suzaku Kururugi is ignoring his desperate pleas for assistance. The Black Knights are slowly losing faith in Lelouch as their leader, and the United Federation of Nations has declared global war on Britannia.\n\nHaving suffered numerous betrayals, a significant loss of power, and an ever-growing resentment towards Emperor Charles zi Britannia, the crippled Lelouch makes a final decision: if he can't destroy the Empire from the outside, he will take it for himself from within.\n\n[Written by MAL Rewrite]"}, {"id": 36702, "name_en": "Attack on Titan: The Roar of Awakening", "name_jp": "劇場版 進撃の巨人 Season2〜覚醒の咆哮〜", "image_url": "https://cdn.myanimelist.net/images/anime/1710/95650.jpg", "genres": ["Action", "Drama", "Suspense"], "themes": ["Gore", "Survival"], "demographics": ["Shounen"], "studios": ["Wit Studio"], "year": 2018, "episodes": 1, "source": "Manga", "score": 7.96, "synopsis": "Eren Yeager and others of the 104th Training Corps have just begun to become full members of the Survey Corps. As they ready themselves to face the Titans once again, their preparations are interrupted by the invasion of Wall Rose—but all is not as it seems as more mysteries are unraveled. As the Survey Corps races to save the wall, they uncover more about the invading Titans and the dark secrets of their own members.\n \n[Written by MAL Rewrite]"}, {"id": 34376, "name_en": "Yuuki Yuuna wa Yuusha de Aru: Washio Sumi no Shou 3 - Yakusoku", "name_jp": "結城友奈は勇者である -鷲尾須美の章- 第３章「やくそく」", "image_url": "https://cdn.myanimelist.net/images/anime/7/86984.jpg", "genres": ["Drama"], "themes": ["Mahou Shoujo"], "demographics": [], "studios": ["Studio Gokumi"], "year": 2017, "episodes": 1, "source": "Light novel", "score": 7.96, "synopsis": "The third and final movie in the Washio Sumi no Shou trilogy."}, {"id": 27831, "name_en": "Durarara!! x2 Ten", "name_jp": "デュラララ!!×２ 転", "image_url": "https://cdn.myanimelist.net/images/anime/8/74981.jpg", "genres": ["Action", "Mystery", "Supernatural"], "themes": [], "demographics": [], "studios": ["Shuka"], "year": 2015, "episodes": 12, "source": "Light novel", "score": 7.96, "synopsis": "In Ikebukuro, the lives of its citizens continue intertwining with each other as if their fates are predestined. Mikado Ryuugamine is now one step closer to his goal of living an exciting life, and in turn, delves deeper into the darker side of Ikebukuro. After gaining absolute control over a former rival, he uses his newfound power as he pleases, purging the Dollars from the inside to mold it into the ideal organization. This proves to be as challenging as it sounds as Mikado must now deal with unwanted outside interference, most notably a re-emerging and dearly missed friend. Meanwhile, Izaya Orihara still has some schemes up his sleeve, although a rival information exchange center has proven to be quite the hindrance, lurking within everyone's favorite downtown district. Undoubtedly, sooner or later, chaos will strike again.\n\n[Written by MAL Rewrite]"}, {"id": 30015, "name_en": "ReLIFE", "name_jp": "ReLIFE", "image_url": "https://cdn.myanimelist.net/images/anime/3/82149.jpg", "genres": ["Drama", "Romance"], "themes": ["School"], "demographics": [], "studios": ["TMS Entertainment"], "year": 2016, "episodes": 13, "source": "Web manga", "score": 7.96, "synopsis": "Dismissed as a hopeless loser by those around him, 27-year-old Arata Kaizaki bounces around from one job to another after quitting his first company. His unremarkable existence takes a sharp turn when he meets Ryou Yoake, a member of the ReLife Research Institute, who offers Arata the opportunity to change his life for the better with the help of a mysterious pill. Taking it without a second thought, Arata awakens the next day to find that his appearance has reverted to that of a 17-year-old.\n\nArata soon learns that he is now the subject of a unique experiment and must attend high school as a transfer student for one year. Though he initially believes it will be a cinch due to his superior life experience, Arata is proven horribly wrong on his first day: he flunks all his tests, is completely out of shape, and can't keep up with the new school policies that have cropped up in the last 10 years. Furthermore, Ryou has been assigned to observe him, bringing Arata endless annoyance. ReLIFE follows Arata's struggle to adjust to his hectic new lifestyle and avoid repeating his past mistakes, all while slowly discovering more about his fellow classmates.\n\n[Written by MAL Rewrite]"}, {"id": 2890, "name_en": "Ponyo", "name_jp": "崖の上のポニョ", "image_url": "https://cdn.myanimelist.net/images/anime/1331/138727.jpg", "genres": ["Adventure", "Award Winning", "Fantasy"], "themes": [], "demographics": [], "studios": ["Studio Ghibli"], "year": 2008, "episodes": 1, "source": "Original", "score": 7.96, "synopsis": "A goldfish sneaks away from home and floats off on the back of a jellyfish. After getting stuck in a glass jar, she drifts to the shore where she is freed by Sousuke, a five-year-old boy who lives with his mother Lisa in a house by the sea while his father Koichi works on a fishing boat. After healing a cut on Sousuke's finger by licking it, the goldfish is named Ponyo by her new friend.\n\nUnknown to Sousuke, Ponyo already has a name and a family. Her father Fujimoto, a sorcerer who forsook his humanity to live underwater, searches frantically for his daughter Brunhilde. When found and captured, Ponyo rejects her birth name and declares that she wants to become a human. Using the power received from Sousuke's blood, she grows arms and legs and escapes to the surface once more. But the magic released into the ocean causes an imbalance in nature, causing the Moon to start falling out of orbit and the tides to grow dangerously stronger. Reunited with Ponyo, Sousuke must pass an ancient test to restore order in the world and let his companion live on as a human.\n\n[Written by MAL Rewrite]"}, {"id": 40716, "name_en": "Kakushigoto", "name_jp": "かくしごと", "image_url": "https://cdn.myanimelist.net/images/anime/1048/128385.jpg", "genres": ["Comedy"], "themes": ["Childcare", "Gag Humor", "Otaku Culture"], "demographics": ["Shounen"], "studios": ["Ajia-do"], "year": 2020, "episodes": 12, "source": "Manga", "score": 7.96, "synopsis": "Kakushi Gotou is a somewhat popular manga artist whose works are known for inappropriate content. Because of this raunchiness, when his daughter Hime was born, he vowed to keep his profession hidden from her, believing that she will be disillusioned if she finds out. \n\nThis paranoia-induced belief leads Kakushi into hectic situations. Despite being a single father, he does his best and often resorts to extreme ends just to protect his secret, such as guising as a salaryman every day or holding emergency drills in case Hime somehow finds her way to his workplace.\n\nKakushigoto tells the story of a father and daughter living side by side, maintaining their peaceful existence as the father attempts to preserve the status quo. However, there is a saying: \"there are no secrets that time cannot reveal.\" In time, Hime must learn the reality behind the things she took for granted as she grew up.\n\n[Written by MAL Rewrite]"}, {"id": 32615, "name_en": "Saga of Tanya the Evil", "name_jp": "幼女戦記", "image_url": "https://cdn.myanimelist.net/images/anime/5/82890.jpg", "genres": ["Action", "Fantasy"], "themes": ["Isekai", "Military", "Reincarnation"], "demographics": [], "studios": ["Nut"], "year": 2017, "episodes": 12, "source": "Light novel", "score": 7.96, "synopsis": "Tanya Degurechaff is a young soldier infamous for predatorial-like ruthlessness and an uncanny, tactical aptitude, earning her the nickname of the \"Devil of the Rhine.\" Underneath her innocuous appearance, however, lies the soul of a man who challenged Being X, the self-proclaimed God, to a battle of wits—which resulted in him being reincarnated as a little girl into a world of magical warfare. Hellbent on defiance, Tanya resolves to ascend the ranks of her country's military as it slowly plunges into world war, with only Being X proving to be the strongest obstacle in recreating the peaceful life she once knew. But her perceptive actions and combat initiative have an unintended side effect: propelling the mighty Empire into becoming one of the most powerful nations in mankind's history.\n\n[Written by MAL Rewrite]"}, {"id": 36963, "name_en": "IDOLiSH7 Vibrato", "name_jp": "アイドリッシュセブン Vibrato", "image_url": "https://cdn.myanimelist.net/images/anime/1131/90676.jpg", "genres": ["Drama"], "themes": ["Idols (Male)", "Music"], "demographics": [], "studios": ["TROYCA"], "year": 2018, "episodes": 8, "source": "Game", "score": 7.96, "synopsis": "On a cold night, idol group TRIGGER reunite at a bar to celebrate the anniversary of the day they started working together. Two years prior, Ryuunosuke Tsunashi leaves his Okinawan home and heads to Tokyo, hoping to become an idol and pay off his father's debt. While waiting for the manager, he is instead picked up by Gaku Yaotome—son of Yaotome Productions' CEO and Ryuunosuke's future co-worker—and they go to meet the mysterious Ten Kujou, the last member of the group.\n\nGaku wants to confront Ten, as he is dubious of Ten's position as the center of the group. Fortunately, with Ryuunosuke's help, the three of them are able to hit it off, and thus TRIGGER is born. However, there still remain obstacles they have to overcome before their rise to fame.\n\nIn the present day, their rivals IDOLiSH7 also have their own set of problems. From planning what to do on their free days to resolving a long-lasting dispute, they are ready to work together and face these problems head-on.\n\n[Written by MAL Rewrite]"}, {"id": 4672, "name_en": "Ghost in the Shell 2.0", "name_jp": "攻殻機動隊2.0", "image_url": "https://cdn.myanimelist.net/images/anime/1797/152914.jpg", "genres": ["Mystery", "Sci-Fi", "Suspense"], "themes": ["Adult Cast", "Detective", "Mecha", "Psychological"], "demographics": ["Seinen"], "studios": ["Production I.G"], "year": 2008, "episodes": 1, "source": "Manga", "score": 7.96, "synopsis": "Set in the year 2029 and following World Wars III and IV, a Japanese-led Asian block dominates world affairs. The alliance maintains its international supremacy through its elite security force whose cybernetically enhanced operatives tackle an array of hi-tech terrorists and other threats to international security. These augmented agents can \"ghost hack\" (i.e., download their consciousness) via the now omnipresent internet into other machines and human/machine cross breeds.\n\nMajor Motoko Kusanagi, a cybernetically augmented female agent, has been tracking a virtual entity known as the Puppet Master with her crack squad of security agents. The shape-shifting Puppet Master, a rogue creation of a rival agency of the security apparatus, has concluded that it is a life form in its own right, \"born in sea of information,\" and requested political asylum and true physical existence in defiance of its creators.\n\n(Source: Manga Entertainment, edited)"}, {"id": 48411, "name_en": "Aria the Benedizione", "name_jp": "ARIA The BENEDIZIONE", "image_url": "https://cdn.myanimelist.net/images/anime/1707/117069.jpg", "genres": ["Sci-Fi", "Slice of Life"], "themes": ["Iyashikei"], "demographics": ["Shounen"], "studios": ["J.C.Staff"], "year": 2021, "episodes": 1, "source": "Manga", "score": 7.96, "synopsis": "The successor and branch manager of the Himeya Company—one of the many Undine associations housing female gondoliers—Aika S. Granzchesta surprises everyone when she rejects tradition and refuses to inherit the company's legendary gondola. Aika's apprentice Azusa B. McLaren, unable to wrap her mind around her mentor's unexpected decision, confronts her directly but to little effect. To better understand her reasoning, Azusa and her fellow Undines secretly plan to arrange a meeting with Aika.\n\nWith the much-awaited winter days in sight, accompanied by the tranquility and sublimity of Neo-Venezia, Aika reminisces about her journey as an Undine that forged her into the person she is today. As she vividly remembers all those invaluable experiences of the past, she trusts they will continue to guide her into the unknown yet boundless future.\n\n[Written by MAL Rewrite]"}, {"id": 401, "name_en": "Samurai X: Reflection", "name_jp": "るろうに剣心 -明治剣客浪漫譚- 星霜編", "image_url": "https://cdn.myanimelist.net/images/anime/1971/90877.jpg", "genres": ["Drama", "Romance"], "themes": ["Historical", "Samurai"], "demographics": ["Shounen"], "studios": ["Studio Deen"], "year": 2001, "episodes": 2, "source": "Manga", "score": 7.96, "synopsis": "Kaoru goes to the harbor every day to see if Kenshin has returned. After a while, she starts looking back at her life with Kenshin and all the things that have happened.\n\n(Source: ANN)"}, {"id": 50593, "name_en": "The Tunnel to Summer, the Exit of Goodbyes", "name_jp": "夏へのトンネル, さよならの出口", "image_url": "https://cdn.myanimelist.net/images/anime/1462/125397.jpg", "genres": ["Drama", "Mystery", "Romance", "Supernatural"], "themes": [], "demographics": [], "studios": ["CLAP"], "year": 2022, "episodes": 1, "source": "Light novel", "score": 7.96, "synopsis": "Kaoru Touno's family is falling apart. After the death of a sibling and his parents' divorce, he no longer feels any peace at home. Following a heated confrontation with his father, Kaoru runs out of his house and finds himself before a mysterious tunnel, which pulls him in. Bewildered by what he sees inside, he rushes to the exit, picking up a bird that looks identical to his deceased pet on his way out.\n\nUpon emerging outside, Kaoru realizes that, despite him spending just a few minutes inside the tunnel, an entire week has passed. The bizarre experience leads him to remember the rumors of \"Urashima Tunnel\"—a passage that grants wishes in exchange for one's lifespan. Given the resurrection of his pet bird, Kaoru cannot help but wonder if another visit can help fix his messed up life.\n\nHowever, when he returns to the entrance of the mysterious tunnel, Kaoru realizes that he has been followed by Anzu Hanashiro, a new transfer student in his class. She also knows about the rumors and asks him to help her with an experiment—it turns out that she, too, has a wish that only the tunnel can grant.\n\n[Written by MAL Rewrite]"}, {"id": 59497, "name_en": "Rising Impact Season 2", "name_jp": "ライジングインパクト シーズン2", "image_url": "https://cdn.myanimelist.net/images/anime/1161/144669.jpg", "genres": ["Sports"], "themes": [], "demographics": ["Shounen"], "studios": ["Lay-duce"], "year": 2024, "episodes": 14, "source": "Manga", "score": 7.95, "synopsis": "Second season of Rising Impact."}, {"id": 58509, "name_en": "Jade Dynasty 2nd Season", "name_jp": "诛仙 第二季", "image_url": "https://cdn.myanimelist.net/images/anime/1700/142221.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Cloud Art"], "year": 2024, "episodes": 26, "source": "Web novel", "score": 7.95, "synopsis": "Ten years have passed since the great war between good and evil. Zhang Xiaofan, now known as Gui Li, fights against internal strife within the demonic sect. At this time, a rare treasure emerges in the Death Swamp, sparking a struggle among various forces. Gui Li and Lu Xueqi reunite, their love and hate deepening. Later, Gui Li investigates the disappearance of his subordinates at Fenxiang Valley, where he unexpectedly rescues the nine-tailed celestial fox, Xiao Bai, and obtains clues about the ancient witch clan. After countless near-death experiences, Gui Li finally persuades a powerful sorcerer with the ability to summon spirits to agree to go and heal Bi Yao. What will become of his ten-year-long wish…?\n\n(Source: Tencent, translated)"}, {"id": 7720, "name_en": "Big Windup! 2", "name_jp": "おおきく振りかぶって　〜夏の大会編〜", "image_url": "https://cdn.myanimelist.net/images/anime/10/18671.jpg", "genres": ["Sports"], "themes": ["Team Sports"], "demographics": ["Seinen"], "studios": ["A-1 Pictures"], "year": 2010, "episodes": 13, "source": "Manga", "score": 7.95, "synopsis": "Picking up where the original series left off, Ren Mihashi, the ace pitcher of the newly-formed Nishiura High School baseball team, and his teammates face the Sakitama High School team in the next round of the High School Baseball Invitational Tournament as they aim to play in the finals at legendary Hanshin Koushien Stadium.\n\n(Source: ANN)"}, {"id": 9996, "name_en": "Hyouge Mono", "name_jp": "へうげもの", "image_url": "https://cdn.myanimelist.net/images/anime/3/77282.jpg", "genres": [], "themes": ["Adult Cast", "Historical", "Samurai", "Visual Arts"], "demographics": ["Seinen"], "studios": ["Bee Train"], "year": 2011, "episodes": 39, "source": "Manga", "score": 7.95, "synopsis": "The story is set during Japan's Sengoku Jidai (Era of the Warring States) and centers on Furuta Sasuke, a vassal of the great warlord Oda Nobunaga and a man obsessed with tea ceremony and material desires in his pursuit of a fortuitous life. Having learned from Oda and the legendary tea master Sen no Soueki, Furuta walks the way of the Hyouge Mono.\n\n(Source: ANN)"}, {"id": 77, "name_en": "Magical Girl Lyrical Nanoha A's", "name_jp": "魔法少女リリカルなのは エース", "image_url": "https://cdn.myanimelist.net/images/anime/4/6767.jpg", "genres": ["Action", "Comedy", "Drama"], "themes": ["Mahou Shoujo"], "demographics": [], "studios": ["Seven Arcs"], "year": 2005, "episodes": 13, "source": "Original", "score": 7.95, "synopsis": "After solving the incident of the scattered Jewel Seeds, Nanoha Takamachi happily returns to her everyday life, though now with added magic practice in the morning. Exchanging video messages with Fate Testarossa and the crew of the Arthra, Nanoha eagerly awaits the chance to speak with them in person again. But while studying in her room one day, Raising Heart suddenly calls out to Nanoha and warns her of an incoming attack!\n\nThe attacker is a young girl named Vita, who calls herself a Belka Knight. She proves her strength by using an intelligent device with a mysterious cartridge system to quickly overwhelm Nanoha. Luckily, the Space-Time Administration Bureau is able to step in before she is completely crushed. Vita and her fellow knights Shamal, Signum, and Zafila are on a mission to steal magical power from mages in order to complete the Book of Darkness, one of the Lost Logia. For what sinister purpose are the knights after this Book of Darkness?\n\n[Written by MAL Rewrite]"}, {"id": 31973, "name_en": "Mobile Suit Gundam Thunderbolt", "name_jp": "機動戦士ガンダム サンダーボルト", "image_url": "https://cdn.myanimelist.net/images/anime/3/77176.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["Mecha", "Military", "Space"], "demographics": ["Seinen"], "studios": ["Sunrise"], "year": 2015, "episodes": 4, "source": "Manga", "score": 7.95, "synopsis": "In Universal Century 0079, forces of the Earth Federation and Principality of Zeon engage in a battle within the Thunderbolt Sector during the One Year War. This section of space—known for its constant strikes of electricity—proves to be a deadly battlefield, as Federation pilot Io Fleming leads a charge against Zeon's ace Daryl Lorenz and his squad of snipers. With the fighters on both sides proving to be formidable soldiers, neither side is willing to back down, fighting strategically amongst the remnants of colonies.\n\nBut when Io gets a hold of a prototype Gundam, Daryl will have to make a sacrifice in order to obtain enough power to crush his enemy and ensure that Zeon is victorious, or watch as his comrades are slaughtered by a single man.\n\n[Written by MAL Rewrite]"}, {"id": 40397, "name_en": "Sleepy Princess in the Demon Castle", "name_jp": "魔王城でおやすみ", "image_url": "https://cdn.myanimelist.net/images/anime/1448/108514.jpg", "genres": ["Comedy", "Fantasy"], "themes": ["Parody"], "demographics": ["Shounen"], "studios": ["Doga Kobo"], "year": 2020, "episodes": 12, "source": "Manga", "score": 7.95, "synopsis": "The Demon Lord Tasogare's castle is a dark and frightening place, filled to the brim with various monsters. Any soul unfortunate enough to be imprisoned here is sure to be terrified by the horrors within. However, the human princess Aurora Suya Rhys \"Syalis\" Kaymin is a different case. Rather indifferent to her situation, Syalis worries about one thing and one thing only—sleep. Ever since the demon lord kidnapped her from her kingdom, she has not had a single good night's rest.\n\nTo alleviate her dozen dozing issues, the princess makes do with what she can find in the castle. Whether it be the fur of fluffy demonic teddy bears or the silky, blanket-like bodies of ghost shrouds, everything is but a means to ensure a peaceful slumber. With so many potential materials to craft items that can help her sleep at her disposal, nothing will stop the sleepy princess—not even death.\n\n[Written by MAL Rewrite]"}, {"id": 33970, "name_en": "Girls und Panzer das Finale – Part 1", "name_jp": "『ガールズ＆パンツァー 最終章』第1話", "image_url": "https://cdn.myanimelist.net/images/anime/1859/91463.jpg", "genres": [], "themes": ["CGDCT", "Military", "School"], "demographics": [], "studios": ["Actas"], "year": 2017, "episodes": 1, "source": "Original", "score": 7.95, "synopsis": "Rumor has it that Momo Kawashima, former student council member at Ooarai Girls Academy, is bound to repeat a year. Having failed her university entry exams, she is far from spending another year at Ooarai, but her plight is unenviable nevertheless.\n\nThe only way for her to gain admission into a university is to be granted an athletic scholarship. With the Winter Continuous Track Cup rapidly approaching, Momo becomes the commander of Ooarai's tankery team, so as to demonstrate her sport skills and wipe away the stain on her honor.\n\nTheir first opponent—French-themed BC Freedom High School—is split into two factions that are constantly at odds with each other. Despite this seemingly fortuitous match against a disorganized rival, Ooarai might soon find itself taking one step too far.\n\n[Written by MAL Rewrite]"}, {"id": 38422, "name_en": "Hi Score Girl: Extra Stage", "name_jp": "ハイスコアガール EXTRA STAGE", "image_url": "https://cdn.myanimelist.net/images/anime/1057/111384.jpg", "genres": ["Comedy", "Romance"], "themes": ["Love Polygon", "School", "Video Game"], "demographics": ["Seinen"], "studios": ["J.C.Staff"], "year": 2019, "episodes": 3, "source": "Manga", "score": 7.95, "synopsis": "Four years after they first met, Haruo Yaguchi , Akira Oono, and Koharu Hidaka remain the best of friends. Tensions start to build up as Akira's teacher, Gouda Moemi, becomes stricter than ever before, and Hidaka develops feelings for Haruo. Though he may not be the most composed and thoughtful person around, he has within him the one thing that started it all: his unconditional love for video games.\n\n[Written by MAL Rewrite]"}, {"id": 41923, "name_en": "A Will Eternal", "name_jp": "一念永恒", "image_url": "https://cdn.myanimelist.net/images/anime/1712/108161.jpg", "genres": ["Action", "Adventure", "Comedy", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["B.CMAY PICTURES"], "year": 2020, "episodes": 52, "source": "Web novel", "score": 7.95, "synopsis": "One will to create oceans. One will to summon the mulberry fields.\n\nOne will to slaughter countless devils. One will to eradicate innumerable immortals.\n\nOnly my will… is eternal.\n\nA Will Eternal tells the tale of Bai Xiaochun, an endearing but exasperating young man who is driven primarily by his fear of death and desire to live forever, but who deeply values friendship and family.\n\n(Source: Novel Updates)"}, {"id": 7465, "name_en": "Time of Eve", "name_jp": "イヴの時間", "image_url": "https://cdn.myanimelist.net/images/anime/9/27711.jpg", "genres": ["Sci-Fi", "Slice of Life"], "themes": [], "demographics": [], "studios": ["Studio Rikka"], "year": 2010, "episodes": 1, "source": "Original", "score": 7.95, "synopsis": "In the Japan of the future, employing androids for various purposes is nothing out of the ordinary. However, treating androids on the same level as humans is frowned upon, and there is constant paranoia surrounding the possibility of robots defying humans, their masters. Those who appear too trustworthy of their androids are chided and labeled \"dori-kei,\" or \"android-holics.\"\n\nHigh school student Rikuo Sakisaka notices when his house droid, Sammy, starts behaving curiously—she has been leaving the house without his instruction. When he inspects the movement logs in her database, a cryptic line grabs his attention: \"Are you enjoying the time of EVE?\" Accompanied by his friend Masakazu Masaki, Rikuo tracks the whereabouts of his houseroid to a cafe called Time of Eve, where it is forbidden for customers to display prejudice against one another. The cafe, Rikuo realizes, is frequented by both man and machine, with no evidence to tell either apart.\n\nEach customer—from the cheerful Akiko, to a robot dangerously close to breaking down—has their own story and challenges to overcome. While Rikuo tries to reveal Sammy's intentions, he begins to question the legitimacy of the fear that drives humans to regard androids as nothing more than mere tools.\n\n[Written by MAL Rewrite]"}, {"id": 50346, "name_en": "Call of the Night", "name_jp": "よふかしのうた", "image_url": "https://cdn.myanimelist.net/images/anime/1045/123711.jpg", "genres": ["Romance", "Supernatural"], "themes": ["Vampire"], "demographics": ["Shounen"], "studios": ["LIDENFILMS"], "year": 2022, "episodes": 13, "source": "Manga", "score": 7.95, "synopsis": "Kou Yamori is an average middle school student who struggles with grasping the complex concept of love. Because he sees little sense in surrendering to the norm, he soon stops going to school. Plagued with insomnia due to his idleness, Kou begins roaming the lonesome streets at night.\n\nOne night, Kou encounters a bizarre girl named Nazuna Nanakusa who believes that people stay awake during the night because they are dissatisfied with how they spent their day and cannot rest until they release their inhibitions. Nazuna offers to help Kou with his sleep issues and invites him over to her place, where she convinces him to share a futon with her. Feeling uncomfortable, Kou only pretends to doze off—which is when Nazuna suddenly bites his neck, revealing herself to be a vampire!\n\nWhile Kou thinks the bite will turn him into a vampire, the specifics of transforming are not that simple. In order to change, he must be bitten by someone he truly loves. Ready to let go of his dreary mortal life, Kou decides on a new goal: he will fall in love with Nazuna and become a vampire himself.\n\n[Written by MAL Rewrite]"}, {"id": 56609, "name_en": "Kizumonogatari: Koyomi Vamp", "name_jp": "傷物語 -こよみヴァンプ-", "image_url": "https://cdn.myanimelist.net/images/anime/1279/140233.jpg", "genres": ["Action", "Mystery", "Supernatural"], "themes": ["Vampire"], "demographics": [], "studios": ["Shaft"], "year": 2024, "episodes": 1, "source": "Light novel", "score": 7.94, "synopsis": "After befriending his classmate Tsubasa Hanekawa, Koyomi Araragi, a cynical and lonely high school student, begins to develop a more positive outlook on life. One night while walking home, however, he encounters an ethereal woman with severed limbs, barely clinging to life. Although reluctant at first to help her, Koyomi ultimately agrees to trade his life for hers, and lets her feast on his blood.\n\nUpon waking in an abandoned building, he is horrified to find that he was turned into a nigh-immortal being by the vampire he saved, Kiss-Shot Acerola-Orion Heart-Under-Blade. Thankfully for the desperate young man, there is a way for him to regain his mortality. Assisted by Meme Oshino, an expert in oddities, Koyomi must defeat the three vampire hunters who stole his new master's limbs to restore Kiss-shot's powers. Determined to return to his old life, Koyomi will stop at nothing to accomplish his goal. However, his largest challenge may not lie in overcoming his adversaries—but in preserving his own humanity. \n\n[Written by MAL Rewrite]"}, {"id": 53499, "name_en": "Aikatsu! 10th Story: Mirai e no Starway (2023)", "name_jp": "アイカツ！ 10th STORY ～未来へのSTARWAY～", "image_url": "https://cdn.myanimelist.net/images/anime/1837/130395.jpg", "genres": [], "themes": ["Idols (Female)", "Music"], "demographics": [], "studios": ["Bandai Namco Pictures"], "year": 2023, "episodes": 1, "source": "Original", "score": 7.94, "synopsis": null}, {"id": 51180, "name_en": "Classroom of the Elite III", "name_jp": "ようこそ実力至上主義の教室へ 3rd Season", "image_url": "https://cdn.myanimelist.net/images/anime/1332/139318.jpg", "genres": ["Drama", "Suspense"], "themes": ["Psychological", "School"], "demographics": [], "studios": ["Lerche"], "year": 2024, "episodes": 13, "source": "Light novel", "score": 7.94, "synopsis": "Third season of Youkoso Jitsuryoku Shijou Shugi no Kyoushitsu e."}, {"id": 7222, "name_en": "Macross Frontier: The Wings of Farewell", "name_jp": "劇場版 マクロスＦ 恋離飛翼 ～サヨナラノツバサ～", "image_url": "https://cdn.myanimelist.net/images/anime/13/76398.jpg", "genres": ["Award Winning", "Romance", "Sci-Fi"], "themes": ["Mecha", "Military", "Music", "Space"], "demographics": [], "studios": ["Satelight"], "year": 2011, "episodes": 1, "source": "Original", "score": 7.94, "synopsis": "Second Macross Frontier movie."}, {"id": 38349, "name_en": "Wotakoi: Love is Hard for Otaku OVA", "name_jp": "ヲタクに恋は難しい OAD", "image_url": "https://cdn.myanimelist.net/images/anime/1892/111383.jpg", "genres": ["Romance"], "themes": ["Adult Cast", "Otaku Culture", "Workplace"], "demographics": [], "studios": ["A-1 Pictures", "Lapin Track"], "year": 2019, "episodes": 3, "source": "Web manga", "score": 7.94, "synopsis": "Sore wa, Ikinari Otozureta = Koi\nTarou Kabakura, a third-year high school student and captain of the boys' volleyball team, is constantly being harassed by his underclassman Hanako Koyanagi, who is in charge of the girls' team. Koyanagi insists that since the girls have a match coming up, Kabakura should give up the courts to let them practice. When he refuses, she pulls out photographic evidence exposing his secret hobby.\n\nWith the danger of his entire team finding out about his otaku interests looming over him, Kabakura agrees to hand over the volleyball courts to Koyanagi, giving her some private coaching as well. As the two grow closer, they begin to forge an everlasting bond.\n\n[Written by MAL Rewrite]\n\nTomodachi no Kyori\nSecond OVA expanding on Naoya and Kou's relationship.\n\nShain Ryokou to Negaigoto\nThird OVA covering the employee retreat chapters.\n\n(Source: MAL News, edited)"}, {"id": 25879, "name_en": "Wagnaria!!3", "name_jp": "Working[ワーキング]!!!", "image_url": "https://cdn.myanimelist.net/images/anime/7/73886.jpg", "genres": ["Comedy", "Romance"], "themes": ["Love Status Quo", "Workplace"], "demographics": ["Seinen"], "studios": ["A-1 Pictures"], "year": 2015, "episodes": 13, "source": "4-koma manga", "score": 7.94, "synopsis": "As the stories of those connected to Wagnaria come to a close, only one thing is certain: the workplace is about to get crazier than ever before! Whether it be incredibly awkward romances, relentless searches for lost relatives, or even uncomfortable family reunions, lover of all things cute and tiny Souta Takanashi and his motley crew have plenty on their plates. With more Napoleon complexes, androphobia, and katana-wielding than you can shake a frying pan at, Working!!! delivers a final serving of the staff's hilarious misadventures working at everybody's favorite family restaurant.\n\n[Written by MAL Rewrite]"}, {"id": 5678, "name_en": "Kobato.", "name_jp": "こばと。", "image_url": "https://cdn.myanimelist.net/images/anime/8/79838.jpg", "genres": ["Drama", "Romance", "Supernatural"], "themes": [], "demographics": [], "studios": ["Madhouse"], "year": 2009, "episodes": 24, "source": "Manga", "score": 7.94, "synopsis": "The friendly and sincere Kobato Hanato has a wish to go to a particular place no matter what. To fulfill this desire, she is tasked with helping people in their times of distress. For each mended broken heart, a small candy-like fragment is produced and fills a special bottle. Once the bottle is full, her wish will be granted.\n\nAs Kobato carries out her mission alongside her stuffed toy companion, Ioryogi, she encounters various people troubled by their different situations. From a child struggling with his parents, a high school girl troubled about romance, and everything in between, Kobato's naturally sweet smile and outgoing personality are ready to brighten their day!\n\n[Written by MAL Rewrite]"}, {"id": 5029, "name_en": "Major Movie: Yuujou no Winning Shot", "name_jp": "劇場版MAJOR 友情の一球（ウイニングショット）", "image_url": "https://cdn.myanimelist.net/images/anime/10/75221.jpg", "genres": ["Sports"], "themes": ["Team Sports"], "demographics": ["Shounen"], "studios": ["Xebec"], "year": 2008, "episodes": 1, "source": "Manga", "score": 7.94, "synopsis": "Goro briefly returns to Japan after a triumphant campaign in the US. He is heading towards Fukuoka where he played baseball when he was a fifth grader. Having just moved into a new town, this is where Goro faced a critical point in his life. \n\n(Source: ANN)"}, {"id": 33221, "name_en": "Mobile Suit Gundam Thunderbolt: December Sky", "name_jp": "機動戦士ガンダム サンダーボルト DECEMBER SKY", "image_url": "https://cdn.myanimelist.net/images/anime/2/79823.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["Mecha", "Military", "Space"], "demographics": ["Seinen"], "studios": ["Sunrise"], "year": 2016, "episodes": 1, "source": "Manga", "score": 7.94, "synopsis": "Federation troops and Zeon forces carry out a fierce battle in the Thunderbolt Sector in what was once Side 4 \"Moore.\" The Thunderbolt Sector is a shoal zone composed of the debris of destroyed space colonies, named for the electrical discharges from the metal debris. MS pilot Io Flemming is among the Federation soldiers who are dispatched to the area, where Zeon sniper Daryl Lorenz awaits them on the battlefield.\n\n(Source: Gundam Wikia)"}, {"id": 39570, "name_en": "Hi Score Girl II", "name_jp": "ハイスコアガール II", "image_url": "https://cdn.myanimelist.net/images/anime/1560/99904.jpg", "genres": ["Comedy", "Romance"], "themes": ["Love Polygon", "School", "Video Game"], "demographics": ["Seinen"], "studios": ["J.C.Staff"], "year": 2019, "episodes": 9, "source": "Manga", "score": 7.94, "synopsis": "The year is 1996, and second-year high school students Haruo Yaguchi, Akira Oono, and Koharu Hidaka live their lives as passionately about video games as they were five years ago. Brought together by arcade games, what began as a healthy rivalry and friendship has turned into something more. As they endeavour towards understanding their unfamiliar feelings, they work with allies, navigate high school, and find that, although life has its many challenges, there's always a game or two they can rely on.\n\n[Written by MAL Rewrite]"}, {"id": 17549, "name_en": "Non Non Biyori", "name_jp": "のんのんびより", "image_url": "https://cdn.myanimelist.net/images/anime/2/51581.jpg", "genres": ["Slice of Life"], "themes": ["CGDCT", "Iyashikei", "School"], "demographics": ["Seinen"], "studios": ["SILVER LINK."], "year": 2013, "episodes": 12, "source": "Manga", "score": 7.94, "synopsis": "Hotaru Ichijou's lifestyle completely changes when she leaves Tokyo and moves with her family to the isolated Asahigaoka village. Her new school has only five students including herself, all sharing a single classroom regardless of grade level. There are no convenience stores in the area, and it can take up to two hours for a bus to arrive.\n\nNevertheless, Hotaru finds herself captivated by the countryside's charm thanks to her four unique schoolmates with whom she quickly forms a genuine bond. The most colorful of them is Renge Miyauchi, a first-grader who is often perceptive despite her age. However, no less intriguing are the three Koshigaya siblings: the quiet oldest brother Suguru, the petite older sister Komari, and the prankish youngest sister Natsumi.\n\nHaving someone from the city join their cheerful little group enlivens the ordinary days in Asahigaoka. Not only does Hotaru bring firsthand knowledge from the alluring outside world, but her fresh outlook on life welcomes a blossom of change to their usual routine.\n\n[Written by MAL Rewrite]"}, {"id": 42916, "name_en": "Sword Art Online the Movie: Progressive - Aria of a Starless Night", "name_jp": "劇場版 ソードアート・オンライン プログレッシブ 星なき夜のアリア", "image_url": "https://cdn.myanimelist.net/images/anime/1590/116274.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Video Game"], "demographics": [], "studios": ["A-1 Pictures"], "year": 2021, "episodes": 1, "source": "Light novel", "score": 7.94, "synopsis": "Excelling socially and academically, Asuna Yuuki is on track to ace her high school entrance exams. Her friend and classmate, Misumi \"Mito\" Tozawa, advises her to take a short break from studying and join her on the launch day of Sword Art Online (SAO)—the highly anticipated online virtual reality multiplayer game. Asuna accepts her offer and soon meets her in the game.\n\nIn a cruel twist of fate, Asuna, Mito, and every other player logged into SAO find themselves trapped in the game permanently. The only way out is to clear all one hundred floors of the game, and to make matters worse, dying inside the game will kill the player in real life. With SAO now turned into a nightmare death trap, Asuna and other gamers—such as the lone swordsman Kazuto \"Kirito\" Kirigaya—must adapt and survive, all whilst attempting to beat the unforgiving competition to the top.\n\n[Written by MAL Rewrite]"}, {"id": 38080, "name_en": "Kono Oto Tomare!: Sounds of Life", "name_jp": "この音とまれ！", "image_url": "https://cdn.myanimelist.net/images/anime/1464/99881.jpg", "genres": ["Drama"], "themes": ["Music", "Performing Arts", "School"], "demographics": ["Shounen"], "studios": ["Platinum Vision"], "year": 2019, "episodes": 13, "source": "Manga", "score": 7.94, "synopsis": "Gen Kudou, a koto maker, believes that his delinquent grandson Chika would never understand the profoundness of the traditional musical instrument. In an attempt to make up for his naivety and understand the words of his late grandfather, Chika tries to join the Tokise High School Koto Club. \n\nEven though the club is in dire need of members, new club president Takezou Kurata is unwilling to easily accept Chika's application due to his bad reputation. Nonetheless, after seeing Chika's seriousness and enthusiasm, Takezou allows the problem child to join, along with koto prodigy Satowa Houzuki and three of Chika's energetic friends. Kono Oto Tomare! follows the merry band of musicians as they aspire to play at the national competition.\n\n[Written by MAL Rewrite]"}, {"id": 1530, "name_en": "Kanon", "name_jp": "カノン (2006)", "image_url": "https://cdn.myanimelist.net/images/anime/1362/128746.jpg", "genres": ["Drama", "Romance", "Supernatural"], "themes": [], "demographics": [], "studios": ["Kyoto Animation"], "year": 2006, "episodes": 24, "source": "Visual novel", "score": 7.94, "synopsis": "As a young child, Aizawa Yuuichi had often visited his cousin in the city; however, something drastic happened to keep him away for seven long years. Now, Yuuichi returns, his memories of those days are simply gone.\n\nSettling into the wintry town, Yuuichi comes across several young girls, all of whom are connected to his past. As he befriends them and continues to interact with them, the long forgotten memories from his childhood begin to resurface...\n\n[Written by MAL Rewrite]"}, {"id": 4772, "name_en": "Aria the Origination Episode 5.5: That Little Secret Place…", "name_jp": "その ちょっぴり秘密の場所に…", "image_url": "https://cdn.myanimelist.net/images/anime/11/15053.jpg", "genres": ["Slice of Life"], "themes": ["Iyashikei"], "demographics": ["Shounen"], "studios": ["HAL Film Maker"], "year": 2008, "episodes": 1, "source": "Manga", "score": 7.94, "synopsis": "With Aria Company closed for the day and her friends attending to other matters, Akari Mizunashi seizes this rare opportunity to go on a casual outing around Neo-Venezia with President Aria Pokoteng. Although her Prima Undine gondola training has taken her to many remarkable tourist spots, Akari finds greater exhilaration reveling in the ordinary sights and sounds of the town. As Akari and President Aria stroll through Neo-Venezia on their own exciting itinerary, some familiar faces are not far behind. Drawn into their private tour, they too may discover a new perspective of the city.\n\n[Written by MAL Rewrite]"}, {"id": 4053, "name_en": "The Prince of Tennis: The National Tournament Finals", "name_jp": "テニスの王子様 Original Video Animation 全国大会編 Final", "image_url": "https://cdn.myanimelist.net/images/anime/1142/133867.jpg", "genres": ["Sports"], "themes": [], "demographics": ["Shounen"], "studios": ["M.S.C"], "year": 2008, "episodes": 7, "source": "Manga", "score": 7.94, "synopsis": "After their victory over Shitenhouji Middle School in the semifinals, Ryouma Echizen and the Seishun Academy Boys Tennis Team must now face off against the two-time defending champions of Rikkai University-Affiliated Middle School in order to claim the national title. Rikkai is not only seeking revenge for its team's previous loss to Seishun at the Kanto Regional Tournament, but they also are aiming to win the national title for a third consecutive year.\n\nIn the conclusion of Tennis no Oujisama, the titular \"Prince of Tennis\" is pitted against the \"Child of God\" in a head-to-head battle for the national championship title. Along the way, Ryouma must teach his opponents and teammates that there is more to tennis than winning.\n\n[Written by MAL Rewrite]"}, {"id": 20507, "name_en": "Noragami", "name_jp": "ノラガミ", "image_url": "https://cdn.myanimelist.net/images/anime/1886/128266.jpg", "genres": ["Action", "Supernatural"], "themes": ["Mythology"], "demographics": ["Shounen"], "studios": ["Bones"], "year": 2014, "episodes": 12, "source": "Manga", "score": 7.94, "synopsis": "In times of need, if you look in the right place, you just may see a strange telephone number scrawled in red. If you call this number, you will hear a young man introduce himself as the Yato God.\n\nYato is a minor deity and a self-proclaimed \"Delivery God,\" who dreams of having millions of worshippers. Without a single shrine dedicated to his name, however, his goals are far from being realized. He spends his days doing odd jobs for five yen apiece, until his weapon partner becomes fed up with her useless master and deserts him.\n\nJust as things seem to be looking grim for the god, his fortune changes when a middle school girl, Hiyori Iki, supposedly saves Yato from a car accident, taking the hit for him. Remarkably, she survives, but the event has caused her soul to become loose and hence able to leave her body. Hiyori demands that Yato return her to normal, but upon learning that he needs a new partner to do so, reluctantly agrees to help him find one. And with Hiyori's help, Yato's luck may finally be turning around.\n\n[Written by MAL Rewrite]"}, {"id": 38084, "name_en": "Fate/Grand Order: Absolute Demonic Front - Babylonia", "name_jp": "Fate/Grand Order -絶対魔獣戦線バビロニア-", "image_url": "https://cdn.myanimelist.net/images/anime/1194/103420.jpg", "genres": ["Action", "Fantasy"], "themes": ["Isekai"], "demographics": [], "studios": ["CloverWorks"], "year": 2019, "episodes": 21, "source": "Game", "score": 7.94, "synopsis": "A.D. 2016, the foundations of humanity have been incinerated by the Mage King Solomon. Chaldea, a secret mages organization with the mission to preserve humanity's future, foresaw mankind's extinction in 2015. Thus commenced the operation to repair the Singularities in history caused by Holy Grails dispersed across time and space—Operation Grand Order. \n\nUsing the Rayshift time travel technology, Chaldea's last master Ritsuka Fujimaru and his demi-servant Mash Kyrielight have traveled to and resolved six Singularities. Now, they depart for their most dangerous destination yet: a civilization in the Age of Gods, B.C. 2655 Mesopotamia. Ritsuka and Mash soon discover that Demonic Beasts roam the land, attacking people and towns. Amidst chaos and terror lies humanity's last defense—Uruk, a fortress city that acts as the frontline for the battle against the beasts. The battlefront is commanded by none other than King Gilgamesh, the King of Heroes, who sought aid from Heroic Spirits and took on the role of a mage to protect his city. \n\nAlong with Gilgamesh and the summoned servants, Ritsuka and Mash must protect Uruk against the magical beasts' onslaught and defeat the Three Goddess Alliance who aims to eradicate humankind; all the while, a greater threat looms over Uruk, preparing for its awakening.\n\n[Written by MAL Rewrite]"}, {"id": 37150, "name_en": "Soul Land", "name_jp": "斗罗大陆", "image_url": "https://cdn.myanimelist.net/images/anime/1438/101531.jpg", "genres": ["Action", "Adventure", "Fantasy", "Romance"], "themes": ["Historical", "Martial Arts", "Reincarnation"], "demographics": [], "studios": ["Sparkly Key Animation Studio"], "year": 2018, "episodes": 26, "source": "Novel", "score": 7.94, "synopsis": "Tang San is one of Tang Sect martial art clan's most prestigious disciples and peerless in the use of hidden weapons. With high expectations, the sect's elders believe his future will be bright; yet Tang chooses to forsake this life at the cost of obtaining the sect's forbidden lore—an action punishable by death. Tang, now content with his ascension of knowledge, sees no reason to keep on living and jumps from Hell's Peak, but little did he know that that would not be the end of his existence. \n\nIn Douluo Continent, the strong prevail and the weak perish. Each person possesses an innate spirit, some of which can be cultivated and strengthened, bestowing its user with various benefits. Those who were born with such spirits can become Spirit Masters, a profession regarded as one of the continent's most noble.\n\nTang, reincarnated into this strange world, knows only the life of a blacksmith's son. At the age of six, he takes part in the Spirit Master ceremony, and discovers his spirit is Blue Silver Grass—supposedly the world's most useless spirit. In contrast, however, he possesses strong spirit power. Now, aided by the memories of his previous life as well, Tang's future as a Spirit Master is in no way bleak.\n\n[Written by MAL Rewrite]"}, {"id": 55993, "name_en": "A Record Of Mortal's Journey To Immortality: Speeding in the Sea of Stars Prologue", "name_jp": "凡人修仙传 星海飞驰 序章", "image_url": "https://cdn.myanimelist.net/images/anime/1690/137149.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Original Force", "Wonder Cat Animation"], "year": 2023, "episodes": 4, "source": "Web novel", "score": 7.93, "synopsis": "Prologue episodes to the third season of Fanren Xiu Xian Zhuan."}, {"id": 54915, "name_en": "The Quintessential Quintuplets~", "name_jp": "五等分の花嫁∽", "image_url": "https://cdn.myanimelist.net/images/anime/1567/135752.jpg", "genres": ["Comedy", "Romance"], "themes": ["Harem", "School"], "demographics": ["Shounen"], "studios": ["Shaft"], "year": 2023, "episodes": 2, "source": "Manga", "score": 7.93, "synopsis": "Featuring stories from the manga not adapted in the main anime."}, {"id": 53924, "name_en": "Toilet-Bound Hanako-kun Season 2", "name_jp": "地縛少年花子くん2", "image_url": "https://cdn.myanimelist.net/images/anime/1878/146291.jpg", "genres": ["Supernatural"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["Lerche"], "year": 2025, "episodes": 12, "source": "Manga", "score": 7.93, "synopsis": "There is a rumor in Kamome Academy that somewhere within the school premises lies a clock that governs the passage of time in the academy. That clock is overseen by three clock keepers—each representing the past, present, and future. Despite this, an unforeseen event causes mayhem, with students rapidly aging and the academy's infrastructure falling apart.\n\nNene Yashiro and Kou Minamoto seek help from Hanako-kun, embarking on a new adventure to find those responsible for this mess. However, what they uncover is more than just the culprits' identity. As hidden truths are revealed and new vows are established, the academy students and its apparitions find themselves in a game of fate, where the living fight to free themselves from its grasp.\n\n[Written by MAL Rewrite]"}, {"id": 11239, "name_en": "Hidamari Sketch x Honeycomb", "name_jp": "ひだまりスケッチ×ハニカム", "image_url": "https://cdn.myanimelist.net/images/anime/2/50501.jpg", "genres": ["Slice of Life"], "themes": ["CGDCT", "Iyashikei", "School", "Visual Arts"], "demographics": [], "studios": ["Shaft"], "year": 2012, "episodes": 12, "source": "4-koma manga", "score": 7.93, "synopsis": "Studying at the Yamabuki Arts High School has been a dream-come-true for Yuno, and she's learned so much already! And not just from her instructors, but from her friends and neighbors who've become her second family and made the Hidamari Apartments such a safe and nurturing home. But as the day of her \"big sisters\" Sae and Hiro's graduation draws slowly closer, it's time for Yuno to start seriously taking on the same role for Nazuna, Nori and the other budding young artists who've entered Hidamari's protective cocoon. And it's also time to tackle some really challenging artistic assignments. That doesn't mean there won't still be time for fun with Miyako and all the others, but it's definitely time to pencil in her plans for the future. And sometimes that means you have to put the art before the course! \n\n(Source: Sentai Filmworks)"}, {"id": 12477, "name_en": "Patema Inverted", "name_jp": "サカサマのパテマ", "image_url": "https://cdn.myanimelist.net/images/anime/12/52415.jpg", "genres": ["Adventure", "Award Winning", "Sci-Fi"], "themes": [], "demographics": [], "studios": ["Studio Rikka", "Purple Cow Studio Japan"], "year": 2013, "episodes": 1, "source": "Original", "score": 7.93, "synopsis": "Patema is a plucky young girl from an underground civilization boasting an incredible network of tunnels. Inspired by a friend that mysteriously went missing, she is often reprimanded due to her constant excursions of these tunnels due to her royal status. After she enters what is known as the \"forbidden zone,\" she accidentally falls into a giant bottomless pit after being startled by a strange creature.\n\nFinding herself on the surface, a world literally turned upside down, she begins falling towards the sky only to be saved by Age, a discontented student of the totalitarian nation known as Aiga. The people of Aiga are taught to believe that \"Inverts,\" like Patema, are sinners that will be \"swallowed by the sky,\" but Age has resisted this propaganda and decides to protect his new friend. A chance meeting between two curious teenagers leads to an exploration of two unique worlds as they begin working together to unveil the secrets of their origins in Sakasama no Patema, a heart-warming film about overcoming differences in order to coexist.\n\n[Written by MAL Rewrite]"}, {"id": 16067, "name_en": "A Lull in the Sea", "name_jp": "凪のあすから", "image_url": "https://cdn.myanimelist.net/images/anime/7/53549.jpg", "genres": ["Drama", "Fantasy", "Romance"], "themes": ["Love Polygon", "Urban Fantasy"], "demographics": [], "studios": ["P.A. Works"], "year": 2013, "episodes": 26, "source": "Original", "score": 7.93, "synopsis": "Long ago, all humans lived beneath the sea. However, some people preferred the surface and abandoned living underwater permanently. As a consequence, they were stripped of their god-given protection called \"Ena\" which allowed them to breathe underwater. Over time, the rift between the denizens of the sea and of the surface widened, although contact between the two peoples still existed.\n\nNagi no Asu kara follows the story of Hikari Sakishima and Manaka Mukaido, along with their childhood friends Chisaki Hiradaira and Kaname Isaki, who are forced to leave the sea and attend a school on the surface. There, the group also meets Tsumugu Kihara, a fellow student and fisherman who loves the sea.\n\nHikari and his friends' lives are bound to change as they have to deal with the deep-seated hatred and discrimination between the people of sea and of the surface, the storms in their personal lives, as well as an impending tempest which may spell doom for all who dwell on the surface.\n\n[Written by MAL Rewrite]"}, {"id": 2154, "name_en": "Tekkonkinkreet", "name_jp": "鉄コン筋クリート", "image_url": "https://cdn.myanimelist.net/images/anime/12/8520.jpg", "genres": ["Action", "Adventure", "Award Winning", "Supernatural", "Suspense"], "themes": ["Psychological"], "demographics": ["Seinen"], "studios": ["Studio 4°C"], "year": 2006, "episodes": 1, "source": "Manga", "score": 7.93, "synopsis": "The streets of Treasure Town are said to belong to \"The Cats.\" They know everything that goes on in the city, and no one can stir up trouble without going through them first. In reality, The Cats are a pair of orphan boys called Black and White, who aren’t afraid of anything or anyone. \n\nBut their rule of the streets is challenged when the Yakuza come to town and start making changes. The wild Black and the carefree White have no one to rely on but themselves to get their Treasure Town back to the way it was. But their bond is tested as they quickly realize going back to how things were may no longer be an option.\n\n[Written by MAL Rewrite]"}, {"id": 122, "name_en": "Looking for the Full Moon", "name_jp": "満月をさがして", "image_url": "https://cdn.myanimelist.net/images/anime/1900/99154.jpg", "genres": ["Comedy", "Drama", "Romance", "Supernatural"], "themes": ["Music", "Showbiz"], "demographics": ["Shoujo"], "studios": ["Studio Deen"], "year": 2002, "episodes": 52, "source": "Manga", "score": 7.93, "synopsis": "Two years ago, Mitsuki Kouyama's friend, Eichi Sakurai, moved to America before she could confess her feelings to him. Though she cannot contact him, they made a promise to fulfill their respective dreams: Mitsuki wants to become a professional singer, and Eichi an astronomer. She hopes that one day her music will reach him across the world with a brilliance like that of the full moon.\n\nThere is just one catch: Mitsuki suffers from throat cancer, which makes her voice quiet and singing strenuous. Her grandmother, who has a hatred of music, insists that Mitsuki undergo surgery to remove the cancer, but she refuses due to the risk of losing her voice. One day, two shinigami—Meroko Yui and Takuto Kira—appear to tell her that she only has one year left to live. This sudden revelation spurs Mitsuki into action, and she decides that with Meroko and Takuto's help, she will become a professional singer in the time she has left.\n\nFull Moon wo Sagashite follows the emotional story of Mitsuki and her shinigami friends as they discover what it means to sing—and ultimately, what it means to live.\n\n[Written by MAL Rewrite]"}, {"id": 39565, "name_en": "My Hero Academia: Heroes Rising", "name_jp": "僕のヒーローアカデミア THE MOVIE　ヒーローズ：ライジング", "image_url": "https://cdn.myanimelist.net/images/anime/1019/103292.jpg", "genres": ["Action"], "themes": ["Super Power"], "demographics": ["Shounen"], "studios": ["Bones"], "year": 2019, "episodes": 1, "source": "Manga", "score": 7.93, "synopsis": "Izuku \"Deku'' Midoriya and his fellow students in Class 1-A of UA High's hero course have been chosen to participate in a safety program on Nabu Island. To further improve their skills and gain experience in more ordinary heroics, the students aid the kind citizens with small services and everyday chores. With the low crime rate in the quiet community, all seems well and good, but the rise of a new villain threatens to put the students' courage to the test and challenge their capabilities as heroes.\n\nA merciless villain by the name of Nine is in search of a certain \"quirk\" needed to fulfill his diabolical plan—creating a society where only those with the strongest quirks reign supreme. As his attack on Nabu Island endangers the lives of the residents, securing the citizens becomes the first priority for Class 1-A; defeating Nine along with his wicked accomplices is also imperative. A straightforward strategy is formulated until a young boy named Katsuma Shimano, whom Deku had befriended, suddenly requires particular protection. Concerned for the boy's wellbeing, Deku and his classmates must now devise a plan to ensure Katsuma's safety at all costs.\n\nWith Nine wreaking havoc to find the catalyst for his ill-intended schemes and the heroes desperate to defend Katsuma from harm, will Deku and his friends be able to come out victorious, or will they find themselves unable to escape a hopeless situation?\n\n[Written by MAL Rewrite]"}, {"id": 36949, "name_en": "Food Wars! The Third Plate: Totsuki Train Arc", "name_jp": "食戟のソーマ 餐ノ皿 遠月列車篇", "image_url": "https://cdn.myanimelist.net/images/anime/1604/93531.jpg", "genres": ["Gourmet", "Ecchi"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["J.C.Staff"], "year": 2018, "episodes": 12, "source": "Manga", "score": 7.93, "synopsis": "A dark age of cooking befalls Tootsuki Culinary Academy. With the Elite Ten's devastating coup d'état, Azami Nakiri is now the director of the prestigious school. Students must now conform to Azami's ideology of \"true gourmet food\" and are forbidden to express creativity, or else face expulsion.\n\nHowever, Souma Yukihira and the members of the Polar Star Dormitory refuse to accept these changes. Aided by other rebellious first-years, including the tenth seat, Erina Nakiri, Souma and his allies band together to fight off supporters of Azami's regime. But corrupt instructors and the menacing Central organization stand in their way, and so they must work together, harder than ever before, to survive every underhanded plot designed to banish them from the school.\n\n[Written by MAL Rewrite]"}, {"id": 61508, "name_en": "Throne of Seal Movie: The Legend of Electrolux", "name_jp": "神印王座剧场版 伊莱克斯传奇", "image_url": "https://cdn.myanimelist.net/images/anime/1183/149275.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": [], "studios": ["Shenman Entertainment"], "year": 2025, "episodes": 1, "source": "Web novel", "score": 7.92, "synopsis": "The legendary epic of Electrolux recreates the glorious journey of the great Holy Necromancer God Electrolux, the Scourge of the Undead.\n\n(Source: Tencent, translated)"}, {"id": 58517, "name_en": "Umamusume: Pretty Derby - Road to the Top (Movie)", "name_jp": "ウマ娘 プリティーダービー Road to the Top", "image_url": "https://cdn.myanimelist.net/images/anime/1421/142243.jpg", "genres": ["Sports"], "themes": ["Anthropomorphic", "Racing"], "demographics": [], "studios": ["CygamesPictures"], "year": 2024, "episodes": 1, "source": "Game", "score": 7.92, "synopsis": "Theatrical compilation of Uma Musume: Pretty Derby - Road to the Top with new scenes."}, {"id": 58883, "name_en": "Dead Dead Demons Dededede Destruction", "name_jp": "デッドデッドデーモンズデデデデデストラクション", "image_url": "https://cdn.myanimelist.net/images/anime/1528/143212.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["School"], "demographics": ["Seinen"], "studios": ["Production +h."], "year": 2024, "episodes": 17, "source": "Manga", "score": 7.92, "synopsis": "When you look up at the sky, what do you see? A blue sky? Freedom? A hopeful future? The people of Tokyo see none of these. Instead, an ominous alien mothership looms overhead, engulfing the city skyscape.\n\nThree years ago, they came. The arrival of the \"Invaders\" sparked the greatest war humanity had ever faced—one that threatened to end the world. The Japanese government scrambled to fight the Invaders. Weapons were mass-produced, sparking controversy and pacifist movements.\n\nThat day, everything changed. And yet, nothing has changed. Kadode Koyama and Ouran Nakagawa live their final days like they always have: going to school, playing with friends, and doing what any carefree high school girl would do.\n\nAs they grow up, they come to learn what it truly means to be an adult, in a world where adults seem to be demons who only deceive and destroy. They come to learn the real threat to humanity is not the Invaders, but humanity itself.\n\n[Written by MAL Rewrite]"}, {"id": 56785, "name_en": "Detective Conan Movie 27: The Million-Dollar Pentagram", "name_jp": "名探偵コナン 100万ドルの五稜星", "image_url": "https://cdn.myanimelist.net/images/anime/1888/141209.jpg", "genres": ["Mystery"], "themes": ["Detective"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2024, "episodes": 1, "source": "Manga", "score": 7.92, "synopsis": "A message has arrived from Kid the Phantom Thief, that he will steal a Japanese sword belonging to the wealthy Onoe Family in Hakodate, Hokkaido. Conan and Heiji Hattori, who happened to be in Hakodate, are on the case to capture Kid. Onoe Family's collections are associated with Toshizo Hijikata, a historic figure who perished in Hakodate. Why is Kid, who specializes in jewels, going after a Japanese sword?\n\nCoincidentally the family lawyer of Onoe is found murdered in the warehouse district, apparently slaughtered by a Japanese sword. The suspect is an investor/arms dealer who is said to be after Onoe family's hidden treasure.\n\nThe grandfather of Onoe family's patriarch was deeply involved with the army industry during wartime, and it was rumored he hid some powerful weapon that could \"change the course of war\" somewhere in Hakodate. Is Kid after that weapon? Meanwhile, Heiji is trying to find a perfect viewpoint to declare his love to Kazuha...\n\nIn the North among cherry blossoms, the exciting hunt for treasure begins!"}, {"id": 54118, "name_en": "IDOLiSH7 Movie: LIVE 4bit - BEYOND THE PERiOD", "name_jp": "劇場版アイドリッシュセブン LIVE 4bit BEYOND THE PERiOD", "image_url": "https://cdn.myanimelist.net/images/anime/1698/136186.jpg", "genres": [], "themes": ["Idols (Male)", "Music"], "demographics": [], "studios": ["Orange"], "year": 2023, "episodes": 2, "source": "Game", "score": 7.92, "synopsis": "A live performance by IDOLiSH7, TRIGGER, Re:vale, and ŹOOĻ , 4 groups with different charms, will meet in one place, and the grand live entertainment will begin! \n\n(Source: Translated, edited from official website)"}, {"id": 52293, "name_en": "New Panty & Stocking with Garterbelt", "name_jp": "New PANTY&STOCKING with GARTERBELT", "image_url": "https://cdn.myanimelist.net/images/anime/1180/150596.jpg", "genres": ["Action", "Comedy", "Fantasy", "Ecchi"], "themes": ["Gag Humor", "Parody"], "demographics": [], "studios": ["Trigger"], "year": 2025, "episodes": 13, "source": "Original", "score": 7.92, "synopsis": "Panty and Stocking Anarchy, the angel twin sisters, have even more ghosts to exorcize in Daten City, thanks to the failed opening of the Hell Gate. They must continue their ghost hunt alongside their longtime rivals, the sisters Scanty and Kneesocks Demon, who must earn Hell Coins to return to Hell. On top of that, the Demon sisters are staying at the church with Garterbelt, the priest in charge of the Anarchy sisters. These two polar opposite duos will have to set aside their differences if they wish to collect enough Heaven and Hell Coins to complete their mission and return to their respective realms.\n\n[Written by MAL Rewrite]"}, {"id": 51318, "name_en": "Baki Hanma 2nd Season", "name_jp": "範馬刃牙 SON OF OGRE", "image_url": "https://cdn.myanimelist.net/images/anime/1800/135847.jpg", "genres": ["Sports"], "themes": ["Combat Sports", "Gore"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2023, "episodes": 27, "source": "Manga", "score": 7.92, "synopsis": "Second season of Hanma Baki: Son of Ogre."}, {"id": 31174, "name_en": "Mr. Osomatsu", "name_jp": "おそ松さん", "image_url": "https://cdn.myanimelist.net/images/anime/7/76540.jpg", "genres": ["Comedy"], "themes": ["Adult Cast", "Gag Humor", "Parody"], "demographics": [], "studios": ["Studio Pierrot"], "year": 2015, "episodes": 25, "source": "Manga", "score": 7.92, "synopsis": "The majority of the Matsuno household is comprised of six identical siblings: self-centered leader Osomatsu, manly Karamatsu, voice of reason Choromatsu, cynical Ichimatsu, hyperactive Juushimatsu, and lovable Todomatsu. Despite each one of them being over the age of 20, they are incredibly lazy and have absolutely no motivation to get a job, choosing to live as NEETs instead. In the rare occurrence that they try to look for employment and are somehow able to land an interview, their unique personalities generally lead to their swift rejection.\n\nFrom trying to pick up girlfriends to finding the perfect job, the daily activities of the Matsuno brothers are never dull as they go on all sorts of crazy, and often downright bizarre, adventures. Though they desperately search for a way to improve their social standing, it won't be possible if they can't survive the various challenges that come with being sextuplets!\n\n[Written by MAL Rewrite]"}, {"id": 4059, "name_en": "Clannad: Another World, Tomoyo Chapter", "name_jp": "-クラナド-　もうひとつの世界　智代編", "image_url": "https://cdn.myanimelist.net/images/anime/12/19620.jpg", "genres": ["Drama", "Romance"], "themes": ["School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2008, "episodes": 1, "source": "Visual novel", "score": 7.92, "synopsis": "Clannad: Mou Hitotsu no Sekai, Tomoyo-hen is set in an alternate reality where Tomoya Okazaki dates his junior, Tomoyo Sakagami.\n\nTomoyo has been elected to be the school's next Student Council President. This is great news as she can now work toward her goal of preventing the school's cherry blossom trees from being axed. Although Tomoya is ecstatic for her, given his reputation as a delinquent in school, his relationship with Tomoyo is making them the subject of gossip around the campus, which can potentially compromise her standing as Student Council President. The school community's disapproval of their relationship becomes more apparent when the Student Council's Vice-President and even the school's administration warn Tomoya to distance himself from Tomoyo.\n\nWith the bad atmosphere widening the rift between Tomoya and Tomoyo, will Tomoya succumb to societal pressure and do as they say, or will their love for each other rise above it all?\n\n[Written by MAL Rewrite]"}, {"id": 10049, "name_en": "Nura: Rise of the Yokai Clan - Demon Capital", "name_jp": "ぬらりひょんの孫 千年魔京", "image_url": "https://cdn.myanimelist.net/images/anime/5/77300.jpg", "genres": ["Action", "Supernatural"], "themes": ["Mythology"], "demographics": ["Shounen"], "studios": ["Studio Deen"], "year": 2011, "episodes": 24, "source": "Manga", "score": 7.92, "synopsis": "Long before Rikuo Nura was born, the legendary youkai Nurarihyon, leader of a \"Night Parade of One Hundred Demons,\" fell in love with a human woman. Though the two would initially find happiness, a threat from the terrifying fox-demon Hagoromo Gitsune would get in the way of their relationship. \n\nIn the present, Rikuo has taken his rightful place as the heir to the Nura Clan. While he has accepted his youkai side, he must continue to maintain the secret of youkai, a difficult task when faced with the Keikain onmyouji clan and his youkai-obsessed friend, Kiyotsugu. Even so, Rikuo will do what he must to protect those important to him.\n\nThe reappearance of the sinister Hagoromo Gitsune marks the start of Rikuo's most fearsome trial yet. The frightening creature bears a personal vendetta against his family and will stop at nothing to see her dream come to fruition. The world stands at a precipice, an all-out war that will drag Rikuo centerstage.\n\n[Written by MAL Rewrite]"}, {"id": 10721, "name_en": "Penguindrum", "name_jp": "輪るピングドラム", "image_url": "https://cdn.myanimelist.net/images/anime/5/30238.jpg", "genres": ["Avant Garde", "Drama", "Mystery", "Supernatural", "Suspense"], "themes": ["Psychological"], "demographics": [], "studios": ["Brain's Base"], "year": 2011, "episodes": 24, "source": "Original", "score": 7.92, "synopsis": "For the Takakura family, destiny is an ever-spinning wheel, pointing passionately in their direction with equal tides of joy and sorrow before ticking on to the next wishmaker. With their parents gone, twin brothers Kanba and Shouma live alone with their beloved little sister Himari, whose poor health cannot decline any further.\n\nOn the day Himari is given permission to temporarily leave the hospital, her brothers take her out to the aquarium to celebrate, where the family's supposed fate is brought forth with her sudden collapse. However, when Himari is inexplicably revived by a penguin hat from the aquarium's souvenir shop, the hand of fate continues to tick faithfully forward.\n\nWith her miraculous recovery, though, comes a cost: there is a new entity within her body, whose condition for keeping her fate at bay sends the boys on a wild goose chase for the mysterious \"Penguin Drum.\" In their search, the boys will have to follow the threads of fate leading from their own shocking past and into the lives of other wishmakers vying for the Penguin Drum, all hoping to land upon their chosen destiny.\n\n[Written by MAL Rewrite]"}, {"id": 34100, "name_en": "Fate/kaleid liner Prisma Illya: Vow in the Snow", "name_jp": "劇場版 Fate/kaleid liner プリズマ☆イリヤ 雪下の誓い", "image_url": "https://cdn.myanimelist.net/images/anime/1996/93042.jpg", "genres": ["Action", "Fantasy"], "themes": [], "demographics": [], "studios": ["SILVER LINK."], "year": 2017, "episodes": 1, "source": "Manga", "score": 7.92, "synopsis": "After succumbing to an unenviable fate, Kiritsugu Emiya has left Shirou Emiya the duty to save mankind in his place. The world is dying and time is ticking, but when the salvation Shirou seeks is at the cost of his only family left—his younger sister—will he still have the heart to pursue this duty?\n\nShirou is now pressed with the choice to continue being a hero of justice—or become the very evil his ideal has vowed to vanquish. The orphan is alone once again, yet his body is still made of swords. The Fifth Holy Grail War now begins with his oath under snow.\n\n[Written by MAL Rewrite]"}, {"id": 38337, "name_en": "Is the Order a Rabbit? Bloom", "name_jp": "ご注文はうさぎですか? BLOOM", "image_url": "https://cdn.myanimelist.net/images/anime/1900/109179.jpg", "genres": ["Slice of Life"], "themes": ["CGDCT", "Iyashikei", "Workplace"], "demographics": [], "studios": ["Encourage Films"], "year": 2020, "episodes": 12, "source": "4-koma manga", "score": 7.92, "synopsis": "Although the year is coming close to an end, there is no lack of fun for Kokoa Hoto and the other café waitresses! From bazaars to festivals, life is as enjoyable as it gets. As for Chino Kafuu and her middle school friends—the friendly Megumi Natsu and the playful Maya Jouga—they begin to look towards the future and decide on a high school to enroll in.\n\nBonds of friendship and exciting adventures blend into Gochuumon wa Usagi Desu ka? Bloom as the joyful lives of the café waitresses continue.\n\n[Written by MAL Rewrite]"}, {"id": 18179, "name_en": "Yowamushi Pedal", "name_jp": "弱虫ペダル", "image_url": "https://cdn.myanimelist.net/images/anime/5/53211.jpg", "genres": ["Sports"], "themes": ["Racing"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2013, "episodes": 38, "source": "Manga", "score": 7.92, "synopsis": "Sakamichi Onoda is a cheerful otaku looking to join his new school's anime club, eager to finally make some friends. Unfortunately, the club has been disbanded and he takes it upon himself to revive it by finding students who are willing to join. Without much luck, Onoda decides to make a round trip to Akihabara on his old, bulky city bicycle, a weekly 90-kilometer ride he has been completing since fourth grade.\n \nThis is when he meets fellow first year student, Shunsuke Imaizumi, a determined cyclist who is using the school's steep incline for practice. Surprised by Onoda's ability to climb the hill with his specific type of bicycle, Imaizumi challenges him to a race, with the proposition of joining the anime club should Onoda win. And thus begins the young boy's first foray into the world of high school bicycle racing!\n\n[Written by MAL Rewrite]"}, {"id": 793, "name_en": "xxxHOLiC The Movie: A Midsummer Night's Dream", "name_jp": "劇場版　×××HOLiC　真夏ノ夜ノ夢", "image_url": "https://cdn.myanimelist.net/images/anime/1482/107802.jpg", "genres": ["Comedy", "Drama", "Mystery", "Supernatural"], "themes": ["Psychological"], "demographics": ["Seinen"], "studios": ["Production I.G"], "year": 2005, "episodes": 1, "source": "Manga", "score": 7.92, "synopsis": "Summer break has arrived, but while his other classmates are out having fun, Kimihiro Watanuki continues to work as compensation for the eccentric Yuuko in her shop. With the spirits and supernatural phenomena that bother him lessening, he pays his dues by cleaning, cooking, and doing whatever else the apparently lazy Yuuko needs.\n\nWatanuki, however, gets involved in a new predicament when Yuuko receives a mysterious invitation to a mansion whose owner seeks Yuuko's wish-granting ability. When he, Yuuko, and his classmate Shizuka Doumeki make their way to the peculiar residence, they meet others who were summoned by the same strange invitation. All of them are collectors of various unique items, drawn there by the chance to expand their collections. But as the collectors begin to disappear one by one, Watanuki and his companions must solve the mystery and put the case to rest, or find themselves in risk of danger.\n\n[Written by MAL Rewrite]"}, {"id": 658, "name_en": "Akagi", "name_jp": "闘牌伝説アカギ　闇に舞い降りた天才", "image_url": "https://cdn.myanimelist.net/images/anime/11/53587.jpg", "genres": ["Suspense"], "themes": ["Adult Cast", "High Stakes Game", "Organized Crime", "Psychological", "Strategy Game"], "demographics": ["Seinen"], "studios": ["Madhouse"], "year": 2005, "episodes": 26, "source": "Manga", "score": 7.92, "synopsis": "While mahjong is a game that is often played with family and friends, it is also a game that is played in the darkest corners of society. Nangou is a compulsive gambler who has accumulated debt over three million yen. In a last ditch attempt to clear his record, he decides to wager his life on a game of mahjong with the mafia. Unfortunately, as the game progresses, Nangou only moves further from the prize and closer to death.\n\nWhen all hope seems lost, the game parlor is suddenly intruded upon by Shigeru Akagi, a young boy on the run from the police. Desperate to turn the game around, Nangou hands the game over to Akagi after teaching him a few of the rules. The mafia can only smirk as Akagi sits down to play. However, they soon come to learn that Akagi is a natural-born gambler. An imposing figure who does not fear death. One who is destined to become a legend.\n\n[Written by MAL Rewrite]"}, {"id": 2450, "name_en": "Crayon Shin-chan: The Storm Called - The Adult Empire Strikes Back", "name_jp": "映画　クレヨンしんちゃん　嵐を呼ぶモーレツ！オトナ帝国の逆襲", "image_url": "https://cdn.myanimelist.net/images/anime/1824/94859.jpg", "genres": ["Comedy", "Sci-Fi"], "themes": [], "demographics": ["Seinen"], "studios": ["Shin-Ei Animation"], "year": 2001, "episodes": 1, "source": "Manga", "score": 7.92, "synopsis": "Adult people all over Japan had been captivated by 20th Century Expo, theme parks that reenacted good old days of the 1970s (the last days of Japan's high economic growth). One day the adults disappeared into the theme parks, leaving their children abandoned. That was a plot by \"Yesterday Once More,\" an organization who despises the 21st century and tries to bring Japan back to the 20th century with the dreams and hopes. Shinnosuke and his parents, Hiroshi and Misae, fight against the plot of \"Yesterday Once More\" in order to live together with family in the 21st century.\n\n(Source: Manabu Tsuribe)"}, {"id": 645, "name_en": "Hikaru no Go: Hokuto Hai e no Michi", "name_jp": "ヒカルの碁 北斗杯への道", "image_url": "https://cdn.myanimelist.net/images/anime/12/78453.jpg", "genres": [], "themes": ["Strategy Game"], "demographics": ["Shounen"], "studios": ["Studio Pierrot"], "year": 2004, "episodes": 1, "source": "Manga", "score": 7.92, "synopsis": "Hikaru is given a phone call asking him to appear in the Japan/China/Korea Junior's Tournament but he finds out he will have to take place in a preliminary tournament to choose Japan's three contestants. Upon discussing this with Akira, he discovers that Akira has already been chosen for the tournament and will not be taking place in the preliminaries. Hikaru then decides not to visit Akira's Go Salon until he takes his place on the team with Akira. That may be tougher than originally planned, as not only does Hikaru have to battle with old friends for one of two coveted spots, but he may have a new challenger, in a promising player from the Kansai Go Institute. \n\n(Source: ANN)"}, {"id": 11113, "name_en": "Bunny Drop Specials", "name_jp": "うさぎドロップ", "image_url": "https://cdn.myanimelist.net/images/anime/7/30759.jpg", "genres": ["Slice of Life"], "themes": ["Childcare", "Iyashikei"], "demographics": ["Josei"], "studios": ["Production I.G"], "year": 2011, "episodes": 4, "source": "Manga", "score": 7.92, "synopsis": "Happa no Suizokukan\nOne morning, Daikichi Kawachi finds Rin Kaga eagerly crafting fish out of autumn leaves. Wanting to spur her newfound interest, Daikichi helps Rin learn more about the beautiful and diverse animals that live under the sea.\n\nSanta-san e\nSpending their first Christmas together, Daikichi and Rin prepare to welcome Santa Claus and his reindeer.\n\nOsora ni Mankai\nWith the cherry blossoms in full bloom, Rin wants to go flower watching the next day with Daikichi alongside Kouki and Yukari Nitani. However, the forecast of a spring storm may dampen their plans.\n\nKaeri Michi\nAfter their visit to a summer festival, Kouki is still energetic and wishes to look around more. Daikichi decides to take the group on a quick detour before heading home.\n\n[Written by MAL Rewrite]"}, {"id": 187, "name_en": "Initial D Third Stage", "name_jp": "頭文字〈イニシャル〉D THIRD STAGE", "image_url": "https://cdn.myanimelist.net/images/anime/13/40397.jpg", "genres": ["Action", "Drama"], "themes": ["Racing"], "demographics": ["Seinen"], "studios": ["Studio Deen"], "year": 2001, "episodes": 1, "source": "Manga", "score": 7.92, "synopsis": "Takumi Fujiwara is a skilled street racer, but he suffers a crushing loss against the team Emperor's leader Kyoichi Sudou due to his AE86 experiencing an engine failure. Doubting his abilities, the recent high school graduate is then approached by the Akagi RedSuns' team leader Ryousuke Takahashi, who proposes the formation of a professional street racing team. Although it would be the ideal way to improve as a street racer, Takumi remains undecided.\n\nDoes the young street racer have what it takes to become a professional? Perhaps Ryousuke and the RedSuns can help him reevaluate his own doubts and misconceptions concerning street racing. However, first and foremost, Takumi decides to settle the score with Kyoichi Sudou...\n\n[Written by MAL Rewrite]"}, {"id": 35968, "name_en": "Wotakoi: Love is Hard for Otaku", "name_jp": "ヲタクに恋は難しい", "image_url": "https://cdn.myanimelist.net/images/anime/1864/93518.jpg", "genres": ["Comedy", "Romance"], "themes": ["Adult Cast", "Otaku Culture", "Workplace"], "demographics": [], "studios": ["A-1 Pictures"], "year": 2018, "episodes": 11, "source": "Web manga", "score": 7.92, "synopsis": "Having slept through all four of her alarms, the energetic Narumi Momose finds herself running late for her first day of work at a new office. As she races to catch her train, she makes a promise to herself that none of her coworkers will find out about her dark secret: that she is an otaku and a fujoshi. Her plan goes instantly awry, though, when she runs into Hirotaka Nifuji, an old friend from middle school. Although she tries to keep her secret by inviting him out for drinks after work, her cover is blown when he casually asks her whether or not she will be attending the upcoming Summer Comiket. Luckily for her, the only witnesses—Hanako Koyanagi and Tarou Kabakura—are otaku as well.\n            \nLater that night, the pair go out for drinks so that they can catch up after all the years apart. After Narumi complains about her previous boyfriend breaking up with her because he refused to date a fujoshi, Hirotaka suggests that she try dating a fellow otaku, specifically himself. He makes a solemn promise to always be there for her, to support her, and to help her farm for rare drops in Monster Hunter. Blown away by the proposal, Narumi agrees immediately. Thus the two otaku start dating, and their adorably awkward romance begins.\n\n[Written by MAL Rewrite]"}, {"id": 61595, "name_en": "Lost in Starlight", "name_jp": "이 별에 필요한", "image_url": "https://cdn.myanimelist.net/images/anime/1371/149504.jpg", "genres": ["Romance", "Sci-Fi"], "themes": ["Adult Cast", "Space"], "demographics": [], "studios": ["Climax Studio"], "year": 2025, "episodes": 1, "source": "Original", "score": 7.91, "synopsis": "Nan-young is a scientist whose astronaut mom failed to return to Earth when things went awry during her Mars probe. Nan-young has always dreamed of exploring space too and longs to go to Mars in hopes of finding a trace of her mom.\n\nHer dream comes true when she is selected as one of the astronauts for a Mars probe project. Before she blasts off though, she meets Jay, a musician who also fixes retro sound instruments. It's all thanks to an old turntable that Nan-young inherited from her mom.\n\n(Source: Netflix, edited)"}, {"id": 60541, "name_en": "Martial Universe 5", "name_jp": "武动乾坤 第五季", "image_url": "https://cdn.myanimelist.net/images/anime/1315/146994.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Motion Magic", "DC Impression Vision"], "year": 2025, "episodes": 12, "source": "Web novel", "score": 7.91, "synopsis": "The young Lin Dong embarks on a journey to search for the whereabouts of the Devouring Ancestor Talisman, venturing deep into the ancient ruins of the Great Wilderness. He continuously devotes himself to cultivation, and finally, the clan competition arrives... Facing his opponent, Lin Langtian, a genius of the Lin family who had crippled his father and ruthlessly harmed him, Lin Dong's heart is filled with unresolved humiliation, and he is eager for revenge and to turn the tables!\n\n(Source: WeTV)"}, {"id": 60285, "name_en": "Sakamoto Days Part 2", "name_jp": "SAKAMOTO DAYS 第２クール", "image_url": "https://cdn.myanimelist.net/images/anime/1758/148719.jpg", "genres": ["Action", "Comedy"], "themes": ["Adult Cast", "Organized Crime"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2025, "episodes": 11, "source": "Manga", "score": 7.91, "synopsis": "The legendary former hitman Tarou Sakamoto has thwarted numerous assassins after an enormous bounty on his head was issued. But he cannot seem to catch a break and simply take it easy with his beloved family. A mysterious and infamous figure known as Slur has brought a group of insane death row inmates to Japan, who hold back from nothing to eliminate their targets.\n\nSakamoto is not the only target—the criminals have been assigned to kill various other people, including the new hires at Sakamoto's convenience store, Shin Asakura and Lu Shaotang. The situation escalates even further when the Order, a group of the most skilled Japanese assassins, becomes involved. As the inmates begin wreaking havoc, Sakamoto and his allies must remain vigilant for the sake of everything they hold dear.\n\n[Written by MAL Rewrite]"}, {"id": 55742, "name_en": "The Ancient Magus' Bride Season 2 Part 2", "name_jp": "魔法使いの嫁 SEASON2 第2クール", "image_url": "https://cdn.myanimelist.net/images/anime/1801/138944.jpg", "genres": ["Drama", "Fantasy", "Romance"], "themes": ["Mythology", "School", "Urban Fantasy"], "demographics": ["Shounen"], "studios": ["Studio Kafka"], "year": 2023, "episodes": 12, "source": "Manga", "score": 7.91, "synopsis": "Following a series of attacks that have left teachers and students alike sapped of their magic, headmistress Liza Quillyn decides to seal off the College, rendering it impossible to enter or leave the premises. As a result, Philomela Sargant is unable to comply with her grandmother Lizbeth's order to withdraw from the school.\n\nTrapped inside the College, Chise Hatori and her classmates begin to adjust to their novel and stressful circumstances. But as time goes on, Philomela's condition only gets worse—both mentally and physically. Meanwhile, details about the tragic past of Chise's roommate, Lucy Webster, continue to resurface. As Chise grapples with worries about her friends and with her evolving self, the identity of the mysterious magic-draining culprit plaguing the College may finally come to light.\n\n[Written by MAL Rewrite]"}, {"id": 55310, "name_en": "My New Boss Is Goofy", "name_jp": "新しい上司はど天然", "image_url": "https://cdn.myanimelist.net/images/anime/1630/138388.jpg", "genres": ["Slice of Life"], "themes": ["Adult Cast", "Iyashikei", "Workplace"], "demographics": [], "studios": ["A-1 Pictures"], "year": 2023, "episodes": 12, "source": "Web manga", "score": 7.91, "synopsis": "After 26-year-old Kentarou Momose develops health problems due to his boss' abuse, he quits his job and joins a different company. Now, he hopes to start anew under the care of his mentor and direct supervisor, 34-year-old Yuusei Shirosaki.\n\nShirosaki is a capable boss admired by his coworkers, which makes Momose fear letting him down and reliving the past traumas. However, Momose can rest easy, as Shirosaki is always delighted to help—even if he looks absolutely ridiculous doing it. Although it may take some adjusting, Momose realizes that there is no need for caution around his new, endearingly eccentric boss after all.\n\n[Written by MAL Rewrite]"}, {"id": 52976, "name_en": "Berserk: The Golden Age Arc - Memorial Edition", "name_jp": "ベルセルク 黄金時代篇　MEMORIAL EDITION", "image_url": "https://cdn.myanimelist.net/images/anime/1607/127730.jpg", "genres": ["Action", "Adventure", "Drama", "Fantasy", "Horror"], "themes": ["Gore", "Military", "Psychological"], "demographics": ["Seinen"], "studios": ["Studio 4°C"], "year": 2022, "episodes": 13, "source": "Manga", "score": 7.91, "synopsis": "Fleeing a traumatic childhood, lone mercenary Guts drifts from battle to battle, striking down powerful enemies with his unwieldy sword. After one skirmish, Guts clashes with the mystifying Griffith and his small army known as the Band of the Hawk. The victorious Griffith drafts Guts into his ranks, granting the swordsman a kinship unlike anything he has ever known.\n\nAlongside the skilled warrior Casca, Guts helps Griffith guide the band to fame and glory. But as he gains military prestige, Griffith becomes the target of assassination attempts and political schemes. He uses Guts to further his ambitious rise to power, taking advantage of the swordsman's lack of aspiration.\n\nEven as he grows closer to Casca, Guts' absence of direction robs him of any sense of belonging. Fostering opposing desires, Guts and Casca fight for Griffith's dream—although fractures forming among the trio threaten to disrupt their world.\n\n[Written by MAL Rewrite]"}, {"id": 52865, "name_en": "Romantic Killer", "name_jp": "ロマンティック・キラー", "image_url": "https://cdn.myanimelist.net/images/anime/1764/142001.jpg", "genres": ["Comedy", "Romance", "Supernatural"], "themes": ["Parody", "Reverse Harem"], "demographics": ["Shounen"], "studios": ["domerica"], "year": 2022, "episodes": 12, "source": "Manga", "score": 7.91, "synopsis": "Anzu Hoshino needs only three things in her life: video games, chocolate, and her beloved cat. Unlike other high school girls, Anzu has no time for or interest in romance. But as she begins playing a poorly programmed 3D otome game, a bizarre flying wizard named Riri emerges from the screen and calls Anzu \"subject one,\" the first person who will experience a dating game harem storyline in real life.\n\nDespite Anzu's fiery protests, Riri confiscates her favorite things to force her to focus on love. Riri orchestrates a series of unlucky incidents and romantic cliches that lead her to meet Tsukasa Kazuki, one of the most attractive boys in her school. Still enraged, Anzu is adamant about resisting Tsukasa's charm. As all the ridiculous fabricated scenarios help Anzu warm up to Tsukasa's pleasant nature, Riri throws other stereotypical pretty boys her way—and avoiding romance quickly becomes almost impossible.\n\n[Written by MAL Rewrite]"}, {"id": 24997, "name_en": "Love Live! The School Idol Movie", "name_jp": "ラブライブ！The School Idol Movie", "image_url": "https://cdn.myanimelist.net/images/anime/6/75700.jpg", "genres": ["Award Winning", "Slice of Life"], "themes": ["Idols (Female)", "Music", "School"], "demographics": [], "studios": ["Sunrise"], "year": 2015, "episodes": 1, "source": "Original", "score": 7.91, "synopsis": "Hot on the heels of the third year students' graduation, μ's is invited to New York in hopes of spreading the joy of school idols to other parts of the world. Due to the events of the recent Love Live!, μ's has reached eminent stardom which results in crowds swarming them whenever they appear in public. With the increased attention, however, comes a difficult choice.\n\nHaving yet to publicly announce the decision they came to regarding their future, the young members of μ's are pushed to continue performing by rival group A-RISE, Otonokizaka High School, and even Love Live! itself. As leader, Honoka Kousaka is left wondering if the path they have chosen is truly for the best, as μ's must re-evaluate their choices and come to a final decision on what they want for the future.\n\nLove Live! The School Idol Movie depicts the final chapter in μ's story as the girls explore just what being an idol means to them as well as the bond that connects the nine of them together.\n\n[Written by MAL Rewrite]"}, {"id": 8740, "name_en": "One Piece Film: Strong World Episode 0", "name_jp": "ワンピース　フィルム　ストロングワールド　エピソードゼロ", "image_url": "https://cdn.myanimelist.net/images/anime/1172/109469.jpg", "genres": ["Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Toei Animation"], "year": 2010, "episodes": 1, "source": "Manga", "score": 7.91, "synopsis": "Set over 20 years prior to the main One Piece story, this limited release OVA chronicles the confrontation between Gold Lion Shiki and Gold Roger as well as other events around the world around the time of the Pirate King's execution."}, {"id": 11739, "name_en": "You and Me 2", "name_jp": "君と僕。2", "image_url": "https://cdn.myanimelist.net/images/anime/10/42107.jpg", "genres": ["Slice of Life"], "themes": ["Iyashikei", "School"], "demographics": ["Shounen"], "studios": ["J.C.Staff"], "year": 2012, "episodes": 13, "source": "Manga", "score": 7.91, "synopsis": "\"No matter how many years go by, I'm sure we'll still be laughing together.\" \n\nTwins Yuta and Yuki, Kaname, and Shun have been childhood friends since kindergarten. When transfer student Chizuru joins them, their five man school life becomes all the more lively. Through the changing seasons, the boys will find laughter, surprises, love, and new encounters waiting for them. \n\nThe second season of the boys growing a little every day of their invaluable daily lives is about to begin! \n\n(Source: Crunchyroll)"}, {"id": 21855, "name_en": "Hanamonogatari", "name_jp": "花物語", "image_url": "https://cdn.myanimelist.net/images/anime/13/65755.jpg", "genres": ["Comedy", "Mystery", "Supernatural"], "themes": [], "demographics": [], "studios": ["Shaft"], "year": 2014, "episodes": 5, "source": "Light novel", "score": 7.91, "synopsis": "Now that Koyomi Araragi and Hitagi Senjougahara have graduated, very few familiar faces remain at Naoetsu Private High School. One of these is Suruga Kanbaru, holder of the Monkey's Paw. When she begins to hear talk of a mysterious being known as the \"Devil\" who will magically solve any problem, Kanbaru immediately thinks these rumors are about her and decides to investigate.\n\nShe discovers the Devil is actually Rouka Numachi, a former basketball rival from junior high who is no longer able to play due to a leg injury. Rouka provides free advice to those who seek her out. Acting as a collector of misfortune, she enjoys relieving the stress of her clients by providing them with the false hope of having their problems solved. Although Kanbaru sees no real harm being done, she reprimands Rouka for lying and heads home, relieved she is not the cause of the rumors. But she may have a reason to worry after all: she finds that her left hand has reverted back to its human form.\n\n[Written by MAL Rewrite]"}, {"id": 37675, "name_en": "Overlord III", "name_jp": "オーバーロードⅢ", "image_url": "https://cdn.myanimelist.net/images/anime/1511/93473.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Isekai"], "demographics": [], "studios": ["Madhouse"], "year": 2018, "episodes": 13, "source": "Light novel", "score": 7.91, "synopsis": "Following the horrific assault on the Re-Estize capital city, the Guardians of the Great Tomb of Nazarick return home to their master Ainz Ooal Gown. After months of laying the groundwork, they are finally ready to set their plans of world domination into action.\n\nAs Ainz's war machine gathers strength, the rest of the world keeps moving. The remote Carne Village, which Ainz once saved from certain doom, continues to prosper despite the many threats on its doorstep. And in the northeastern Baharuth Empire, a certain Bloody Emperor sets his sights on the rising power of Nazarick.\n\nBlood is shed, heroes fall, and nations rise. Can anyone, or anything, challenge the supreme power of Ainz Ooal Gown?\n\n[Written by MAL Rewrite]"}, {"id": 34944, "name_en": "Bungo Stray Dogs: Dead Apple", "name_jp": "文豪ストレイドッグス DEAD APPLE", "image_url": "https://cdn.myanimelist.net/images/anime/1127/93981.jpg", "genres": ["Action", "Mystery"], "themes": ["Adult Cast", "Detective", "Organized Crime", "Super Power"], "demographics": ["Seinen"], "studios": ["Bones"], "year": 2018, "episodes": 1, "source": "Manga", "score": 7.91, "synopsis": "A large-scale catastrophe is occurring across the planet. Ability users are discovered after the appearance of a mysterious fog, apparently having committed suicide, so the Armed Detective Agency sets out to investigate these mysterious deaths. The case seems to involve an unknown ability user referred to as \"Collector,\" a man who could be the mastermind behind the incident.\n\nTrust and courage are put to the test in order to save the city of Yokohama and ability users across the world from the grip of Collector where the Armed Detective Agency forms an unlikely partnership with the dangerous Port Mafia. \n\n[Written by MAL Rewrite]"}, {"id": 5682, "name_en": "Phantom: Requiem for the Phantom", "name_jp": "Phantom 〜Requiem for the Phantom〜", "image_url": "https://cdn.myanimelist.net/images/anime/8/22470.jpg", "genres": ["Action", "Drama", "Suspense"], "themes": ["Organized Crime"], "demographics": [], "studios": ["Bee Train"], "year": 2009, "episodes": 26, "source": "Visual novel", "score": 7.91, "synopsis": "Mafia is rife in America where assassinations are a regular occurrence on the streets. Inferno, a mysterious company, is behind most of these dealings through the use of their near-invincible human weapon, \"Phantom.\"\n\nOne day, a Japanese tourist accidentally witnesses Phantom's latest murder. Desperate to escape, the tourist hides in a secluded building. However, Phantom, revealed to be a young woman named Ein, and the leader of Inferno \"Scythe Master\" captures the tourist and brainwashes him.\n\nGiven the name \"Zwei,\" this once peaceful tourist is now a puppet of Inferno with no memories. Drawn into a world of lies, deceit, and violence, Zwei must fight to survive, hopefully to one day regain his memories and escape from this world where he is constantly on the brink of death.\n\n[Written by MAL Rewrite]"}, {"id": 27775, "name_en": "Plastic Memories", "name_jp": "プラスティック・メモリーズ", "image_url": "https://cdn.myanimelist.net/images/anime/4/72750.jpg", "genres": ["Drama", "Romance", "Sci-Fi"], "themes": [], "demographics": [], "studios": ["Doga Kobo"], "year": 2015, "episodes": 13, "source": "Original", "score": 7.91, "synopsis": "Eighteen-year-old Tsukasa Mizugaki has failed his college entrance exams, but after pulling some strings, he manages to land a job at the Sion Artificial Intelligence Corporation. SAI Corp is responsible for the creation of \"Giftias\"—highly advanced androids which are almost indiscernible from normal humans. However, unlike humans, Giftias have a maximum lifespan of 81,920 hours, or around nine years and four months. Terminal Service One, the station Tsukasa was assigned to, is responsible for collecting Giftias that have met their expiration date, before they lose their memories and become hostile.\n\nPromptly after joining Terminal Service One, Tsukasa is partnered with a beautiful Giftia named Isla. She is a Terminal Service veteran and considered the best in Giftia retrievals, contrary to her petite figure and placid nature. Time is fleeting though, and Tsukasa must come to terms with his feelings for Isla before her time is up. No matter how much someone desires it, nothing lasts forever.\n\n[Written by MAL Rewrite]"}, {"id": 35790, "name_en": "The Rising of the Shield Hero", "name_jp": "盾の勇者の成り上がり", "image_url": "https://cdn.myanimelist.net/images/anime/1490/101365.jpg", "genres": ["Action", "Adventure", "Drama", "Fantasy"], "themes": ["Isekai"], "demographics": [], "studios": ["Kinema Citrus"], "year": 2019, "episodes": 25, "source": "Light novel", "score": 7.91, "synopsis": "The Four Cardinal Heroes are a group of ordinary men from modern-day Japan summoned to the kingdom of Melromarc to become its saviors. Melromarc is a country plagued by the Waves of Catastrophe that have repeatedly ravaged the land and brought disaster to its citizens for centuries. The four heroes are respectively bestowed a sword, spear, bow, and shield to vanquish these Waves. Naofumi Iwatani, an otaku, becomes cursed with the fate of being the \"Shield Hero.\" Armed with only a measly shield, Naofumi is belittled and ridiculed by his fellow heroes and the kingdom's people due to his weak offensive capabilities and lackluster personality.\n\nWhen the heroes are provided with resources and comrades to train with, Naofumi sets out with the only person willing to train alongside him, Malty Melromarc. He is soon betrayed by her, however, and becomes falsely accused of taking advantage of her. Naofumi then becomes heavily discriminated against and hated by the people of Melromarc for something he didn't do. With a raging storm of hurt and mistrust in his heart, Naofumi begins his journey of strengthening himself and his reputation. Further along however, the difficulty of being on his own sets in, so Naofumi buys a demi-human slave on the verge of death named Raphtalia to accompany him on his travels.\n\nAs the Waves approach the kingdom, Naofumi and Raphtalia must fight for the survival of the kingdom and protect the people of Melromarc from their ill-fated future.\n\n[Written by MAL Rewrite]"}, {"id": 1695, "name_en": "Les Misérables: Shoujo Cosette", "name_jp": "レ・ミゼラブル 少女コゼット", "image_url": "https://cdn.myanimelist.net/images/anime/1479/154965.jpg", "genres": ["Drama"], "themes": ["Historical"], "demographics": [], "studios": ["Nippon Animation"], "year": 2007, "episodes": 52, "source": "Novel", "score": 7.91, "synopsis": "In 19th century France, a struggling single mother, Fantine, leaves her three-year-old daughter Cosette in the care of her new acquaintances, the Thernadiers. Unfortunately, Cosette's caretakers prove to be anything but loving, and the poor girl is subjected to repeated abuse and forced servitude. Still, she endures the torment in the hopes of seeing her mother once again.\n\nOne night, while doing errands for her host family, Cosette is assisted by an honorable stranger named Jean Valjean. After a brief conversation with the young girl, Jean acknowledges her as the type of person he has been seeking and rescues her from the clutches of the Thernadiers. They make their way to a nearby town where Cosette enjoys a new life thanks to her savior.\n\nUnder Jean's guidance, Cosette promises to help others with her newfound freedom. She pledges to heal the nation, ensuring that no one else suffers her fate. Though the road ahead is paved with tragedies left by the French Revolution, this idealistic girl will not rest until France is freed from poverty and suffering.\n\n[Written by MAL Rewrite]"}, {"id": 33206, "name_en": "Miss Kobayashi's Dragon Maid", "name_jp": "小林さんちのメイドラゴン", "image_url": "https://cdn.myanimelist.net/images/anime/5/85434.jpg", "genres": ["Slice of Life", "Supernatural"], "themes": [], "demographics": [], "studios": ["Kyoto Animation"], "year": 2017, "episodes": 13, "source": "Manga", "score": 7.91, "synopsis": "As Kobayashi sets off for another day at work, she opens her apartment door only to be met by an unusually frightening sight—the head of a dragon, staring at her from across the balcony. The dragon immediately transforms into a cute, busty, and energetic young girl dressed in a maid outfit, introducing herself as Tooru.\n\nIt turns out that the stoic programmer had come across the dragon the previous night on a drunken excursion to the mountains, and since the mythical beast had nowhere else to go, she had offered the creature a place to stay in her home. Thus, Tooru had arrived to cash in on the offer, ready to repay her savior's kindness by working as her personal maidservant. Though deeply regretful of her words and hesitant to follow through on her promise, a mix of guilt and Tooru's incredible dragon abilities convinces Kobayashi to take the girl in.\n\nDespite being extremely efficient at her job, the maid's unorthodox methods of housekeeping often end up horrifying Kobayashi and at times bring more trouble than help. Furthermore, the circumstances behind the dragon's arrival on Earth seem to be much more complicated than at first glance, as Tooru bears some heavy emotions and painful memories. To top it all off, Tooru's presence ends up attracting several other mythical beings to her new home, bringing in a host of eccentric personalities. Although Kobayashi makes her best effort to handle the crazy situation that she has found herself in, nothing has prepared her for this new life with a dragon maid.\n\n[Written by MAL Rewrite]"}, {"id": 21647, "name_en": "Tamako Love Story", "name_jp": "たまこラブストーリー", "image_url": "https://cdn.myanimelist.net/images/anime/1417/91333.jpg", "genres": ["Award Winning", "Romance"], "themes": [], "demographics": [], "studios": ["Kyoto Animation"], "year": 2014, "episodes": 1, "source": "Original", "score": 7.91, "synopsis": "As the seasons pass by, the end of Mochizou Ooji's third and final school year quickly approaches. He aims to study at a university in Tokyo, but at the cost of leaving behind his loved ones—including his beloved childhood crush, Tamako Kitashirakawa. Having no such plans for the future, Tamako will merely remain in town to work at her family's humble mochi shop.\n\nAs the time for Mochizou's departure draws closer, the reserved young man must gather up the courage to confess his feelings to Tamako before it is too late—lest his love go unnoticed forever.\n\n[Written by MAL Rewrite]"}, {"id": 46422, "name_en": "Sacrificial Princess and the King of Beasts", "name_jp": "贄姫と獣の王", "image_url": "https://cdn.myanimelist.net/images/anime/1397/133339.jpg", "genres": ["Fantasy", "Romance"], "themes": ["Anthropomorphic"], "demographics": ["Shoujo"], "studios": ["J.C.Staff"], "year": 2023, "episodes": 24, "source": "Manga", "score": 7.91, "synopsis": "Located at the end of the world, the Kingdom of Ozmargo is inhabited by beasts of all shapes and forms, ruled by a fearsome nameless king. After a hundred-year war between Ozmargo and the neighboring human kingdom ended, a peace treaty was signed: every year, a sacrifice must be sent to be devoured by the king on the night of revelation.\n\nSariphi is the 99th and latest human sacrifice. Although frail-looking, she neither fears the king nor has a family or home to return to should she manage to escape from her impending demise. Intrigued by the audacious girl, the king lets Sariphi roam around the palace despite the disapproval of his advisor, Anubis.\n\nLittle by little, the king and Sariphi begin to warm up to each other, but the night of revelation is fast approaching. Unbeknownst to them, the fateful day unleashes a series of events that will change the course of destiny of not only Sariphi and the king but also of their respective worlds.\n\n[Written by MAL Rewrite]"}, {"id": 41169, "name_en": "Love Live! SuperStar!!", "name_jp": "ラブライブ！スーパースター!!", "image_url": "https://cdn.myanimelist.net/images/anime/1758/115692.jpg", "genres": ["Slice of Life"], "themes": ["Idols (Female)", "Music", "School"], "demographics": [], "studios": ["Sunrise"], "year": 2021, "episodes": 12, "source": "Other", "score": 7.91, "synopsis": "Everyone has a dream they strive to achieve, and so does the musically talented Kanon Shibuya. However, due to her stage fright, Kanon fails to make it into Yuigaoka Girls' High School's music program and instead ends up in the general curriculum. Even though Kanon makes the conscious decision to quit music altogether, her classmate Tang Keke rekindles Kanon's passion for music with her own: a passion for school idols.\n\nAlthough Keke and Kanon try to form a school idol group, their effort meets strong objections from Ren Hazuki, the daughter of the school's director. Even so, the girls soon visit the director, who allows them to set up their school idol club under one condition—they must win first place at the Yoyogi School Idol Festival.\n\nThe resilience of these aspiring idols is put to the test as they face the challenges that come their way. Throughout their journey, the girls make new friends and meet new members that join them to become the dazzling idols others can only imagine.\n\n[Written by MAL Rewrite]"}, {"id": 48171, "name_en": "Summer Ghost", "name_jp": "サマーゴースト", "image_url": "https://cdn.myanimelist.net/images/anime/1651/117943.jpg", "genres": ["Drama", "Supernatural"], "themes": [], "demographics": [], "studios": ["Flat Studio"], "year": 2021, "episodes": 1, "source": "Mixed media", "score": 7.91, "synopsis": "It is said that fireworks can calm the souls of the dead. For Tomoya Sugisaki, Aoi Harukawa, and Ryou Kobayashi, fireworks are what allowed them to meet the Summer Ghost. Believed to be the spirit of a young woman who committed suicide, the Summer Ghost only appears in a specific area and can only be seen by those who are within arm's reach of death. \n\nTomoya is a creative soul being crushed by his academic obligation to get into a good university. Aoi is a meek girl who is relentlessly bullied by her classmates. Ryou is a former basketball star who had to forfeit his passion following a grim diagnosis. The only thing these three have in common is their ability to see the Summer Ghost. The Summer Ghost is said to be able to answer any and all questions pertaining to death—something the three teenagers desperately want to know more about.\n\nDissatisfied with their initial meeting, Tomoya sets out to find the Summer Ghost once again. But the more time he spends with the ghost, the more the mystery surrounding her existence unravels.\n\n[Written by MAL Rewrite]"}, {"id": 16001, "name_en": "Kokoro Connect OVA", "name_jp": "ココロコネクト ミチランダム", "image_url": "https://cdn.myanimelist.net/images/anime/10/45526.jpg", "genres": ["Drama", "Romance", "Supernatural"], "themes": ["School"], "demographics": [], "studios": ["SILVER LINK."], "year": 2012, "episodes": 4, "source": "Light novel", "score": 7.91, "synopsis": "Not long after putting the previous supernatural incident behind them, the members of Yamaboshi Academy's Student Cultural Society (StuCS) must deal with Fuusenkazura's newest trial—emotion transmission. This phenomenon allows the club members to hear each others' true thoughts, but with one catch: the timing, sender, and recipient are all completely random. To make matters worse, the club's supervisor, Ryuuzen Gotou, may have to step down due to his responsibilities for the upcoming school year. With emotions running high and Valentine's Day around the corner, the seemingly close bonds of the StuCS will be tested when their true feelings for each other are laid bare.\n\n[Written by MAL Rewrite]"}, {"id": 15689, "name_en": "Nekomonogatari Black", "name_jp": "猫物語（黒）", "image_url": "https://cdn.myanimelist.net/images/anime/1170/121597.jpg", "genres": ["Mystery", "Romance", "Supernatural", "Ecchi"], "themes": [], "demographics": [], "studios": ["Shaft"], "year": 2012, "episodes": 4, "source": "Light novel", "score": 7.91, "synopsis": "After surviving a vampire attack, Koyomi Araragi notices that his friend and savior, Tsubasa Hanekawa, has been acting strange. When he happens to cross paths with her on his way to a bookstore and sees she has a bandage on her face, he knows something must definitely be wrong. Araragi wants to help her, but Hanekawa assures him that her wound is just something she received at home and that he should not concern himself with it. But when a white cat with no tail is hit and killed by a car, the pair bury the creature and the real trouble begins.\n\nWhen Araragi later pays a visit to his friend Meme Oshino and recounts the day's events, he is informed what they have buried is actually an apparition, one perfect for Hanekawa in her current state. Tasked with finding his friend to confirm her safety, he discovers that she has attacked her parents, possessed by the \"Sawari Neko.\" Now, it is up to Araragi to help Hanekawa as she once helped him.\n\n[Written by MAL Rewrite]"}, {"id": 73, "name_en": "Full Metal Panic! The Second Raid", "name_jp": "フルメタル パニック！The Second Raid", "image_url": "https://cdn.myanimelist.net/images/anime/11/75261.jpg", "genres": [], "themes": ["Mecha", "Military"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2005, "episodes": 13, "source": "Light novel", "score": 7.91, "synopsis": "After overcoming various tumultuous events, mercenary sergeant Sousuke Sagara of Mithril and \"Whispered\" school girl Kaname Chidori return to their normal high school lives. Sousuke continues his efforts to assimilate with the civilian population and manage his mission at the same time, while Kaname attempts to support his endeavors (even though his antics often drive her up a wall). \n\nHowever, as their lives calm, new problems begin to appear. The appearance of an independent terrorist organization  known as Amalgam and internal conflicts within Mithril threaten to wreak unity into a series of failures and betrayals. And new, powerful Arm Slave models only worsen the situation when the world's peace is at stake. \n\nAs Kaname and Sousuke face their own inner battles amidst life-threatening fights, will they be able to make the right choices and change their future?\n\n[Written by MAL Rewrite]"}, {"id": 38409, "name_en": "Scissor Seven", "name_jp": "刺客伍六七", "image_url": "https://cdn.myanimelist.net/images/anime/1620/94968.jpg", "genres": ["Action", "Comedy", "Drama", "Mystery", "Romance"], "themes": ["Martial Arts", "Super Power"], "demographics": [], "studios": ["Sharefun Studio"], "year": 2018, "episodes": 10, "source": "Original", "score": 7.91, "synopsis": "To the casual eye, the amnesiac bounty hunter Wu Liuqi looks quite intimidating. With his deadly telekinetic scissor techniques and his ability to seamlessly transform into anything, one would not expect his modest demeanor. In fact, Wu is quite terrible at his job. Often times the freelancer can be found botching an assassination or targeting the wrong person. While his failures could be due to his subpar skills, it usually boils down to him being a normal kid, with a heart unsuited for his line of work.\n\nAccompanied by his feathered friend Dai Bo, Wu is on a simple quest to regain his memories. Although his inconspicuous day job as a hairdresser and his after-hours occupation are simply a means for him to repay debt, his various ventures seem to intertwine with his pursuit to recover his lost past.\n\n[Written by MAL Rewrite]"}, {"id": 28, "name_en": "Yakitate!! Japan", "name_jp": "焼きたて!! ジャぱん", "image_url": "https://cdn.myanimelist.net/images/anime/3/76432.jpg", "genres": ["Comedy", "Gourmet"], "themes": [], "demographics": ["Shounen"], "studios": ["Sunrise"], "year": 2004, "episodes": 69, "source": "Manga", "score": 7.91, "synopsis": "While countries such as France, England, and Germany all have their own internationally celebrated bread, Japan simply does not have one that can match in reputation.\n\nThus after discovering the wonders of breadmaking at a young age, Kazuma Azuma embarks on a quest to create Japan's own unique national bread. And being blessed with unusually warm hands that allow dough to ferment faster, Azuma is able to bring his baking innovations to another level.\n\nAs he begins working at the prestigious Japanese bakery chain, Pantasia, Azuma encounters other talented bakers and experiences firsthand the competitive world of baking. Along with his newfound friends and rivals, Azuma strives to create new and unparalleled bread that will start a baking revolution. \n\n[Written by MAL Rewrite]"}, {"id": 15, "name_en": "Eyeshield 21", "name_jp": "アイシールド21", "image_url": "https://cdn.myanimelist.net/images/anime/1079/133529.jpg", "genres": ["Sports"], "themes": ["Team Sports"], "demographics": ["Shounen"], "studios": ["Gallop"], "year": 2005, "episodes": 145, "source": "Manga", "score": 7.91, "synopsis": "Shy, reserved, and small-statured, Deimon High School student Sena Kobayakawa is the perfect target for bullies. However, as a result of running errands throughout his life, Sena has become agile and developed a skill for avoiding crowds of people. After the cunning Youichi Hiruma—captain of the Deimon Devil Bats football team—witnesses Sena's rapid legs in motion, he coerces the timid boy into joining his squad.\n\nAs Hiruma wants to conceal Sena's identity from other clubs, Sena is forced to hide under the visored helmet of \"Eyeshield 21,\" a mysterious running back wearing the number 21 jersey. The legendary Eyeshield 21 can supposedly run at the speed of light and has achieved remarkable feats in the United States during his time at the Notre Dame College.\n\nAccustomed to avoiding his problems in the past, Sena's specialty might just help him become the new secret weapon of the Deimon Devil Bats. As he interacts with his teammates, Sena gradually gains more self-confidence and forges valuable bonds along the way.\n\n[Written by MAL Rewrite]"}, {"id": 60108, "name_en": "One Piece Log: Fish-Man Island Saga", "name_jp": "SPECIAL EDITED VERSION『ONE PIECE』魚人島編", "image_url": "https://cdn.myanimelist.net/images/anime/1928/146063.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Toei Animation"], "year": 2024, "episodes": 21, "source": "Manga", "score": 7.9, "synopsis": "Two years after the Summit War, Monkey D. Luffy plans to reunite with the Straw Hat Pirates in the Sabaody Archipelago. However, the Marines obstruct Luffy's path with overwhelming force. Meanwhile, the Straw Hats must also foil the schemes of a group of impersonators acting in their likenesses.\n\nWith two years of intensive training with allies old and new, Luffy regroups with his friends aboard the Thousand Sunny to set sail for the New World. As the Straw Hat Crew departs for Fish-Man Island, new threats creep up from the abyss. But more determined than ever to become the King of the Pirates, Luffy is prepared to defeat any enemy that may stand between him and his dream.\n\n[Written by MAL Rewrite]"}, {"id": 59360, "name_en": "Rock Is a Lady's Modesty", "name_jp": "ロックは淑女の嗜みでして", "image_url": "https://cdn.myanimelist.net/images/anime/1781/150071.jpg", "genres": ["Comedy"], "themes": ["Music", "School"], "demographics": ["Seinen"], "studios": ["Bandai Namco Pictures"], "year": 2025, "episodes": 13, "source": "Manga", "score": 7.9, "synopsis": "Oushin Girls' Academy is a respected institution reserved only for wealthy young ladies. Very few of its students excel enough to earn the title of Noble Maiden, awarded to those who embody the ideal Japanese woman of culture and refinement. Lilisa Suzunomiya, a girl of common roots, has suddenly found herself as the daughter of a newly remarried real estate mogul and seeks to become a Noble Maiden to prove that commoners like her can be noble as well.\n\nThe most difficult challenge Lilisa has faced in pursuit of this goal is abandoning her love of rock music to keep up the rich girl facade she must now wear. But her resolve crumbles once she happens upon Otoha Kurogane, the daughter of a prominent politician, skillfully playing the drums while rocking out in an abandoned school building. Despite initially being at odds, they embrace their passion for music together while leading a double life.\n\n[Written by MAL Rewrite]"}, {"id": 57616, "name_en": "The 100 Girlfriends Who Really, Really, Really, Really, Really Love You Season 2", "name_jp": "君のことが大大大大大好きな100人の彼女 2期", "image_url": "https://cdn.myanimelist.net/images/anime/1093/145470.jpg", "genres": ["Comedy", "Romance"], "themes": ["Harem", "Parody", "School"], "demographics": ["Seinen"], "studios": ["Bibury Animation Studios"], "year": 2025, "episodes": 12, "source": "Manga", "score": 7.9, "synopsis": "Ever since Rentarou Aijou found out that he would meet a total of one hundred soulmates in high school, his life has been anything but boring. Now with six girlfriends—each with their own unique quirks—Rentarou has never been happier.\n\nThe group expands once again when Rentarou meets Kurumi Haraga, a girl with a never-ending appetite; and Mei Meido, the maid of the Hanazono family. As he encounters more of his soulmates, Rentarou has to juggle ways to date them all, since failure results in a grim fate. Fortunately, Rentarou has no shortage of love to give to each of his girlfriends!\n\n[Written by MAL Rewrite]"}, {"id": 7062, "name_en": "Hidamari Sketch x ☆☆☆", "name_jp": "ひだまりスケッチ×☆☆☆", "image_url": "https://cdn.myanimelist.net/images/anime/9/22349.jpg", "genres": ["Slice of Life"], "themes": ["CGDCT", "Iyashikei", "School", "Visual Arts"], "demographics": [], "studios": ["Shaft"], "year": 2010, "episodes": 12, "source": "4-koma manga", "score": 7.9, "synopsis": "When we first met Yuno, she'd just been accepted to the prestigious Yamabuki Art School, and it was up to her new neighbors at Hidamari to help her find her way in the big scary world. Now a seasoned vet of the Hidamari Apartments herself, Yuno's newest challenge is to help the latest arrivals, Nazuna and Nori, acclimate to life on their own. Of course, since it IS Hidamari, there are still lots of trials and tribulations ahead, and nothing will ever go quite as expected. But with friends like Miyako, Hiro, and Sae ready to lend a hand, there will always be a ray of sunshine at the end of every storm in Hidamari Sketch x Hoshimittsu.\n\n(Source: Sentai Filmworks)"}, {"id": 34437, "name_en": "Code Geass: Lelouch of the Re;surrection", "name_jp": "コードギアス 復活のルルーシュ", "image_url": "https://cdn.myanimelist.net/images/anime/1274/113436.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["Mecha", "Military", "Super Power"], "demographics": [], "studios": ["Sunrise"], "year": 2019, "episodes": 1, "source": "Original", "score": 7.9, "synopsis": "Since the demise of the man believed to be Britannia's most wicked emperor one year ago, the world has enjoyed an unprecedented peace under the guidance of the United Federation of Nations. However, this fragile calm is shattered when armed militants successfully kidnap former princess Nunnally vi Britannia and Suzaku Kururugi, the chief advisor of the Black Knights, sparking an international crisis.\n\nThe powerful and untrustworthy Kingdom of Zilkhstan is accused of orchestrating their capture. To investigate, world authorities send Kallen Stadtfeld and her associates on a covert operation into the country. There, they encounter the immortal witch C.C., who is on a mission to complete the resurrection of the man responsible for the greatest revolution in history—a legend who will rise up, take command, and save the world from peril once again.\n\n[Written by MAL Rewrite]"}, {"id": 35222, "name_en": "School Babysitters", "name_jp": "学園ベビーシッターズ", "image_url": "https://cdn.myanimelist.net/images/anime/8/89978.jpg", "genres": ["Slice of Life"], "themes": ["Childcare", "Iyashikei", "School"], "demographics": ["Shoujo"], "studios": ["Brain's Base"], "year": 2018, "episodes": 12, "source": "Manga", "score": 7.9, "synopsis": "After losing both parents in a fatal plane crash, teenager Ryuuichi Kashima must adjust to his new life as the guardian of his younger brother Kotarou. Although Ryuuichi is able to maintain a friendly and kindhearted demeanor, Kotarou is a reserved toddler still too young to understand the reality of the situation. At their parents' funeral, they are approached by Youko Morinomiya, the stern chairman of an elite academy, who decides to take them under her care.\n \nHowever, there is one condition Ryuuichi must fulfill in exchange for a roof over their heads and enrolment in the school—he must become the school's babysitter. In an effort to support the female teachers at the academy, a babysitter's club was established to look after their infant children; unfortunately, the club is severely short-staffed, so now not only is Ryuuichi responsible for his little brother, but also a handful of toddlers who possess dynamic personalities.\n\n[Written by MAL Rewrite]"}, {"id": 17265, "name_en": "Log Horizon", "name_jp": "ログ・ホライズン", "image_url": "https://cdn.myanimelist.net/images/anime/5/84004.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Adult Cast", "Video Game"], "demographics": [], "studios": ["Satelight"], "year": 2013, "episodes": 25, "source": "Light novel", "score": 7.9, "synopsis": "In the blink of an eye, thirty thousand bewildered Japanese gamers are whisked from their everyday lives into the world of the popular MMORPG, Elder Tale, after the game's latest update—unable to log out. Among them is the socially awkward college student Shiroe, whose confusion and shock lasts only a moment as, a veteran of the game, he immediately sets out to explore the limits of his new reality.\n\nShiroe must learn to live in this new world, leading others and negotiating with the NPC \"natives\" in order to bring stability to the virtual city of Akihabara. He is joined by his unfortunate friend Naotsugu, having logged in for the first time in years only to find himself trapped, and Akatsuki, a petite but fierce assassin who labels Shiroe as her master. A tale of fantasy, adventure, and politics, Log Horizon explores the elements of gaming through the eyes of a master strategist who attempts to make the best of a puzzling situation.\n\n[Written by MAL Rewrite]"}, {"id": 427, "name_en": "Kaleido Star", "name_jp": "カレイドスター", "image_url": "https://cdn.myanimelist.net/images/anime/1230/98847.jpg", "genres": ["Comedy", "Drama", "Supernatural"], "themes": ["Performing Arts", "Showbiz"], "demographics": [], "studios": ["Gonzo"], "year": 2003, "episodes": 51, "source": "Original", "score": 7.9, "synopsis": "The Kaleido Stage is known throughout the world for captivating audiences with its amazing acrobatics, innovative routines, and extravagant costumes and sets. It is a place for guests to believe in magic, and Sora Naegino wants nothing more than to be a part of that magic—by becoming an acrobat for the famed circus herself.\n\nTo realize her dream, she travels from Japan to California to audition for a place in the group. However, Sora learns that she needs much more than her natural talent to bring joy to the faces in the crowd. She quickly discovers just how difficult it is to be a professional performer where the stakes—and the stunts—are higher and mistakes spell danger! To put on performances worthy of the Kaleido Stage, she will need to endure rigorous training, unconventional assignments, fierce competition, and the antics of a mischievous spirit named Fool.\n\nCan Sora reach new heights, make new friends, conquer her fears, and surpass her limits to become a Kaleido Star?\n\n[Written by MAL Rewrite]"}, {"id": 30279, "name_en": "Yuru Yuri San☆Hai!", "name_jp": "ゆるゆり さん☆ハイ！", "image_url": "https://cdn.myanimelist.net/images/anime/7/76667.jpg", "genres": ["Comedy", "Girls Love"], "themes": ["CGDCT", "Gag Humor", "School"], "demographics": [], "studios": ["TYO Animations"], "year": 2015, "episodes": 12, "source": "Manga", "score": 7.9, "synopsis": "The members of the Amusement Club are back with more of Kyouko Toshinou's antics, Chinatsu Yoshikawa's schemes, Yui Funami's retorts, and Akari Akaza's small social presence as they strive to make each day as fun as the last! Whether the activity is an arm wrestling contest or making pancakes together, the group of entertainment-seekers are sure to give it their all.\n\nThe club members are often joined by their friends in the Student Council, who know how to have a good time as well. While it can get a little chaotic when all eight girls get together, it sure doesn't stop them from meeting up and having fun!\n\n[Written by MAL Rewrite]"}, {"id": 7711, "name_en": "The Secret World of Arrietty", "name_jp": "借りぐらしのアリエッティ", "image_url": "https://cdn.myanimelist.net/images/anime/1974/116417.jpg", "genres": ["Award Winning", "Supernatural"], "themes": [], "demographics": [], "studios": ["Studio Ghibli"], "year": 2010, "episodes": 1, "source": "Novel", "score": 7.9, "synopsis": "While spending the summer at his aunt's house, the young but sickly Shou makes an amazing discovery: after following the house cat into the bushes, he gets a glimpse of a miniature girl about the size of his finger! Calling her kind \"Borrowers,\" as they survive on tiny bits of human possessions, the girl introduces herself as Arrietty. As he discovers that she lives in the house basement with her parents, Pod and Homily, Shou becomes imaginably excited at the idea of such unique neighbors.\n\nHowever, he fails to understand the adversities they face on a daily basis. In addition to keeping their existence hidden, they must also embark on perilous adventures into human territory, from the house to the outdoors, in order to make a living. Despite her parents' warnings, Arrietty befriends Shou, stirring up unexpected events that may change their lives forever.\n\nDelighting the eye and conquering the heart, the breath-taking story of a friendship transcending the tensions between two different human kinds begins.\n\n[Written by MAL Rewrite]"}, {"id": 6438, "name_en": "Detective Conan OVA 09: The Stranger in 10 Years...", "name_jp": "名探偵コナン～10年後の異邦人[ストレンジャー]～", "image_url": "https://cdn.myanimelist.net/images/anime/5/14746.jpg", "genres": ["Adventure", "Comedy", "Mystery"], "themes": ["Detective"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2009, "episodes": 1, "source": "Manga", "score": 7.9, "synopsis": "This OVA takes place ten years in the future and chronicles the possible events if Conan Edogawa and Ai Haibara do not take the antidote to APTX 4869 and grow up alongside their friends Genta, Mitsuhiko, and Ayumi, while still maintaining their detective endeavors."}, {"id": 22961, "name_en": "Date A Live II: Kurumi Star Festival", "name_jp": "デート・ア・ライブII 狂三スターフェスティバル", "image_url": "https://cdn.myanimelist.net/images/anime/1759/131412.jpg", "genres": ["Romance"], "themes": [], "demographics": [], "studios": ["Production IMS"], "year": 2014, "episodes": 1, "source": "Light novel", "score": 7.9, "synopsis": "On the day of the Tanabata festival, Shidou Itsuka happens to meet the eerie Kurumi Tokisaki, who proceeds to ask him out on a date. Although nervous, Shidou accepts the offer. The two spend their time visiting to a planetarium, buying food at the festival, and trying out a mock wedding ceremony. Meanwhile, Shirou's friends—Tooka Yatogami, Yoshino, and Kotori Itsuka—are looking for him, while also enjoying the festival themselves.\n \nAs Shidou spends the day with Kurumi, he begins to notice an unexpected softer side of her. He knows that Kurumi is infamously called the \"Worst Spirit,\" but the girl standing before him is a gentle soul. However, as the day draws to a close, Shirou finally begins to understand the truth behind it all.\n \n[Written by MAL Rewrite]"}, {"id": 4106, "name_en": "Trigun: Badlands Rumble", "name_jp": "トライガン", "image_url": "https://cdn.myanimelist.net/images/anime/1930/116400.jpg", "genres": ["Action", "Adventure", "Sci-Fi"], "themes": ["Adult Cast"], "demographics": ["Shounen"], "studios": ["Madhouse"], "year": 2010, "episodes": 1, "source": "Manga", "score": 7.9, "synopsis": "Vash the Stampede is a contradiction. He has a notorious reputation as \"The Humanoid Typhoon,\" laying anything he comes across to waste on the desolate planet of Gunsmoke. However, Vash is in fact very non-confrontational and kind-hearted, living by a code of pacifism.\n\nTwenty years ago, a high-profile bank heist went sour. The ringleader, Gasback Gallon Getaway, swore to get back at his backstabbing crew and the man who stopped him from killing them: Vash the Stampede. In the present day, the traitorous crew has been living the good life as successful entrepreneurs and politicians. Although two decades have passed, Gasback's bitterness has not waned as he aims to take them down one by one, by any means necessary. \n\nJust in time to foil Gasback's plot, Vash has arrived in Macca City. Teaming up with the mysterious Amelia Ann McFly, along with the insurance agents Milly Thompson and Meryl Stryfe, Vash is ready to rumble. \n\n[Written by MAL Rewrite]"}, {"id": 790, "name_en": "Ergo Proxy", "name_jp": "エルゴプラクシー", "image_url": "https://cdn.myanimelist.net/images/anime/1183/136187.jpg", "genres": ["Mystery", "Sci-Fi", "Suspense"], "themes": ["Adult Cast", "Psychological"], "demographics": [], "studios": ["Manglobe"], "year": 2006, "episodes": 23, "source": "Original", "score": 7.9, "synopsis": "Within the domed city of Romdo lies one of the last human civilizations on Earth. Thousands of years ago, a global ecological catastrophe doomed the planet; now, life outside these domes is virtually impossible. To expedite mankind's recovery, \"AutoReivs,\" humanoid-like robots, have been created to assist people in their day-to-day lives. However, AutoReivs have begun contracting an enigmatic disease called the \"Cogito Virus,\" which grants them self-awareness. Re-l Mayer, the  granddaughter of Romdo's ruler, is assigned to investigate this phenomenon alongside her AutoReiv partner, Iggy. But what begins as a routine investigation quickly spirals into a conspiracy as Re-l is confronted by humanity's darkest sins.\n\nElsewhere in Romdo, an AutoReiv specialist by the name of Vincent Law must also face his demons when surreal events begin occurring around him. Re-l, Iggy, Vincent, and the child AutoReiv named Pino will form an unlikely faction as they struggle to uncover Romdo's mysteries and discover the true purpose of the mythical beings called \"Proxies.\"\n\n[Written by MAL Rewrite]"}, {"id": 7338, "name_en": "Darker Than Black: Gemini of the Meteor OVAs", "name_jp": "Darker than BLACK -黒の契約者 外伝", "image_url": "https://cdn.myanimelist.net/images/anime/3/17469.jpg", "genres": ["Action", "Mystery"], "themes": ["Super Power"], "demographics": [], "studios": ["Bones"], "year": 2010, "episodes": 4, "source": "Original", "score": 7.9, "synopsis": "Fleeing from the consequences of his decision at the Hell's Gate, superpowered Contractor Hei and his companion Yin take refuge in a quiet inn, adopting the guise of a married couple in order to not draw suspicion. In an attempt to recover from recent events, Hei befriends the inn's other guests. He discovers that one of them is a fellow Contractor tasked with killing him. Their resulting encounter spells disaster for both Hei and Yin, who are forced to fight for their lives and grapple with the emotional wounds sustained in their previous life together.\n\n[Written by MAL Rewrite]"}, {"id": 32995, "name_en": "Yuri!!! On Ice", "name_jp": "ユーリ!!! on ICE", "image_url": "https://cdn.myanimelist.net/images/anime/6/81149.jpg", "genres": ["Award Winning", "Sports"], "themes": ["Performing Arts"], "demographics": [], "studios": ["MAPPA"], "year": 2016, "episodes": 12, "source": "Original", "score": 7.9, "synopsis": "Reeling from his crushing defeat at the Grand Prix Finale, Yuuri Katsuki, once Japan's most promising figure skater, returns to his family home to assess his options for the future. At age 23, Yuuri's window for success in skating is closing rapidly, and his love of pork cutlets and aptitude for gaining weight are not helping either.\n\nHowever, Yuuri finds himself in the spotlight when a video of him performing a routine previously executed by five-time world champion, Victor Nikiforov, suddenly goes viral. In fact, Victor himself abruptly appears at Yuuri's house and offers to be his mentor. As one of his biggest fans, Yuuri eagerly accepts, kicking off his journey to make it back onto the world stage. But the competition is fierce, as the rising star from Russia, Yuri Plisetsky, is relentlessly determined to defeat Yuuri and win back Victor's tutelage.\n\n[Written by MAL Rewrite]"}, {"id": 48580, "name_en": "The Case Study of Vanitas", "name_jp": "ヴァニタスの手記", "image_url": "https://cdn.myanimelist.net/images/anime/1401/118483.jpg", "genres": ["Action", "Fantasy", "Mystery"], "themes": ["Historical", "Urban Fantasy", "Vampire"], "demographics": ["Shounen"], "studios": ["Bones"], "year": 2021, "episodes": 12, "source": "Manga", "score": 7.9, "synopsis": "Scorned by others of his kind for being born under a blue moon, the vampire Vanitas grew afraid and desolate. According to legend, he created a cursed grimoire known as the \"Book of Vanitas,\" and it is said he would one day use it to bring retribution upon all vampires of the crimson moon.\n\nIn 19th century Paris, Noé Archiviste is searching for the fabled Book of Vanitas. Whilst traveling aboard an airship, he is saved from a vampire attack by an eccentric doctor who calls himself Vanitas and carries the very tome he seeks. Ironically, the self-proclaimed vampire specialist is a mere human who inherited both his name and the book from his master, the same Vanitas of legend. As the odd case of the Charlatan's Parade crops up, the doctor's ability to restore sanity to vampires by recovering their true name will prove most beneficial.\n\n[Written by MAL Rewrite]"}, {"id": 29803, "name_en": "Overlord", "name_jp": "オーバーロード", "image_url": "https://cdn.myanimelist.net/images/anime/1945/136600.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Isekai"], "demographics": [], "studios": ["Madhouse"], "year": 2015, "episodes": 13, "source": "Light novel", "score": 7.9, "synopsis": "The final hour of the popular virtual reality game Yggdrasil has come. However, Momonga, a powerful wizard and master of the dark guild Ainz Ooal Gown, decides to spend his last few moments in the game as the servers begin to shut down. To his surprise, despite the clock having struck midnight, Momonga is still fully conscious as his character and, moreover, the non-player characters appear to have developed personalities of their own!\n\nConfronted with this abnormal situation, Momonga commands his loyal servants to help him investigate and take control of this new world, with the hopes of figuring out what has caused this development and if there may be others in the same predicament.\n\n[Written by MAL Rewrite]"}, {"id": 37932, "name_en": "The King's Avatar 2", "name_jp": "全职高手2", "image_url": "https://cdn.myanimelist.net/images/anime/1573/109220.jpg", "genres": ["Action"], "themes": ["Video Game"], "demographics": [], "studios": ["Colored Pencil Animation"], "year": 2020, "episodes": 12, "source": "Web novel", "score": 7.9, "synopsis": "The \"Unspecialized\" character Lord Grim is infamous in the 10th server of the popular online game Glory. His reputation alone is enough to draw many curious players to his newly formed Guild Happy. Other competing guilds have enough to worry about with some of their own members abandoning them for Happy. However, they are also concerned by rumors that the person behind Lord Grim is really the retired professional gamer and \"Glory Textbook\" Ye Qiu, whom they have little chance of opposing. \n\nUnsure of the truth, the powerhouse guilds attempt to suppress Lord Grim's growing influence, harboring differing motives for doing so. But regardless of what obstacles he faces, Lord Grim is determined to break into the cross-server of Glory—the Heavenly Domain—where characters, including himself, can reach even greater levels. There, he hopes to round out the team of rookies who will fight alongside him in the Challenger League, which would be only their first step toward the coveted Glory Championship.\n\n[Written by MAL Rewrite]"}, {"id": 61274, "name_en": "Milky☆Subway: The Galactic Limited Express", "name_jp": "銀河特急 ミルキー☆サブウェイ", "image_url": "https://cdn.myanimelist.net/images/anime/1464/150712.jpg", "genres": ["Comedy", "Sci-Fi"], "themes": ["Space"], "demographics": [], "studios": ["Shin-Ei Animation"], "year": 2025, "episodes": 12, "source": "Original", "score": 7.89, "synopsis": "Arrested for reckless space driving, super human Chiharu and cyborg Makina are sentenced to clean the Milky☆Subway—an old interplanetary train.\n\nEasy enough, right? Wrong. When the train suddenly takes off, they are thrown into a high-speed space-train spectacle packed with chaos, no plans, and zero strategy.\n\nJust pure momentum!\n\n(Source: Official website, translated)"}, {"id": 57066, "name_en": "Is It Wrong to Try to Pick Up Girls in a Dungeon? V", "name_jp": "ダンジョンに出会いを求めるのは間違っているだろうかV 豊穣の女神篇", "image_url": "https://cdn.myanimelist.net/images/anime/1299/144738.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": [], "studios": ["J.C.Staff"], "year": 2024, "episodes": 15, "source": "Light novel", "score": 7.89, "synopsis": "Goddess Festival—A fruit festival that brings the labyrinth city of Orario to life. Goddesses symbolizing fertility are enshrined on the altar, and among them is the Goddess of Beauty. Bell Cranel, who has survived and returned to his daily life from the dead depths of the dungeon, is here and ready to enjoy the bustle of the Goddess Festival until he receives a letter from a girl at a small bar in a corner of Orario—\"To Mr. Bell, please go on a date with just the two of us at the upcoming Goddess Festival. From Syr.\" Syr's single-minded determination will drive both Bell and the labyrinth city crazy. Meanwhile, the Einherjar, the warriors who claim to be the \"strongest,\" are now suddenly on the move...\n\n(Source: HIDIVE)"}, {"id": 53287, "name_en": "Love Live! Superstar!! Season 3", "name_jp": "ラブライブ！スーパースター!!", "image_url": "https://cdn.myanimelist.net/images/anime/1489/144640.jpg", "genres": ["Slice of Life"], "themes": ["Idols (Female)", "Music", "School"], "demographics": [], "studios": ["Sunrise"], "year": 2024, "episodes": 12, "source": "Other", "score": 7.89, "synopsis": "As the Love Live! Competition draws to a close, Kanon Shibuya takes an offer to study abroad and bids farewell to her friends and fellow members of the school idol group Liella!. Although they will live on opposite sides of the world, both Kanon and the group remain motivated to continue their idol activities.\n\nUnbeknownst to Kanon, however, her plans are cut short by Wien Margarete's parents, who request that Kanon remain in Japan for another year so that their daughter can learn from her. While Kanon finds herself in awkward limbo, Wien cannot bring herself to join her once fierce rival Liella! and decides to start a group of her own.\n\nNot wanting Wien's determination to go to waste, Kanon joins the new group, recruiting her former group member's sister Tomari Onitsuka in the process. As the newfound unit strives to wash off Wien's controversial past, the new Liella! is also wrestling with the struggles of losing their irreplaceable center.\n\n[Written by MAL Rewrite]"}, {"id": 16395, "name_en": "SKET Dance: Imouto no Nayami ni Nayamu Ani ni Nayamu Imouto to Sono Nakama-tachi", "name_jp": "スケットダンス 妹の悩みに悩む兄に悩む妹とその仲間達", "image_url": "https://cdn.myanimelist.net/images/anime/1703/97779.jpg", "genres": ["Comedy"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["Tatsunoko Production"], "year": 2013, "episodes": 1, "source": "Manga", "score": 7.89, "synopsis": "An OVA of SKET Dance bundled with the 29th volume of the manga, covering chapters 198, 199, and 200.\n\nThis is a conclusion of some of the events shown in the last episode of the anime series."}, {"id": 9289, "name_en": "Hanasaku Iroha: Blossoms for Tomorrow", "name_jp": "花咲くいろは", "image_url": "https://cdn.myanimelist.net/images/anime/1491/117229.jpg", "genres": ["Drama"], "themes": ["Workplace"], "demographics": [], "studios": ["P.A. Works"], "year": 2011, "episodes": 26, "source": "Original", "score": 7.89, "synopsis": "Ohana Matsumae is an energetic and wild teenager residing in Tokyo with her carefree single mother. Abruptly, her mother decides to run away with her new boyfriend from debt collectors, forcing the young girl to fend for herself—as per her mother's \"rely only on yourself\" philosophy—in rural Japan, where her cold grandmother runs a small inn. Driven to adapt to the tranquil lifestyle of the countryside, Ohana experiences and deals with the challenges of working as a maid, as well as meeting and making friends with enthralling people at her new school and the inn.\n\n[Written by MAL Rewrite]"}, {"id": 6774, "name_en": "Welcome to Irabu's Office", "name_jp": "空中ブランコ", "image_url": "https://cdn.myanimelist.net/images/anime/3/18386.jpg", "genres": ["Avant Garde", "Drama"], "themes": ["Adult Cast", "Medical", "Psychological"], "demographics": [], "studios": ["Toei Animation"], "year": 2009, "episodes": 11, "source": "Novel", "score": 7.89, "synopsis": "The world of psychology is far from strange to the unusual Dr. Ichirou Irabu, a resident psychiatrist of Irabu General Hospital. He and his charming nurse Mayumi run through several patients, each suffering from a mental illness that harms their everyday life. \n\nPatients should be wary of the seductive Mayumi, with her spellbinding looks and devilishly short pink nurse uniform. On the other hand, the doctor seems to have three separate personalities: a child with an oversized lab coat; an intelligent, youthful man with feminine traits; and a selfish, outgoing green bear. While curing his patients in questionable ways, Dr. Irabu often tries to gain something from them outside of his profession—and in doing so, occasionally forgets his role as a doctor.\n\nAs each patient struggles to face the nature of their distress, an obvious yet invisible thread ties their paths together.\n\n[Written by MAL Rewrite]"}, {"id": 20057, "name_en": "Space Dandy", "name_jp": "スペース☆ダンディ", "image_url": "https://cdn.myanimelist.net/images/anime/4/56611.jpg", "genres": ["Comedy", "Sci-Fi"], "themes": ["Adult Cast", "Space"], "demographics": [], "studios": ["Bones"], "year": 2014, "episodes": 13, "source": "Original", "score": 7.89, "synopsis": "Dandy is a groovy, pompadour-wearing man who explores the stars in search of strange aliens for money, desiring to visit his favorite place: BooBies, the best diner in all the cosmos. Traversing the galaxies in his trusty ship, the Aloha Oe, Dandy is accompanied by his two equally unique friends: QT, the vacuum cleaner robot; and Meow, an alien with a cat-like appearance. \n\nAlthough exciting, Dandy's job is never simple, and each new day promises a completely different, thrilling adventure. Together, Dandy and his crew get dragged into perilous journeys, misadventures in romance, and out of this world scenarios—all whilst steering clear of the evil scientist, Dr. Gel of the Gogol Empire.\n\n[Written by MAL Rewrite]"}, {"id": 41462, "name_en": "BanG Dream! Film Live 2nd Stage", "name_jp": "BanG Dream! FILM LIVE 2nd Stage", "image_url": "https://cdn.myanimelist.net/images/anime/1229/117735.jpg", "genres": [], "themes": ["Music"], "demographics": [], "studios": ["SANZIGEN"], "year": 2021, "episodes": 1, "source": "Mixed media", "score": 7.89, "synopsis": "The second animated live movie for the BanG Dream! franchise and the sequel to BanG Dream! FILM LIVE.\n\n(Source:BanG Dream! Wikia)"}, {"id": 3228, "name_en": "Zoku Sayonara Zetsubou Sensei", "name_jp": "俗・さよなら絶望先生", "image_url": "https://cdn.myanimelist.net/images/anime/13/75517.jpg", "genres": ["Comedy"], "themes": ["Gag Humor", "Parody", "School"], "demographics": ["Shounen"], "studios": ["Shaft"], "year": 2008, "episodes": 13, "source": "Manga", "score": 7.89, "synopsis": "More crazy antics abound as Nozomu Itoshiki (a.k.a Zetsubou-sensei), who is the worlds most negative person, tries to teach class 2-F about how life is filled with despair and darkness. A harder task than it first may seem, with his students being far from ordinary themselves, including the likes of the most positive girl he has ever met, his very own stalker, a foreign exchange student with split personalities and a perfectionist acting class president, due to the fact the real class president can't be seen by anyone, among others. \n\n(Source: ANN)"}, {"id": 27947, "name_en": "Lupin the Third Part 4", "name_jp": "ルパン三世", "image_url": "https://cdn.myanimelist.net/images/anime/9/75949.jpg", "genres": ["Action", "Adventure", "Comedy", "Mystery"], "themes": ["Adult Cast"], "demographics": ["Seinen"], "studios": ["Telecom Animation Film"], "year": 2015, "episodes": 24, "source": "Manga", "score": 7.89, "synopsis": "Lupin is the main character in this series which takes place in Italy and San Marino.\n\nReflecting its setting, the anime debuted in Italy before it aired in Japan. Lupin III fans will note that the titular thief now dons a blue coat, following his earlier appearances in green (first series and some OVAs), red (the second series as well as most films and television specials) and pink (third series).\n\n(Source: Crunchyroll)"}, {"id": 18397, "name_en": "Attack on Titan OAD", "name_jp": "進撃の巨人ＯＡＤ", "image_url": "https://cdn.myanimelist.net/images/anime/9/59221.jpg", "genres": ["Action", "Drama", "Suspense"], "themes": ["Gore", "Survival"], "demographics": ["Shounen"], "studios": ["Wit Studio"], "year": 2013, "episodes": 3, "source": "Manga", "score": 7.89, "synopsis": "Ilse no Techou: Aru Chousa Heidanin no Shuki\nDuring the Survey Corps' 49th recon mission, Hange Zoë is determined to capture a titan specimen. Despite not receiving clearance from Commander Erwin Smith, when a titan is spotted in nearby forestland, Hange rides out alone to meet it. Recklessly luring it out, she asks the titan numerous questions and puts her life on the line for the sake of her research. However, the behavior of this particular titan is far from normal. It quickly turns back and enters the wood once again, leading Hange to somewhere specific. What Hange finds is the legacy of former scout Ilse Langnar. In spite of her death, she provides a valuable piece of information that may serve to turn the tide for titan research—a diary documenting her last moments.\n\nTotsuzen no Raihousha: Sainamareru Seishun no Noroi\nJean Kirstein would do anything to escape his boring home life and overbearing mother. After enlisting in the military, it became his ultimate goal to join the Military Police regiment and live out in peace and luxury. However, during his time with the 104th Training Corps, things never really go the way Jean wants them to. Eventually, the stolen glory and condescending banter of his comrades become too much—and Jean challenges fellow cadet Sasha Blouse to a battle, in order to determine which of them is strongest—but who will come out on top?\n\nKonnan\nThe 104th Training Corps' most recent mission is a trek on horseback into the forest. Although a test of their ability to stay alert even in non-threatening situations, the task is boring and can lead to in-fighting. This is especially true for one of the groups, lead by Marco Bott. Some want to stay true to the mission they have been tasked with, and the rest would rather slack off, occupying themselves with more exciting activities. But when trouble strikes, they are completely unprepared.\n\n[Written by MAL Rewrite]"}, {"id": 35848, "name_en": "Promare", "name_jp": "PROMARE（プロメア）", "image_url": "https://cdn.myanimelist.net/images/anime/1008/101845.jpg", "genres": ["Sci-Fi"], "themes": ["Mecha", "Super Power"], "demographics": [], "studios": ["Trigger"], "year": 2019, "episodes": 1, "source": "Original", "score": 7.89, "synopsis": "Thirty years ago, a new race of flame-wielding mutants suddenly appeared, destroying a large portion of humanity. These so-called “Burnish” have continued to appear at random, leaving a trail of death and destruction in their wake.\n\nThe autonomous republic of Promepolis is a thriving nation thanks to the incredible efforts of their leader, Kray Foresight, against the Burnish. A team of firefighters known as the Burning Rescue is tasked with stopping these horrifying monsters, using the most performant technology available thanks to their incredible mechanic Lucia Fex. Galo Thymos is an energetic young man, who considers Foresight his hero for saving his life and is the rescue team's most recent recruit. \n\nA terrorist group calling themselves Mad Burnish has been causing havoc all over the nation. After an encounter with Mad Burnish leader Lio Fotia, Galo sets out on his fated journey to find the truth about these mutants, ultimately leading him to question everything he previously held to be true.\n\n[Written by MAL Rewrite]"}, {"id": 28297, "name_en": "My Love Story!!", "name_jp": "俺物語!!", "image_url": "https://cdn.myanimelist.net/images/anime/13/69455.jpg", "genres": ["Comedy", "Romance"], "themes": [], "demographics": ["Shoujo"], "studios": ["Madhouse"], "year": 2015, "episodes": 24, "source": "Manga", "score": 7.89, "synopsis": "With his muscular build and tall stature, Takeo Gouda is no ordinary high school freshman. However, behind Takeo's intimidating appearance lies a pure heart of gold, and he is considered a hero by his male peers for his courage and chivalry. \n\nUnfortunately for Takeo, his appearance does not bode well for his love life. As if his looks were not already enough to scare the opposite sex away, Takeo's cool, handsome best friend and constant companion Makoto Sunakawa easily, and unintentionally, steals the hearts of the female students—including every girl Takeo has ever liked.\n\nOne day, when Takeo saves cute Rinko Yamato from being molested, he falls in love with her instantly. Unfortunately, he suspects that she might be interested in Sunakawa. Despite his romantic feelings for Yamato continuing to bloom, Takeo decides to act as her cupid, even as he yearns for his own love story.\n\n[Written by MAL Rewrite]"}, {"id": 47904, "name_en": "Hololive Alternative", "name_jp": "ホロライブ・オルタナティブ", "image_url": "https://cdn.myanimelist.net/images/anime/1692/112088.jpg", "genres": [], "themes": ["Music"], "demographics": [], "studios": ["Hololive Production"], "year": 2021, "episodes": 2, "source": "Mixed media", "score": 7.89, "synopsis": "Promotional videos for Hololive Alternative multimedia project."}, {"id": 12815, "name_en": "Polar Bear Cafe", "name_jp": "しろくまカフェ", "image_url": "https://cdn.myanimelist.net/images/anime/6/75649.jpg", "genres": ["Slice of Life"], "themes": ["Anthropomorphic", "Iyashikei", "Workplace"], "demographics": ["Josei"], "studios": ["Studio Pierrot"], "year": 2012, "episodes": 50, "source": "Manga", "score": 7.89, "synopsis": "Situated near the local zoo and owned by the charismatic polar bear Shirokuma, Shirokuma Cafe is a popular spot for animals and humans alike, allowing them to sit back and relax after a hard day of work. Whether it's a cold beverage or the latest item on his menu, Shirokuma finds joy in being able to serve his customers, often striking up conversations about various subjects.\n\nTogether with the sarcastic Penguin and the clumsy Panda, they form an odd trio who get themselves caught up in all sorts of misadventures with their other friends such as Grizzly, a bar owner, and Sasako, a human who works at the cafe. From dealing with unrequited love, outdoor camping trips, karaoke sessions, and even the secret to brewing delicious coffee, there's always something bound to be happening in Shirokuma Cafe!\n\n[Written by MAL Rewrite]"}, {"id": 14075, "name_en": "Blast of Tempest", "name_jp": "絶園のテンペスト", "image_url": "https://cdn.myanimelist.net/images/anime/7/42453.jpg", "genres": ["Action", "Drama", "Fantasy", "Mystery"], "themes": ["Psychological", "Urban Fantasy"], "demographics": ["Shounen"], "studios": ["Bones"], "year": 2012, "episodes": 24, "source": "Manga", "score": 7.89, "synopsis": "Yoshino Takigawa, an ordinary teenager, is secretly dating his best friend Mahiro Fuwa's younger sister, Aika. But when Aika mysteriously dies, Mahiro disappears, vowing to find the one responsible and make them pay for murdering his beloved sister. Yoshino continues his life as usual and has not heard from Mahiro in a month—until he is confronted by a strange girl who holds him at gunpoint, and his best friend arrives in the nick of time to save him.\n\nYoshino learns that Mahiro has enlisted the help of a witch named Hakaze Kusaribe to find Aika's killer. However, the witch has been banished to a deserted island due to infighting within her clan. Hakaze's brother, Samon, selfishly desires to make use of the Tree of Exodus' power, in spite of both his sister's opposition and the impending peril to the world. With Hakaze out of the picture, it is now up to Yoshino and Mahiro to help her save the world, all while inching ever closer to the truth behind Aika's death.\n\n[Written by MAL Rewrite]"}, {"id": 58930, "name_en": "Genshin Impact: The Road Not Taken", "name_jp": "《原神》「未行之路」", "image_url": "https://cdn.myanimelist.net/images/anime/1613/143280.jpg", "genres": ["Drama", "Fantasy"], "themes": ["Isekai", "Music"], "demographics": [], "studios": [], "year": 2024, "episodes": 1, "source": "Game", "score": 7.88, "synopsis": "Animated short film about the twins Aether and Lumine from Genshin Impact."}, {"id": 57183, "name_en": "Martial Universe 4", "name_jp": "武动乾坤 第四季", "image_url": "https://cdn.myanimelist.net/images/anime/1323/153745.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["DC Impression Vision"], "year": 2023, "episodes": 12, "source": "Web novel", "score": 7.88, "synopsis": "In the world of the Great Yan Empire, strength commands respect. Lin Dong, from the banished Lin Clan, witnesses his family's downfall at the hands of Lin Langtian, a prodigy of the clan. Driven by revenge, Lin Dong seeks to avenge his family's suffering. His journey takes an unexpected turn when he discovers a mysterious talisman, unraveling a destiny beyond his imagination.\n\n(Source: WeTV)"}, {"id": 53199, "name_en": "Mobile Suit Gundam: The Witch from Mercury Season 2", "name_jp": "機動戦士ガンダム 水星の魔女", "image_url": "https://cdn.myanimelist.net/images/anime/1023/134768.jpg", "genres": ["Sci-Fi"], "themes": ["Mecha", "School", "Space"], "demographics": [], "studios": ["Sunrise"], "year": 2023, "episodes": 12, "source": "Original", "score": 7.88, "synopsis": "After surviving a brutal terrorist attack on Plant Quetta, Suletta Mercury returns to dueling at the Asticassia School of Technology, while Miorine Rembran sits at her injured father's side, waiting to see if he will recover. As the residents of Earth House are preoccupied with their respective troubles, two members of Dawn of Fold—the perpetrators of the attack—secretly infiltrate the school, putting Suletta and her friends at risk.\n\nAt the same time, Prospera, Suletta's mother, approaches Miorine to propose the continuation of \"Quiet Zero\"—a project headed by Miorine's father that can allegedly end all war. Prospera seems to be another political figure with hidden, opposing motives, and whether she truly has Suletta's well-being at heart remains to be seen.\n\n[Written by MAL Rewrite]"}, {"id": 34438, "name_en": "Code Geass: Lelouch of the Rebellion I - Initiation", "name_jp": "コードギアス 反逆のルルーシュⅠ 興道", "image_url": "https://cdn.myanimelist.net/images/anime/6/87709.jpg", "genres": ["Sci-Fi"], "themes": ["Mecha", "Military", "School", "Super Power"], "demographics": [], "studios": ["Sunrise"], "year": 2017, "episodes": 1, "source": "Original", "score": 7.88, "synopsis": "The Holy Britannian Empire is a powerful nation that uses its military strength to expand its rule, and the small island nation of Japan is yet another victim. During a worldwide conflict in 2010 known as the Second Pacific War, the once-proud country was reduced to rubble and mockingly renamed to Area 11. \n\nHowever, one student plans to free Japan from Britannia's control through an extraordinary rebellion. His name is Lelouch Lamperouge, and he is fueled with hatred for the Empire, plotting to wipe its accursed name off the face of the planet. Lelouch's malicious plans are unexpectedly accelerated upon meeting the mysterious woman C.C., who grants him a supernatural ability known as Geass that makes anyone Lelouch commands unable to defy his will. \n\nNow armed with a devastating power, Lelouch assumes the persona of Zero, a revolutionary figure who carries the hopes and dreams of the oppressed around the world. He vows to restore the glorious nation of Japan and deliver divine retribution to Britannia.\n\n[Written by MAL Rewrite]"}, {"id": 13331, "name_en": "Jormungand Season 2: Perfect Order", "name_jp": "ヨルムンガンド PERFECT ORDER", "image_url": "https://cdn.myanimelist.net/images/anime/4/73278.jpg", "genres": ["Action", "Adventure"], "themes": ["Adult Cast", "Military", "Organized Crime"], "demographics": ["Seinen"], "studios": ["White Fox"], "year": 2012, "episodes": 12, "source": "Manga", "score": 7.88, "synopsis": "Still in pursuit of her ambitious goal, ingenious arms dealer Koko Hekmatyar, inexpressive child soldier Jonathan “Jonah” Mar, and the rest of their squad continue their mercenary activities. From professional assassins to private militaries, the group's work constantly puts them in the face of danger. \n\nBut internal conflicts soon arise after Renato \"R\" Socci, one of Koko's bodyguards, is revealed to be an undercover agent for \"Operation Undershaft\"—a plan devised by the CIA to infiltrate HCLI and exploit Koko as a tool. Shocked by his betrayal, Koko's leadership is needed now more than ever to rally her squad and rebuild their foundation of trust before they are torn apart.\n\nJormungand: Perfect Order follows Koko and the rest of her crew as they take on persistent adversaries, overcome internal struggles, and make Koko's vision of world peace a reality—where everything is in perfect order.\n\n[Written by MAL Rewrite]"}, {"id": 30952, "name_en": "Initial D Legend 3: Dream", "name_jp": "新劇場版　頭文字［イニシャル］D Legend3 -夢現-", "image_url": "https://cdn.myanimelist.net/images/anime/9/76759.jpg", "genres": [], "themes": ["Racing"], "demographics": ["Seinen"], "studios": ["SANZIGEN", "LIDENFILMS"], "year": 2016, "episodes": 1, "source": "Manga", "score": 7.88, "synopsis": "After spending his summer racing against some of the best racers in Gunma, Takumi Fujiwara receives a formal challenge from the leader of the Akagi Red Suns, Ryousuke Takahashi. Despite his initial reluctance to get into the hobby, Takumi cannot help but feel excited to go up against the former \"White Comet of Akagi.\" In preparation for the battle, Ryousuke downgrades his car, putting his pride and racing career at stake. \n\nWith the title of the fastest racer in all of Gunma on the line, will Takumi and his panda-colored Toyota AE86 be able to defeat Ryousuke's Mazda RX-7 FC? \n\n[Written by MAL Rewrite]"}, {"id": 37786, "name_en": "Bloom Into You", "name_jp": "やがて君になる", "image_url": "https://cdn.myanimelist.net/images/anime/1783/96153.jpg", "genres": ["Drama", "Girls Love"], "themes": ["School"], "demographics": [], "studios": ["TROYCA"], "year": 2018, "episodes": 13, "source": "Manga", "score": 7.88, "synopsis": "Yuu Koito has always been entranced with romantic shoujo manga and the lyrics of love songs. She patiently waits for the wings of love to sprout and send her heart aflutter on the day that she finally receives a confession. Yet, when her classmate from junior high declares his love for her during their graduation, she feels unexpectedly hollow. The realization hits her: she understands romance as a concept, but she is incapable of experiencing the feeling first-hand.\n \nNow, having enrolled in high school, Yuu, disconcerted and dispirited, is still ruminating over how to respond to her suitor. There, she happens upon the seemingly flawless student council president, Touko Nanami, maturely rejecting a confession of her own. Stirred by Touko's elegant manner, Yuu approaches her for advice, only to be bewildered when the president confesses to her! Yuu quickly finds herself in the palm of Touko's hand, and unknowingly sets herself on a path to find the emotion which has long eluded her.\n\n[Written by MAL Rewrite]"}, {"id": 36028, "name_en": "Golden Kamuy", "name_jp": "ゴールデンカムイ", "image_url": "https://cdn.myanimelist.net/images/anime/1145/90880.jpg", "genres": ["Action", "Adventure"], "themes": ["Adult Cast", "Historical", "Military"], "demographics": ["Seinen"], "studios": ["Geno Studio"], "year": 2018, "episodes": 12, "source": "Manga", "score": 7.88, "synopsis": "In early 1900s Hokkaido after the Russo-Japanese war, Saichi Sugimoto tirelessly pans for gold. Nicknamed \"Sugimoto the Immortal\" for his death-defying acts in battle, the ex-soldier seeks fortune in order to fulfill a promise made to his best friend before he was killed in action: to support his family, especially his widow who needs treatment overseas for her deteriorating eyesight. One day, a drunken companion tells Sugimoto the tale of a man who murdered a group of Ainu and stole a fortune in gold. Before his arrest by the police, he hid the gold somewhere in Hokkaido. The only clue to its location is the coded map he tattooed on the bodies of his cellmates in exchange for a share of the treasure, should they manage to escape and find it.\n\nSugimoto does not think much of the tale until he discovers the drunken man's corpse bearing the same tattoos described in the story. But before he can collect his thoughts, a grizzly bear—the cause of the man's demise—approaches Sugimoto, intent on finishing her meal. He is saved by a young Ainu girl named Asirpa, whose father happened to be one of the murdered Ainu. With Asirpa's hunting skills and Sugimoto's survival instincts, the pair agree to join forces and find the hidden treasure—one to get back what was rightfully her people's, and the other to fulfill his friend's dying wish.\n\n[Written by MAL Rewrite]"}, {"id": 38436, "name_en": "Fights Break Sphere 3", "name_jp": "斗破苍穹 第3季", "image_url": "https://cdn.myanimelist.net/images/anime/1643/102418.jpg", "genres": ["Action", "Adventure", "Fantasy", "Romance"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Motion Magic"], "year": 2019, "episodes": 12, "source": "Web novel", "score": 7.88, "synopsis": "To obtain the Magic Flame, Xiao Yan and his teacher went to a desert full of snakes, which angered the Master of the Snake Tribe, Yue Mei. When Xiao Yan was on the very edge of death, he was rescued by the Master of Fight, whose face was hidden by a mask. As it turned out, he was also looking for the Magic Flame. The Snake Tribe refused to hand over the Magic Flame to the Master of Fight, but when he attacked the Queen of the tribe, it turned out that it was just a shell, and the real queen is preparing to absorb the Magic Flame in order to become the God of Fight. The Snake Tribe attacked those who dared to raise their hand against their queen, while Xiao Yan was able to find traces of the Magic Flame in the midst of the battle and hunt down the Queen..."}, {"id": 35075, "name_en": "Hozuki's Coolheadedness 2", "name_jp": "鬼灯の冷徹 第弐期", "image_url": "https://cdn.myanimelist.net/images/anime/8/88280.jpg", "genres": ["Comedy", "Fantasy"], "themes": [], "demographics": ["Seinen"], "studios": ["Studio Deen"], "year": 2017, "episodes": 13, "source": "Manga", "score": 7.88, "synopsis": "Just as in this life, the afterlife needs a calm troubleshooter to deal with the bureaucratic headaches that come from keeping things in order. Enter Hozuki: a cool and collected demon who’s badly in need of a vacation.\n\n(Source: HIDIVE)"}, {"id": 39164, "name_en": "Dramaturgy", "name_jp": "ドラマツルギー", "image_url": "https://cdn.myanimelist.net/images/anime/1876/110194.jpg", "genres": [], "themes": ["Music"], "demographics": [], "studios": [], "year": 2017, "episodes": 1, "source": "Original", "score": 7.88, "synopsis": "An original song by Eve, sung by Hatsune Miku, animated by Mah. Eve's self-cover uses the same animated footage and was released on October 11, 2017. Eve included his self-cover MV on his own self-made record label harapeco records's CD+DVD release on December 13, 2017 as a part of his \"Bunka\" album."}, {"id": 3572, "name_en": "Macross Frontier", "name_jp": "マクロスF（フロンティア）", "image_url": "https://cdn.myanimelist.net/images/anime/9/15648.jpg", "genres": ["Award Winning", "Romance", "Sci-Fi"], "themes": ["Mecha", "Military", "Music", "Space"], "demographics": [], "studios": ["Satelight"], "year": 2008, "episodes": 25, "source": "Original", "score": 7.88, "synopsis": "Following a catastrophic war against a race of giants known as the Zentradi, humanity has escaped towards the center of the galaxy aboard a fleet of colonial vessels called the Macross Frontier. As the extraterrestrial threat is left further and further behind, life on Macross Frontier proceeds as usual.\n\nIn the year 2059, a young mecha pilot trainee named Alto Saotome and his colleagues are preparing to perform an accompanying routine for the famous singer Sheryl Nome, who has come to Macross Frontier for a concert. During the performance, a biomechanical alien species known as the Vajra make a sudden appearance, breaking through the defensive perimeter surrounding the vessel and crash-landing near the concert venue, plunging the entire city into chaos. As the concertgoers evacuate, a young girl named Ranka Lee is left behind and gets targeted by the Vajra, but she is saved at the last minute by Alto. Following these events, the Strategic Military Services program notes Alto's skill in battle, resulting in his recruitment to combat the new alien threat.\n\n[Written by MAL Rewrite]"}, {"id": 31098, "name_en": "Ushio & Tora (2016)", "name_jp": "うしおととら", "image_url": "https://cdn.myanimelist.net/images/anime/1092/117404.jpg", "genres": ["Action", "Adventure", "Supernatural"], "themes": ["Mythology"], "demographics": ["Shounen"], "studios": ["MAPPA", "Studio VOLN"], "year": 2016, "episodes": 13, "source": "Manga", "score": 7.88, "synopsis": "Continuation of Ushio to Tora TV series."}, {"id": 29893, "name_en": "Aria the Avvenire", "name_jp": "ARIA The AVVENIRE", "image_url": "https://cdn.myanimelist.net/images/anime/10/74338.jpg", "genres": ["Sci-Fi", "Slice of Life"], "themes": ["Iyashikei"], "demographics": ["Shounen"], "studios": ["TYO Animations"], "year": 2015, "episodes": 3, "source": "Manga", "score": 7.88, "synopsis": "Now that Akari Mizunashi is a Prima Undine and head of the Aria Company with her own apprentice, Ai Aino, she cannot help but reminisce about her time as a Single. She has new responsibilities and much less time on her hands, but these changes bring with them new forms of miracles that Neo Venezia can give. Growing up into new roles is hard, but Akari can always count on the friends she has made on Aqua in times of need.\n\n[Written by MAL Rewrite]"}, {"id": 38201, "name_en": "Mr. Osomatsu the Movie", "name_jp": "えいがのおそ松さん", "image_url": "https://cdn.myanimelist.net/images/anime/1492/113860.jpg", "genres": ["Comedy"], "themes": ["Gag Humor", "Parody"], "demographics": [], "studios": ["Studio Pierrot"], "year": 2019, "episodes": 1, "source": "Manga", "score": 7.88, "synopsis": "After an embarrassing high school class reunion with their successful classmates, who have \"proper\" jobs, relationships, and even children, the Matsuno sextuplets return home to sulk about their status as NEETs. They spend the night drinking away their sorrows, eventually falling unconscious, until they awaken in a strange world of memories, centered around one of the brothers' biggest high school regret.\n\nAs the sextuplets navigate this memory world, they encounter their embarrassing past selves from high school, as well as those of their friends. All the while, they try to uncover whose regret was great enough to trap them in this strange world, so that they can make their way back to reality. However, they're running out of time; this world of memories is unstable, and the sextuplets may lose their chance to make things right if they're too slow...."}, {"id": 2952, "name_en": "Final Fantasy VII: Advent Children Complete", "name_jp": "ファイナルファンタジーVII アドベントチルドレンコンプリート", "image_url": "https://cdn.myanimelist.net/images/anime/1065/94641.jpg", "genres": ["Action", "Fantasy"], "themes": [], "demographics": [], "studios": ["Square Enix Visual Works"], "year": 2009, "episodes": 1, "source": "Game", "score": 7.88, "synopsis": "Two years after saving the world from a cataclysmic event, the members of the activist organization Avalanche have gone their separate ways. Tifa Lockhart opens a new bar, and her childhood friend Cloud Strife starts up a motorcycle-based courier service. Although most of their former allies have fallen out of touch, Cloud and Tifa live in fragile peace with their two young wards Marlene and Denzel.\n\nTheir life together begins to crumble as a contagious plague and overwhelming grief pull Cloud away from his newfound family. While distancing himself through his work, Cloud is attacked by three silver-haired figures in black uniforms who bear a passing resemblance to his bitter rival, Sephiroth. As the antagonistic trio stalks Cloud and brings harm to his loved ones, the former mercenary struggles against his own demons while fighting to protect his family and prevent the will of Sephiroth from returning to the planet.\n\n[Written by MAL Rewrite]"}, {"id": 322, "name_en": "Paradise Kiss", "name_jp": "Paradise kiss", "image_url": "https://cdn.myanimelist.net/images/anime/7/80636.jpg", "genres": ["Drama", "Romance"], "themes": ["School", "Visual Arts"], "demographics": ["Josei"], "studios": ["Madhouse"], "year": 2005, "episodes": 12, "source": "Manga", "score": 7.88, "synopsis": "On her way home from school, Yukari Hayasaka is approached by a weird-looking guy who starts looking at her body intently. He's got blond spiky hair, a spiked choker, and multiple piercings on his ears and face. She wants nothing to do with him, and runs away, only to bump into a very tall and beautiful purple-haired woman with a flower pattern around her eye. Yukari faints from shock and wakes up later in a strange place called the Atelier. It turns out that these strangers are fashion designers who attend the most famous art school around, Yazawa Art Academy, and their group wants Yukari to model for their brand in Yazawa Academy's upcoming show.\n\nYukari turns down their offer and escapes the Atelier, but unknowingly leaves her school ID behind. George Koizumi, the head designer, later sees it and immediately knows she would be the perfect model for them and will not stop until he gets what he wants—and he wants her. Yukari had never considered something as frivolous as modeling before, but could life among these eccentric designers actually prove to be fun? Or will Yukari lose herself in this world of art and passion?\n\n[Written by MAL Rewrite]"}, {"id": 114, "name_en": "Cromartie High School", "name_jp": "魁!! クロマティ高校", "image_url": "https://cdn.myanimelist.net/images/anime/1782/130913.jpg", "genres": ["Comedy"], "themes": ["Delinquents", "Gag Humor", "Parody", "School"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2003, "episodes": 26, "source": "Manga", "score": 7.88, "synopsis": "Takashi Kamiyama is your typical mild-mannered high school student—polite, aloof, and pacifistic, with a slightly above-average IQ. But would your average high school student really enroll himself at the infamous Cromartie High School, known as a breeding ground for the toughest delinquents out there?\n\nApparently so, as that is exactly what Takashi does, though for reasons he'd rather leave unmentioned. However, one thing is for sure: the \"hard-boiled rabbit in a den full of hungry lions\" is never going to have another dull day. And how could he, now that he's surrounded by mohawked punks, obnoxious robots, and... gorillas? And was that Freddie Mercury riding a horse down the corridor? Follow Takashi as he earnestly dedicates his new high school life to better his school's reputation while his classmates are hellbent on wreaking havoc.\n\n[Written by MAL Rewrite]"}, {"id": 24, "name_en": "School Rumble", "name_jp": "スクールランブル", "image_url": "https://cdn.myanimelist.net/images/anime/1465/142014.jpg", "genres": ["Comedy", "Romance"], "themes": ["Gag Humor", "Love Polygon", "School"], "demographics": ["Shounen"], "studios": ["Studio Comet"], "year": 2004, "episodes": 26, "source": "Manga", "score": 7.88, "synopsis": "Just the words \"I love you,\" and everything changes—such is the nature of the bittersweet trials of high school romance. Tenma Tsukamoto, a second year, is on a quest to confess her feelings to the boy she likes. Kenji Harima, a delinquent with a sizable reputation, is in a similar situation, as he cannot properly convey his feelings to the one he loves. Between school, friends, rivalries, and hobbies, these two will find that high school romance is no walk in the park, especially as misunderstandings further complicate their plight.\n\nSchool Rumble is a high-octane romantic comedy full of relatable situations, as Tenma and Kenji both try to win the hearts of those they desire.\n\n[Written by MAL Rewrite]"}, {"id": 31339, "name_en": "Drifters", "name_jp": "DRIFTERS", "image_url": "https://cdn.myanimelist.net/images/anime/10/80271.jpg", "genres": ["Action", "Adventure", "Comedy", "Fantasy"], "themes": ["Adult Cast", "Gore", "Historical", "Isekai", "Military", "Samurai"], "demographics": ["Seinen"], "studios": ["Hoods Entertainment"], "year": 2016, "episodes": 12, "source": "Manga", "score": 7.88, "synopsis": "At the Battle of Sekigahara in 1600, Toyohisa Shimazu is the rearguard for his retreating troops, and is critically wounded when he suddenly finds himself in a modern, gleaming white hallway. Faced with only a stoic man named Murasaki and hundreds of doors on both sides, Toyohisa is pulled into the nearest door and into a world completely unlike his own.\n\nThe strange land is populated by all manner of fantastical creatures, as well as warriors from different eras of Toyohisa's world who were thought to be dead. Quickly befriending the infamous warlord Nobunaga Oda and the ancient archer Yoichi Suketaka Nasu, Toyohisa learns of the political unrest tearing through the continent. Furthermore, they have been summoned as \"Drifters\" to fight against the \"Ends,\" people who are responsible for the creation of the Orte Empire and are trying to annihilate the Drifters. As the Ends grow more powerful, so does the Empire's persecution of elves and other demihumans. It is up to Toyohisa and his group of unconventional heroes to battle in a brand-new world war to help the Empire's subjects, while challenging the Ends protecting the land to claim it for themselves.\n\n[Written by MAL Rewrite]"}, {"id": 60787, "name_en": "Stellar Transformation 6th Season", "name_jp": "星辰变 第6季", "image_url": "https://cdn.myanimelist.net/images/anime/1662/150035.jpg", "genres": ["Action", "Adventure", "Fantasy", "Romance"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Foch Film"], "year": 2025, "episodes": 28, "source": "Web novel", "score": 7.87, "synopsis": "The story revolves around Qin Yu and his three brothers after ascending to the God Realm. As Qin Yu gradually learns the rules of the God Realm, he aspires to become the strongest craftsman god in order to marry Jiang Li, a member of one of the eight great families of the God Realm, whose status is vastly different from his own. Starting with eliminating bandits, he rises to become a high-level god, gaining the favor of the Holy Emperor and becoming a rising star… step by step, he finally reaches a position where he can openly propose to Jiang Li. \n\nHowever, faced with numerous suitors, the Jiang family, unable to choose, decides to hold a marriage contest for Jiang Li. Whoever can present three gifts that surpass the other competitors will win Jiang Li. Qin Yu endures hardships to forge an unprecedented first-class Primordial Spiritual Treasure as his final betrothal gift. He believes he has everything under control, but at the last moment, the Jiang family disregards fairness and declares Zhou Xian the winner. Qin Yu erupts in anger…\n\n(Source: Tencent, translated)"}, {"id": 60334, "name_en": "Anne Shirley", "name_jp": "アン・シャーリー", "image_url": "https://cdn.myanimelist.net/images/anime/1564/150049.jpg", "genres": ["Drama"], "themes": ["Historical"], "demographics": [], "studios": ["The Answer Studio"], "year": 2025, "episodes": 24, "source": "Novel", "score": 7.87, "synopsis": "Eleven-year-old orphan Anne Shirley has spent her life moving from one family to another, always hoping to find a place where she truly belongs. When she is sent to live with elderly siblings Marilla and Matthew Cuthbert at Green Gables, a quiet farm in the town of Avonlea, it is quickly revealed that she is there by mistake. Having expected a boy able to help out with farm work, the Cuthberts are surprised to instead receive a young girl with a vivid imagination and a flair for the dramatic.\n\nDespite the Cuthberts' initial hesitation, Anne's energy, intelligence, and charm soon win them over. Anne settles into her new life, experiencing many things for the first time—such as attending school and making close friends—as she learns to navigate the joys and challenges of growing up.\n\n[Written by MAL Rewrite]"}, {"id": 59711, "name_en": "Shiboyugi: Playing Death Games to Put Food on the Table", "name_jp": "死亡遊戯で飯を食う。", "image_url": "https://cdn.myanimelist.net/images/anime/1834/151169.jpg", "genres": ["Action", "Suspense"], "themes": ["High Stakes Game"], "demographics": [], "studios": ["Studio Deen"], "year": 2026, "episodes": 11, "source": "Light novel", "score": 7.87, "synopsis": "Yuki wakes up to find herself in an unfamiliar manor, wearing a maid uniform and lying on a luxurious bed. Soon, she discovers five more girls, all dressed the same as her. This is the Ghost House, and the only way they can survive is to make it through the traps that lie in wait—deadly games full of blowguns, buzz saws, locked rooms, and weapons. It's a hopeless, terrifying situation for everyone there...well, everyone except Yuki. After all, this is her career at seventeen. Do you think that's odd? She would agree. But that's how some people are—they earn their living playing death games.\n\n(Source: Yen Press)"}, {"id": 55153, "name_en": "The Yuzuki Family's Four Sons", "name_jp": "柚木さんちの四兄弟。", "image_url": "https://cdn.myanimelist.net/images/anime/1194/138253.jpg", "genres": ["Slice of Life"], "themes": ["School"], "demographics": ["Shoujo"], "studios": ["Shuka"], "year": 2023, "episodes": 12, "source": "Manga", "score": 7.87, "synopsis": "The Yuzuki family, which consists of four brothers, lost their parents two years ago. Hayato, the eldest son, is a hard worker and the pillar of the family. Mikoto, the second son, is calm and collected, but he has a soft spot for his younger brother Minato, whom he adores and thinks is cute. Minato, the third son, is energetic and adores his brothers, but he is clumsy and awkward. Gakuto, the fourth son, is a studious and mature first-grader who is often referred to as a \"wise man.\" The manga depicts the four brothers' daily lives as they navigate their family, school, and community.\n\n(Source: MAL News)"}, {"id": 52168, "name_en": "Mobile Suit Gundam: The Witch from Mercury - Prologue", "name_jp": "機動戦士ガンダム 水星の魔女 PROLOGUE", "image_url": "https://cdn.myanimelist.net/images/anime/1870/124666.jpg", "genres": ["Sci-Fi"], "themes": ["Mecha", "Space"], "demographics": [], "studios": ["Sunrise"], "year": 2022, "episodes": 1, "source": "Original", "score": 7.87, "synopsis": "On the planet Mercury, the advanced technology of cybernetic prostheses has led to the development of giant pilotable robots. However, prolonged use of these achievements in bioengineering takes a significant toll on a pilot's body. As the Merucians' mobile suits gain formidable power, fear rises among the people of Earth.\n\nOn her daughter Ericht's fourth birthday, pilot Elnora Samaya tests the state-of-the-art Gundam Lfrith under the watchful eye of its creator, Cardo Nabo. Ericht visits Elnora at work despite orders not to, and chaos unfolds after she forms a strange bond with her mother's mecha.\n\n[Written by MAL Rewrite]"}, {"id": 10012, "name_en": "Carnival Phantasm", "name_jp": "カーニバル・ファンタズム", "image_url": "https://cdn.myanimelist.net/images/anime/1018/92921.jpg", "genres": ["Comedy", "Supernatural"], "themes": ["Parody"], "demographics": [], "studios": ["Lerche"], "year": 2011, "episodes": 12, "source": "Manga", "score": 7.87, "synopsis": "The Carnival Moment is a time when several narratives from Type-Moon's famous works intersect. It happens every decade in an entirely new dimension, and those who have never run into each other can meet. And what's more, all individuals involved gain the opportunity to fulfill any of their wishes! However, they will have to come a long way, as they will need to partake in contests.\n\nIn Carnival Phantasm, various characters are put into situations that ridicule their own worlds. Nevertheless, they all have the same objective: to win the contests by any means necessary!\n\n[Written by MAL Rewrite]"}, {"id": 26213, "name_en": "Free! Eternal Summer: Kindan no All Hard!", "name_jp": "Free! -Eternal Summer- 禁断のオールハード！", "image_url": "https://cdn.myanimelist.net/images/anime/6/73125.jpg", "genres": ["Comedy"], "themes": ["School"], "demographics": [], "studios": ["Kyoto Animation", "Animation Do"], "year": 2015, "episodes": 1, "source": "Original", "score": 7.87, "synopsis": "Unaired episode included with volume 7 of the Blu-ray/DVD."}, {"id": 12113, "name_en": "Berserk: The Golden Age Arc II - The Battle for Doldrey", "name_jp": "ベルセルク 黄金時代篇Ⅱ ドルドレイ攻略", "image_url": "https://cdn.myanimelist.net/images/anime/12/37193.jpg", "genres": ["Action", "Adventure", "Drama", "Fantasy", "Horror"], "themes": ["Gore", "Military", "Psychological"], "demographics": ["Seinen"], "studios": ["Studio 4°C"], "year": 2012, "episodes": 1, "source": "Manga", "score": 7.87, "synopsis": "The Band of the Hawk and their enigmatic leader Griffith continue winning battle after battle as their prestige throughout the kingdom of Midland grows. But their latest task is one that has seen failure from everyone who has attempted it: the subjugation of the impenetrable fortress of Doldrey. \n\nBut with members like Guts—the captain of the Hawks' raiders who can easily fell 100 men with his gigantic sword—such tasks prove to be trivial. However, in the aftermath of the battle, Guts decides to leave the Hawks in order to pursue his own dream and bids farewell to his companions, despite Griffith's attempts to make him stay. This single event causes Griffith to lose his composure, and leads him to make a decision that will alter his and the Hawks' fates forever.\n\n[Written by MAL Rewrite]"}, {"id": 32547, "name_en": "Non Non Biyori Repeat: Hotaru Had Fun", "name_jp": "のんのんびより りぴーと 蛍が楽しんだ", "image_url": "https://cdn.myanimelist.net/images/anime/13/78136.jpg", "genres": ["Slice of Life"], "themes": ["CGDCT", "Iyashikei", "School"], "demographics": ["Seinen"], "studios": ["SILVER LINK."], "year": 2016, "episodes": 1, "source": "Manga", "score": 7.87, "synopsis": "Life in rural Asahigaoka moves at its own pace. Devoid of the pleasures and worries of a bustling metropolis, this small village might seem painfully dull at first glance. However, despite living this far from her hometown of Tokyo, Hotaru Ichijou manages to find a group of friends whom she can spend quality time with. No matter whether it is winter, spring, summer, or fall, they make full use of everything the nature around them has to offer.\n\n[Written by MAL Rewrite]"}, {"id": 5680, "name_en": "K-ON!", "name_jp": "けいおん!", "image_url": "https://cdn.myanimelist.net/images/anime/10/76120.jpg", "genres": ["Comedy"], "themes": ["CGDCT", "Music", "School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2009, "episodes": 13, "source": "4-koma manga", "score": 7.87, "synopsis": "A fresh high school year always means much to come, and one of those things is joining a club. Being in a dilemma about which club to join, Yui Hirasawa stumbles upon and applies for the Light Music Club, which she misinterprets to be about playing simple instruments, such as castanets. Unable to play an instrument, she decides to visit to apologize and quit.\n\nMeanwhile, the Light Music Club faces disbandment due to a lack of members. This causes the club members to offer anything, from food to slacking off during club time, in order to convince Yui to join. Despite their efforts, Yui insists on leaving due to her lack of musical experience. As a last resort, they play a piece for Yui, which sparks her fiery passion and finally convinces her to join the club.\n\nFrom then onward, it is just plain messing around with bits and pieces of practice. The members of the Light Music Club are ready to make their time together a delightful one!\n\n[Written by MAL Rewrite]"}, {"id": 35082, "name_en": "Sound! Euphonium the Movie: May the Melody Reach You!", "name_jp": "劇場版 響け！ユーフォニアム～届けたいメロディ～", "image_url": "https://cdn.myanimelist.net/images/anime/5/88024.jpg", "genres": ["Drama"], "themes": ["Music", "Performing Arts", "School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2017, "episodes": 1, "source": "Novel", "score": 7.87, "synopsis": "Following their success in the qualifying round for the Kansai regional competition, the members of the Kitauji High School concert band set their sights on the next upcoming performance. Utilizing their summer break to the utmost, the band participates in a camp where they are instructed by their band advisor Noboru Taki and his friends who make their living as professional musicians.\n\nKumiko Oumae and her friends remain determined to attain gold at the Kansai competition, but trouble arises when a student who once quit the band shows interest in rejoining and sparks unpleasant memories for the second-year members. Kumiko also learns about her teacher's surprising past and the motivation behind his desire to lead the band to victory. Reaching nationals will require hard work, and the adamant conviction in each student's commitment to the band will be put to the test.\n\n[Written by MAL Rewrite]"}, {"id": 41782, "name_en": "BanG Dream! Poppin'Dream!", "name_jp": "劇場版 BanG Dream! ぽっぴん'どりーむ！", "image_url": "https://cdn.myanimelist.net/images/anime/1856/119931.jpg", "genres": [], "themes": ["CGDCT", "Music"], "demographics": [], "studios": ["SANZIGEN"], "year": 2022, "episodes": 1, "source": "Mixed media", "score": 7.87, "synopsis": "\"Bands... are always making me dream.\"\n\nPoppin'Party, Roselia, and RAISE A SUILEN's successful concert at the Budokan has passed.\nKasumi and the others, who have left a lingering sound on the stage of their dreams, are approached by a mysterious woman.\nTheir next stage is overseas?!\n\nPoppin'Party's music isn't stopping yet!\n\n(Source: BanG Dream! Wiki)"}, {"id": 36563, "name_en": "Megalobox", "name_jp": "メガロボクス", "image_url": "https://cdn.myanimelist.net/images/anime/1958/93533.jpg", "genres": ["Sci-Fi", "Sports"], "themes": ["Adult Cast", "Combat Sports"], "demographics": [], "studios": ["TMS Entertainment"], "year": 2018, "episodes": 13, "source": "Original", "score": 7.87, "synopsis": "\"To be quiet and do as you're told, that's the cowardly choice.\" These are the words of Junk Dog, an underground fighter of Megalo Box, an evolution of boxing that utilizes mechanical limbs known as Gear to enhance the speed and power of its users. Despite the young man's brimming potential as a boxer, the illegal nature of his participation forces him to make a living off of throwing matches as dictated by his boss Gansaku Nanbu. However, this all changes when the Megalo Box champion Yuuri enters his shabby ring under the guise of just another challenger. Taken out in a single round, Junk Dog is left with a challenge: \"If you're serious about fighting me again, then fight your way up to me and my ring.\"\n\nFilled with overwhelming excitement and backed by the criminal syndicate responsible for his thrown matches, Junk Dog enters Megalonia: a world-spanning tournament that will decide the strongest Megalo Boxer of them all. Having no name of his own, he takes on the moniker of \"Joe\" as he begins his climb from the very bottom of the ranked list of fighters. With only three months left to qualify, Joe must face off against opponents the likes of which he has never fought in order to meet the challenge of his rival.\n\n[Written by MAL Rewrite]"}, {"id": 7645, "name_en": "Heartcatch Precure!", "name_jp": "ハートキャッチプリキュア!", "image_url": "https://cdn.myanimelist.net/images/anime/2/25915.jpg", "genres": ["Action", "Comedy"], "themes": ["Mahou Shoujo"], "demographics": [], "studios": ["Toei Animation"], "year": 2010, "episodes": 49, "source": "Original", "score": 7.87, "synopsis": "Young flower enthusiast Tsubomi Hanasaki is often modest and quiet. But with her family moving to a new town, she aims to reinvent her image at her new school as someone more confident and outgoing. On moving day, she dreams of a mysterious tree in the sky guarded by a warrior named \"Cure Moonlight.\"\n\nTsubomi quickly learns that this was no ordinary dream when she encounters two mysterious fairies—Chypre and Coffret—who are being hunted down by a strange woman. When the woman summons a giant monster to attack the city, Tsubomi finds herself transforming into a warrior to fight the enemy! Taking on the alias \"Cure Blossom,\" Tsubomi learns that the woman is part of a villainous group that aims to turn the world into a lifeless desert, with her new duty being to stop it from happening. As Tsubomi continues to battle more monsters and uncover the secrets behind Cure Moonlight, will she find the confidence needed to overcome her timid nature?\n\n[Written by MAL Rewrite]"}, {"id": 2159, "name_en": "Big Windup!", "name_jp": "おおきく振りかぶって", "image_url": "https://cdn.myanimelist.net/images/anime/2/20751.jpg", "genres": ["Sports"], "themes": ["Team Sports"], "demographics": ["Seinen"], "studios": ["A-1 Pictures"], "year": 2007, "episodes": 25, "source": "Manga", "score": 7.87, "synopsis": "Ren Mihashi was the ace of his middle school's baseball team, but due to his poor pitching, they could never win. Constant losses eventually lead to his teammates bullying him and reached the point where his teammates no longer tried to win, causing Mihashi to graduate with little self-esteem. As a result, Mihashi decides to go to a high school in a different prefecture where he has no intention of playing baseball. Unfortunately, upon his arrival at Nishiura High, he is dragged into joining their new team as the starting pitcher.\n\nAlthough unwilling at first, Mihashi realizes that this is a place where he will be accepted for who he is; with help from the catcher Takaya Abe, he starts to have more confidence in his own abilities. Abe, seeing the potential in Mihashi, makes it a goal to help him become a pitcher worthy of being called an ace.\n\n[Written by MAL Rewrite]"}, {"id": 11813, "name_en": "KenIchi: The Mightiest Disciple OVA", "name_jp": "史上最強の弟子 ケンイチ OVA", "image_url": "https://cdn.myanimelist.net/images/anime/1125/102421.jpg", "genres": ["Action", "Comedy"], "themes": ["Martial Arts", "School"], "demographics": ["Shounen"], "studios": ["Brain's Base"], "year": 2012, "episodes": 11, "source": "Manga", "score": 7.87, "synopsis": "OVAs of History's Strongest Disciple Kenichi bundled with volumes 46, 47, 49, 53, 54, 55, &amp; 56 of the manga."}, {"id": 1594, "name_en": "Hell Girl: Two Mirrors", "name_jp": "地獄少女 二籠", "image_url": "https://cdn.myanimelist.net/images/anime/5/86975.jpg", "genres": ["Horror", "Mystery", "Supernatural", "Suspense"], "themes": ["Psychological"], "demographics": [], "studios": ["Studio Deen"], "year": 2006, "episodes": 26, "source": "Original", "score": 7.87, "synopsis": "Ai Enma and her companions continue to offer their service of revenge against those who have wronged others, and the price is as steep as ever—for damning the offender to hell, the person exacting vengeance is sent to the abyss as well. As they cast soul after soul into the darkness, a new sinister force is watching them: a little girl named Kikuri.\n\nWhile Ai continues her duties, she meets a boy named Takuma Kurebayashi, known as the \"Devil's Child\" because of the horrific events that occur around him. Unfortunately, Takuma's reputation leads the townspeople to use him as a scapegoat for those who have been ferried off to hell. When things quickly spiral out of control, Ai must find a way to bring an end to this senseless violence, as it poses a threat to her very existence.\n\n[Written by MAL Rewrite]"}, {"id": 47405, "name_en": "Perfect World", "name_jp": "完美世界", "image_url": "https://cdn.myanimelist.net/images/anime/1809/153679.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Foch Film"], "year": 2021, "episodes": 286, "source": "Web novel", "score": 7.87, "synopsis": "Born into a unique world where villages fight to gain power and control, the main character, Shi Hao, is a genius blessed by the heavens born under the poorest of conditions.\n\nHis clan, however, has a mysterious past. To rise up and become the genius he is meant to be, the clan goes through every effort to aid his cultivation as they battle through fanatical monsters and engage in power struggles with other clans.\n\nHis journey will bring him through unknown lands until he is able to become a person that can truly shake the world.\n\n(Source: Novels Xianxia&amp;Xuanhuan Wiki)"}, {"id": 957, "name_en": "The Story of Saiunkoku", "name_jp": "彩雲国物語", "image_url": "https://cdn.myanimelist.net/images/anime/1976/139963.jpg", "genres": ["Adventure", "Fantasy", "Romance"], "themes": ["Historical", "Reverse Harem"], "demographics": ["Shoujo"], "studios": ["Madhouse"], "year": 2006, "episodes": 39, "source": "Light novel", "score": 7.87, "synopsis": "Shuurei Kou, the daughter of a noble yet impoverished family, is a clever young lady who dreams of becoming a government official and contributing toward her country. However, her dream is out of her reach as such a position is forbidden to women. While her father works a low wage job as an archivist at the palace, Shuurei has to juggle odd jobs to make ends meet. Then, one day, an unexpected visit changes her life. \n\nShuurei is called to assist Ryuuki Shi, the new emperor who is known for slacking on his duties and preferring the company of men. Tempted by the generous compensation, she readily accepts the chance to become the young emperor's consort for six months. Luckily, she is not alone as Seiran Shi, her trusty friend, joins her as Ryuuki's bodyguard. While tasked with transforming the new emperor into a responsible ruler, court life and politics prove troublesome as Shuurei faces the challenges of her new life.\n\nSet in a fictional country, Saiunkoku Monogatari centers on the idea of meaningful leadership, its adversities and the rewards that come alongside a prospering nation.\n\n[Written by MAL Rewrite]"}, {"id": 934, "name_en": "Higurashi: When They Cry", "name_jp": "ひぐらしのなく頃に", "image_url": "https://cdn.myanimelist.net/images/anime/12/19634.jpg", "genres": ["Horror", "Mystery", "Suspense"], "themes": ["Gore", "Psychological"], "demographics": [], "studios": ["Studio Deen"], "year": 2006, "episodes": 26, "source": "Visual novel", "score": 7.87, "synopsis": "Keiichi Maebara has just moved to the quiet little village of Hinamizawa in the summer of 1983, and quickly becomes inseparable friends with schoolmates Rena Ryuuguu, Mion Sonozaki, Satoko Houjou, and Rika Furude. However, darkness lurks underneath the seemingly idyllic life they lead. \n\nAs the village prepares for its annual festival, Keiichi learns about the local legends surrounding it. To his horror, he discovers that there have been several murders and disappearances in the village in the recent years, and that they all seem to be connected to the festival and the village's patron god, Oyashiro. Keiichi tries to ask his new friends about these incidents, but they are suspiciously silent and refuse to give him the answers he needs. As more and more bizarre events occur, he wonders just what else his friends might be keeping from him, and if he can even trust them at all.\n\nWhen madness and paranoia begin taking root in Keiichi's heart, he will stumble straight into the mysteries at work in Higurashi no Naku Koro ni, a story that is told across multiple arcs.\n\n[Written by MAL Rewrite]"}, {"id": 14353, "name_en": "Death Billiards", "name_jp": "デス・ビリヤード", "image_url": "https://cdn.myanimelist.net/images/anime/11/48721.jpg", "genres": ["Drama", "Fantasy", "Suspense"], "themes": ["Adult Cast", "High Stakes Game", "Psychological"], "demographics": [], "studios": ["Madhouse"], "year": 2013, "episodes": 1, "source": "Original", "score": 7.87, "synopsis": "Two men have just arrived at a location known as Quindecim and are unable to remember how they got there. They are immediately greeted by a young woman who escorts them to a small bar, where a bartender awaits them. They are told that they will have to participate in a game, randomly chosen by roulette, and will be unable to leave until its completion; if they refuse, the consequences will be dire. In addition to the rules of the game, the two men are told to play as if their lives are at stake.\n \nThe game that has been chosen is billiards. But there's more to it than just pocketing pool balls, as the two are about to find out the outcome could mean life or death.\n\n[Written by MAL Rewrite]"}, {"id": 49570, "name_en": "Martial Universe 3", "name_jp": "武动乾坤 第三季", "image_url": "https://cdn.myanimelist.net/images/anime/1282/123462.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["DC Impression Vision"], "year": 2022, "episodes": 12, "source": "Web novel", "score": 7.87, "synopsis": "In order to become stronger and defeat Lin Langtian in the subsequent clan competitions, Lin Dong, Xiao Diao and Xiao Yan embarked on the road of practice together, with constant adventures along the way. Under Xiao Diao's guidance, Lin Dong finally came to the Ancient Monument of the Great Wilderness, and successfully obtained the clue to devour the ancestral talisman, and under the competition of Lin Langtian and many strong men, he successfully obtained the martial arts of good fortune, the Great Wilderness Prisoner's Finger!\n\n(Source: WeTV)"}, {"id": 14719, "name_en": "JoJo's Bizarre Adventure (2012)", "name_jp": "ジョジョの奇妙な冒険", "image_url": "https://cdn.myanimelist.net/images/anime/3/40409.jpg", "genres": ["Action", "Adventure", "Supernatural"], "themes": ["Historical", "Vampire"], "demographics": ["Shounen"], "studios": ["David Production"], "year": 2012, "episodes": 26, "source": "Manga", "score": 7.87, "synopsis": "The year is 1868; English nobleman George Joestar and his son Jonathan become indebted to Dario Brando after being rescued from a carriage incident. What the Joestars don't realize, however, is that Dario had no intention of helping them; he believed they were dead and was trying to ransack their belongings. After Dario's death 12 years later, George—hoping to repay his debt—adopts his son, Dio. \n\nWhile he publicly fawns over his new father, Dio secretly plans to steal the Joestar fortune. His first step is to create a divide between George and Jonathan. By constantly outdoing his foster brother, Dio firmly makes his place in the Joestar family. But when Dio pushes Jonathan too far, Jonathan defeats him in a brawl. \n\nYears later, the two appear to be close friends to the outside world. But trouble brews again when George falls ill, as Jonathan suspects that Dio is somehow behind the incident—and it appears he has more tricks up his sleeve.\n\n[Written by MAL Rewrite]"}, {"id": 249, "name_en": "InuYasha", "name_jp": "犬夜叉", "image_url": "https://cdn.myanimelist.net/images/anime/1589/95329.jpg", "genres": ["Action", "Adventure", "Fantasy", "Romance"], "themes": ["Historical", "Love Polygon", "Mythology", "Time Travel"], "demographics": ["Shounen"], "studios": ["Sunrise"], "year": 2000, "episodes": 167, "source": "Manga", "score": 7.87, "synopsis": "Kagome Higurashi's 15th birthday takes a sudden turn when she is forcefully pulled by a demon into the old well of her family's shrine. Brought to the past, when demons were a common sight in feudal Japan, Kagome finds herself persistently hunted by these vile creatures, all yearning for an item she unknowingly carries: the Shikon Jewel, a small sphere holding extraordinary power. \n\nAmid such a predicament, Kagome encounters a half-demon boy named Inuyasha who mistakes her for Kikyou, a shrine maiden he seems to resent. Because of her resemblance to Kikyou, Inuyasha takes a violent dislike to Kagome. However, after realizing the dire circumstances they are both in, he sets aside his hostility and lends her a hand. \n\nUnfortunately, during a fight for the Shikon Jewel, the miraculous object ends up shattered into pieces and scattered across the land. Fearing the disastrous consequences of this accident, Kagome and Inuyasha set out on a challenging quest to recover the shards before they fall into the wrong hands.\n\n[Written by MAL Rewrite]"}, {"id": 50403, "name_en": "No Doubt In Us 2nd Season", "name_jp": "两不疑 第二季", "image_url": "https://cdn.myanimelist.net/images/anime/1385/119524.jpg", "genres": ["Comedy", "Romance"], "themes": ["Historical"], "demographics": [], "studios": ["Paper Plane Animation Studio"], "year": 2022, "episodes": 24, "source": "Web manga", "score": 7.87, "synopsis": "Second season of Liang Bu Yi."}, {"id": 12031, "name_en": "Kingdom", "name_jp": "キングダム", "image_url": "https://cdn.myanimelist.net/images/anime/13/39511.jpg", "genres": ["Action"], "themes": ["Historical", "Military"], "demographics": ["Seinen"], "studios": ["Studio Pierrot"], "year": 2012, "episodes": 38, "source": "Manga", "score": 7.87, "synopsis": "China’s Warring States period, a raging dragon that would raze the land for 500 years, saw many kingdoms rise and fall, making way for the next generation of kings and generals to fight for supremacy. Eventually, seven powerful states emerged from the endless cycle of warfare.\n\nIn the kingdom of Qin, Xin, a war-orphaned slave, trains vigorously with fellow slave and best friend, Piao, who shares his proud dream of one day becoming a Great General of the Heavens. However, the two are suddenly forced to part ways when Piao is recruited to work in the royal palace by a retainer of the King.\n\nAfter a fierce coup d'état unfolds, Piao returns to Xin, half dead, with a mission that will lead him to a meeting with China's young King, Ying Zheng, who bears a striking resemblance to Piao. Kingdom follows Xin as he takes his first steps into the great blood-soaked pages of China's history. He must carve his own path to glory on his long quest to become a Great General of the historic Seven Warring States.\n\n[Written by MAL Rewrite]"}, {"id": 995, "name_en": "The Prince of Tennis: National Championship Chapter", "name_jp": "テニスの王子様 Original Video Animation 全国大会編", "image_url": "https://cdn.myanimelist.net/images/anime/12/10572.jpg", "genres": ["Sports"], "themes": [], "demographics": ["Shounen"], "studios": ["M.S.C"], "year": 2006, "episodes": 13, "source": "Manga", "score": 7.87, "synopsis": "Freshman tennis prodigy Ryouma Echizen and the Seishun Academy Boys' Tennis Team have finally reached the National Tournament, eager to fight for the championship title and claim it as their own.\n\nSeishun's first match is against Higa Middle School, the Okinawa-based team labeled as the \"dark horse\" of the tournament. After watching Higa defeat their first round of opponents using dirty tactics, the Seishun team is determined to show them the value of a fair, hard-earned victory.\n\nAwaiting the winners of the matchup in the quarterfinals is one of Seishun's old rivals: Hyoutei Academy, who are determined to restore their pride after the events at the Kanto Regionals. Hyoutei seeks to prove to everyone that they deserve to be at the Nationals despite only qualifying through a special invitation.\n\nTennis no Oujisama: Zenkoku Taikai-hen covers the beginning of the long-awaited National Tournament as Ryouma and the Seishun team meet foes of past and present, each with their own unique ideals on how tennis should be played!\n\n[Written by MAL Rewrite]"}, {"id": 62249, "name_en": "Martial Universe 6", "name_jp": "武动乾坤 第6季", "image_url": "https://cdn.myanimelist.net/images/anime/1937/151456.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Motion Magic"], "year": 2025, "episodes": 12, "source": "Web novel", "score": 7.86, "synopsis": "Lin Dong and his friends venture into a dangerous ancient land to gain strength for the Hundred Dynasties War. He defeats his rival Lin Langtian, gains new skills, and saves his friend. After earning a spot in the final and winning again, he stops a plot from the Western Domain to take over the battlefield. In the end, Lin Dong becomes the champion and joins the prestigious Dao Sect.\n\n(Source: WeTV)"}, {"id": 56805, "name_en": "The Brave", "name_jp": "勇者", "image_url": "https://cdn.myanimelist.net/images/anime/1947/138863.jpg", "genres": [], "themes": ["Music"], "demographics": [], "studios": ["Madhouse"], "year": 2023, "episodes": 1, "source": "Original", "score": 7.86, "synopsis": "Music video for the song Yuusha by YOASOBI."}, {"id": 55855, "name_en": "Black Butler: Public School Arc", "name_jp": "黒執事 -寄宿学校編-", "image_url": "https://cdn.myanimelist.net/images/anime/1505/141820.jpg", "genres": ["Action", "Mystery", "Supernatural"], "themes": ["Historical", "Mythology", "School"], "demographics": ["Shounen"], "studios": ["CloverWorks"], "year": 2024, "episodes": 11, "source": "Manga", "score": 7.86, "synopsis": "At the request of Queen Victoria, Earl Ciel Phantomhive goes to Weston College—a prestigious public academy for boys, known for its traditions and outstanding curriculum—to search for a relative of hers who has not returned home in months. Thanks to his noble status, the 13-year-old Ciel is swiftly able to enroll as a student, while his devilish butler Sebastian Michaelis assumes a teaching position at the school. \n\nCiel does not anticipate being unable to locate the Queen's relative on campus. To make matters worse, he cannot request a meeting with the headmaster of the college, who may be the only person that knows anything pertaining to the disappearance. The only ones in frequent contact with the headmaster are the heads of the college dormitories, known as the Prefect 4 (P4): Edgar Redmond, Lawrence Bluewer, Herman Greenhill, and Gregory Violet.\n\nCiel must concoct a plan to get in the P4's good graces if he wants to use their connections to succeed in his mission. But the distinguished college and its students hide much that is unseen, making this task more improbable than he could imagine.\n\n[Written by MAL Rewrite]"}, {"id": 54764, "name_en": "Totto-chan, the Little Girl at the Window", "name_jp": "窓ぎわのトットちゃん", "image_url": "https://cdn.myanimelist.net/images/anime/1001/139067.jpg", "genres": ["Drama"], "themes": ["Historical", "School"], "demographics": [], "studios": ["Shin-Ei Animation"], "year": 2023, "episodes": 1, "source": "Book", "score": 7.86, "synopsis": "This engaging series of childhood recollections tells about an ideal school in Tokyo during World War II that combined learning with fun, freedom, and love. This unusual school had old railroad cars for classrooms, and it was run by an extraordinary man—its founder and headmaster, Sousaku Kobayashi—who was a firm believer in freedom of expression and activity.\n\n(Source: Kodansha, edited)"}, {"id": 25517, "name_en": "Magic Kaito 1412", "name_jp": "まじっく快斗1412", "image_url": "https://cdn.myanimelist.net/images/anime/3/67807.jpg", "genres": ["Adventure", "Comedy", "Romance"], "themes": [], "demographics": ["Shounen"], "studios": ["A-1 Pictures"], "year": 2014, "episodes": 24, "source": "Manga", "score": 7.86, "synopsis": "Eight years after the mysterious death of his father, Kaito Kuroba, a slightly mischievous but otherwise ordinary teenager, discovers a shocking secret: the Phantom Thief Kaito Kid—also known as \"The Magician Under the Moonlight\"—was none other than his own father. The former thief was murdered by a criminal organization seeking a mythical stone called the Pandora Gem, said to shed a tear with the passing of the Valley Comet that comes every ten thousand years. When the tear is consumed, the gem supposedly grants immortality.\n\nVowing to bring those responsible for his father's death to justice, Kaito dons the Phantom Thief's disguise, stealing priceless jewels night after night to find the Pandora Gem before his enemies can use the power for themselves.\n\n[Written by MAL Rewrite]"}, {"id": 31490, "name_en": "One Piece Film: Gold", "name_jp": "ONE PIECE FILM GOLD", "image_url": "https://cdn.myanimelist.net/images/anime/1081/137690.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Toei Animation"], "year": 2016, "episodes": 1, "source": "Manga", "score": 7.86, "synopsis": "Monkey D. Luffy and his Straw Hat Crew have finally arrived on Gran Tesoro, a ship carrying the largest entertainment city in the world. Drawn in by the chances of hitting the jackpot, the crew immediately head to the casino. There, they quickly find themselves on a winning streak, playing with what seems to be endless luck.\n\nWhen offered a special gamble by Gild Tesoro—the master of the city himself—the crew agrees, choosing to believe in their captain's luck. However, when they find themselves victims of a despicable scam, the crew quickly realize that there is something darker happening beneath the city's surface.\n\nLeft penniless and beaten down, the Straw Hat Crew are forced to rely on another gamble of a plan. With the help of a new friend or two, the group must work to reclaim what they've lost before time, and what remains of their luck, runs out.\n\n[Written by MAL Rewrite]"}, {"id": 38770, "name_en": "Case Closed Movie 23: The Fist of Blue Sapphire", "name_jp": "劇場版 名探偵コナン 紺青の拳（フィスト）", "image_url": "https://cdn.myanimelist.net/images/anime/1010/98309.jpg", "genres": ["Action", "Comedy", "Drama", "Mystery"], "themes": ["Detective"], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2019, "episodes": 1, "source": "Manga", "score": 7.86, "synopsis": "The world's greatest blue sapphire, the \"blue lapis fist\", said to have sunk in a pirate ship in the late 19th century, on the coasts of Singapore. A local millionaire plots to retrieve it, and when it's exhibited in an exhibition at the Singaporean Marina Sands hotel, a murder takes place. A bloody Kaitou Kid announcement card is found in the crime scene.\n\nIn the meantime, Ran and Sonoko have come to Singapore to see a karate tournament held there. Conan, who doesn't have a passport and can't travel overseas, was supposed to stay at home. But Kid, who wants to make use of him, uses a magical trick to forcibly drag him to Singapore. Conan must obey Kid if he wants to get back to Japan, gets his glasses, wristwatch and clothes, amongst others, all stolen from him and he has to disguise himself.\n\nRan, who doesn't realize who he is, asks him his name and he improvises the name Arthur Hirai. Kid eventually gets a hold of information about the \"fist\" being stored in a manor's underground vault. Just when he was thinking that he'd made it inside very easily, he finds a dangerous trap awaiting him. Facing him is the strongest karateka with 400 consecutive victories: Makoto Kyogoku.\n\n(Source: Detective Conan World)"}, {"id": 6007, "name_en": "Spice and Wolf II: The Wolf and the Amber Melancholy", "name_jp": "狼と香辛料II 狼と琥珀色の憂鬱", "image_url": "https://cdn.myanimelist.net/images/anime/12/13070.jpg", "genres": ["Adventure", "Romance", "Supernatural"], "themes": ["Adult Cast"], "demographics": [], "studios": ["Brain's Base", "Marvy Jack"], "year": 2009, "episodes": 1, "source": "Light novel", "score": 7.86, "synopsis": "Traveling merchant Kraft Lawrence and wolf deity Holo sit down to have dinner with Norah Arendt, a young shepherdess who helped them smuggle gold. Holo's dislike of Norah—due to both the latter's profession and her closeness with Lawrence—prevents her from enjoying her meal.\n\nWhen Holo falls ill from fatigue, she is cared for by Lawrence, who frets over the health of his dear companion. As she regains her strength, Holo reminisces about the adventures they had together.\n\n[Written by MAL Rewrite]"}, {"id": 4985, "name_en": "Magical Girl Lyrical Nanoha: The Movie 1st", "name_jp": "魔法少女リリカルなのは The MOVIE 1st", "image_url": "https://cdn.myanimelist.net/images/anime/1058/94433.jpg", "genres": ["Action", "Comedy", "Drama", "Sci-Fi"], "themes": ["Mahou Shoujo"], "demographics": [], "studios": ["Seven Arcs"], "year": 2010, "episodes": 1, "source": "Original", "score": 7.86, "synopsis": "Nanoha Takamachi, an ordinary third-grader, loves her family and friends more than anything else. One day, after having a strange dream in which a ferret gets injured, she sees the very same ferret in real life and rescues it. That ferret turns out to be Yuuno Scrya, a mage from another world who is trying to capture the 21 scattered Jewel Seeds before they cause serious damage to the universe. Yuuno is not powerful enough to capture the Jewel seeds on his own, so he grants Nanoha the intelligent device \"Raising Heart\" and begins training her as a mage.  \n\nUnfortunately, the powerful Jewel Seeds attract those with ill intentions. Another mage, Fate Testarossa, is desperate to collect the seeds for some unknown and sinister purpose, though the solemn look in her eyes makes Nanoha think that there is more to Fate than meets the eye. Mahou Shoujo Lyrical Nanoha: The Movie 1st is a retelling of the original series, which tells the story of two young mages and how their strong emotions shape their actions.\n\n[Written by MAL Rewrite]"}, {"id": 10080, "name_en": "The World God Only Knows II", "name_jp": "神のみぞ知るセカイ II", "image_url": "https://cdn.myanimelist.net/images/anime/11/30030.jpg", "genres": ["Comedy", "Romance", "Supernatural"], "themes": ["Harem", "Otaku Culture"], "demographics": ["Shounen"], "studios": ["Manglobe"], "year": 2011, "episodes": 12, "source": "Manga", "score": 7.86, "synopsis": "Keima Katsuragi, the \"God of Conquest,\" returns to his quest of expelling runaway spirits that have possessed the hearts of women. Still stuck in his contract with the demon Elsie, he must continue to utilize the knowledge he has gained from mastering multitudes of dating simulators and chase out the phantoms that reside within by capturing the hearts of that which he hates most: three-dimensional girls.\n\nHowever, the God of Conquest has his work cut out for him. From exorcising karate practitioners and student teachers to the arrival of Elsie's best friend from Hell, he is up against a wide array of girls that will test his wit and may even take him by surprise. Though he would much rather stick to the world of 2D, he is trapped in lousy reality, and so Keima must trudge forward in his conquest of love.\n\n[Written by MAL Rewrite]"}, {"id": 34021, "name_en": "Lupin the Third: Goemon's Blood Spray", "name_jp": "LUPIN THE IIIRD 血煙の石川五ェ門", "image_url": "https://cdn.myanimelist.net/images/anime/8/83091.jpg", "genres": ["Action", "Adventure", "Drama"], "themes": ["Adult Cast"], "demographics": ["Seinen"], "studios": ["Telecom Animation Film"], "year": 2017, "episodes": 1, "source": "Manga", "score": 7.86, "synopsis": "Lupin's friend, the samurai Goemon Ishikawa, takes a job as bodyguard for a yakuza boss. But a brutal assassin kills the yakuza and Goemon is honor-bound to track him down.\n\n(Source: IMDB)"}, {"id": 6377, "name_en": "Zan Sayonara Zetsubou Sensei", "name_jp": "懺・さよなら絶望先生", "image_url": "https://cdn.myanimelist.net/images/anime/2/78774.jpg", "genres": ["Comedy"], "themes": ["Gag Humor", "Parody", "School"], "demographics": ["Shounen"], "studios": ["Shaft"], "year": 2009, "episodes": 13, "source": "Manga", "score": 7.86, "synopsis": "Nozomu Itoshiki is still the bizarre teacher of the even stranger Class 2-F. He attempts to teach his students the negative aspects of the world and society, only to have each circumstance thrown at his face whenever he tries. With more students and friends than before, Zetsubo-sensei's life becomes harder and crazier than ever before.\n\n(Source: ANN)"}, {"id": 37936, "name_en": "Fog Hill of Five Elements", "name_jp": "雾山五行", "image_url": "https://cdn.myanimelist.net/images/anime/1141/112764.jpg", "genres": ["Action", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Samsara Animation Studio"], "year": 2020, "episodes": 3, "source": "Original", "score": 7.86, "synopsis": "A legend tells that a long time ago, monsters could give the ability to certain elected officials to master the five elements. But for that, you have to go through the dangerous foggy mountains...\n\nWe follow the adventures of one of these elected officials.\n\n(Source: Nautiljon)"}, {"id": 2752, "name_en": "The Prince of Tennis: The National Tournament Semifinals", "name_jp": "テニスの王子様 全国大会篇 Semifinal", "image_url": "https://cdn.myanimelist.net/images/anime/1640/133533.jpg", "genres": ["Sports"], "themes": [], "demographics": ["Shounen"], "studios": ["M.S.C"], "year": 2007, "episodes": 6, "source": "Manga", "score": 7.86, "synopsis": "The National Tournament continues for Ryouma Echizen and the Seishun Academy Boys' Tennis Team as they face off against last year's semifinalist, Shitenhouji Middle School. As winners of the Kansai Regionals, Shitenhouji is one of the top contenders to win this year's tournament. What's more, each member of the Shitenhouji team has skills that are said to surpass their Seishun counterparts. Shitenhouji also has Kintarou Tooyama, another \"super rookie\" who rivals Ryouma in strength and technique. Seishun will have to push the limits of their abilities to stand a chance at defeating the Kansai champions in this East versus West matchup.\n\n[Written by MAL Rewrite]"}, {"id": 23775, "name_en": "Attack on Titan: Crimson Bow and Arrow", "name_jp": "劇場版「進撃の巨人」前編～紅蓮の弓矢～", "image_url": "https://cdn.myanimelist.net/images/anime/7/63283.jpg", "genres": ["Action", "Drama", "Suspense"], "themes": ["Gore", "Survival"], "demographics": ["Shounen"], "studios": ["Wit Studio"], "year": 2014, "episodes": 1, "source": "Manga", "score": 7.86, "synopsis": "Recap of episodes 1-13."}, {"id": 38408, "name_en": "My Hero Academia Season 4", "name_jp": "僕のヒーローアカデミア", "image_url": "https://cdn.myanimelist.net/images/anime/1412/107914.jpg", "genres": ["Action"], "themes": ["School", "Super Power"], "demographics": ["Shounen"], "studios": ["Bones"], "year": 2019, "episodes": 25, "source": "Manga", "score": 7.86, "synopsis": "After successfully passing his Provisional Hero License exam, Izuku \"Deku\" Midoriya seeks out an extracurricular internship with a professional hero agency. At the recommendation of his mentor All Might, Midoriya lands a position under All Might's former sidekick, Sir Nighteye, now a famous hero in his own right.\n\nAs Midoriya's classmates further their own abilities through various internships, up-and-coming villain Kai Chisaki utilizes his terrifying powers to gather favor in the criminal underworld. Known by the moniker Overhaul, Chisaki's ambitions collide with the League of Villains and its leader, Tomura Shigaraki.\n\nThrough his work with Sir Nighteye, Midoriya discovers Chisaki's crime syndicate and the villain's hostile relationship with a mysterious young girl named Eri. Fearing for the child's safety, Midoriya and his upperclassman Mirio Toogata must work together to put an end to Chisaki's reign of terror.\n\n[Written by MAL Rewrite]"}, {"id": 2924, "name_en": "ef - a tale of memories.", "name_jp": "ef - a tale of memories.", "image_url": "https://cdn.myanimelist.net/images/anime/1246/107820.jpg", "genres": ["Drama", "Mystery", "Romance"], "themes": ["Love Polygon"], "demographics": [], "studios": ["Shaft"], "year": 2007, "episodes": 12, "source": "Visual novel", "score": 7.86, "synopsis": "On Christmas Eve, Hiro Hirono runs into Miyako Miyamura, a frivolous girl who \"borrows\" his bicycle in order to chase down a purse thief. After Hiro finds his bicycle wrecked and Miyako unconscious, the two unexpectedly spend their Christmas Eve together, and when they discover they go to the same high school, their accidental relationship develops even further. This sparks the jealousy of Hiro's childhood friend Kei Shindou, whose pure approach to life catches the eye of Kyosuke Tsutsumi, a womanizing photographer searching for the perfect shot.\n\nElsewhere, Renji Asou, a boy who dreams of being a girl's knight in shining armor, has a chance encounter with Kei's twin sister—the overly shy Chihiro Shindou, who spends her time reading alone—at an abandoned train station. The two quickly become friends and eventually decide to write a novel together. However, when Renji discovers Chihiro's secret, his childish ideals are put to the test.\n\nGuided by two mysterious adults, these youths' relationships intertwine in a heart-rending tale of love, rejection, acceptance, and memories.\n\n[Written by MAL Rewrite]"}, {"id": 58059, "name_en": "Wistoria: Wand and Sword", "name_jp": "杖と剣のウィストリア", "image_url": "https://cdn.myanimelist.net/images/anime/1281/144104.jpg", "genres": ["Action", "Fantasy"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["Actas", "Bandai Namco Pictures"], "year": 2024, "episodes": 12, "source": "Manga", "score": 7.85, "synopsis": "When humanity was oppressed by mysterious foes known as the Celestial Hosts, five exceptional mages joined forces to defeat them. In fear that these formidable enemies would return, the five most powerful mages, known as the Magia Vander, built a magical dome and a tower to contain them. Since then, the five strongest mages of every generation are tasked with monitoring the dome from the top of the Wizard's Tower.\n\nInspired by this story, childhood friends Will Serfort and Elfaria Albis Serfort promised each other that they would climb to the top of the Wizard's Tower. However, now a sixth-year student at Regarden Magic Academy, Will's future looks bleak. Although Elfaria managed to join the ranks of the Magia Vander five years prior thanks to her unparalleled magical power, Will has no magical abilities whatsoever, attracting the ire of teachers and students alike.\n\nHowever, blessed with an exceptional physique, Will is able to slay monsters in the labyrinth and prevail against skilled magicians with the only aid of his sword and a few magical items. Determined to climb the Wizard's Tower at all cost, Will is determined to not let anyone or anything prevent him from keeping the promise he made to Elfaria.\n\n[Written by MAL Rewrite]"}, {"id": 54846, "name_en": "The Girl Downstairs", "name_jp": "爱上她的理由", "image_url": "https://cdn.myanimelist.net/images/anime/1070/134906.jpg", "genres": ["Drama", "Romance"], "themes": ["Adult Cast", "Love Polygon"], "demographics": [], "studios": ["Red Dog Culture House"], "year": 2023, "episodes": 22, "source": "Web manga", "score": 7.85, "synopsis": "The day Lee Joon joins Minsong University to reunite with Kim Margo, his high school crush, he is attacked by his new neighbor Lee Duna, a gorgeous former idol. Violent and impulsive, Duna turns Joon's world upside down. Later, to Joon's confusion, Margo seems incapable of making time to meet with him. To add to his dismay, Joon meets fellow student Choi Ira at a party. She becomes enamored with him after the pair partake in a risqué game. As Joon progressively discovers the truth about Duna's troubled past and Margo's harsh family conditions, he tries to sort out his tangled feelings.\n\n[Written by MAL Rewrite]"}, {"id": 54790, "name_en": "Undead Murder Farce", "name_jp": "アンデッドガール・マーダーファルス", "image_url": "https://cdn.myanimelist.net/images/anime/1946/136661.jpg", "genres": ["Fantasy", "Mystery"], "themes": ["Adult Cast", "Detective", "Historical", "Urban Fantasy", "Vampire"], "demographics": [], "studios": ["Lapin Track"], "year": 2023, "episodes": 13, "source": "Novel", "score": 7.85, "synopsis": "In 19th-century France, the wife of vampire noble Jean Duchet Godard is murdered in her own home. With the local human authorities unwilling to properly investigate the case, Godard hires a pair of private detectives known to specialize in the supernatural: Tsugaru Shinuchi, a man with a mysterious birdcage; and his partner, Aya Rindou. This enigmatic duo has come from faraway Japan for one purpose—to track down the man who stole both Aya's body and Tsugaru's humanity.\n\n[Written by MAL Rewrite]"}, {"id": 51680, "name_en": "Play It Cool, Guys", "name_jp": "クールドジ男子", "image_url": "https://cdn.myanimelist.net/images/anime/1123/134677.jpg", "genres": ["Slice of Life"], "themes": ["Adult Cast", "Iyashikei"], "demographics": [], "studios": ["Studio Pierrot"], "year": 2022, "episodes": 24, "source": "Web manga", "score": 7.85, "synopsis": "Despite their distinctive personalities, Souma Shiki, Hayate Ichikura, Shun Futami, and Takayuki Mima all have one thing in common: though naturally clumsy, the four disguise their embarrassment from tiny slip-ups by maintaining a composed demeanor. However, it is actually the guys' airheaded natures that makes the girls' hearts throb. No matter what happens in their daily lives, the boys do their best not to lose their cool!\n\n[Written by MAL Rewrite]"}, {"id": 37029, "name_en": "Hozuki's Coolheadedness 2: Part 2", "name_jp": "鬼灯の冷徹 第弐期その弐", "image_url": "https://cdn.myanimelist.net/images/anime/1113/92465.jpg", "genres": ["Comedy", "Fantasy"], "themes": [], "demographics": ["Seinen"], "studios": ["Studio Deen"], "year": 2018, "episodes": 13, "source": "Manga", "score": 7.85, "synopsis": "The second cour of Hoozuki no Reitetsu season 2."}, {"id": 36215, "name_en": "One Piece: Episode of East Blue - Luffy and His Four Crewmates' Great Adventure", "name_jp": "ワンピース エピソードオブ東の海[イーストブルー] ~ルフィと4人の仲間の大冒険~", "image_url": "https://cdn.myanimelist.net/images/anime/10/87473.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Toei Animation"], "year": 2017, "episodes": 1, "source": "Manga", "score": 7.85, "synopsis": "The words that Gold Roger, the King of the Pirates, uttered just before his death excited the masses and the world has entered a Great Pirate Era! There is a group of young pirates who are about to set sail on the Grand Line. Monkey D. Luffy, a rubber man. Roronoa Zoro aka “Pirate Hunter.” Usopp, a sniper. Sanji, a seafaring cook. And “Cat Burglar” Nami. They, the Straw Hats, all place a foot upon a barrel and make their vows before their next journey across the great ocean. Luffy shouts “In order to be the King of the Pirates!” A decade ago in the Windmill Village... A little boy Luffy was enthralled with a pirate boss Red-Haired Shanks. But a group of mountain bandits shows up and makes fun of the pirates. “Why didn't you fight them?!” Luffy yells out angrily but Shanks says that it's nothing worth getting mad over. At that time, Luffy snatches the Gum-Gum Fruit from a treasure box and eats it and as a result, his entire body becomes rubber and he loses the ability to swim for the rest of his life! A few days later, Luffy is surrounded by the mountain bandits again and Shanks comes to help. “No matter what the reason, anyone who hurts my friends has to pay!!” Shanks and his pirate crew are incredibly strong and they beat down the mountain bandits. Higuma, the head of the mountain bandits, runs away kidnapping Luffy and heads for the sea. However, they encounter a local Sea Monster there and are attacked. Luffy is in a desperate situation... But again, Shanks saves the day. He outstares the Sea Monster and chases it away but in exchange, he loses his left arm... When Shanks leaves the village, he leaves his straw hat with Luffy. “Come bring it back to me someday! Once you've become a great pirate!” 10 years later, Luffy has grown up strong and he sets out for an adventure on a small boat. He again encounters the local Sea Monster for that fateful day. However, Luffy takes it down with a single blow of Gum-Gum Pistol. Luffy’s journey to become the King of the Pirates now begins!\n\n(Source: Crunchyroll)"}, {"id": 35798, "name_en": "Case Closed Movie 22: Zero the Enforcer", "name_jp": "劇場版 名探偵コナン ゼロの執行人", "image_url": "https://cdn.myanimelist.net/images/anime/12/90104.jpg", "genres": ["Action", "Award Winning", "Drama", "Mystery"], "themes": ["Detective"], "demographics": [], "studios": ["TMS Entertainment"], "year": 2018, "episodes": 1, "source": "Manga", "score": 7.85, "synopsis": "In the film's story, there is a sudden explosion at Tokyo Summit's giant Edge of Ocean facility. The shadow of Tooru Amuro, who works for the National Police Agency Security Bureau as Zero, appears at the site. In addition, the \"triple-face\" character is known as Rei Furuya as a detective and Kogorou Mouri's apprentice, and he is also known as Bourbon as a Black Organization member. Kogorou is arrested as a suspect in the case of the explosion. Conan conducts an investigation to prove Kogorou's innocence, but Amuro gets in his way.\n\n(Source: ANN)"}, {"id": 30415, "name_en": "Free! Starting Days", "name_jp": "映画 ハイ☆スピード！ -Free! Starting Days-", "image_url": "https://cdn.myanimelist.net/images/anime/8/76967.jpg", "genres": ["Drama", "Sports"], "themes": ["School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2015, "episodes": 1, "source": "Light novel", "score": 7.85, "synopsis": "Haruka Nanase and Makoto Tachibana have started middle school and must adjust to the changes that come along with growing up. While Makoto fits in with his classmates and remains positive about swimming, Haruka struggles to befriend others or join his school's swim club, as his previous issues with swimming trouble him. Distancing himself from his lively classmates and the swimming club, he has difficulty deciding which club to join instead. The rest of his classmates, including Makoto, are also hesitant as to which clubs to participate in. After an argument leads them to join the swimming club anyway, the boys strive to hone their skills, harmonize their swimming styles, and refine their conflicting feelings toward swimming and each other.\n\nAs determination and talent run high, Haruka and Makoto—along with their classmates—discover themselves and improve their talents during their starting days.\n\n[Written by MAL Rewrite]"}, {"id": 9734, "name_en": "K-On!!: Plan!", "name_jp": "けいおん!! 計画!", "image_url": "https://cdn.myanimelist.net/images/anime/7/26965.jpg", "genres": ["Comedy"], "themes": ["CGDCT", "Music", "School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2011, "episodes": 1, "source": "4-koma manga", "score": 7.85, "synopsis": "The summer holidays are coming to an end, but the girls from Houkago Tea Time want to take one more trip before their next semester starts. With countless travel destinations to choose from and as many preferences as there are club members, coming to an agreement seems far-flung.\n\nUnable to reach a decision, they remember that they must first apply for new passports. As simple as it may sound, the routine visit to a government office and filing a form soon turns into an all-day adventure for Yui Hirasawa and the rest of the band.\n\n[Written by MAL Rewrite]"}, {"id": 18429, "name_en": "Lupin III vs. Detective Conan: The Movie", "name_jp": "ルパン三世vs.名探偵コナン THE MOVIE", "image_url": "https://cdn.myanimelist.net/images/anime/4/51663.jpg", "genres": ["Action", "Comedy", "Mystery"], "themes": [], "demographics": ["Shounen"], "studios": ["TMS Entertainment"], "year": 2013, "episodes": 1, "source": "Manga", "score": 7.85, "synopsis": "It is a cross over between the series Lupin III and Detective Conan and takes place after the television special Lupin the 3rd vs Detective Conan. The plot follows Conan Edogawa who sets out to apprehend Arsène Lupin III, the suspect of stealing a jewel called Cherry Sapphire.\n\n(Souce: Wikipedia)"}, {"id": 39392, "name_en": "Master Piece The Animation", "name_jp": "マスターピース THE ANIMATION", "image_url": "https://cdn.myanimelist.net/images/anime/1195/101107.jpg", "genres": ["Hentai"], "themes": [], "demographics": [], "studios": ["T-Rex"], "year": 2019, "episodes": 2, "source": "Manga", "score": 7.85, "synopsis": "After his parents passed away many years ago, Yuuji Enomoto was adopted into the family of his childhood friends Yuna and Nina Tsubakihara and their mother Mira. Now in high school and known as Yuuji Tsubakihara, he lives under the same roof as a pair of cute twins and a beautiful older woman. Although the shy, reserved Yuna and the outgoing, talented Nina are his family, Yuuji submits to his desires after witnessing Yuna turn down a confession. But as Yuuji and Yuna finally consummate their lifelong love, can Nina and Mira stand idly by?\n\n[Written by MAL Rewrite]"}, {"id": 6862, "name_en": "K-ON! Live House!", "name_jp": "けいおん! ライブハウス!", "image_url": "https://cdn.myanimelist.net/images/anime/9/15892.jpg", "genres": ["Comedy"], "themes": ["CGDCT", "Music", "School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2010, "episodes": 1, "source": "4-koma manga", "score": 7.85, "synopsis": "It is almost the end of the year, and Houkago Tea Time has been invited to participate in a live house on New Year's Eve! The iconic band members are Yui Hirasawa, the carefree guitarist who is enthusiastic to play music; Mio Akiyama, the shy bassist who gets embarrassed easily; Tsumugi Kotobuki, the gentle and sweet keyboardist who finds joy in normal activities; Ritsu Tainaka, the extroverted drummer who likes to tease Mio; and Azusa Nakano, the rhythm guitarist who is one year younger than the rest but slightly more mature.\n\nPerforming in the set gives the girls the rare opportunity to meet various people from different bands, including the one that invited them, Love Crysis. Will Houkago Tea Time be able to delight their audiences successfully?\n\n[Written by MAL Rewrite]"}, {"id": 16782, "name_en": "The Garden of Words", "name_jp": "言の葉の庭", "image_url": "https://cdn.myanimelist.net/images/anime/1597/112995.jpg", "genres": ["Award Winning", "Drama", "Romance"], "themes": ["Visual Arts"], "demographics": [], "studios": ["CoMix Wave Films"], "year": 2013, "episodes": 1, "source": "Original", "score": 7.85, "synopsis": "On a rainy morning in Tokyo, Takao Akizuki, an aspiring shoemaker, decides to skip class to sketch designs in a beautiful garden. This is where he meets Yukari Yukino, a beautiful yet mysterious woman, for the very first time. Offering to make her new shoes, Takao continues to meet with Yukari throughout the rainy season, and without even realizing it, the two are able to alleviate the worries hidden in their hearts just by being with each other. However, their personal struggles have not disappeared completely, and as the end of the rainy season approaches, their relationship will be put to the test.\n\n[Written by MAL Rewrite]"}, {"id": 33926, "name_en": "The King's Avatar", "name_jp": "全职高手", "image_url": "https://cdn.myanimelist.net/images/anime/1534/104725.jpg", "genres": ["Action"], "themes": ["Video Game"], "demographics": [], "studios": ["B.CMAY PICTURES"], "year": 2017, "episodes": 12, "source": "Web novel", "score": 7.85, "synopsis": "Widely regarded as a trailblazer and top-tier professional player in the online multiplayer game Glory, Ye Xiu is dubbed the \"Battle God\" for his skills and contributions to the game over the years. However, when forced to retire from the team and to leave his gaming career behind, he finds work at a nearby internet café. There, when Glory launches its tenth server, he throws himself into the game once more using a new character named \"Lord Grim.\"\n\nYe Xiu's early achievements on the new server immediately catch the attention of many players, as well as the big guilds, leaving them to wonder about the identity of this exceptional player. However, while he possesses ten years of experience and in-depth knowledge, starting afresh with neither sponsors nor a team in a game that has changed over the years presents numerous challenges. Along with talented new comrades, Ye Xiu once again dedicates himself to traversing the path to Glory's summit!\n\n[Written by MAL Rewrite]"}, {"id": 2158, "name_en": "Toward the Terra (TV)", "name_jp": "地球へ…", "image_url": "https://cdn.myanimelist.net/images/anime/1013/117558.jpg", "genres": ["Action", "Drama", "Sci-Fi"], "themes": ["Military", "Space", "Super Power"], "demographics": ["Shounen"], "studios": ["Tokyo Kids", "Minami Machi Bugyousho"], "year": 2007, "episodes": 24, "source": "Manga", "score": 7.85, "synopsis": "When humanity realized that the Earth, or Terra, had been polluted beyond fixing, the planet's governments united under a unique authoritarian system named Superior Domination (SD) and started to colonize space. Centuries later, mankind has given up its freedom in favor of the AI Grand Mother; when someone reaches the age of 14, their memories are wiped and they are automatically assigned a new role in society.\n\nJomy Marquis Shin, a sensitive young man raised by loving parents, is saved from this grim fate by Soldier Blue, the current leader of the Mu race—a new species of human beings blessed with psychic abilities. However, in the eyes of SD, the existence of people like Jomy threatens the new paradigm of humanity. Hunted down by the hounds of the government, Jomy finds solace in the welcoming arms of the Mu, who hide aboard the spaceship Shangri-La.\n\nNow the leader of the Mu resistance years later, Jomy embarks on a journey toward Terra to escape from SD's manhunt led by Keith Anyan, an ambitious and ruthless rising star of humanity. Will Jomy and his people manage to reach the cradle of humanity before Keith destroys their last hope?\n\n[Written by MAL Rewrite]"}, {"id": 6586, "name_en": "Yume-iro Pâtissière", "name_jp": "夢色パティシエール", "image_url": "https://cdn.myanimelist.net/images/anime/1369/154654.jpg", "genres": ["Gourmet"], "themes": ["School"], "demographics": ["Shoujo"], "studios": ["Studio Hibari"], "year": 2009, "episodes": 50, "source": "Manga", "score": 7.85, "synopsis": "Aside from her deep passion for eating cakes, the clumsy Ichigo Amano has never been successful at anything.  After an encounter with Henri Lucas, a famous patissier, Ichigo's amazing tasting abilities—although unbeknownst to her—are discovered.  Recognizing her talent, Henri offers her the chance to attend St. Marie Academy, a prestigious culinary school which specializes in desserts.\n\nIn spite of the fact that she is a beginner lacking skills, thanks to Henri's recommendation, Ichigo is placed in the topmost group with the \"Sweets Princes.\" The princes, famous throughout the school for their magical treats, are Sennosuke Andou, an expert in traditional Japanese sweets; Satsuki Hanabusa, with a flair for candied flowers; and Makoto Kashino, a gifted chocolatier. Ichigo and the Sweets Princes, each accompanied by their \"Sweets Spirits\"—fairies who make patissiers' dreams come true—all work towards their goals in the competitive world of sweets.\n\n[Written by MAL Rewrite]"}, {"id": 50634, "name_en": "Love Me: Kaede and Suzu The Animation", "name_jp": "らぶみー『楓と鈴』 THE ANIMATION", "image_url": "https://cdn.myanimelist.net/images/anime/1881/120300.jpg", "genres": ["Hentai"], "themes": [], "demographics": [], "studios": ["Studio 1st"], "year": 2022, "episodes": 3, "source": "Manga", "score": 7.85, "synopsis": "Kaede is the student council president and is also twin sisters with Suzu. Both are childhood friends with Hayato, of which all three are part of the student council. \nThrough their time together, Kaede and Suzu both both fell in love with Hayato. Both try to compete for Hayato's love and sexual favor."}, {"id": 3588, "name_en": "Soul Eater", "name_jp": "ソウルイーター", "image_url": "https://cdn.myanimelist.net/images/anime/1071/149486.jpg", "genres": ["Action", "Comedy", "Fantasy"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["Bones"], "year": 2008, "episodes": 51, "source": "Manga", "score": 7.85, "synopsis": "Death City is home to the famous Death Weapon Meister Academy (Shibusen), a technical academy headed by the Shinigami—Lord Death himself. Its mission: to raise Death Scythes for the Shinigami to wield against the many evils of their fantastical world. These Death Scythes, however, are not made from physical weapons; rather, they are born from human hybrids who have the ability to transform their bodies into Demon Weapons, and only after they have consumed the souls of 99 evil beings and one witch's soul.\n\nSoul Eater Evans, a Demon Scythe who only seems to care about what's cool, aims to become a Death Scythe with the help of his straight-laced wielder, or meister, Maka Albarn. The contrasting duo work and study alongside the hot headed Black☆Star and his caring weapon Tsubaki, as well as the Shinigami's own son, Death the Kid, an obsessive-compulsive dual wielder of twin pistols Patty and Liz.\n\nAs they take on missions to collect souls and protect the city from the world's threats, the Shibusen students work together under the snickering sun to become sounder in mind, body, and soul.\n\n[Written by MAL Rewrite]"}, {"id": 50739, "name_en": "The Angel Next Door Spoils Me Rotten", "name_jp": "お隣の天使様にいつの間にか駄目人間にされていた件", "image_url": "https://cdn.myanimelist.net/images/anime/1240/133638.jpg", "genres": ["Romance"], "themes": ["School"], "demographics": [], "studios": ["Project No.9"], "year": 2023, "episodes": 12, "source": "Light novel", "score": 7.85, "synopsis": "Mahiru Shiina is worthy of her nickname \"Angel\": she is a divine beauty loved by all, and she excels in both academics and athletics. Shiina lives in an entirely different world from Amane Fujimiya, her next-door neighbor. Despite living so close together, they have never spoken once. But their silence is broken when Fujimiya spots Shiina gloomily sitting on a swing amidst a heavy rainstorm and lends her his umbrella. \n\nWhen Fujimiya catches a cold the next day, Shiina wishes to return the favor for the umbrella by nursing him back to health. Believing that this would be their first and last interaction, he silently appreciates her kindness. However, Shiina—who cannot help but worry about Fujimiya's lack of tidiness and proper nutrition—begins to cook and clean for him. As the unlikely pair spend time together in Fujimiya's apartment, they explore the true nature of their relationship and the gentle emotions that emerge from it.\n\n[Written by MAL Rewrite]"}, {"id": 54829, "name_en": "Urusei Yatsura (2022) 2nd Season", "name_jp": "うる星やつら第2期", "image_url": "https://cdn.myanimelist.net/images/anime/1517/140830.jpg", "genres": ["Comedy", "Romance", "Sci-Fi"], "themes": [], "demographics": ["Shounen"], "studios": ["David Production"], "year": 2024, "episodes": 23, "source": "Manga", "score": 7.84, "synopsis": "Second season of Urusei Yatsura (2022)."}, {"id": 52505, "name_en": "Dark Gathering", "name_jp": "ダークギャザリング", "image_url": "https://cdn.myanimelist.net/images/anime/1346/138731.jpg", "genres": ["Horror", "Supernatural"], "themes": ["Gore"], "demographics": ["Shounen"], "studios": ["OLM"], "year": 2023, "episodes": 25, "source": "Manga", "score": 7.84, "synopsis": "Spirits have always been attracted to Keitarou Gentouga due to his strong spiritual presence. A traumatic encounter with one had caused his right hand to be cursed, forcing him to wear a glove at all times. Now a college freshman, Keitarou is trying to live as normal a life as possible. With the encouragement of his childhood friend Eiko Houzuki, he becomes a private tutor. Since he is at the top of his intake class, his first client is the tutoring firm's most promising student, Yayoi Houzuki.\n\nTo Keitarou's surprise, Yayoi is Eiko's cousin, and she possesses two pupils in each eye—allowing her to see into the spirit world and purposely seek vengeful spirits to exorcise. Ever since a car accident killed both her parents, Yayoi has been searching for the powerful spirit that kidnapped her mother's departed soul. Seeing that spirits are so attracted to Keitarou, she ropes him into helping her collect other evil spirits that will assist in her future fight. Although her quest is dangerous, Keitarou reluctantly decides to join her, even as each new spirit they meet has him questioning his resolve at every turn.\n\n[Written by MAL Rewrite]"}, {"id": 36593, "name_en": "Hug tto! Precure", "name_jp": "HuGっと！プリキュア", "image_url": "https://cdn.myanimelist.net/images/anime/1220/92883.jpg", "genres": ["Action"], "themes": ["Mahou Shoujo"], "demographics": [], "studios": ["Toei Animation"], "year": 2018, "episodes": 49, "source": "Original", "score": 7.84, "synopsis": "It's her first day at a new school, and the cheerful Hana Nono is ready to make a fresh start, dreaming of becoming mature and reliable—a far cry from her usual childish self. Although she makes a disastrous first impression, Hana soon befriends two of her classmates: the gentle and studious class representative Saaya Yakushiji, and a cool and mysterious girl Homare Kagayaki.\n\nThat night, a magical baby falls from the sky right onto Hana's balcony. She is immediately smitten with her and names her Hug-tan. Alongside Hug-tan is a snarky talking hamster, Hariham Harry, who mutters that Hana \"isn't the one\" before vanishing with the baby.\n\nThe next day, Hana believes the encounter to be a dream, until people all over the city begin collapsing and a giant monster appears, feeding on their energy. As Hana escapes, she spots Hug-tan and Harry under attack. Making a stand to protect them, she and Hug-tan suddenly both glow with energy and a heart-shaped gem forms.\n\nWith the crystal's power, Hana transforms into a hero called \"Cure Yell\" and defeats the monster. Surprised at Hana's power, Harry explains that he and Hug-tan came from a future frozen in time by the Criasu Corporation, the same organization that created the monster. Only the Future Crystals, manifestations of one's hopes and dreams, and the Precure they create have a chance at defeating Criasu and saving the future!\n\n[Written by MAL Rewrite]"}, {"id": 30885, "name_en": "Noragami Aragoto OVA", "name_jp": "ノラガミ OAD", "image_url": "https://cdn.myanimelist.net/images/anime/11/77510.jpg", "genres": ["Comedy", "Supernatural"], "themes": ["Mythology"], "demographics": ["Shounen"], "studios": ["Bones"], "year": 2015, "episodes": 2, "source": "Manga", "score": 7.84, "synopsis": "Yoru to Shinrenzoku Satsujin Jiken: Noragami Suspense Gekijou\nHiyori Iki goes on a skiing trip with her parents and happens to bump into Yato and Yukine. After a short while, they find the other gods who are there for a company vacation. But amidst all the fun, someone is plotting a heinous crime, and Yato is the primary target.\n\nIssho ni Shashin wo\nOn a different day, Yato’s been able to make a small fortune from his last job and decides to take Hiyori and Yukine to Capyper Land. Although she agrees without knowing the destination, will Hiyori actually enjoy the day considering what happened on her last visit?\n\n[Written by MAL Rewrite]"}, {"id": 26123, "name_en": "Seitokai Yakuindomo* OVA", "name_jp": "生徒会役員共＊ OAD", "image_url": "https://cdn.myanimelist.net/images/anime/5/79008.jpg", "genres": ["Comedy"], "themes": ["Gag Humor", "School"], "demographics": ["Shounen"], "studios": ["GoHands"], "year": 2014, "episodes": 10, "source": "4-koma manga", "score": 7.84, "synopsis": "OVAs of Seitokai Yakuindomo* which were either bundled with the limited editions of the manga volumes or as standalone OVAs."}, {"id": 36796, "name_en": "Owarimonogatari Second Season Recaps", "name_jp": "終わりに向かう物語; 阿良々木暦ノ物語", "image_url": "https://cdn.myanimelist.net/images/anime/9/88962.jpg", "genres": ["Comedy", "Mystery", "Supernatural"], "themes": ["Vampire"], "demographics": [], "studios": ["Shaft"], "year": 2017, "episodes": 2, "source": "Light novel", "score": 7.84, "synopsis": "Two recaps aired before the first episode of the day at the time Owarimonogatari 2nd Season was airing.\n\nEpisode 0.5 (aired before Mayoi Hell) - Owari ni Mukau Monogatari (Heading Towards the End of the Story): Araragi's voice actor Hiroshi Kamiya retells events from Koyomi Vamp, Mayoi Maimai, Shinobu Time, Yotsugi Doll, Koyomi Nothing and Koyomi Dead.\n\nEpisode 4.5 (aired before Ougi Dark) - Araragi Koyomi no Monogatari (Araragi Koyomi's Story): Hanekawa's voice actor Yui Horie looks back at Araragi's past year and what happened to him throughout the series."}, {"id": 37435, "name_en": "Carole & Tuesday", "name_jp": "キャロル&チューズデイ", "image_url": "https://cdn.myanimelist.net/images/anime/1611/96157.jpg", "genres": ["Drama", "Sci-Fi"], "themes": ["Music"], "demographics": [], "studios": ["Bones"], "year": 2019, "episodes": 24, "source": "Original", "score": 7.84, "synopsis": "It has been 50 years since mankind began its migration to the terraformed Mars, where they live in comfort due to advancements in AI. Carole lives in the metropolis of Alba City, working part-time by day and playing keyboard by night. Tuesday has run away from her home in Hershell City to escape the grip of her wealthy family, and instead hopes to pursue music with her acoustic guitar.\n\nAfter a fateful encounter, the two decide to perform music together. Up against the AI singers that dominate the music world, the two of them believe that together they can convey their feelings through their songs. Will hard work and luck be enough for the duo to create the biggest miracle that Mars has ever seen?\n\n[Written by MAL Rewrite]"}, {"id": 2605, "name_en": "Sayonara, Zetsubou-Sensei", "name_jp": "さよなら絶望先生", "image_url": "https://cdn.myanimelist.net/images/anime/5/6559.jpg", "genres": ["Comedy"], "themes": ["Gag Humor", "Parody", "School"], "demographics": ["Shounen"], "studios": ["Shaft"], "year": 2007, "episodes": 12, "source": "Manga", "score": 7.84, "synopsis": "Nozomu Itoshiki is a high school teacher so pessimistic that even the smallest of misfortunes can send him into a pit of raging despair; some of these \"catastrophes\" even lead to suicide attempts. Sayonara Zetsubou Sensei is a satirical slice-of-life comedy set in the modern day, covering various aspects of Japanese life and culture through Nozomu and his interactions with his students: Kiri Komori, a recluse who refuses to leave the school; Abiru Kobushi, an enigma who frequently arrives to class with severe and mysterious injuries; the hyper-optimistic Kafuuka Fuura, Nozomu's polar opposite; and several other unusual girls, all of whom are just as eccentric as their teacher.\n\n[Written by MAL Rewrite]"}, {"id": 8063, "name_en": "Sekaiichi Hatsukoi OVA", "name_jp": "世界一初恋 OVA", "image_url": "https://cdn.myanimelist.net/images/anime/11/35863.jpg", "genres": ["Boys Love"], "themes": [], "demographics": [], "studios": ["Studio Deen"], "year": 2011, "episodes": 2, "source": "Manga", "score": 7.84, "synopsis": "Two OVA episodes featuring additional stories. \n\nEpisode 0: Ritsu and Takano used to date back in high school, but broke up due to a misunderstanding. This is that story. \n\nEpisode 12.5: Hatori and Chiaki go to visit the latter's family. It's really awkward in many, many ways."}, {"id": 24471, "name_en": "Hoozuki no Reitetsu OVA", "name_jp": "鬼灯の冷徹 OAD", "image_url": "https://cdn.myanimelist.net/images/anime/1531/138993.jpg", "genres": ["Comedy", "Fantasy"], "themes": [], "demographics": ["Seinen"], "studios": ["Studio Deen", "Wit Studio"], "year": 2015, "episodes": 4, "source": "Manga", "score": 7.84, "synopsis": "Bundled with the limited editions of 17th, 18th, 19th, and 24th volumes of the Hoozuki no Reitetsu manga."}, {"id": 23225, "name_en": "Yuru Yuri Nachuyachumi!", "name_jp": "ゆるゆり なちゅやちゅみ！", "image_url": "https://cdn.myanimelist.net/images/anime/2/73281.jpg", "genres": ["Comedy", "Girls Love"], "themes": ["CGDCT", "Gag Humor"], "demographics": [], "studios": ["TYO Animations"], "year": 2015, "episodes": 1, "source": "Manga", "score": 7.84, "synopsis": "Even during summer break, the Amusement Club lives up to its name! When Akari Akaza, Kyouko Toshinou, Chinatsu Yoshikawa, and Yui Funami find a camping tent, they decide to put it to good use by spending the weekend outside in the mountains. When some student council members catch wind of this, they are invited to come along as well.\n\nAfter a very long journey, the group arrives at their destination and is ready to make the most of their trip. They engage in classic camping activities, such as outdoor cooking and tests of courage, and finish it all off with a visit to the hot springs. An extravagant trip like this doesn't happen every day, but the Amusement Club is sure to savor each moment.\n\n[Written by MAL Rewrite]"}, {"id": 41389, "name_en": "Tonikawa: Over The Moon For You", "name_jp": "トニカクカワイイ", "image_url": "https://cdn.myanimelist.net/images/anime/1613/108722.jpg", "genres": ["Comedy", "Romance"], "themes": [], "demographics": ["Shounen"], "studios": ["Seven Arcs"], "year": 2020, "episodes": 12, "source": "Manga", "score": 7.84, "synopsis": "Nasa Yuzaki is determined to leave his name in the history books. Ranking first in the national mock exam and aiming for a distinguished high school, he is certain that he has his whole life mapped out. However, fate is a fickle mistress. On his way home one snowy evening, Nasa's eyes fall upon a peerless beauty across the street. Bewitched, Nasa tries to approach her—only to get blindsided by an oncoming truck. \n\nThankfully, his life is spared due to the girl's swift action. Bleeding by the side of an ambulance, he watches as the girl walks away under the moonlight—reminiscent of Princess Kaguya leaving for the moon. Refusing to let this chance meeting end, he forces his crippled body to chase after her and asks her out. Surprised by his foolhardiness and pure resolve, the girl accepts his confession under a single condition: they can only be together if he marries her!\n\n[Written by MAL Rewrite]"}, {"id": 1376, "name_en": "Ojamajo Doremi Na-i-sho", "name_jp": "おジャ魔女どれみ ナ・イ・ショ", "image_url": "https://cdn.myanimelist.net/images/anime/1986/99238.jpg", "genres": ["Comedy"], "themes": ["Mahou Shoujo"], "demographics": [], "studios": ["Toei Animation"], "year": 2004, "episodes": 13, "source": "Original", "score": 7.84, "synopsis": "Na-i-sho is a 13 episode OAV series detailing the summer between the ~motto~ and the Dokkaan season. It is a bit more mature than the TV seasons, and looks to develop each of the five witches romantically. Hana is back to a baby, and the mahou shop is a bakery. \n\n(Source: ANN)"}, {"id": 48849, "name_en": "Sonny Boy", "name_jp": "Sonny Boy (サニーボーイ)", "image_url": "https://cdn.myanimelist.net/images/anime/1509/117149.jpg", "genres": ["Award Winning", "Mystery"], "themes": ["Isekai", "Super Power"], "demographics": [], "studios": ["Madhouse"], "year": 2021, "episodes": 12, "source": "Original", "score": 7.84, "synopsis": "Thirty-six students find themselves and their school building suddenly adrift in a void-like dimension. When supernatural powers awaken in some of them, a sense of detachment begins to divide the group. Despite the student council's attempts to impose order, they clash with the students possessing special abilities, who rebel against their strict control.\n\nThis conflict leads them to discover that this world has its own set of rules—and following them is necessary for survival. After one of the students decides to take a leap of faith, the school switches dimensions once again. While they deal with the unique challenges and circumstances that each world presents, the students must unravel the mysterious phenomenon and find a way back home.\n\n[Written by MAL Rewrite]"}, {"id": 48830, "name_en": "Free! the Final Stroke: The Second Volume", "name_jp": "劇場版 Free!-the Final Stroke-", "image_url": "https://cdn.myanimelist.net/images/anime/1417/124151.jpg", "genres": ["Sports"], "themes": ["School"], "demographics": [], "studios": ["Kyoto Animation"], "year": 2022, "episodes": 1, "source": "Original", "score": 7.84, "synopsis": "After his first appearance on the competitive world stage, Haruka is held captive by the swimming of the unmistakable champion, Albert, and feels like he's lost his way. With the current state of things, his swimming and what the water means to him are called into question. Haruka begins to sink to the bottom of the murky water as he struggles on his own.\n\nPushed on by a restless urge in his heart, Haruka purposefully puts himself through a rigorous and intensive training regiment, relentlessly devoting himself to practice. In his heart, Haruka knows that he's pushing himself beyond his limits both physically and mentally. His friends can do nothing but watch over and have faith in him.\n\nMemories of Albert's cold, dry swimming keep replaying in his mind.\nI have to get faster, I have to become stronger.\nI have to compete against Albert again.\n\nHaruka unleashes his stroke to cut through the heavy, indifferent water.\nHis friends draw closer to watch.\n\nWhat scenery will Haruka see at the end of this struggle＊──!\n\n(Source: Free! website, Translated)"}, {"id": 49828, "name_en": "Mobile Suit Gundam: The Witch from Mercury", "name_jp": "機動戦士ガンダム 水星の魔女", "image_url": "https://cdn.myanimelist.net/images/anime/1440/127624.jpg", "genres": ["Sci-Fi"], "themes": ["Mecha", "School", "Space"], "demographics": [], "studios": ["Sunrise"], "year": 2022, "episodes": 12, "source": "Original", "score": 7.84, "synopsis": "Suletta Mercury leaves her planet and enters the Asticassia School of Technology at the behest of her mother. There, right and wrong are determined through duels between students, and the top-ranking duelist will receive Miorine Rembran as their fiancée—this prize being decided by Miorine's father.\n\nWhen Guel Jeturk, the best pilot in school and Miorine's current fiancé, demands that his betrothed move in with him, Suletta disapproves, so Guel challenges her. Although she emerges as champion, Suletta is subsequently detained on suspicion of piloting a forbidden type of mobile suit—a GUND-ARM, or \"Gundam\"—which results in her victory being voided.\n\nMiorine refuses to accept any more injustices and proposes another duel with even higher stakes. Now, Suletta must triumph a second time, otherwise she will be expelled and the Gundam Aerial that means so much to her will be destroyed.\n\n[Written by MAL Rewrite]"}, {"id": 49889, "name_en": "Tsukimichi -Moonlit Fantasy- Season 2", "name_jp": "月が導く異世界道中 第二幕", "image_url": "https://cdn.myanimelist.net/images/anime/1794/142621.jpg", "genres": ["Action", "Adventure", "Comedy", "Fantasy"], "themes": ["Isekai"], "demographics": [], "studios": ["J.C.Staff"], "year": 2024, "episodes": 25, "source": "Light novel", "score": 7.84, "synopsis": "Despite a rough start, Makoto Misumi's life in another world takes a positive turn when he meets more demihumans who recognize his presence. Now as the leader of a fast-growing demihuman community, Makoto wishes to make human society more accepting of them and thus builds a business showcasing the demihumans' abilities. To extend his reach further, Makoto enrolls at the Rotsgard Academy to learn magic and expand his business venture there.\n\nMeanwhile, the goddess who summoned and forsaken Makoto had brought two other people to her world shortly after sending him away, making them heroes of their respective nations. Given enough time, Makoto's path will intertwine with both of these heroes, which may cause the world to take a drastic direction.\n\n[Written by MAL Rewrite]"}, {"id": 9513, "name_en": "Beelzebub", "name_jp": "べるぜバブ", "image_url": "https://cdn.myanimelist.net/images/anime/3/28013.jpg", "genres": ["Action", "Comedy", "Supernatural"], "themes": ["Childcare", "Delinquents", "Mythology", "School"], "demographics": ["Shounen"], "studios": ["Pierrot Plus"], "year": 2011, "episodes": 60, "source": "Manga", "score": 7.84, "synopsis": "Ishiyama High is a school populated entirely by delinquents, where nonstop violence and lawlessness are the norm. However, there is one universally acknowledged rule—don't cross first year student Tatsumi Oga, Ishiyama's most vicious fighter.\n\nOne day, Oga is by a riverbed when he encounters a man floating down the river. After being retrieved by Oga, the man splits down the middle to reveal a baby, which crawls onto Oga's back and immediately forms an attachment to him. Though he doesn't know it yet, this baby is named Kaiser de Emperana Beelzebub IV, or \"Baby Beel\" for short—the son of the Demon Lord!\n\nAs if finding the future Lord of the Underworld isn't enough, Oga is also confronted by Hildegard, Beel's demon maid. Together they attempt to raise Baby Beel—although surrounded by juvenile delinquents and demonic powers, the two of them may be in for more of a challenge than they can imagine.\n\n[Written by MAL Rewrite]"}, {"id": 429, "name_en": "Kaleido Star: Legend of Phoenix - The Layla Hamilton Story", "name_jp": "カレイドスター Legend of Phoenix ～レイラ・ハミルトン物語～", "image_url": "https://cdn.myanimelist.net/images/anime/7/28863.jpg", "genres": ["Comedy", "Drama"], "themes": [], "demographics": [], "studios": ["Gonzo"], "year": 2005, "episodes": 1, "source": "Original", "score": 7.84, "synopsis": "Layla is going to perform Phoenix in Broadway. Playing this important role, she sets out on a journey by bicycle alone without leaving any message in order to find out what she's missing inside."}, {"id": 42745, "name_en": "The Demon Girl Next Door 2", "name_jp": "まちカドまぞく 2丁目", "image_url": "https://cdn.myanimelist.net/images/anime/1036/121135.jpg", "genres": ["Comedy"], "themes": ["CGDCT", "Mahou Shoujo", "School"], "demographics": [], "studios": ["J.C.Staff"], "year": 2022, "episodes": 12, "source": "4-koma manga", "score": 7.84, "synopsis": "Second season of Machikado Mazoku."}, {"id": 40262, "name_en": "Haikyu!! Land vs. Air", "name_jp": "ハイキュー!! 陸VS空", "image_url": "https://cdn.myanimelist.net/images/anime/1527/102671.jpg", "genres": ["Sports"], "themes": ["School", "Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2020, "episodes": 2, "source": "Manga", "score": 7.84, "synopsis": "An intense battle rages on at the Tokyo Qualifiers for the three remaining spots in the national volleyball competition. Nekoma High School, Fukurodani High School, Nohebi Academy, and Itachiyama Academy all passionately strive to participate in the tournament. Despite various issues on the court, Nekoma especially wishes to prove they are worthy of moving on to the national level. \n\nAs the teams aim to secure their place by overcoming both their opponents and their own weaknesses, the Tokyo Qualifiers determine which teams will reign victorious and join the national competition.\n\n[Written by MAL Rewrite]"}, {"id": 25161, "name_en": "One Piece 3D2Y: Overcoming Ace's Death! Luffy's Pledge to His Friends", "name_jp": "ワンピース〝3D2Y〟 エースの死を越えて！　ルフィ仲間との誓い", "image_url": "https://cdn.myanimelist.net/images/anime/3/64755.jpg", "genres": ["Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Toei Animation"], "year": 2014, "episodes": 1, "source": "Manga", "score": 7.84, "synopsis": "After suffering great personal loss during the battle of Marineford, Monkey D. Luffy finds himself stranded on Rusukaina, a treacherous island crawling with huge and dangerous creatures. There, he has committed himself to a two-year stretch of training to learn \"Haki,\" the energy that combatants can use to grant themselves a variety of abilities in battle.\n\nLuffy is jerked away from his otherwise peaceful training by the abduction of Sandersonia and Marigold, sisters of his friend and ally Boa Hancock. With Boa's aid, Luffy seeks to track down the infamous pirate responsible for the kidnapping, a man whose recent prison break is the result of Luffy's own reckless actions. Luffy must use his developing grasp of Haki to defeat this new foe while coming to terms with his overbearing grief in the process.\n\n[Written by MAL Rewrite]"}, {"id": 22, "name_en": "The Prince of Tennis", "name_jp": "テニスの王子様", "image_url": "https://cdn.myanimelist.net/images/anime/6/21624.jpg", "genres": ["Sports"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["Trans Arts"], "year": 2001, "episodes": 178, "source": "Manga", "score": 7.84, "synopsis": "At the request of his father, tennis prodigy Ryouma Echizen has returned from America and is ready to take the Japanese tennis scene by storm. Aiming to become the best tennis player in the country, he enrolls in Seishun Academy—home to one of the best middle school tennis teams in Japan. \n\nAfter Ryouma catches the captain's eye, he finds himself playing for a spot on the starting lineup in the intra-school ranking matches despite only being a freshman. Due to his age, the rest of the Seishun Boys' Tennis Team are initially reluctant to accept him, but his skill and determination convinces them to let him in.\n\nArmed with their new \"super rookie,\" Seishun sets out to claim a spot in the National Tournament, hoping to take the coveted title for themselves. In order to do so, the team must qualify by playing through the Tokyo Prefectural and Kanto Regionals. Yet, the road ahead of them is shared by a plethora of strong schools, each playing tennis in unique ways for their own reasons. Ryouma and his teammates must learn to cooperate if they want to become the champions they aspire to be.\n\n[Written by MAL Rewrite]"}, {"id": 61784, "name_en": "Jade Dynasty 3rd Season", "name_jp": "诛仙 第三季", "image_url": "https://cdn.myanimelist.net/images/anime/1953/150409.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Cloud Art"], "year": 2025, "episodes": 26, "source": "Web novel", "score": 7.83, "synopsis": "The Beast God is born, blood bathing the world, Daoxuan stands up to fight, the forces of good and evil usher in a huge change, Gui Li and Xueqi once again stand at the intersection of fate. After the catastrophe, people's hearts and artifacts are both on the verge of being shattered...\n\n(Source: Tencent, translated)"}, {"id": 58555, "name_en": "Fabulous Beasts 3rd Season", "name_jp": "有兽焉 第三季", "image_url": "https://cdn.myanimelist.net/images/anime/1102/142342.jpg", "genres": ["Comedy", "Fantasy"], "themes": [], "demographics": [], "studios": ["Fenz"], "year": 2024, "episodes": 12, "source": "Manga", "score": 7.83, "synopsis": null}, {"id": 56511, "name_en": "Hatsukoi Jikan", "name_jp": "初恋時間.", "image_url": "https://cdn.myanimelist.net/images/anime/1163/138201.jpg", "genres": ["Hentai"], "themes": [], "demographics": [], "studios": ["T-Rex"], "year": 2023, "episodes": 6, "source": "Manga", "score": 7.83, "synopsis": "Rin is an ordinary student who has, so far, spent his time studying rather than with love and friends. This only changes when he meets the lovely Ena, who seems to live in a completely different world. Rumour has it that she has several boyfriends at the same time and has slept with over 100 men, but she's not exactly the smartest either, which is why Rin agrees to tutor her. Soon, his lessons are crowned with success, and as a thank you for improving her grades, Ena has come up with something special for the nerd..."}, {"id": 53859, "name_en": "All Saints Street 4", "name_jp": "万圣街4", "image_url": "https://cdn.myanimelist.net/images/anime/1567/137044.jpg", "genres": ["Comedy", "Fantasy"], "themes": ["Mythology", "Urban Fantasy"], "demographics": [], "studios": ["Fenz", "HMCH"], "year": 2023, "episodes": 12, "source": "Web manga", "score": 7.83, "synopsis": "Fourth season of Wan Sheng Jie."}, {"id": 14175, "name_en": "Hanasaku Iroha the Movie: Home Sweet Home", "name_jp": "劇場版 花咲くいろは HOME SWEET HOME", "image_url": "https://cdn.myanimelist.net/images/anime/9/70701.jpg", "genres": ["Drama"], "themes": ["Workplace"], "demographics": [], "studios": ["P.A. Works"], "year": 2013, "episodes": 1, "source": "Original", "score": 7.83, "synopsis": "Ohana Matsumae has been working at Kissui Inn as a waitress for a while now. However, she realizes that she is starting to lose her desire to sparkle, having grown accustomed to the routines of her job. As this was a desire she had when she first moved to the inn, the realization bothers her. While having Yuina Wakura—Ohana's classmate, friend, and the daughter of rival Fukuya Inn's owner—under her as an apprentice, Ohana stumbles upon some old archives that mention her mother, Satsuki. Ohana does not know much about her mother, but these archives could shed some light on her past.\n\nBesides learning more about her mother, it is business as usual at Kissui Inn—though with a couple of challenges to test Ohana and the staff of the inn.\n\n[Written by MAL Rewrite]"}, {"id": 32871, "name_en": "Winter Cup Highlights Episode 3 – Winter Cup Highlights -Crossing the Door-", "name_jp": "劇場版「黒子のバスケ」ウインターカップ総集編～扉の向こう～", "image_url": "https://cdn.myanimelist.net/images/anime/7/80729.jpg", "genres": ["Comedy", "Sports"], "themes": ["Team Sports"], "demographics": ["Shounen"], "studios": ["Production I.G"], "year": 2016, "episodes": 1, "source": "Manga", "score": 7.83, "synopsis": "The Winter Cup finals have arrived, and the Seirin High School basketball team braces itself for the game against its indomitable opponent, Rakuzan High School. With the former Generation of Miracles leader Seijuurou Akashi as their captain, Rakuzan has established itself as the unyielding emperor of the court. Akashi's innate ability, known as the Emperor Eye, allows him to predict his enemies' next move and meticulously dismantle each play, further cementing his absolute authority over the court. Seirin's \"light and shadow\" duo, Taiga Kagami and Tetsuya Kuroko, now find themselves given the ultimate test of teamwork in which they must play with the strongest synergy possible to break through Akashi's omniscience. \n\n[Written by MAL Rewrite]"}, {"id": 8408, "name_en": "Durarara!! Specials", "name_jp": "デュラララ!!", "image_url": "https://cdn.myanimelist.net/images/anime/4/28684.jpg", "genres": ["Action", "Comedy", "Supernatural"], "themes": [], "demographics": [], "studios": ["Brain's Base"], "year": 2010, "episodes": 2, "source": "Light novel", "score": 7.83, "synopsis": "Celty Sturluson is tasked to deliver a suspicious red handbag as part of her courier duties—the problem is: it is being sought by several organizations. As she makes her way through Ikebukuro toward the place the bag is supposed to be brought to, she is chased by mysterious men speaking a foreign language, and her package ends up dragging many of the city's residents into the conflict.\n\nSubsequently, famous actor Yuuhei Hanejima has just arrived in Ikebukuro as part of a special TV program, searching for the best couple to give them a chance to appear in one of his movies. However, Yuuhei Hanejima is actually a stage name for Kasuka Heiwajima, Shizuo's younger brother, and when an anonymous internet user threatens to kill the superstar, this user learns the weight of what that relationship means. Moreover, Shizuo discovers that the one responsible for the attempted attack is the meddlesome pest that he loathes with a burning passion.\n\n[Written by MAL Rewrite]"}, {"id": 36106, "name_en": "Attack on Titan: Lost Girls", "name_jp": "進撃の巨人 LOST GIRLS", "image_url": "https://cdn.myanimelist.net/images/anime/3/89417.jpg", "genres": ["Action", "Drama"], "themes": [], "demographics": [], "studios": ["Wit Studio"], "year": 2017, "episodes": 3, "source": "Novel", "score": 7.83, "synopsis": "Wall Sina, Goodbye\nAnnie Leonhart has a job to do—and a resulting absence that must stay off her record at all costs. With no one else to turn to, she asks her comrade Hitch Dreyse to cover for her. She agrees but puts forward a single condition: Annie must solve the fruitless missing person case Hitch was assigned. The case revolves around Carly Stratmann, a university graduate and the daughter of wealthy businessman Elliot Stratmann. With only a single day to solve the case and the underground of the Stohess District crawling with thugs, Annie must put her all into finding this girl. Yet, every answer she uncovers only leads to further questions—how has the illegal drug coderoin found its way to Stohess, what is Elliot hiding, and where has Carly disappeared to?\n\nLost in the Cruel World\nWith worry for Eren Yeager gripping her heart, Mikasa Ackerman begins to remember. She remembers her conversations with Armin Arlert, her concern for her friends, and most painfully, the time she had almost lost everything. As fear takes control, she begins to experience an alternate version of her past—some things can be changed, but are there events so inescapable that she cannot even prevent them in her dreams?\n\n[Written by MAL Rewrite]"}, {"id": 3604, "name_en": "Hidamari Sketch x 365", "name_jp": "ひだまりスケッチ×365", "image_url": "https://cdn.myanimelist.net/images/anime/1571/134092.jpg", "genres": ["Slice of Life"], "themes": ["CGDCT", "Iyashikei", "School", "Visual Arts"], "demographics": [], "studios": ["Shaft"], "year": 2008, "episodes": 13, "source": "4-koma manga", "score": 7.83, "synopsis": "After passing her entrance exam, Yuno enrolls at Yamabuki High School as part of an art degree. She stays at the Hidamari Apartments along with best friend Miyako, and seniors Hiro and Sae. Each episode follows a day in her life as she mingles with her friends, close peers, overly hyper teachers and closely follows the mundane lives of the high school girls.\n\n(Source: ANN)"}, {"id": 6582, "name_en": "Tentai Senshi Sunred 2nd Season", "name_jp": "天体戦士サンレッド", "image_url": "https://cdn.myanimelist.net/images/anime/7/15051.jpg", "genres": ["Comedy"], "themes": ["Gag Humor", "Parody", "Super Power"], "demographics": ["Seinen"], "studios": ["AIC ASTA"], "year": 2009, "episodes": 26, "source": "Manga", "score": 7.83, "synopsis": "The second season of Tentai Senshi Sunred."}, {"id": 6505, "name_en": "There She Is!!", "name_jp": "떳다 그녀!!", "image_url": "https://cdn.myanimelist.net/images/anime/2/29995.jpg", "genres": ["Comedy", "Romance"], "themes": ["Anthropomorphic"], "demographics": [], "studios": [], "year": 2003, "episodes": 5, "source": "Original", "score": 7.83, "synopsis": "The plot revolves around Doki, a female rabbit, meeting, falling in love with, and chasing Nabi, a male cat, in a world where love between the two species is socially unacceptable. The theme of the short is that all love can be accepted and has a chance. Nabi attempts to cure Doki's infatuation for him, but after seeing the extent of her love, he gives in and finds something he can appreciate in her.\n\n(Source: Wikipedia)"}, {"id": 32380, "name_en": "KonoSuba: God's Blessing on This Wonderful World! - God's Blessing on This Wonderful Choker!", "name_jp": "この素晴らしい世界に祝福を！ 第11話 この素晴らしいチヨーカーに祝福を！", "image_url": "https://cdn.myanimelist.net/images/anime/1649/98516.jpg", "genres": ["Comedy", "Fantasy"], "themes": ["Isekai", "Parody"], "demographics": [], "studios": ["Studio Deen"], "year": 2016, "episodes": 1, "source": "Light novel", "score": 7.83, "synopsis": "While exploring Wiz's magic shop with his party, Kazuma Satou finds a magical wish-granting choker and decides to try it on. Only then does Wiz tell him that the choker strangles its wearer to death in four days unless their desires are fulfilled. This wouldn't be a problem if Kazuma knew what his wish was. Fearing for Kazuma's life, Aqua, Megumin, and Lalatina Ford \"Darkness\" Dustiness all agree to do his bidding in order to satisfy his desires and hopefully grant his wish, no matter what he asks for.\n\n[Written by MAL Rewrite]"}, {"id": 20371, "name_en": "Sekaiichi Hatsukoi Movie: Yokozawa Takafumi no Baai", "name_jp": "劇場版 世界一初恋 横澤隆史の場合", "image_url": "https://cdn.myanimelist.net/images/anime/11/65927.jpg", "genres": ["Boys Love", "Comedy", "Drama"], "themes": ["Childcare", "Workplace"], "demographics": [], "studios": ["Studio Deen"], "year": 2014, "episodes": 1, "source": "Light novel", "score": 7.83, "synopsis": "Movie adaptation of the Sekaiichi Hatsukoi: Yokozawa Takafumi no Baai BL light novel."}, {"id": 31741, "name_en": "Magi: Adventure of Sinbad", "name_jp": "マギ シンドバッドの冒険", "image_url": "https://cdn.myanimelist.net/images/anime/10/78783.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": [], "demographics": ["Shounen"], "studios": ["Lay-duce"], "year": 2016, "episodes": 13, "source": "Manga", "score": 7.83, "synopsis": "In the small, impoverished Tison Village of the Parthevia Empire, a boy, Sinbad, is born to the jaded ex-soldier Badr and his kind-hearted wife Esra. His birth creates a radiant surge throughout the rukh, a declaration of a singularity to those who stand at the pinnacle of magical might: the \"Child of Destiny\" is here. Despite his country being plagued by economic instability and the repercussions of war, Sinbad leads a cheerful life—until a stranger's arrival shatters his peaceful world, and tragedy soon befalls him.\n\nYears later, mysterious edifices called \"dungeons\" have been erected all over the world. Rumored to contain great power and treasures, these dungeons piqued the interest of adventurers and armies alike; though to this day, none have returned therefrom. Sinbad, now 14, has grown into a charming and talented young boy. Inspired by the shocking events of his childhood and by his father's words, he yearns to begin exploring the world beyond his village. As though orchestrated by fate, Sinbad meets an enigmatic traveler named Yunan. Stirred by Sinbad's story and ambitions, Yunan directs him to a dungeon which he claims holds the power Sinbad needs to achieve his goals—the \"power of a king.\"\n\nMagi: Sinbad no Bouken tells the epic saga of Sinbad's early life as he travels the world, honing his skill and influence, while gathering allies and power to become the High King of the Seven Seas.\n\n[Written by MAL Rewrite]"}, {"id": 4087, "name_en": "Michiko & Hatchin", "name_jp": "ミチコとハッチン", "image_url": "https://cdn.myanimelist.net/images/anime/10/59535.jpg", "genres": ["Action", "Adventure"], "themes": [], "demographics": [], "studios": ["Manglobe"], "year": 2008, "episodes": 22, "source": "Original", "score": 7.83, "synopsis": "Under the unrelenting heat of the South American sun, hardened criminal Michiko Malandro breaks out of a high security prison for the fourth time in search of a man from her past. Michiko finds a clue in the form of Hana Morenos, a young girl trapped under the fists of her abusive foster family. In her powerlessness, Hana fantasizes about the day when she is finally whisked away from her captors by her very own Prince Charming. Little does she know that her fated prince would turn out to be the buxom and husky convict who charges in atop a stolen motorbike, claiming to be her mother.\n\nThe unlikely duo chase down their dreams in the sun-drenched land of Diamandra, navigating through the cacophony of betrayal, poverty, and child exploitation rings hiding in plain sight. However, wind of Michiko's manhunt soon reaches the ears of criminal syndicate Monstro Preto, and a storm of gang warfare begins brewing over the horizon…\n\nMichiko to Hatchin is the story of vibrant people and their clashing agendas, and of all the unlikely human connections drawn together by one elusive man. \n\n[Written by MAL Rewrite]"}, {"id": 15039, "name_en": "Anohana: The Flower We Saw That Day The Movie", "name_jp": "劇場版 あの日見た花の名前を僕達はまだ知らない。", "image_url": "https://cdn.myanimelist.net/images/anime/5/49993.jpg", "genres": ["Drama", "Supernatural"], "themes": [], "demographics": [], "studios": ["A-1 Pictures"], "year": 2013, "episodes": 1, "source": "Original", "score": 7.83, "synopsis": "A year after their deceased friend Honma Meiko appeared to them, Jinta Yadomi and the other members of the Super Peace Busters decide to write letters in her memory. Attempting to enjoy their summer together, they reminisce about their time together before and after her death.\n\nAnoHana. Movie retells the main events of the parent story in the perspective of each member of the Super Peace Busters.\n\n[Written by MAL Rewrite]"}, {"id": 49784, "name_en": "Welcome to Demon School! Iruma-kun Season 3", "name_jp": "魔入りました！入間くん", "image_url": "https://cdn.myanimelist.net/images/anime/1688/128720.jpg", "genres": ["Comedy", "Fantasy"], "themes": ["School"], "demographics": ["Shounen"], "studios": ["Bandai Namco Pictures"], "year": 2022, "episodes": 21, "source": "Manga", "score": 7.83, "synopsis": "Following their heroic efforts at Walter Park, the students of the misfit class return to Babyls Demon School after their summer vacation. What awaits them is not only adoration and admiration but also the shocking revelation that, in order to stay in the luxurious Royal One classroom, the entire class must be promoted to Dalet rank before entering the second year.\n\nAs the Harvest and Music Festivals are right around the corner, there seem to be ample opportunities to rank up. Doing so will not be simple, however, as no class thus far has managed to accomplish such a feat. Hoping to give the misfit class a chance to achieve the improbable, the school appoints special tutors to aid in confronting the challenges that lie ahead.\n\nWith his sights set beyond Dalet, Iruma Suzuki decides to take strides toward the goal of ranking up, starting with gaining acknowledgement from his special tutor: the short-tempered and selfish Bachiko Barbatos.\n\n[Written by MAL Rewrite]"}, {"id": 31964, "name_en": "My Hero Academia", "name_jp": "僕のヒーローアカデミア", "image_url": "https://cdn.myanimelist.net/images/anime/10/78745.jpg", "genres": ["Action"], "themes": ["School", "Super Power"], "demographics": ["Shounen"], "studios": ["Bones"], "year": 2016, "episodes": 13, "source": "Manga", "score": 7.83, "synopsis": "The appearance of \"quirks,\" newly discovered super powers, has been steadily increasing over the years, with 80 percent of humanity possessing various abilities from manipulation of elements to shapeshifting. This leaves the remainder of the world completely powerless, and Izuku Midoriya is one such individual.\n\nSince he was a child, the ambitious middle schooler has wanted nothing more than to be a hero. Izuku's unfair fate leaves him admiring heroes and taking notes on them whenever he can. But it seems that his persistence has borne some fruit: Izuku meets the number one hero and his personal idol, All Might. All Might's quirk is a unique ability that can be inherited, and he has chosen Izuku to be his successor!\n\nEnduring many months of grueling training, Izuku enrolls in UA High, a prestigious high school famous for its excellent hero training program, and this year's freshmen look especially promising. With his bizarre but talented classmates and the looming threat of a villainous organization, Izuku will soon learn what it really means to be a hero.\n\n[Written by MAL Rewrite]"}, {"id": 42249, "name_en": "Tokyo Revengers", "name_jp": "東京リベンジャーズ", "image_url": "https://cdn.myanimelist.net/images/anime/1839/122012.jpg", "genres": ["Action", "Drama"], "themes": ["Delinquents", "Time Travel"], "demographics": ["Shounen"], "studios": ["LIDENFILMS"], "year": 2021, "episodes": 24, "source": "Manga", "score": 7.83, "synopsis": "Takemichi Hanagaki's second year of middle school was the highest point in his life. He had respect, a gang of friends he could count on, and even a girlfriend. But that was twelve years ago. Today, he's a nobody: a washed-up nonentity made fun of by children and always forced to apologize to his younger boss. A sudden news report on the Tokyo Manji Gang's cruel murder of the only girlfriend he ever had alongside her brother only adds insult to injury. Half a second before a train ends his pitiful life for good, Takemichi flashes back to that same day 12 years ago, when he was still dating Hinata Tachibana.\n\nAfter being forced to relive the very same day that began his downward spiral, Takemichi meets Hinata's younger brother. Without thinking, he admits to his seeming death before flashing back to the past. Takemichi urges him to protect his sister before inexplicably returning to the future. Miraculously, he is not dead. Stranger still, the future has changed. It seems as though Takemichi can alter the flow of time. Given the chance to prevent his ex-girlfriend's tragic death at the hands of the Tokyo Manji Gang, Takemichi decides to fly through time to change the course of the future.\n\n[Written by MAL Rewrite]"}, {"id": 35507, "name_en": "Classroom of the Elite", "name_jp": "ようこそ実力至上主義の教室へ", "image_url": "https://cdn.myanimelist.net/images/anime/5/86830.jpg", "genres": ["Drama", "Suspense"], "themes": ["Psychological", "School"], "demographics": [], "studios": ["Lerche"], "year": 2017, "episodes": 12, "source": "Light novel", "score": 7.83, "synopsis": "On the surface, Koudo Ikusei Senior High School is a utopia. The students enjoy an unparalleled amount of freedom, and it is ranked highly in Japan. However, the reality is less than ideal. Four classes, A through D, are ranked in order of merit, and only the top classes receive favorable treatment.\n\nKiyotaka Ayanokouji is a student of Class D, where the school dumps its worst. There he meets the unsociable Suzune Horikita, who believes she was placed in Class D by mistake and desires to climb all the way to Class A, and the seemingly amicable class idol Kikyou Kushida, whose aim is to make as many friends as possible.\n\nWhile class membership is permanent, class rankings are not; students in lower ranked classes can rise in rankings if they score better than those in the top ones. Additionally, in Class D, there are no bars on what methods can be used to get ahead. In this cutthroat school, can they prevail against the odds and reach the top?\n\n[Written by MAL Rewrite]"}, {"id": 42091, "name_en": "Attack on Titan: Chronicle", "name_jp": "進撃の巨人 〜クロニクル〜", "image_url": "https://cdn.myanimelist.net/images/anime/1786/110717.jpg", "genres": ["Action", "Drama", "Suspense"], "themes": ["Gore", "Military", "Survival"], "demographics": ["Shounen"], "studios": ["Wit Studio"], "year": 2020, "episodes": 1, "source": "Manga", "score": 7.83, "synopsis": "The compilation film recaps the anime's 59 episodes from seasons one to three."}, {"id": 59389, "name_en": "The Swords", "name_jp": "剑来", "image_url": "https://cdn.myanimelist.net/images/anime/1740/144466.jpg", "genres": ["Action", "Adventure", "Fantasy"], "themes": ["Historical", "Martial Arts"], "demographics": [], "studios": ["Cloud Art"], "year": 2024, "episodes": 26, "source": "Web novel", "score": 7.82, "synopsis": "The world is vast and full of wonders. In the mystical realm of Lizhu Cave, a destitute youth destined for greatness finds his fate upended when his life-bound porcelain shatters. Though fortune beckons, it remains tantalizingly out of reach. As a result, powerful figures begin weaving intricate schemes around him, turning the boy into the linchpin of their designs. While those around him reap extraordinary opportunities, the youth himself is left standing alone at the eye of the storm...\n\n(Source: WeTV)"}, {"id": 59205, "name_en": "Clevatess", "name_jp": "クレバテス-魔獣の王と赤子と屍の勇者-", "image_url": "https://cdn.myanimelist.net/images/anime/1255/150593.jpg", "genres": ["Action", "Fantasy"], "themes": [], "demographics": [], "studios": ["Lay-duce"], "year": 2025, "episodes": 12, "source": "Web manga", "score": 7.82, "synopsis": "Surrounded by four armies of dark beasts, five humanoid races strive to push the known boundaries of the world. Unfortunately, the 13 heroes dispatched to the southern region are easily wiped out by Clevatess, one of the Lords of Dark Beasts. Determined to get revenge against humanity, Clevatess bursts into the capital of the Kingdom of Hiden, leaving a trail of death and destruction behind him.\n\nAlthough the situation looks desperate, humanity still has a chance to appease Clevatess' anger. Alicia Glenfall, one of the 13 heroes, is revived by Clevatess to help him raise a newborn infant, Luna, whose choices shall shape the fate of the human world. Although reluctant to help the dark beast at first, Alicia understands that Luna is the only thing that stands between humanity and complete annihilation. \n\nHiding himself by taking on a human form, Clevatess sets out in search for a wet nurse with Alicia and Luna. With Clevatess unwilling to reveal his identity, it falls on Alicia to slay all enemies that stand in their way. Meanwhile, the neighboring countries prepare to advance on the weakened Hiden, aiming to control the only forge capable of producing the heroes' weapons.\n\n[Written by MAL Rewrite]"}, {"id": 56704, "name_en": "Kengan Ashura Season 2 Part 2", "name_jp": "ケンガンアシュラ Season2 Part2", "image_url": "https://cdn.myanimelist.net/images/anime/1086/144400.jpg", "genres": ["Action"], "themes": ["Martial Arts"], "demographics": [], "studios": ["Larx Entertainment"], "year": 2024, "episodes": 16, "source": "Web manga", "score": 7.82, "synopsis": "Part two of Kengan Ashura Season 2."}, {"id": 54968, "name_en": "Pseudo Harem", "name_jp": "疑似ハーレム", "image_url": "https://cdn.myanimelist.net/images/anime/1607/143547.jpg", "genres": ["Comedy", "Romance"], "themes": ["Love Status Quo", "School"], "demographics": ["Shounen"], "studios": ["Nomad"], "year": 2024, "episodes": 12, "source": "Web manga", "score": 7.82, "synopsis": "Eiji Kitahama has a dream shared by many high school guys: to be popular and have a harem of girls fawning over him. Luckily for him, his new junior in the drama club, Rin Nanakura, has decided to help him realize this wish. Using her impressive acting abilities, she takes on many personalities, from teasing to calm and collected, to play characters in Eiji's simulated harem.\n\nAs her connection with Eiji deepens, Rin expands her repertoire, quickly swapping between characters while enjoying high school life together in the club. Through her acting, the real Rin Nanakura tries her best to win Eiji's heart.\n\n[Written by MAL Rewrite]"}, {"id": 53013, "name_en": "Let You Down", "name_jp": "Let You Down", "image_url": "https://cdn.myanimelist.net/images/anime/1300/128136.jpg", "genres": ["Action", "Sci-Fi"], "themes": ["Music"], "demographics": [], "studios": ["Studio Massket"], "year": 2022, "episodes": 1, "source": "Game", "score": 7.82, "synopsis": "Music video for the song Let You Down by Dawid Podsiadło.\n\nThis song also serves as the ending theme for the anime Cyberpunk: Edgerunners."}, {"id": 52093, "name_en": "Trigun Stampede", "name_jp": "TRIGUN STAMPEDE", "image_url": "https://cdn.myanimelist.net/images/anime/1426/129194.jpg", "genres": ["Action", "Adventure", "Sci-Fi"], "themes": ["Adult Cast"], "demographics": ["Shounen"], "studios": ["Orange"], "year": 2023, "episodes": 12, "source": "Manga", "score": 7.82, "synopsis": "On the planet Noman's Land, reporters Meryl Stryfe and Roberto De Niro traverse the desert in search of the infamous outlaw Vash the Stampede. But the man they find near the desolate town of Jeneora Rock is a far cry from the lethal terrorist they expect. In reality, Vash is a passive and carefree drifter; he is a proponent of peace, beloved by the residents of the town. His inaccurate reputation actually stems from the widespread atrocities committed by his twin brother Millions Knives. Still, Vash is dubbed \"The Humanoid Typhoon'' due to the tendency for violent chaos to follow in his wake.\n\nChaos soon arrives in the form of bounty hunters seeking the high price on Vash's head, and their violent pursuit poses great danger to the town and its precious power plant. Thanks to his gunslinging prowess, Vash is able to resist most of these nefarious forces. Yet he must soon face off against the ultimate evil: the unstoppable destructive power of his malevolent brother.\n\n[Written by MAL Rewrite]"}, {"id": 51716, "name_en": "Pretty Guardian Sailor Moon Cosmos the Movie", "name_jp": "劇場版 美少女戦士セーラームーンCosmos", "image_url": "https://cdn.myanimelist.net/images/anime/1388/133092.jpg", "genres": ["Romance"], "themes": ["Mahou Shoujo"], "demographics": ["Shoujo"], "studios": ["Toei Animation", "Studio Deen"], "year": 2023, "episodes": 2, "source": "Manga", "score": 7.82, "synopsis": "The final battle for the Sailor Guardians begins as they face Sailor Iron Mouse, Sailor Aluminum Siren, Sailor Lead Crow, Sailor Tin Nyanko and more.\n\n(Source: Netflix)"}]

